INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,1,1,'In naam van Allah, de Barmhartige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,1,2,'Alle lof zij Allah, de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,1,3,'De Barmhartige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,1,4,'Meester van de Dag des Oordeels.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,1,5,'U alleen aanbidden wij en U alleen smeken wij om hulp.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,1,6,'Leid ons op het rechte pad,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,1,7,'Het pad dergenen, aan wie Gij gunsten hebt geschonken - niet dat van hen, op wie toorn is nedergedaald, noch dat der dwalenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,1,'Alif Laam Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,2,'Dit is een volmaakt Boek, daaraan is geen twijfel, een richtsnoer voor de godvrezenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,3,'Die in het onzienlijke geloven en het gebed houden en die weldoen met hetgeen Wij hun hebben geschonken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,4,'En die geloven in hetgeen u is geopenbaard en in hetgeen v��r u is geopenbaard, en een standvastig vertrouwen hebben in dat wat komen zal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,5,'Zij zijn het, die de leiding van hun Heer volgen en dezen zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,6,'Zeker, zij die (de Waarheid) verwerpen, het is hun om het even, of gij hen waarschuwt, of dat gij hen niet waarschuwt - zij zullen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,7,'Allah heeft hun hart en oren verzegeld en over hun ogen is een sluier; hun wacht een zware straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,8,'En er zijn mensen, die zeggen: \"Wij geloven in Allah en in de laatste Dag, hoewel zij geen gelovigen zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,9,'Zij trachten Allah en de gelovigen te bedriegen, zij misleiden echter niemand dan zichzelf en zij beseffen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,10,'Er is een ziekte in hun hart en Allah heeft die ziekte verergerd; er wacht hun een pijnlijke straf, omdat zij plachten te liegen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,11,'Wanneer hun wordt gezegd: \"Richt geen onheil op aarde aan\" dan zeggen zij: \"Wij zijn slechts vredestichters\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,12,'Pas op! Voorzeker zij zijn het die onheil stichten, doch zij beseffen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,13,'En wanneer hun wordt gezegd: \"Gelooft, zoals andere mensen geloven\", zeggen zij: \"Zullen wij geloven, zoals de dwazen hebben geloofd?\" Ziet toe! Zij zijn het die dwaas zijn, doch zij weten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,14,'En wanneer zij de gelovigen ontmoeten, zeggen zij: \"Wij geloven\", doch wanneer zij naar hun leiders gaan, zeggen zij: \"Wij zijn waarlijk met u, wij spotten slechts (met hen).\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,15,'Allah zal hun spotternij bestraffen en Hij zal hen blindelings in hun overtreding verder laten afdwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,16,'Zij zijn het die dwaling hebben aanvaard in ruil voor de rechte weg, maar hun handelwijze heeft hun geen gewin gebracht, noch konden zij worden geleid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,17,'Hun toestand is als de toestand van iemand die een vuur ontstak en toen het zijn omgeving verlichtte, nam Allah hun licht weg en liet hen in diepe duisternis, zodat zij niet meer zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,18,'Doof, stom en blind, derhalve keren zij niet terug;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,19,'Of, (dat zij) bij zware regen uit de hemel waarmede dichte duisternis, donder en bliksem komt, uit doodsangst hun vingers in de oren steken vanwege de donderslagen. Allah omringt de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,20,'Bijna beneemt het bliksemlicht hun het gezichtsvermogen; telkens als het hen beschijnt, wandelen zij daarin, maar wordt het weder donker, dan staan zij stil. En, zo Allah het wilde, zou Hij hun het gehoor en het gezicht kunnen ontnemen, waarlijk, Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,21,'O gij mensen, aanbidt uw Heer, die u en degenen, die v��r u waren, schiep - opdat gij behouden zult worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,22,'Die u de aarde tot een legerstede maakte en de hemel tot een gewelf en Die water van de wolken deed nederkomen en daardoor vruchten voortbracht, als voedsel voor u. Plaatst derhalve geen gelijken nevens Allah, tegen beter weten in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,23,'En, indien gij in twijfel zijt omtrent hetgeen Wij aan Onze dienaar hebben geopenbaard, probeert dan een dergelijk hoofdstuk voort te brengen en roept uw helpers buiten Allah, als gij waarachtig zijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,24,'Doch, indien gij het niet kunt doen - en gij zult het nimmer kunnen doen - wacht u dan voor het Vuur, dat voor de ongelovigen is bereid, welks brandstof mensen en stenen zign.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,25,'En verkondig aan degenen, die geloven en goede werken doen de blijde tijding, dat er tuinen (het paradijs) voor hen zijn, waardoorheen rivieren vloeien. Telkens, wanneer hun van de vruchten hieruit wordt geschonken, zullen zij zeggen: \"Ziehier, hetgeen ons reeds voorheen werd gegeven\"; en hun werd het soortgelijke gegeven. En zij zullen er reine metgezellen hebben en zij zullen er vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,26,'Waarlijk, Allah acht het niet beneden zich, een mug of iets nog kleiners als gelijkenis te stellen. Zij die geloven weten, dat dit de Waarheid van hun Heer is, terwijl degenen, die niet geloven, zeggen:\"Wat bedoelt Allah met zulk een voorbeeld?\" Velen laat Hij daardoor dwalen en velen leidt Hij daardoor terecht - en niemand laat Hij daarmede dwalen, dan de ongehoorzamen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,27,'Die het verbond met Allah breken na de bekrachtiging er van en datgene, wat Allah gebood te verenigen, scheiden en die onheil op aarde stichten, dezen zijn de verliezers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,28,'Hoe kunt gij Allah verwerpen, terwijl gij levenloos waart en Hij u leven schonk? Hij zal u doen sterven en daarna zal Hij u doen herleven en dan zult gij tot Hem worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,29,'Hij is het, Die alles, wat op aarde is, voor u schiep: daarna wendde Hij Zich tot de hemel en vervolmaakte deze tot zeven hemelen, want Hij heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,30,'En toen uw Heer tot de engelen zeide: \"Ik wil een stedehouder op aarde plaatsen,\" zeiden zij: \"Wilt Gij er iemand plaatsen die er onheil zal stichten en bloed zal vergieten, terwijl wij U verheerlijken met de lof die U toekomt en Uw Heiligheid prijzen,\" antwoordde Hij: \"Ik weet wat gij niet weet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,31,'En Hij leerde Adam al de namen. Dan plaatste Hij (de voorwerpen dezer) namen voor de engelen en zeide: \"Noemt Mij hun namen, indien gij in uw recht staat.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,32,'Zij zeiden: \"Heilig zijt Gij. Wij bezitten geen kennis, buiten hetgeen Gij ons hebt geleerd; waarlijk, Gij zijt de Alwetende, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,33,'Hij zeide: \"O, Adam, zeg hun de namen van deze dingen\", en toen hij de namen had genoemd, zeide Hij: \"Zeide Ik u niet: Waarlijk Ik ken de geheimen der hemelen en der aarde en Ik weet, wat gij onthult en wat gij verbergt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,34,'En toen Wij tot de engelen zeiden: \"Onderwerpt u aan Adam\", onderwierpen zich allen, behalve Iblies. Hij weigerde, hij was hoogmoedig. Hij behoorde tot de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,35,'En Wij zeiden: \"O Adam, verblijf gij met uw gade in de tuin en eet overvloedig, waar gij ook wilt, doch nader deze boom niet, anders zult gij tot de zondaren behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,36,'Doch door middel van de boom verleidde Satan hen beiden en dreef hen uit de staat waarin zij zich bevonden. En Wij zeiden: \"Gaat heen - gij zijt elkander vijandig. Er zal op aarde een tijdelijke woonplaats en levensonderhoud voor u zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,37,'Toen leerde Adam enkele woorden van zijn Heer. Zo schonk Hij hem vergiffenis; gewis Hij is Berouwaanvaardend, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,38,'Wij zeiden: \"Gaat allen weg van hier. En, indien er leiding van Mij tot u komt, zullen zij, die Mijn leiding volgen, vrees noch droefheid kennen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,39,'Doch zij, die niet geloven en Onze tekenen verloochenen, zullen de bewoners van het Vuur zijn; zij zullen daarin verblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,40,'\"O kinderen Isra�ls! Gedenkt Mijn gunsten, welke Ik u bewees en weest getrouw aan Mijn verbond. Ik zal Mijn verbond met u houden en Mij alleen zult gij vrezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,41,'En gelooft in hetgeen Ik heb geopenbaard, vervullende datgene, wat gij reeds bezit en weest niet de eersten, die het verwerpen; verruilt evenmin mijn tekenen voor geringe prijs en zoekt bescherming in Mij alleen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,42,'En verwart de waarheid niet met de onwaarheid, noch verbergt de waarheid tegen beter weten in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,43,'En houdt het gebed en betaalt de Zakaat en bidt met hen, die bidden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,44,'Beveelt gij de mensen het goede te doen en vergeet daarbij u zelf, hoewel gij het Boek leest? Wilt gij dan niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,45,'Zoekt hulp door geduld en gebed; dit is inderdaad moeilijk, behalve voor de ootmoedigen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,46,'Die er zeker van zijn, dat zij hun Heer zullen ontmoeten en dat zij tot Hem zullen wederkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,47,'O kinderen Isra�ls! Gedenkt Mijn gunsten, die Ik u bewees, dat Ik u boven de volkeren verhief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,48,'En vreest de Dag, dat de ene ziel de andere niet zal kunnen helpen, waarop voor haar noch voorspraak zal worden aanvaard, noch een losprijs zal worden aangenomen, noch zij zullen worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,49,'En toen Wij u redden van Pharao´s volk, dat u met bittere marteling kwelde, Uw zonen dodend en uw vrouwen sparend; hierin was voor u een zware beproeving van uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,50,'En toen Wij de zee voor u spleten en u redden en Pharao´s volk lieten verdrinken, terwijl gij toezaagt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,51,'En toen Wij met Mozes een tijd afspraken van veertig nachten; toen naamt gij in zijn afwezigheid het kalf, (om het te aanbidden) en gij werdt overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,52,'Daarna vergaven Wij u, opdat gij dankbaar zoudt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,53,'En toen gaven Wij Mozes het Boek en het oordeel des onderscheids, opdat gij recht geleid zoudt worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,54,'En toen Mozes tot zijn volk zeide: \"O mijn volk, gij hebt uzelf onrecht aangedaan door het kalf te aanvaarden: derhalve keert terug tot Uw Schepper en doodt uw eigen ik, dat is het beste voor u in het oog van uw Schepper\". Daarna wendde Hij zich genadig tot u. Voorzeker, Hij is Berouwaanvaardend, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,55,'En toen gij zeidet: \"O Mozes, wij zullen u geenszins geloven, totdat wij Allah van aangezicht tot aangezicht zien\", toen trof u een donderslag, terwijl gij toezaagt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,56,'Toen deden Wij u verrijzen na uw dood, opdat gij dankbaar zoudt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,57,'En Wij deden de wolken een schaduw over u zign en zonden u manna en kwartels, (zeggende): \"Eet van de goede dingen, waarmede Wij u hebben voorzien.\" Zij schaadden Ons niet, maar zij plachten hun eigen ziel te schaden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,58,'En toen Wij zeiden: \"Gaat in deze stad en eet er overvloedig, waar gij ook wilt; treedt de poort onderdanig binnen en vraagt om vergiffenis. Wij zullen u uw fouten vergeven en Wij zullen meer geven aan degenen, die goed doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,59,'Maar de onrechtvaardigen vervingen het woord door een ander, dat niet tegen hen gesproken was. Daarom zonden Wij over de onrechtvaardigen een grote straf vanuit de hemel, omdat zij plachten te overtreden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,60,'En toen Mozes om water voor zijn volk bad zeiden Wij: \"Sla op de rots met uw staf\" en er ontsprongen twaalf bronnen aan, waardoor elke stam zijn drinkplaats kende. Eet en drinkt van wat Allah heeft voortgebracht en wandelt niet op aarde, onheil stichtende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,61,'En toen gij zeidet: \"O Mozes, wij verdragen niet langer ��n soort voedsel, bid daarom voor ons tot uw Heer, dat Hij van hetgeen op aarde groeit - groenten en komkommers en tarwe en linzen en uien - voor ons voortbrenge,\" zeide Hij: \"Zoudt gij hetgeen minderwaardig is in ruil willen nemen voor hetgeen beter is? Gaat naar een stad, daar zult gij vinden, waarom gij vraagt.\" En zij kwamen in vernedering en arrmoede en brachten Allah´s toorn over zich; dit kwam, omdat zij de tekenen van Allah verwierpen en de profeten onrechtvaardig doodden, want zij waren ongehoorzaam en telkens weer in overtreding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,62,'Voorzeker, de gelovigen, de Joden, de Christenen en de Sabianen - wie onder hen ook in Allah en de laatste Dag geloven en goede daden verrichten, zullen hun beloning bij hun Heer ontvangen en er zal geen vrees over hen komen, noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,63,'En toen Wij een verbond met u aangingen en de berg hoog boven u verhieven, zeiden Wij: \"Houdt vast, wat Wij u hebben gegeven en bedenkt wat het bevat, zodat gij behoed zult worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,64,'Maar gij wenddet u af en, had Allah u Zijn genade en barmhartigheid niet betoond, dan zoudt gij zeker zijn ondergegaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,65,'Gij hebt degenen onder u gekend, die inzake de Sabbath overtraden. Alzo zeiden Wij tot hen: \"Weest verachte apen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,66,'Zo maakten Wij hen tot een voorbeeld voor hen die in die tijd leefden en voor degenen, die na hen kwamen en tot een les voor de godvrezenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,67,'En toen Mozes tot zijn volk zeide: \"Waarlijk, Allah gebiedt u, een koe te slachten\", zeiden zij: \"Drijft gij de spot met ons?\" Hij zeide: \"Ik zoek toevlucht bij Allah, om niet tot de onwetenden te behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,68,'Zij zeiden: \"Bid voor ons tot uw Heer, opdat Hij het ons duidelijk make, wat voor een koe dit moet zijn.\" Hij antwoordde: \"Hij zegt, dat het een koe moet zijn, noch oud, noch jong, volwassen, tussen beide in - doet nu, wat u geboden is.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,69,'Zij zeiden: \"Bid voor ons tot uw Heer, dat Hij het ons duidelijk make, welke kleur zij heeft\" Hij antwoordde: \"Hij zegt, dat het een gele koe is met een diepe kleur, aangenaam voor hen, die haar zien.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,70,'Zij zeiden: \"Bid voor ons tot uw Heer, dat Hij ons mededele, hoe zij is, want al zulke koeien zien er voor ons gelijk uit; en als Allah het wil, zullen wij juist worden geleid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,71,'Hij antwoordde: \"Hij zegt, dat het een koe is, die nog nooit afgericht is geweest, om de aarde te beploegen, of de akkers te bevloeien, een koe, gaaf en vlekkeloos.\" Zij zeiden: \"Nu hebt gij het precies gezegd.\" Toen slachtten zij haar, doch liever hadden zij het niet gedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,72,'En toen gij trachttet een mens te doden en onder elkander er over twisttet, was Allah de onthuller van wat gij verborgen hieldt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,73,'Toen zeiden Wij: \"Treft hem (de moordenaar) voor een gedeelte van het vergrijp tegen hem (de gedode)\". Aldus geeft Allah leven aan de doden en toont u Zijn tekenen, opdat gij zult begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,74,'Daarna verhardde zich uw hart. Zij zijn als stenen, of nog harder, want er zijn stenen, waaruit stromen ontspringen en er zijn er zeker, die splijten en er vloeit water uit. En sommige zijn er die uit vrees voor Allah neervallen. En Allah is niet achteloos, ten opzichte van wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,75,'Verwacht gij, dat zij u zullen geloven, terwijl een aantal hunner het woord van Allah heeft vernomen en het verdraait, nadat zij het hebben begrepen, tegen beter weten in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,76,'Wanneer zij de gelovigen ontmoeten zeggen zij: \"Wij geloven\" en wanneer zij onder elkander zijn zeggen zij: \"Verhaalt gij hun, wat Allah u heeft geopenbaard, zodat zij daardoor met u kunnen redetwisten voor uw Heer.\" Wilt gij dan niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,77,'Begrijpen zij dan niet, dat Allah weet, wat zij verbergen en wat zij openbaar maken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,78,'En sommigen hunner zijn ongeletterd; zij weten niets van het Boek, maar hebben hun valse denkbeelden: zij vermoeden slechts.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,79,'Wee daarom degenen, die een boek met hun eigen handen schrijven en dan zeggen: \"Dit is van Allah\", opdat zij er een onwaardige prijs voor kunnen nemen. Wee hun dan, voor hetgeen hun handen schrijven en wee hun voor hetgeen zij verdienen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,80,'En zij zeggen: \"Het Vuur zal ons slechts voor een klein aantal dagen deren\". Vraag hun: \"Hebt gij dan een woord van Allah verkregen? Dan zal Allah Zijn belofte nooit breken. Of zegt gij iets over Allah, dat gij niet weet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,81,'Voorzeker, die kwaad doet en door zijn zonden is omringd - zij zijn de bewoners van het Vuur; daarin zullen zij verblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,82,'Maar zij, die geloven en goede werken doen, - zij zijn de bewoners van de Hemel, daarin zullen zij verblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,83,'En toen Wij een verbond sloten met de kinderen Isra�ls, zeiden Wij, dat gij niemand zult aanbidden, dan Allah alleen en dat gij goed zult zijn voor uw ouders, uw verwanten, de wezen en de armen; spreekt goed tegen de mensen en houdt het gebed en geeft de Zakaat. Doch gij wenddet u af, - behalve weinigen onder u, en gij zijt afkerig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,84,'En toen Wij een verbond met u sloten: \"Gij zult uw bloed niet vergieten noch uw volk uit hun huizen verdrijven\", toen hebt Gij dit bekrachtigd en gij waart er getuige van.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,85,'Toch zijt gij het volk, dat uw eigen broeders doodt en een gedeelte van uw volk uit hun huizen verdrijft, elkaar tegen hen helpende in zonde en overtreding. En, indien zij als gevangenen tot u terugkomen, koopt gij hen vrij, terwijl juist hun verdrijving voor u verboden was. Gelooft gij dan slechts in een gedeelte van het Boek en verwerpt gij een ander gedeelte? Er is geen beloning voor degenen uwer, die zulks doen, behalve schande in dit leven; en op de Dag van Opstanding zullen zij de strengste kastijding moeten ondergaan, want Allah is niet onachtzaam betreffende hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,86,'Dezen zijn het, die het Hiernamaals voor het tegenwoordig leven hebben verkocht. Derhalve zal hun straf niet worden verzacht, noch zullen zij worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,87,'Voorwaar, Wij gaven Mozes het Boek en deden boodschappers de een na de ander zijn voetsporen volgen. En Wij gaven aan Jezus, zoon van Maria, duidelijke tekenen en versterkten hem met de geest der heiligheid. Telkens als een boodschapper tot u kwam, met hetgeen uw ziel niet behaagde, hebt gij u laatdunkend gedragen, sommigen hunner hebt gij verloochend en anderen gedood.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,88,'En zij zeiden: \"Ons hart is verhuld.\" Neen, Allah heeft hen vanwege hun ongeloof vervloekt. Weinig is derhalve hetgeen zij geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,89,'En toen een Boek van Allah tot hen kwam, vervullend datgene, dat bij hen was, hoewel zij voordien om overwinning over de ongelovigen plachten te bidden, toen dat tot hen kwam, herkenden zij dat niet en verwierpen het. Gods vloek rust derhalve op de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,90,'Kwaad is datgene, waarvoor zij hun ziel hebben verkocht; daar zij verwerpen, hetgeen Allah heeft geopenbaard, er afkerig van zijnde, dat Allah Zijn genade doet dalen over diegenen Zijner dienaren, die Hij wil. Daardoor brachten zij toorn op toorn over zich en er is een vernederende kastijding voor de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,91,'En wanneer er tot hen wordt gezegd: \"Gelooft in hetgeen Allah heeft geopenbaard,\" zeggen zij: \"Wij geloven slechts in hetgeen ons is geopenbaard,\" maar zij verwerpen hetgeen daarna is geopenbaard, hoewel het de Waarheid is, vervullende wat zij reeds bezaten. Zeg hun \"Waarom hebt gij dan de vroegere profeten van Allah gedood, als gij inderdaad gelovigen waart?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,92,'En Mozes kwam voorzeker tot u met duidelijke tekenen, maar gij hebt in zijn afwezigheid het (gouden) kalf genomen (om het te aanbidden) en gij waart onrechtvaardig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,93,'En toen Wij een verbond met U sloten en de berg (Sina�) hoog boven u verhieven, zeggende: \"Houdt stevig vast, hetgeen Wij u gegeven hebben en luistert,\" zeiden zij: \"Wij horen, maar wij gehoorzamen niet\"; hun hart was vervuld van het kalf, wegens hun ongeloof. Zeg: \"Slecht is hetgeen uw geloof u oplegt, zo gij al enig geloof bezit\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,94,'Zeg: \"Indien het tehuis van het Hiernamaals, bij Allah alleen voor u is, met uitsluiting van andere mensen, wenst dan eer de dood, als gij te goeder trouw zijt\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,95,'Maar zij zullen deze nooit wensen, wegens het werk hunner handen. En Allah kent de boosdoeners goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,96,'Voorzeker, gij zult hen (Joden) het meest van alle mensen verlangend naar het leven vinden, zelfs meer dan de afgodendienaren. Ieder van hen wenst, dat hem een leven van duizend jaren geschonken moge worden, doch al ware hem zulk een lang leven vergund, dan zou het hem tegen de straf toch niet beschermen. Allah ziet hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,97,'Zeg: \"Al wie een vijand van Gabri�l is\" - want waarlijk, hij openbaarde het op Allah´s bevel aan uw hart, vervullende datgene, wat voordien kwam, een leidraad zijnde en een blijde mare voor de gelovlgen. -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,98,'\"Al wie een vijand is van Allah en Zijn engelen en Zijn boodschappers en Gabri�l en Micha�l, waarlijk, Allah is een vijand van zulke ongelovigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,99,'En Wij hebben u voorzeker duidelijke tekenen gegeven en niemand, dan de overtreders, verwerpt ze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,100,'Hoe kwam dat? Telkens wanneer zij een verbond aangingen, schond een gedeelte hunner het. Neen, de meesten hunner geloven niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,101,'En nu er een boodschapper van Allah tot hen is gekomen, vervullend wat zij reeds bezaten, heeft een gedeelte der mensen van het Boek, Allah´s Boek achter zich geworpen, alsof zij het niet kenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,102,'En zij volgen dezelfde weg, die de duivels volgden tegen de regering van Salomo - en Salomo was niet ongelovig, maar ongelovig waren de duivels en zij leerden de mensen leugen en bedrog. En (zij handelen naar) hetgeen aan de twee engelen, Haroet en Maroet te Babylon was geopenbaard. Maar deze beiden leerden niemand, voordat zij hadden gezegd: \"Wij zijn slechts een beproeving; weest daarom niet ongelovig\". Zo leren zij (de mensen) van hen datgene waarmede zij een geschil maken tussen een man en zijn vrouw, maar zij schaden er niemand mede, tenzij door Allah´s bevel; maar dezen leren wat hen schaadt en geen goed doet, hoewel zij weten, dat hij, die in deze zaken handelt geen voordeel heeft in het Hiernamaals; slecht is hetgene waarvoor zij hun ziel hebben verkocht; hadden zij het slechts ingezien!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,103,'Indien zij hadden geloofd en rechtvaardig gehandeld, zou een schonere beloning van Allah gewis hun deel zijn geweest, hadden zij het slechts geweten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,104,'O, gij die gelooft, zegt niet: \"Raainaa\", maar zegt: \"Onzornaa\" en luistert. Er is voor de ongelovigen een pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,105,'Zij die niet geloven onder de mensen van het Boek, en de afgodendienaren, gunnen niet, dat iets goeds tot u nedergezonden wordt van uw Heer; maar Allah kiest voor Zijn barmhartigheid, wie Hij wil en Allah is de Heer van grote genade.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,106,'Welk teken Wij ook opheffen of doen vergeten, daarvoor brengen Wij betere of daaraan gelijke. Weet gij niet, dat Allah macht heeft over alle dingen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,107,'Weet gij niet, dat het koninkrijk der hemelen en der aarde aan Allah behoort? En buiten Allah is er geen beschermer of helper Xoor u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,108,'Zoudt gij de boodschapper die tot u z werd gezonden, willen ondervragen, zoals - Mozes voorheen werd ondervraagd? Maar wie ongeloof in ruil neemt voor geloof, is voorzeker van het rechte pad afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,109,'Velen van de mensen van het Boek, wensen, nadat gij gelovig geworden zijt, u uit afgunst weder tot ongelovigen te maken, nadat de Waarheid hun is duidelijk geworden. Maar vergeeft en weest toegefelijk totdat Allah Zijn gebod uitbrengt. Voorzeker, Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,110,'En onderhoudt het gebed en betaalt de Za´kaat; het goede dat gij vooruit zendt voor uzelf, gij zult het bij Allah vinden. Voorzeker, Allah ziet al hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,111,'En zij zeggen: \"Niemand, behalve de Joden en de Christenen, zal ooit de Eemel binnengaan.\" Dat zijn hun ijdele wensen. Zeg: \"Toont uw bewijs, aJs gij waarachtig zijt\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,112,'Neen, wie zich volledig aan Allah onderwerpt en goede daden verricht, zal zijn beloning bij zijn Heer hebben. Vrees noch droefheid zal over hem komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,113,'De Joden zeggen: \"De Christenen hebben geen ware grondslag en de Christenen zeggen: \"De Joden hebben geen ware grondslag\", terwijl zij beiden hetzelfde Boek lezen. Hetzelfde zeggen degenen, die geen kennis hebben. Maar Allah zal op de Dag der Opstanding uitspraak doen in hun geschil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,114,'En wie is onrechtvaardiger dan hij, die verbiedt, dat de naam van Allah wordt verheerlijkt in Allah´s bedehuizen en deze tracht te vernietigen? Zij behoorden (de bedehuizen) slechts in vreze binnen te gaan. Er is schande over hen in deze wereld en er zal een grote straf voor hen zijn in het Hiernamaals.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,115,'En aan Allah behoort het Oosten en het Westen; waarheen gij u ook wendt, daar zal het Aangezicht van Allah zijn. Zeker, Allah is Alomvattend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,116,'En zij zeggen: \"Allah heeft Zich een zoon verwekt. Heilig is Hij. Neen, alles, wat in de hemelen en op aarde is, behoort Hem toe en alles gehoorzaamt Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,117,'Wondere Schepper van de hemelen en aarde. Wanneer Hij iets besluit, zegt Hij slechts: \"Wees\" en het wordt\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,118,'En de onwetenden zeggen: \"Waarom spreekt Allah niet tot ons, of, komt er geen teken tot ons?\" Zo spraken ook degenen, die v��r hen waren. Hun harten zijn aan elkander gelijk. Wij hebben de tekenen voorzeker duidelijk gemaakt, voor een volk, dat standvastig gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,119,'Voorzeker Wij hebben u als drager van blijde tijdingen en waarschuwer gezonden met de Waarheid. En gij zult niet verantwoordelijk worden gesteld voor de bewoners der hel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,120,'En de Joden en de Christenen zullen u nooit welgezind zijn, tenzij gij hun godsdienst belijdt. Zeg: \"Voorzeker, Allah´s leiding is de Merkelijke leiding\". En, indien gij hun wensen volgt, nadat de kennis tot u is gekomen, zult gij aan Allah Vriend noch Helper hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,121,'Zij, wie Wij het Boek hebben gegeven, volgen het na, zoals het behoort te worden nagevolgd; dezen zijn het, die er in geloven. En die er niet in geloven, zullen de verliezers zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,122,'O, gij kinderen Isra�ls, gedenkt Mijn gunsten die Ik u bewees, dat Ik u boven die volkeren verhief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,123,'En vreest de Dag, waarop geen ziel een andere ziel van nut kan zijn, waarop geen losprijs van haar zal worden aanvaard, geen voorspraak haar zal baten, noch zullen zij worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,124,'En toen Abrahams Heer hem met zekere opdrachten beproefde en Abraham deze vervulde, zeide Hij: \"Ik zal u tot leider der mensen maken\". Abraham vroeg: \"En ook aran onder mijn nakomelingen?\" Hij zeide: \"Mijn verbond betreft de overtreders niet\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,125,'En toen Wij het Huis tot een plaats van verzameling voor de mensheid en een toevluchtsoord maakten, zeggende: \"Neemt de plaats van Abraham als een plaats voor gebed\". En Wij geboden Abraham en Isma�l, zeggende: \"Reinigt Mijn Huis voor degenen, die de ommegang verrichten en voor degenen, die er toegewijd in verblijven en voor degenen, die zich neder buigen en zich ter aarde werpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,126,'En toen Abraham bad: \"Mijn Heer, maak deze plaats toch tot een oord van vrede en geef vruchten aan haar bewoners, die aan Allah en de laatste dag geloven\", zeide Hij: \"Ik zal voor een korte tijd ook aan hem, die niet gelooft weldaden schenken, daarna zal Ik hem in het Vuur drijven: het is een slechte verblijfplaats\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,127,'En toen Abraham en Isma�l de muren van het Huis optrokken, biddende: \"Heer, aanvaard dit van ons, want Gij zijt de Alhorende, de Alwetende,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,128,'Heer, maak ons beiden aan U onderdanig en maak van ons nageslacht een volk, dat U onderdanig zij. En toon ons onze wijzen van aanbidding en wend U met barmhartigheid tot ons, zeker, Gij zijt Berouwaanvaardend en Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,129,'Heer, doe onder hen een boodschapper opstaan, die hun Uw tekenen zal verkondigen en hun het Boek en de Wijsheid zal verklaren en hen zal louteren. Voorzeker, Gij zijt de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,130,'En wie zal zich van het geloof van Abraham afwenden, behalve hij, die dwaas tegen zichzelf handelt? Voorzeker, Wij hebben hem in deze wereld uitverkoren en in de volgende zal hij gewis onder de rechtvaardigen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,131,'Toen zijn Heer tot hem zeide: \"Onderwerp U\", zeide hij: \"Ik heb mij aan de Heer der Werelden onderworpen\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,132,'En hetzelfde legde Abraham aan zijn zonen op en Jacob deed desgelijks, zeggende: \"O mijn zonen, Allah heeft waarlijk dit geloof voor u verkozen, sterft daarom niet, tenzij gij Moslims zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,133,'Of waart gij aanwezig, toen de dood tot Jacob kwam en hij tot zijn zonen zeide: \"Wat zult gij na mij aanbidden?\" Zij antwoordden: \"Wij zullen uw God aanbidden, de God uwer vaderen, Abraham, Isma�l en Iza�k, de enige God, aan Hem zijn wij onderworpen\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,134,'Dit is een volk, dat is heengegaan: voor hen is, hetgeen zij verdienden en voor u is, hetgeen gij verdient en gij zult niet worden ondervraagd over hetgeen zij plachten te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,135,'En zij zeggen: \"Weest Joden of Christenen, dan zult gij worden geleid\". Zeg (hun): \"Neen, maar (volg) de godsdienst van Abraham, de oprechte: hij behoorde niet tot de afgodendienaren\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,136,'Zegt: \"Wij geloven in Allah en in hetgeen ons is geopenbaard en in hetgeen tot Abraham, Isma�l, Iza�k, Jacob en de stammen werd nedergezonden en in hetgeen aan Mozes en Jezus werd gegeven en in hetgeen aan alle andere profeten werd gegeven door hun Heer. Wij maken geen onderscheid tussen hen en aan Hem onderwerpen wij ons.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,137,'En indien zij geloven, zoals gij hebt geloofd, dan zijn zij juist geleid, maar indien zij zich afwenden, dan zijn zij in verzet; Allah zal u zeker voldoende zijn tegen hen, want Hij is de Alhorende, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,138,'Maakt Allah´s kleur tot de uwe en wie is beter in kleur, dan Allah? Hem alleen aanbidden wij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,139,'Zeg: \"Twist gij met ons omtrent Allah, terwijl Hij uw Heer en onze Heer is? En voor ons zijn onze werken en voor u uw werken. En Hem alleen zijn wij oprecht toegewijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,140,'Zegt gij, dat Abraham en Isma�l en Iza�k en Jacob en de stammen Joden of Christenen waren? Zeg: \"Weet gij het beter of Allah?\" En wie is onrechtvaardiger, dan hij, die een getuigenis verbergt, die hij van Allah heeft? En Allah is niet onbekend met hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,141,'Dit is een volk, dat is heengegaan: voor hen is, hetgeen zij verdienden en voor u is, hetgeen gij verdient; en gij zult niet worden ondervraagd over hetgeen zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,142,'De dwazen onder het volk zullen zeggen: \"Wat heeft hen van hun Qiblah, die zij volgden, afgekeerd?\" Zeg: \"Aan Allah behoort het Oosten en het Westen. Hij leidt, wie Hij wil naar het rechte pad\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,143,'En zo hebben Wij u tot een verheven volk gemaakt, opdat gij getuige zult zijn tegenover de mensen en de Gezant zij een getuige tegenover u. Wij bepaalden de Qiblah, die gij volgdet slechts, opdat Wij hem, die de gezant van Allah volgt, onderscheiden van degene die hem de rug toekeert. En dit is inderdaad zeer moeilijk, behalve voor hen, die Allah heeft geleid. En Allah zal u uw geloof niet doen verliezen; voorzeker, Allah is Liefderijk en Genadevol jegens de mensen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,144,'Waarlijk, Wij zien uw aangezicht zich naar de hemel wenden, daarom zullen Wij u tot beheerder maken van de Qiblah, die u behaagt. Wend daarom uw aanaangezicht naar de Heilige Moskee en waar gij ook moogt zijn, wendt uw aangezicht daarheen. En voorzeker, zij wie het Boek is gegeven, weten, dat dit de Waarheid is van hun Heer; Allah is niet achteloos ten aanzien van wat zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,145,'Zelfs al bracht gij elk teken aan degenen aan wie het Boek is gegeven, zouden zij nooit uw Qiblah volgen, noch kunt gij hun Qiblah volgen, noch zijn er onder hen, die de Qiblah van anderen volgen. En indien gij aan hun wens zoudt voldoen, nadat kennis tot u is gekomen, zoudt gij zeker tot de onrechtvaardigen behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,146,'Degenen aan wie Wij het Boek hebben gegeven erkennen dit, zoals zij hun zonen erkennen, maar voorzeker, sommigen hunner verbergen de Waarheid tegen beter weten in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,147,'De Waarheid is van uw Heer, schaar u daarom niet onder hen die twijfelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,148,'Iedereen heeft een richting, waarheen hij zich wendt, wedijvert daarom met elkander in goede werken. Waar gij ook zijt, Allah zal u allen tezamen brengen. Voorzeker, Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,149,'Vanwaar gij ook komt, wend uw aangezicht naar de Heilige Moskee, want dat is inderdaad de Waarheid van uw Heer. En Allah is niet achteloos ten aanzien van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,150,'Vanwaar gij ook komt, wend uw aangezicht naar de Heilige Moskee; waar gij ook zijt, wendt uw aangezicht daarheen, opdat de mensen, met uitzondering van de onrechtvaardigen geen bezwaar tegen u mogen aanvoeren - vreest hen dus niet, maar vreest Mij - en opdat Ik Mijn gunst aan u moge voltooien en opdat gij juist geleid moogt worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,151,'Omdat Wij uit uw midden een boodschapper hebben gezonden, die u Onze tekenen verkondigt, u zuivert, u het Boek en de Wijsheid onderwijst en u leert, hetgeen gij niet wist.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,152,'Gedenkt Mij daarom en Ik zal u gedenken en weest Mij dankbaar en weest Mij niet ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,153,'O, gij die gelooft, zoekt hulp met geduld en gebed; voorzeker, Allah is met de geduldigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,154,'En zegt niet van degenen, die voor Allah´s zaak zijn gedood, dat zij dood zijn - neen, zij leven, maar gij bemerkt het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,155,'En Wij zullen u een weinig beproeven door vrees, honger, verlies van bezittingen, levens en vruchten; maar verkondig blijde tijdingen aan de geduldigen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,156,'Zij die, wanneer een rampspoed hen achterhaalt, zeggen: \"Voorzeker, wij zijn van Allah en tot Hem zullen wij wederkeren\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,157,'Dezen zijn het, op wie de zegeningen en de barmhartigheid van hun Heer rusten en dezen zijn het, die de rechte weg volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,158,'Voorzeker, Safaa en Marwah zijn onder de tekenen van Allah. Er rust derhalve op hem, die de Hadj (pelgrimstocht) doet, of (of soms) de Omrah verricht, geen blaam, indien hij om beiden (heen) loopt. En wie vrijwillig goed doet, voorzeker, Allah is Waarderend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,159,'Voorzeker, degenen, die hetgeen Wij aan tekenen en leiding hebben nedergezonden, verbergen, nadat Wij zein het Boek aan de mensen duidelijk hebben gemaakt, zijn het, die Allah vervloekt en zij die het recht hebben te vervloeken, vervloeken hen ook.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,160,'Maar zij, die berouw hebben en zich beteren en (de Waarheid) verkondigen, dezen zijn het, tot wie Ik Mij met vergiffenis wend - Ik ben Berouwaanvaardend, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,161,'Voorzeker, die verwerpen en als ongelovigen sterven, over hen zal de vloek komen van Allah en van de engelen en van alle mensen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,162,'Daarin zullen zij blijven. Hun straf zal niet worden verlicht, noch zal hun uitstel worden verleend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,163,'En uw God is ��n God, er is geen God buiten Hem, de Barmhartige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,164,'Voorwaar, in de schepping der hemelen en der aarde en in de wisseling van nacht en dag en in de schepen die de zee bevaren, met datgene wat de mensen tot voordeel strekt; en in het water dat Allah van de hemel nederzendt, waarmede Hij de aarde doet herleven na haar dood en daarop alle soorten dieren verspreidt, en in de verandering der winden, en in de wolken die tussen de hemel en de aarde in dienst zijn gesteld, zijn inderdaad tekenen voor een volk, dat begrijpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,165,'Onder de mensen zijn er, die voorwerpen van aanbidding buiten Allah nemen en ze liefhebben, zoals zij Allah behoren lief te hebben. Maar zij die geloven zijn sterker in hun liefde voor Allah. En als zij die overtreden (nu) de tijd kunnen zien wanneer zij de straf zullen zien, (dan zouden zij beseffen) dat alle macht aan Allah toebehoort en dat Allah streng is in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,166,'Wanneer de leiders hun volgelingen zullen verzaken en de straf zullen bemerken en al hun banden zullen worden verbroken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,167,'Zullen de volgelingen zeggen: \"Indien wij slechts terug konden keren, zouden wij hen verzaken, zoals zij ons hebben verzaakt\". Zo zal Allah aan hen hun werken tonen tot wroeging en zij zullen het Vuur niet kunnen ontkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,168,'O gij mensen, eet van hetgeen geoorloofd en goed is op aarde en treedt niet in de voetstappen van Satan; voorzeker, hij is voor u een openlijke vijand.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,169,'Hij gebiedt u alleen, wat kwaad en wat onrein is en dat gij over Allah zegt, wat gij niet weet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,170,'En wanneer er tot hen wordt gezegd: \"Volgt hetgeen Allah heeft geopenbaard\", zeggen zij: \"Neen, wij zullen datgene volgen, wat wij onze vaderen zagen volgen\". Zelfs al hadden hun vaderen in het geheel geen verstand en volgden zij ook de rechte weg niet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,171,'De ongelovigen gelijken op hem, die schreeuwt naar hetgeen niets hoort, het blijft een roep en een schreeuw. Zij zijn doof, stom en blind, zij begrijpen dus niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,172,'O gij die gelooft, eet van de goede dingen, waarmede Wij u hebben voorzien en dankt Allah, indien gij Hem alleen aanbidt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,173,'Hij heeft u slechts het gestorvene, het bloed, het varkensvlees en datgene, waarover een andere naam, dan die van Allah is uitgeroepen, verboden. Maar hij, die gedwongen is en dit niet wenst en geen overtreder is, op hem rust geen zonde. Want Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,174,'Voorzeker, zij, die datgene verbergen, wat Allah heeft geopenbaard, namelijk het Boek en het voor een geringe prijs verruilen, vullen hun buik met niets, dan Vuur. Allah zal op de Dag der Opstanding niet tot hen spreken, noch zal Hij hen rein achten. Er wacht hun een pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,175,'Zij zijn het, die dwaling in ruil voor leiding hebben aanvaard en straf voor vergiffenis. Hoe groot is hun overmoed tegenover het Vuur!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,176,'Dit komt, omdat Allah het Boek met de Waarheid heeft nedergezonden en voorzeker, zij, die tegen het Boek ingaan, zijn in verregaand verzet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,177,'Het is geen deugd, dat gij uw gezicht naar het Oosten of naar het Westen wendt, maar waarlijke deugd is in hem, die in Allah, de Laatste Dag, de engelen, het Boek en de profeten gelooft en die van zijn vermogen geeft uit liefde voor Hem aan de verwanten, de wezen, de armen, de reiziger, de bedelaars en voor het vrijkopen van slaven en die het gebed onderhoudt en de Zakaat betaalt; verder in degenen, die hun belofte nakomen, wanneer zij een belofte doen en de geduldigen in armoede, in kwellingen en in oorlogstijd; dezen zijn het, die bewezen hebben, waarachtig te zijn en dezen zijn vromen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,178,'O gij die gelooft, vergelding inzake doodslag is u voorgeschreven: de vrije man tegen de vrije man, de slaaf tegen de slaaf en de vrouw tegen de vrouw. Maar, indien iemand kwijtschelding is verleend door zijn broeder, dan moet de eis billijk zijn, en betaling moet hem worden gedaan met goedheid. Dit is verzachting en barmhartigheid van uw Heer. Wie daarna overtreedt, hem wacht een pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,179,'En in vergelding is leven voor u, o mensen van begrip, zodat gij behouden zult worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,180,'Het is u voorgeschreven, dat wanneer de dood tot ��n uwer komt, en hij een vermogen nalaat, hij een testament opmake voor ouders en naaste familieleden, billijkerwijze. Dit is een verplichting voor de godvruchtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,181,'En hij, die het vervalst nadat hij het heeft gehoord, de schuld er van zal gewis op hem rusten, die dat verandert. Waarlijk, Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,182,'Maar hij die vreest, dat degene, die het testament maakte, partijdig werd, of een fout heeft begaan, en die een schikking treft (tussen de belanghebbenden), die zal daarmede geen zonde begaan. Voorzeker, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,183,'O, gij gelovigen, het vasten is u voorgeschreven, zoals het degenen die v��r u waren was voorgeschreven, opdat gij vroom zult zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,184,'Voor een zeker aantal dagen (zult gij vasten) maar wie onder u ziek is, of op reis, vaste een aantal andere dagen - er is een losprijs voor degenen, die niet kunnen vasten - het voeden van een arme. Maar hij, die vrijwillig goed doet, het zal beter voor hem zijn. Het vasten is goed voor u, indien gij het beseft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,185,'De maand Ramadaan is die, waarin de Koran als een richtsnoer voor de mensen werd nedergezonden en als duidelijke bewijzen van leiding en onderscheid. Wie onder u daarom deze maand beleeft, laat hem daarin vasten. Maar wie onder u ziek of op reis is, een aantal andere dagen. Allah wenst gemak voor u en geen ongemak, en opdat gij het aantal zult voltooien en opdat gij Allah´s grootheid zult prijzen, omdat Hij u terecht heeft geleid en opdat gij dankbaar zult zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,186,'En wanneer Mijn dienaren u over Mij vragen, zeg dan: \"Ik ben nabij. Ik verhoor het gebed van de smekeling, wanneer hij Mij aanroept.\" Daarom moeten zij naar Mij luisteren en in Mij geloven, opdat zij geleid zullen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,187,'Het is u veroorloofd, om op de nacht van het vasten tot uw vrouwen in te gaan. Zij zijn een gewaad voor u en gij zijt haar een gewaad. Allah weet, dat gij onrechtvaardig hebt gehandeld tegenover uzelf en heeft Zich met barmhartigheid tot u gewend en u verlichting geschonken. Daarom moogt gij nu tot haar ingaan en betrachten, hetgeen Allah u heeft verordend; en eet en drinkt, totdat bij de dageraad de witte draad zich onderscheidt van de zwarte draad. Voltooit dan het vasten tot het vallen van de avond. En verbreng uw tijd niet met uw vrouwen wanneer u in de Moskee�n ??? houdt. Dit zijn de beperkingen van Allah - dus nadert deze niet. Zo zet Allah zijn geboden uiteen voor de mensen, opdat zij vroom zullen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,188,'En verteert uw rijkdommen niet onder elkander door valse middelen en brengt ze niet naar de rechters, opdat gij een deel der rijkdommen der mensen in zonde kunt verteren, tegen beter weten in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,189,'Zij vragen u betreffende de nieuwe manen. Zeg: \"Zij zijn tijdsaanwijzingen voor de mensen en voor de bedevaart.\" Het is geen deugd, dat gij de huizen binnengaat aan de achterzijde: maar deugdzaamheid is in hem, die Allah vreest. Dus gaat de huizen door de deuren binnen en vreest, Allah, opdat gij zult slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,190,'En strijdt voor de zaak van Allah tegen degenen, die tegen u strijden, maar overschrijdt de grens niet. Voorzeker, Allah heeft de overtreders niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,191,'En doodt hen, waar gij hen ook ontmoet en drijft hen uit, vanwaar zij u hebben uitgedreven; want vervolging is erger dan doden. En bevecht hen niet nabij de heilige Moskee, voordat zij u daarin bevechten. Maar indien zij u bevechten, bevecht hen dan - zo is de vergelding voor de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,192,'Maar als zij ophouden, dan is Allah zeker Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,193,'En bestrijdt hen, totdat er geen vervolging meer is en de godsdienst alleen voor Allah wordt. Maar indien zij (met strijden) ophouden, dan is er geen vijandelijkheid meer toegestaan, behalve tegen de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,194,'De heilige maand voor de heilige maand! Er is (een wet van) vergelding voor alle heilige dingen. Wie daarom agressief tegen u handelt, vergeldt hem naarmate hij tegen u heeft gedaan. En vreest Allah en weet, dat Allah met de godvruchtzgen is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,195,'En besteedt uw bezit voor de zaak van Allah en stort u niet met uw eigen handen in het verderf doch doet goed: voorzeker, Allah heeft hen lief, die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,196,'En voleindigt de Hadj (pilgrimstocht) en Omrah, ter wille van Allah, maar als gij verhinderd zijt, brengt dan het offer, dat gemakkelijk verkrijgbaar is en scheert uw hoofd niet, voordat het offer zijn bestemming heeft bereikt. En wie onder u ziek is of een kwaal in het hoofd heeft, moet een losprijs geven, �f door te vasten, �f door aalmoezen te geven, �f door een offer te brengen. En wanneer gij veilig zijt, moet hij die gebruik maakt van Omrah, tegelijk met de Hadj een offer brengen, dat gemakkelijk verkrijgbaar is. Maar degenen, die geen (offer) kunnen vinden, moeten drie dagen gedurende de bedevaart vasten en zeven dagen, wanneer (men) terugkeert; dit is tien dagen in het geheel. Dit is voor hem, wiens familie niet dicht bij de Heilige Moskee woont. En vreest Allah en weet, dat Allah streng is in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,197,'De maanden der bedevaart zijn bekend, dus, wie besluit ter bedevaart te gaan in deze maanden, bedenke, dat er geen onreine taal, noch enige overtreding, noch enige twist gedurende de bedevaart mag zijn. En wat gij ook aan goeds doet, Allah weet het. En rust u uit met het nodige, maar de beste uitrusting is godsvrucht. En vreest Mij alleen, o mensen van begrip.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,198,'Het is voor u geen zonde, wanneer gij de overvloed van uw Heer zoekt. Maar, wanneer gij van (de berg ) Arafaat weggaat, gedenkt dan Allah te het Sacrale Monument en gedenkt Hem, omdat Hij u heeft geleid, terwijl gij voordien tot de dwalenden behoordet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,199,'Gaat dan voort, vanwaar het volk voortgaat en zoekt vergiffenis van Allah; Voorwaar, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,200,'En wanneer gij uw wijdingen hebt verricht, gedenkt dan Allah, zoals gij uw vaderen gedenkt en zelfs meer dan dat. En er zijn mensen, die zeggen: \"Onze Heer, schenk ons (veel) in deze wereld\", maar voor hen is er geen aandeel in het Hiernamaals.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,201,'Sommigen hunner zeggen: \"Onze Heer, schenk ons het goede in deze wereld, alsook in de komende wereld en bescherm ons voor de marteling van het Vuur.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,202,'Voor dezen zal er een aandeel zijn wegens hetgeen zij hebben verdiend. En Allah is vlug in het verrekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,203,'En gedenkt Allah gedurende het vastgestelde aantal dagen, maar wie na twee dagen zich haast (om te vertrekken) het zal voor hem geen zonde zijn en wie achterblijft, ook voor hem zal het geen zonde zijn. Dit geldt voor hem, die God vreest. Vreest Allah en weet, dat gij voor Hem zult worden verzameld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,204,'En onder de mensen is iemand, wiens spreken over dit leven u zou behagen en hij stelt Allah tot getuige voor wat in zijn hart is en toch is hij de meest twistzieke.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,205,'Wanneer hij gezag heeft, gaat hij in het land rond, om er wanorde te stichten en de oogst en het nageslacht (van de mens) te vernietigen, maar Allah houdt niet van wanorde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,206,'En wanneer er tegen hem wordt gezegd: \"Vrees Allah\", dan spoort de trots hem aan tot verdere zonde. Daarom is de hel goed genoeg voor hem en voorzeker, deze is een kwade rustplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,207,'En onder de mensen is iemand, die zich weggeeft, Allah´s welbehagen zoekende; Allah is goedertieren jegens Zijn dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,208,'O gij die gelooft, komt in volledige overgave en volgt de voetstappen van Satan niet; hij is voorzeker uw verklaarde vijand.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,209,'Maar indien gij uitglijdt nadat de duidelijke tekenen tot u zijn gekomen, weet dan, dat Allah Almachtig, Alwijs is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,210,'Zij wachten op niets anders, dan dat Allah en de engelen in de schaduw der wolken tot hen komen en dat de zaak beslist wordt. En tot Allah worden alle dingen teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,211,'Vraag de kinderen Isra�ls, hoeveel duidelijke tekenen Wij hun hebben gegeven. Maar hij die de gunst van Allah verandert, nadat zij tot hem is gekomen, (wete) dat Allah streng is in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,212,'Het leven dezer wereld is voor de ongelovigen schoonschijnend gemaakt en zij bespotten de gelovigen. Maar de godvrezenden zullen boven hen verheven zijn op de dag der opstanding: Allah schenkt Zijn gaven overvloedig aan wie Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,213,'De mensheid was ��n gemeenschap. Daarna verwekte Allah profeten als brengers van goede tijdingen en als waarschuwers en zond met hen het Boek neder, dat de waarheid bevatte, om onder de mensen te richten over datgene waarin zij verschilden. En niemand verschilde er over, dan degenen aan wie het (Boek) was gegeven, nadat duidelijke tekenen tot hen waren gekomen, - uit afgunst jegens elkander. Dan heeft Allah door Zijn gebod de gelovigen geleid betreffende de waarheid, waarover zij hot oneens waren; en Allah leidt naar het rechte pad, wie Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,214,'Denkt gij dat gij de Hemel zult binnengaan, terwijl cle toestand dergenen, die v��r u gingen, nog niet over u is gekomen? Armoede en tegenslagen kwamen over hen en zij werden hevig geschokt, totdat de boodschapper en de gelovigen met hem zeiden: \"Wanneer komt Allah´s hulp?\" Ja, voorzeker, de hulp van Allah is nabij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,215,'Zij vragen u, wat zij moeten besteden. Zeg hun: \"Welke rijkdom gij ook weggeeft, het moet zijn voor ouders, naaste verwanten, wezen, behoeftigen en reizigers. En welke weldaad gij ook doet - Allah weet het goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,216,'Vechten is u geboden ofschoon gij er afkerig van zijt; maar het kan zijn, dat gij tegenzin hebt in iets terwijl het goed voor u is en het kan zijn, dat u iets behaagt terwijl het slecht voor u is. Allah weet het en gij weet het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,217,'Zij vragen u omtrent het vechten in de heilige maand. Zeg: \"Het vechten hierin is een grote overtreding, maar de mensen van de weg van Allah af te houden en Hem ondankbaar te zijn en (de toegang tot) de Heilige Moskee (te verhinderen) en haar mensen er van te verdrijven, is bij Allah een grotere zonde; en vervolging is erger dan doden.\" En zij zullen niet ophouden, u te bevechten, totdat zij u van uw geloof hebben afgebracht, als zij kunnen. Maar wie onder u zich van zijn geloof afkeert en sterft als een ongelovige - diens werken zullen tevergeefs zijn in deze wereld en in de toekomende. Dezulken zijn de bewoners van het Vuur en zij zullen daarin verblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,218,'Zij, die geloven en zij die voor de zaak van Allah hun land verlaten en er voor ijveren, zijn het, die Allah´s barmhartigheid verwachten en Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,219,'Zij vragen u omtrent wijn en kansspel. Zeg hun: \"In beide is groot nadeel en ook enig voordeel voor de mensen, maar het nadeel is groter dan het voordeel.\" En zij vragen u, wat zij moeten weggeven. Zeg hun: \"Hetgeen gij kunt missen.\" Zo maakt Allah u Zijn geboden duidelijk, opdat gij over deze en de volgende wereld zult nadenken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,220,'En zij vragen u omtrent de wezen. Zeg hun: \"De bevordering van hun welzijn is een goede daad. En als gij met hen omgaat zijn zij uw broeders. En Allah weet de kwaadstichters van de vredestichters te onderscheiden. En indien Allah het had gewild, zou Hij het u moeilijk hebben gemaakt. Voorzeker, Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,221,'En huwt geen afgodendienaressen voordat zij geloven; waarlijk een gelovige slavin is beter, dan een afgodendienares, ofschoon zij u moge behagen. En huwt haar (gelovige vrouwen) niet aan afgodendienaren uit, voordat zij geloven; waarlijk een gelovige slaaf is beter, dan een afgodendienaar, ofschoon hij u moge behagen. Zij noden tot het Vuur, maar Allah noodt u tot de Hemel en tot vergiffenis door Zijn gebod. En Hij maakt Zijn tekenen aan de mensen duidelijk, opdat zij lering zullen trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,222,'En zij vragen u omtrent de menstruatie. Zeg (hun): \"Het is iets schadelijks, blijft dus gedurende de menstruatie van de vrouwen weg en gaat niet tot haar in, voordat zij hersteld zijn. Maar wanneer zij zich hebben gereinigd, gaat tot haar in, zoals Allah het u heeft bevolen. Allah bemint hen, die zich tot Hem wenden en zich rein houden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,223,'Uw vrouwen zijn een akker voor u - komt daarom tot uw akker, zoals het u behaagt en doet goed voor uzelf en vreest Allah en weet, dat gij Hem zult ontmoeten en geef goede tijdingen aan de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,224,'En verschuilt u niet achter Allah met uw eden om u te onthouden van het goeddoen en het rechtvaardig handelen en het stichten van vrede tussen de mensen. Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,225,'Allah zal u niet ter verantwoording roepen voor uw ijdele eden, maar Hij zal u ter verantwoording roepen voor hetgeen uw hart heeft verdiend. Allah is Vergevensgezind, Verdraagzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,226,'Voor hen, die onthouding zweren jegens hun vrouwen is de wachtperiode vier maanden; als zij echter ervan terugkomen, voorzeker, dan is Allah Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,227,'En indien zij besluiten tot echtscheiding voorzeker Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,228,'De gescheiden vrouwen moeten drie menstruatieperioden wachten; en het is haar niet geoorloofd, hetgeen Allah in haar baarmoeder heeft geschapen, te verbergen, indien zij in Allah en de laatste dag geloven; en haar echtgenoten hebben het recht, haar (intussen) terug te nemen, indien zij verzoening wensen. En v��r haar geldt hetzelfde als tegen haar, hetgeen billijk is, de mannen hebben voorrang boven haar, Allah is Machtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,229,'Is de echtscheiding twee keer geschied, behoud haar dan op behoorlijke wijze of zend haar met vriendelijkheid weg. En het is u niet geoorloofd, iets te nemen van hetgeen gij haar hebt gegeven, tenzij beiden vrezen, Allah´s bepalingen niet in acht te kunnen nemen. Indien gij (familieleden) vreest, dat zij Allah´s bepalingen niet in acht kunnen nemen, dan zal er voor geen van hen beiden zonde zijn in hetgeen zij teruggeeft om daardoor vrij te worden. Dit zijn de door Allah voorgeschreven beperkingen, overschrijdt ze daarom niet; wie de door Allah voorgeschreven grenzen overschrijden, zijn overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,230,'Indien hij van haar (ten derden male) scheidt, is zij voor hem niet meer geoorloofd, voordat ze een andere echtgenoot heeft gehuwd en indien deze van haar scheidt, zal het voor hen geen zonde zijn, tot elkander terug te keren, indien zij er van overtuigd zijn, dat zij de door Allah voorgeschreven beperkingen in acht zullen nemen. Dit zijn Allah´s bepalingen, welke Hij aan de mensen, die kennis hebben duidelijk maakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,231,'En wanneer gij van uw vrouwen scheidt en zij het einde van de haar voorgeschreven periode bereiken, behoudt haar dan op een behoorlijke manier, of zendt haar op een betamelijke manier weg, maar behoudt haar niet tot haar nadeel, waardoor gij de perken te buiten gaat. Wie zulks doet, doet gewis zijn eigen ziel onrecht. En drijft niet de spot met Allah´s geboden en gedenkt Allah´s gunst aan u en (gedenkt) het Boek en de wijsheid, die Hij u heeft nedergezonden, waarmede Hij u vermaant. En vreest Allah en weet, dat Allah de Kenner is van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,232,'En wanneer gij van vrouwen scheidt en zij het einde van haar wachtperiode hebben bereikt, verhindert haar niet, haar (aanstaande) man te huwen, als zij met elkander op de gebruikelijke wijze tot overeenstemming zijn gekomen. Dit is een vermaning voor hem, die onder u in Allah en de laatste dag gelooft. Het is beter en reiner voor u; Allah weet en gij niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,233,'Moeders (gescheiden vrouwen) zullen haar kinderen twee volle jaren zogen, dit is voor hen, die de zoogtijd wensen te voltooien. En op de vader rust de zorg voor voedsel en kleding voor haar volgens gebruik. Geen ziel wordt belast boven haar vermogen. De moeder zal geen leed worden aangedaan wegens haar kind, noch zal de vader leed worden aangedaan wegens zijn kind en hetzelfde geldt voor de erfgenaam. Als beiden besluiten, het kind te spenen door wederzijdse overeenkomst en overleg, rust er geen schuld op hen. En als gij verkiest, een min voor uw kinderen te nemen, zal er geen blaam op u rusten, mits gij hetgeen gij overeenkomt naar billijkheid betaalt. En vreest Allan en weet, dat Allah ziet, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,234,'En diegenen uwer, die sterven en vrouwen achterlaten, (hun vrouwen) moeten vier maanden en tien dagen wachten. Wanneer zij het einde der wachtperiode hebben bereikt, zal er op u geen zonde rusten voor hetgeen zij voor zichzelf op behoorlijke wijze doen; Allah weet, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,235,'En er zal geen schuld op u rusten, indien gii niet rechtstreeks spreekt over een huwelijksaanzoek aan die vrouwen, of indien gij dit in uw gedachten verborgen houdt. Allah weet, dat gij het haar zult zeggen. Maar belooft haar niets in het geheim tenzij gij op de goede wijze spreekt. En besluit niet tot de huwelijksband, voordat de voorgeschreven wachttijd ten einde is. En weet, dat Allah weet, wat in uw gedachten is en vreest derhalve voor Hem en weet, dat Allah Vergevensgezind, Verdraagzaam is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,236,'Het zal voor u geen zonde zijn, indien gij van uw vrouw scheidt, voordat gij haar hebt benaderd of voor haar een bruidsschat hebt vastgesteld. Maar maakt een voorziening voor haar, de rijke naar zijn middelen en de arme naar zijn middelen, een gebruikelijke voorziening - dit is een verplichting voor de deugdzamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,237,'En indien gij van haar scheidt, voor gij haar hebt benaderd maar haar een bruidsschat hebt toegekend, (geeft) dan de helft van hetgeen gij hebt vastgesteld, tenzij zij het u kwijtschelden, of degene, die de huwelijksband in handen heeft het u zou kwijtschelden. En, indien gij kwijtscheldt is dit dichter bij de godsvrucht. En vergeet niet, elkander goed te doen. Voorzeker, Allah ziet, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,238,'Waakt over uw gebeden en het tussengebed en stelt u ootmoedig voor Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,239,'Als gij in gevaar verkeert, bidt dan lopende of rijdende, maar wanneer gij veilig zijt, gedenkt dan Allah, zoals Hij u heeft geleerd, wat gij niet wist.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,240,'En degenen uwer, die wanneer zij sterven vrouwen achterlaten, moeten voor hun vrouwen een testament maken voor hun levensonderhoud gedurende ��n jaar, zonder dat zij worden uitgezet. Doch indien zij weggaan zal er geen schuld op u rusten, wegens datgene, wat zij omtrent zichzelf op behoorlijke wijze doen. En Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,241,'En er moet voor de gescheiden vrouwen een billijke voorziening zijn, dit is een verplichting voor de godvruchtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,242,'Zo zet Allah Zijn geboden uiteen, opdat gij zult begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,243,'Weet gij niet van degenen, die uit angst voor de dood hun huizen verlieten - het waren er duizenden. Allah zeide tot hen: \"Sterft\" en dan schonk Hij hun leven. Voorzeker, Allah is genadig jegens de mensen, maar de meeste mensen zijn ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,244,'Strijdt voor de zaak van Allah en weet, dat Allah Alhorend, Alwetend is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,245,'Wie aan Allah het goede deel afstaat, Hij zal het voor hem vele malen vermenigvuldigen en Allah vermindert en vermeerdert en tot Hem zult gij worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,246,'Weet gij niet van de leiders der kinderen Isra�ls na Mozes, toen zij tot ��n hunner profeten zeiden: \"Stel ons een koning aan, opdat wij ter wille van Allah kunnen strijden.\" Hij zeide: \"Is het niet waarschijnlijk, dat gij niet zult willen vechten, wanneer het u wordt voorgeschreven?\" Zij zeiden: \"Welke reden hebben wij om ons van het vechten voor Allah´s zaak te willen onthouden, wanneer wij van onze huizen en onze kinderen zijn verdreven?\" Maar, toen het vechten hun werd bevolen, wendden zij zich af, met uitzondering van een klein aantal hunner; Allah kent de overtreders goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,247,'En hun profeet zeide tot hen: \"Waarlijk, Allah heeft Taloet (Saul) als koning over u aangesteld.\" Zij zeiden: \"Hoe kan hij over ons regeren, terwijl wij meer recht op heerschappij hebben dan hij en hem geen overvloed van rijkdommen is gegeven?\" Hij zeide: \"Voorzeker, Allah heeft hem boven u gekozen en heeft hem overvloedig toegerust met kennis en kracht.\" En Allah geeft Zijn heerschappij aan wie Hij wil en Allah is Milddadig, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,248,'En hun profeet zeide tot hen: \"Het teken van zijn heerschappij is, dat u een hart zal worden gegeven, waarin de kalmte van uw Heer zal zijn, het beste van de nalatenschap der volgelingen van Mozes en der volgelingen van A�ron, (een hart) door de engelen gebracht. Voorzeker, hierin is voor u een teken, als gij gelovigen zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,249,'En toen Taloet met de strijdkrachten uitrukte, zeide hij: \"Voorzeker, Allah zal u door een stroom beproeven: dus hij die er van drinkt, is niet met mij, behalve wanneer hij maar een handvol neemt, en hij die er niets van neemt, is zeker met mij.\" Maar behoudens enigen hunner dronken zij er van. En toen zij de rivier overstaken, hij en de gelovigen met hem - zeiden zij: \"Wij hebben vandaag geen macht over Djaloet (Goliath) en zijn strijdkrachten.\" Maar zij, die er zeker van waren, dat zij Allah zouden ontmoeten, zeiden: \"Hoevele kleine groepen hebben niet onder Allah´s bevel over een grote groep gezegevierd.\" En Allah is met de geduldigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,250,'En toen zij uitgingen om Djaloet en zijn strijdkrachten te ontmoeten, zeiden zij: \"Onze Heer, stort geduld over ons uit en maak onze voetstappen vast en help ons tegen het ongelovige volk!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,251,'Zo versloegen zij hen door het gebod van Allah en David doodde Djaloet en Allah gaf hem heerschappij en wijsheid en onderwees hem, hetgeen Hij wilde. Had Allah sommige mensen niet door anderen laten terugdrijven, dan zou de aarde verdorven zijn. Maar Allah is genadig jegens de werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,252,'Dit zijn de tekenen van Allah. Wij dragen ze u voor naar waarheid. Voorzeker, gij zijt ��n der boodschappers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,253,'Van deze boodschappers hebbell wij sommigen boven anderen verheven; tot sommigen hunner sprak Allah en sommigen hunner verhief Hij in rang. En Wij gaven Jezus, zoon van Maria duidelijke tekenen en versterkten hem met de geest der heiligheid. En indien Allah wilde, zouden zij, die na hem kwamen, elkander niet hebben bestreden, nadat de duidelijke tekenen tot hen waren gekomen, maar zij twistten, daar sommigen hunner geloofden en anderen verwierpen. En indien Allah wilde, zouden zij elkander niet hebben bestreden, maar Allah doet, wat Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,254,'O, gij die gelooft, geeft van hetgeen Wij u hebben geschonken, voordat de dag komt, waarop noch handel, noch vriendschap, noch voorspraak zal zijn; en de ongelovigen zijn de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,255,'Allah! Er is geen God dan Hij, de Levende, de Zelfbestaande. Sluimer, noch slaap overmant Hem. Al wat in de hemelen en wat op aarde is, behoort Hem. Wie kan bij Hem bemiddelen zonder Zijn verlof? Hij kent hetgeen voor hen is en wat achter hen is en zij kunnen niets van Zijn kennis omvatten, dan wat Hij wil. Zijn troon strekt zich uit over hemelen en aarde en het waken over beide vermoeit Hem niet; Hij is de Verhevene, de Grote.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,256,'Er is geen dwang in de godsdienst. Voorzeker, het juiste pad is van dwaling onderscheiden; derhalve, hij die de duivel verloochent en in Allah gelooft, heeft een sterk houvast gegrepen, dat onbreekbaar is. Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,257,'Allah is de Vriend dergenen, die geloven; Hij brengt hen uit de duisternis tot het licht. Maar de vrienden der ongelovigen zijn de duivelen, zij brengen hen uit het licht in de duisternis; dezen zijn de bewoners van het vuur, daarin zullen zij wonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,258,'Hebt gij niet vernomen van hem, die met Abraham over zijn Heer redetwistte, omdat Allah hem het koninkrijk had gegeven? Toen Abraham zeide: \"Mijn Heer is Hij, die het leven geeft en doet sterven\", zeide hij: \"Ik geef leven en doe sterven.\" Abraham zeide: \"Nu, Allah doet de zon van het Oosten opgaan, doet gij haar van het Westen opgaan.\" Daarop verstomde de ongelovige in verbazing. En Allah leidt het onrechtvaardige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,259,'Of, gelijk degene, die langs een stad komende, welke was ingestort, uitriep: \"Hoe zal Allah haar doen herleven na haar vernietiging?\" Toen deed Allah hem sterven voor honderd jaren; daarna wekte Hij hem op en zeide: \"Hoelang zijt gij hier reeds?\" Hij antwoordde: \"Ik ben een dag, of een gedeelte van een dag gebleven.\" Hij zeide: \"Neen, gij zijt honderd jaren gebleven. Kijk nu naar uw voedsel en uw drank; zij zijn niet bedorven. En kijk naar uw ezel; (dit is) opdat Wij u tot een teken voor de mensen maken. En kijk naar de beenderen, hoe Wij ze in elkaar zetten en ze daarna met vlees bekleden.\" En toen hem dit duidelijk werd zeide hij: \"Ik weet, dat Allah macht heeft over alle dingen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,260,'En toen Abraham zeide: \"Mijn Heer, toon mij, hoe Gij de doden tot leven opwekt.\" Hij zeide: \"Gelooft gij dan niet?\" Hij zeide: \"Ja, maar opdat mijn hart rustig zij.\" Hij antwoordde: \"Neem vier vogels en maak ze aan u gehecht. Zet dan ieder hunner op een heuvel; roep hen dan; ze zullen haastig tot u komen. En weet, dat Allah Almachtig, Alwijs is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,261,'De gelijkenis van degenen, die hun rijkdommen voor de zaak van Allah besteden, is als de gelijkenis van een graankorrel, die zeven aren voortbrengt, in elke aar honderd korrels. Allah vermeerdert voor wie Hij wil; Allah is Alomvattend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,262,'Zij, die hun rijkdommen ter wille van Allah besteden, en het besteden niet doen volgen door (anderen) te verwijten of te krenken, voor hen is er beloning bij hun Heer en zij zullen geen vrees hebben, noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,263,'Een vriendelijk woord en vergiffenis schenken is beter, dan liefdadigheid, gevolgd door krenking. En Allah is Zichzelf genoeg, Verdraagzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,264,'O, gij die gelooft, maakt uw aalmoezen niet waardeloos door verwijt of krenking, zoals hij, die zijn rijkdommen weggeeft, om op te vallen bij de mensen en hij gelooft niet in Allah en de laatste dag. Hij is als een gladde rots, die met aarde is bedekt, waarop een stortregen valt, welke haar kaal achterlaat. Zij hebben geen macht over wat zij verdienen. En Allah leidt het ongelovige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,265,'En de gelijkenis van degenen, die hun rijkdommen weggeven, Allah´s welbehagen zoekende en hun ziel versterkende, is als een tuin op hooggelegen grond, die bij regen tweevoudig vruchten voortbrengt. En als er geen regen op valt, dan is dauw voldoende. Allah ziet, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,266,'Zou iemand uwer wensen dat er voor hem een tuin was met palmbomen en wijnstokken waardoor beken vloeien en waarin voor hem allerlei vruchten groeien, terwijl hij oud is en een zwak nakomelingschap heeft, en dat hem (de tuin) een vurige wervelwind treft en hem verschroeit? Zo zet Allah u Zijn woorden uiteen, op dat gij tot nadenken zult komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,267,'O, gij die gelooft, geeft van de goede dingen weg, die gij hebt verdiend en van hetgeen Wij voor u uit de aarde voortbrengen en zoekt niet hetgeen slecht is, om er van weg te geven, wanneer gij het zelf niet zoudt nemen, tenzij oogluikend; en weet, dat Allah Zichzelf-genoeg, Geprezen is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,268,'Satan dreigt u met armoede en gelast u hetgeen slecht is, terwijl Allah uit Zichzelf u vergiffenis en overvloed belooft; en Allah is Overvloedig-gevend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,269,'Hij schenkt wijsheid aan wie Hij wil en wie wijsheid is geschonken is inderdaad overvloedig begiftigd en niemand trekt er lering uit, behalve zij, die begrip hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,270,'En alles wat gij geeft en elke gelofte, die gij aflegt, voorzeker Allah weet het; er is geen hulp voor de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,271,'Als gij openlijk aalmoezen geeft is het goed, maar als gij dit in stilte doet en aan de armen geeft is het beter voor u en Hij zal de fouten van u wegnemen. En Allah weet, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,272,'Hen te leiden is niet uw plicht, maar Allah leidt wie Hij wil. En welke rijkdommen gij ook weggeeft, het komt u ten goede en gij geeft alleen om Allah´s welbehagen te zoeken. En welke rijkdommen gij ook besteedt, het zal u ten volle worden terugbetaald en u zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,273,'(Aalmoezen zijn) voor de armen, die gebonden zijn (door hun dienst) aan Allah, en in het land niet kunnen rondtrekken. De onwetende beschouwt hen als rijken wegens hun hescheidenheid. Gij zult hen aan hun tekenen herkennen, daar zij niet op een opdringerige wijze bij de mensen vragen. En welke rijkdommen gij ook besteedt, voorzeker, Allah weet het goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,274,'Zij, die hun rijkdommen nacht en dag, heimelijk of openlijk weggeven, ontvangen hun beloning van hun Heer; zij zullen niet vrezen, noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,275,'Degenen, die woekerwinst maken, verrijzen zoals iemand, die door Satan met krankzinnigheid is geslagen. Dat komt, omdat zij zeggen: \"Handel is gelijk aan rente\", terwijl Allah de heeft wettig en de rente onwettig heeft verklaard. Die daarom een vermaning van zijn Heer krijgt en er mee ophoudt, hem zal toebehoren, hetgeen hij vroeger heeft ontvangen en zijn zaak is bij Allah. En zij, die terugvallen, zij zijn de mensen van het Vuur, daarin zullen zij vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,276,'Allah schaft de rente af en doet de weldadigheid toenemen. En Allah heeft niet lief alle ondankbaren en zondaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,277,'Voorzeker, zij die geloven en goede daden doen en het gebed houden en de Zakaat betalen, hun beloning is bij hun Heer en voor hen is geen vrees, noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,278,'O, gij die gelooft, vreest Allah en doet afstand van de rest van de rente, als gij gelovigen zijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,279,'Maar indien gij dit niet doet, bereidt u dan ten oorlog met Allah en Zijn boodschapper; indien gij berouw hebt is voor u het oorspronkelijke kapitaal: zo zult gij geen onrecht doen, noch zal u onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,280,'En indien iemand in verlegenheid is, laat er dan uitstel zijn tot het hem past. En wanneer gij kwijtscheldt is het beter voor u; wist gij het slechts.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,281,'En vreest de dag, waarop gij tot Allah zult worden teruggebracht; dan zal aan elke ziel ten volle worden betaald hetgeen zij heeft verdiend; en onrecht zal hen niet worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,282,'O, gij die gelooft, wanneer gij van elkander leent voor een vastgestelde periode, schrijft het dan op. Laat een schrijver het naar waarheid in uw bijzijn optekenen en geen schrijver moet weigeren, te schrijven, zoals Allah hem heeft onderwezen; laat hem daarom schrijven en laat de schuldenaar dicteren en hij moet Allah, zijn Heer vrezen en niets daaraan afdoen. Maar, indien de schuldenaar weinig verstand heeft, of zwak is, of zelf niet kan dicteren, laat dan zijn zaakwaarnemer eerlijk dicteren. En roept van onder uw mannen twee getuigen en als er geen twee mannen zijn, dan ��n man en twee vrouwen van degenen, die u als getuigen aanstaan, zodat, wanneer ��n der twee vrouwen zich zou vergissen, de ene de andere indachtig moge maken. En de getuigen mogen niet weigeren, wanneer zij worden gedaagd. En wordt het schrijven niet moe, of het weinig of veel zij, betreffende de vervaltijd. Dit is in Allah´s ogen eerder rechtvaardig, het maakt het getuigenis zekerder en weerhoudt u van twijfel. Maar wanneer het contante handel is, die gij onderling drijft, zal het geen blaam voor u zijn, als gij het niet neerschrijft. En hebt getuigen, wanneer gij aan elkander verkoopt en de schrijver en de getuigen mag geen leed worden aangedaan. En indien gij zulks doet, zal het zeker overtreding van u zijn. Vreest Allah. Allah schenkt u kennis en Allah weet alle dingen goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,283,'En indien gij op reis zijt en geen schrijver vindt, laat er dan een onderpand voor worden gegeven. En indien ��n uwer de ander iets toevertrouwt, laat dan degene aan wie het toevertrouwd is, het toevertrouwde teruggeven en laat hem Allah zijn Heer vrezen. Verbergt geen getuigenis; en wie dat wel doet diens hart is zeker zondig en Allah weet goed, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,284,'Aan Allah behoort wat in de hemelen en wat op de aarde is; en indien gij openbaart hetgeen in uw innerlijk is of het verborgen houdt, Allah zal u er rekenschap voor vragen; dan zal Hij vergeven wie Hij wil en straffen, wie Hij wil. Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,285,'Deze boodschapper gelooft in hetgeen hem van zijn Heer is geopenbaard en ook de gelovigen, allen geloven in Allah, Zijn engelen, Zijn boeken en Zijn boodschappers, zeggende: \"Wij maken geen verschil tussen Zijn boodschappers\"; en zij zeggen: \"Wij hebben gehoord en gehoorzaamd, Heer, wij vragen U vergiffenis en tot U is (onze) terugkeer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,2,286,'Allah belast geen ziel boven haar vermogen. Voor haar is wat zij verdient en tegen haar is ook wat zij verdient. \"Onze Heer, straf ons niet als wij vergeten of een fout hebben begaan, Heer, en belast ons niet, zoals Gij degenen, die v��r ons waren hebt belast; onze Heer belast ons niet met datgene, waarvoor wij de kracht niet hebben (het te dragen), wis onze fouten uit en schenk ons vergiffenis en wees ons barmhartig; Gij zijt onze Meester, help ons daarom tegen het ongelovige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,1,'Alif Laam Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,2,'Allah! Er is geen God, dan Hij, de Levende, de Zelfbestaande.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,3,'Hij heeft u het Boek met de waarheid nedergezonden, vervullende, hetgeen er aan voorafgaat en Hij zond voordien de Torah en het Evangelie als leiding voor het volk en Hij heeft het Verschil geopenbaard.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,4,'Voorzeker, zij, die de tekenen van Allah verwerpen, zullen een strenge straf ontvangen; Allah is machtig, de Heer der Vergelding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,5,'Voorzeker, er is niets op aarde of in de hemelen voor Allah verborgern.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,6,'Hij is het, Die u in de baarmoeder vormt zoals Hij wil; er is geen God dan Hij, de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,7,'Hij is het, Die u het Boek heeft nedergezonden; er zijn verzen in, die onoverdrachtelijk zijn, zij vormen de grondslag van het Boek, en er zijn andere (verzen), die zinnebeeldig zijn. Maar degenen in wier hart dwaling is, volgen die, welke zinnebeeldig (bedoeld) zijn en zoeken tweedracht en de verkeerde uitleg. En niemand kent de juiste uitleg dan Allah en degenen, die vast gegrondvest zijn in kennis, die zeggen: \"Wij geloven er in; het geheel is van onze Heer\"; en niemand trekt er lering uit, dan zij, die begrip hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,8,'\"Onze Heer, laat ons hart niet afdwalen nadat Gij ons hebt geleid en schenk ons Uw barmhartigheid; waarlijk, Gij zijt de Milddadige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,9,'Onze Heer, Gij zijt het, Die de mensen zal verzamelen op de Dag, waaraan geen twijfel is; voorzeker, Allah breekt de belofte niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,10,'Voorzeker zullen de bezittingen en kinderen der ongelovigen hun tegen Allah in het geheel niet baten: dezen zullen brandstof voor het Vuur zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,11,'Op de wijze van Pharao´s volk en degenen, die v��r hen waren. Zij verloochenden Onze tekenen, dus strafte Allah hen voor hun zonden; Allah is streng in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,12,'Zeg tot de ongelovigen: \"Gij zult worden terneergeslagen en in de hel worden verzameld, dit is een kwade rustplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,13,'Voorzeker was er voor u een teken in de twee legers die elkander ontmoetten, het ene leger vechtend voor de zaak van Allah en het andere ongelovig, dezen zagen de anderen voor hun eigen ogen dubbel zo talrijk als zijzelf. En Allah versterkt met Zijn hulp, wie Hij wil. Daarin is zeker een les voor hen, die ogen hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,14,'Voor de mensen is de liefde tot begeerten schoonschijnend gemaakt, vrouwen, kinderen, stapels goud en zilver, raspaarden, vee en akkers. Dat is de voorziening van het leven dezer wereld, maar Allah is het, bij Wie het juiste einddoel ligt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,15,'Zeg: \"Zal ik u over iets beters inlichten dan over dit alles?\" Voor degenen, die God vrezen, zijn er tuinen bij hun Heer, waar doorheen rivieren stromen; daar zullen zij vertoeven en voor hen zijn reine metgezellen, alsmede Allah´s welbehagen. En Allah ziet Zijn dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,16,'Hen die zeggen: \"Onze Heer, voorzeker hebben wij geloofd, vergeef ons daarom onze zonden en red ons van de straf van het Vuur.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,17,'En de geduldigen, de waarachtigen, de gehoorzamen en zij die wel doen en zij die vergiffenis vragen in de morgenstond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,18,'Allah getuigt, dat er geen God is dan Hij en de engelen en degenen, die kennis bezitten, getuigen dit eveneens, handhavende de rechtvaardigheid: er is geen God dan Hij, de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,19,'Gewis, de ware godsdienst voor Allah is de Islam. En degenen, aan wie het Boek was gegeven, verschilden eerst onderling uit afgunst, nadat kennis tot hen was gekomen. En wie de tekenen van Allah verwerpt, (wete) dat Allah vlug is in het verrekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,20,'En zeg wanneer zij met u redetwisten: \"Ik, en degenen die mij volgen hebben zich aan Allah onderworpen.\" En zeg tot degenen aan wie het Boek is gegeven en tot de onwetenden: \"Hebt gij u onderworpen?\" Als zij zich onderwerpen, dan zijn zij op de rechte weg, maar indien zij zich afwenden, dan is uw plicht slechts de duidelijke verkondiging ervan; en Allah ziet zijn dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,21,'Voorzeker, degenen, die de tekenen van Allah verwerpen en de profeten ten onrechte doden en ook trachten de mensen te doden welke tot rechtvaardigheid aanmanen, verkondig hun een pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,22,'Dezen zijn het wier daden in deze wereld en voor het Hiernamaals verloren zijn gegaan; er zal geen hulp zijn voor hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,23,'Kent gij niet degenen, aan wie een gedeelte van het Boek werd gegeven? Zij worden tot het Boek van Allah geroepen, opdat het onder hen rechter zij; dan wendt zich een gedeelte hunner af terwijl zij afkerig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,24,'Dat komt, doordat zij zeggen: \"Het Vuur zal ons slechts voor een luttel aantal dagen deren.\" En wat zij plachten te verzinnen, heeft hen in hun godsdienst bedrogen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,25,'Hoe zal het dan zijn, wanneer Wij hen verzamelen op de Dag, waarover geen twijfel bestaat en waarop elke ziel voor hetgeen zij verdient ten volle zal worden betaald en hun geen onrecht zal worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,26,'Zeg: \"O, Allah, Heer van het Koninkrijk, Gij geeft heerschappij aan wie Gij wilt en neemt terug van wie Gij wilt. Gij verheft, wie Gij wilt en vernedert, wie Gij wilt. Slechts in Uw hand is het goede. En Gij hebt macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,27,'Gij doet de nacht in de dag overgaan en de dag in de nacht. En Gij brengt het levende uit het dode voort en Gij brengt het dode uit het levende voort. En Gij geeft onbeperkt aan wie Gij wilt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,28,'Laat de gelovigen geen ongelovigen als vrienden verkiezen boven de gelovigen - en wie dat doet heeft geen deel aan Allah, tenzij gij u zorgvuldig voor hen hoedt. En Allah waarschuwt u voor Hemzelf en tot Allah zullen allen wederkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,29,'Zeg: \"Of gij dat wat in uw hart is verbergt of onthult, Allah weet het en Hij weet wat in de hemelen en op aarde is. Allah heeft de macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,30,'(Gedenkt) de Dag, waarop iedere ziel zich geplaatst zal vinden tegenover het goede dat zij heeft verricht en het kwade dat zij heeft gedaan, dan zal zij wensen dat er een grote afstand ware tussen haar en het kwade. En Allah waarschuwt u voor Hemzelf. En Allah is liefderijk jegens Zijn dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,31,'Zeg: \"Indien gij Allah liefhebt, volgt mij, Allah zal u liefhebben en uw zonden vergeven. Allah is Vergevensgezind, Genadig.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,32,'Zeg: \"Gehoorzaamt Allah en de boodschapper\", maar als zij zich afwenden, dan heeft Allah de ongelovigen niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,33,'Allah verkoos Adam en Noach en de nakomelingen van Abraham en de nakomelingen van Imraan boven de volkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,34,'Afstammelingen, de een van de ander. En Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,35,'Toen de vrouw van Imraan zeide: \"Ik draag aan U op wat in mijn baarmoeder is, dat het vrij zal zijn (om U te dienen), aanvaard het van mij, Gij zijt gewis Alhorend, Alwetend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,36,'Maar, toen zij er van verlost was, zeide zij: \"Mijn Heer, ik ben verlost van een meisje.\" - Allah wist het beste wat zij voortbracht. \"En de man is niet gelijk aan de vrouw. En ik heb haar Maria genoemd en ik stel haar en haar nageslacht onder Uw bescherming tegen Satan, de verworpene.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,37,'Daarom nam haar Heer haar (Maria) met welbehagen aan en deed haar goed opgroeien en vertrouwde haar aan Zacharia toe. Telkens, wanneer Zacharia bij haar in de kamer ging, vond hij voedsel bij haar. Hij zeide: \"O, Maria, waar hebt gij dit vandaan?\" Zij antwoordde: \"Het komt van Allah.\" Voorzeker, Allah geeft volop aan wie Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,38,'Toen bad Zacharia tot zijn Heer: \"Mijn Heer geef mij een rein nageslacht; voorzeker, Gij verhoort het gebed.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,39,'En de engelen riepen tot hem, terwijl hij in de kamer stond te bidden: \"Allah geeft u de blijde tijding over Johannes, die Allah´s woord zal vervullen - en hij zal edel, kuis en een profeet onder de rechtvaardigen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,40,'Hij zeide: \"Heer, hoe zal er een zoon voor mij zijn, waar ouderdom al over mij gekomen en mijn vrouw onvruchtbaar is?\" Hij antwoordde: \"Zo doet Allah, wat Hij wil.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,41,'Hij zeide: \"Heer, geef mij een teken.\" Hij antwoordde: \"Uw teken zal zijn, dat gij drie dagen slechts door gebaar tot de mensen zult spreken. Gedenk uw Heer vaak en verheerlijk Hem ´s avonds en ´s morgens.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,42,'Toen zeiden de engelen: \"O, Maria, Allah heeft u uitverkoren en u gereinigd en u boven de vrouwen aller vollkeren uitverkoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,43,'\"O, Maria, wees uw Heer gehoorzaam en werp u neder en aanbid met degenen, die aanbidden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,44,'Dit is een van de tijdingen van het ongeziene, die wij u openbaren. En gij waart niet bij hen toen zij lootten (om te zien), wie hunner de voogd van Maria zou zijn, noch waart gij bij hen, toen zij met elkander redetwistten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,45,'Toen de engelen zeiden: \"O, Maria, waarlijk, Allah geeft u blijde tijding door Zijn woord: Zijn naam zal zijn: de Messias, Jezus, zoon van Maria, ge�erd in deze wereld en in de volgende en hij zal tot hen behoren die in Gods nabijheid zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,46,'En hij zal tot het volk spreken in de wieg en op middelbare leeftijd en hij zal ��n der rechtvaardigen zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,47,'Zij zeide: \"Heer, hoe zal ik een zoon hebben, daar geen man mij heeft benaderd?\" Hij zeide: \"Zo schept Allah, wat Hij wil. Wanneer Hij iets beslist, zegt Hij daartoe slechts: \"Wees\" en het wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,48,'\"En Hij zal hem het Boek (de goddelijke Wet) en de Wijsheid en de Torah en het Evangelie onderwijzen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,49,'En hij zal een boodschapper voor de kinderen Isra�ls zijn. \"Ik kom tot u met een teken van uw Heer; ik zal u uit klei de vorm van een vogel maken, dan adem ik daarin en hij zal een vogel worden, door Allah´s gebod. En ik genees de blinden en de melaatsen en doe de doden herleven en ik deel u mede, wat gij zult eten en wat gij in uw huizen zult opslaan. Voorzeker, daarin is voor u een teken, indien gij gelovigen zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,50,'Ik kom tot u met een teken van uw Heer bevestigende wat v��r mij was, namelijk, de Torah en om u iets, van wat u was verboden toe te staan; vreest daarom Allah en gehoorzaamt mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,51,'Voorzeker, Allah is mijn Heer en uw Heer; aanbidt Hem daarom, dit is het rechte pad.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,52,'Toen Jezus hun (der Isra�lieten) ongeloof bemerkte, zeide hij: \"Wie zullen mijn helpers zijn terwille van Allah?\" De discipelen antwoordden: \"Wij zijn de helpers van Allah. Wij geloven in Allah. En getuigt gij dat wij Moslims zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,53,'\"Onze Heer, wij geloven in hetgeen Gij hebt geopenbaard en volgen deze boodschapper. Schrijf ons onder hen die getuigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,54,'En zij maakten plannen (tegen Jezus). Allah maakte ook plannen (tegen hen), maar Allah voorziet het beste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,55,'Toen Allah zeide: \"O, Jezus, ik zal u doen sterven en u tot Mij; opheffen en u zuiveren van de ongelovigen en zal uw volgelingen tot de laatste dag over hen doen zegevieren die u niet geloven; dan zal uw terugkeer tot Mij zijn en Ik zal onder u rechtspreken over datgeen waarin gij verschildet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,56,'Doch de ongelovigen zal Ik in deze wereld en in de volgende streng straffen en zij zullen geen helpers hebben.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,57,'De gelovigen die goede werken verrichten zal Ik volle beloning toekennen. Maar Allah heeft de onrechtvaardigen niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,58,'Dat is hetgeen Wij u van de tekenen en de wijze vermaning meedelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,59,'Voorzeker, het geval van Jezus is bij Allah hetzelfde als dat van Adam. Hij (Allah) schiep hem uit stof en zeide: \"Wees\" en hij werd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,60,'De waarheid is van uw Heer, behoort daarom niet tot degenen, die twijfelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,61,'Zou men nu met u over hem (Jezus) redetwisten, nadat de kennis tot u gekomen is, zeg dan: \"Kom, laat ons onze kinderen en uw kinderen en onze vrouwen en uw vrouwen en ons volk en uw volk roepen; laat ons daarna vurig bidden en de vloek van Allah roepen over degenen, die liegen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,62,'Dit is voorzeker de ware uitleg, en er is geen God dan Allah en waarlijk, Hij is de Almachtige, de Alwijze. v 63. Doch indien zij zich afwenden, Allah kent de onheilstichters toch goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,63,'64. Zeg: \"O, mensen van het Boek, komt tot ��n woord, waarin wij met elkander overeenstemmen: dat wij niemand dan Allah aanbidden en dat wij niets met Hem vereenzelvigen en dat sommigen onzer geen anderen tot goden nemen, buiten Allah.\" Maar, als zij zich afwenden, zegt dan: \"Getuigt, dat wij Moslims zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,64,'65. O, mensen van het Boek, waarom redetwist gij over Abraham, wanneer de Torah en het Evangelie eerst na hem werden geopenbaard? Wilt gij dan niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,65,'66. Ziet, gij twist over hetgeen, waarvan gij kennis hebt. Waarom twist gij dan (eveneens) over hetgeen, waarvan gij geen kennis hebt? Allah weet en gij weet niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,66,'67. Abraham was noch een Jood, noch een Christen, maar hij was een oprecht Moslim. En hij behoorde niet tot de afgodendienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,67,'68. Voorzeker, zij die Abraham het dichtst nabijkomen, zijn degenen, die hem volgen; en deze profeet en de gelovigen; en Allah is de Vriend der gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,68,'69. Een deel der mensen van het Boek zou u gaarne willen doen dwalen, maar zij doen niemand dwalen dan zichzelf; en zij beseffen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,69,'70. O, mensen van het Boek, waarom verwerpt gij de tekenen van Allah terwijl gij er getuige van zijt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,70,'71. O, mensen van het Boek, waarom verwart gij de waarheid met de leugen en verbergt de waarheid tegen beter weten in?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,71,'72. En een gedeelte der mensen van het Boek zegt: \"Gelooft in hetgeen de gelovigen (Moslirns) is geopenbaard, in de vroege ochtendstond en verwerpt het aan het einde van de dag; misschien keren zij wel terug.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,72,'73. \"En gelooft niet, behalve in hem, die uw godsdienst belijdt. - Zeg: \"Voorzeker, de ware leiding is Allah´s leiding - dat iemand zal worden gegeven, als aan u werd gegeven, anders zullen zij met u redetwisten bij uw Heer.\" Zeg: \"Genade is in Allah´s hand. Hij schenkt deze aan wie Hij wil\". En Allah is Milddadig, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,73,'74. Hij geeft Zijn genade aan wie Hij wil. Allah is de Heer van grote genade.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,74,'75. Onder de mensen van het Boek is hij, die, als gij hem een schat toevertrouwt, u deze zal teruggeven, en er zijn er onder, die, als gij hun een dinar toevertrouwt, deze niet aan u zullen teruggeven, tenzij gij er voortdurend om vraagt. Dat komt, omdat zij (de Joden) zeggen: \"Wij zijn niet aansprakelijk voor de zaak van de ongeletterden.\" Daarmede uiten zij tegen beter weten in een leugen tegen Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,75,'76. Neen, maar wie zijn belofte vervult en vreest - voorwaar, Allah heeft de godvrezenden lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,76,'77. Die een geringe prijs (het wereldse) in ruil nemen voor hun verbond met Allah en voor hun eed, voor dezen is er geen voordeel in het Hiernamaals en Allah zal niet tot hen spreken, noch hen aanzien op de Dag des Oordeels, noch zal Hij hen als rein beschouwen en er zal een smartelijke straf voor hen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,77,'78. En voorzeker, onder hen zijn er, die hun tong verdraaien, terwijl zij het Boek voordragen, opdat gij het van het Boek moogt achten, hoewel het niet van het Boek is. En zij zeggen: \"Dit is van Allah,\" ofschoon het niet van Allah is en zij uiten een leugen tegen Allah, tegen beter weten in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,78,'79. Het betaamt een mens niet, als Allah hem het Boek en de macht en het profeetschap geeft, dat hij dan tot de mensen zou zeggen: \"Weest mijn dienaren buiten Allah´´; maar (veeleer): \"Weest aanbidders van de Heer, daar gij het Boek onderwijst en zelf bestudeert.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,79,'80. Noch zal hij u gebieden de engelen en de profeten als goden te aanvaarden. Zou hij u ongeloof bevelen, nadat gij Moslims werd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,80,'81. En toen Allah met de profeten een verbond sloot, zeide Hij: \"Voorwaar, Ik heb u het Boek en de Wijsheid geschonken en daarna zal een boodschapper tot u komen, vervullend hetgeen bij u is, in hem zult gij geloven en hem zult gij helpen.\" En Hij zeide: \"Hebt gij bekrachtigd en daarmede Mijn verbond aanvaard?\" Zij antwoordden: \"Wij bekrachtigen het.\" Hij zeide: \"Getuigt dan en Ik ben met u onder de getuigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,81,'82. Maar die zich hierna terugtrekken, (zij) zijn voorzeker de overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,82,'83. Zoeken zij een godsdienst anders, dan die van Allah, terwijl al hetgeen in de hemelen en op aarde is zich willens of onwillens aan Hem moet onderwerpen? En tot Hem zullen zij worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,83,'84. Zeg: \"Wij geloven in Allah en in hetgeen ons werd geopenbaard en hetgeen werd geopenbaard aan Abraham, Isma�l, Iza�k, Jacob, en de stammen en hetgeen aan Mozes en Jezus en de profeten door hun Heer werd gegeven. Wij maken geen onderscheid tussen wie dan ook van hen. Aan Hem alleen onderwerpen wij ons.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,84,'85. En wie een andere godsdienst zoekt dan de Islam, het zal van hem niet worden aanvaard en hij zal in het Hiernamaals onder de verliezers zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,85,'86. Hoe zal Allah een volk leiden, dat heeft verworpen, na te hebben geloofd, en de getuigenis te hebben afgelegd dat de boodschapper waarachtig was en nadat de duidelijke bewijzen tot hen waren gekomen? Allah leidt het onrechtvaardige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,86,'87. De vergelding van dezulken is slechts, dat de vloek van Allah, de engelen en de mensen, op hen rust.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,87,'88. Zij zullen daaronder blijven. Hun straf zal niet worden verlicht, noch zal hun uitstel worden verleend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,88,'89. Behalve degenen die daarna berouw hebben en zich verbeteren. Allah is voorzeker Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,89,'90. Voorzeker, degenen die terugvallen na te hebben geloofd en dan in ongeloof toenemen: hun berouw zal niet worden aanvaard, dezen zijn de dwalenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,90,'91. Degenen die ongelovig zijn en als ongelovigen sterven, van geen hunner zal een aarde vol goud worden aanvaard als hij zich daarmede zou willen vrijkopen. Dezen zijn het wie een smartelijke straf wacht en er zullen voor hen geen helpers zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,91,'92. Gij zult stellig geen goedheid bereiken, tenzij gij mededeelt van hetgeen u lief is en wat gij ook besteedt. Allah weet dit eveneens.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,92,'93. Alle voedsel was de kinderen Isra�ls geoorloofd, uitgezonderd hetgeen Isra�l zichzelf verbood voordat de Torah was nedergezonden. Zeg: \"Komt met de Torah en leest haar als gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,93,'94. Degenen die hierna een leugen verzinnen tegen Allah, zijn de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,94,'95. Zeg: \"Allah heeft de waarheid gesproken; volgt daarom de godsdienst van Abraham, de oprechte, hij behoorde niet tot de afgodendienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,95,'96. Voorzeker, het eerste huis dat voor de mensheid bestemd werd, is dat te Bekka (Mekka) vol van zegeningen en als richtsnoer voor alle werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,96,'97. Daarin zijn duidelijke tekenen: het is de plaats van Abraham en wie het binnengaat is in vrede. En de bedevaart naar het Huis is door Allah aan de mensen opgelegd die er een weg naartoe kunnen vinden. En wie niet gelooft, Allah is voorzeker Onafhankelijk van alle werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,97,'98. Zeg: \"O, mensen van het Boek, waarom verwerpt gij de tekenen van Allah, terwijl Allah ziet hetgeen gij doet?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,98,'99. Zeg: \"O, mensen van het Boek waarom houdt gij de mensen af van het (rechte) pad van Allah en wenst gij het krom te maken, terwijl gij er getuige van zijt? Allah is niet onachtzaam over hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,99,'100. O gij die gelooft, als gij sommigen hunner wie het Boek is gegeven gehoorzaamt, zullen zij u weer tot ongelovigen maken, nadat gij hebt geloofd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,100,'101. Hoe kunt gij verwerpen, terwijl u de tekenen van Allah worden voorgedragen en Zijn boodschapper onder u aan wezig is? En hij, die zich aan Allah vasthoudt, is inderdaad naar het rechte pad geleid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,101,'102. O gij die gelooft, vreest Allah zoals het behoort en sterft niet, tenzij gij Moslim zijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,102,'103. En houdt u allen tezamen vast aan het koord van Allah en weest niet verdeeld en gedenkt de gunst van Allah, die Hij u bewees toen gij vijanden waart en Hij uw harten verenigde, zo werdt gij door Zijn gunst broeders en gij waart aan de rand van een vuurput en Hij redde u er van. Zo legt Allah u Zijn geboden uit opdat gij zult worden geleid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,103,'104. En laat er een groep onder u zijn die tot goedheid aanspoort en tot rechtvaardigheid maant en het kwade verbiedt; dezen zijn het die zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,104,'105. En weest niet als degenen, die verdeeld waren en van mening verschilden nadat de duidelijke bewijzen tot hen waren, gekomen. Voor hen zal er een zware straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,105,'106. Op den dag, waarop sommige gezichten verlicht en andere gezichten verduisterd zullen zign. Wat hen betreft, wier gezicht verduisterd zal zijn: \"Hebt gij verworpen, nadat gij hadt geloofd? Ondergaat dan de straf, omdat gij placht te verwerpen\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,106,'107. Maar degenen wier gezicht verlicht zal zijn, dezen zullen Allah´s barmhartigheid smaken; daarin zullen zij verblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,107,'108. Dit zijn de tekenen van Allah welke wij u naar waarheid voordragen; Allah wenst de werelden geen kwaad toe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,108,'109. En aan Allah behoort al hetgeen in de hemelen en al hetgeen op aarde is en tot Allah worden alle dingen teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,109,'110. Gij (Moslims) zijt het beste volk dat voor de mensheid (ter lering) is verwekt; gij gebiedt wat goed is, verbiedt wat kwaad is en gelooft in Allah. En, indien de mensen van het Boek hadden geloofd, zou het zeker beter voor hen zijn geweest. Sommigen hunner zijn gelovigen, maar de meesten hunner zijn overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,110,'111. Zij kunnen u niet schaden, alleen enige moeilijkheid bezorgen en als zij tegen u vechten zullen zij u spoedig hun rug tonen. Dan zullen zij niet worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,111,'112. Waar zij zich ook bevinden, worden zij door vernedering getroffen, tenzij zij een verbond met Allah of een verbond met andere volkeren hebben. Zij keerden terug met Allah´s toorn en werden door armoede getroffen. Dat kwam, doordat zij de tekenen van Allah verwierpen en de profeten onrechtvaardig doodden. Dat kwam, doordat zij ongehoorzaam waren en (zijn gebod) overtraden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,112,'113. Zij zijn niet allen gelijk. Onder de mensen van het Boek is een oprechte groep, die het Woord van Allah in de uren van de nacht opzegt en zich met het gelaat ter aarde werpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,113,'114. Zij geloven in Allah en de laatste Dag en gebieden het goede en verbieden het kwade en wedijveren met elkander in goede werken. Dezen behoren tot de rechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,114,'115. En het goede dat zij doen, zal niet worden ontkend en Allah kent de Godvrezenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,115,'116. Voorzeker, degenen die verwerpen hun bezittingen noch kinderen zullen hun iets kunnen baten tegen Allah en dezen worden de bewoners van het Vuur. Zij zullen daarin verblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,116,'117. De gelijkenis van hetgeen zij voor het tegenwoordige leven besteden is als de wind, gepaard aan een hevige koude, die de oogst treft van een volk, dat zichzelf onrecht heeft aangedaan en deze vernietigt. En Allah had hun geen onrecht aangedaan, maar zij doen zichzelf onrecht aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,117,'118. O gij die gelooft, neemt buiten uw volk geen ander tot intieme vrienden; zij zullen niet in gebreke blijven u te benadelen. Zij houden van leedvermaak. Nijd laten zij blijken en wat hun innerlijk verbergt is nog erger. Wij hebben u onze geboden duidelijk gemaakt, indien gij ze wilt begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,118,'119. Ziet, gij hebt hen lief, maar zij hebben u niet lief. En gij gelooft in het gehele Boek; wanneer zij u ontmoeten zeggen zij: \"Wij geloven.\" maar wanneer zij alleen zijn, bijten zij op hun vingertoppen van razernij over u. Zeg: \"Sterft in uw razernij.\" Waarlijk, Allah weet goed wat in de harter is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,119,'120. Als u iets goeds overkomt verdriet het hen en als u iets kwaads overkomt verheugen zij zich er over. Maar, indien gij geduldig blijft en God vreest, zullen hun plannen u in het geheel niet schaden; voorzeker, Allah weet hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,120,'121. Toen gij in de vroege morgen van uw huisgezin wegtrokt om de gelovigen hun plaatsen voor het gevecht aan te wijzen, - Allah is Alhorend, Alwetend. -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,121,'122. Toen wilden twee uwer groepen lafheid tonen, hoewel Allah hun Vriend was. En in Allah behoren de gelovigen te vertrouwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,122,'123. En Allah had u reeds bij Badr geholpen, terwijl gij machteloos waart. Vreest daarom Allah, opdat gij dankbaar zult zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,123,'124. Toen gij tot de gelovigen zeidet: \"Zal het niet genoeg voor u zijn, dat uw Heer u met drie duizend nedergezonden engelen zal helpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,124,'125. Ja, indien gij geduldig en rechtvaardit zijt en zij (de ongelovigen) u dadelijk in wilde vaart aanvallen, zal uw Heer u met vijf duizend nedergezonden engelen bijstaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,125,'126. En Allah heeft het alleen als blijde boodschap voor u gemaakt om uw hart daardoor gerust te stellen en hulp komt slechts van Allah, de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,126,'127. Opdat Hij een deel der ongelovigen kon afsnijden en hen vernederen, zodat zij onverrichter zake zouden teruggaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,127,'128. Gij hebt met de zaak niets uitstaande: Hij (Allah) moge Zich in barmhartigheid tot hen wenden of hen straffen, voorzeker zij zijn de boosdoeners.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,128,'129. En aan Allah behoort al hetgeen in de hemelen en al hetgeen op aarde is. Hij vergeeft wie Hij wil en Hij straft wie Hij wil en Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,129,'130. O gij die gelooft, neemt geen rente, daar (deze) aanleiding geeft tot eindeloze vermeerdering (van bezit) en vreest Allah, opdat gij moogt slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,130,'131. En vreest het Vuur dat voor de ongelovigen is bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,131,'132. En gehoorzaamt Allah en de boodschapper, zodat u barmhartigheid moge worden betoond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,132,'133. Wedijvert met elkaar in het vragen om vergiffenis van uw Heer en om het paradijs, welks uitgestrektheid de hemelen en de aarde is, bereid voor de godvrezenden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,133,'134. Zij, die in voorspoed en in tegenspoed wel doen en zij, die toorn onderdrukken en mensen vergeven; Allah heeft hen die goed doen, lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,134,'135. En zij, die wanneer zij een slechte daad begaan of zichzelf onrecht aandoen Allah gedenken en om vergiffenis vragen voor hun zonden - wie kan deze zonden vergeven buiten Allah? - en niet volharden in hun (slechte) daden tegen beter weten in,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,135,'136. Dezen zijn het, wier loon vergiffenis is van hun Heer; in tuinen waar doorheen rivieren stromen zullen zij vertoeven; hoe goed is het loon van degenen die werken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,136,'137. Voorzeker, v��r u zijn verschillende volkeren voorbijgegaan, reist daarom over de aarde en ziet, hoe het einde was van degenen, die loochenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,137,'138. Dit is een duidelijke verklaring voor de mensen, een leiding en vermaning voor de godvrezenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,138,'139. Verslapt noch treurt; gij zult zeker overwinnaar worden, als gij gelovig blijft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,139,'140. Als gij (Moslims) letsel krijgt (in de strijd); dat volk (de tegenstander) is reeds een dergelijk letsel overkomen. Zulke dagen laten Wij onder de mensen wisselen, opdat Allah degenen, die geloven onderscheide en uit uw midden getuigen (martelaren) neme en Allah heeft de onrechtvaardigen niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,140,'141. Opdat Allah de gelovigen moge louteren en de ongelovigen vernietigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,141,'142. Denkt gij, dat gij het paradijs moogt binnengaan, terwijl Allah degenen uwer die strijden en standvastig zijn nog niet heeft onderscheiden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,142,'143. En gij placht deze dood te wensen voordat gij hem ontmoettet, nu hebt gij hem gezien en gij staart er naar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,143,'144. En Mohammed is slechts een boodschapper. Waarlijk, alle boodschappers v��r hem zijn heengegaan. Zult gij u dan op de hielen omkeren als hij sterft of gedood wordt? Hij, die zich omkeert zal aan Allah in het geheel geen schade berokkenen. En Allah zal de dankbaren gewis belonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,144,'145. Geen ziel kan sterven zonder Allah´s toestemming, daar de tijd is vastgesteld. En wie de beloning van de tegenwoordige wereld wenst, zullen Wij er van geven; en wie de beloning van het Hiernamaals wenst, hem zullen Wij daar van geven en Wij zullen de dankbaren gewis belonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,145,'146. Er zijn vele profeten geweest aan wier zijden talrijke aanbidders van de Heer streden. Zij verslapten door niets wat hen op de weg van Allah overkwam, noch verzwakten zij, noch vernederden zij zich. En Allah heeft de geduldigen lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,146,'147. En hun woord was slechts: \"Onze Heer, vergeef ons onze zonden en de buitensporigheden in ons gedrag en maak ons standvastig en help ons tegen het ongelovige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,147,'148. Daarom gaf Allah hun de beloning van deze wereld, alsmede een goede beloning in de volgende en Allah heeft degenen die goeddoen, lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,148,'149. O gij die gelooft, als gij de ongelovigen gehoorzaamt, zullen zij u doen omkeren (op het goede pad); dan zult gij als verliezers terugkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,149,'150. Neen, Allah is uw Beschermer en Hij is de Beste der helpers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,150,'151. Wij zullen de harten der ongelovigen met ontzag vervullen omdat zij aan Allah deelgenoten toeschrijven waarvoor Hij geen gezag heeft nedergezonden. Hun verblijfplaats is het Vuur en slecht is de woning der overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,151,'152. En Allah heeft Zijn belofte aan u gehouden, toen gij hen met Zijn verlof dooddet totdat gij onstandvastig werdt en het over het gebod onder elkander oneens werdt en gij niet gehoorzaamdet, nadat Hij u hetgeen u behaagde had laten zien. Onder u waren er die deze tegenwoordige wereld begeerden en er waren onder u die het Hiernamaals begeerden. Toen wendde Hij u van hen af, opdat Hij u mocht beproeven; maar Hij heeft het u vergeven. Allah is Genadevol jegens de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,152,'153. Toen gij wegvluchttet en naar niemand omzaagt, terwijl de boodschapper u van verre nariep, gaf Hij u smart op smart, opdat gij niet zoudt treuren over hetgeen was verloren, noch over hetgeen met u gebeurde. En Allah is goed op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,153,'154. Toen zond Hij na de smart een vredige sluimer over u neder, die een deel uwer overviel en het andere deel was bezorgd over zichzelf, terwijl zij ten onrechte, over Allah de gedachte der onwetendheid koesterden. Zij zeiden: \"Hebben wij iets met de zaak uit te staan?\" Zeg: \"De zaak is geheel in Allah´s handen.\" Zij verbergen in hun gedachten hetgeen zij niet aan u onthullen; zij zeggen: \"Als de zaak in onze handen was geweest zouden wij hier niet hebben moeten vechten.\" Zeg: \"Indien gij in uw huizen waart gebleven, zouden zij wie het strijden was bevolen, zeker naar de plaats waar zij zouden sterven, zijn gegaan, opdat Allah mocht beproeven wat in uw innerlijk was en louteren wat in uw hart was. Allah weet, wat in het innerlijk is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,154,'155. Voorzeker, diegenen onder u die op de dag waarop de twee scharen elkander ontmoetten, omkeerden, werden door Satan wegens hun daden aan het wankelen gebracht. Maar Allah heeft het hen vergeven. Voorwaar, Allah is Vergevensgezind, Verdraagzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,155,'156. O gij die gelooft, weest niet als de ongelovigen die over hun broeders, wanneer zij door het land reizen of ten strijde trekken, zeggen: \"Waren zij bij ons gebleven, zij zouden niet zijn gestorven of gedood; opdat Allah dit tot een oorzaak van wroeging in hun (der ongelovigen) hart moge maken. Allah geeft leven en veroorzaakt de dood; Allah ziet, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,156,'157. En als gij voor de zaak van Allah wordt gedood of sterft, zal Allah´s vergiffenis en barmhartigheid zeker beter zijn, dan hetgeen zij bijeengaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,157,'158. En indien gij sterft of gedood wordt, voorzeker, tot Allah zult gij worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,158,'159. Door de barmhartigheid van Allah zijt gij (de Profeet) zachtmoedig jegens hen (gelovigen); als gij ruw en hardvochtig waart geweest zouden zij zich zeker uit uw omgeving hebben verwijderd. Vergeef hen daarom en vraag voor hen vergiffenis en raadpleeg hen in belangrijke zaken en wanneer gij vastbesloten zijt, leg dan uw vertrouwen in Allah. Voorzeker, Allah heeft degenen lief die vertrouwen in Hem hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,159,'160. Als Allah u helpt, zal niemand u overweldigen, maar als Hij u verlaat, wie is er dan die u buiten Hem kan helpen? In Allah zullen de gelovigen hun vertrouwen leggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,160,'161. En het betaamt een profeet niet oneerlijk te handelen; wie oneerlijk handelt zal op de Dag der Opstanding zijn oneerlijke handelingen met zich meebrengen. Dan zal iedere ziel ten volle worden vergolden naar hetgeen zij verdiende, - toch zal hun geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,161,'162. Is hij die het behagen van Allah zoekt en hij die de toorn van Allah tot zich trekt en wiens verblijfplaats de hel is, gelijk? Deze (laatste) is een slechte bestemming.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,162,'163. Zij hebben bij Allah graden en Allah ziet wat zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,163,'164. Voorwaar, Allah heeft de gelovigen een gunst bewezen, daar Hij een boodschapper uit hun midden opwekte, die hun Zijn tekenen verkondigt, hen loutert en hun het Boek en de wijsheid onderwijst, hoewel zij voordien duidelijk dwaalden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,164,'165. Wanneer u rampspoed overkomt - en gij hadt het dubbele er van aangedaan (aan uw vijanden) - zegt gij: \"Vanwaar komt dit?\" Zeg: \"Het komt door uzelf.\" Voorwaar, Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,165,'166. En hetgeen u op de dag waarop de twee partijen elkander ontmoetten, is overkomen, geschiedde met Allah´s verlof en dit was om de gelovigen te onderscheiden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,166,'167. En opdat Hij de huichelaars mocht doen onderkennen. En er werd tot hen gezegd: \"Komt en vecht voor Allah´s zaak en verdedigt u.\" Zij zeiden: \"Als wij wisten dat het vechten was, zouden wij u zeker hebben gevolgd.\" Zij stonden op die dag dichter bij het ongeloof dan bij het geloof. Zij zeggen met hun mond wat niet in hun hart is. Doch Allah weet goed wat zij verbergen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,167,'168. Degenen, die omtrent hun broeders zeiden terwijl zij zelf achterbleven: \"Als zij ons hadden gehoorzaamd, zouden zij niet zijn gedood.\" Zeg: \"Wendt dan de dood van uzelf af, als gij waarheid spreekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,168,'169. En denkt niet over degenen, die terwille van Allah zijn gedood, als doden. Neen, zij zijn levend en bij hun Heer worden hun gaven geschonken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,169,'170. Jubelend, over hetgeen Allah hun van Zijn overvloed heeft gegeven, zich verblijdend over degenen die achterbleven, en hen nog niet hebben ingehaald, dat er geen vrees over hen zal komen, noch dat zij zullen treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,170,'171. Zij verheugen zich over Allah´s gunst en Zijn overvloed en dat Allah de beloning der gelovigen niet verloren doet gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,171,'172. Degenen, die de roep van Allah en de boodschapper beantwoordden, nadat zij gewond waren - diegenen hunner, die goeddoen en rechtvaardig handelen, zullen een grote beloning ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,172,'173. En toen de mensen tot hen zeiden: \"De volkeren hebben zich tegen u verzameld, vreest hen daarom,\" vermeerderde dit hun geloof en zij antwoordden: \"Allah is ons genoeg en Hij is een uitstekende Beschermer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,173,'174. Daarom keerden zij met de gunst en genade van Allah terug, geen kwaad had hen aangeraakt en zij volgden Allah´s welbehagen; en Allah is de Heer van grote overvloed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,174,'175. Satan alleen maakt zijn vrienden bang: vreest dezen niet maar vreest Mij, als gij gelovigen zijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,175,'176. En laat degenen, die vlug tot ongeloof vervallen, u niet verdrieten; voorzeker, zij kunnen Allah niets aandoen. Allah wil hen aan het toekomstig leven geen deel doen hebben, er zal voor hen een strenge straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,176,'177. Waarlijk, degenen die het ongeloof hebben aanvaard in ruil voor het geloof, kunnen Allah niets aandoen; hen wacht een pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,177,'178. En laat de ongelovigen niet denken dat het uitstel, dat Wij hun geven, goed voor hen is; Wij geven hun slechts uitstel, zodat zij in zonde toenemen; er zal voor hen een vernederende straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,178,'179. Allah is niet zo dat Hij de gelovigen in de toestand laat waarin zij verkeren, totdat Hij de kwaden van de goeden scheidt, noch is Allah zo, dat Hij u het ongeziene bekend maakt. Maar Allah kiest tot Zijn boodschappers, wie Hij wil. Gelooft daarom in Allah en Zijn boodschappers. Als gij gelooft en rechtvaardig zijt, zal er een grote beloning voor u zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,179,'180. En laat degenen, die gierig zijn, ten opzichte van wat Allah hun van Zijn overvloed heeft gegeven, niet denken, dat het goed voor hen is, neen, het is slecht voor hen. Hetgene, waarmee zij gierig zijn zal op de Dag der Opstanding als een halsband om hun nek worden gelegd. En aan Allah behoort het erfdeel der hemelen en der aarde en Allah is goed op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,180,'181. En voorzeker, Allah heeft de uiting gehoord van degenen, die zeiden: \"Allah is arm en wij zijn rijk.\" Wij zullen hetgeen zij hebben gezegd en hun pogingen om de profeten onrechtvaardig te doden, optekenen en Wij zullen zeggen: \"Ondergaat de straf van het branden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,181,'182. Dit is hetgeen gij hebt verdiend: Allah is in het geheel niet onrechtvaardig jegens zijn dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,182,'183. En degenen, die zeggen: \"Allah heeft ons opgedragen in geen boodschapper te geloven, voordat deze ons een offer brengt dat door het vuur wordt verteerd\", zeg hun: \"Er zijn reeds v��r mij boodschappers tot u gekomen met duidelijke tekenen en met hetgeen, waarover gij spreekt. Waarom trachttet gij hen dan te doden, als gij eerlijk zijt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,183,'184. En wanneer men u (de profeet) verloochent, (weet dan) dat er eveneens boodschappers v��r u verloochend werden die met duidelijke tekenen en geschriften en het stralende Boek kwamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,184,'185. Elke ziel zal de dood ondergaan. En voorzeker zal u op de Dag der Opstanding uw beloning ten volle worden uitbetaald. Wie daarom van het Vuur wordt verwijderd en de Hemel binnengelaten, heeft inderdaad zijn doel bereikt. Het leven dezer wereld is niets dan een middel tot bedrog.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,185,'186. Gij zult zeker worden beproefd in uw bezittingen en in uzelf en gij zult gewis vele pijnlijke dingen horen van degenen, aan wie het Boek was gegeven v��r u en van degenen, die afgoderij bedrijven. Maar als gij geduldig blijft en rechtvaardig handelt, dat is waarlijk een zaak van vastberadenheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,186,'187. En toen Allah een verbond sloot met degenen, die het Boek gegeven was, zeide Hij: \"Gij zult dit aan de mensen bekend maken en het niet verbergen.\" Maar zij verwaarloosden dat voor luttel gewin. Kwaad was hetgeen zij in ruil namen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,187,'188. Degenen die juichen over hetgeen zij hebben gedaan en gaarne worden geroemd voor hetgene zij niet deden, denkt niet, dat zij veilig zijn voor straf. Er wacht hen een pijnlijke kastijding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,188,'189. En aan Allah behoort het koninkrijk der hemelen en der aarde en Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,189,'190. Er zijn voorzeker in de schepping der hemelen en der aarde en in de wisseling van dag en nacht tekenen voor mensen van begrip.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,190,'191. Zij die staande, zittende en op hun zijden liggende Ailah gedenken, en nadenken over de schepping der hemelen en der aarde, zeggende: \"Onze Heer, Gij hebt dit niet tevergeefs geschapen; neen, heilig zijt Gij; red ons daarom van de straf van het Vuur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,191,'192. Onze Heer, wie Gij het Vuur doet ingaan, hem hebt Gij voorzeker vernederd. En de onrechtvaardigen zullen geen helper vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,192,'193. Onze Heer, wij hebben een prediker gehoord, die opriep tot het geloof: ´Gelooft in de Heer´ en wij hebben geloofd. Onze Heer, vergeef ons daarom onze zonden en bedek onze zwakheden en doe ons sterven met de rechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,193,'194. Onze Heer, schenk ons hetgeen Gij ons door Uw boodschappers hebt beloofd en verneder ons niet op de Dag der Opstanding. Voorzeker, Gij breekt de belofte niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,194,'195. En hun Heer verhoorde hen, zeggende: \"Ik zal het werk van iedere werker onder u, hetzij man of vrouw, niet verloren doen gaan. - Gij behoort tot elkander. - En Ik zal van hen, die hun land verlieten en van hun huizen zijn verjaagd en voor Mijn zaak zign vervolgd en die hebben gevochten en zijn gedood, de fouten zeker bedekken en zal hen tuinen doen binnengaan, waar doorheen rivieren stromen: een beloning van Allah en bij Allah is de beste beloning.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,195,'196. Laat de bewegingen der ongelovigen in het land u niet bedriegen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,196,'197. Het is een gering voordeel voor hen; daarna zal de hel hun tehuis zijn en slecht is deze rustplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,197,'198. Maar zij, die hun Heer vrezen, zullen tuinen hebben, waar doorheen rivieren stromen, daarin zullen zij vertoeven als onthaal van Allah. En hetgeen bij Allah is, is voor de rechtvaardigen beter.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,198,'199. En voorzeker, onder de mensen van het Boek zijn er, die in Allah en in hetgeen u is geopenbaard en in hetgeen tot hen was neergedaald, geloven, zich voor Allah verootmoedigend. Zij ruilen de tekenen van Allah niet in voor een geringe prijs. Dezen zijn het, die hun beloning bij hun Heer zullen ontvangen. Voorzeker, Allah is vlug in het verrekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,3,199,'200. O, gij die gelooft, blijft geduldig en spoort anderen aan volhardend te zijn en blijft op uw hoede en vreest Allah, opdat gij zult slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,1,'O, gij mensen, vreest uw Heer, Die u van ��n enkele ziel schiep en daaruit haar gezellin schiep en uit hen beiden mannen en vrouwen verspreidde en vreest Allah in Wiens naam gij een beroep op elkander doet en (weest plichtsgetrouw) betreffende de familiebanden. Voorwaar, Allah is Bewaker over u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,2,'En geeft de wezen hun eigendom en verruilt het slechte (van u) niet voor het goede (van hen) noch verbruikt hun eigendom met het uwe. Voorzeker, dat is een grote zonde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,3,'En als gij vreest dat gij niet rechtschapen zult zijn bij het behandelen der wezen, huwt dan vrouwen die u behagen, twee of drie, of vier en als gij vreest, dat gij niet rechtvaardig zult handelen, dan ��n of wat uw rechter handen bezitten. Dat is voor u de beste weg, om onrechtvaardigheid te voorkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,4,'En geeft de vrouwen gewillig haar huwelijksgift. Maar als zij naar haar eigen behagen u er een gedeelte van kwijtschelden, geniet het dan met genoegen en heilzaam gevolg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,5,'En geeft eigendom, dat Allah als middel van bestaan heeft gegeven niet aan de dwazen (in eigen beheer), maar voedt hen er mee en kleedt hen en spreekt vriendelijke woorden tot hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,6,'En ondervraagt de wezen, wanneer zij de huwbare leeftijd bereikt hebben: als gij in hen rijpheid van verstand vindt stelt hun dan hun eigendom ter hand; en verteert het niet in buitensporigheid en haast, omdat zij opgroeien. En laat hij, die rijk is zich onthouden en laat hij die arm is naar billijkheid er gebruik van maken. En wanneer gij hun eigendommen overhandigt, neemt er dan getuigen bij. Allah is toereikend om rekenschap te vragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,7,'Er is voor mannen een aandeel van hetgeen hun ouders en bloedverwanten nalaten en er is voor vrouwen een aandeel van hetgeen hun ouders en bloedverwanten nalaten, of het weinig of veel zij: een vastgesteld gedeelte.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,8,'Wanneer verwanten en wezen en de armen bij de verdeling (der erfenis) aanwezig zijn, geeft hun er iets van en spreekt vriendelijke woorden tot hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,9,'En laat hen Allah vrezen, die, indien zij hun eigen zwakke nageslacht mochten achterlaten, bezorgd zouden zijn. Laat hen Allah daarom vrezen en laat hen het juiste woord spreken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,10,'Voorzeker, zij, die het eigendom van wezen onrechtvaardig verteren, verteren slechts vuur in hun buik en zij zullen in een laaiend Vuur branden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,11,'Allah gebiedt u aangaande uw kinderen: voor het mannelijke kind evenveel als het deel van twee vrouwelijke kinderen, maar als er alleen meisjes zijn, meer dan twee, dan is er voor haar tweederde van de nalatenschap en als er slechts ��n is, voor haar is de helft. En voor elk zijner ouders is er een zesde deel der erfenis, als hij een kind heeft, maar als hij geen kind heeft en zijn ouders van hem erven, dan is er voor zijn moeder een derde deel en als hij broeders en zusters heeft, dan is er voor zijn moeder een zesde deel na de betaling van enig legaat, dat hij heeft nagelaten of van (niet vereffende) schuld. Uw ouders en uw kinderen, gij weet niet, wie van hen u het meest tot heil is. Dit is vastgesteld door Allah. Voorzeker, Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,12,'En gij zult de helft hebben van hetgeen uw vrouwen nalaten, indien zij geen kind hebben, maar indien zij een kind hebben, is er voor u een vierde van hetgeen zij nalaten na de betaling van enig legaat, dat zij hebben nagelaten of van schuld. En zij zullen een vierde hebben van hetgeen gij nalaat, als gij geen kind hebt, maar als gij een kind hebt, zo is er voor hen een achtste deel van hetgeen gij nalaat, na de betaling van enig legaat of van onverrekende schuld. En indien er een man of een vrouw is, van wie wordt ge�rfd en deze is ouderloos en kinderloos en heeft een broeder of een zuster, dan is er voor elk hunner een zesde deel. Maar als er meer dan dezen zijn, dan zijn zij deelgenoten in een derde na de betaling van enig legaat, dat is nagelaten of van schuld, zonder benadeling. Dit is gebod van Allah en Allah is Alwetend, Verdraagzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,13,'Dit zijn de door Allah vastgestelde bepalingen en wie Allah en Zijn boodschapper gehoorzaamt, Hij zal hem tuinen doen binnengaan, waar doorheen rivieren stromen, daar zullen zij in verblijven en dat is een grote zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,14,'En wie Allah en Zijn boodschapper niet gehoorzaamt en Zijn grenzen overschrijdt zal Hij het Vuur doen binnengaan; hij zal daarin verblijven en dit zal voor hem een vernederende straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,15,'En voor degenen uwer vrouwen, die zich aan ontucht schuldig maken, roept vier uwer als getuigen tegen haar en als zij getuigen, sluit haar dan in de huizen op, totdat de dood haar achterhaalt, of totdat Allah haar een weg opent.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,16,'En als twee temiffen van u zich hieraan schuldig maken, straft hen beiden. En als zij berouw hebben en zich verbeteren, laat hen dan met rust, voorzeker, Allah is Berouwaanvaardend, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,17,'Waarlijk, berouw bestaat bij Allah alleen van degenen, die in onwetendheid kwaad doen en dan daarna berouw hebben. Dezen zijn het, tot wie Allah Zich met barmhartigheid wendt; en Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,18,'Er is geen (aanvaarding van) berouw voor degene, die kwaad doet, totdat de dood hem in het gezicht staart en hij zegt: ´Ik heb berouw;´ noch voor degenen die als ongelovigen sterven. Dezen zijn het, voor wie Wij een pijnlijke straf hebben bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,19,'O, gij die gelooft, het is u niet geoorloofd, vrouwen te erven tegen haar wil, noch moogt gij haar tegenhouden opdat gij een gedeelte van wat gij haar hebt gegeven, moogt terugnemen, tenzij zij schuldig zijn aan een schandelijk kwaad; en blijft met haar vriendelijk omgaan en als gij afkeer van haar hebt, kan het zijn, dat gij afkeer hebt van iets, waarin Allah veel goeds kan hebben gelegd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,20,'En indien gij een vrouw in plaats van een andere wenst te nemen en gij hebt ��n harer een schat gegeven, neemt er niets van terug. Wilt gij het door laster en een klaarblijkelijk zondige manier nemen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,21,'En hoe kunt gij het nemen, wanneer de een uwer tot de andere is ingegaan en zij een sterk verbond met u hebben gesloten?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,22,'En huwt niet de vrouwen, die uw vaders huwden, met uitzondering van wat reeds gebeurd is. Het is een slecht en afschuwelijk iets en een verkeerde weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,23,'Verboden zijn u uw moeders en uw dochters en uw zusters en uw vaders zusters en uw moeders zusters en uw broeders dochters en uw zusters dochters en uw minnen en uw zoogzusters en de moeders uwer vrouwen en uw stiefdochters, die uw beschermelingen zijn door uw vrouwen tot wie gij zijt ingegaan, maar als gij niet tot haar zijt ingegaan zal er geen zonde op u rusten en de vrouwen uwer eigen zonen (zign ook verboden) alsmede twee zusters tezamen te hebben, met uitzondering van wat reeds voorbij is; gewis, Allah is Vergevensgezind, Genadevol;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,24,'En getrouwde vrouwen, met uitzondering van haar, die gij bezit. Dit is een gebod van Allah voor u. Degenen, die daar buiten vallen, zijn u toegestaan; dat gij zoekt door middel van wat gij bezit haar behoorlijk te huwen en geen overspel te plegen. En geeft haar een huwelijksgift, tegenover de voordelen, die gij van haar hebt, dit is verplicht; er zal na het vaststellen daarvan geen zonde op u rusten in alles wat gij onderling overeenkomt. Voorzeker, Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,25,'En wie uwer het niet kan bekostigen vrije gelovige vrouwen te huwen, hij huwe hetgeen gij bezit, namelijk gelovige slavinnen. En Allah kent uw geloof het beste. Gij zijt van elkander; huwt haar daarom met de toestemming van haar meesters en geeft haar een huwelijksgift op de gebruikelijke wijze, kuis zijnde, geen ontucht plegende, noch er heimelijke minnaars op nahoudende. En indien zij, nadat zij gehuwd zijn zich schuldig maken aan ontrouw - geldt voor haar de helft van de straf, die voor de vrije vrouwen is voorgeschreven. Dit is voor degene uwer die vreest te zondigen. Maar het is beter voor u dat gij u weerhoudt en Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,26,'Allah wenst u te onderrichten en te leiden naar de paden van degenen die v��r u waren en u Zijn barmhartigheid te betonen. Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,27,'En Allah wenst Zich in barmhartigheid tot u te wenden, maar zij, die hun lagere begeerte volgen, wensen dat gij ver zult afdwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,28,'Allah wil uw last verlichten, want de mens is zwak geschapen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,29,'O, gij die gelooft, gebruikt elkanders eigendom niet met leugen en bedrog maar handelt bij onderlinge overeenkomst. En pleeg geen zelfmoord. Voorzeker, Allah is u Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,30,'En wie dit ook doet bij wijze van overtreding en onrechtvaardigieid, hem zullen Wij in het Vuur werpen; en dat is voor Allah eenvoudig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,31,'Als gij de grootste dingen die u verboden zijn vermijdt, zullen Wij uw zwakheden voor u bedekken en u tot een plaats van grote eer toelaten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,32,'En begeert niet datgene, waarmede Allah sommigen uwer boven anderen deed uitblinken. Mannen zullen een aandeel hebben in hetgeen zij hebben verdiend en vrouwen zullen een aandeel hebben in hetgeen zij hebben verdiend. En vraagt om Allah´s overvloed. Waarlijk, Allah kent alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,33,'En voor een ieder hebben Wij erfgenamen bepaald ten aanzien van hetgeen de ouders en de bloedverwanten nalaten; en van degenen met wie uw eden een overeenkomst hebben bekrachtigd, geeft ieder hunner daarom zijn deel. Waarlijk, Allah is Bewaker over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,34,'Mannen zijn voogden over de vrouwen omdat Allah de enen boven de anderen heeft doen uitmunten en omdat zij van hun rijkdommen besteden. Deugdzame vrouwen zijn dus zij, die gehoorzaam zijn en heimelijk bewaren, hetgeen Allah onder haar hoede heeft gesteld. En degenen, van wie gij ongehoorzaamheid vreest, wijst haar terecht en laat haar in haar bedden alleen en tuchtigt haar. Als zij u dan daarna gehoorzamen, zoekt geen weg tegen haar. Waarlijk, Allah is Verheven, Groot.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,35,'En als gij een breuk tussen hen vreest, stelt dan een scheidsrechter van zijn familie en van haar familie aan. Indien zij verzoening wensen zal Allah deze tussen hen tot stand brengen. Voorzeker, Allah is Alwetend, Alkennend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,36,'En aanbidt Allah en vereenzelvigt niets met Hem en bewijst vriendelijkheid aan ouders, verwanten, wezen, de behoeftigen en aan de nabuur, die een vreemdeling is en de nabuur die een bloedverwant is en aan de metgezel, de reiziger en aan degenen die onder uw macht zijn. Voorzeker, Allah heeft de pochers en de opscheppers niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,37,'Evenmin die gierig zijn en de mensen aansporen ook gierig te zijn en die hetgeen Allah hun van Zijn overvloed heeft gegeven, verbergen. Wij hebben voor de ongelovigen een vernederende straf bereid,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,38,'En voor degenen, die hun rijkdommen besteden om door de mensen te worden gezien en niet in Allah noch in de laatste Dag geloven. En wie Satan als metgezel heeft, heeft een kwade metgezel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,39,'Wat kan hun overkomen, indien zij in Allah en de laatste Dag zouden geloven en hadden weggegeven van hetgeen Allah hen heeft geschonken? Allah kent hen zeer goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,40,'Waarlijk, Allah doet in het geheel geen onrecht aan. Als het een goede daad is vermenigvuldigt Hij deze en geeft van Zijn kant een grote beloning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,41,'En wat geschiedt, wanneer Wij een getuige van elk volk zullen roepen en u als getuige tegen deze zullen brengen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,42,'Op die Dag zullen zij, die verwierpen en de boodschapper niet gehoorzaamden, wensen, dat de aarde met hen gelijk zou worden gemaakt en zij zullen geen woord voor Allah kunnen verbergen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,43,'O, gij die gelooft, komt niet tot het gebed als gij bedwelmd zijt; totdat gij weet wat gij zegt, noch, wanneer gii onrein zijt tot gij u hebt gebaad, tenzij gij onderweg zijt. En indien gij ziek zijt, of op reis, of een uwer van de afzondering komt, of gij hebt vrouwen aangeraakt en gij vindt geen water, neemt dan uw toevlucht tot zuivere aarde en veegt er uw gezicht en handen mee af. Waarlijk, Allah is Inschikkelijk, Vergevensgezind.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,44,'Kent gij niet degenen, die deel hebben aan het Boek? Zij geven de voorkeur aan dwaling en wensen, dat ook gij van de (rechte) weg moogt afdwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,45,'Allah kent uw vijanden goed. Allah is voldoende als Vriend en Allah is toereikend als Helper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,46,'Er zijn onder de Joden, die woorden uit hun verband rukken. En zij zeggen: \" Wij horen en gehoorzamen niet\" en \"luistert gij, zonder te horen\" en \"Raainaa\", terwijl zij woorden verdraaien en het geloof zoeken te schenden. En indien zij gezegd hadden: \"Wij horen en wij gehoorzamen\" en \"hoort toe\" en ,,Kijk ons aan\" het dit beter en oprechter voor hen zijn geweest. Maar Allah heeft hen wegens hun ongeloof vervloekt, zij geloven dus slechts weinig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,47,'O, mensen van het Boek, gelooft in hetgeen Wij hebben nedergezonden, vervullende hetgeen bij u is voordat Wij uw leiders vernietigen en neerwerpen of hen vervloeken, zoals Wij het volk van de Sabbath vervloekten. Allah´s gebod zal volbracht worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,48,'Waarlijk, Allah vergeeft niet dat men iets met Hem vereenzelvigt, maar Hij zal al hetgeen daarbuiten staat vergeven, wie Hij wil. En wie iets met Allah vereenzelvigt, heeft inderdaad een zeer grote zonde begaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,49,'Hebt gij over hen niet vernomen die zichzelf rein achten? Neen, Allah is het, Die reinigt, wie Hij wil. Hen zal niet het minste onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,50,'Zie, hoe zij een leugen tegen Allah smeden. En dat is, voorzeker, een klaarblijkelijke zonde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,51,'Hebt gij degenen niet waargenomen aan wie een gedeelte van het Boek was gegeven? Zij geloven in afgoden en duivelen en zeggen van de ongelovigen: \"Dezen zijn beter geleid op het pad dan de gelovigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,52,'Dezen zijn degenen die Allah heeft vervloekt en die Allah vervloekt voor hen zult gij geen helper vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,53,'Hadden zij een aandeel in het koninkrijk dan zouden zij de mensen zelfs het geringste onthouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,54,'Of benijden zij de mensen om hetgeen Allah hun vanuit Zijn overvloed heeft gegeven? Waarlijk, Wij gaven aan de kinderen van Abraham het Boek en de Wijsheid en Wij gaven hun ook een groot koninkrijk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,55,'En sommigen hunner geloofden er in en sommigen hunner weerhielden anderen er van te (geloven). De hel, met het laaiende vuur is toereikend (voor hen).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,56,'Gewis, degenen die Onze tekenen verwerpen zullen Wij weldra het Vuur doen binnengaan. Wij zullen hen telkens, wanneer hun huiden zijn verbrand, andere huiden er voor in de plaats geven; opdat zij de straf ten volle zullen ondergaan. Waarlijk, Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,57,'En degenen, die geloven en goede daden verrichten, zullen Wij tuinen doen binnengaan waar doorheen rivieren stromen om er eeuwig te vertoeven, daarin zullen zij reine metgezellen hebben en Wij zullen hen door schaduw omringen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,58,'Voorwaar, Allah gebiedt u het u toevertrouwde over te geven aan hen die er recht op hebben en dat, wanneer gij tussen mensen richt, gij rechtvaardig handelt. En waarlijk, voortreffelijk is datgene, waartoe Allah u maant. Voorzeker, Allah is de Alhorende, de Alziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,59,'O, gij die gelooft, gehoorzaamt Allah en Zijn boodschapper en degenen, die onder u gezag hebben. En indien gij over iets twist, verwijst het naar Allah en Zijn boodschapper, als gij gelooft in Allah en de laatste Dag. Dit is beter en uiteindelijk het beste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,60,'Kent gij niet degenen, die beweren dat zij geloven in hetgeen u is geopenbaard en hetgeen v��r u is geopenbaard? Zij wensen recht te zoeken bij de opstandigen ofschoon het hun was geboden, dezen te verwerpen. En Satan wenst hen ver van het rechte pad te doen afdwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,61,'En wanneer er tot hen wordt gezegd: \"Komt tot hetgeen Allah heeft nedergezonden en tot Zijn boodschapper\", ziet gij dan de huichelaars zich vol afkeer van u afwenden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,62,'Hoe kan het dan dat, wanneer een rampspoed over hen komt door hetgeen zij verdienden, zij zwerend tot u komen: \"Bij Allah, wij beoogden niets dan het goede (te doen) en verzoening?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,63,'Allah weet wat in het hart van dezen is. Wend u daarom van hen af en vermaan hen en spreek tot hen een doeltreffend woord ten bate van henzelf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,64,'Wij zenden geen boodschapper of hij moet worden gehoorzaamd volgens Allah´s gebod. Als zij tot u waren gekomen, toen zij hun ziel onrecht hadden aangedaan en Allah om vergiffenis hadden gevraagd en de boodschapper ook om vergiffenis voor hen had gevraagd, zouden zij Allah voorzeker Berouwaanvaardend, Genadevol hebben bevonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,65,'Maar neen, bij uw Heer, zij zullen geen gelovigen zijn, voordat zij u (profeet) tot rechter maken over al hun geschillen en in hun hart geen aarzeling vinden aangaande hetgeen gij oordeelt en zij zich geheel en al onderwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,66,'En indien Wij hun hadden geboden: \"Wijdt u ten dode\" of: \"verlaat uw huizen\" zouden zij het met uitzondering van weinigen hunner niet hebben gedaan en indien zij hetgeen hun gemaand was te doen, hadden gedaan, zou het voor hen zeker goed zijn geweest en het, beste ter versterking (van hun geloof).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,67,'En Wij zouden hun gewis een grote beloning van Ons hebben gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,68,'En Wij zouden hen zeker op het rechte pad hebben geleid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,69,'En wie aldus Allah en deze boodschapper gehoorzaamt, zal zijn onder degenen wie Allah Zijn zegeningen heeft geschonken, namelijk, de profeten, de waarachtigen, de getuigen (martelaars) en de goeden en dezen zijn uitstekende metgezellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,70,'Dit is de genade van Allah en Allah is toereikend als de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,71,'O, gij die gelooft, neemt uw voorzorgsmaatregelen, rukt dan op in afzonderlijke groepen of allen tezamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,72,'En onder u is hij, die achter blijft en als u een rampspoed overkomt, zegt hij: \"Waarlijk, Allah is mij genadig geweest omdat ik niet bij hen tegenwoordig was.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,73,'Maar als een genade van Allah tot u komt, zegt hij - alsof er geen vriendschap tussen u en hem bestond - \"Ware ik bij hen geweest, dan zou ik inderdaad een groot voordeel hebben bereikt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,74,'Laten derhalve zij, die hun tegenwoordig leven voor het leven in het Hiernamaals willen offeren, voor de zaak van Allah strijden. En wie voor de zaak van Allah strijdt, hetzij hij gedood wordt of overwint, weldra zullen Wij hem een grote beloning geven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,75,'En waarom strijdt gij niet voor de zaak van Allah en voor de zwakken -- mannen, vrouwen en kinderen - die zeggen: \"Onze Heer, neem ons uit deze stad waarvan de bewoners onderdrukkers zijn en schenk ons een vriend en een helper uwerzijds.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,76,'Zij die geloven, strijden voor de zaak van Allah, maar de ongelovigen strijden voor de zaak van de boze. Strijdt daarom tegen de vrienden van Satan; voorzeker, Satan´s plan is zwak.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,77,'Ziet gij niet degenen, tot wie gezegd werd: \"Weerhoudt uw handen, houdt het gebed en betaalt de Zakaat? \" En wanneer het strijden hun is voorgeschreven, ziet, een deel hunner vreest de mensen zoals men Allah behoort te vrezen, of zelfs nog erger en zij zeggen: \"Onze Heer, waarom hebt Gij ons het strijden voorgeschreven? Waarom hebt Gij ons niet voor een korte tijd uitstel verleend?\" Zeg: \"Het voordeel van deze wereld is gering en het Hiernamaals zal beter zijn voor hem die Allah vreest. En u zal niet het minste onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,78,'Waar gij ook zijt, de dood zal u achterhalen, zelfs al waart gij in sterk gebouwde torens. En als hen iets goeds overkomt zeggen zij: \"Dit komt van Allah\" en als hen iets kwaads overkomt zeggen zij: \"Dit komt van u\" (van de profeet). Zeg: \"Alles komt van Allah\". Wat scheelt deze mensen, dat zij het woord niet willen begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,79,'Welk goed ook tot u komt, dat komt van Allah en welk kwaad u overkomt, komt door uzelf. En wij hebben u als boodschapper tot de mensheid gezonden; Allah is als Getuige toereikend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,80,'Wie de boodschapper gehoorzaamt, gehoorzaamt inderdaad Allah en wie zich afkeert - tot hen hebben wij u niet als bewaker gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,81,'En zij zeggen: \"Gehoorzaamheid\", maar wanneer zij van u weggaan, smeedt een deel hunner plannen in de nacht tegen hetgeen gij hebt gezegd. Allah tekent op, al hetgeen zij beramen gedurende de nacht. Wend u dus van hen af en stelt uw vertrouwen in Allah alleen. Allah is toereikend als Voogd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,82,'Denken zij dan niet na over de Koran? Was deze van iemand anders dan van Allah dan hadden zij zeker menige tegenstrijdigheid daarin ontdekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,83,'En als er enig nieuws tot hen komt, hetzij over vrede of over vrees, verspreiden zij het en indien zij het naar de boodschapper hadden verwezen en naar hun gezaghebbers dan zouden degenen, die het konden verwerken, het zeker hebben begrepen. En ware Allah´s genade en Zijn barmhartigheid niet over u, dan zoudt gij zeker met uitzondering van enkelen, Satan hebben gevolgd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,84,'Strijd daarom voor de zaak van Allah - slechts gij wordt verantwoordelijk gesteld - en spoor de gelovigen aan. Het kan zijn, dat Allah de macht der ongelovigen zal beteugelen en Allah is sterker in macht en streng in het opleggen van straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,85,'Wie het goede bijvalt, zal er aandeel aan hebben en wie het kwade bijvalt zal er een gelijk aandeel aan hebben; En Aliah houdt toezicht over alles.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,86,'En wanneer gij met een groet wordt begroet, groet dan terug met een betere groet, of geeft deze althans terug. Voorzeker, Allah houdt rekening met alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,87,'Allah! Er is geen God, dan Hij. Hij zal u zeker bijeenroepen op de Dag der Opstanding, waaromtrent geen twijfel is. En wie is waarachtiger in Zijn woord, dan Allah?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,88,'Waarom zijt gij betreffende de huichelaars (in) twee partijen (verdeeld)? Allah heeft hen neergeslagen wegens hetgeen zij verdienden. Wenst gij hen te leiden, die Allah te gronde deed gaan? En voor hen, die Allah doet dwalen, zult gij geen uitweg vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,89,'Zij wensen dat gij verwerpt, evenals zij hebben verworpen, zodat gij aan hen gelijk zult worden. Neemt derhalve geen vrienden uit hun midden totdat zij voor de zaak van Allah werken. En indien zij tot vijandschap vervallen, grijpt hen dan en doodt hen waar gij hen ook vindt; en neemt vriend noch helper uit hun midden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,90,'Behalve degenen die tot een volk behoren waarmee gij een verbond gesloten hebt, of zij die tot u komen terwijl hun hart zich verzet u te bestrijden of hun eigen volk aan te vallen. En indien Allah wilde, zou Hij hun macht tegen u hebben gegeven; dan zouden zij zeker tegen u hebben gevochten. Dus, als zij zich van u op een afstand houden en u niet bestrijden en u vrede aanbieden - heeft Allah u niet toegestaan iets tegen hen te ondernemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,91,'Gij zult anderen vinden die veilig bij u willen zijn en bij hun eigen volk; telkens wanneer zij tot vijandigheid worden opgeroepen, doen zij blindelings mee. Als zij zich derhalve niet op een afstand van u houden, noch u vrede aanbieden, noch hun handen terughouden, grijpt hen dan aan en doodt hen waar gij hen ook vindt. Tegen dezen hebben Wij u duidelijk gezag gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,92,'Het betaamt een gelovige niet, een andere gelovige te doden, tenzij dit bij vergissing gebeurt. En wie een gelovige bij vergissing doodt moet een gelovige slaaf bevrijden en bloedgeld betalen ter overhandiging aan de erfgenamen, tenzij deze het uit liefdadigheid kwijtschelden. Maar indien hij (de gedode) tot een u vijandig gezind volk behoort en een gelovige is, dan moet (de overtreder) een gelovige slaaf bevrijden en als hij van een volk is waarmede gij een verbond hebt, dan moet een bloedgeld aan zijn familie worden betaald en een gelovige slaaf worden bevrijd. Maar wie er geen vindt, moet twee maanden achtereenvolgens vasten - een boete van Allah. En Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,93,'En wie een gelovige opzettelijk doodt, zijn vergelding zal de hel zijn; daarin zal hij vertoeven. Allah´s toorn is op hem; Hij heeft hem vervloekt en zal hem een grote straf bereiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,94,'O, gij die gelooft, wanneer gij voor Allah´s zaak oprukt, onderzoekt dan en zegt niet tegen iemand die u met de vredesgroet begroet: \"Gij zijt geen gelovige\". Zoekt gij de goederen van dit leven? Bij Allah zijn goede dingen in overvloed. Zo waart gij voordien maar Allah bewees u Zijn gunst; stelt daaromtrent een nauwkeurig onderzoek in. Voorzeker, Allah weet, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,95,'Diegenen der gelovigen die niets doen, met uitzondering der onbekwamen, zijn niet gelijk aan degenen die met hun rijkdommen en hun persoon terwille van Allah strijden. Allah heeft degenen, die met hun rijkdommen en hun persoon strijden doen uitmunten boven de rustenden en aan ieder heeft Allah het goede beloofd. Allah zal de strijders boven de stilzittenden doen uitblinken door een grote beloning,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,96,'nl. in graden, ook van vergiffenis en barmhartigheid. En Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,97,'Voorwaar de engelen zullen tot hen die ze doen sterven, terwijl dezen hun eigen ziel onrecht aandoen, zeggen: \"In welke toestand waart gij?\" Zij zullen antwoorden: \"Wij waren in het land machteloos.\" Zij (de engelen) zullen echter zeggen: \"Was Allah´s aarde u niet groot genoeg om daarop te verhuizen?\" Zij zijn het, wier tehuis de hel zal zijn en dat is een kwade bestemming.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,98,'Met uitzondering van de zwakken onder de mannen en vrouwen en kinderen, die geen middelen tot en beschikking hebben, noch een weg kunnen vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,99,'Dezen zijn het, wie Allah moge vergeven, want Allah is de Inschikkelijke, de Vergevensgezinde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,100,'Wie terwille van Allah vlucht, zal op aarde toevluchtsoorden en overvloed vinden. En wie van zijn huis weggaat, zijn land verlatend terwille van Allah en Zijn boodschapper en de dood achterhaalt hem - zijn beloning bij Allah staat vast; Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,101,'En wanneer gij door het land reist, zal het voor u geen zonde zijn het gebed te bekorten, als gij vreest dat degenen, die niet geloven u last zullen veroorzaken. Voorwaar, de ongelovigen zijn een openlijke vijand voor u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,102,'En wanneer gij in hun midden zijt en het gebed voor hen leidt, laat een deel hunner bij u staan en hun wapenen meenemen. En wanneer zij hun prostratie hebben verricht, laat hen achter u gaan en laat die andere groep, die nog niet gebeden heeft naar voren komen en met u bidden en laat hen hun afweermiddelen en wapenen medenemen. De ongelovigen wensen, dat gij onachtzaam wordt op uw wapenen en uw bagage, zodat zij u plotseling overvallen. En als gij uw wapenen opzij legt indien de regen u stoort, of indien gij ziek zijt, zal dat voor u geen zonde zijn. Maar gij dient uw afweermiddelen steeds mede te nemen. Voorzeker, Allah heeft voor de ongelovigen een vernederende straf bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,103,'Wanneer gij het gebed hebt be�indigd, gedenkt dan Allah, staande, zittende en op uw zijde liggende. En, wanneer gij veilig zijt, houdt het gebed, voorwaar, het gebed is de gelovigen op vastgestelde uren opgelegd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,104,'En toont geen zwakheid in de vervolging van dit (vijandige) volk. Als gij lijdt, lijden zij ook zoals gij lijdt. Maar gij verwacht van Allah, wat zij niet verwachten. En Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,105,'Wij hebben u waarlijk het Boek (de Koran), dat. de waarheid bevat, nedergezonden, opdat gij tussen de mensen zoudt richten door hetgeen Allah u heeft onderwezen. En wees geen pleiter voor de oneerlijken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,106,'En vraagt vergiffenis van Allah. Voorzeker, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,107,'Pleit niet voor degenen, die hun ziel onrecht aandoen. Voorzeker, Allah heeft degene, die volkomen oneerlijk en een groot zondaar is, niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,108,'Zij trachten zich voor de mensen te verbergen, maar zij kunnen zich niet voor Allah verbergen en Hij is bij hen wanneer zij de nacht doorbrengen met een bespreking, die Hem niet behaagt. Allah weet, wat zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,109,'Ziet, gij zijt degenen die in het tegenwoordige leven voor hen pleiten. Maar wie zal bij Allah voor hen pleiten op de dag der opstanding, of wie zal een voogd over hen zijn?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,110,'Wie kwaad doet of zijn ziel onrecht aandoet en daarna Allah om vergiffenis vraagt, zal Allah Vergevensgezind, Genadevol vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,111,'En wie een zonde begaat, begaat deze slechts jegens zijn eigen ziel. En Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,112,'Wie een fout of een zonde begaat en deze dan aan een onschuldige toeschrijft, draagt voorzeker de (schuld van) lastering en klaarblijkelijke zonde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,113,'Ware Allah´s genade en barmhartigheid niet over u, dan zou een gedeelte hunner hebben besloten u te vernietigen, maar zij vernietigen niemand dan zichzelf en zij kunnen u in het geheel niet schaden. En Allah heeft u het Boek en de Wijsheid nedergezonden en heeft u in hetgeen gij niet wist, onderwezen en Allah´s genade aan u is groot.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,114,'Er steekt in de beraadslagingen (der huichelaars) niets goeds; in tegenstelling tot diegenen die tot liefdadigheid of goedheid, of het stichten van vrede onder de mensen aansporen. En wie dit doet wijl hij Allah´s welbehagen zoekt, hem zullen Wij een grote beloning schenken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,115,'En hij, die zich tegen de boodschapper verzet nadat diens leiding hem duidelijk is geworden en die een andere weg dan die der gelovigen volgt, Wij zullen hem laten volgen wat hij wil en Wij zullen hem in de hel werpen. Dat is een kwade bestemming.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,116,'Allah vergeeft niet dat iets met Hem vereenzelvigd wordt en Hij zal, buiten dat, vergeven wie Hij wil. En wie iets met Allah vereenzelvigt, is inderdaad ver afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,117,'Zij roepen buiten Hem niets aan dan godinnen dingen en zij roepen niemand aan dan Satan, de opstandige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,118,'Allah heeft hem vervloekt. En hij (Satan) zeide: \"Ik zal voorzeker een bepaald deel van uw dienaren nemen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,119,'\"En ik zal hen zeker doen dwalen en ijdele begeerten in hen opwekken en ik zal hen voorzeker ophitsen en zij zullen de oren van het vee afsnijden en ik zal hen voorzeker aansporen en zij zullen Allah´s schepping bederven.\" Derhalve hij, die buiten Allah Satan tot vriend neemt, zal zeker zichtbaar verlies leiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,120,'Hij doet hun beloften en wekt begeerten en Satan belooft hun niets dan bedrog.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,121,'Dezen zijn het, wier tehuis de hel is en zij zullen voor het Vuur geen wijkplaats vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,122,'Degenen, die geloven en goede werken verrichten, zullen Wij in tuinen toelaten, waar doorheen rivieren stromen en zij zullen daar voor eeuwig vertoeven. De belofte van Allah is werkelijkheid en wie is waarachtiger in woord, dan Allah?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,123,'Niet naar uw wensen (de ongelovigen), noch naar de wensen van de mensen van het Boek. Wie kwaad doet zal er voor worden gestraft en hij zal buiten Allah vriend, noch helper vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,124,'Maar, wie goede werken verricht, hetzij man of vrouw, en gelovig is, zal de Hemel binnengaan en hem zal niet het geringste onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,125,'En wie is beter in geloof dan hij, die zich aan Allah onderwerpt en die het goede doet en de godsdienst volgt van Abraham de oprechte? Allah nam Abraham tot vriend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,126,'En aan Allah behoort alles, wat in de hemelen en alles wat op aarde is en Allah omvat alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,127,'En zij (de gelovigen) vragen uw uitspraak over de vrouwen; Zeg: \"Allah geeft u Zijn uitspraak over haar; alsmede hetgeen u in het Boek (de Koran) is verkondigd over de weesmeisjes, aan wie gij het haar toegekende niet geeft en die gij wenst te huwen en over de zwakken onder de kinderen en dat gij de wezen rechtvaardig moet behandelen. En welke weldaad gij ook verricht, voorzeker, Allah weet het goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,128,'Als een vrouw mishandeling of onverschilligheid van haar man vreest, zal het geen blaam voor hen zijn als zij een verzoening met elkander tot stand brengen - verzoening is het beste. De mensen zijn tot gierigheid geneigd. En als gij goed doet en rechtvaardig zijt, waarlijk dan is Allah op de hoogts van wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,129,'Gij kunt geen volkomen gelijkheid tussen vrouwen handhaven, hoe gaarne gij het ook zoudt wensen. Maar neigt niet geheel tot ��n, zodat gij de andere in onzekerheid laat. En als gij u betert en vroom zijt, dan is Allah voorzeker Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,130,'En als zij scheiden, dan zal Allah hen beiden door Zijn overvloed onafhankelijk maken; Allah is Milddadig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,131,'Aan Allah behoort, wat in de hemelen en wat op aarde is. En wij hebben zeker degenen aan wie v��r u het Boek werd gegeven en ook u geboden: Allah te vrezen. Maar als gij verwerpt - voorzeker wat in de hemelen en op de aarde is behoort aan Allah en Allah is Onafhankelijk, Lofwaardig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,132,'En aan Allah behoort alles, wat in de hemelen en alles, wat op aarde is en Allah is voldoende als Voogd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,133,'Indien Hij wil, zal Hij u, o volk, wegnemen en anderen in uw plaats brengen en Allah heeft de volle macht, dit te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,134,'Wie de beloning dezer wereld verlangt - bij Allah is de beloning dezer wereld en van de volgende en Allah is Alhorend, Alziend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,135,'O, gij die gelooft, weest voorstanders der rechtvaardigheid, getuigen voor Allah, zelfs al was het tegen uzelf, of ouders en verwanten. Hetzij rijk of arm, Allah is beter dan beiden. Volgt niet de begeerten, opdat gij niet onrechtvaardig zult zijn. En als gij de waarheid omzeilt of er u van afwendt, Allah is goed op de hoogte van wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,136,'O gij die gelooft, gelooft in Allah en Zijn boodschapper en in het Boek dat Hij Zijn boodschapper heeft geopenbaard, en in het Boek, dat Hij voordien openbaarde. En wie Allah en Zijn engelen en Zijn Boeken en Zijn boodschappers en de laatste Dag verwerpt, is waarlijk ver afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,137,'Voorzeker, degenen die geloven, daarna verwerpen, dan wederom geloven dan wederom verwerpen en daarna in ongeloof toenemen, hen zal Allah niet vergeven, noch zal Hij hen op de rechte weg leiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,138,'Verkondig de huichelaars, dat hen een pijnlijke straf wacht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,139,'Zij, die ongelovigen tot vrienden nemen liever dan gelovigen, - zoeken zij eer bij hen hoewel alle eer aan Allah behoort?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,140,'En Hij heeft u reeds in het Boek ( Koran) geopenbaard, dat wanneer gij hoort dat Allah´s tekenen worden verloochend en bespot, gij niet (eerder) met hen samen zult zijn, dan dat zij zieh met een ander onderwerp bezig houden, anders zoudt gij hun gelijk zijn. Voorzeker, Allah zal de huichelaars en de ongelovigen allen tezamen in de hel bijeenbrengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,141,'Degenen, die afwachten tot u een overwinning van Allah ten deel valt, zeggen: \"Waren wij niet met u?\" En als de ongelovigen er aandeel in krijgen, zeggen zij (tot hen): \"Hebben wij niet de overhand over u gekregen en u beschermd tegen de gelovigen?\" Allah zal op de Dag des Oordeels tussen u richten en Allah zal de ongelovigen op generlei wijze over de gelovigen doen zegevieren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,142,'De huichelaars trachten Allah te bedriegen, maar Hij zal hen voor hun bedrog straffen. En wanneer zij zich oprichten om te bidden, staan zij loom, en tonen zich aan de mensen en gedenken Allah slechts weinig,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,143,'Weifelend tussen dat en dit. Zij behoren noch tot dezen, noch tot genen. En voor hem, die Allah doet dwalen, zult gij geen uitweg vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,144,'O, gij die gelooft, neemt geen ongelovigen tot vrienden boven de gelovigen. Wilt gij Allah een duidelijk bewijs tegen uzelf geven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,145,'De huichelaars zullen zeker in de diepste diepte van het Vuur zijn en gij zult voor hen geen helper vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,146,'Behalve degenen, die berouw hebben en zich verbeteren en aan Allah vasthouden en hun gehoorzaamheid zuiver houden voor Allah. Dezen behoren tot de gelovigen. En Allah zal de gelovigen weldra een grote beloning geven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,147,'Waarom zou Allah u straffen, als gij dankbaar zijt en gelooft? Allah is Waarderend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,148,'Allah houdt niet van het uiten van beledigende taal in het openbaar, behalve door iemand, die onrecht wordt aangedaan; en Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,149,'Of gij een goede daad openlijk verricht of deze verbergt, of een kwaad vergeeft, Allah is voorzeker de Inschikkelijke, de Almachtige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,150,'Waarlijk, degenen die Allah en Zijn boodschappers verwerpen en onderscheid wensen te maken tussen Allah en Zijn boodschappers, zeggende: \"Wij geloven in sommige en niet in andere,\" zij willen een tussenweg volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,151,'Dezen zijn inderdaad de ongelovigen en Wij hebben voor de ongelovigen een vernederende straf bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,152,'En degenen, die in Allah en al Zijn boodschappers geloven en geen onderscheid tussen wie dan ook, maken, dezen zijn het, wie Hij spoedig hun beloning zal geven; Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,153,'De mensen van het Boek vragen u een Boek uit de hemel op hen te doen nederdalen. Zij vroegen Mozes meer dan dit, zij zeiden: \"Toon ons Allah openlijk.\" Toen trof hen de bliksem wegens hun overtreding. Daarna, hoewel duidelijke tekenen tot hen gekomen waren, namen zij toch het (gouden) kalf (ter aanbidding) aan, doch Wij vergaven hun dat. En Wij bekleedden Mozes met duidelijk gezag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,154,'Wij verhieven de berg hoog boven hen tijdens het verbond met hen en Wij zeiden: \"Gaat de poort ootmoedig binnen\" en: \"Overtreedt niet inzake de Sabbath\". En Wij sloten met hen een vast verbond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,155,'Om hun schending van hun verbond en de verwerping van Allah´s tekenen en het ten onrechte doden van de profeten en omdat ze zeggen: \"Onze harten zijn gesluierd\" - neen, Allah heeft deze wegens hun ongeloof verzegeld, derhalve geloven zij slechts weinig;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,156,'Om hun ongeloof en het uiten van een kwaadaardige laster tegen Maria;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,157,'En om hun zeggen: \"Wij hebben de Messias, Jezus, zoon van Maria, de boodschapper van Allah gedood\", - maar zij doodden hem niet, noch kruisigden zij hem (ten dode), - doch het werd hun verward, en zij, die hierover van mening verschilden zijn zeker in twijfel, zij hebben er geen kennis van doch volgen slechts een vermoeden en zij doodden hem gewis niet,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,158,'Integendeel, Allah verhief hem tot Zich en Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,159,'Er is niemand onder de mensen van het Boek die er niet in zal geloven v��r zijn dood. En op de Dag der Opstanding zal hij (Jezus) getuige tegen hen zijn, -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,160,'En wegens de onrechtvaardigheid van de Joden en hun weerhouden van Allah´s weg, verboden Wij hen de reine dingen die ben (voordien) waren toegestaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,161,'En om het nemen van rente, ofschoon het hun was verboden en het onrechtvaardig opslokken van ´s mensen rijkdommen, hebben Wij voor degenen onder hen die niet geloven een pijnlijke straf bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,162,'Maar degenen hunner, die een grondige kennis bezitten en de gelovigen, geloven in hetgeen u is geopenbaard en hetgeen v��r u werd nedergezonden; en degenen, die het gebed houden en degenen, die de Zakaat betalen en degenen, die in Allah en de laatste Dag geloven, dezen zullen Wij zeker een grote beloning geven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,163,'Waarlijk, Wij hebben u de openbaring gezonden, zoals Wij Noach en de profeten na hem openbaring zonden en Wij gaven een openbaring aan Abraham en Isma�l en Iza�k en Jacob en de stammen; en aan Jezus, Job, Jonas, A�ron en Salomo en Wij gaven David een psalmen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,164,'Wij zonden boodschappers, welke Wij reeds hebben genoemd en boodschappers welke Wij u niet hebben genoemd en Allah sprak openlijk tot Mozes.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,165,'Boodschappers, brengende blijde tijding en waarschuwende, dat de mensen geen tegenwerping tegen Allah zullen maken nadat de boodschappers (waren gekomen). En Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,166,'Maar Allah getuigt dat, hetgeen Hij u heeft nedergezonden, Hij dit heeft nedergezonden met Zijn kennis en de engelen getuigen eveneens en Allah is als getuige toereikend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,167,'Zij, die verwerpen en (anderen) van Allah´s weg afhouden, zijn zeker ver afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,168,'Waarlijk degenen, die niet geloven en die onrechtvaardig handelen, Allah zal hen niet vergeven, noch zal Hij hun een andere weg wijzen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,169,'Dan de weg der hel, waarin zij voor een lange tijd zullen vertoeven. Dat is voor Allah gemakkelijk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,170,'O mensdom, de boodschapper is inderdaad met waarheid van uw Heer gekomen, gelooft daarom; het zal beter voor u zijn. Maar als gij niet gelooft, voorwaar, aan Allah behoort wat in de hemelen en op aarde is en Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,171,'O, mensen van het Boek, overdrijft in uw godsdienst niet en zegt van Allah niets dan de waarheid. Voorwaar, de Messias, Jezus, zoon van Maria was slechts een boodschapper van Allah en Zijn woord tot Maria gegeven als barmhartigheid van Hem. Gelooft dus in Allah en Zijn boodschappers en zegt niet: \"Drie (in ��n).\" Houdt op, dat is beter voor u. Voorwaar, Allah is de enige God. Het is verre van Zijn heiligheid, dat Hij een zoon zou hebben. Aan Hem behoort wat in de hemelen en op aarde is en Allah is als Bewaarder afdoende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,172,'Voorzeker, de Messias zal het nooit versmaden, een dienaar van Allah te zijn, noch zullen de nabijzijnde engelen dit doen en wie het versmaadt Hem te aanbidden, en hoogmoedig is, Hij zal hen toch allen tot Zich roepen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,173,'Maar degenen die geloven en goede werken verrichten, zal Hij hun beloning ten volle geven en meer dan dat uit Zijn overvloed, maar degenen die versmaadden en hoogmoedig waren, zal Hij met een pijnlijke straf straffen; zij zullen buiten Allah vriend, noch helper voor zich vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,174,'O, gij mensen, een duidelijk bewijs is inderdaad van uw Heer tot u gekomen en Wij hebben een helder licht tot u nedergezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,175,'Daarom, zij die in Allah geloven en aan Hem vasthouden zal Hij zeker tot Zijn barmhartigheid en genade toelaten en hen op het rechte pad tot Zich voeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,4,176,'Zij vragen om een uitspraak. Zeg: \"Allah geeft Zijn uitspraak betreffende \"Kalalah\": Indien een man sterft en geen kind achterlaat en hij heeft een zuster, dan moet zij de helft van hetgeen hij nalaat ontvangen en hij zal van haar erven (alles) indien zij geen kind heeft. Maar als er twee zusters zijn, dan moeten zij twee derde van hetgeen hij nalaat ontvangen. En als er meer zijn - zowel mannen als vrouwen - dan zal de man evenveel als het aandeel van twee vrouwen ontvangen. Allah legt u dit uit, opdat gij niet zult afdwalen; Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,1,'O, gij die gelooft, komt uw verdragen na. Viervoetige dieren buiten die welke u zijn aangegeven, zijn u geoorloofd; het wild is niet geoorloofd te achten terwijl gij ter bedevaart zijt. Voorwaar, Allah gebiedt wat Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,2,'O, gij die gelooft, ontheiligt de tekenen van Allah niet, noch de heilige maand, noch de offerdieren, noch dieren met offertekens, noch degenen, die zich naar het heilige Huis begeven om genade van hun Heer en Zijn welbehagen te zoeken. Maar wanneer gij u van uw pelgrimskleed ontdoet, moogt gij jagen. En laat de vijandschap van een volk, omdat zij u de toegang tot de heilige Moskee verhinderen, u niet tot geweld aansporen. En helpt elkander in deugdzaamheid en vroomheid maar helpt elkander niet in zonde en overtreding. En vreest Allah. Waarlijk, Allah is streng in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,3,'Verboden is u het gestorvene, het bloed en het varkensvlees en al waarover een andere naam dan die van Allah is aangeroepen; hetgeen is geworgd en is doodgeslagen en hetgeen is doodgevallen of hetgeen door de horens van dieren is gedood en hetgeen door een wild beest is aangevreten, behalve wat gij hebt geslacht. Verder hetgeen voor afgoden is geslacht en wat gij loot door pijlen, dit is een overtreding. Heden zullen de ongelovigen aan uw godsdienst wanhopen. Vreest dus niet hen, maar Mij. Nu heb Ik uw godsdienst voor u vervolmaakt, Mijn gunst aan u voltooid en de Islam voor u als godsdienst gekozen. Maar wie door honger wordt gedwongen zonder dat hij tot de zonde is geneigd, voorzeker, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,4,'Zij vragen u, wat hun geoorloofd is. Zeg: Alle goede dingen zijn u geoorloofd en hetgeen gij dieren en roofvogels hebt geleerd terwille van de jacht, zoals Allah u heeft onderwezen. Eet dus van hetgeen zij voor u vangen en spreekt er Allah´s Naam over uit. En vreest Allah. Voorzeker, Allah is vlug in het verrekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,5,'Alle goede dingen zijn u deze dag geoorloofd. Het voedsel der mensen van het Boek is u geoorloofd en uw voedsel is hun toegestaan. En geoorloofd zijn voor u kuise, gelovige vrouwen en kuise vrouwen uit het midden dergenen, wie het Boek was gegeven v��r u, wanneer gij haar haar huwelijksgift geeft, een geldig huwelijk aangaande en geen ontucht plegende, noch heimelijk minnaressen nemende. En wie het geloof verwerpt, diens werk is waarlijk tevergeefs en hij zal in het Hiernamaals onder de verliezers zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,6,'O, gij die gelooft, wanneer gij u opricht tot het gebed, wast uw gezicht en uw handen tot aan de ellebogen en wrijft uw (natte) handen over uw hoofden en (wast) uw voeten tot aan de enkels. En als gij onrein zijt, reinigt u. En als gij ziek of op reis zijt en een uwer komt van de afzondering, of gij hebt vrouwen aangeraakt en gij vindt geen water, zoekt dan uw toevlucht tot zuivere aarde en veegt daarmede uw gezicht en handen af. Allah wenst u niet in moeilijkheden te brengen, maar Hij wenst u te reinigen en Zijn gunst aan u te vervolmaken, opdat gij dankbaar zult zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,7,'En gedenkt Allah´s gunst aan u en het verbond dat Hij met u sloot, toen gij zeidet: \"Wij horen en wij gehoorzamen.\" En vreest Allah. Voorzeker, Allah weet goed, wat in uw innerlijk is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,8,'O, gij die gelooft, weest oprecht voor Allah en getuigt met rechtvaardigheid. En laat de vijandschap van een volk u niet aansporen, om onrechtvaardig te handelen. Weest rechtvaardig, dat is dichter bij de vroomheid en vreest Allah, voorzeker, Allah is op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,9,'Allah heeft degenen, die geloven en goede daden verrichten beloofd, dat zij vergiffenis en een grote beloning zullen verkrijgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,10,'En degenen, die niet geloven en Onze tekenen verwerpen, zullen de bewoners der hel zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,11,'O, gij die gelooft, gedenkt Allah´s gunst aan u toen een volk zijn handen tegen u wilde uitsteken, maar Hij weerhield hun handen en vreest Allah. Op Allah moeten de gelovigen zich verlaten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,12,'Waarlijk Allah sloot een verbond met de kinderen Isra�ls en Wij verwekten twaalf leiders uit hun midden. En Allah zeide: \"Voorzeker, Ik ben met u. Indien gij het gebed houdt en de Zakaat betaalt en in Mijn boodschappers gelooft en hen bijstaat en aan Allah´s (dienst) een goede lening verstrekt, zal Ik uw zonden van u verwijderen en u in tuinen toelaten, waar doorheen rivieren stromen. Maar wie onder u daarna dit verwerpt, is inderdaad van het rechte pad afgedwaald.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,13,'En wegens hun breken van het verbond hebben Wij hen vervloekt en hun hart verhard. Zij rukken de woorden uit hun verband en hebben een deel van hetgeen hun was vermaand, vergeten. En gij zult hen altijd oneerlijk bevinden op enkelen na, derhalve vergeef hen en wend u van hen af. Voorzeker, Allah heeft degenen, die goeddoen, lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,14,'En met degenen die zeggen: \"Wij zijn Christenen, sloten Wij (eveneens) een verbond, maar zij vergaten een deel van hetgeen hen was voorgehouden. Daarom deden Wij vijandschap en haat onder hen ontstaan, tot de Dag der Opstanding. Allah zal hen weldra laten weten, wat zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,15,'O, mensen van het Boek, Onze boodschapper is tot u gekomen, die veel van hetgeen voor u verborgen bleef van het Boek heeft ontsluierd en veel overgeslagen. Er is van Allah inderdaad een licht en een duidelijk Boek tot u gekomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,16,'En Allah leidt daarmede degenen die Zijn welbehagen zoeken op de paden van vrede en leidt hen uit de duisternis tot het licht door Zijn gebod en leidt hen naar het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,17,'Voorzeker, zij lasteren God die zeggen: \"De Messias, zoon van Maria, is zeker Allah.\" Zeg: \"Wie heeft dan macht tegen Allah, als Hij de Messias, zoon van Maria en zijn moeder en allen die op aarde zijn, teniet wil doen?\" Aan Allah behoort het koninkrijk der hemelen en der aarde en al wat daartussen is. Hij schept wat Hij wil en Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,18,'De Joden en de Christenen zeggen: \"Wij zijn Allah´s kinderen en Zijn geliefden.\" Zeg: \"Waarom straft Hij u dan voor uw zonden? Neen, gij zijt mensen onder degenen die Hij schiep. Hij vergeeft, wie Hij wil en Hij straft, wie Hij wil. En aan Allah behoort het koninkrijk der hemelen en der aarde en wat daartussen is en tot Hem is de terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,19,'O, gij mensen van het Boek, Onze boodschapper is tot u gekomen na een onderbreking in de reeks van boodschappers, die u klaarheid brengt, opdat gij niet zult zeggen: \"Er is geen brenger van een blijde tijding en geen waarschuwer tot ons gekomen.\" Waarlijk er is een brenger van een blijde boodschap en een waarschuwer tot u gekomen. Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,20,'En toen Mozes tot zijn volk zeide: \"O, mijn volk, herinner u Allah´s gunst aan u, toen Hij profeten onder u aanstelde en u koningen aanwees en Hij u gaf, wat Hij aan niemand onder de volkeren heeft gegeven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,21,'\"O, mijn volk, gaat het heilige land binnen dat Allah voor u heeft bestemd en keert het niet de rug toe, anders zult gij verliezers worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,22,'Zij zeiden: \"O, Mozes, daarin is een trots en machtig volk en wij zullen er niet binnengaan voordat zij er uit weggaan. En indien zij er uit weggaan, zullen wij het binnentrekken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,23,'Daarop zeiden twee mannen van degenen die hun Heer vreesden en wie Allah Zijn gunst had bewezen: \"Gaat de poort (van de stad) binnen, hen tegemoet - wanneer gij er eenmaal binnen zijt, dan zult gij zeker overwinnaar worden. En stelt uw vertrouwen in Allah, als gij gelovigen zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,24,'Zij zeiden: \"O, Mozes, wij zullen er stellig niet binnengaan zolang zij er in zijn. Gaat gij en uw Heer en strijdt - wij blijven hier zitten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,25,'Hij zeide: \"Mijn Heer, ik heb macht over niemand dan over mijzelf en mijn broeder, maak daarom een onderscheid tussen ons en het opstandige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,26,'Allah zeide: \"Voorzeker, dat (land) is voor hen voor veertig jaren verboden; dwalende zullen zij door het land trekken. Bekommer u daarom niet over het ongehoorzame volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,27,'En vertel naar waarheid het verhaal van de twee zonen van Adam, toen zij een offer brachten en het van ��n hunner werd aangenomen en van de ander niet. De laatstgenoemde zeide: \"Ik zal u zeker doden.\" - De eerste zeide: \"Allah neemt alleen iets van de rechtvaardigen aan.\" -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,28,'\"Als gij uw hand naar mij uitstrekt om mij te doden, zal ik mijn hand niet naar u uitstrekken, om u te doden. Ik vrees Allah, de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,29,'Ik wens, dat gij zowel met de zonde tegen mij, als met uw zonde terugkeert, zodat gij tot de bewoners van het Vuur zult behoren, dat is de beloning der misdadigers.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,30,'Maar zijn kwade neiging dreef hem er toe zijn broeder te doden, dus doodde hij hem en werd een der verliezers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,31,'Toen zond Allah een raaf, die in de grond krabde, om hem te beduiden, hoe het lijk van zijn broeder te verbergen. Hij zeide: \"Ware ik maar de raaf gelijk, zodat ik het lijk van mijn broeder kon verbergen.\" En toen kreeg hij berouw.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,32,'Deswegen schreven Wij de kinderen Isra�ls voor, dat wie ook een mens doodt, behalve wegens het doden van anderen of het scheppen van wanorde in het land, het ware alsof hij het gehele mensdom had gedood, en voor hem, die iemand het leven schenkt, alsof hij aan het gehele mensdom het leven heeft geschonken. En voorzeker Onze boodschappers kwamen met duidelijke tekenen tot hen en toch - werden daarna -velen hunner op aarde tot over treders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,33,'De vergelding dergenen die oorlog tegen Allah en Zijn boodschappers voeren en er naar streven wanorde in het land te scheppen, is slechts dat zij gedood of gekruisigd worden, of dat hun handen en hun voeten de ene rechts en de andere links, worden afgesneden, of dat zij het land worden uitgezet. Dat zal voor hen een schande in deze wereld zijn en in het Hiernamaals zullen zij een grote straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,34,'Dit, met uitzondering van hen die berouw tonen, voordat gij hen in uw macht hebt. Weet derhalve, dat Allah Vergevensgezind, Genadevol is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,35,'O gij die gelooft, vreest Allah en zoekt de weg tot toenadering tot Hem en strijdt voor Zijn zaak, opdat gij moogt slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,36,'Voorzeker, al hadden de ongelovigen al hetgeen op aarde is en nog eens zoveel, om zich daarmede van de straf op de Dag der Opstanding vrij te kopen, dan zou het van hen toch niet worden aanvaard; er wacht hen een pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,37,'Zij zullen uit het vuur willen komen, maar zij zullen er niet kunnen uitgaan en dit zal voor hen een blijvende straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,38,'En snijdt de dief en de dievegge de hand af, als straf voor wat zij misdeden, een voorbeeldige straf van Allah. Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,39,'Maar degene, die na zijn overtreding berouw heeft en zich betert - Allah zal Zich gewis in barmhartigheid tot hem wenden; voorwaar, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,40,'Weet gij niet, dat het koninkrijk der hemelen en der aarde aan Allah toebehoort? Hij straft, wie Hij wil en Hij vergeeft, wie Hij wil en Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,41,'O gij boodschapper, laat degenen, die gemakkelijk in het ongeloof vervallen u niet verdrieten, n.l. zij die met hun mond zeggen: \"Wij geloven,\" maar in hun hart hebben zij niet geloofd. En onder de Joden zijn er die naar een leugen zouden willen luisteren, dezen luisteren terwille van een ander volk dat niet tot u is gekomen. Zij verdraaien woorden, nadat zij op hun juiste plaatsen waren gezet en zeggen: \"Als u dit wordt gegeven, neemt het dan aan, maar als het u niet wordt gegeven, past dan op.\" En wie Allah wenst te beproeven, gij zult hem tegen Allah stellig niets baten. Dit zijn degenen, wier hart het Allah niet heeft behaagd te louteren; er zal voor hen schande in deze wereld en een grote straf in het Hiernamaals zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,42,'Zij zijn luisteraars naar leugens en verbruikers van verboden dingen. Indien zij tot u om recht komen, spreek recht tussen hen of wend u van hen af. En indien gij u van hen afwendt kunnen zij u in het geheel niet schaden. En indien gij rechtspreekt, richt tussen hen met rechtvaardigheid. Voorzeker, Allah heeft de rechtvaardigen lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,43,'Hoe zullen zij u tot rechter maken wanneer zij de Torah bij zich hebben waarin Allah´s oordeel is? Toch wenden zij zich af. En zij zijn geen gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,44,'Waarlijk, Wij zonden de Torah neder, waarin leiding en licht was, waarmede de profeten die gehoorzaam waren recht spraken voor de Joden en de Rabbijnen en de wetgeleerden, omdat hun de bewaking van Allah´s Boek was opgelegd en zij waren daarvan getuigen. Vreest daarom de mensen niet, doch vreest Mij en ruilt Mijn tekenen niet in tegen het wereldse. En wie niet rechtspreken volgens hetgeen Allah heeft nedergezonden, zij zijn ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,45,'En Wij schreven hen daarin voor: Een leven voor een leven, oog om oog, neus om neus, oor om oor, tand om tand en (rechtvaardige) vergelding voor wonden. En hij, die van het recht hierop afziet, dit zal een verzoening voor zijn zonden zijn en wie niet rechtspreken bij hetgeen Allah heeft nedergezonden, zijn onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,46,'En Wij deden Jezus, zoon van Maria in hun voetsporen treden, vervullende, hetgeen v��r hem in de Torah was (geopenbaard), en Wij gaven hem het Evangelie, dat licht en leiding bevatte, bevestigende hetgeen daarv��r in de Torah was en een leiding en een vermaning voor de godvrezenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,47,'En laat de mensen van het Evangelie richten naar hetgeen Allah daarin heeft geopenbaard en wie niet richten naar hetgeen Allah heeft geopenbaard, zijn de overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,48,'En Wij hebben u het Boek (de Koran) met de waarheid geopenbaard vervullende hetgeen daarv��r in het Boek (de Bijbel) was (verkondigd) en als bewaker daarover. Richt daarom tussen hen naar hetgeen Allah heeft geopenbaard en volg hun boze neigingen niet tegen de waarheid die tot u is gekomen. Voor iedereen bepaalden Wij een wet en een weg. En indien Allah had gewild zou Hij u allen tot ��n volk hebben gemaakt, maar Hij wenst u te beproeven met hetgeen Hij u heeft gegeven. Wedijvert dus met elkander in goede werken. Tot Allah zult gij allen terugkeren, dan zal Hij u datgene mededelen, waarover gij van mening verschilt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,49,'En spreek recht tussen hen naar hetgeen Allah u heeft geopenbaard en volg hun boze neigingen niet en wees op uw hoede dat zij u niet afleiden van hetgeen Allah u heeft geopenbaard. Maar indien zij zich afwenden, weet dan, dat Allah hen voor sommige hunner zonden wenst te treffen. En een groot aantal mensen is inderdaad ongehoorzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,50,'Wensen zij het oordeel van onwetendheid? En wie is een betere rechter dan Allah voor een volk dat zekerheid van geloof bezit?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,51,'O, gij die gelooft, neemt de Joden en de Christenen niet tot vrienden. Zij zijn elkanders vrienden. En wie uwer hen tot vrienden neemt, is inderdaad ��n hunner. Voorwaar, Allah leidt het overtredende volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,52,'En gij zult degenen in wier hart een ziekte is, zich tot hen zien haasten, zeggende: \"Wij vrezen, dat ons rampspoed zal overkomen.\" Het is echter waarschijnlijk dat Allah een overwinning of iets anders tot stand zal brengen. Dan zullen zij berouw hebben over hetgeen zij in hun innerlijk verborgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,53,'En de gelovigen zullen zeggen: \"Zijn dit degenen die met hun ernstige eden bij Allah zwoeren dat zij waarlijk met u waren?\" Hun werken zijn verloren gegaan en zij zijn verliezers geworden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,54,'O, gij die gelooft, wie onder u zich van zijn godsdienst afkeert, laat hem weten, dat Allah weldra een ander volk zal voortbrengen dat Hij zal liefhebben en die Hem zullen liefhebben vriendelijk en nederig zijnde jegens de gelovigen en hard en streng jegens de ongelovigen. Zij zullen voor Allah´s zaak strijden en het verwijt van een berisper niet vrezen. Dit is Allah´s genade; Hij schenkt deze aan wie Hij wil en Allah is Milddadig, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,55,'Uw vrienden zijn slechts Allah en Zijn boodschapper en de gelovigen die het gebed houden en de Zakaat betalen en aanbidden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,56,'En hij, die Allah en de boodschapper en de gelovigen tot vrienden neemt (wete) dat de partij van Allah gewis zal zegevieren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,57,'O, gij die gelooft, neemt niet degenen tot vrienden die een spotternij en een spel maken van uw godsdienst, uit de kring dergenen wie het Boek was gegeven v��r u, noch van de ongelovigen. En vreest Allah als gij gelovigen zijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,58,'En zij die, wanneer gij tot het gebed roept het tot spotternij en spel maken. Dit komt doordat zij een volk zijn dat niet begrijpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,59,'Zeg: \"O, mensen van het Boek, gij haat ons slechts, omdat wij in Allah geloven en in hetgeen ons is nedergezonden en in hetgeen voordien was nedergezonden of doordat de meesten van u ongehoorzaam zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,60,'Zeg: \"Zal ik u vertellen over degenen wier straf bij Allah erger is dan dit? Dezen zijn het, die Allah heeft vervloekt en over wie Hij Zijn toorn heeft uitgestort en van wie Hij apen, zwijnen en duivelsdienaren heeft gemaakt. Dezen zijn inderdaad in een slechte toestand en ver van het rechte pad afgedwaald.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,61,'Wanneer zij tot u komen, zeggen zij: \"Wij geloven,\" terwijl zij met ongeloof binnenkomen en er mee heengaan en Allah weet het beste, wat zij verbergen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,62,'En gij ziet velen hunner zich haasten om zonde te bedrijven en overtreding en van verboden dingen te gebruiken. Het is inderdaad slecht, wat zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,63,'Waarom weerhouden hun priesters en schriftgeleerden hen niet van zondige woorden en het eten van verboden dingen? Het is inderdaad slecht wat zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,64,'En de Joden zeggen: \"De hand van Allah is gebonden.\" Hun handen zijn gebonden en zij zijn vervloekt voor hetgeen zij zeggen. Neen, Zijn handen zijn wijd open, Hij geeft, zoals Hij wil. En hetgeen u van uw Heer is nedergezonden zal velen hunner in opstandigheid en ongeloof doen toenemen. En Wij hebben vijandschap en haat onder hen gezaaid tot aan de Dag der Opstanding. Telkens wanneer zij het oorlogsvuur ontsteken, dooft Allah het en zij pogen wanorde te scheppen op aarde en Allah heeft de onruststokers niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,65,'Als de mensen van het Boek hadden geloofd en rechtvaardig gehandeld, zouden Wij gewis hun zonden hebben vergeven en hen in tuinen van zaligheid hebben toegelaten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,66,'En als zij de Torah en het Evangelie en hetgeen hun van hun Heer is nedergezonden, in acht hadden genomen, zouden zij zeker van wat boven hen is en van hetgeen onder hun voeten is, hebben gegeten. Onder hen is een groep die matig is, maar de handelwijze van velen hunner is slecht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,67,'O boodschapper, verkondig hetgeen u van uw Heer is geopenbaard en indien gij dat niet doet, dan hebt gij Zijn boodschap niet overgebracht. Allah zal u tegen de mensen beschermen. Voorzeker, Allah leidt het ongelovige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,68,'Zeg: \"O, mensen van het Boek, gij steunt op niets voordat gij de Torah en het Evangelie en hetgeen u van uw Heer is nedergezonden, onderhoudt. En waarlijk, hetgeen u van uw Heer is nedergezonden zal velen hunner in opstandigheid en ongeloof doen toenemen; treurt derhalve niet over het ongelovige volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,69,'Voorzeker, de gelovigen en de Joden en de Sabianen en de Christenen die in Allah en de laatste Dag geloven en goede daden verrichten - over hen zal geen vrees komen, noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,70,'Wij hebben waarlijk een verbond met de kinderen Isra�ls gesloten en Wij zonden boodschappers tot hen. Maar telkens, wanneer een boodschapper tot hen kwam met hetgeen hun hart niet wenste, behandelden zij sommigen als leugenaars en trachtten zij sommigen te doden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,71,'En zij dachten, dat er geen beproeving zou zijn, derhalve werden zig blind en doof. Doch Allah wendde Zich in barmhartigheid tot hen; toch werden velen weer blind en doof en Allah is waakzaam over hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,72,'Zij lasteren God, die zeggen: \"Waarlijk Allah, Hij is de Messias, de zoon van Maria,\" terwijl de Messias zelf zeide: \"O, kinderen Isra�ls, aanbidt Allah, Die mijn Heer en uw Heer is.\" Gewis, voor hem die iets met Allah vereenzelvigt, heeft Allah de Hemel verboden en het Vuur zal zijn verblijfplaats zijn. Er is voor de onrechtvaardigen geen helper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,73,'Waarlijk zij lasteren God, die zeggen: \"Allah is E�n der Drie.\" Er is geen God dan de enige God. En indien zij niet ophouden met hetgeen zij beweren, zal de ongelovigen een smartelijke straf overkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,74,'Willen zij zich dan niet tot Allah wenden en om Zijn vergiffenis vragen terwijl Allah Vergevensgezind, Genadevol is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,75,'De Messias, de zoon van Maria was slechts een boodschapper; voorzeker, alle boodschappers v��r hem zijn heengegaan. En zijn moeder was een waarheidslievende vrouw. Zij plachten beiden voedsel tot zich te nemen. Zie, hoe Wij de tekenen voor hen (de mensen) verduidelijken, en zie, hoe zij zich afwenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,76,'Zeg: \"Aanbidt gij naast Allah datgene wat geen macht heeft u goed of kwaad te doen? En het is Allah, Die Alhorend, Alwetend is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,77,'Zeg: \"O, mensen van het Boek, overdrijft uw godsdienst niet ten onrechte, noch volgt de neigingen van een volk dat voordien afdwaalde en velen deed dwalen en van het rechte pad afweek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,78,'Degenen onder de kinderen Isra�ls, die niet geloofden, werden door de mond van David en door Jezus de zoon van Maria, vervloekt. Dit geschiedde, omdat zij niet gehoorzaamden en plachten te overtreden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,79,'Zij plachten elkander de ongerechtigheid niet te verbieden, welke Zij begingen. Slecht is inderdaad hetgeen zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,80,'Gij zult velen hunner de ongelovigen tot vrienden zien nemen. Waarlijk slecht is hetgeen zij voor zichzelf deden zodat Allah toornig op hen is geworden en zij zullen in de straf verblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,81,'En indien zij in Allah en deze profeet en hetgeen hem werd geopenbaard hadden geloofd, zouden zij hen niet tot vrienden hebben genomen, doch velen hunner zijn ongehoorzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,82,'Waarlijk, gij zult de Joden en de afgodendienaren het meest vijandig jegens de gelovigen vinden. En gij zult degenen die zeggen: \"Wij zijn Christenen\" het vriendschappelijkst vinden jegens de gelovigen. Dit is, wijl er onder hen geleerden en monniken zijn en wijl zij niet trots zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,83,'En indien zij hetgeen deze boodschapper is geopenbaard, horen, ziet gij hun ogen vol tranen vanwege de waarheid welke zij hebben herkend. Zij zeggen: \"Onze Heer, wij geloven. Reken ons daarom onder de getuigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,84,'\"En waarom zouden wij niet in Allah en in de waarheid die tot ons is gekomen geloven en begeren dat onze Heer ons onder de rechtvaardige mensen zou rekenen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,85,'Derhalve beloonde Allah hen voor hetgeen zij zeiden met tuinen, waardoorheen rivieren stromen. Daarin zullen zij vertoeven en dit is de beloning voor hen die goeddoen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,86,'Maar de ongelovigen die Onze tekenen verloochenen zullen de bewoners der hel zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,87,'O, gij die gelooft, maakt de goede dingen die Allah voor u wettig heeft gemaakt, niet onwettig en overtreedt niet. Waarlijk, Allah heeft de overtreders niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,88,'En eet wat goed en geoorloofd is waarvan Allah u heeft voorzien. En vreest Allah in Wie gij gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,89,'Allah zal u niet ter verantwoording roepen voor uw ijdele eden, maar Hij zal u ter verantwoording roepen voor de eden welke gij in ernst aflegt. De boetedoening er voor is: tien armen te spijzigen met het gemiddelde voedsel waarmede gij uw huisgezinnen voedt, of hen te kleden, of het vrijmaken van een slaaf. Maar wie dat niet kan doen zal drie dagen vasten. Dit is de boete voor uw eden, wanneer gij zweert. Maar houdt uw eden. Zo legt Allah u Zijn tekenen uit, opdat gij dankbaar moogt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,90,'O gij die gelooft, wijn en het hazardspel en afgoden en toverpijlen zijn niet anders dan gruwelen, door Satan gewrocht. Vermijdt ze dus, opdat gij voorspoedig moogt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,91,'Voorzeker, door middel van wijn en hazardspel, wenst Satan onder u vijandschap en afgunst te zaaien en u af te houden van het gedenken van Allah en van het gebed. Zult gij dan worden weerhouden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,92,'En gehoorzaamt Allah en gehoorzaamt de boodschapper en weest op uw hoede. Maar indien gij u afwendt, weet dan, dat op Onze boodschapper slechts het duidelijk verkondigen rust.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,93,'Op de gelovigen die goede werken verrichten zal geen zonde rusten voor hetgeen zij eten mits zij Allah vrezen en geloven en goede werken doen en nogmaals vrezen en geloven en zelfs nogmaals vrezen en goeddoen. En Allah heeft degenen die goeddoen lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,94,'O, gij gelovigen, voorzeker, Allah zal u beproeven door het wild, hetwelk uw handen of lansen kunnen vangen, opdat Allah degenen zal onderscheiden die Hem in het verborgene vrezen. Derhalve zal voor hen, die na deze (waarschuwing) overtreden, een pijnlijke straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,95,'O, gij die gelooft, doodt geen wild, terwijl gij ter bedevaart zijt. En wie onder u het opzettelijk doodt diens vergoeding is een huisdier gelijk aan hetgeen hij heeft gedood - twee rechtvaardige mannen onder u zullen dat beoordelen; - hetwelk als offer naar de Kaba moet worden gebracht; of hij moet als boetedoening (een aantal) arme mensen voeden, of een gelijk aantal dagen vasten, opdat hij het gevolg van zijn daad zal ondergaan. Allah heeft vergeven wat voorbij is, maar wie er in terugvalt, hem zal Allah straffen. Allah is Machtig, de Meester der vergelding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,96,'De vangst uit zee en het eten ervan is wettig voor u als voorziening voor u zelf en de reizigers, doch zolang gij ter bedevaart zijt is het wild van het land u verboden. En vreest Allah, tot Wie gij zult worden verzameld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,97,'Allah heeft de Kaaba, het onschendbare Huis tot behoud van de mensheid gemaakt, alsook de heilige maand en het offer, en de kamelen met de halsbanden. Dit is, opdat gij zult begrijpen, dat Allah weet, wat in de hemelen en wat op aarde is en dat Allah kennis heeft van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,98,'Weet, dat Allah streng is in het straffen en dat Allah (ook) Vergevensgezind, Genadevol is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,99,'Op de boodschapper rust slechts (de plicht van) het overbrengen (der boodschap). En Allah weet, wat gij openbaart en wat gij verbergt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,100,'Zeg: \"De bozen en de goeden zijn niet gelijk, ofschoon de overvloed der bozen u in verwondering brengt. Vreest daarom Allah, o mensen van begrip, opdat gij moogt slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,101,'O, gij die gelooft, vraagt niet naar dingen die u, als zij u zullen worden geopenbaard, zullen mishagen; indien gij er naar vraagt terwijl de Koran wordt nedergezonden, zullen zij u worden onthuld. Allah heeft ze achterwege gelaten. En Allah is Vergevensgezind, Verdraagzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,102,'V��r u stelde een volk vragen omtrent zo iets - naderhand werden zij er ongelovigen door.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,103,'Allah heeft geen Bahira, Saiba, Wasila of Haam verordend, maar de ongelovigen verzinnen een leugen tegen Allah en de meesten hunner begrijpen dit niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,104,'En wanneer er tot hen wordt gezegd: \"Komt tot hetgeen Allah heeft geopenbaard en tot de boodschapper,\" zeggen zij: \"Voor ons is datgene waarin wij onze vaderen zagen geloven, voldoende.\" Zelfs indien hun vaderen niets wisten en geen leiding hadden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,105,'O, gij die gelooft, past op uzelf. Hij die dwaalt kan u niet schaden wanneer gij juist geleid zijt. Tot Allah zult gij allen terugkeren, dan zal Hij u tonen wat gij gedaan hebt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,106,'O, gij die gelooft, wanneer de dood ��n uwer nadert, ten tijde dat gij een testament maakt, zal er een getuigenis zijn van twee uwer rechtvaardige mannen; of van twee anderen die niet van uit uw midden zijn indien gij door het land reist en de rampspoed des doods u overvalt. Indien gij twijfelt, houdt hen na het gebed en laat hen zweren bij Allah, zeggende: \"Wij nemen hier geen waarde voor in ruil, hoewel hij een bloedverwant is, wij verbergen Allah´s getuigenis niet, wij zouden in dat geval tot de zondaars behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,107,'Maar indien ontdekt wordt, dat de twee schuldig zijn aan zonde dan moeten twee anderen hun plaats innemen uit het midden van degenen, tegen wie de twee eersten hebben getuigd; en de laatste twee moeten bij Allah zweren en zeggen: \"Waarlijk, ons getuigenis is oprechter dan de getuigenis van hen (de eersten) en wij zijn geen overtreders, want dan zouden wij inderdaad tot de onrechtvaardigen behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,108,'Dit zal hen eerder getuigenis doen afleggen naar de feiten, of hen doen vrezen, dat andere eden na hun eden zullen worden afgelegd. En vreest Allah en luistert. En Allah leidt het ongehoorzame volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,109,'Gedenkt de dag, waarop Allah de boodschappers zal verzamelen en zeggen: \"Hoe werd gij aangenomen?\" Zij zullen zeggen: \"Wij hebben geen kennis, Gij alleen zijt de Oerkenner van het verborgene.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,110,'Wanneer Allah zal zeggen: \"O Jezus, zoon van Maria, gedenk Mijn gunst aan u en uw moeder, toen Ik u met de geest van heiligheid versterkte, dat gij als kind en op middelbare leeftijd tot het volk spraakt en toen Ik u het Boek en de wijsheid en de Torah en het Evangelie onderwees en toen gij door Mijn gebod uit klei de vorm van een vogel maakte, dan er in blies en het een vogel werd door Mijn gebod; en toen gij de blinden en de melaatsen door Mijn gebod hebt genezen en de doden opgewekt; en toen Ik de kinderen Isra�ls er van weerhield, (u te doden), toen gij met duidelijke tekenen tot hen kwaamt en degenen onder hen die verwierpen, zeiden: \"Dit is niets, dan klaarblijkelijke tovenarij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,111,'\"En toen Ik de discipelen bezielde om in Mij en Mijn boodschapper te geloven, zeiden zij: \"Wij geloven en getuigt Gij, dat wij ons hebben onderworpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,112,'Toen de discipelen zeiden: \"O, Jezus, zoon van Maria, is uw Heer bij machte, ons een (met voedsel) gedekte tafel van de hemel neder te zenden?\", antwoordde hij: \"Vreest Allah, als gij gelovigen zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,113,'Zij zeiden: \"Wij verlangen zeer, er van te mogen eten zodat ons hart gerustgesteld moge worden en wij mogen weten dat gij de waarheid tot ons hebt gesproken en wij daarvan getuigen mogen zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,114,'Jezus, de zoon van Maria, zeide: \"O Allah, onze Heer, zend ons een (met voedsel) gedekte tafel van de hemel neder, opdat het voor de eersten en de laatsten onzer een feest moge zijn en een teken van U en tot onderhoud van ons, want Gij zijt de Beste der onderhouders.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,115,'Allah zeide: \"Waarlijk, Ik zal haar (de tafel) tot u nederzenden, maar wie uwer nadien ondankbaar wordt, zal Ik z� straffen als Ik geen ander onder de volkeren gestraft heb.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,116,'En wanneer Allah zal zeggen: \"O Jezus, zoon van Maria, hebt gij tot de mensen gezegd: ´Beschouwt mij en mijn moeder als twee Goden naast Allah,´? zal hij antwoorden: \"Heilig zijt Gij! Ik zou nooit kunnen zeggen, waarop ik geen recht had. Indien ik het had gezegd zoudt Gij het zeker hebben geweten. Gij weet, wat in mijn innerlijk is en ik weet niet, wat in U is. Gij zijt de Kenner van het onzienlijke.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,117,'Ik zeide niets tot hen, dan hetgeen Gij mij hebt geboden: \"Aanbidt Allah, mijn Heer en uw Heer.\" En ik was getuige van hen, zolang ik in hun midden verbleef, maar nadat Gij mij deedt sterven, waart Gij de Bewaker over hen en Gij zijt Getuige van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,118,'Indien Gij hen straft, zijn zij Uw dienaren en indien Gij hen vergeeft, zijt Gij zeker de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,119,'Allah zal zeggen: \"Dit is een dag waarop waarachtigheid de waarachtigen zal baten. Voor hen zijn tuinen, waar doorheen rivieren stromen; zij zullen daarin voor eeuwig vertoeven.\" Allah heeft behagen in hen en zij hebben behagen in Hem, dit is de grote zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,5,120,'Aan Allah behoort het koninkrijk der hemelen en der aarde en wat daartussen is en Hij heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,1,'Alle lof komt Allah toe, Die de hemelen en de aarde schiep en de duisternis en het licht deed ontstaan; toch stellen de ongelovigen gelijken naast hun Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,2,'Hij is het, Die u uit klei schiep en daarna een termijn bepaalde. De vastgestelde termijn is bij Hem. Toch twijfelt gij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,3,'En Hij is Allah in de hemelen en op aarde. Hij kent uw innerlijk en uw uiterlijk en Hij weet, wat gij verdient.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,4,'En er komt van de tekenen van hun Heer geen teken tot hen of zij wenden zich er van af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,5,'Zij hebben de waarheid verloochend toen deze tot hen kwam, maar de tijdingen waarover zij spotten zullen hen weldra bereiken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,6,'Zien zij niet, hoeveel geslachten Wij v��r hen hebben vernietigd? Wij hadden hun zulke macht op de aarde gegeven als Wij u niet hebben geschonken en Wij zonden wolken over hen die regelmatig regen deden neerstromen en Wij deden rivieren onder hen vloeien; daarna vernietigden Wij hen vanwege hun zonden en deden een ander geslacht na hen ontstaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,7,'En al hadden Wij u een boek op perkament nedergezonden en al hadden zij het met hun handen betast, zouden de ongelovigen toch hebben gezegd: \"Dit is niets dan klaarblijkelijke tovenarij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,8,'Zij zeggen: \"Waarom is er geen engel tot hem (de Profeet) nedergezonden?\" En indien Wij een engel zenden dan wordt de zaak afgedaan en er wordt hun geen uitstel gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,9,'En als Wij een engel hadden aangesteld, zouden Wij hem als mens hebben doen voorkomen en zo zouden Wij hetgeen zij verwarren, voor hen nog verwarder hebben gemaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,10,'En voorzeker de boodschappers v��r u werden ook bespot, zo trof diegenen die bespotten, de straf voor hetgeen zij bespotten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,11,'Zeg: \"Gaat op aarde rond en ziet, wat het einde was der loochenaars.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,12,'Zeg: \"Aan wie behoort hetgeen in de hemelen en op aarde is?\" Zeg: \"Aan Allah.\" Hij heeft het op Zich genomen, barmhartigheid te tonen. Voorzeker Hij zal u op de Dag der Opstanding verzamelen, daaraan is geen twijfel. Zij, die hun zielen hebben tekort gedaan, geloven niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,13,'Aan Hem behoort wat in nacht en dag bestaat. En Hij is de Alhorende, Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,14,'Zeg: \"Zal ik een andere Beschermer nemen, dan Allah, de Schepper der hemelen en der aarde, Die voedt en niet wordt gevoed?\" Zeg: \"Het is mij bevolen, de eerste te zijn die zich onderwerpt.\" En behoort niet tot de afgodendienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,15,'Zeg: \"Ik vrees, als ik mijn Heer niet gehoorzaam, de straf van de grote Dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,16,'Van wie deze straf op die Dag is afgewend, Allah heeft hem inderdaad barmhartigheid betoond. En dat is een klaarblijkelijke overwinning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,17,'En als Allah u door schade treft, is er niemand die dit kan afwenden dan Hij; en als Hij u met weldaad omringt - Hij heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,18,'Hij is de Oppermachtige over Zijn dienaren en Hij is de Alwijze, en van alles op de hoogte.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,19,'Zeg: \"Wie is het gewichtigst als getuige?\" Zeg: \"Allah is getuige tussen u en mij. En deze Koran is mij geopenbaard, opdat ik u en wie hij bereikt, moge waarschuwen. Getuigt gij werkelijk dat er andere goden buiten Allah zijn?\" Zeg: \"Ik getuig niet.\" Zeg: \"Hij is de ene God en ik heb niets uitstaande met datgene wat gij met Hem vereenzelvigt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,20,'Degenen, wie Wij het Boek gaven, erkennen hem (de Profeet), zoals zij hun kinderen erkennen. Maar zij, die hun ziel hebben tekort gedaan, willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,21,'En wie is onrechtvaardiger dan hij, die een leugen tegen Allah uitdenkt of Zijn tekenen verloochent? Voorzeker, de onrechtvaardigen zullen niet slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,22,'(Gedenk) de Dag, waarop Wij hen allen zullen verzamelen, dan zullen Wij zeggen tot degenen, die afgoderij pleegden: \"Waar zijn uw mededingers, die gij beweerdet (te bezitten)?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,23,'Dan zal hun antwoord niet anders zijn dan dat zij zeggen: \"Bij Allah, onze Heer; wij waren geen afgodendienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,24,'Zie, hoe zij tegen zichzelven liegen en hoe hetgeen zij plachten te verzinnen voor hen verloren is gegaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,25,'Er zijn sommigen hunner, die naar u luisteren, maar Wij hebben sluiers om hun hart gelegd en hun oren verstopt, zodat zij niet begrijpen. En al zagen zij elk teken, zouden zij er toch niet in geloven; wanneer zij tot u komen redetwisten zij met u, en de ongelovigen zeggen: \"Dit zijn niets dan fabelen der ouden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,26,'En zij verbieden (anderen) en blijven er zelt verre van. En zij deren niemand dan zichzelven, zij bemerken het echter niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,27,'En als gij het slechts zoudt kunnen zien, wanneer zij voor het Vuur zullen worden gebracht! Zij zullen dan zeggen: \"O, mochten wij slechts worden teruggezonden, dan zouden wij de tekenen van onze Heer niet meer verloochenen en wij zouden tot de gelovigen behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,28,'Neen, hetgeen zij voorheen plachten te verbergen is hun duidelijk geworden. En als zij zouden worden teruggezonden zoudden zij gewis tot hetgeen hen was verboden terugkeren; Voorzeker zij zijn leugenaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,29,'En zij zeggen: \"Er is niets dan ons leven van deze wereld en wij kunnen niet worden opgewekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,30,'En wanneer gij het slechts zoudt kunnen zien, wanneer zij voor hun Heer zullen worden gebracht, zal Hij zeggen: \"Is dit niet de waarheid?\" Zij zullen antwoorden: \"Ja zeker, bij onze Heer.\" Hij zal zeggen: \"Ondergaat dan de straf, omdat gij placht te verwerpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,31,'Voorzeker, zij, die de ontmoeting met Allah verloochenen, benadelen zich, totdat het uur onverwachts over hen komt, en zij zullen zeggen: \"O, wij hebben wroeging, vanwege onze tekortkoming hierin.\" En zij zullen hun lasten op hun ruggen dragen. Ziet toe, wat zij dragen is zeker slecht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,32,'Het wereldse leven is niets dan een spel en een ijdel vermaak. Doch voor degenen die God vrezen, is het tehuis van het Hiernamaals beter. Wilt gij dan niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,33,'Wij weten zeer goed dat hetgeen zij zeggen u verdriet doet, doch zij verloochenen u (profeet) niet, maar het zijn de tekenen van Allah die de boosdoeners verwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,34,'Gewis, de boodschappers v��r u werden ook verloochend en gekweld, niettemin bleven zij geduldig in datgene, waarvoor zij waren verloochend; totdat onze hulp tot hen kwam. Er is niemand die de woorden van Allah kan veranderen. En er zijn reeds tijdingen omtrent die boodschappers tot u gekomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,35,'En als hun afkeer u onverdraaglijk is, breng hun dan een teken, indien gij een opening in de aarde of een ladder naar de hemelen kunt vinden. En indian Allah wilde zou Hij hen zeker onder ��n leiding hebben verzameld. Behoor dus niet tot de onwetenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,36,'Alleen degenen die luisteren, kunnen aannemen. De doden zal Allah opwekken en dan zullen zij tot Hem worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,37,'En zij zeggen: \"Waarom is er over hem geen teken van zijn Heer nedergezonden?\" Zeg: \"Voorzeker, Allah heeft de macht om een teken neder te zenden.\" Maar de meesten hunner beseffen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,38,'En er is geen beest dat op de aarde kruipt, noch een vogel die op zijn vleugels vliegt, of zij vormen gemeenschappen, zoals gij. Wij hebben niets uit het Boek weggelaten. Dan zullen zij tot hun Heer tezamen worden gebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,39,'Zij, die Onze tekenen verloochenen, zijn doof en stom, in de duisternis. Allah laat wie Hij wil dwalen en Hij plaatst op het rechte pad wie Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,40,'Zeg: \"Wat denkt gij? Als de straf van Allah, of het uur over u komt, zult gij dan iemand anders aanroepen, dan Allah, als gij waarachtig zijt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,41,'\"Neen, Hem alleen zult gij aanroepen; dan zal Hij datgene verwijderen waarvoor gij Hem aanroept als Hij dat wil en gij zult uw afgoderij vergeten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,42,'Wij zonden inderdaad tot de volkeren die v��r u waren, (een profeet) toen troffen Wij hen (die volkeren) met armoede en tegenspoed opdat zij zich mochten verootmoedigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,43,'Waarom verootmoedigden zij zich niet toen Onze straf over hen kwam? Maar hun hart was verhard en Satan deed hun schoon schijnen al hetgeen zij verrichtten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,44,'Toen zij dan hetgeen waarvoor zij waren gewaarschuwd vergaten, openden Wij hun de poorten van alle dingen (der wereld) totdat zij verheugd werden over hetgeen hun was gegeven, dan grepen Wij hen onverwachts aan en zie, zij werden wanhopig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,45,'Zo werd de levensader van de mensen, die slecht handelden, afgesneden. Alle lof komt Allah toe, de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,46,'Zeg: \"Wat denkt gij? Als Allah uw gehoor en gezicht zou wegnemen en uw hart zou verzegelen, wie is dan God buiten Allah die het u kon teruggeven? Zie, hoe Wij de tekenen verklaren, toch wenden Zij zich af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,47,'Zeg: \"Wat denkt gij? Als de straf van Allah onverwachts of openlijk tot u komt, zal iemand anders dan het onrechtvaardige volk vernietigd worden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,48,'Wij zenden de boodschappers alleen als dragers van blijde tijding en als waarschuwers. Over degenen, die geloven en zich verbeteren, zal geen vrees komen noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,49,'En degenen, die Onze tekenen verloochenen, hen zal straf raken, omdat zij niet gehoorzaam waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,50,'Zeg: \"Ik zeg niet tot u, dat ik de schatten van Allah bezit, noch dat ik het onzienlijke ken, noch zeg ik tot u: ´Ik ben een engel´; ik volg slechts hetgeen mij wordt geopenbaard.\" Zeg: \"Kunnen de blinde en de ziende gelijk zijn? Wilt gij dan niet nadenken?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,51,'Waarschuw daarmede degenen die vrezen, dat zij tot hun Heer worden verzameld, dat zij buiten Hem vriend noch bemiddelaar hebben, opdat zij (God) mogen vrezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,52,'En verdrijf niet degenen die hun Heer morgen en avond aanroepen, Zijn aangezicht zoekend. Gij zijt volstrekt niet verantwoordelijk voor hen, noch zijn zij enigermate verantwoordelijk voor u. Zoudt gij hen verdrijven, dan zult gij tot de onrechtvaardigen behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,53,'En op deze wijze hebben Wij sommigen hunner door anderen beproefd, zodat zij kunnen zeggen: \"Zijn dezen het, die Allah onder ons heeft begunstigd?\" Kent Allah degenen die dankbaar zijn niet het beste?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,54,'Wanneer degenen die in Onze tekenen geloven, tot u komen, zeg dan: \"Vrede zij u.\" Uw Heer heeft barmhartigheid op zich genomen; dus wie uwer in onwetendheid kwaad doet en daarna berouw heeft en zich verbetert, (voor hem) is Hij Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,55,'En zo zetten Wij de tekenen uiteen opdat de weg der schuldigen openbaar worde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,56,'Zeg: \"Het is mij verboden degenen, die gij naast Allah aanroept, te aanbidden. Zeg: \"Ik wil uw boze neigingen niet volgen. In dat geval zal ik tot de dwalenden behoren en niet tot hen die het rechte pad volgen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,57,'Zeg: \"Ik ben op de rechte weg van mijn Heer en gij verloochent die. Maar wat gij verhaast is niet in mijn macht. De beslissing berust slechts bij Allah. Hij zet de waarheid uiteen en Hij is de beste der seheidsrechters.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,58,'Zeg: \"Als hetgeen gij verhaast in mijn macht was, zou de zaak voorzeker tussen u en mij reeds zijn beslist. En Allah kent de onrechtvaardigen met beste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,59,'En bij Hem zijn de sleutels van het onzienlijke; niemand kent dit, behalve Hij. En Hij weet wat op het land en wat in de zee is. En er valt geen blad zonder dat Hij het weet, noch is er een korrel in de duisternis der aarde, noch iets dat groen of droog is, zonder dat het in een duidelijk Boek is vermeld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,60,'Hij is het, Die uw ziel in de nacht neemt en weet hetgeen gij overdag doet; daarna wekt Hij u weder op, opdat de vastgestelde termijn moge worden voltooid. Dan is uw terugkeer tot Hem. Daarna zal Hij u inlichten over hetgeen gij deedt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,61,'Hij is oppermachtig over Zijn dienaren en Hij zendt bewakers over u, totdat, wanneer de dood tot een uwer komt, Onze boodschappers zijn ziel wegnemen; zij falen daarin niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,62,'Dan worden zij tot Allah, hun ware Heer teruggebracht. Voorzeker, de beslissing ligt in Zijn handen; en Hij verrekent het snelst.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,63,'Zeg: \"Wie verlost u van de rampen van het land en van de zee wanneer gij Hem in nederigheid en in het geheim aanroept? (zeggende): ´Indien Hij ons hiervan redt zullen wij zeker tot de dankbaren behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,64,'Zeg: \"Allah verlost u van deze en van elke andere nood en toch schrijft gij deelgenoten (medegoden) aan Hem toe.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,65,'Zeg: \"Hij heeft macht om u van boven of van onder u straf toe te zenden, u in groepen te verdelen en elkander geweld aan te laten doen.\" Zie, hoe Wij de tekenen uiteenzetten opdat zij mogen begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,66,'En uw volk heeft het verworpen, ofschoon het de waarheid is. Zeg: \"Ik ben geen voogd over u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,67,'Er is voor elke profetie een vastgestelde tijd en gij zult het weldra te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,68,'Wanneer gij degenen ziet, die Onze tekenen bespotten, wendt u dan van hen af, totdat zij een ander gesprek beginnen. En als Satan het u doet vergeten zit dan niet, nadat het in uw herinnering opkomt, met het onrechtvaardige volk bijeen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,69,'En degenen die God vrezen, zijn in het geheel niet verantwoordelijk voor hen, behalve voor de vermaning, opdat zij behoed zullen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,70,'Laat degenen die hun geloof tot een spel en tijdverdrijf hebben gemaakt en wie het wereldse leven heeft bedrogen, met rust. En waarschuw hiermee, opdat een ziel niet moge worden overgeleverd voor hetgeen zij heeft gedaan. Zij zal naast Allah geen helper of bemiddelaar hebben. En indien zij (zelfs) alles als losprijs zou aanbieden, zal deze van haar niet worden aanvaard. Dezen zijn het, die zijn overgeleverd voor hetgeen zij verdienden. Zij zullen een drank van kokend water en een smartelijke straf ontvangen, omdat zij verwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,71,'Zeg: \"Zullen wij naast Allah datgene aanroepen wat ons noch bevoordelen noch schaden kan, dan worden wij, nadat Allah ons heeft geleid, van het rechte pad verwijderd, zoals iemand die de bozen hebben neergeveld op de aarde in een toestand van verbijstering en die metgezellen heeft die hem tot de weg roepen, zeggende: ´Kom tot ons´?\" Zeg: \"De leiding van Allah is voorzeker de enige leiding en het is ons bevolen ons aan de Heer der Werelden te onderwerpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,72,'En: \"Onderhoudt het gebed en vreest Hem, tot Wie gij zult worden verzameld.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,73,'En Hij is het, Die de hemelen en de aarde in werkelijkheid schiep. En de dag, waarop Hij zegt: \"Wees\", wordt het. Zijn woord is werkelijkheid; en aan Hem behoort het koninkrijk op de Dag waarop de bazuin zal worden geblazen. De Kenner v an het onzichtbare en het zichtbare. Hij is de Alwijze, de Al- kennende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,74,'Toen Abraham tot zijn vader Azar zeide: \"Neemt gij afgoden tot Goden? Ik zie u en uw volk in duidelijke dwaling.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,75,'Zo toonden Wij Abraham het koninkrijk der hemelen en der aarde, opdat hij tot de vastgelovenden zou behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,76,'En toen de nacht over hem kwam, zag hij een ster. Hij zeide: \"Dit is mijn Heer.\" Maar toen zij onderging, zeide hij: \"Ik heb de dingen, die ondergaan niet lief.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,77,'En toen hij de maan zag glanzen, zeide hij: \"Dit is mijn Heer.\" Maar toen zij onderging zeide hij: \"Had mijn Heer mij niet geleid dan zou ik zeker tot het dwalende volk behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,78,'En toen hij de zon zag stralen zeide hij: \"Dit is mijn Heer. Dit is de grootste\" Maar toen zij onderging, zeide hij: \"O, mijn volk, ik heb niets uitstaande met uw afgoden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,79,'\"Ik heb mijn aangezicht oprecht gewend tot Hem, Die de hemelen en de aarde schiep en ik behoor niet tot de afgodendienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,80,'En zijn volk redetwistte met hem. Hij zeide: \"Redetwist gij met mij omtrent Allah, terwijl Hij mij recht heeft geleid? En ik vrees hetgeen gij met Hem vereenzelvigt niet, tenzij mijn Heer iets wenst. Mijn Heer omvat alle dingen in Zijn kennis. Wilt gij er dan geen lering uit trekken?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,81,'\"En hoe kan ik uw afgoden vrezen, terwijl gij zelf uw afgoderij niet vreest waarvoor Allah u geen gezag heeft nedergezonden? Wie van de twee partijen is dan veiliger, als gij dat weet?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,82,'Zij die geloven en hun geloof niet met onrechtvaardigheid vermengen - dezen zijn het, die vrede zullen hebben want zij zijn recht geleid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,83,'En dit is onze bewijsgrond die Wij Abraham tegen zijn volk gaven. Wij verheffen graadsgewijze, wie Wij willen. Voorzeker, Uw Heer is Alwijs, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,84,'En Wij gaven hem Iza�k en Jacob; Wij leidden elk hunner en voordien leidden Wij Noach en van zijn afstammelingen: David, Salomo, Job, Jozef, Mozes en A�ron. Zo belonen Wij de goeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,85,'En Zacharia, Johannes, Jezus en Elias. Elk hunner behoorde tot de deugdzamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,86,'En Isma�l, Elisa, Jonas en Lot; elk hunner verhieven Wij boven de volkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,87,'En van hun vaderen en hun kinderen en hun broederen verkozen Wij enigen en leidden hen op het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,88,'Dit is de leiding van Allah, Hij leidt daarmede van Zijn dienaren, wie Hij wil. En, indien zij iets naast Hem hadden aanbeden, zou voorzeker al hetgeen zij plachten te doen, verloren zijn gegaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,89,'Dezen zijn het, wie Wij het Boek en de heerschappij en het profetenambt gaven. Maar nu dezen er ondankbaar voor zijn, hebben Wij deze aan een volk toevertrouwd dat er niet ondankbaar voor zal zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,90,'Dezen zijn het, die Allah juist heeft geleid; volgt daarom hun leiding. Zeg: \"Ik vraag u er geen beloning voor. Dit is niets dan een vermaning aan alle volkeren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,91,'En zij schatten de juiste waarde van Allah niet wanneer zij zeggen: \"Allah heeft aan niemand iets geopenbaard.\" Zeg: \"Wie openbaarde het Boek dat Mozes bracht als licht en leiding voor de mensen - dat gij op papieren schrijft, en bekend maakt, terwijl gij toch veel verbergt en (waardoor) aan u is onderwezen, hetgeen gij noch uw vaderen wisten?\" - Zeg: \"Allah\". Laat hen dan met rust om zich met hun ledig spel te vermaken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,92,'En dit Boek vol zegeningen, hebben Wij geopenbaard, vervullende, hetgeen er aan voorafging, opdat gij de moeder der steden (Mekka) en wat er omheen is zoudt waarschuwen. En degenen die in het Hiernamaals geloven, geloven er in en zij waken over hun gebed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,93,'En wie is onrechtvaardiger dan hij die een leugen over Allah uitdenkt of zegt: \"Het is mij geopenbaard,\" terwijl hem niets is geopenbaard en die zegt: \"Ik zal iets nederzenden dat gelijk is aan hetgeen Allah heeft nedergezonden?\" O, kondet gij het waarnemen, wanneer de onrechtvaardigen in doodsstrijd zijn en de engelen hun handen uitstrekken, (zeggende): \"Geeft uw zielen op. Deze dag zal u de straf der schande worden toegekend, voor hetgeen gij ten onrechte tegen Allah zeidet en omdat gij u hoogmoedig van Zijn tekenen afwenddet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,94,'Nu zijt gij ��n voor ��n tot Ons gekomen zoals Wij u eerst schiepen en gij hebt, hetgeen Wij u schonken achter u gelaten en Wij zien de bemiddelaren, waarvan gij beweerdet dat zij deelgenoten waren in uw zaken, niet bij u. Voorzeker is nu (de band) tussen u afgesneden en hetgeen gij placht te beweren is verloren gegaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,95,'Voorwaar, het is Allah die de graankorrel en de dadelpit doet uitspruiten. Hij brengt de levenden uit de doden voort en is de Voortbrenger van doden uit de levenden. Dat is Allah, waarheen wordt gij dan afgewend?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,96,'Hij doet de dag aanbreken en Hij heeft de nacht voor rust ingesteld en de zon en de maan voor het uitrekenen (der jaargetijden). Dat is de ordening van de Almachtige, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,97,'Hij is het, Die de sterren voor u heeft gemaakt, opdat gij daardoor de juiste richting in de duisternissen van het land en van de zee moogt volgen. Wij hebben de tekenen uitgelegd aan een volk, dat kennis bezit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,98,'En Hij is het, Die u van uit ��n ziel heeft voortgebracht en er is een verblijfplaats en een bewaarplaats voor u. Wij hebben de tekenen verklaard aan een volk dat begrijpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,99,'En Hij is het, Die water uit de wolken nederzendt en daardoor elke soort van groei voortbrengt. En evenzo brengen Wij daarmee groen, waarvan Wij korenaren voortbrengen. En er komen uit de scheden van de dadelpalm laaghangende trossen. En Wij (brengen er) wijngaarden en de olijf en de granaatappel (mee voort) van gelijke en ongelijke soort. Kijkt naar het fruit ervan, wanneer het vrucht zet en naar het rijpen daarvan. Hierin zijn voorzeker tekenen voor een volk dat (wil) geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,100,'En zij houden de djinn voor deelgenoten van Allah ofschoon Hij dezen schiep; en zij dichten Hem, zonder kennis, zonen en dochters toe. Heilig is Hij en verheven boven hetgeen zij Hem toeschrijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,101,'Wondere Schepper der hemelen en der aarde. Hoe kan Hij een zoon hebben, wanneer Hij geen gemalin heeft? Hij heeft alles geschapen; en Hij is de Kenner van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,102,'Zo is Allah, uw Heer. Er is geen God naast Hem, (Hij is) de Schepper aller dingen, aanbidt Hem Want Hij is de Voogd over alles.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,103,'Ogen kunnen Hem niet bereiken; maar Hij bereikt de ogen. Want Hij is de Ontastbare, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,104,'\"Er zijn inderdaad bewijzen van uw Heer tot u gekomen, wie dus ziet het is voor hemzelf en wie blind wordt het is tegen hemzelf. En ik ben geen bewaker over u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,105,'En zo zetten Wij de tekenen uiteen, zodat zij zeggen: \"Gij hebt het geleerd (van iemand)\", en opdat Wij het aan een volk dat kennis heeft, mogen duidelijk maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,106,'Volg, hetgeen u van uw Heer is geopenbaard: er is geen God naast Hem; en wend u van de afgodendienaren af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,107,'En als Allah had gewild, zouden zij geen goden hebben opgericht. Wij hebben u (de Profeet) geen bewaker over hen gemaakt, noch zijt gij voogd over hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,108,'En scheldt degenen, die zij naast Allah aanroepen niet uit, anders zullen zij uit nijd in hun onwetendheid Allah uitschelden. Zo hebben Wij voor elk volk hun daden schoon doen schijnen. Dan zullen zij tot hun Heer terugkeren en Hij zal hen inlichten over hetgeen zij plachten te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,109,'En zij zweren hun sterkste eden bij Allah, dat, indien er een teken tot hen zou komen, zij er gewis in zouden geloven. Zeg: \"Voorzeker, de tekenen zijn bij Allah.\" En wat weet gij: Wanneer de tekenen komen, zullen zij stellig niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,110,'En Wij zullen hun hart en ogen in verwarring brengen, omdat zij er voor de eerste keer niet in geloofden en Wij zullen hen in hun overtreding blindelings laten dwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,111,'En zelfs al zonden Wij engelen tot hen neder en al spraken de doden tot hen en Wij verzamelden voor hen alle dingen van aangezicht tot aangezicht, zij zouden er niet in geloven, tenzij Allah dit wilde. Maar de meesten hunner gedragen zich onwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,112,'Op dezelfde wijze hebben Wij een vijand voor elke profeet gemaakt, bozen van onder de mensen en de djinn. Zij fluisteren elkander vergulde woorden in om te bedriegen - en als uw Heer had gewild, zouden zij het niet hebben gedaan; laat hen daarom met rust met hetgeen zij verzinnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,113,'En opdat de harten dergenen die niet in het Hiernamaals geloven er zich toe neigen en zij er tevreden mee mogen zijn en dat zij mogen verdienen hetgeen zij willen verdienen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,114,'Zal ik als rechter iemand anders zoeken dan Allah, terwijl Hij het is, Die u het Boek heeft nedergezonden dat uitvoerig is verklaard? En degenen, wie Wij het Boek gaven weten dat het van uw Heer is nedergezonden met de waarheid; behoort daarom niet tot degenen die twijfelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,115,'En het woord van uw Heer is in waarheid en rechtvaardigheid vervuld. Niemand kan Zijn woorden veranderen; Hij is de Alhorende, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,116,'En als gij het merendeel dergenen die op aarde zijn, volgt, zullen zij u van Allah´s weg doen afdwalen. Zij volgen slechts vermoedens en zij doen niets dan gissen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,117,'Voorzeker, uw Heer weet het beste wie van Zijn weg afdwaalt en Hij kent degenen, die recht geleid zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,118,'Eet daarom van hetgeen waarover de naam van Allah is uitgesproken als gij in Zijn tekenen gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,119,'En welke reden hebt gij, dat gij niet van datgene zoudt eten, waarover de naam van Allah is uitgesproken, terwijl Hij u reeds heeft uitgelegd wat Hij u heeft verboden, - met uitzondering van datgene waartoe gij gedwongen zijt. En voorzeker, velen misleiden door hun boze neigingen zonder kennis. Waarlijk, uw Heer kent de overtreders het beste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,120,'En schuwt openlijke, alsmede geheime zonden. Gewis, degenen die zonden begaan, zal voor hetgeen zij doen, worden vergolden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,121,'En eet niet van hetgeen, waarover de naam van Allah niet is uitgesproken, want dat is zeker ongehoorzaamheid. En de bozen sporen hun vrienden aan opdat zij met u mogen redetwisten. Als gij hen gehoorzaamt zult gij inderdaad afgodendienaren zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,122,'Is hij, die dood was en wie Wij het leven gaven en voor wie Wij een licht maakten waardoor hij onder de mensen wandelt, gelijk aan hem, wiens toestand zodanig is dat hij in de duisternissen verblijft waaruit hij niet kan wegkomen? Zo werd voor de ongelovigen schoonschijnend gemaakt hetgeen zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,123,'En zo hebben Wij in elke stad de groten tot haar schuldigen gemaakt zodat zij er in samenspannen en zij smeden slechts tegen hun eigen ziel, maar zij bemerken het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,124,'En wanneer er tot hen een teken komt, zeggen zij: \"Wij zullen niet geloven voordat ons hetzelfde is gegeven als hetgeen Allah´s boodschappers is gegeven.\" Allah weet het beste waar Zijn boodschapte plaatsen. Vernedering bij Allah en een strenge straf zal de overtreders voorzeker treffen, wegens hetgeen zij beramen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,125,'Wie Allah ook wenst te leiden, Hij verruimt zijn hart voor de Islam en wie Hij wenst te laten dwalen, zijn hart maakt Hij eng en gesloten alsof hij een hoogte aan het beklimmen was. Zo legt Allah degenen die niet geloven, onreinheid op.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,126,'En dit is het rechtleidende pad van uw Heer. Wij hebben de tekenen inderdaad verduidelijkt voor een volk dat er lering uit wil trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,127,'Voor hen is het Huis van Vrede (het Paradijs) bij hun Heer en Hij is hun Vriend, wegens hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,128,'De Dag, waarop Hij hen allen tezamen zal verzamelen, (zal Hij zeggen): \"O, gezelschap van djinn, gij hebt een grote hoeveelheid mensen tot u getrokken.\" En hun vrienden onder de mensen zullen zeggen: \"Onze Heer, wij hebben van elkander geprofiteerd, maar nu hebben wij de termijn welke Gij voor ons hebt vastgesteld bereikt.\" Hij zal zeggen: \"Het Vuur is uw tehuis waarin gij zult vertoeven, behalve wat Allah moge behagen.\" Voorzeker, uw Heer is Alwijs, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,129,'En op dezelfde wijze maken Wij sommigen der onrechtvaardigen tot vrienden voor de anderen, voor hetgeen zij verdienen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,130,'O, gezelschap van djinn en mensen. Kwamen er niet uit uw midden boodschappers tot u die u Mijn tekenen verhaalden en die u voor de ontmoeting van deze Dag waarschuwden? Zij zullen zeggen: \"Wij getuigen tegen onszelven.\" Het wereldse leven bedroog hen. En zij zullen tegen zichzelf getuigen, dat zij ongelovigen waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,131,'Dit komt, omdat uw Heer de steden niet onrechtvaardig wilde vernietigen, terwijl de mensen er van onbewust waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,132,'En er zijn voor allen graden overeenkomstig hetgeen zij doen en uw Heer is niet onopmerkzaam jegens hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,133,'En uw Heer is Onafhankelijk, Barmhartig. En als Hij het wil, kan Hij u wegnemen en u doen opvolgen wie Hij wil, zoals Hij u uit het nageslacht van andere mensen deed ontstaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,134,'Hetgeen u is beloofd, zal voorzeker geschieden en gij kunt het niet voorkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,135,'Zeg: \"O mijn volk, handel naar uw vermogen, ik handel ook. Gij zult weldra weten voor wie de uiteindelijke beloning van het tehuis zal zijn.\" Waarlijk de onrechtvaardigen slagen nooit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,136,'En zij hebben Allah een deel van de oogsten en van het vee aangewezen, dat Hij heeft voortgebracht en zij zeggen: \"Dit is voor Allah en dit is voor onze goden,\" zoals zij het zich denken. Maar hetgeen voor hun afgoden is, bereikt Allah niet, terwijl hetgeen voor Allah is, hun afgoden wel bereikt. Slecht is hetgeen zij oordelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,137,'Op dezelfde manier hebben voor velen der afgodendienaren hun afgoden het doden hunner kinderen schoonschijnend gemaakt, opdat zij hen mogen vernietigen en verwarring in hun godsdienst doen ontstaan. En als Allah het wilde, zouden zij dit niet hebben gedaan, laat hen daarom met rust met hetgeen zij verzinnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,138,'Zij zeggen: \"Dit en dat vee en die en die oogsten zijn verboden, niemand zal er van eten, dan wie het ons belieft\" - alzo beweren zij - en er is vee, welks ruggen verboden zijn en er is vee, waarover zij de naam van Allah niet uitspreken en zij bedenken een leugen over Hem. Hij zal hen weldra vergelden, hetgeen zij verzinnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,139,'En zij zeggen: \"Hetgeen in de baarmoeders van dit en dat vee is, is uitsluitend voor onze mannen en is onze vrouwen verboden, maar als het dood geboren wordt hebben zij allen er deel aan.\" Hij zal hen naar hun bewering belonen. Voorzeker, Hij is Alwijs, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,140,'Zij, die hun kinderen door gebrek aan kennis uit domheid doden en hetgeen, waarvan Allah hen heeft voorzien, onwettig maken, een leugen over Allah smedende, zijn inderdaad afgedwaald - noch kunnen zij recht geleid worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,141,'Hij is het, Die tuinen doet ontstaan, wel of niet gestut en de dadelpalm en de korenvelden, waarvan de vruchten van verschillende soorten zijn en de olijf en de granaatappel van gelijke en ongelijke soort. Eet de vruchten ervan wanneer zij vruchten dragen, maar betaalt op de dag van de oogst, wat Hem verschuldigd is en verkwist het niet. Voorzeker, Allah heeft de verkwisters niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,142,'En Hij schiep onder het vee lastvee en slachtvee. Eet van hetgeen Allah u heeft voorzien en volgt de voetstappen van Satan niet. Voorzeker, hij is een openlijke vijand voor u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,143,'Acht, in paren: Twee van de schapen en twee van de geiten. Zeg: \"Zijn het de twee mannelijke dieren, die Hij heeft verboden, of de twee vrouwelijke dieren, ofwel, hetgeen de baarmoeders der twee vrouwelijke dieren bevatten? Onderricht mij met zekerheid, indien gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,144,'En twee der kamelen en twee der runderen. Zeg: \"Zijn het de twee mannelijke dieren die Hij heeft verboden of de twee vrouwelijke dieren ofwel, hetgeen de baarmoeders der twee vrouwelijke dieren bevatten? Waart gij aanwezig toen Allah u dit oplegde? Wie is dan onrechtvaardiger dan hij die een leugen over Allah bedenkt om de mensen zonder kennis te doen dwalen?\" Voorzeker, Allah leidt het onrechtvaardige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,145,'Zeg: \"Ik vind in hetgeen mij is geopenbaard niets, dat een eter is verboden te eten, met uitzondering van het gestorvene of vloeiend bloed of varkensvlees, want dit alles is onrein - of, wat in overtreding is, waarover een andere naam dan Allah´s is aangeroepen. Maar wie door noodzaak wordt gedreven en niet begerig is noch de grens overschrijdt: uw Heer is dan voorzeker Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,146,'Wij verboden de Joden alle dieren die klauwen hebben en Wij verboden hun het vet van runderen, schapen en geiten, anders dan wat hun ruggen of hun ingewanden dragen of hetgeen met een been is gemengd. Dit is de vergelding, welke Wij hun voor hun opstandigheid gaven. En Wij zijn voorzeker Waarachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,147,'En indien zij u verloochenen zeg: \"Uw Heer is de Heer der alomvattende Barmhartigheid doch Zijn straf zal van het schuldige volk niet worden afgewend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,148,'Zij die afgoderij bedrijven, zullen zeggen: \"Als Allah het had gewild hadden wij noch onze vaderen afgoderij bedreven, noch hadden wij iets onwettig verklaard.\" Op dezelfde wijze loochenden ook zij die v��r hen waren, totdat zij Onze straf ondergingen. Zeg: \"Hebt gij enige kennis? Toont het ons dan. Gij volgt niets dan vermoedens en gij doet niets dan liegen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,149,'Zeg: \"Van Allah is het afdoende bewijs. Als Hij had gewild zou Hij u zeker allen hebben geleid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,150,'Zeg: \"Brengt uw getuigen die getuigenis willen afleggen, dat Allah dit heeft verboden.´´ Als zij getuigen, getuig niet met hen, noch volg de boze neigingen van degenen die Onze tekenen verloochenen en van degenen, die niet in het Hiernamaals geloven en die medegoden aan hun Heer toeschrijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,151,'Zeg: \"Komt, ik zal u verkondigen, wat uw Heer heeft verboden;\" n.l. dat gij iets met Hem vereenzelvigt en dat gij uw ouders niet goed behandelt en dat gij uw kinderen uit armoede doodt. - Wij zijn het, Die voor u en voor hen zorgen - en dat gij onbetamelijke daden hetzij openlijk of in het geheim begaat en dat gij een ziel ten onrechte doodt die Allah heilig heeft verklaard. Dit is, hetgeen Hij u heeft bevolen, opdat gij moogt begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,152,'Beheert het eigendom van de wees, voordat hij volwassen is, niet anders dan op de beste wijze. En geeft de volle maat en het volle gewicht met rechtvaardigheid. Wij belasten geen ziel boven haar vermogen. En leeft, wanneer gij spreekt, rechtvaardigheid na, zelfs wanneer het een bloedverwant betreft en vervult het verbond van Allah. Dit is, hetgeen Hij u vermaant, opdat gij er lering uit moogt trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,153,'En dit is het rechte pad dat tot Mij leidt. Volgt het daarom en volgt geen andere wegen opdat zij u niet van Mijn weg afleiden. Hiertoe vermaant Hij u, opdat gij vroom moogt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,154,'En Wij gaven Mozes het Boek, als voltooiing van de gunst aan hem die goed wilde doen en een uitleg van alle dingen en een leidraad en een barmhartigheid, opdat zij in de ontmoeting van hun Heer mochten geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,155,'En dit is een Boek dat Wij hebben nedergezonden, vol van zegeningen. Volgt het daarom en hoedt u, opdat u barmhartigheid mag worden betoond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,156,'Opdat gij niet zoudt zeggen: \"Het Boek was alleen geopenbaard voor twee volkeren die v��r ons leefden, en wij waren inderdaad met de inhoud er van onbekend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,157,'Of ingeval gij zoudt zeggen: \"Voorzeker, als ons het Boek was nedergezonden, zouden wij beter zijn geleid dan zij.\" Er is nu een duidelijk bewijs, leiding en barmhartigheid van uw Heer tot u gekomen. Wie is onrechtvaardiger dan hij die de tekenen van Allah verwerpt en er zich van afkeert? Wij zullen degenen, die zich van Onze tekenen afwenden met een vreselijke straf vergelden omdat zij zich hebben afgewend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,158,'Verwachten zij niets anders dan dat engelen tot hen zouden komen, of dat hun Heer zou verschijnen of dat enige der tekenen van uw Heer zouden plaatshebben? Op de dag, wanneer enige der tekenen van uw Heer zullen verschijnen, zal het geloven geen ziel baten die voorheen niet geloofde noch iets goeds door haar geloof verdiende. Zeg: \"Wacht, Wij wachten ook.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,159,'Degenen, die scheiding in hun godsdienst maken en zich in secten verdelen - gij hebt met hen niets uitstaande. Hun zaak rust in Allah´s handen dan zal Hij hen bekend maken met hetgeen zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,160,'Wie een goede daad verricht zal tienmaal zoveel ontvangen, maar wie een slechte daad verricht zal alleen een daaraan gelijke vergelding ontvangen; hun zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,161,'Zeg: \"Wat mij betreft, mijn Heer heeft mij op het rechte pad geleid - een goed geloof, de godsdienst van Abraham, de oprechte. En hij behoorde niet tot de afgodendienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,162,'Zeg: \"Mijn gebed en mijn offer, mijn leven en mijn dood zijn gewijd aan Allah, de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,163,'\"Hij heeft geen gelijken. Zo is mij bevolen en ik ben de eerste der Moslims.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,164,'Zeg: \"Zal ik een andere Heer begeren buiten Allah, terwijl Hij de Heer aller dingen is?\" En geen ziel handelt dan voor zichzelf alleen, noch draagt een lastdrager de last van anderen. Dan zal uw terugkeer tot uw Heer zijn en Hij zal u verklaren, waarover gij twisttet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,6,165,'En Hij is het, die u op aarde tot opvolgers maakte en Hij heeft sommigen uwer in rang boven anderen verheven, opdat Hij u door hetgeen Hij u heeft gegeven, moge beproeven. Voorzeker, uw Heer is vlug in het straffen en voorzeker, Hij is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,1,'Alif Laam Miem Saad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,2,'(Dit is) een (volmaakt) Boek, dat aan u is geopenbaard - laat er daarom in uw hart geen twijfel zijn om er mede te waarschuwen; - dit is een aanmaning voor de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,3,'Volgt hetgeen u van uw Heer is nedergezonden en volgt geen andere vrienden, dan Hem. Hoe gering is de lering, die gij trekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,4,'Hoeveel steden hebben Wij vernietigd! Onze straf overviel hen gedurende de nacht of tijdens de middagslaap.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,5,'Toen Onze Straf over hen kwam, was hun roep niet anders dan dat zij zeiden: \"Wij waren inderdaad onrechtvaardigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,6,'En Wij zullen degenen, tot wie de boodschappers waren gezonden zeker ter verantwoording roepen; en Wij zullen de boodschappers ook ondervragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,7,'Dan zullen Wij hen zeker met kennis doen weten; want Wij zijn nooit afwezig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,8,'En het wegen (der menselijke daden) zal op die Dag eerlijk zijn. Degenen, wier schalen zwaar zijn zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,9,'En zij, wier schalen licht zullen zijn, deden hun zielen tekort, omdat zij ten opzichte van Onze tekenen onrechtvaardig waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,10,'En Wij hebben u op aarde gevestigd en u daarop van middelen van bestaan voorzien. Hoe weinig dankbaar zijt gij!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,11,'Wij schiepen u, daarna vormden Wij u; toen zeiden Wij tot de engelen: \"Onderwerpt u aan Adam\" en zij onderwierpen zich, behalve Iblies; hij behoorde niet tot degenen die zich onderwierpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,12,'(Allah) zeide: \"Wat belette u, u te onderwerpen, toen Ik u (dat) gebood?\" Hij antwoordde: \"Ik ben beter dan hij. Gij hebt mij uit vuur en hem uit klei geschapen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,13,'(Allah) zeide: \"Verwijder u van hier - het is niet aan u, hier hoogmoedig te zijn. Ga heen, gij behoort stellig tot degenen, die vernederd zullen worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,14,'Hij zeide: \"Geef mij uitstel tot aan de Dag waarop zij zullen worden opgewekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,15,'(Allah) zeide: \"U is uitstel verleend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,16,'Hij antwoordde: \"Welnu, daar gij mij liet dwalen zal ik hen voorzeker in de weg gaan zitten op Uw rechte pad.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,17,'\"Dan zal ik mij gewis v��r hen en achter hen en van hun rechter en van hun linker zijde tonen en Gij zult de meesten hunner niet dankbaar vinden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,18,'(Allah) zeide: \"Ga heen, veracht en verworpen. Wie hunner u ook zal volgen, Ik zal voorzeker de hel met u allen vullen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,19,'\"O, Adam, vertoef met uw vrouw in de tuin en eet, wat gij wilt, maar nadert deze boom niet, anders zult gij tot de onrechtvaardigen behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,20,'Maar Satan fluisterde hun (boze ingevingen) in opdat hij hun naaktheid zou openbaren die voor hen verborgen was, en zeide: \"Uw Heer heeft u deze boom alleen verboden, opdat gij geen engelen of eeuwig- levenden zoudt worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,21,'En hij zwoer tot hen: \"Ik ben voor u zeker een oprechte raadgever.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,22,'Zo deed hij hen door bedrog vallen. En toen zij van de boom proefden werd hun naaktheid hun duidelijk en zij begonnen zich te bedekken met bladeren uit de tuin. En hun Heer riep hen en zeide: \"Verbood Ik u die boom niet en zeide Ik niet tot u: ´Voorwaar, Satan is een openlijke vijand voor u´?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,23,'Zij antwoordden: \"Onze Heer, wij hebben onszelf onrecht aangedaan en als Gij ons niet vergeeft en ons niet genadig zijt, zullen wij zeker tot de benadeelden behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,24,'Hij zeide: \"Gaat heen, sommigen uwer zullen de vijanden van anderen zijn. En er is voor u een verblijfplaats op aarde en een voorziening voor een bepaalde tijd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,25,'Hij zeide: \"Gij zult daarop leven en sterven en gij zult daarvandaan worden opgewekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,26,'O kinderen van Adam! Wij hebben u inderdaad kleding nedergezonden om uw naaktheid te bedekken, ook om sierlijk te zijn, doch het kleed van godsvrucht is het beste. Dit is een teken van Allah, opdat zij er lering uit mogen trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,27,'O kinderen van Adam, laat Satan u niet verleiden, zoals hij uw ouders uit het paradijs verdreef en hen van hun kleding beroofde, opdat hij hun hun naaktheid mocht tonen. Waarlijk, hij ziet u, hij en zijn stam, vanwaar gij hen niet ziet. Voorzeker, Wij hebben de duivelen vrienden gemaakt voor hen, die niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,28,'En wanneer zij een slechte daad begaan, zeggen zij: \"Wij zagen dit onze vaderen doen en Allah heeft het ons bevolen.\" Zeg: \"Allah legt nooit slechte daden op. Zegt gij van Allah, hetgeen gij niet weet?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,29,'Zeg: \"Mijn Heer heeft rechtvaardigheid bevolen. En dat gij uw aandacht behoorlijk richt, ter gelegenheid van aanbidding en Hem aanroept in zuivere gehoorzaamheid aan Hem. Zoals Hij u deed ontstaan, zo zult gij wederkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,30,'Sommigen heeft Hij geleid en bij anderen werd dwaling hun deel. Zij hebben buiten Allah de bozen tot vrienden genomen en zij denken dat zij recht geleid zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,31,'O, kinderen van Adam, let op uw uiterlijk ter gelegenheid van aanbidding en eet en drinkt, maar verkwist niet. Hij heeft de verkwisters zeker niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,32,'Zeg: \"Wie heeft de tooi van Allah, die Hij voor Zijn dienaren heeft voortgebracht en zuiver voedsel, verboden?\" Zeg: \"Zij zijn ook voor de gelovigen in het tegenwoordige leven en voor hen alleen op de Dag der Opstanding.\" Zo verklaren Wij de tekenen aan een volk dat begrip heeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,33,'Zeg: \"Mijn Heer heeft slechte daden, hetzij openlijk of in het geheim verboden en zonde en ongerechtvaardigde opstand; en dat gij datgene met Allah vereenzelvigt, waarvoor Hij u geen gezag heeft nedergezonden en dat gij van Allah dingen zegt, die gij niet weet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,34,'En er is voor elk volk een termijn en wanneer hun tijd is gekomen kunnen zij geen uur uitstel krijgen, noch kunnen zij vooruitlopen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,35,'O, kinderen van Adam, als boodschappers vanuit uw midden tot u komen, die Mijn tekenen aan u voordragen, dan, wie Allah zal vrezen en goede daden verrichten, over hen zal geen vrees komen, noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,36,'Maar zij, die Onze tekenen verloochenen en er zich hoogmoedig van afkeren - dezen zullen de bewoners van het Vuur zijn, zij zullen daarin vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,37,'Wie is dan onrechtvaardiger dan hij, die een leugen over Allah uit, of Zijn tekenen verloochent? Dezen zijn het, die hun lot zullen ondergaan zoals het verordend is, als Onze boodschappers hen zullen bezoeken om hun zielen weg te nemen; zij zullen hen vragen: \"Waar is hetgeen gij naast Allah aanriept?\" Zij zullen antwoorden: \"Het is verloren geraakt voor ons,\" en zij zullen tegen zichzelven getuigen, dat zij ongelovig waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,38,'Hij zal zeggen: \"Gaat onder de volkeren van djinn en mensen die v��r u heengingen, het Vuur binnen.\" Steeds wanneer een volk er binnengaat zal het zijn zustervolk vervloeken, totdat, wanneer zij er allen opeenvolgend in zijn aangekomen, de laatsten over de eersten hunner zullen zeggen: \"Onze Heer, dezen deden ons dwalen, geef hun daarom een dubbele straf van het Vuur.\" Hij (Allah) zal zeggen: \"Er is voor iedereen het dubbele, maar gij weet het niet.´´');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,39,'En de eersten hunner zullen tot de laatsten zeggen: \"Gij zijt niet boven ons verheven, smaakt daarom de straf voor al hetgeen gij deedt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,40,'Voorzeker, voor hen die Onze tekenen verloochenen en er zich hoogmoedig van afwenden, zullen de poorten van de Hemel niet worden geopend, noch zullen zij in het paradijs komen; eer zou een kameel door het oog van een naald gaan. En zo vergelden Wij de daden der schuldigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,41,'Zij zullen de hel tot bed en bedekkingen hebben. En zo vergelden Wij de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,42,'Maar, die geloven en goede werken verrichten - Wij belasten geen ziel boven haar vermogen - dezen zullen de bewoners van het paradijs zijn, zij zullen daarin vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,43,'Welke wrok er ook in hun hart moge zijn, wij zullen deze van hen verwijderen. Er zullen rivieren voor hen vloeien. En zij zullen zeggen: \"Alle lof komt Allah toe, Die ons hiertoe heeft geleid. En als Allah ons niet had terechtgewezen, hadden wij geen leiding kunnen vinden. De boodschappers van onze Heer brachten inderdaad de waarheid.\" En er zal hen worden toegeroepen: \"Dit is het paradijs, dat u als erfdeel is gegeven, voor hetgeen gij deedt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,44,'De bewoners van het paradijs zullen naar de bewoners van de hel roepen: \"Wij hebben bevonden waar te zijn, wat onze Heer ons beloofde. Hebt gij ook bevonden, waar te zijn wat uw Heer u beloofde?\" Zij zullen zeggen: \"Ja.\" Dan zal er een woordvoerder onder hen verkondigen: \"De vloek van Allah rust op de onrechtvaardigen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,45,'Die anderen van het pad van Allah weerhielden, het oneffen wensende, en die het Hiernamaals verwierpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,46,'En er zal een scheiding tussen beiden zijn; en er zullen op de verheven plaatsen mannen zijn die allen aan hun merktekenen herkennen. En zij zullen tot de bewoners van het paradijs roepen: \"Vrede zij over u.´´ Dezen zullen het paradijs nog niet zijn binnengegaan, maar zij hopen het.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,47,'En wanneer hun ogen naar de bewoners van het Vuur zijn gericht, zullen zij zeggen: \"Onze Heer, plaats ons niet onder het onrechtvaardige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,48,'En de bewoners van de verheven plaatsen zullen tot de mensen die zij aan hun merktekenen herkennen roepen: \"Uw aantal, noch datgene waarover gij hoogmoedig waart, heeft u kunnen helpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,49,'Zijn dezen het aangaande welke gij hebt gezworen dat Allah hun geen barmhartigheid zou schenken? \"Gaat het paradijs binnen, er zal geen vrees over u komen, noch zult gij treuren,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,50,'En de bewoners van het Vuur zullen tot de bewoners van het paradijs roepen: \"Giet wat water over ons uit of iets, waarmnee Allah u heeft voorzien.\" Zij zullen antwoorden: \"Allah heeft voorzeker dit voor de ongelovigen verboden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,51,'Degenen, die hun godsdienst tot tijdverdrijf en tot vermaak namen en wie het leven van de wereld had bedrogen, Wij zullen hen deze Dag vergeten, zoals zij de ontmoeting op deze dag vergaten en zoals zij Onze tekenen verwierpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,52,'En Wij hebben hun voorzeker een Boek gebracht, dat Wij met kennis hebben uiteengezet, als leiding en barmhartigheid voor een volk dat gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,53,'Wachten zij slechts op (een andere) verklaring daarvan? De Dag, waarop deze komen zal, zullen degenen die het voorheen vergaten, zeggen: \"De boodschappers van onze Heer brachten inderdaad de waarheid, zullen wij dan enige bemiddelaars hebben, die voor ons zullen bemiddelen? Of konden wij worden teruggezonden (naar de aarde), opdat wij anders mochten doen, dan hetgeen wij deden?\" Zij deden hun ziel inderdaad tekort en hetgeen zij verzonnen is voor hen verloren gegaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,54,'Voorzeker, uw Heer is Allah, Die de hemelen en de aarde in zes dagen schiep; daarna zette Hij Zich op deTroon neder. Hij doet de nacht de dag bedekken, die hem snel opvolgt. De zon en de maan en de sterren zijn door Zijn gebod in dienst gesteld. Voorwaar, van Hem is de schepping en het gebod. Gezegend is Allah, de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,55,'Roept uw Heer in nederigheid en in het verborgene aan. Hij heeft de overtreders zeker niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,56,'En schept geen wanorde op aarde, nadat zij is geordend en roept Hem met vrees en hoop aan. Voorzeker, de Barmhartigheid van Allah is de goeden nabij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,57,'En Hij is het, Die de winden als blijde tijdingen voor Zijn barmhartigheid uitzendt; totdat, wanneer zij zware wolken dragen, Wij ze naar een dor land drijven, daarna zenden Wij er water uit neder, dan brengen Wij alle soorten vruchten voort; zo wekken Wij de doden op, opdat gij er lering uit moogt trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,58,'En het goede land - de plantengroei komt er in overvloed van voort door het gebod van zijn Heer - en hetgeen slecht is levert alleen schaarste op. Zo wisselen Wij de tekenen af voor een volk dat dankbaar is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,59,'Wij zonden Noach tot zijn volk en hij zeide: \"O, mijn volk, aanbidt Allah, gij hebt geen god naast Hem. Ik vrees voor u de straf van de grote Dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,60,'De leiders van zijn volk antwoordden: \"Wij zien dat gij in openlijke dwaling verkeert.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,61,'Hij zeide: \"O, mijn volk, er is in mij geen dwaling, doch ik ben een boodschapper van de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,62,'\"Ik breng u de boodschappen van mijn Heer over en geef u oprechte raad en ik weet van Allah wat gij niet weet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,63,'\"Verwondert gij u, dat er een aanmaning van uw Heer tot u is gekomen door een man uit uw midden opdat hij u moge waarschuwen en opdat gij rechtvaardig moogt worden en opdat u barmhartigheid moge worden betoond?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,64,'Maar zij verloochenden hem; Wij redden hem en degenen die met hem in de ark waren en Wij verdronken degenen, die Onze tekenen verwierpen. Zij waren inderdaad een verblind volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,65,'En tot (het volk van) Aad (zonden Wij) hun broeder Hoed. Hij zeide: \"O mijn volk, aanbidt Allah, gij hebt geen andere god naast Hem. Wilt gij dan niet (God) vrezen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,66,'De ongelovige leiders van zijn volk zeiden: \"Wij zien u als een dwaze en wij denken, dat gij tot de leugenaars behoort.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,67,'Hij antwoordde: \"O, mijn volk, er is in mij geen dwaasheid, maar ik ben een boodschapper van de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,68,'\"Ik breng u de woorden van mijn Heer en ik ben voor u een eerlijke raadgever.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,69,'\"Verwondert gij u, dat er een waarschuwing van uw Heer tot u is gekomen door een man uit uw midden, opdat hij u moge waarschuwen? \" Hij maakte u na het volk van Noach tot erfgenamen en deed u overvloedig in kracht toenemen. Gedenkt daarom de gunsten van Allah, opdat gij moogt slagen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,70,'Zij zeiden: \"Zijt gij tot ons gekomen opdat wij Allah alleen moeten aanbidden en de goden die onze vaderen aanbaden zullen verlaten? Breng ons dan hetgeen waarmede gii ons bedreigt, als gij oprecht zigt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,71,'Eij antwoordde: \"Straf en toorn van uw Heer hebben u reeds getroffen. Redetwist gij met mij over namen, die gij hebt genoemd - gij en uw vaderen - waarvoor Allah geen gezag heeft nedergezonden? Wacht dan, ik ben met u onder de wachtenden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,72,'En Wij redden hem en degenen, die met hem waren door Onze barmhartigheid en Wij sneden de levenswortel af van degenen die Onze tekenen verloochenden. En dezen waren geen gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,73,'Naar de Samoed (kwam) hun broeder Salih. Hij zeide: \"O mijn volk, aanbidt Allah; gij hebt geen andere god naast Hem. Voorwaar er is een duidelijk bewijs van uw Heer tot u gekomen; deze kamelin is van Allah, een teken voor u. Laat haar daarom met rust opdat zij zich van Allah´s aarde moge voeden en doet haar geen leed, anders zal een pijnlijke straf u bereiken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,74,'En herinnert u, toen Hij u na (het volk van) Aad tot opvolgers maakte en u vestigde in het land; gij bouwdet paleizen in de vlakten en gij hieuwt huizen uit de bergen. Gedenkt daarom de gunsten van Allah en wandelt niet op aarde, onheil stichtend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,75,'De leiders van zijn volk, die aanmatigend waren, zeiden tot de gelovigen, die zij zwak achtten: \"Weet gij zeker, dat Salih een door zijn Heer gezondene is?\" Zij antwoordden: \"Wij geloven voorzeker in hetgeen, waarmede hij gezonden is.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,76,'Degenen die aanmatigend waren zeiden: \"Voorwaar, wij geloven niet in hetgeen waarin gij gelooft.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,77,'Toen verlamden zij de kamelin en overtraden het gebod van hun Heer en zeiden: \"O, Salih, breng ons hetgeen, waarmede gij ons hebt bedreigd, als gij tot de boodschappers behoort.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,78,'De aardbeving overviel hen en zij lagen uitgestrekt op de grond in hun huizen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,79,'Toen wendde Salih zich van hen af en zeide: \"O, mijn volk, ik bracht u de boodschap van mijn Heer en bood u oprechte raad aan, maar gij houdt niet van oprechte raadgevers.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,80,'En Lot, toen hij tot zijn volk zeide: \"Pleegt gij een gruweldaad zoals niemand ter wereld ooit v��r u pleegde?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,81,'\"Gij nadert met wellust mannen, in plaats van vrouwen. Neen, gij zijt een volk dat de perken te buiten gaat.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,82,'Het antwoord van zijn volk was slechts: \"Verdrijft hen uit uw stad, want zij zijn mannen die zich rein willen houden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,83,'Wij redden hem en zijn familie, met uitzondering van zijn vrouw, zij behoorde tot de achterblijvenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,84,'En Wij deden een regen van stenen over hen komen. Ziet nu wat het einde was van de schuldigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,85,'En tot Midian hun broeder Shoaib. Hij zeide: \"O, mijn volk, aanbidt Allah, gij hebt geen god naast Hem. Er is inderdaad een duidelijk teken van uw Heer tot u gekomen. Geeft daarom volle maat en ruim gewicht en vermindert het aan de mensen verschuldigde niet en schept geen wanorde op aarde nadat zij geordend is. Dit is beter voor u, als gij gelovigen zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,86,'\"En wacht niet op de wegen om degenen die in Hem geloven te bedreigen en van het pad van Allah af te houden en het oneffen te maken. En gedenkt, hoe gij weinigen waart en Hij u vermenigvuldigde. En ziet wat het einde was van de onruststokers.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,87,'\"En als er een groep onder u is die gelooft in hetgeen waarmede ik ben gezonden en een andere groep die dit niet gelooft, wacht dan geduldig totdat Allah onder ons richt. Want Hij is de beste Rechter.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,88,'De leidende mannen van zijn volk die aanmatigend waren, antwoordden: \"Wij zullen u, o Shoaib, en de gelovigen met u zeker uit onze stad verdrijven tenzij gij tot onze godsdienst terugkeert.\" Hij zeide: \"Zelfs al zijn wij er afkerig van?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,89,'En indien wij tot uw godsdienst terugvallen, nadat Allah ons er van heeft gered, dan hebben wij voorzeker een leugen aangaande Allah verzonnen. En het past ons niet er naar te willen terugkeren, behalve, wanneer Allah, onze Heer, dit zou willen. Onze Heer omvat alle dingen in Zijn kennis. Wij hebben in Allah ons vertrouwen gelegd. Oordeel daarom, onze Heer, tussen ons en ons volk in waarheid en Gij zijt de beste Rechter.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,90,'En de leidende mannen van zijn volk die niet geloofden, zeiden: \"Als gij Shoaib volgt, zult gij zeker verliezers zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,91,'Daarom greep de aardbeving hen en zij lagen uitgestrekt op de grond in hun huizen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,92,'Degenen, die Shoaib verloochenden werden (zo vernietigd) alsof zij er nooit in hadden gewoond. Degenen, die Shoaib van leugen beschuldigden - zij waren de verliezers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,93,'Daarna wendde hij zich van hen af en zeide: \"O mijn volk, ik heb u inderdaad de boodschap van mijn Heer overgebracht en ik gaf u oprechte raad. Hoe moet ik dan om een ongelovig volk treuren?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,94,'En Wij zonden nimmer een profeet naar een stad zonder dat Wij het volk er van met tegenspoed en lijden troffen, opdat zij zouden verootmoedigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,95,'Daarna verwisselden Wij het boze met het goede, totdat zij groeiden en zeiden: \"Lijden en geluk wedervoeren onze vaderen ook.\" Dan grepen Wij hen plotseling terwijl zij er niet aan dachten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,96,'En indien de mensen van die steden hadden geloofd en rechtvaardig waren geweest, zouden Wij zeker zegeningen van de hemel en van de aarde voor hen hebben gezonden, maar zij verloochenden (onze profeet); daarom grepen Wij hen vanwege hun daden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,97,'Zijn de bewoners der steden veilig voor de komst van Onze straf over hen, ´s nachts, terwijl zij slapen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,98,'Of zijn de bewoners dezer steden veilig voor Onze straf die over hen zou kunnen komen, des daags terwijl zij zich vermaken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,99,'Zijn zij dan veilig voor Allah´s voornemen? En niemand waant zich veilig voor Allah´s voornemen, dan het volk dat te gronde gaat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,100,'Doet het degenen, die de aarde beerven na haar (vroegere) bewoners niet inzien, dat, indien Wij het willen, Wij hen om hun zonden treffen en hun hart verzegelen, zodat zij niet meer horen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,101,'Zo waren de steden wier verhaal Wij u hebben verteld. En voorzeker hun boodschappers kwamen met duidelijke tekenen tot hen. Doch zij wilden niet geloven omdat zij voorheen reeds loochenden. Zo zegelt Allah het hart der ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,102,'Wij vonden in de meesten hunner geen (trouw aan het) verbond en Wij bevonden dat de meesten hunner overtreders waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,103,'Toen zonden Wij na hen (de vorige boodschappers) Mozes met Onze tekenen naar Pharao en zijn leiders, maar zij geloofden er niet in. Ziet hoe het einde was van de onruststokers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,104,'En Mozes zeide: \"O, Pharao, ik ben waarlijk een boodschapper van de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,105,'\"Ik mag slechts de waarheid over Allah spreken. Ik ben met een duidelijk teken van uw Heer tot u gekomen; zend daarom de kinderen Isra�ls met mij mee.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,106,'Hij antwoordde: \"Als gij inderdaad met een teken zijt gekomen breng het naar voren als gij tot de waarachtigen behoort.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,107,'Hij (Mozes) wierp zijn stok neder en ziet, het was duidelijk een slang.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,108,'En hij haalde zijn hand tevoorschijn en ziet, zij was wit (geworden) voor de toeschouwers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,109,'De leiders van het volk van Pharao zeiden: \"Dit is gewis een vaardige tovenaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,110,'\"Hij wil u uit uw land zetten. Wat raadt gij nu aan?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,111,'Zij zeiden: \"Geef hem en zijn broeder tijd en zend (intussen) omroepers de steden in,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,112,'Die elke vaardige tovenaar tot u zullen brengen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,113,'En de tovenaars kwamen tot Pharao en zeiden: \"Wij zullen natuurlijk als wij de overhand krijgen een beloning ontvangen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,114,'Hij (Pharao) antwoordde: \"Ja en gij zult tot de gunstelingen behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,115,'Zij zeiden: \"O Mozes zult gij of zullen wij het eerst werpen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,116,'Hij antwoordde: \"Werpt gij.\" En toen zij wierpen, betoverden zij de ogen der mensen en deden hen vrezen en toonden hun grote toverkunst.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,117,'En Wij bezielden Mozes en zeiden: \"Werp uw stok neder\" en ziet, deze slokte al hetgeen zij getoverd hadden op.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,118,'Zo werd de waarheid bevestigd en bleek wat zij deden ijdel te zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,119,'Zo werden zij verslagen en vernederd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,120,'En de tovenaars werden bewogen zich neder te werpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,121,'En zeiden: \"Wij geloven in de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,122,'\"De Heer van Mozes en A�ron.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,123,'Pharao zeide: \"Hebt gij v��r ik het u toestond in Hem geloofd? Dit is voorzeker een complot dat gij in de stad hebt gesmeed, opdat gij haar bewoners er uit moogt verdrijven maar gij zult het weldra te weten komen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,124,'\"Ik zal gewis uw handen en uw voeten aan tegengestelde zijden (rechts en links) doen afsnijden. Dan zal ik u allen tezamen laten kruisigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,125,'Zij antwoordden: \"Wij zullen voorzeker naar onze Heer terugkeren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,126,'En gij neemt alleen wraak op ons omdat wij in de tekenen van onze Heer hebben geloofd toen zij ons getoond werden. Onze Heer, stort standvastigheid over ons uit en doe ons sterven terwijl wij Moslims zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,127,'En de leiders van het volk van Pharao zeiden: \"Wilt gij Mozes en zijn volk in het land wanorde laten scheppen en u en uw goden laten verzaken?\" Hij antwoordde: \"Wij zullen hun zonen doden en hun vrouwen sparen. Zeker wij hebben macht over hen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,128,'Mozes zeide tot zijn volk: \"Zoekt de hulp van Allah en weest geduldig. Voorzeker, de aarde behoort aan Allah. Hij geeft haar als erfdeel aan wie Zijner dienaren Hij wil en de uiteindelijke overwinning is voor de godvrezenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,129,'Zij antwoordden: \"Wij werden vervolgd, voordat gij tot ons kwaamt en nadat gij tot ons zijt gekomen.\" Hij (Mozes) zeide: \"Waarschijnlijk gaat uw Heer uw vijand vernietigen en u tot stedehouders in het land maken, dan zal Hij zien hoe gij handelt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,130,'En Wij straften het volk van Pharao door droogte en met schaarste van vruchten, opdat zij er lering uit mochten trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,131,'Wanneer er goeds tot hen kwam zeiden zij: \"Dit komt ons toe.\" En als hen kwaad overkwam, schreven zij de tegenspoed toe aan Mozes en zijn metgezellen. Let op! Hun tegenspoed was eveneens van Allah. Maar de meesten hunner weten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,132,'En zij zeiden (tot Mozes): \"Welk teken gij ons ook moogt brengen om er ons mede te betoveren, wij zullen stellig niet in u geloven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,133,'Toen zonden Wij de storm en de sprinkhanen en de luizen en de kikvorsen en bloed over hen - als duidelijke tekenen, doch zij gedroegen zich hoogmoedig en waren een schuldig volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,134,'En toen de straf op hen viel, zeiden zij: \"O, Mozes, bid voor ons tot uw Heer, zoals Hij u heeft beloofd. Als gij de plaag van ons verwijdert, zullen wij u zeker geloven en wij zullen de kinderen Isra�ls voorzeker met u laten gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,135,'Maar toen Wij de straf van hen verwijderden voor een bepaalde termijn, die zij moesten voleindigen, ziet, toen braken zij (hun beloften.)');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,136,'Wij straften hen derhalve en verdronken hen in zee, omdat zij Onze tekenen verloochenden en er geen acht op sloegen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,137,'En Wij deden de mensen die voor zwak werden gehouden de oostelijke en westelijke gedeelten van het land, welke Wij zegenden, erven. En het genadevolle woord van uw Heer werd voor de kinderen Isra�ls vervuld omdat zij geduldig waren geweest; en Wij vernietigden al hetgeen Pharao en zijn volk hadden gebouwd en al hetgeen zij hadden opgericht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,138,'En Wij deden de kinderen Isra�ls door de zee trekken en zij kwamen tot een volk dat aan zijn afgoden was gehecht. Zij zeiden: \"O, Mozes, maak ons een god zoals dit (volk) goden heeft.\" Hij antwoordde: \"Gij zijt zeker een onwetend volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,139,'\"Wat dezen betreft, al hetgeen waarmede zij zich bezig houden, zal worden vernietigd en al hetgeen zij doen zal vergeefs zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,140,'Hij zeide (verder): \"Zal ik u een andere god dan Allah zoeken, terwijl Hij u boven de volkeren heeft verheven?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,141,'Toen Wij u van Pharao´s volk verlosten dat u aan een marteling onderwierp en uw zonen doodde en uw vrouwen spaarde. En daarin lag voor u een zware beproeving van uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,142,'En Wij maakten met Mozes een overeenkomst van dertig nachten en vulden ze met tien nachten aan. Aldus werd de periode, die door zijn Heer was vastgesteld tot veertig nachten aangevuld. En Mozes zeide tot zijn broeder A�ron: \"Wees mijn plaatsvervanger onder mijn volk in mijn afwezigheid en beheer wel en volg de weg der onruststokers niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,143,'En toen Mozes op Onze vastgestelde tijd kwam en zijn Heer tot hem sprak, zeide hij: \"Mijn Heer, toon U aan mij, opdat ik U moge aanschouwen.\" Hij (Allah) antwoordde: \"Gij zult Mij stellig niet kunnen aanschouwen, maar kijk naar de berg en als deze op zijn plaats blijft, dan zult gij Mij wel kunnen zien.\" En toen zijn Heer Zich op de berg openbaarde, brak deze in stukken en Mozes viel bewusteloos neder. En toen hij tot zichzelf kwam, zeide hij: \"Heilig zijt Gij, ik wend mij tot U en ik ben de eerste der gelovigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,144,'Allah zeide: \"O, Mozes, Ik heb u door Mijn boodschappen en Mijn woord boven de volkeren uitverkoren. Houd u daarom vast aan hetgeen Ik u heb gegeven en behoor tot de dankbaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,145,'En Wij schreven op de tafelen allerhande raad en uitleg voor alles. Houd u er aan en beveel uw volk, dit alles stipt op te volgen. Ik zal u weldra de verblijfplaats der overtreders tonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,146,'Ik zal voorzeker degenen, die ten onrechte trots handelen op aarde weldra van Mijn tekenen afkeren; en hoewel zij alle tekenen zien, zullen zij er niet in geloven, en als zij het pad der rechtvaardigheid zien zullen zij dit als weg niet aanvaarden, maar indien zij het pad der dwaling zien, zullen zij deze als weg wel inslaan. Dat komt, omdat zij Onze tekenen verloochenden en er onachtzaam op waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,147,'En zij, die Onze tekenen en de laatste Ontmoeting verloochenen - hun werken zullen verloren gaan. Zullen zij worden beloond, anders dan voor hetgeen zij deden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,148,'En het volk van Mozes maakte van hun sieraden in zijn afwezigheid het lichaam van een kalf - dat een loeiende toon voortbracht. Zagen zij niet, dat het niet tot hen kon spreken, noch hen naar een goede weg leiden? Zij namen het, (als hun god) en zij waren overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,149,'Toen zij wroeging gevoelden en zagen, dat zij inderdaad gedwaald hadden, zeiden zij: \"Als onze Heer ons geen barmhartigheid betoont en ons vergeeft, zullen wij gewis tot de verliezers behoren.´´');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,150,'En toen Mozes verontwaardigd en bedroefd tot zijn volk terugkeerde, zeide hij: \"Hetgeen gij in mijn afwezigheid deedt, was slecht. Hebt gij u gehaast v��r het gebod van uw Heer?\" En hij legde de tafelen neder en greep zijn broeders haar en sleepte hem naar zich toe. Hij (A�ron) zeide: \"Zoon van mijn moeder, het volk achtte mij inderdaad zwak en wilde mij doden. Laat zich de vijanden daarom niet over mij verblijden en plaats mij niet bij het onrechtvaardige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,151,'Hij (Mozes) zeide: \"Mijn Heer, vergeef mij en mijn broeder en laat ons tot Uw barmhartigheid toe want Gij zijt de Allergenadigste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,152,'Voorzeker, degenen die het kalf aanbaden zal de toorn van hun Heer en de vernedering in het tegenwoordig leven treffen En zo bejegenen Wij degenen, die een leugen verzinnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,153,'Doch diegenen die kwaad doen en daarna berouw tonen en geloven, voorzeker uw Heer is dan Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,154,'Toen Mozes´ toorn was gekalmeerd, nam hij de tafelen en er was leiding en barmhartigheid in het geschrift voor degenen, die hun Heer vrezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,155,'En Mozes koos voor Onze ontmoeting zeventig mannen van zijn volk. Maar toen de aardbeving hen achterhaalde, zeide hj: \"Mijn Heer, als het U had behaagd, kondet, Gij hen en mij voordien reeds hebben vernietigd. Wilt Gij ons verdelgen voor hetgeen de dommen onder ons hebben gedaan? Dit is niets dan een beproeving van U. Gij laat daardoor dwalen wie Gij wilt en Gij leidt wie Gij wilt. Gij zijt onze Beschermer , vergeef one daarom en toon ons barmhartigheid en Gij zijt de Beste Vergevensgezinde.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,156,'\"En verorden het goede voor ons in deze wereld en in het Hiernamaals; wij zijn tot U gekomen.\" Allah antwoordde: \"Ik zal Mijn straf opleggen aan wie Ik wil, maar Mijn barmhartigheid omvat alle dingen. Zo zal Ik het verordenen voor degenen die Mij vrezen en de Zakaat betalen en voor hen die in Onze tekenen geloven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,157,'\"Hun, die de boodschapper, de reine profeet volgen, die zij in de Torah en het Evangelie beschreven vinden, legt hij het goede op en verbiedt het kwade, veroortooft hun de goede dingen en verbiedt de slechte en ontheft hen van de last en de kluisters die hen bonden. Zij, die in hem geloven en hem eren en ondersteunen en het licht dat met hem is nedergezonden volgen, zullen gewis slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,158,'Zeg: \"O mensdom, ik ben u allen tot een boodschapper van Allah, aan Wie het koninkrijk der hemelen en der aarde behoort. Er is geen God naast Hem. Hij geeft het leven en doet sterven. Gelooft daarom in Allah en Zijn boodschapper, de reine Profeet, die in Allah en Zijn woorden gelooft en volgt hem opdat gij recht geleid moogt worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,159,'Er is een deel van het volk van Mozes dat tot waarheid aanspoort en daarmede rechtvaardig handelt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,160,'En Wij verdeelden hen in twaalf stammen, als afzonderlijke volkeren. En Wij openbaarden aan Mozes, toen zijn volk om drinken vroeg: \"Sla de rots met uw staf\" en er ontsprongen twaalf bronnen aan: elke stam kende zijn drinkplaats. En Wij deden wolken hen overschaduwen en Wij zonden Manna en kwartels voor hen neder. \"Eet van de goede dingen, waarmede Wij u hebben voorzien.\" En zij deden Ons geen onrecht aan, maar zij schaadden zichzelf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,161,'En toen er tot hen werd gezegd: \"Woont in deze stad en eet ervan waar gij ook wilt en zegt: ´God, verlicht onze last´, en gaat de poort in nederigheid binnen, Wij zullen u uw tekortkomingen vergeven. Wij zullen meer geven aan hen die goed doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,162,'Maar de onrechtvaardigen onder hen vervingen het woord door een ander dat niet tot hen was gesproken. Daarom zonden Wij een kastijding van de hemel over hen neder omdat zij onrechtvaardig waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,163,'En vraag hun omtrent de stad, die aan de zee lag. Toen zij de Sabbath ontheiligden verscheen vis op hun Sabbath aan de oppervlakte van het water, maar de dag waarop zij geen Sabbath hielden kwam zij niet tot hen. Zo beproefden Wij hen omdat zij overtreders waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,164,'Toen een gedeelte hunner zeide: \"Waarom predikt gij tot een volk dat Allah wil vernietigen of met een strenge kastijding gaat straffen?\" Het andere deel antwoordde: \"Als een verontschuldiging tegenover uw Heer en opdat zij rechtvaardig mogen worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,165,'En toen zij de vermaning vergaten redden Wij degenen die het kwade verboden en grepen de onrechtvaardigen met een strenge straf aan, omdat zij verkeerd handelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,166,'En toen zij overtraden, hetgeen hun was verboden, zeiden Wij tot hen: \"Weest verachte apen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,167,'En toen verkondigde uw Heer dat Hij dezulken zou zenden, die hen (de Joden) met een marteling zouden kwellen tot de dag der Opstanding. Voorzeker, uw Heer is vlug in vergelding en Hij is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,168,'En Wij verdeelden hen in groepen over de aarde. Er zijn onder hen rechtvaardigen en er zijn onrechtvaardigen. Wij beproefden hen door voor- en tegenspoed, opdat zij zich mochten bekeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,169,'Na hen kwam er een boos geslacht dat het Boek erfde. Zij namen de goederen van deze wereld en zeiden: \"Het zal ons worden vergeven.\" Maar als meer dergelijke goederen tot hen kwamen zouden zij deze ook hebben genomen. Werd de belofte in het Boek, dat zij van Allah slechts de waarheid zouden spreken, niet van hen afgenomen? En hebben zij hetgeen er in staat, niet gelezen? En het tehuis van het Hiernamaals is beter voor degenen, die (God) vrezen. Begrijpt gij dat niet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,170,'En die zich aan het Boek vasthouden en in het gebed volhardend zijn - voorzeker Wij doen de beloning der goeden niet verloren gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,171,'Toen Wij de berg (Sina�) boven hen deden schudden alsof hij een losse bedekking was, dachten zij, dat deze op hen zou vallen; Wij zeiden: \"Houdt u aan hetgeen Wij u hebben gegeven vast en gedenkt wat er in staat, opdat gij moogt worden behouden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,172,'En toen uw Heer van Adams kinderen een nageslacht uit hun lendenen voortbracht, en hen deed getuigen over henzelf: \"Ben ik uw Heer niet?\" antwoordden zij: \"Ja, wij getuigen\" zodat gij op de Dag der Opstanding niet zoudt zeggen: \"Wij waren ons hiervan zeker niet bewust.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,173,'Of gij zolldt zeggen: \"Het waren alleen onze vaderen die afgoderij bedreven en wij waren een geslacht na hen. Wilt Gij ons dan vernietigen om hetgeen de leugenaars deden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,174,'En zo verklaren Wij de tekenen opdat zij zich mogen bekeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,175,'En vertel hun het verhaal van de man die Wij Onze tekenen gaven, maar hij wendde zich af, daarom volgde Satan hem en hij werd verleid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,176,'En indien Wij wilden, konden Wij hem er door verheffen doch hij verkoos de aarde en volgde zijn begeerten, hij is als een hond: als gij hem achtervolgt laat deze zijn tong (uit de bek) hangen en indien gij hem met rust laat steekt hij ook zijn tong uit. Dit is het geval van de mensen, die Onze tekenen verloochenen. Vertel daarom deze gelijkenis opdat zij mogen nadenken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,177,'Slecht is de toestand van een volk dat Onze tekenen verloochent, het handelt onjuist tegen zichzelf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,178,'Wie Allah leidt is op het rechte pad. En wie Hij laat dwalen, zal tot de verliezers behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,179,'Voorwaar, Wij hebben menige djinn en mens geschapen wier einde de hel zal zijn. Zij hebben harten maar begrijpen er niet mede en zij hebben ogen maar zij zien er niet mede en zij hebben oren maar zij horen er niet mede. Zij zijn als vee, neen zij dwalen nog meer (dan dit), zij zijn de achtelozen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,180,'Aan Allah behoren alle goede eigenschappen. Roept Hem daarbij aan. En laat degenen, die ten opzichte van Zijn eigenschappen van de rechte weg afwijken, met rust. Hun zal worden vergolden naar hetgeen zij hebben bedreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,181,'En er is onder hen die Wij hebben geschapen een volk, dat de mensen met waarheid leidt en rechtvaardig oordeelt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,182,'En degenen, die Onze tekenen verwerpen zullen Wij geleidelijk aangrijpen, op een wijze die zij niet verwachten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,183,'Ik geef hun uitstel. Mijn plan is voorzeker machtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,184,'Hebben zij er niet over nagedacht dat er in hun metgezel (Mohammed) geen krankzinnigheid is? Hij is slechts een duidelijk waarschuwer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,185,'Hebben zij het koninkrijk der hemelen en der aarde en alle dingen die Allah geschapen heeft, niet bekeken? En dat hun termijn waarschijnlijk reeds naderbij is gekomen? In welk woord zullen zij dan daarna geloven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,186,'En wie Allah laat dwalen, voor hem kan er geen gids zijn. Hij laat dezulken in hun koppigheid blindelings zwerven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,187,'Vragen zij u omtrent het uur, wanneer het zal plaatsvinden? Zeg: \"De kennis daarvan is slechts bij mijn Heer. Niemand dan Hij kan het op zijn tijd openbaren. Het rust zwaar op de hemel en op de aarde. Het zal slechts onverwacht tot u komen. Zij ondervragen u of gij er goed van op de hoogte zijt. Zeg: \"De kennis er van is slechts bij Allah, maar de meeste mensen weten het niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,188,'Zeg: \"Ik heb buiten hetgeen Allah wil, geen macht over goed of kwaad voor mijzelf. En als ik het onzienlijke kende zou ik een overvloed van goed hebben bemachtigd en het kwade zou mij niet hebben gedeerd. Ik ben slechts een waarschuwer en een drager van goede tijding voor een volk dat gelooft.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,189,'Hij is het, Die u uit een enkele ziel heeft geschapen en daaruit haar gade maakte, opdat deze troost in haar mocht vinden. En nadat hij haar bekend heeft, draagt zij een lichte last en gaat er mede rond. En wanneer deze zwaar wordt, bidden zij beiden tot Allah hun Heer: \"Als Gij ons een goed kind geeft, zullen wij zeker tot de dankbaren behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,190,'Maar als Hij hun een welgeschapen kind geeft, schrijven zij deelgenoten aan Hem toe, betreffende hetgeen Hij hun beiden heeft gegeven. Maar Allah is verheven boven hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,191,'Vereenzelvigen zij met Allah degenen die niets scheppen terwijl deze zelf geschapen zijn?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,192,'En zij kunnen anderen geen hulp verlenen noch kunnen zij zichzelf helpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,193,'En als gij hen tot leiding roept zullen zij u niet volgen. Het is gelijk of gij hen roept of zwijgt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,194,'Voorwaar, degenen die gij naast Allah aanroept zijn dienaren, zoals gij. Roept hen dan aan en laat hen u verhoren als gij waarheid spreekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,195,'Hebben zij voeten waarmede zij lopen of hebben zij handen waarmede zij vasthouden, of hebben zij ogen waarmede zij zien of hebben zij oren waarmede zij horen? Zeg: \"Roept de deelgenoten aan. Smeedt plannen tegen mij (profeet) en geeft mij geen uitstel\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,196,'Waarlijk, mijn Beschermer is alleen Allah Die het Boek (de Koran) heeft geopenbaard. En Hij is de Beschermer der goeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,197,'En zij, die gij naast Hem aanroept hebben geen macht om u te helpen noch kunnen zij zichzelf helpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,198,'En als gij hen tot leiding uitnodigt horen zij u niet. En gij ziet hen naar u kijken maar zij zien niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,199,'Neig u tot vergiffenis en spoor tot vriendelijkheid aan en wend u van de onwetenden af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,200,'En als een boze ingeving van Satan u (tot het kwade) aanspoort, zoek dan uw toevlucht bij Allah; voorzeker, Hij is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,201,'Degenen die (God) vrezen, wanneer hen een boze neiging van Satan overvalt, gedenken Allah en ziet, zij zijn ziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,202,'En hun broederen trachten hen te doen toenemen in dwaling, doch zij falen niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,203,'En wanneer gij hun geen teken brengt, zeggen zij: \"Waarom verzint gij het niet? \" Antwoord: \"Ik volg alleen hetgeen mij van mijn Heer wordt geopenbaard.\" Dit zijn de bewijzen van uw Heer en een leiding en een barmhartigheid voor een volk, dat gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,204,'En wanneer de Koran wordt voorgedragen, luistert er naar en weest stil, opdat u barmhartigheid moge geschieden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,205,'En gedenk uw Heer, ´s morgens en ´s avonds in uw gedachte met nederigheid en vrees en zonder luidruchtigheid van spraak en behoor niet tot de onachtzamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,7,206,'Waarlijk, degenen die dicht bij uw Heer zijn wenden zich niet met trots van Zijn aanbidding af doch zij verheerlijken Hem en werpen zich voor Hem neder.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,1,'Zij vragen u omtrent de oorlogsbuit. Antwoord: \"De oorlogsbuit behoort aan Allah en de boodschapper. Vreest daarom Allah en regelt (uw geschillen) onderling inschikkelijk en gehoorzaamt Allah en Zijn boodschapper als gij gelovigen zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,2,'Ware gelovigen zijn slechts degenen wier hart vol vrees klopt, wanneer de naam van Allah wordt genoemd en wanneer Zijn tekenen hun worden voorgelezen, doet dit hen in geloof toenemen en op hun Heer vertrouwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,3,'Die het gebed houden en van hetgeen, waarmede Wij hen hebben voorzien, mededelen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,4,'Dezen zijn de ware gelovigen. Voor hen zijn graden bij hun Heer, vergiffenis en een waardige voorziening.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,5,'Toen uw Heer u in waarheid van uw huis deed weggaan, was een gedeelte van de gelovigen er afkerig van.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,6,'Zij redetwistten met u over de waarheid nadat deze was bekend gemaakt alsof zij zienderogen tot de dood werden gedreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,7,'En toen Allah u ��n der twee partijen beloofde dat zij de uwe zou zijn, wenstet gij, dat de partij zonder wapenen de uwe zou worden, maar Allah wilde door Zijn Woorden de waarheid bevestigen en de levenswortel der ongelovigen afsnijden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,8,'Opdat Hij de waarheid mocht bevestigen en de leugen teniet mocht doen, ofschoon de schuldigen er afkerig van zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,9,'Toen gij de hulp van uw Heer afsmeektet en Hij u antwoordde: \"Ik zal u met duizend engelen helpen die elkander opvolgen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,10,'Allah gaf het slechts als verblijdend nieuws en opdat uw hart daardoor mocht worden gerustgesteld. Want hulp komt alleen van Allah; voorzeker, Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,11,'Toen Hij slaap over u deed komen als beveiliging van Hem en water van de wolken over u nederzond, opdat Hij u daardoor mocht reinigen en het vuil van Satan van u mocht verwijderen en opdat Hij uw hart mocht sterken en u mocht doen volhouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,12,'Toen uw Heer aan de engelen openbaarde: \"Ik ben met u; versterkt de gelovigen. Ik boezem ontzag in de harten der ongelovigen. Slaat daarom hun hoofd af en slaat alle toppen van hun vingers af.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,13,'Dit is, omdat zij zich tegen Allah en Zijn boodschapper hebben verzet. En wie tegen Allah en Zijn boodschapper strijdt, (wete) Allah is voorzeker streng in vergelding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,14,'Dat is (uw straf), ondergaat haar daarom en weet dat er voor de ongelovigen de straf van het Vuur is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,15,'O, gij die gelooft, wanneer gij degenen die niet geloven, op u af ziet komen wendt hun dan niet uw rug toe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,16,'En wie op die dag zijn rug toekeert, tenzij hij voor het gevecht manoeuvreert of om plaats te nemen bij een andere groep, doet inderdaad de toorn van Allah over zich komen en de hel zal zijn tehuis zijn en dat is een slechte verblijfplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,17,'Gij dooddet hen niet, doch Allah was het, Die hen doodde. En gij wierpt niet toen gij wierpt, maar Allah was het die wierp, opdat Hij de gelovigen een grote gunst van Zich mocht bewijzen. Voorzeker, Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,18,'Dit (geschiedde) en voorzeker is Allah degene, Die het plan van de ongelovigen verijdelt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,19,'Als gij een oordeel zoekt, dan is het oordeel reeds tot u gekomen. En als gij ophoudt, zal het beter voor u zijn, maar als gij terugkeert, zullen Wij ook terugkeren. En uw partij zal u in het geheel niet baten hoe talrijk zij ook moge zijn en Allah is voorzeker met de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,20,'O, gij die gelooft, gehoorzaamt Allah en Zijn boodschapper en wendt u niet van hem af, terwijl gij hoort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,21,'En weest niet zoals degenen, die zeggen: \"Wij horen,\" maar zij horen niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,22,'Voorzeker, erger dan de beesten zijn in de ogen van Allah de doven en de stommen die niet willen begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,23,'Als Allah enig goed in hen had ontdekt, zou Hij hen voorzeker hebben doen horen. En als Hij hen zou hebben laten horen hadden zij zich in afkerigheid afgewend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,24,'O, gij die gelooft, geeft gehoor aan Allah en de boodschapper wanneer Hij u roept, opdat Hij u leven moge geven en weet, dat Allah tussen een man en zijn hart komt en dat Hij het is tot Wie gij zult worden vergaderd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,25,'En behoedt u voor het onheil, dat niet alleen degenen, die onder u kwaad doen zal treffen. En weet, dat Allah streng is in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,26,'En gedenkt, toen gij weinigen waart en zwak werd geacht in het land en toen gij vreesdet, dat de mensen u weg zouden voeren, hoe Hij u beschermde en sterkte met Zijn hulp en u voorzag van goede dingen, opdat gij dankbaar mocht zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,27,'O, gij die gelooft, weest Allah en de boodschapper niet ontrouw en weest niet ontrouw aan het u toevertrouwde tegen beter weten in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,28,'En weet, dat uw bezittingen en uw kinderen slechts een beproeving zijn en dat voorzeker bij Allah een grote beloning is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,29,'O, gij die gelooft, als gij Allah vreest zal Hij u een onderscheiding verlenen en uw tekortkomingen voor u bedekken en u vergeven; Allah is Heer van grote Genade.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,30,'Toen smeedden de ongelovigen tegan u plannen, opdat zij u gevangen mochten nemen of doden of verbannen. En zij maakten plannen en Allah maakte plannen en Allah is het best in staat plannen te verijdelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,31,'En wanneer Onze verzen worden voorgelezen aan hen, zeggen zij: \"Wij hebben het gehoord. Als wij willen kunnen wij gewis iets dergelijks uiten. Dit zijn niets dan fabelen der ouden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,32,'En toen zij zeiden: \"O Allah, als dit inderdaad de waarheid van U is, doe dan stenen uit de hemel over ons regenen of geef ons een (andere) smartelijke straf.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,33,'Maar Allah zal hen niet straffen zolang gij onder hen zijt noch zal Allah hen straffen indien zij om vergiffenis vragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,34,'Waarom zal Allah hen niet straffen, wanneer zij de mensen beletten de heilige moskee binnen te gaan en er geen bewakers van zijn? De bewakers er van zijn alleen de godvruchtigen, maar de meesten hunner beseffen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,35,'En hun gebed in het Huis (de Kaaba) is niet anders dan fluiten en klappen in de handen. \"Ondergaat daarom de straf omdat gij placht te verwerpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,36,'Voorzeker, de ongelovigen besteden hun rijkdommen om anderen van de weg van Allah af te leiden. Zij zullen doorgaan ze te verspillen maar daarna zullen zij spijt hebben en worden overwonnen. En zij die verwerpen zullen in de hel worden verzameld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,37,'Zodat Allah de bozen van de goeden moge scheiden en de bozen bij elkander moge drijven en hen allen tezamen moge ophopen en hen dan in de hel moge werpen. Dit zijn de verliezers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,38,'Zeg tot degenen die niet geloven, dat als zij ophouden (u te vervolgen), hetgeen voorby is hen zal worden vergeven en indien zij er weer in vervallen, voorwaar, dan is er akeeds het voorbeeld van vroegere volkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,39,'En bestrijdt hen totdat er geen vervolging is en de godsdienst geheel voor Allah wordt. Maar als zij ophouden dan ziet Allah voorzeker hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,40,'En als zij terugvallen weet dan, dat Allah uw Beschermer is, een uitstekende Beschermer en een uitstekende Helper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,41,'En weet, dat wat gij ook als buit neemt, er een vijfde van voor Allah, de boodschapper, de verwanten, de wezen, de armen en de reiziger is, - indien gij in Allah gelooft en in hetgeen Wij aan Onze dienaar op de dag der onderscheiding nederzonden, de dag waarop de twee legers elkander ontmoetten. En Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,42,'Toen gij op de nabijzijnde kant waart en zij zich op de andere zijde bevonden en de karavaan beneden u was; en indien gij een onderlinge afspraak hadt gemaakt, zoudt gij ten opzichte van die afspraak zeker (van mening) hebben verschild. Maar (dit gebeurde) zodat Allah hetgeen gedaan moest worden tot stand zou brengen, zodat hij die zou omkomen door een duidelijk teken zou sterven en dat hij die zou leven door een even duidelijk teken zou blijven leven. En voorzeker, Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,43,'Gedenk de tijd toen Allah hen (de vijanden) in uw ogen als weinigen toonde; had Hij hen u als velen getoond, dan zoudt gij voorzeker hebben geweifeld en met elkander over de zaak getwist; maar Allah bewaarde u; voorzeker, Hij heeft volle kennis over hetgeen in het innerlijk is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,44,'En toen Hij hen in de tijd van uw ontmoeting als weinigen in uw ogen deed voorkomen en u als weinigen in hun ogen deed voorkomen, zodat Allah hetgeen gedaan moest worden tot stand mocht brengen. En tot Allah worden alle dingen teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,45,'O, gij die gelooft, blijft standvastig wanneer gij een leger (van ongelovigen) ontmoet en gedenkt Allah vaak, opdat gij moogt slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,46,'En gehoorzaamt Allah en Zijn boodschapper en redetwist niet met elkander, anders zult gij laf worden en uw kracht zal vergaan. En weest geduldig, voorzeker Allah is met de geduldigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,47,'En weest niet zoals degenen die pochend uit hun huizen kwamen om door de mensen te worden gezien en om anderen van het pad van Allah af te leiden; en Allah omvat al hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,48,'Toen deed Satan hun hun daden schoon schijnen en zeide: \"Niemand onder de mensen zal deze dag de overhand over u hebben want ik ben uw metgezel.\" Maar toen de twee legers elkander in het zicht kwamen, wendde hij zich af en zeide: \"Voorzeker, ik heb niets met u uitstaande, waarlijk, ik zie wat gij niet ziet, ik vrees Allah en Allah is streng in het straffen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,49,'Toen de huichelaars en degenen in wier hart een ziekte is, zeiden: \"Hun (Moslims) geloof heeft dezen bedrogen.\" Maar wie zijn vertrouwen in Allah legt: voorzeker Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,50,'O, hadt gij het slechts kunnen zien, wanneer de engelen de ziel der ongelovigen wegnemen, hun gezicht en hun rug treffende: \"Ondergaat de straf van het branden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,51,'Dit komt door hetgeen uw handen hebben gewrocht; Allah is in het geheel niet onrechtvaardig voor Zijn dienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,52,'Zoals het volk van Pharao en degenen die v��r hen waren; zij verwierpen de tekenen van Allah, daarom strafte Allah hen voor hun zonden. Voorzeker, Allah is Machtig, Streng in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,53,'Dit is omdat Allah nooit een gunst die Hij een volk heeft bewezen zal veranderen totdat zij, wat in hun hart is, veranderen. En voorzeker Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,54,'Zoals het volk van Pharao en degenen, die v��r hen waren; zij verloochenden de tekenen van hun Heer daarom vernietigden Wij hen voor hun zonden. En Wij verdronken het volk van Pharao want zij waren allen onrechtvaardig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,55,'Voorzeker, in de ogen van Allah zijn zij, die (de waarheid) verwerpen erger dan beesten want zij willen niet geloven:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,56,'Degenen met wie gij een verbond sluit, daarna schenden zij dit verbond telkens weer en zij vrezen niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,57,'Als gij hen in de oorlog ontmoet, jaagt dan degenen die achter hen zign vrees aan wegens hen, opdat zij er lering uit mogen trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,58,'En als gij ontrouw van een volk vreest verstoot hen dan op gelijke wijze. Voorzeker, Allah heeft de ongelovigen niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,59,'En laat de ongelovigen niet denken dat zij een voorsprong hebben. Voorzeker, zij kunnen Ons niet ontkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,60,'En maakt aan de grens alle mogelijke strijdkrachten en vastgehouden paarden voor hen gereed, waarmede gij de vijand van Allah en uw vijand en anderen buiten hen, die gij niet kent, doch die Allah kent, moogt afschrikken. En wat gij ook voor de zaak van Allah besteedt, het zal u ten volle worden terugbetaald en u zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,61,'En als zij tot vrede neigen, neigt u er dan ook toe en legt uw vertrouwen in Allah. Voorzeker Hij is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,62,'En als zij u willen bedriegen is Allah voorzeker (als Helper) toereikend voor u. Hij is het, Die u heeft versterkt met Zijn hulp en met die der gelovigen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,63,'en Hij heeft hun harten verenigd. Indien gij al hetgeen op aarde is had besteed, kondet gij hun harten niet hebben verzoend, maar Allah heeft hen verenigd. Voorzeker, Hij is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,64,'O profeet, Allah is toereikend voor u en voor diegenen der gelovigen die u volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,65,'O profeet, spoor de gelovigen aan om te vechten. Als er twintig onder u zijn die stand houden, zullen zij tweehonderd overwinnen en als er honderd uwer zijn zullen zij duizend der ongelovigen verslaan, omdat zij een volk zijn dat niet wil begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,66,'Maar nu heeft Allah uw last verlicht, want Hij weet dat er zwakheid in u is. Als er daarom honderd uwer zijn die standvastig zijn, zullen zij tweehonderd overweldigen en als er duizend uwer zijn zullen zij door het gebod van Allah twee duizend overwinnen. En Allah is met degenen die standvastig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,67,'Een profeet kan geen gevangenen maken voordat hij tot geregeld vechten in het land komt. Gij wenst de goederen van deze wereld terwijl Allah het Hiernamaals voor u wenst. En Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,68,'En indien er geen gebod van Allah was geweest zou u voorzeker een grote rampspoed zijn overkomen voor hetgeen gij naamt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,69,'Eet van de buit die gij ontvangt als wettig en goed en vreest Allah. Voorzeker, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,70,'O profeet, zeg tot de gevangenen die in uw handen zijn: \"Als Allah enig goed in uw hart vindt, zal Hij u beter geven dan hetgeen van u is weggenomen en zal Hij u vergeven\". Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,71,'Maar als zij voornemens zijn u ontrouw te worden, zijn zij reeds voorheen Allah ontrouw geweest, daarom gaf Hij u macht over hen. Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,72,'Voorzeker, degenen die hebben geloofd en hun huizen verlieten en met hun bezittingen en hun persoon voor de zaak van Allah hebben gestreden en degenen die schuilplaats verstrekten en hielpen, zijn vrienden van elkander. Maar degenen die geloven en die hun huizen niet verlieten, gij zijt in het geheel niet verantwoordelijk voor hun bescherming tenzij zij hun huizen verlaten. Maar als zij hulp inzake het geloof zoeken dan is het uw plicht hen te helpen behalve tegen een volk, met hetwelk gij een verbond hebt. Allah ziet, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,73,'De ongelovigen zijn vrienden van elkander. Als gij niet ingrijpt zal er onheil en grote wanorde in het land komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,74,'En degenen die geloven en hun huizen verlaten en die streden voor de zaak van Allah en degenen die hun schuilplaats verstrekken en hen helpen zijn de ware gelovigen. Er is voor hen vergiffenis en een waardige voorziening.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,8,75,'En degenen die naderhand zullen geloven en hun huizen verlaten en tezamen met u strijden, zullen tot u behoren; en bloedverwanten staan nader tot elkander in het Boek van Allah. Voorzeker, Allah is de Oerkenner van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,1,'Dit is de verklaring van ontheffing door Allah en zijn boodschapper tegenover degenen der afgodendienaren met wie gij een verdrag hebt gesloten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,2,'Gaat daarom in het land rond voor vier maanden en weet, dat gij Allah niet kunt ontsnappen en dat Allah de ongelovigen zal vernederen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,3,'En dit is een verklaring van Allah en Zijn boodschapper aan de mensen op de dag van de grote bedevaart, dat Allah alsmede Zijn boodschapper niets uitstaande hebben met de afgodendienaren. Als gij daarom berouw toont zal het beter voor u zijn, maar indien gij u afwendt, weet dan, dat gij Allah niet kunt ontsnappen. En geeft tijding van een pijnlijke straf aan de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,4,'Met uitzondering van diegenen der afgodendienaren met wie gij een verbond hebt gesloten en die in niets hebben gefaald, noch iemand tegen u hebben geholpen. Vervult daarom aan dezen het verbond tot hun bepaalde termijn. Voorzeker, Allah heeft de godvruchtigen lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,5,'Wanneer de heilige maanden voorbij zijn, doodt dan de afgodendienaren waar gij hen ook vindt en grijpt hen en belegert hen en loert op hen uit elke hinderlaag. Maar als zij berouw hebben en het gebed houden en de Zakaat betalen, laat hun weg dan vrij. Voorzeker, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,6,'En als ��n der afgodendienaren u om bescherming vraagt, schenk hem dan bescherming dat hij het woord van Allah moge horen; voer hem dan naar de plaats, waar hij veilig is. Dit is omdat zij een volk zijn dat niet weet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,7,'Hoe kan er een verbond bestaan voor de afgodendienaren met Allah en Zijn boodschapper, met uitzondering van hen, met wie gij in de heilige Moskee een verbond hebt gesloten? Zolang zij daarom getrouw jegens u zijn, weest getrouw jegens hen. Voorzeker, Allah heeft de godvruchtigen lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,8,'Hoe kan het zijn dat wanneer zij de overhand over u hebben, zij geen band van verwantschap en verbond tegenover u in acht zullen nemen? Zij behagen u met hun mond terwijl hun hart dit weigert en de meesten hunner overtreden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,9,'Zij verkopen de tekenen van Allah voor een geringe prijs en keren (mensen) van Zijn weg af. Slecht is inderdaad hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,10,'Zij nemen geen band van verwantschap of verbond betreffende een gelovige in acht, en zij zijn overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,11,'Maar als zij berouw tonen en het gebed houden en de Zakaat betalen worden zij uw broeders in het geloof. Wij leggen de tekenen uit aan een volk dat wil begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,12,'Maar indien zij na hun verbond hun eden breken en uw godsdienst smaden, bestrijdt dan de leiders van het ongeloof - waarlijk, hun eden zijn niets - opdat zij mogen ophouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,13,'Wilt gij een volk niet bestrijden dat zijn eden heeft gebroken en plannen smeedde om de boodschapper te verdrijven en dat het eerste was om tegen u te beginnen? Vreest gij hen? Neen, Allah is het meest waardig, dat gij Hem zoudt vrezen als gij gelovigen zijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,14,'Bestrijdt hen, Allah zal hen door uw handen straffen en vernederen en u tot een overwinning over hen helpen en het gemoed van een volk dat gelooft, verlichten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,15,'En Hij zal de nijd van hun hart wegnemen. Allah wendt Zich met barmhartigheid tot wie Hij wil. Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,16,'Denkt gij, dat gij met rust zoudt worden gelaten terwijl Allah diegenen uwer nog niet heeft onderscheiden die (voor Hem) strijden en niemand buiten Allah en Zijn boodschapper en de gelovigen tot boezemvriend nemen? -Allah is goed op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,17,'De afgodendienaren kunnen de Moskee�n van Allah niet onderhouden, terwijl zij van ongeloof tegen zichzelf getuigen. Zij zijn het wier werken ijdel zullen zijn en zij zullen in het Vuur vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,18,'Alleen hij kan de Moskee�n onderhouden die in Allah en de laatste Dag gelooft en het gebed houdt en de Zakaat betaalt en niemand vreest behalve Allah. Dezen zijn het die tot de geleiden behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,19,'Acht gij het geven van dranken aan de bedevaartgangers en het bezoeken van de heilige Moskee gelijk aan de werken van hem die in Allah en de laatste Dag gelooft en voor de zaak van Allah strijdt? Zij zijn in de ogen van Allah niet gelijk. En Allah leidt het onrechtvaardige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,20,'Zij, die geloven en van hun woonplaatsen verhuizen en met hun bezit en met hun persoon voor de zaak van Allah strijden, hebben in de ogen van Allah de hoogste rang. Dezen zullen zegevieren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,21,'Hun Heer geeft hun blijde tijdingen van Zijn barmhartigheid en van welbehagen en van tuinen waarin een blijvende zaligheid voor hen zal zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,22,'Zij zullen daarin voor eeuwig vertoeven. Voorwaar er is bij Allah een grote beloning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,23,'O gij, die gelooft, neemt uw vaders en uw broeders niet tot vrienden als zij ongeloof boven geloof verkiezen. En wie onder u met hen bevriend is behoort tot de overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,24,'Zeg: \"Indien uw vaders en uw zonen en uw broeders en uw vrouwen en uw verwanten en de rijkdommen die gij verkregen hebt en de handel waarvan gij slapte vreest en de woningen waarvan gij houdt, u liever zijn, dan Allah en Zijn boodschapper en het streven voor Zijn zaak, wacht dan, tot Allah met Zijn oordeel komt; Allah leidt het ongehoorzame volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,25,'Voorzeker, Allah heeft u op menig slagveld geholpen en op de dag van Honain, toen uw grote aantal u verheugde, maar dit baatte u niets en de aarde werd ondanks haar uitgestrektheid voor u te eng; toen hebt gij u vluchtende afgewend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,26,'Daarna zond Allah Zijn vrede over de boodschapper en over de gelovigen neder en Hij zond scharen, die gij niet zaagt en Hij strafte de ongelovigen. En dit is de vergelding voor hen die niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,27,'Daarna zal Allah Zich met Barmhartigheid wenden tot wie Hij wil en Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,28,'O, gij die gelooft, de afgodendienaren zijn voorzeker onrein. Zij zullen daarom na (verloop van) dit jaar de heilige Moskee niet naderen. En als gij armoede vreest, zal Allah u als Hij wil, uit Zijn overvloed verrijken. Voorzeker, Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,29,'Bestrijdt diegenen onder de mensen van het Boek, die in Allah noch in de laatste Dag geloven, noch voor onwettig houden wat Allah en Zijn boodschapper voor onwettig hebben verklaard, noch de ware godsdienst belijden totdat zij de belasting met eigen hand betalen, terwijl zij onderdanig zign.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,30,'En de Joden zeggen: \"Ezra is de zoon van Allah\" en de Christenen zeggen: \"De Messias is de zoon van Allah.\" Dit is, hetgeen zij met hun mond zeggen. Zij spreken de woorden na van degenen die v��r hen ongelovig waren; Allah´s vloek zij over hen, hoe zijn zij afgekeerd!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,31,'Zij hebben naast Allah hun geleerde mannen en hun monniken tot Heren genomen. En ook de Messias, de zoon van Maria, hoewel hun was bevolen slechts de ene God te aanbidden. Er is geen God naast Hem. Hij is verheven boven hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,32,'Zij wensen het licht van Allah door hun mond te doven, maar Allah belet dit. Hij zal Zijn licht vervolmaken, zelfs al mogen de ongelovigen er een afkeer van hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,33,'Hij is het, Die Zijn boodschapper met leiding en de ware godsdienst heeft gezonden om deze te doen zegevieren boven alle godsdiensten, ofschoon de afgodendienaren er afkerig van zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,34,'O, gij die gelooft, velen der priesters en monniken verteren de rijkdommen der mensen door valse middelen en leiden de mensen van de weg van Allah af. En degenen, die goud en zilver ophopen en het niet voor de zaak van Allah besteden, deel hun het nieuws van een pijnlijke straf mee.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,35,'Op de Dag, waarop het (geld) in het Vuur der hel verhit zal worden en hun voorhoofd, hun zijden en hun rug er mede zullen worden gebrandmerkt, (wordt hun gezegd:) \"Dit is hetgeen gij voor uzelf hebt vergaard, ondergaat daarom nu (de gevolgen van) hetgeen gij voor uzelf verzameld hebt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,36,'Het aantal der maanden is volgens Allah´s verordening twaalf sinds de tijd waarop Hij de hemelen en de aarde schiep. Vier hiervan zijn heilig. Dit is het juiste geloof. Doet u zelf dus hierin geen onrecht aan. En bestrijdt de afgodendienaren allen tezamen, zoals zij u bestrijden en weet, dat Allah met de rechtvaardigen is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,37,'Voorzeker, het uitstellen (van een heilige maand) is een toevoeging aan het ongeloof. Degenen, die niet geloven worden daardoor op een dwaalspoor gebracht. Het ene jaar staan zij het toe en het andere jaar verbieden zij het, opdat zij betreffende het aantal dat Allah heilig heeft gemaakt mogen overeenkomen, waardoor zij hetgeen Allah heeft verboden wettig maken. Het boze hunner daden werd voor hen schoonschijnend gemaakt. Allah leidt het ongelovige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,38,'O, gij die gelooft, waarom buigt gij ter aarde wanneer er tot u wordt gezegd: \"Gaat op de weg van Allah voort?\" Zijt gij met het tegenwoordige leven tevreden boven het Hiernamaals? Maar het genoegen van het tegenwoordige leven is vergeleken bij het Hiernamaals slechts nietig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,39,'Als gij niet voortgaat te vechten zal Hij u met een pijnlijke straf straffen en zal Hij een ander volk in uw plaats stellen en gij zult Hem in het geheel niet deren. Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,40,'Als gij hem (de profeet) niet helpt, voorzeker Allah hielp hem, toen de ongelovigen hem verdreven - toen hij ��n van de twee was - en zij beiden in de grot waren en hij tot zijn metgezel zeide: \"Treur niet, want Allah is met ons.\" Toen zond Allah Zijn vrede op hem neder en versterkte hem met scharen die gij niet zaagt en vernederde het woord van de ongelovigen en Allah´s woord is het allerhoogste. En Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,41,'Gaat voort licht of zwaar, streeft met uw bezit en uw persoon voor de zaak van Allah. Dit is beter voor u als gij het slechts weet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,42,'Als het een onmiddellijke winst en een korte reis was geweest, zouden zij u zeker zijn gevolgd, maar de vermoeiende reis scheen hun te lang. Toch willen zij bij Allah zweren: \"Als wij er toe in staat waren geweest, zouden wij zeker met u zijn gegaan.\" Zij doen hun ziel te gronde gaan en Allah weet dat zij leugenaars zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,43,'Allah vergeve het u! Waarom stondt gij het hun toe, voordat degenen die de waarheid spraken u bekend waren geworden en totdat gij de leugenaars had herkend?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,44,'Degenen, die in Allah en de laatste Dag geloven zullen u niet om toestemming vragen om te worden vrijgesteld van het strijden met hun bezit en hun persoon. Allah kent de rechtvaardigen goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,45,'Alleen degenen, die niet in Allah en de laatste Dag geloven en wier hart vol twijfel is, zullen u vragen om te worden vrijgesteld daar zij aarzelen in hun twijfel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,46,'Indien zij hadden willen vertrekken, zouden zij er zeker enige voorbereiding voor hebben gemaakt, maar Allah was afkerig van hun vertrek. Hij hield hen daarom terug en er werd gezegd: \"Zit met de zittenden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,47,'En als zij met u waren gegaan, zouden zij u niets dan last hebben bezorgd en zij zouden zich heen en weer hebben gehaast, tweedracht tussen u zaaiende. En er zijn er onder u die naar hen geluisterd zouden hebben. En Allah kent de onrechtvaardigen goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,48,'Voorzeker, zij zochten voordien reeds tweedracht te scheppen en zij smeedden complotten tegen u, totdat de waarheid kwam en het voornemen van Allah de overhand kreeg, ofschoon zij er afkerig van waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,49,'En onder hen is hij die zegt: \"Geef mij verlof en stel mij niet op de proef.\" Voorzeker, zij zijn reeds op de proef gesteld. De hel zal de ongelovigen zeker omvatten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,50,'Indien u iets goeds overkomt, verdriet het hen, maar als u een rampspoed overkomt, zeggen zij: \"Wij hadden inderdaad onze voorzorgen genomen.\" En zij wenden zich juichend af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,51,'Zeg: \"Niets kan ons overkomen, behalve hetgeen Allah voor ons heeft verordend. Hij is onze Beschermer. En in Allah zullen de gelovigen hun vertrouwen leggen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,52,'Zeg: \"Gij verwacht voor ons niets dan een der beide goede dingen (overwinning, martelaarschap), terwijl wij betreffende u verwachten, dat Allah u een straf zal opleggen van Hemzelf of door onze handen. Wacht daarom, wij wachten ook met u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,53,'Zeg: \"Besteedt vrijwillig of onwillig, het zal van u niet worden aangenomen. Gij zijt inderdaad een ongehoorzaam volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,54,'En niets verhindert, dat hun gaven worden aangenomen behalve dat zij in Allah en de boodschapper niet geloven. En zij komen slechts in luiheid tot het gebed en zij geven niet, dan onwillig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,55,'Laat daarom hun rijkdommen noch hun kinderen uw verwondering opwekken. Allah wenst hen er slechts mede te straffen en hun ziel zal heengaan, terwijl zij ongelovigen Zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,56,'En zij zweren bij Allah dat zij inderdaad tot de uwen behoren, terwijl zij (in feite) niet tot de uwen behoren, toch zijn zij een volk dat vreest.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,57,'Als zij een schuilplaats of grotten of zelfs een gat konden vinden om er binnen te gaan, zouden zij er zich zeker met grote spoed heenwenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,58,'Er zijn onder hen die u inzake aalmoezen belasteren. Als hun ervan wordt gegeven zijn zij tevreden, maar als hun er niet van wordt gegeven, ziet, worden zij boos.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,59,'Waren zij slechts tevreden geweest met hetgeen Allah en Zijn boodschapper hun hadden gegeven en hadden zij gezegd: \"Allah is ons toereikend: Allah zal ons van Zijn overvloed geven evenals Zijn boodschapper. Voorzeker, tot Allah zijn wij geneigd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,60,'De aalmoezen zijn alleen voor de armen en de behoeftigen en voor degenen die daarbij werkzaam zijn en voor degenen wier hart verzoend is en voor de slaven en voor degenen die schuld hebben en voor de zaak van Allah en voor de reiziger: dit is een gebod van Allah. En Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,61,'En er zijn onder hen, die de profeet lastig vallen en zeggen: \"Hij luistert naar iedereen.\" Zeg: \"Zijn luisteren is goed voor u, hij gelooft in Allah en hij gelooft de gelovigen en hij is een barmhartigheid voor de gelovigen onder u.\" En zij, die de boodschapper van Allah lastig vallen, zullen een pijnlijke straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,62,'Zij zweren bij Allah om u te behagen, maar Allah en Zijn boodschapper zijn waardiger, dat zij hen zouden behagen, als zij gelovigen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,63,'Weten zij niet, dat hem die Allah en Zijn Boodschapper vijandig gezind is het Vuur der hel wacht, waarin hij zal vertoeven? Dat is de grote vernedering.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,64,'De huichelaars vrezen, dat een Soerah tegen hen zou worden geopenbaard die hen zou onderrichten over hetgeen in hun hart is. Zeg (tot hen): \"Spot maar, voorzeker, Allah zal al hetgeen gij vreest aan het licht brengen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,65,'En indien gij hen ondervraagt, zullen zij beslist zeggen: \"Wij spraken slechts ijdellijk (onder elkander) en vermaakten ons.\" Zeg: \"Was het over Allah en Zijn tekenen en Zijn boodschapper dat gij spotte?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,66,'\"Biedt geen verontschuldiging aan. Gij hebt, na te hebben geloofd, verworpen. Als Wij een deel uwer vergeven, zullen Wij een ander deel uwer straffen, omdat zij schuldig waren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,67,'De huichelaars, mannen en vrouwen zijn allen met elkander verbonden. Zij sporen aan tot het kwade en verbieden het goede en houden hun handen gesloten (om geen aalmoezen te geven). Zij vergaten Allah, daarom heeft Hij hen vergeten. Voorzeker, de huichelaars zijn ongehoorzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,68,'Allah belooft de huichelaars, mannen en vrouwen en de ongelovigen het Vuur der hel, waarin zij zullen vertoeven. Het zal hun genoeg zijn. Allah heeft hen vervloekt, en zij zullen een blijvende straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,69,'Evenals die v��r u waren: zij hadden meer macht dan gij en waren rijker in bezittingen en kinderen. Dezen genoten hun deel; gij zult dus uw deel genieten, zoals zij die voor u waren hun deel genoten. En gij spreekt ijdellijk, evenals zij ijdellijk spraken. Dezen zijn het wier werken in deze wereld en in het Hiernamaals verloren zijn gegaan. En zij zijn de verliezers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,70,'Heeft hen het verhaal niet bereikt van degenen, die v��r hen waren? Het volk van Noach en Aad en Samoed en het volk van Abraham en de bewoners van Midian en van de steden die verwoest werden? Hun boodschappers kwamen met duidelijke tekenen tot hen. Allah was het niet die hun onrecht aandeed, maar zij deden zichzelf onrecht aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,71,'En de gelovigen, mannen en vrouwen, zijn vrienden van elkander. Zij sporen aan tot het goede en verbieden het kwade en houden het gebed en betalen de Zakaat en gehoorzamen Allah en Zijn boodschapper. Dezen zijn het, wie Allah barmhartigheid zal betonen. Voorzeker, Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,72,'Allah heeft de gelovigen, mannen en vrouwen tuinen beloofd waar doorheen rivieren stromen, heerlijke woonplaatsen in tuinen der eeuwigheid. En het behagen van Allah is het grootste. Dit is de grootste zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,73,'O profeet, strijd tegen de ongelovigen en de huichelaars. En wees streng jegens hen. Hun tehuis is de hel en deze is een boze bestemming.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,74,'Zij zweren bij Allah, dat zij niets zeiden, maar voorzeker zij spraken het woord des ongeloofs en na de Islam te hebben aanvaard, verwierpen zij deze en zij besloten tot hetgeen zij niet konden volbrengen. Zij koesterden haat alleen omdat Allah en Zijn boodschapper hen uit Zijn overvloed hadden verrijkt. Als zij berouw tonen zal het beter voor hen zijn, maar indien zij zich afwenden zal Allah hen met een pijnlijke straf in deze wereld en in het Hiernamaals straffen en zij zullen op aarde vriend noch helper hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,75,'En er zijn onder hen die met Allah een verbond sloten. Zij zeiden: \"Als Hij ons van Zijn overvloed geeft zullen wij beslist aalmoezen geven en tot de deugdzamen behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,76,'Maar toen Hij hun van Zijn overvloed gaf werden zij er vrekkig mede en wendden zich om en waren afkerig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,77,'Hij vergold het hun door huichelachtigheid in hun hart op te wekken tot aan de Dag, waarop zij Hem zullen ontmoeten, omdat zij hun belofte aan Allah braken en leugens uitten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,78,'Weten zij niet dat Allah hun geheimen alsook hun heimelijk overleg kent en dat Allah de Oerkenner is van het onzienlijke?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,79,'Zij, die de gelovigen belasteren welke vrijwillig aalmoezen geven en hen die niets vinden (te geven) dan naar hun vermogen, bespotten: Allah zal hun spotternij vergelden en er is voor hen een pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,80,'Of gij vergiffenis voor hen vraagt of dat gij geen vergiffenis voor hen vraagt - zelfs al vraagt gij zeventig maal vergiffenis voor hen - Allah zal hen toch niet vergeven. Dit is omdat zij in Allah en Zijn boodschapper niet geloven. Allah leidt het trouweloze volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,81,'Zij die achter de boodschapper van Allah bleven verheugden zich over hun thuiszitten en waren er afkerig van met hun eigendommen en hun persoon voor de zaak van Allah te strijden. En zij zeiden: \"Trekt niet uit in de hitte.\" Zeg: \"Het Vuur der hel is heter.\" Konden zij dit slechts begrijpen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,82,'Laten zij weinig lachen en veel wenen als vergelding voor hetgeen zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,83,'En als Allah u tot een gedeelte hunner terugzendt en zij u om toestemming vragen om uit te trekken (tot het gevecht), zeg dan: \"Gij zult met mij niet uittrekken en gij zult nooit een vijand met mij bestrijden. Gij verkoost eerst thuis te blijven, zit daarom thans met degenen, die achterblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,84,'En bid voor geen enkele hunner die sterft, noch sta bij zijn graf, want zij verwierpen Allah en Zijn boodschapper en stierven, terwijl zij overtreders waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,85,'Laat hun eigendommen en hun kinderen uw verwondering niet opwekken: Allah wenst hen daarmede in deze wereld te straffen; hun ziel zal hen verlaten, terwijl zij ongelovigen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,86,'En wanneer een Soerah wordt geopenbaard: \"Gelooft in Allah en strijdt tezamen met Zijn boodschapper,\" vragen de rijken onder hen u om toestemming en zeggen: \"Laat ons achter, opdat vij bij de achterblijvers zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,87,'Zij stellen zich tevreden om met de achterblijvenden te zijn en hun hart is verzegeld, derhalve begrijpen zij niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,88,'Maar de boodschapper en de gelovigen met hem, strijden met hun bezit en hun persoon en zij zijn het, die het goede zullen ontvangen en zij zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,89,'Allah heeft tuinen voor hen bereid waar doorheen rivieren stromen; zij zullen daarin vertoeven. Dat is de opperste zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,90,'Van de woestijn-Arabieren kwamen er, uitvluchten zoekend opdat hun vrijstelling mocht worden verleend. En degenen, die logen jegens Allah en Zijn boodschapper, bleven thuis. En degenen hunner, die niet geloven, zal een pijnlijke straf treffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,91,'Er rust op de zwakken en op de zieken en op degenen die niets vinden om weg te geven, geen schuld, indien zij oprecht zijn jegens Allah en Zijn boodschapper. Er rust geen blaam op degenen die goed doen; Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,92,'Noch op degenen, die tot u kwamen en verzochten dat gij hun een rijdier zoudt verschaffen, en gij antwoorddet: \"Ik kan niets vinden waarop ik u kan doen rijden.\" Zij gingen met hun ogen vol tranen terug uit spijt, dat zij niets konden vinden om hiertoe zelf bij te dragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,93,'De aanleiding tot verwijt is alleen tegen degenen die u om verlof vragen, terwijl zij rijk zijn. Zij verkozen om met de achterblijvenden te zijn. En Allah heeft op hun hart een zegel gelegd, derhalve begrijpen zij niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,94,'Zij zullen met uitvluchten tot u komen, wanneer gij tot hen wederkeert. Zeg: \"Maakt geen verontschuldigingen, wij zullen u niet geloven. Allah heeft ons reeds omtrent uw gedrag ingelicht. En Allah en Zijn boodschapper zullen u uw gedrag weldra tonen, dan zult gij tot Hem die het onzienlijke en het zienlijke kent, worden teruggebracht en Hij zal u over al hetgeen gij deedt, inlichten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,95,'Zij zullen, wanneer gij tot hen weder keert, u bij Allah zweren, dat gij hen met rust moogt laten. Laat hen daarom alleen. Voorzeker, zij zijn onrein en hun huis is de hel, een vergelding voor wat zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,96,'Zij zullen u zweren, opdat gij welwillend zult zijn. Maar zelfs al zoudt gij tevreden met hen zijn, zal Allah met het overtredende volk niet tevreden zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,97,'De woestijn-Arabieren zijn de hardnekkigsten in ongeloof en huichelarij en het meest geneigd de geboden, die Allah tot Zijn boodschapper heeft nedergezonden niet na te komen. Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,98,'Er zijn onder de woestijn-Arabieren, die hetgeen zij weggeven als boete beschouwen en wachten dat er rampspoed over u komt. Op hen zal echter de rampspoed rusten. En Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,99,'En er zijn onder de woestijn-Arabieren, die in Allah en de laatste Dag geloven en die hetgeen zij weggeven als middelen beschouwen tot Allah´s nabijheid en tot de zegeningen van de profeet. Ziet toe! Het is stellig voor hen een middel tot Zijn nabijheid. Allah zal hen weldra tot Zijn barmhartigheid toelaten. Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,100,'En de vooruitstrevenden en de eersten der Migranten en Hulpgevers en degenen, die hen in goedheid volgen, Allah heeft welbehagen in hen en zij hebben welbehagen in Hem; en Hij heeft voor hen tuinen bereid, waar doorheen rivieren stromen. Daarin zullen zij voor eeuwig vertoeven. Dat is de grote zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,101,'Van de u omringende woestijn-Arabieren zijn sommigen huichelaars evenals van het volk van Madina, dezen volharden in huichelarij. Gij kent hen niet; Wij kennen hen en Wij zullen hen hier dubbel straffen, daarna zullen zij aan een grote straf worden overgeleverd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,102,'En er zijn anderen, die hun fouten bekennen. Zij vermengden een goede met een slechte daad. Het kan zijn, dat Allah Zich met barmhartigheid tot hen zal wenden. Voorzeker, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,103,'Neem aalmoezen van hun rijkdommen aan opdat gij hen daardoor moogt reinigen en louteren. En bid voor hen; uw gebed is voor hen inderdaad een bron van geruststelling. En Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,104,'Weten zij niet, dat Allah berouw van Zijn dienaren aanneemt en aalmoezen aanvaardt en dat Allah Berouw-aanvaardend, Genadevol is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,105,'En zeg: \"Werkt en Allah zal met Zijn boodschapper en de gelovigen uw werk zien. Weldra zult gij tot de Kenner van het onzienlijke en het zienlijke worden teruggebracht en dan zal Hij u inlichten over hetgeen gij hebt bedreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,106,'En anderen wachten Allah´s gebod af. Zal Hij hen bestraffen of Zich met barmhartigheid tot hen wenden? Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,107,'En degenen die een moskee hebben gebouwd om te schaden, om het ongeloof (te verbreiden) en om een splitsing onder de gelovigen te veroorzaken en als een hinderlaag voor hem, die voorheen tegen Allah en Zijn boodschapper oorlog voerde; zij zullen voorzeker zweren: \"Wij bedoelden slechts het goede,\" maar Allah getuigt, dat zij leugenaars zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,108,'Sta er nooit in (voor het gebed). Een Moskee, die van het begin af op godsvrucht was gesticht is zeker waardiger dat gij er in zijt. Er zijn daarin mensen die gaarne gelouterd willen worden en Allah heeft degenen, die zich louteren lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,109,'Is daarom hij, die zijn gebouw op godsvrucht en op Zijn behagen stichtte, beter of hij, die zijn gebouw op een afbrokkelende, door water aangetaste rand stichtte, dat met hem in het Vuur der hel zal storten? En Allah leidt het onrechtvaardige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,110,'Het gebouw dat zij hebben opgericht, zal een bron van onrust in hun hart blijven, tenzij hun hart in stukken wordt gescheurd. Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,111,'Voorzeker, Allah heeft van de gelovigen hun persoon en hun bezittingen gekocht in ruil voor het paradijs - zij vechten voor de zaak van Allah en zij doden en worden gedood - een onfeilbare belofte in de Torah en het Evangelie en de Koran. En wie is getrouwer aan zijn belofte, dan Allah? - Verheugt u dan in de verbintenis, die gij met Hem hebt gesloten en dat is de grote zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,112,'Die zich tot Allah bekeren, die aanbidden, die prijzen, die vasten, die zich nederbuigen, die zich ter aarde werpen, die tot het goede aansporen en het kwade verbieden, die de door Allah gestelde grenzen in acht nemen; breng aan de gelovigen blijde tijding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,113,'Het is de profeet en de gelovigen niet geoorloofd om vergiffenis te vragen voor de afgodendienaren, zelfs al waren dezen verwanten, nadat hun (de gelovigen) duidelijk is geworden, dat zij (afgodendienaren) het volk der hel zullen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,114,'Het vragen om vergiffenis door Abraham voor zijn vader, geschiedde alleen wegens een belofte die hij hem had afgelegd, maar toen het hem duidelijk werd dat deze een vijand van Allah was, trok hij zich van hem terug. Voorzeker, Abraham was uiterst zachtmoedig, verdraagzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,115,'En Allah laat een volk niet dwalen nadat Hij het heeft geleid, voordat Hij hun heeft duidelijk gemaakt, waartegen zij zich behoren te behoeden. Voorzeker, Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,116,'Gewis, Allah is het, aan Wie het koninkrijk der hemelen en der aarde behoort. Hij schenkt het leven en veroorzaakt de dood. En gij hebt geen vriend of helper naast Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,117,'Allah heeft zich voorzeker met barmhartigheid tot de profeet gewend en tot de Migranten en de Hulpgevers, die deze (profeet) in het uur van nood volgden, nadat het hart van een gedeelte hunner bijna was bezweken. Toen vergaf Hij hen. Voorzeker, Hij is Liefderijk, Genadevol jegens hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,118,'En (Hij heeft Zich met barmhartigheid) tot de drie die waren achtergelaten gewend, totdat de aarde met haar uitgestrektheid hun te eng werd en hun eigen leven voor hen te moeilijk en zij geloofden dat er tegen Allah geen schuilplaats is, behalve bij Hem. Toen wendde Hij Zich met barmhartigheid tot hen, opdat zij zich mochten bekeren. Voorzeker, Allah is Berouwaanvaardend, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,119,'O gij die gelooft, vreest Allah en weest met de waarachtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,120,'Het betaamt het volk van Madinah en de hen omringende woestijn-Arabieren niet, dat zij achter de boodschapper van Allah zouden blijven, of dat zij hun eigen leven in plaats van het zijne zouden verkiezen. Dit is zo, omdat dorst, noch vermoeienis, noch honger hen in de weg van Allah teistert, noch betreden zij een spoor, dat de ongelovigen vertoornt, noch berokkenen zij een vijand enige schade, of er wordt daarmede voor hen een goede daad opgetekend. Voorzeker, Allah doet de beloning van degenen, die goed doen niet verloren gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,121,'En zij besteden geen som, groot of klein, noch doorkruisen zij een landstreek, of dit is voor hen opgetekend, opdat Allah hun de beste beloning moge geven voor hetgeen zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,122,'Het is de gelovigen niet opgelegd, allen tezamen op te trekken. Waarom trekt dan niet van elke groep een deel hunner op, opdat zij in de godsdienst goed onderlegd mogen worden en opdat zij hun volk, wanneer zij tot hen terugkeren mogen waarschuwen, zodat zij gered mogen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,123,'O, gij die gelooft, bestrijdt de ongelovigen die in uw nabijheid zijn en laat hen hardheid in u vinden en weet, dat Allah met de godvruchtigen is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,124,'En wanneer er een Soerah wordt nedergezonden, zijn er sommigen hunner die zeggen: \"Wie uwer heeft deze in geloof doen toenemen?\" Maar de gelovigen doet dit in geloof toenemen en zij verheugen zich daarover.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,125,'En voor degenen in wier hart een ziekte is, voegt het onreinheid bij onreinheid en zij sterven terwijl zij ongelovig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,126,'Zien zij niet, dat zij elk jaar ��n- of tweemaal op de proef worden gesteld? Toch tonen zij geen berouw noch trekken zij er lering uit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,127,'En wanneer er een Soerah wordt nedergezonden kijken zij elkander aan zeggende: \"Ziet iemand ons?\" Dan wenden zij zich af. Allah heeft hun hart afgewend, omdat zij tot een volk behoren dat niet begrijpen wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,128,'Voorzeker, een boodschapper is uit uw midden tot u gekomen; het is hard voor hem wat u pijn doet; hij is bezorgd voor uw welzijn, liefderijk en barmhartig voor de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,9,129,'Maar indien zij zich afwenden zeg dan: \"Allah is mij toereikend. Er is geen God naast Hem. In Hem leg ik mijn vertrouwen want Hij is de Heer van de grote heerschappij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,1,'Alif, Laam, Raa. Dit zijn de verzen van het Boek vol van Wijsheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,2,'Is het vreemd voor de mensen, dat Wij een man uit hun midden openbaarden: \"Waarschuw het mensdom en geef blijde tijding aan degenen die geloven, dat zij een ware rang bij hun Heer zullen hebben\"? De ongelovigen zeggen: \"Voorzeker, deze is een openlijke tovenaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,3,'Voorwaar, Allah is uw Heer, Die de hemelen en de aarde in zes dagen schiep, en Hij zette Zich op de troon, alles regelend. Er is geen bemiddelaar, dan met Zijn goedkeuring. Dit is Allah, uw Heer, aanbidt Hem daarom. Wilt gij dan geen lering trekken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,4,'Tot Hem is uw aller terugkeer, dit is de ware belofte van uw Heer. Voorzeker, Hij begint de schepping, daarna zet Hij haar voort, opdat Hij degenen die geloven en goede werken doen met rechtvaardigheid moge belonen. En de ongelovigen zullen een drank van kokend water en een pijnlijke straf ontvangen, daar zij (de waarheid) verwierpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,5,'Hij is het, Die de zon tot een stralend licht maakte en de maan tot een helder licht en er stadia voor verordende, zodat gij het getal der jaren en het berekenen (van de tijd) mocht kennen. Allah heeft dit niet dan in waarheid geschapen. Hij zet de tekenen uiteen voor een volk, dat wil weten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,6,'Voorwaar, in de wisseling van dag en nacht en in al hetgeen Allah in de hemelen en op aarde heeft geschapen zijn er tekenen voor een godvrezend volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,7,'Voorzeker, die niet uitzien naar de ontmoeting met Ons en die met het leven dezer wereld tevreden zijn en er voldoening in vinden en degenen, die onoplettend op Onze tekenen zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,8,'Dezen zijn het, wier verblijfplaats het Vuur is, voor hetgeen zij verdienen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,9,'Maar degenen die geloven en goede werken doen, hun Heer zal hen wegens hun geloof leiden. Rivieren zullen voor hen stromen in de tuinen der zaligheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,10,'Hun aanroep daarin zal zijn: \"Heilig zijt Gij, O Allah!\" en hun groet \"Vrede\". En het einde van hun aanroep zal zijn: \"Alle lof komt Allah toe, de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,11,'En indien Allah het boze voor de mensen zou verhaasten, zoals Hij voor hen het goede verhaast, zou hun tijd reeds gekomen zijn. Maar Wij laten degenen die niet naar de ontmoeting met Ons uitzien, in opstand, blindelings dwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,12,'En wanneer de mens een moeilijkheid overkomt, bidt hij tot Ons, op zijn zijde liggende, of zittende, of staande, maar wanneer Wij zijn last van hem hebben verwijderd, gaat hij zijn gang, alsof hij Ons nooit v��r de verwijdering van zijn moeilijkheid had aangeroepen. Zo werd in de ogen der buitensporigen schoonschijnend gemaakt, wat zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,13,'En Wij vernietigden de geslachten die v��r u bestonden toen zij kwaad verrichtten en er kwamen tot hen boodschappers met duidelijke tekenen, maar zij wilden niet geloven. Zo vergelden Wij het schuldige volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,14,'En na hen hebben Wij u tot stedehouders op aarde gesteld, opdat Wij zien, hoe gij zoudt handelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,15,'En wanneer hun Onze duidelijke tekenen worden voorgedragen, zeggen degenen, die niet naar de ontmoeting met Ons uitzien: \"Breng een andere Koran dan deze, of verander hem.\" Zeg: \"Het staat niet aan mij, hem te veranderen uit mijzelf. Ik volg slechts hetgeen mij is geopenbaard. Voorzeker, ik vrees, als ik mijn Heer niet gehoorzaam, de straf van de grote Dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,16,'Zeg: \"Als Allah het zo had gewild, zou ik u niet hebben voorgedragen (de Koran), noch zou Hij u deze bekend hebben gemaakt. Voorzeker, ik heb voordien een heel leven onder u doorgebracht. Wilt gij dan niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,17,'Wie is dan onrechtvaardiger, hij, die een leugen over Allah spreekt, of die Zijn tekenen verloochent? Voorzeker, de schuldigen zullen nooit slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,18,'En zij bidden buiten Allah om tot datgene wat hen schaden noch baten kan en zij zeggen: \"Dezen zijn onze bemiddelaars bij Allah.\" Zeg: \"Wilt gij Allah over iets, dat Hij in de hemelen of op aarde nog niet zou kennen, inlichten?\" Heilig is Hij en hoog verheven boven al hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,19,'En het mensdom was slechts ��n gemeenschap, daarna verschilden zij en ware het Woord van uw Heer niet uitgegaan, voorzeker zou er over hun geschil beslist zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,20,'En zij zeggen: \"Waarom is er geen teken van zijn Heer tot hem (de profeet) nedergezonden?\" Zeg: \"Het onzienlijke behoort alleen Allah toe. Wacht, ik ben met u onder de wachtenden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,21,'En wanneer Wij mensen barmhartigheid doen smaken nadat tegenspoed hen overviel, zie! zij beginnen tegen Onze tekenen plannen te smeden. Zeg: \"Allah is vlugger in het maken van plannen.\" Voorzeker Onze boodschappers schrijven al hetgeen gij verzint op.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,22,'Hij is het, Die u in staat stelt door het land en op zee te reizen, totdat, wanneer gij op de schepen zijt en zij met een mooie bries varen en (de opvarenden) er zich in verheugen, hen een geweldige wind achterhaalt en de golven van alle zijden over hen komen en zij overtuigd zijn dat zij verloren zijn; dan roepen zij Allah in oprechte aanbidding aan: \"Als Gij ons hiervan redt, zullen wij zeker tot de dankbaren behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,23,'Maar wanneer Hij hen heeft gered, ziet, beginnen zij ten onrechte een opstand in het land te ontketenen. O, gij mensen, voorzeker uw opstand keert zich slechts tegen u zelf. Thans geniet gij het genoegen van het tegenwoordige leven. Daarna zal uw terugkeer tot Ons zijn en Wij zullen u inlichten over hetgeen gij deedt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,24,'De gelijkenis van het tegenwoordige leven is slechts als water, dat Wij uit de wolken nederzenden, daarna groeit hierdoor het gewas van de aarde weelderig, waarvan mensen en vee eten, totdat, wanneer de aarde haar sier ontvangt en er schoon uitziet en haar eigenaars denken, dat zij er macht over bezitten, Ons gebod bij dag of bij nacht tot haar komt, dan maken Wij haar tot een gemaaid veld, alsof er de vorige dag niets was geweest. Zo leggen Wij de tekenen uit aan een volk, dat nadenkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,25,'En Allah roept naar het tehuis van Vrede en leidt wie Hij wil naar het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,26,'Er zal voor degenen die goede daden verrichten het goede zijn en nog meer. Zwartheid noch schande zal hun gezicht bedekken. Dezen zullen de bewoners van het paradijs zijn, zij zullen daarin vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,27,'En degenen die boze daden verrichten, de vergelding van het kwaad zal het gelijke daaraan zijn en de schaamte zal hen bedekken. Zij zullen niemand hebben om hen tegen Allah te beschermen. (En het zal zijn) alsof hun gezicht met de duisternis van de nacht bedekt ware. Dezen zullen de bewoners van het Vuur zijn, zij zullen daarin vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,28,'En de Dag waarop Wij hen allen zullen verzamelen, zullen Wij tot de afgodendienaren zeggen: \"Blijft ter plaatse, gij en uw deelgenoten.\" Daarna zullen Wij hen ver van elkander scheiden en hun deelgenoten zullen zeggen: \"Voorzeker gij placht ons niet te aanbidden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,29,'\"Allah is nu toereikend als Getuige tussen u en ons. Wij waren zeker van uw aanbidden onbewust.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,30,'Daarna zal iedere ziel ondervinden wat zij heeft gedaan. En zij zullen tot Allah, hun ware Meester worden teruggebracht en al hetgeen zij plachten te verzinnen zal verloren gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,31,'Zeg: \"Wie voorziet u van voedsel van de hemel en de aarde? Of wie is het, die macht heeft over de oren en de ogen? En wie brengt de levenden uit de doden en de doden uit de levenden voort? En wie bestuurt het al?\" Zij zullen zeggen: \"Allah.\" Zeg: \"Wilt gij dan niet Zijn bescherming zoeken?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,32,'Zo is Allah, uw ware Heer. Wat is er buiten de waarheid anders, dan dwaling? Waarheen wordt gij dan afgewend?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,33,'Zo is het woord van uw Heer bewaarheid tegen degenen, die overtraden omdat zij niet geloofden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,34,'Zeg: \"Is er ��n uwer afgoden die de schepping voortbrengt en deze daarna voortzet?\" Zeg: \"Allah is het, Die de schepping voortbrengt, en deze voortzet. Hoe zijt gij dan afgewend?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,35,'Zeg: \"Is er ��n uwer afgoden, die tot de waarheid leidt?\" Zeg: \"Allah is het, Die tot de waarheid leidt. Is daarom Hij, Die tot de waarheid leidt waardiger om te worden gevolgd, ofwel hij, die zelf de weg niet vindt, tenzij hij wordt geleid? Wat is er met u? Hoe oordeelt gij?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,36,'En de meesten hunner volgen niets dan vermoeden. Voorzeker vermoeden baat niet tegen de waarheid. Waarlijk, AIlah weet goed wat zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,37,'En deze Koran kon door niemand buiten Allah worden voortgebracht. Integendeel, hij is de vervulling van datgene wat er v��r was en is een uiteenzetting van de Wet door de Heer der Werelden, daaraan is geen twijfel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,38,'Of zeggen zij: \"Hij (de profeet) heeft het verzonnen\"? Zeg: \"Brengt dan een hieraan gelijke Soerah voort en roept buiten Allah wie gij kunt (om hulp aan), als gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,39,'Neen, zij loochenen datgene waarvan zij de kennis niet konden omvatten, noch is de uiteindelijke betekenis er van tot hen gekomen. Zo deden ook degenen, die v��r hen waren. Maar ziet, wat het einde was van de overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,40,'En er zijn sommigen onder hen die er in geloven en er zijn sommigen onder hen die er niet in geloven en uw Heer kent de onruststokers goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,41,'En indien zij u van leugen besehuldigen, zeg dan: \"Aan mij mijn werk en aan u uw werk. Gij hebt niets uitstaande met hetgeen ik doe noch heb ik iets uitstaande met hetgeen gij doet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,42,'En er zijn sommigen onder hen die naar u luisteren. Maar kunt gij de doven doen horen, zelfs al willen zij niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,43,'En er zijn sommigen onder hen die naar u kijken. Maar kunt gij de blinden leiden, zelfs al willen zij niet zien?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,44,'Voorzeker, Allah doet de mensen in het geheel geen onrecht aan, maar de mensen doen hun eigen ziel onrecht aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,45,'En de Dag, waarop Hij hen zal verzamelen, zal het hun toeschijnen, alsof zig slechts een uur van een dag (in de wereld) hadden vertoefd. Zij zullen elkander herkennen. Verliezers zijn zeker degenen die de ontmoeting met Allah loochenen en geen leiding willen volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,46,'En als Wij u sommige der dingen, waarmede Wij hen hebben bedreigd, tonen, of als Wij u doen sterven, dan is tot Ons hun terugkeer en Allah is Getuige van al hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,47,'Voor elk volk is er een boodschapper. Wanneer daarom hun boodschapper komt, wordt er met rechtvaardigheid onder hen geoordeeld en hun wordt geen onrecht aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,48,'En zij zeggen: \"Wanneer zal deze belofte worden vervuld, als gij de waarheid spreekt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,49,'Zeg: \"Ik heb voor mij zelf geen macht over schade of voordeel, behalve, wat Allah wil. Er is voor elk volk een vastgestelde termijn; wanneer hun termijn is verlopen kunnen zij hem geen uur uitstellen, noch kunnen zij hem vervroegen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,50,'Zeg: \"Vertelt mij, als Zijn straf bij dag of nacht over u komt, hoe zullen dan de schuldigen weg kunnen lopen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,51,'\"Zult gij dan, wanneer het u overvalt er in geloven?\" Nu? Terwijl gij dit wilde verhaasten?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,52,'Dan zal er tot degenen die kwaad deden worden gezegd: \"Ondergaat de blijvende straf. Er wordt u niets vergolden dan hetgeen gij verdiendet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,53,'En zij vragen u: \"Is dit de waarheid?\" Zeg: \"Ja, bij mijn Heer, het is zeker waar en gij kunt het niet verijdelen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,54,'En indien elke ziel die onrechtvaardig handelt al hetgeen op aarde is, zou bezitten, zou zij er zich voorzeker mede trachten vrij te kopen. En wanneer zij de straf zien zullen zij hun spijt tonen. Er zal met rechtvaardigheid over hen worden gericht en hun zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,55,'Ziet toe! aan Allah behoort al hetgeen in de hemelen en op aarde is en weet, dat Allah´s belofte waar is. Maar de meesten hunner beseffen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,56,'Hij geeft leven en doet sterven en tot Hem zult gij worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,57,'O mensdom! Er is van uw Heer een vermaning tot u gekomen en genezing voor wat in de harten is en een leiding en barmhartigheid jegens de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,58,'Zeg: \"Dit alles is door de genade van Allah en door Zijn barmhartigheid; laat hen er zich daarom in verheugen. Dat is beter, dan hetgeen zij vergaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,59,'Zeg: \"Hebt gij overwogen, dat Allah u een voorziening heeft nedergezonden en dat gij daarna een gedeelte er van onwettig en een gedeelte er van wettig verklaardet?\" Vraag (hen): \"Heeft Allah u dat toegestaan, of verzint gij leugens tegen Allah?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,60,'Wat denken degenen die leugens tegen Allah verzinnen van de Dag der Opstanding? Voorzeker, Allah is genadevol tegenover het mensdom, maar de meesten hunner zijn niet dankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,61,'In welke toestand gij u bevindt, of gij de Koran voordraagt, of iets anders doet; Wij zijn uw getuigen, terwijl gij u er in verdiept. Er is voor uw Heer zelfs geen gewicht van een atoom op aarde of in de hemel verborgen. En er is niets dat kleiner of groter is, of het staat in het duidelijke Boek vermeld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,62,'Ziet! voorzeker, de vrienden van Allah zullen geen vrees hebben, noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,63,'Zig die geloven en zich aan rechtvaardigheid houden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,64,'Er zijn voor hen blijde tijdingen in het tegenwoordige leven en het Hiernamaals. De woorden van Allah kennen geen verandering - dat is inderdaad de opperste zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,65,'En laat hun woorden u niet verdrieten. Voorzeker, alle macht behoort Allah. Hij is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,66,'Ziet! voorzeker, van Allah is al hetgeen in de hemelen en op aarde bestaat. Wat volgen zij die buiten Allah afgoden aanroepen? Zij volgen slechts een vermoeden en doen niets dan gissen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,67,'Hij is het, Die de nacht voor u heeft gesteld, opdat gij er in moogt rusten en de dag vol van licht. Voorzeker, daarin zijn tekenen voor een volk, dat luistert.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,68,'Zij zeggen: \"Allah heeft een zoon tot Zich genomen. Heilig is Hij, Hij is Zichzelf genoeg. Aan Hem behoort wat in de hemelen en op aarde is. Gij hebt hier geen gezag over. Zegt gij over Allah wat gij niet weet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,69,'Zeg: \"Degenen, die over Allah een leugen verzinnen, zullen niet slagen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,70,'Zij zullen in deze wereld tijdelijk genieten, daarna zal hun terugkeer tot Ons zijn, dan zullen Wij hen een strenge straf doen ondergaan, omdat zij niet geloofden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,71,'En verkondig hun het verhaal van Noach, toen hij tot zijn volk zeide: \"O, mijn volk, als mijn houding en mijn vermaning door de tekenen van Allah u aanstoot geven - ik leg mijn vertrouwen in Allah - breng dan al uw plannen en uw afgoden bijeen; laat dan uw handelwijze duidelijk blijken, komt dan tegen mij op en geeft mij geen uitstel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,72,'Maar als gij u terugtrekt vraag ik van u geen beloning. Mijn beloning is bij Allah alleen en het is mij bevolen tot de Moslims te behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,73,'Maar zij verloochenden hem; daarom redden Wij hem en degenen die met hem in de ark waren. En dezen maakten Wij tot de stedehouders, terwijl Wij degenen die Onze tekenen verloochenden lieten verdrinken. Zie! hoe het einde was van degenen, die werden gewaarschuwd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,74,'Toen zonden Wij na hem andere boodschappers naar hun volk en deze kwamen tot hen met duidelijke bewijzen. Maar dezen wilden in datgene niet geloven wat zij voorheen hadden verloochend. Zo verzegelen Wij het hart der overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,75,'Dan zonden Wij na hen Mozes en A�ron met Onze tekenen naar Pharao en zijn leiders, maar zij handelden aanmatigend. En zij waren een misdadig volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,76,'En toen de waarheid van Ons tot hen kwam, zeiden zij: \"Dit is gewis duidelijke tovenarij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,77,'Mozes zeide: \"Zegt gij dit van de waarheid nadat zij tot u is gekomen? Is dit tovenarij? Maar tovenaars slagen nooit.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,78,'Zij antwoordden: \"Zijt gij tot ons gekomen, opdat wij ons mogen afwenden van hetgeen wij onze vaderen zagen volgen zodat er voor u beiden grootheid in het land zou zijn? Maar wij zullen in u niet geloven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,79,'En Pharao zeide: \"Brengt mij elke bedreven tovenaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,80,'En toen de tovenaars kwamen, zeide Mozes tot hen: \"Werpt hetgeen gij wildet werpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,81,'En toen zij wierpen zeide Mozes: \"Wat gij hebt gebracht is slechts bedrog. Voorzeker, Allah zal het ijdel maken. Voorwaar, Allah laat het werk der kwaadstichters niet gedijen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,82,'En Allah bevestigt de waarheid door Zijn woorden, zelfs al zijn de sehuldigen afkerig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,83,'En niemand geloofde Mozes, dan enige jongelingen van onder zijn volk, uit vrees voor Pharao en zijn leiders, in geval hij hen zou vervolgen. En waarlijk. Pharao was een tiran in het land en behoorde tot de buitensporigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,84,'En Mozes zeide: \"O mijn volk, indien gij in Allah hebt geloofd stelt dan uw vertrouwen in Hem, als gij Moslims zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,85,'En zij antwoordden: \"Wij leggen ons vertrouwen in Allah: Onze Heer, maak ons niet tot voorwerp van vervolging voor het onrechtvaardige volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,86,'En red ons door Uw barmhartigheid van de ongelovigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,87,'Wij openbaarden aan Mozes en zijn broeder: \"Neemt gij beiden huizen voor uw volk in Egypte en bouwt uw huizen tegenover elkaar en houdt het gebed. En geeft de gelovigen blijde tijdingen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,88,'En Mozes zeide: \"Onze Heer, Gij hebt Pharao en zijn leiders versieringen en rijkdommen in het tegenwoordige leven geschonken, zodat zij, Onze Heer, van Uw pad afleiden. Onze Heer, vernietig hun bezittingen en verhard hun hart, want zij zullen niet geloven voordat zij de pijnlijke straf zien.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,89,'Allah zeide: \"Uw gebed is aanvaard. Weest gij beiden daarom bestendig en volgt niet het pad der onwetenden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,90,'En Wij brachten de kinderen Isra�ls over de zee; Pharao en zijn scharen vervolgden hen op een onrechtvaardige en aanvallende wijze, totdat hij toen hij bijna verdronk, zeide: \"Ik geloof dat er geen God is dan Hij, in Wie de kinderen Isra�ls geloven en ik behoor tot de Moslims.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,91,'Nu? Terwijl gij voordien ongehoorzaam waart en tot de onruststokers behoordet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,92,'Heden zullen Wij uw lichaam redden, opdat gij een teken moogt zijn voor degenen die na u komen. En waarlijk, het merendeel der mensen is achteloos ten opzichte van Onze tekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,93,'En Wij wezen de kinderen Isra�ls een uitstekend tehuis aan en Wij voorzagen hen van goede dingen en zij verschilden niet van mening voordat de kennis tot hen kwam. Voorzeker, uw Heer zal op de Dag der Opstanding onder hen richten over hetgeen waarin zij verschilden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,94,'En als gij over hetgeen Wij tot u hebben nedergezonden twijfelt, vraagt dan degenen die het Boek v��r u hebben gelezen. Inderdaad, de waarheid is van uw Heer tot u gekomen; behoor daarom niet tot de twijfelaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,95,'En behoor niet tot degenen, die de tekenen van Allah verloochenen, anders zult gij tot de verliezers behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,96,'Degenen tegen wie het woord van uw Heer van kracht is geworden, willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,97,'Zelfs al werd elk teken hun getoond, voordat zij de smartelijke straf hebben gezien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,98,'Waarom heeft, behalve het volk van Jonas geen stad geloofd, zodat hun geloof hen zou hebben kunnen helpen? Toen zij geloofden, verwijderden Wij de straf der schande in het tegenwoordige leven van hen en Wij lieten hen voor een wijle genieten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,99,'En indien uw Heer had gewild, zouden allen die op aarde zijn, zeker tezamen hebben geloofd. Wilt gij de mensen dan dwingen, gelovigen te worden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,100,'Doch geen ziel kan geloven zonder verlof van Allah. En Hij werpt onreinheid over degenen die hun verstand niet gebruiken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,101,'Zeg: \"Overweeg, wat in de hemelen en op aarde gebeurt.\" Maar tekenen, noch waarschuwers baten een volk dat niet wil geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,102,'Verwachten zij iets anders dan het gelijke (oordeel) van de dagen dergenen, die v��r hen stierven? Zeg: \"Wacht daarom, ik ben met u onder de wachtenden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,103,'Dan redden Wij Onze boodschappers en de gelovigen. Zo is het aan Ons, de gelovigen te redden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,104,'Zeg: \"O gij mensen, als gij over mijn godsdienst in twijfel verkeert, (weet) dan dat ik niet aanbid degenen die gij naast Allah aanbidt, maar ik aanbid Allah Die u doet sterven en het is mij geboden tot de gelovigen te behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,105,'En wend uw aangezicht oprecht tot deze godsdienst en behoor niet tot de afgodendienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,106,'En roep naast Allah niet datgene aan, dat u bevoordelen noch schaden kan. En indien gij dat toch doet, dan zult gij zeker tot de onrechtvaardigen behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,107,'En als Allah u door het kwade treft, is er niemand die dit kan verwijderen dan Hij; en als Hij het goede voor u wenst, is er niemand die Zijn genade kan beletten. Hij kent haar toe aan diegene van Zijn dienaren, die Hem behaagt. En Hij is de Vergevensgezinde, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,108,'Zeg: \"O, gij mensen, nu is de waarheid van uw Heer tot u gekomen. Wie daarom die leiding volgt, volgt haar ten bate van zijn eigen ziel en wie dwaalt, dwaalt slechts tot haar nadeel. En ik ben geen bewaker over u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,10,109,'En volg hetgeen u is geopenbaard en wees standvastig, totdat Allah oordeelt. En Hij is de beste Rechter.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,1,'Alif Laam Raa. Dit is een Boek, waarvan de verzen onherroepelijk zijn gemaakt en bovendien zijn zij in bijzonderheden uitgelegd, door de Alwijze, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,2,'(Daarom) aanbidt slechts Allah. Voorzeker, ik (Mohammed) ben voor u een waarschuwer en drager van blijde tijdingen van Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,3,'En vraagt vergiffenis aan uw Heer en wendt u tot Hem, Hij zal u voor een vastgestelde periode van het goede voorzien. En Hij schenkt Zijn genade aan ieder die zich hiervoor verdienstelijk maakt. En als gij u afwendt dan vrees ik, voorzeker, voor u de straf van de grote Dag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,4,'Uw terugkeer is tot Allah en Hij heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,5,'Let op, zij verbergen hun vijandschap voor Hem in hun innerlijk. Ja, wanneer zij zich met hun kleding bedekken, weet Hij wat zij verbergen en wat zij tonen. Voorzeker, Hij weet goed wat in het innerlijk is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,6,'En er is geen schepsel dat op aarde kruipt, of zijn voorziening berust bij Allah, Hij kent zijn tehuis en zijn verblijfplaats. Alles staat in een duidelijk Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,7,'En Hij is het, Die de hemelen en de aarde in zes dagen schiep en Zijn troon rustte op water, opdat Hij u moge beproeven wiens gedrag het beste is. En indien gij (Profeet) zegt: \"Voorzeker, gij zult na de dood worden opgewekt,\" zullen de ongelovigen zeggen: \"Dit is niets dan een zuiver bedrog.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,8,'En als Wij hun straf tot een bepaalde tijd uitstellen, zeggen zij: \"Wie weerhoudt haar?\" Ziet toe! de dag waarop zij over hen komt zal niemand haar kunnen afwenden, en hetgeen zij plachten te bespotten zal op hen nederkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,9,'Wanneer Wij de mens Onze barmhartigheid doen smaken en deze daarna van hem wegnemen, wordt hij voorwaar wanhopig en ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,10,'En als Wij, nadat tegenspoed hem heeft geraakt, voorspoed doen smaken, zal hij voorzeker zeggen: \"De rampspoed is van mij geweken.\" Ziet! hij wordt jubelend en aanmatigend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,11,'Maar degenen die geduldig zijn en goede werken verrichten, zullen vergiffenis en een grote beloning ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,12,'(Zij verbeelden zich dat) gij misschien een gedeelte van hetgeen is geopenbaard, zult opgeven; uw hart wordt er door benauwd, omdat zij zeggen: \"Waarom is er tot hem geen schat nedergezonden of waarom is er geen engel met hem gekomen?\" Voorwaar, gij zijt slechts een waarschuwer en Allah is Voogd over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,13,'Zeggen zij: \"Hij heeft dit (de Koran) verzonnen?\" Antwoord: \"Breng dan tien dergelijke verzonnen hoofdstukken voort en roept buiten Allah wie gij kunt, als gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,14,'En indien zij uw (uitdaging) niet aannemen, weet dan, dat het met Allah´s kennis is geopenbaard en dat er geen God is behalve Hij. Zult gij u dan onderwerpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,15,'Wie het tegenwoordige leven en de schoonheden er van wenst, Wij zullen hen volgens hun werken in dit leven ten volle belonen en zij zullen daarin niet tekort worden gedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,16,'Dezen zijn degenen, die in het Hiernamaals niets dan het Vuur zullen ontvangen en hetgeen zij in dit leven verrichtten zal teniet gaan en hetgeen zij doen is vergeefs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,17,'Is hij dan (aan hen gelijk), die een duidelijk bewijs van zijn Heer bezit en wie een groot getuige van Hem volgt, en die voorafgegaan is door het Boek van Mozes, als richtsnoer en tot barmhartigheid? Dezen geloven in hem. En wie van de volkeren hem verwerpt, het Vuur zal zijn bestemming zijn. Koester dus geen twijfel daaromtrent. Voorzeker dit is de waarheid van uw Heer, maar de meeste mensen willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,18,'En wie is onrechtvaardiger dan hij, die een leugen tegen Allah smeedt? Zulken zullen voor hun Heer worden gebracht en de getuigen zullen zeggen: \"Dezen zijn degenen die tegen hun Heer logen.\" Ziet toe! de vloek van Allah rust op de onrechtvaardigen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,19,'Die van het pad van Allah afleiden, het krom wensend. En zij geloven niet in het Hiernamaals.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,20,'Dezen kunnen in de wereld de straf niet ontvluchten, noch hebben zij enige vrienden naast Allah. De straf zal voor hen worden verdubbeld. Zij deden geen moeite om te horen, of te zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,21,'Dezen zijn het, die hun ziel hebben te kort gedaan en hetgeen zij verzinnen, zal falen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,22,'Zij zijn ongetwijfeld degenen, die in het Hiernamaals de grootste verliezers zullen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,23,'Voorwaar, die geloven en goede werken verrichten en die hun Heer gehoorzamen, zijn de bewoners van de Hemel, waarin zij zullen vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,24,'Het geval van de beide partijen is als de blinde en de dove, de ziende en de horende. Staat het geval van beiden gelijk? Wilt gij dan geen lering (hieruit) trekken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,25,'Wij zonden Noach tot zijn volk zeggende: \"Waarlijk, ik ben voor u een duidelijke waarschuwer,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,26,'Dat gij niemand dan Allah zult aanbidden. Anders vrees ik voor u de straf van een pijnlijke dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,27,'De leiders der ongelovigen onder zijn volk antwoordden: \"Wij zien in u slechts een man zoals wij en wij zien dat niemand u heeft gevolgd, behalve de minsten en de eenvoudigen van geest onder ons. En wij zien u niet uitmunten boven ons; neen, wij geloven dat gij een leugenaar zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,28,'Hij (Noach) zeide: \"O, mijn volk, zeg mij, als ik mij op een duidelijk bewijs van mijn Heer beroep en Hij mij grote barmhartigheid heeft geschonken, die voor u duister is gemaakt, moeten wij u dit opdringen, terwijl gij er afkerig van zijt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,29,'\"O, mijn volk, ik vraag u er geen geld voor. Mijn beloning is alleen bij Allah. En ik wil de gelovigen niet verdrijven, zij zullen voorzeker hun Heer ontmoeten. Maar ik beschouw u als een volk, dat onwetend handelt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,30,'\"O, mijn volk, wie zou mij tegen Allah helpen als ik hen zou verdrijven? Wilt gij dan geen lering hieruit trekken?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,31,'\"En ik zeg u niet: ´Ik bezit de schatten van Allah´, noch ken ik het onzienlijke, noch zeg ik: ´Ik ben een engel´.\" \"Noch zeg ik over degenen, die gij minacht dat Allah hun geen goeds zal schenken. Allah weet het best, wat in hun innerlijk is. Anders zou ik zeker tot de onrechtvaardigen behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,32,'Zij antwoordden: \"O Noach, gij hebt inderdaad met ons getwist en veel getwist, breng ons nu de straf waarmede gij ons hebt gedreigd, als gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,33,'Hij zeide: \"Alleen Allah zal deze over u brengen als Hij wil, en gij kunt niets verijdelen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,34,'\"En als ik u raad geef zal mijn raad u niet baten als Allah u wenst te vernietigen. Hij is uw Heer en tot Hem zult gij worden teruggebracht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,35,'Zeggen zij: \"Hij heeft het verzonnen?\" Zeg: \"Als ik het heb verzonnen, zal mijn zonde op mij rusten doch ik heb niets uitstaande met hetgeen gij begaat.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,36,'En er werd aan Noach geopenbaard: \"Niemand onder uw volk zal geloven, dan degenen die reeds hebben geloofd; treur daarom niet over hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,37,'En bouw de ark voor Onze ogen en volgens Onze voorschriften op. En roep Mij omtrent de onrechtvaardigen niet aan. Zij zullen zeker worden verdronken.´´');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,38,'En hij was de ark aan het bouwen en steeds wanneer de leiders van zijn volk hem voorbijgingen, bespotten zij hem. Hij zeide: \"Als gij ons bespot, zullen wij u (later) bespotten zoals gij (ons) nu doet,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,39,'Dan zult gij weten wie het is, over wie een vernederende straf komt en op wie een blijvende straf zal rusten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,40,'Toen Ons gebod kwam en de bronnen der aarde spoten, zeiden Wij: \"Scheept twee paar van alles in, en uw familie - met uitzondering van degenen, tegen wie het woord reeds is uitgegaan - en de gelovigen.\" En met hem geloofden slechts weinigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,41,'En hij (Noach) zeide: \"Scheept u in. In naam van Allah zij haar vaart en haar ankeren. Mijn Heer is voorzeker Vergevensgezind, Genadevol.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,42,'En zij bewoog zich met hen op golven als bergen voort. En Noach riep tot zijn zoon, die zich afzijdig hield: \"O mijn zoon, scheep u met ons in en wees niet met de ongelovigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,43,'Hij antwoordde: \"Ik zal mijn toevlucht weldra op een berg zoeken, die mij tegen het water zal beschermen.\" Hij antwoordde: \"Er is deze dag geen beschermer tegen het gebod van Allah, met uitzondering van degenen wie Hij barmhartigheid toont.\" En een golf kwam tussen beiden, hij behoorde tot de drenkelingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,44,'En er werd gezegd: \" O, aarde, slok op uw water en o, hemel, houd op (met regenen).\" En het water werd tot zakken gebracht en het gebod was vervuld. En de Ark kwam op (de berg) Al-Djoedie te rusten. En er werd gezegd: \"Vervloekt zij het onrechtvaardige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,45,'En Noach riep zijn Heer aan en zeide: \"Mijn Heer, mijn zoon is voorwaar van mijn familie en Uw belofte is voorzeker waar en Gij zijt de Rechter der rechters.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,46,'Hij (God) zeide: \"O, Noach, hij behoort niet tot uw gezin omdat zijn daden niet goed zijn; daarom vraag Mij niet over hetgeen waarvan gij geen kennis bezit. Ik geef u raad om niet tot de onwetenden te behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,47,'Hij zeide: \"Mijn Heer, ik zoek mijn toevlucht tot U om niet te vragen waar ik geen kennis van heb. En indien Gij mij niet vergeeft noch mij barmhartigheid betoont, zal ik onder de verliezers zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,48,'En er werd gezegd: \"O Noach, daal dan af (uit de ark) met Onze vrede en met zegeningen over u en over de volkeren die met u zijn. En er zullen andere volkeren zijn wie Wij een (aardse) voorziening zullen schenken, daarna zal een pijnlijke straf van Ons hen raken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,49,'Dit zijn de mededelingen van het onzienlijke die Wij u openbaren, welke gij noch uw volk voorheen kende. Wees geduldig, waarlijk het einde is voor de godvrezenden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,50,'En tot de Aad zeide hun broeder Hoed: \"O, mijn volk, aanbid Allah. Gij hebt geen God naast Hem. Gij verzint slechts leugens.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,51,'\"O, mijn volk, ik vraag van u geen beloning hiervoor; mijn beloning is alleen bij Hem, Die mij schiep. Wilt gij dan niet begrijpen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,52,'\"En o, mijn volk, vraag vergiffenis van uw Heer, wend u daarna tot Hem, Hij zal wolken die regelmatig regen nedergieten over u zenden en kracht bij uw kracht voegen. En wend u niet af als schuldigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,53,'Zij zeiden: \"O Hoed, gij hebt ons geen enkel duidelijk bewijs gebracht en wij zullen onze Goden niet in de steek laten, om hetgeen gij zegt noch zullen wij u geloven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,54,'\"Wij kunnen alleen zeggen dat sommige onzer Goden u met kwaad hebben bezocht.\" Hij antwoordde: \"Voorzeker, ik roep Allah tot getuige en getuigt gij ook, dat ik niets met uw afgoden uitstaande heb.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,55,'\"Smeedt daarom allen buiten Hem plannen tegen mij en geeft mij geen uitstel.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,56,'\"Ik heb voorzeker mijn vertrouwen in Allah gesteld, Die mijn Heer en uw Heer is. Er is geen schepsel, dat zich op aarde beweegt, of Hij houdt het in Zijn macht. Voorzeker, mijn Heer is op het rechte pad.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,57,'\"Indien gij u afwendt, dan heb ik u hetgeen waarmede ik tot u ben gezonden medegedeeld, en mijn Heer zal een ander volk uw plaats doen innemen. Gij kunt Hem in het geheel niet deren. Voorzeker, mijn Heer is Bewaker over alle dingen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,58,'En toen Ons gebod kwam, redden Wij Hoed en de gelovigen met hem, door Onze barmhartigheid. En Wij bevrijdden hen van een zware foltering.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,59,'En dezen waren de Aad. Zij verloochenden de tekenen van hun Heer en gehoorzaamden Zijn boodschappers niet en volgden het bevel van elke opstandige vijand op.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,60,'En er werd een vloek op hen gelegd in deze wereld en op de dag der Opstanding. Ziet! de Aad verwierpen hun Heer. Ziet! vervloekt zij de Aad, het volk van Hoed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,61,'En tot de Samoed zeide hun broeder Salih: \"O, mijn volk, aanbid Allah; gij hebt geen God naast Hem. Hij wekte u op vanuit de aarde en vestigde u er. Vraagt vergiffenis aan Hem en bekeert u tot Hem. Voorwaar, mijn Heer is nabij, Verhorende.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,62,'Zij zeiden: \"O Salih, gij waart onze hoop. Verbiedt gij ons datgene te aanbidden wat onze vaderen aanbaden? En wij zijn voorzeker in verontrustende twijfel over hetgeen, waartoe gij ons roept.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,63,'Hij zeide: \"O, mijn volk, zeg mij, als ik een duidelijk bewijs van mijn Heer heb ontvangen en Hij mij barmhartigheid heeft geschonken, wie zal mij dan naast Allah helpen als ik Hem niet gehoorzaam? Gij zult slechts tot mijn ondergang bijdragen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,64,'\"En o, mijn volk, dit is de kamelin van Allah als teken voor u; laat haar daarom met rust opdat zij zich (in vrijheid) op Allah´s aarde moge voeden en doe haar geen kwaad, anders zal de eerste de beste straf u treffen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,65,'Maar zij verlamden haar; toen zeide hij (Salih): \"Vermaakt u voor drie dagen in uw huizen. Dit is een belofte die niet geloochend kan worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,66,'En toen Ons gebod kwam, redden Wij Salih en met hem de gelovigen door Onze barmhartigheid en Wij redden hen van de schande van die dag. Voorzeker, uw Heer is Sterk, Almachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,67,'De straf achterhaalde degenen die kwaad hadden gesticht en zij lagen uitgestrekt in hun huizen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,68,'Alsof zij er nooit in hadden gewoond. Ziet! de Samoed verwierpen hun Heer; ziet! vervloekt zij de Samoed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,69,'En voorzeker Onze boodschappers kwamen met blijde tijdingen tot Abraham. Zij zeiden: \"Vrede zij met u.\" Hij antwoordde: \"Vrede zij met u\" en terstond bracht hij een gebraden kalf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,70,'Maar toen hij zag dat hun handen er zich niet naar uitstrekten, vond bij hen vreemd en vreesde hen. Zij zeiden: \"Vrees niet, want wij zijn tot het volk van Lot gezonden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,71,'En zijn vrouw stond er bij en verwonderde zich, waarop Wij haar de blijde tijding van de geboorte van Izaak gaven en na Izaak van Jacob.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,72,'Zij zeide: \"O wonder! Zal ik een kind baren nu ik een oude vrouw ben en deze mijn echtgenoot een oude man is? Dit is inderdaad iets wonderbaarlijks.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,73,'Zij zeiden: \"Verwondert gij u over Allah´s gebod? De barmhartigheid van Allah en Zijn zegeningen zijn over u, o bewoners van dit huis. Voorzeker, Hij is Geprezen, Glorierijk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,74,'En toen de vrees Abraham verliet en de blijde tijding tot hem kwam, begon hij met ons over het volk van Lot te redetwisten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,75,'Abraham was inderdaad verdraagzaam, zachtmoedig en wendde zich dikwijls (tot God).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,76,'\"O Abraham, wend u hiervan af. Het gebod van uw Heer is uitgegaan en een onafwendbare straf komt over hen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,77,'En toen Onze boodschappers tot Lot kwamen was hij verdrietig en voelde zich bezwaard om hen en zeide: \"Dit is een moeilijke dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,78,'Zijn volk kwam haastig naar hem toe. Ook voordien plachten zij kwaad te doen. Hij (Lot) zeide: \"O, mijn volk, dit zijn mijn dochters, zij zijn te rein voor u. Vrees daarom Allah en onteer mij niet wegens mijn gasten. Is er onder u geen weldenkend man?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,79,'Zij antwoordden: \"Gij weet wel, dat wij geen recht hebben op uw dochters en gij weet ook, wat wij wensen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,80,'Hij zeide: \"Ach, had ik slechts de macht u weerstand te kunnen bieden of tot een machtige steun toevlucht te nemen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,81,'Zij (de boodschappers) zeiden: \"O Lot, Wij zijn de boodschappers van uw Heer, zij zullen u stellig niet bereiken. Vertrek met uw familie gedurende de nacht, laat niemand uwer omkijken dan uw vrouw. Zeker zal haar overkomen wat hun gaat overkomen. Voorwaar, de vastgestelde tijd is de ochtendstond. Is de morgen niet nabij?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,82,'Toen Ons gebod kwam, keerden Wij die stad ondersteboven en Wij deden er brokken klei laag boven laag op regenen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,83,'Die volgens de verordening van uw Heer waren gemerkt. En zulk een straf is niet ver verwijderd van de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,84,'En tot Midian zeide hun broeder Shoaib: \"O mijn volk, aanbid Allah. Gij hebt geen andere God, dan Hem. En geef geen korte maat of licht gewicht. Ik zie u in voorspoed en ik vrees voor u de straf van een alles omvattende dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,85,'\"En o, mijn volk, geef volle maat en juist gewicht met rechtvaardigheid en bedrieg de mensen niet met hun goederen noch sticht onheil op aarde.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,86,'\"Hetgeen Allah u heeft toebedeeld, is beter voor u als gij gelovigen zijt. En ik ben geen bewaker over u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,87,'Zij antwoordden: \"O Shoaib, beveelt uw gebed, dat wij hetgeen onze vaderen aanbaden, zouden verlaten of dat wij zouden ophouden met ons eigendom te doen wat wij willen? Gij zijt inderdaad verstandig, recht geleid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,88,'Hij zeide: \"O mijn volk, wat meent gij indien ik een duidelijk bewijs van mijn Heer heb en Hij mij een goede voorziening heeft geschonken? En ik wil niet, in tegenstelling tot u, mijzelf veroorloven, hetgeen ik u verbied. Ik wil alleen, voor zover ik kan, een verbetering aanbrengen. Alleen door Allah ben ik hiertoe in staat. In Hem vertrouw ik en tot Hem wend ik mij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,89,'\"O, mijn volk, laat vijandigheid jegens mij u niet er toe leiden, dat hetzelfde u overkome als hetgeen het volk van Noach of het volk van Hoed of het volk van Salih overkwam; en het volk van Lot is niet ver van u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,90,'\"En zoek vergiffenis van uw Heer en bekeer u tot Hem. Voorwaar, mijn Heer is Genadig, Liefdevol.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,91,'Zij antwoordden: \"O, Shoaib, wij begrijpen niet veel van hetgeen gij zegt en wij zien voorzeker, dat gij zwak zijt tegenover ons. Was het niet, om uw gezin, wij zouden u zeker stenigen, want gij zijt niet in aanzien bij ons.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,92,'Hij zeide: \"O, mijn volk, is mijn gezin waardiger bij u dan Allah? En gij hebt Hem als waardeloos verworpen. Voorzeker, mijn Heer omvat al hetgeen gij doet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,93,'\"En o, mijn volk, handel op uw wijze, ik handel op de mijne. Gij zult weldra te weten komen over wie een vernederende straf komt en wie een leugenaar is. En wacht af, ik wacht gewis met u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,94,'En toen Ons gebod kwam, redden Wij Shoaib en met hem de gelovigen door Onze barmhartigheid en kastijding greep de onrechtvaardigen zodat zij uitgestrekt in hun huizen lagen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,95,'Alsof zij er nooit hadden gewoond. Ziet! het volk van Midian is vervloekt, zoals Samoed was vervloekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,96,'Wij zonden Mozes voorzeker met Onze tekenen en duidelijk gezag,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,97,'Naar Pharao en zijn leiders, zij volgden het gebod van Pharao maar het gebod van Pharao was in het geheel niet verstandig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,98,'Hij zal op de Dag der Opstanding voor zijn volk uitgaan en hen naar het Vuur leiden. En slecht is de plaats die wordt bereikt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,99,'En er werd hun in dit leven en op de Dag der Opstanding een vloek opgelegd. Slecht is de gave, die zal worden gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,100,'Dit zijn de tijdingen over de steden die Wij u verhalen. Sommige er van bleven staan en andere werden weggevaagd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,101,'En Wij deden hun geen onrecht maar zij deden zichzelf onrecht aan. En hun goden, die zij naast Allah aanriepen, baatten hen in het geheel niet toen het gebod van uw Heer kwam; zij voegden hun slechts verderf toe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,102,'Zo is de greep van uw Heer, wanneer Hij de steden grijpt, terwijl zij kwaad verrichten. Voorzeker, Zijn greep is smartelijk en hard.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,103,'Hierin is gewis een teken voor hem die de straf van het Hiernamaals vreest. Dat is een dag waarop de mensheid zal worden verzameld en dat is een dag waarvan men getuige zal zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,104,'En Wij stellen het slechts voor een bepaalde tijd uit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,105,'De dag, waarop het komt, zal geen ziel zonder Zijn toestemming spreken; dan zullen sommigen hunner ongelukkig en anderen gelukkig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,106,'Degenen dan, die ongelukkig zullen zijn, zullen in het Vuur zijn waarin zij zullen zuchten en steunen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,107,'En er, zolang de Hemelen en de Aarde bestaan in vertoeven, met uitzondering van hetgeen uw Heer moge behagen. Zeker, uw Heer brengt teweeg wat Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,108,'Maar degenen, die gelukkig zullen blijken te zijn, zullen in de Hemel vertoeven, zolang de Hemelen en de Aarde bestaan, met uitzondering van hetgeen uw Heer moge behagen, een gave, die niet zal worden afgesneden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,109,'Wees dus niet in twijfel, omtrent hetgeen deze mensen aanbidden: zij aanbidden slechts, zoals hun vaderen voorheen aanbaden en Wij zullen hun voorzeker hetgeen hen toekomt ten volle en onverminderd geven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,110,'En Wij gaven Mozes voorzeker het Boek, maar men werd er oneens over; en ware het niet door een woord dat reeds van uw Heer was uitgegaan de zaak zou voorzeker voor hen zijn beslist; en waarlijk zij zijn er in een verontrustende twijfel over.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,111,'En uw Heer zal hen allen naar hun werken ten volle vergelden. Hij is wel op de hoogte van hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,112,'Blijf daarom standvastig zoals u is bevolen en ook degenen, die zich met u hebben bekeerd en overtreedt de grenzen niet, want Hij ziet voorzeker, wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,113,'En neig u niet tot de onrechtvaardigen, anders zal het Vuur ook u aanraken en gij zult naast Allah geen vrienden hebben noch zult gij worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,114,'Houd het gebed aan de twee uitersten van de dag en gedurende de eerste uren van de nacht. Voorzeker, goede werken verdrijven kwade werken. Dit is een aanmaning voor degenen die er lering uit trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,115,'En wees standvastig, voorzeker, Allah doet het loon der rechtvaardigen niet verloren gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,116,'Waarom waren er onder de geslachten die v��r u waren dan geen verstandige mensen, die het verderf op aarde konden verhinderen op enkelen na, die Wij uit hun midden redden? Maar de onrechtvaardigen volgden datgene waarin hun overvloed werd verleend en zij waren schuldig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,117,'Uw Heer zal de steden niet onrechtvaardig vernietigen, terwijl de bewoners er van oprecht zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,118,'En indien uw Heer had gewild, zou Hij het mensdom voorzeker tot ��n volk hebben gemaakt, maar zij zullen blijven verschillen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,119,'Met uitzondering van degenen, die uw Heer barmhartigheid heeft betoond - hiervoor heeft Hij hen geschapen - maar het woord van uw Heer: \"Voorwaar Ik zal de hel met djinn en mensen allen tezamen vullen,\" is vervuld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,120,'En de tijdingen der boodschappers verhalen Wij u om daardoor uw hart te versterken. Hierdoor is de waarheid en een vermaning en een les voor de gelovigen tot u gekomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,121,'En zeg tot degenen die niet geloven: \"Handelt naar uw vermogen, wij handelen ook.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,122,'\"En wacht af, wij wachten ook.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,11,123,'En aan Allah behoren de geheimen van de hemelen en de aarde en naar Hem zal het geheel worden teruggebracht. Aanbid Hem daarom en leg uw vertrouwen in Hem. En uw Heer is niet onachtzaam over hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,1,'Alif Laam Raa. Dit zijn de verzen van het Boek, dat alles verklaart:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,2,'Wij hebben het geopenbaard - als de Arabische Koran- opdat gij moogt begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,3,'Wij verhalen u het schoonste verhaal door u deze Koran te openbaren, ofschoon gij voorheen onwetend waart.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,4,'Toen Jozef tot zijn vader zeide: \"O mijn vader, (in mijn droom) zag ik elf sterren en de zon en de maan en ik zag ze zich voor mij nederwerpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,5,'Hij zeide: \"O, mijn zoon, verhaal uw broedars uw droom niet, anders zullen zij plannen tegen u smeden, want Satan is een openlijke vijand der mensen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,6,'\"En zo zal uw Heer u verkiezen en u de verklaring der dingen onderwijzen en Zijn gunst aan u en aan de familie van Jacob vervohnaken, zoals Hij die voordien aan twee uwer voorvaderen, Abraham en Izaak had voltooid. Voorwaar, uw Heer is Alwetend, Alwijs.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,7,'Voorzeker, er zijn voor de zoekers (naar waarheid) tekenen in (de geschiedenis van) Jozef en zijn broeders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,8,'Toen zij zeiden: \"Voorwaar, Jozef en zijn broeder zijn onze vader liever dan wij, ofschoon wij een sterke groep zijn. Voorzeker, onze vader dwaalt openlijk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,9,'\"Doodt Jozef of verdrijft hem naar een (ver) land, zodat uw vaders gunst uitsluitend voor u moge zijn, waarna gij een rechtvaardig volk zult worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,10,'E�n hunner zeide: \"Doodt Jozef niet, maar als gij iets moet doen werpt hem dan op de bodem van een diepe put; iemand uit een karavaan zal hem opnemen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,11,'Zij zeiden: \"O, onze vader, waarom vertrouwt gij ons niet aangaande Jozef, hoewel wij hem welgezind zijn?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,12,'\"Zend hem morgen met ons mede, opdat hij zich moge vermaken en spelen en wij zullen voorzeker zijn bewakers zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,13,'Hij zeide: \"Het verdriet mij, dat gij hem zoudt medenemen en ik vrees, dat de wolf hem zal verslinden terwijl gij niet op hem let.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,14,'Zij zeiden: \"Indien de wolf hem zou verslinden terwijl wij een sterke groep vormen, dan zijn wij inderdaad de verliezers.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,15,'Toen zij hem medenamen, kwamen zij overeen hem op de bodem van een diepe put neer te laten en Wij zonden hem een openbaring: \"Gij; zult hun van deze zaak vertellen zonder dat zij het beseffen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,16,'´s Avonds kwamen zij wenend tot hun vader.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,17,'En zeiden: \"O, onze vader, wij hielden een wedloop en lieten Jozef met onze goederen achter en de wolf verslond hem; maar zelfs al spreken wij de waarheid, zult gij ons niet geloven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,18,'En zij brachten zijn hemd met bloed, dat niet van hem was. Hij (Jacob) zeide: \"Neen, gij hebt de zaak veel te licht opgevat. Daarom is geduld passend. En het is Allah Wiens hulp dient te worden gezocht over hetgeen gij beweert.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,19,'Er kwam een karavaan langs en deze zond een waterputter, die zijn emmer nederliet. \"O, goed nieuws,\" zeide hij. \"Hier is een jongeling.\" En zij verborgen hem als een stuk koopwaar en Allah wist goed, wat zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,20,'Zij verkochten hem voor een geringe prijs, een paar zilverstukken, want zij waren onverschillig jegens hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,21,'En de Egyptenaar, die hem kocht, zeide tot zijn vrouw: \"Maak zijn verblijf behoorlijk. Het is waarschijnlijk dat hij ons van nut kan zijn, of dat wij hem als zoon aannemen.\" En zo vestigden Wij Jozef in het land, opdat Wij hem in het verklaren der dingen mochten onderwijzen. Allah heeft macht over Zijn gebod, maar de meeste mensen weten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,22,'Toen hij volwassen was, schonken Wij hem oordeel en kennis; zo belonen Wij de goeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,23,'En zij, in wier huis hij was, zocht hem (tegen zijn wil) te verleiden. Zij grendelde de deuren en zeide: \"Kom nu.\" Hij antwoordde: \"Dat verhoede Allah, hij is mijn heer. Hij heeft mijn verblijf waardig gemaakt. Voorwaar, de boosdoeners slagen nooit.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,24,'En zij nam een besluit betreffende hem en hij nam een besluit betreffende haar. Als hij geen duidelijk teken van zijn Heer had gezien, (kon hij zo´n vastberadenheid niet hebben getoond). Zo kwam het dat Wij het kwaad en de onbetamelijkheid van hem mochten afwenden. Voorzeker hij was een Onzer uitverkoren dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,25,'En zij holden beiden naar de deur en zij scheurde zijn hemd van achteren en zij ontmoetten haar echtgenoot aan de deur. Zij zeide: \"Wat zal de straf zijn voor iemand die kwade bedoelingen had met uw vrouw, anders dan gevangenneming of een pijnlijke kastijding?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,26,'Hij (Jozef) zeide: \"Zij is het die mij tegen mijn wil zocht te verleiden.\" En een familielid van haar getuigde: \"Als zijn hemd van voren is gescheurd, heeft zij de waarheid gesproken en behoort hij tot de leugenaars,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,27,'Maar als zijn hemd van achteren is gescheurd, heeft zij gelogen en behoort hij tot de waarachtigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,28,'Toen hij (haar man) zag dat zijn hemd van achteren was gescheurd, zeide hij: \"Dit is zeker een list van u, vrouwen. Uw list is inderdaad sterk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,29,'\"O, Jozef, wend u hiervan af en gij (vrouw), vraag vergiffenis voor uw zonde. Gij behoort zeker tot de schuldigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,30,'En de vrouwen in de stad zeiden: \"De vrouw van Aziez zoekt haar slaaf tegen zijn wil te verleiden. Hij heeft haar met verliefdheid vervuld. Wij zien haar inderdaad klaarblijkelijk dwalen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,31,'En toen zij van hun plannen hoorde, nodigde zij haar uit en bereidde haar een maaltijd en gaf ieder een mes en zeide dan (tot Jozef): \"Ga naar hen toe.\" En toen zij hem zagen achtten zij hem grotelijks en zij sneden zich in de handen en zeiden: \"Allah zij verheerlijkt. Dit is geen mens, dit is een edele engel.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,32,'Zij zeide: \"Dit is hij nu over wie gij mij beschuldigdet, ik zocht hem werkelijk tegen zijn wil te verleiden, maar hij redde zich. En als hij nu niet doet wat ik hem verzoek, zal hij zeker gevangen genomen en vernederd worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,33,'Hij (Jozef) zeide: \"O mijn Heer, ik zou de gevangenis verkiezen boven hetgeen waartoe zij mij roepen; tenzij Gij haar list van mij afwendt zal ik mij tot haar neigen en tot de onwetenden behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,34,'Daarom verhoorde zijn Heer zijn gebed en wendde hun list van hem af. Voorwaar, Hij is de Alhorende, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,35,'Dus kwam het hun (mannen) voor, nadat zij de tekenen van zijn onschuld hadden gezien, dat zij hem voor een tijd gevangen moesten nemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,36,'En er gingen met hem twee jonge mannen de gevangenis binnen. Een hunner zeide: \"Ik zag mij wijn persen.\" En de andere zeide: \"Ik zag mij in een droom brood op mijn hoofd dragen waarvan de vogelen aten. Geef ons de verklaring er van, voorzeker, wij zien dat gij tot de goeden behoort.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,37,'Hij antwoordde: \"Het voedsel, dat u wordt gegeven, zal niet tot u komen, voordat ik u de verklaring er van heb gegeven. Dit is naar aanleiding van hetgeen mijn Heer mij heeft onderwezen. Ik heb van de godsdienst van het volk dat niet in Allah en in het Hiernamaals gelooft, afstand gedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,38,'\"En ik volg de godsdienst van mijn vaderen, Abraham, Izaak en Jacob. Het betaamt ons niet dat wij iets met Allah vereenzelvigen. Dit behoort tot Allah´s genade voor ons en de mensheid, maar de meeste mensen zijn niet dankbaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,39,'\"O, mijn twee medegevangenen, zijn verscheidene Heren beter of is Allah, de Ene, de Opperste beter?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,40,'\"Gij aanbidt naast Allah niets, dan ijdele namen die gij hebt uitgedacht, gij en uw vaderen; Allah heeft daar geen gezag voor nedergezonden. De beslissing berust bij Allah alleen. Hij heeft bevolen dat gij naast Hem niets zult aanbidden. Dit is de juiste godsdienst, maar de meeste mensen beseffen het niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,41,'\"O mijn twee medegevangenen, wat ��n uwer betreft, hij zal wijn voor zijn Heer schenken en wat de ander betreft, hij zal worden gekruisigd, zodat de vogels van zijn hoofd zullen eten. De zaak waarover gij hebt gevraagd, is besloten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,42,'En hij zeide tot degene van hen, van wie hij wist dat hij bevrijd zou worden: \"Vermeld mij bij uw heer.\" Maar Satan deed hem vergeten het aan zijn heer te zeggen daarom bleef hij voor enige jaren in de gevangenis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,43,'En de koring (van Egypte) zeide: \"Ik zag zeven vette koeien, die door zeven magere koeien werden verslonden en zeven groene korenaren en zeven verwelkte aren. O gij leiders, legt mij de betekenis van mijn droom uit als gij een droom kunt verklaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,44,'Zij antwoordden: \"Het zijn verwarde dromen en wij kennen de verklaring van zulke dromen niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,45,'En degene van de twee die bevrijd was, herinnerde zich na enige tijd Jozef, en zeide toen: \"Ik zal u de verklaring er van laten weten, zend mij daarom.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,46,'\"O, Jozef! gij man der waarheid, leg ons de betekenis uit van zeven vette koeien die door zeven magere worden verslonden en van zeven groene korenaren en andere verwelkte aren opdat ik tot het volk moge terugkeren, zodat zij mogen weten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,47,'Hij antwoordde: \"Gij zult zeven jaren lang voortdurend zaaien en wat gij maait in de aar laten, met uitzondering van een weinig, dat gij zult eten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,48,'\"Dan zullen er nadien zeven harde jaren komen, die al hetgeen gij van te voren hebt opgeslagen zullen verteren, met uitzondering van een weinig dat gij zult bewaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,49,'\"Dan zal er nadien een jaar komen, waarin de mensen zullen worden geholpen en waarin zij (vruchten) zullen persen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,50,'En de koning zeide: \"Brengt hem tot mij.\" Maar toen de boodschapper tot hem (Jozef) kwam, zeide hij: \"Ga terug naar uw heer en vraag hem hoe het met de vrouwen is gesteld die zich in de handen sneden, voorzeker mijn Heer kent haar sluwe plan goed.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,51,'Hij, (de koning) zeide tot de vrouwen: \"Wat was het geval met u toen gij Jozef tegen zijn wil zocht te verleiden?\" Zij zeiden: \"Allah zij verheerlijkt. Wij hebben geen kwaad van hem geweten.\" De vrouw van de Aziez zeide: \"Nu is de waarheid aan het licht gekomen. Ik was het die hem tegen zijn wil zocht te verleiden en hij behoort zeker tot de waarachtigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,52,'\"Dit is, opdat hij moge weten dat ik hem in zijn afwezigheid niet ontrouw was en dat Allah het plan van de ontrouwe mensen niet laat slagen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,53,'\"En ik verklaar mijzelf niet vrij (van zwakheid) te zijn, want het menselijke, ik´ spoort tot het kwade aan, uitgezonderd dat waarover mijn Heer barmhartigheid betoont. Voorzeker, mijn Heer is Vergevensgezind, Genadevol.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,54,'En de koning zeide: \"Brengt hem bij mij, ik wil hem voor mijzelf houden.\" En toen hij tot hem (Jozef) had gesproken, zeide hij: \"Gij zijt van deze dag af een man van positie en vertrouwen bij ons.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,55,'Hij antwoordde: \"Stel mij aan over de schatten van het land want ik ben een deskundig bewaarder.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,56,'En zo vestigden Wij Jozef in het land. Hij vertoefde er in, waar hij ook wilde. Wij schenken Onze barmhartigheid aan wie Ons behaagt en Wij laten het loon Aer rechtvaardigen niet te gronde gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,57,'En het loon van het Hiernamaals is zeker beter voor degenen die geloven en God vrezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,58,'En Jozefs broeders kwamen en gingen bij hem binnen en hij herkende hen, maar zij herkenden hem niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,59,'En toen hij hen van levensmiddelen had voorzien, zeide hij: \"Brengt mij uw broeder van vaderskant. Ziet gij niet, dat ik u met volle maat geef en dat ik een goed gastheer ben?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,60,'\"Maar indien gij hem niet tot mij brengt dan zal er van mij geen maat (koren) voor u zijn noch zult gij in mijn nabijheid komen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,61,'Zij antwoordden: \"Wij zullen trachten zijn vader hiertoe over te halen, wij zullen het voorzeker kunnen doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,62,'En hij (Jozef) zeide tot zijn dienaren: \"Stopt hun geld in de zadeltassen, dat zij het mogen herkennen, wanneer zij tot hun familie terugkeren, opdat zij terug mogen komen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,63,'En toen zij tot hun vader terugkeerden, zeiden zij: \"Onze vader, een (verdere) maat is ons ontzegd, zend daarom onze broeder met ons mede, opdat wij onze maat (koren) mogen verkrijgen en wij zullen zeker op hem passen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,64,'Hij (Jacob) antwoordde: \"Zal ik u hem toevertrouwen, zoals ik u voorheen zijn broeder toevertrouwde? Maar Allah is de beste Beschermer en Hij is de Genadigste der genadigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,65,'En toen zij hun reisgoederen openden, vonden zij hun geld aan hen teruggegeven. Zij riepen uit: \"O, onze vader, wat kunnen wij meer wensen? Hier is ons geld aan ons teruggegeven. Wij zullen (nogmaals) koren voor onze familie halen en op onze broeder passen en wij zullen als toegift de maat van een kameellast ontvangen. Dat is een maat die gemakkelijk verkrijgbaar is.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,66,'Hij (Jacob) zeide: \"Ik zal hem niet met u medezenden voordat gij mij een ernstige belofte aflegt in de naam van Allah, dat gij hem zeker tot mij zult brengen tenzij gij allen omsingeld zoudt worden.\" En toen zij de belofte hadden afgelegd, zeide hij: \"Allah waakt over hetgeen wij zeggen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,67,'En hij zeide: \"O mijn zonen, gaat niet door ��n poort binnen maar gaat door verschillende poorten binnen; en ik kan u in niets tegen Allah helpen. De beslissing berust alleen bij Allah. In Hem stel ik mijn vertrouwen en laat allen die willen vertrouwen, alleen in Hem hun vertrouwen stellen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,68,'Maar toen zij (de stad) binnen gingen zoals hun vader hen had bevolen, kon hen dit tegen Allah toch niets baten; het was slechts dat Jacob zijn zin gedaan kreeg, want hij had voorzeker grote kennis, omdat Wij hem hadden onderwezen, maar de meeste mensen weten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,69,'En toen zij Jozef bezochten, huisvestte deze zijn broeder bij zich. En hij zeide: \"Ik ben uw broeder, treur daarom niet over hetgeen zij hebben gedaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,70,'En toen hij hen van hun provisie had voorzien, legde hij een drinkbeker in zijn broeders zadeltas. Toen riep een omroeper: \"O, karavaan, gij zijt waarlijk dieven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,71,'Zij vroegen, zich tot hem wendend: \"Wat mist gij?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,72,'Men antwoordde: \"Wij missen des konings maatkop en wie hem brengt zal een kameellast koren ontvangen en ik ben er borg voor.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,73,'Zij antwoordden: \"Bij Allah, gij weet goed, dat wij niet kwamen om slecht in het land te handelen en wij zijn geen dieven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,74,'Zij (de Egyptenaren) zeiden: \"Wat zal er dan de straf voor zijn als gij leugenaars zijt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,75,'Zij antwoordden: \"De straf er voor zal zijn: hij, in wiens zadeltas ze wordt gevonden zal zelf de boete er voor zijn. Zo straffen wij de boosdoeners.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,76,'Daarna begon hij met (het onderzoek van) hun tassen alvorens de tas van zijn broeder (te onderzoeken); dan nam men hem (drinkbeker) uit zijn broeders tas. Zo maakten Wij plannen voor Jozef. Hij kon zijn broeder volgens de wet van de koning (van Egypte) niet houden, tenzij Allah het zo had gewild. Wij bevorderen in graden (van kennis en eer) wie Wij willen. Boven elke wetende staat de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,77,'Zij (zijn broeders) zeiden: \"Als deze heeft gestolen, had zijn broeder voorheen ook diefstal gepleegd.\" Maar Jozef hield het in zijn hart geheim en onthulde het hun niet. Hij zeide: \"Gij verkeert in een slechte toestand. Allah weet het beste wat gij beweert.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,78,'Zij zeiden: \"O Aziez, hij heeft een zeer oude vader, neem daarom ��n onzer in zijn plaats, want wij zien dat gij tot degenen behoort die goed doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,79,'Hij (Jozef) zeide: \"Allah verhoede, dat wij iemand anders dan hem zouden nemen bij wie wij ons eigendom vonden; want dan zouden wij zeker onrechtvaardig zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,80,'En toen zij wanhoopten trokken zij zich terug om in afzondering te beraadslagen. De oudste zeide: \"Weet gij niet, dat uw vader een plechtige belofte in de naam van Allah van u heeft genomen en hoe gij voorheen in uw plicht tegenover Jozef hebt gefaald? Ik zal het land daarom niet verlaten voordat mijn vader het mij toestaat, of Allah voor mij beslist en Hij is de beste Beoordelaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,81,'\"Keert gij tot uw vader terug en zegt: ´Onze vader uw zoon heeft gestolen en wij hebben alleen hetgeen wij wisten vermeld en wij konden waarlijk over het ongeziene niet waken.´');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,82,'´En vraag het volk der stad waarin wij waren en de karavaan waarmede wij reisden en wij spreken voorzeker de waarheid.´\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,83,'Hij (hun vader) zeide: \"Neen, uw ziel heeft een groot iets voor u gering gemaakt. Daarom is geduld passend. Het is mogelijk, dat Allah hen allen te zamen tot mij zal brengen; waarlijk Hij is de Alwetende, de Alwijze.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,84,'En hij wendde zich van hen af en zeide: \"O ik heb verdriet over Jozef.\" En zijn ogen werden gevuld met tranen van smart doch hij bedwong zich.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,85,'Zij zeiden: \"Bij Allah, gij zult niet ophouden over Jozef te praten, totdat gij zijt weggekwijnd of totdat gij te gronde gaat.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,86,'Hij antwoordde: \"Ik klaag alleen over mijn zorg en verdriet tot Allah en ik weet van Allah, wat gij niet weet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,87,'\"O mijn zonen, gaat en zoekt naar Jozef en zijn broeder en wanhoopt niet aan de genade van Allah, want niemand wanhoopt aan Allah´s barmhartigheid dan het ongelovige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,88,'En toen zij (opnieuw) voor hem (Jozef) kwamen, zeiden zij: \"O, Aziez, armoede heeft ons en onze familie getroffen en wij hebben een armzalige geldsom meegebracht, geef ons daarvoor de volle maat en wees liefdadig. Voorzeker, Allah beloont de liefdadigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,89,'Hij zeide: \"Weet gij wat gij Jozef en zijn broeder aandeedt, toen gij onwetend waart?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,90,'Zij vroegen. \"Zijt gij dan Jozef?\" Hij zeide: \"Ik ben Jozef en dit is mijn broeder. Allah is ons inderdaad genadig geweest. Voorwaar, wie godvrezend en geduldig is - Allah doet het loon der goeden nooit verloren gaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,91,'Zij antwoordden: \"Bij Allah, waarlijk Allah heeft u boven ons verkozen en wij zijn inderdaad zondaren geweest.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,92,'Hij (Jozef) zeide: \"Heden zij er geen verwijt tegen u: Moge Allah u vergeven, Hij is de Genadigste der genadigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,93,'\"Gaat met dit hemd van mij en legt het voor het aangezicht van mijn vader neder; hij zal het begrijpen. En brengt mij uw gehele familie.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,94,'En toen de karavaan (uit Egypte) vertrok, zeide hun vader: \"Ik bemerk voorzeker de geur van Jozef, zelfs al ziet gij mij voor zwakzinnig aan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,95,'Zij antwoordden: \"Bij Allah, gij houdt zeker aan uw oude dwaling vast.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,96,'En toen de drager van de blijde tijding kwam, legde hij het (hemd) voor hem (Jacob) neder zodat hij zekerheid verkreeg. Dan riep hij uit: \"Zei ik u niet: ´Ik weet van Allah wat gij niet weet´?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,97,'Zij antwoordden: \"O, onze vader, vraag voor ons vergiffenis voor onze zonden: wij zijn inderdaad zondaren geweest.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,98,'Hij (Jacob) zeide: \"Ik zal mijn Heer om vergiffenis voor u vragen. Voorzeker, Hij ls de Vergevensgezinde, de Genadevolle.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,99,'En toen zij tot Jozef kwamen, huisvestte hij zijn ouders bij zich en zeide: \"Komt zoals het Allah behaagt Egypte in vrede binnen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,100,'Hij hief zijn ouders op de troon en zij wierpen zich voor hem neder. En hij zeide: \"O mijn vader, dit is de vervulling van mijn vroegere droom. Mijn Heer heeft deze verwezenlijkt. En Hij schonk mij een gunst toen Hij mij uit de gevangenis verloste en u uit de woestijn bracht, nadat Satan tweedracht tussen mij en mijn broeders had gezaaid. Voorzeker, mijn Heer is goedertieren voor wie Hij wil. Waarlijk, Hij is de Alwetende, de Alwijze.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,101,'\"O, mijn Heer, Gij hebt mij macht gegeven en de verklaring van dromen onderwezen. O, Schepper der hemelen en der aarde, Gij zijt mijn Beschermer in deze wereld en in het Hiernamaals. Doe mij sterven als Moslim en verenig mij met de rechtvaardigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,102,'Dit behoort tot de tijdingen van het verborgene die Wij u (o Profeet ) openbaren. Gij waart niet bij hen, toen zij zich (tegen u) verenigden en plannen smeedden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,103,'En de meeste mensen willen niet geloven zelfs al wenst gij het vurig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,104,'Gij vraagt er hun geen beloning voor. Het is niets dan een vermaning aan alle werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,105,'En hoeveel tekenen zijn er niet in de hemelen en op aarde waaraan zij, zich afwendend, voorbijgaan!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,106,'En de meesten hunner geloven niet in Allah, zonder medegoden aan Hem toe te schrijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,107,'Voelen zij zich dan nu veilig voor het komen van een overweldigende straf over hen van Allah of voor het onverwacht komen van het Uur over hen, terwijl zij het niet bemerken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,108,'Zeg: \"Dit is mijn weg: ik roep tot Allah in zeker weten, ik en mijn volgelingen. Heilig is Allah en ik behoor niet tot de afgodendienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,109,'En Wij zonden v��r u slechts mensen uit de inwoners der steden, die Wij inspireerden. Hebben zij dan niet op aarde gereisd en gezien wat het einde was dergenen die v��r hen waren? En het tehuis van het Hiernamaals is voorzeker beter voor degenen, die vrezen. Wilt gij dan niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,110,'Totdat, wanneer de boodschappers wanhoopten en zij dachten dat zij voor leugenaars verden gehouden, Onze hulp tot hen kwam en dan werd gered, wie Ons behaagde. En Onze kastijding wordt van een zondig volk niet afgewend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,12,111,'Er is in hun verhaal gewis een les voor mensen van begrip. Het is niet iets, dat is verzonnen, doch een vervulling van hetgeen er v��r is en een uiteenzetting van alle dingen en een leiding en een barmhartigheid voor een volk, dat gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,1,'Alif Laam Miem Raa. Dit zijn de verzen van het Boek. En hetgeen u door uw Heer is geopenbaard is waar, maar de meeste mensen geloven niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,2,'Allah is Hij, Die de hemelen heeft doen verrijzen zonder pilaren die gij kunt zien. Daarna zette Hij Zich op de troon. En Hij heeft de zon en de maan in dienst gesteld; elk volgt zijn baan tot een vastgestelde termijn. Hij regelt het al. Hij legt de tekenen duidelijk uit, opdat gij zeker zult zijn van de ontmoeting met uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,3,'En Hij is het, Die de aarde uitspreidde, er bergen op verhief en rivieren op vormde. En Hij maakte er elke vruchtensoort in twee geslachten op. Hij doet de nacht de dag bedekken. Voorwaar, daarin zijn tekenen voor een volk, dat nadenkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,4,'En er zijn op aarde aan elkaar grenzende streken en tuinen van wijnstokken, en korenvelden en dadelpalmen, met ��n wortel of met verschillende wortels, zij worden met hetzelfde water besproeid en toch doen Wij sommigen er van in fruit boven anderen uitmunten. Daarin zijn tekenen voor een volk, dat begrijpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,5,'En indien gij u verwondert, dan is hun zeggen verwonderlijker: \"Wanneer wij stof zijn geworden, zullen wij dan opnieuw worden geschapen?\" Deze zijn het, die hun Heer hebben verworpen, daarom zullen zij ketenen om hun hals hebben en de bewoners van het Vuur zijn; daarin zullen zij vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,6,'En zij vragen eerder het kwade van u dan het goede; hoewel er voor hen voorbeeldige straffen zijn voorgekomen. Voorwaar, uw Heer is vol van vergiffenis voor het mensdom, ondanks hun onrechtvaardigheid en voorwaar, uw Heer is streng in het vergelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,7,'En de ongelovigen zeggen: \"Waarom is hem (de profeet) geen teken van zijn Heer nedergezonden?\" Gij zijt waarlijk een waarschuwer en er is voor elk volk een leidsman.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,8,'Allah weet wat elke vrouw baart en wat de baarmoeders niet voldragen en wat zij doen groeien. En bij Hem heeft alles een eigen maat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,9,'Hij is de Kenner van het onzienlijke en het zienlijke, de Grote, de Verhevene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,10,'Voor Hem is hij gelijk die onder u het woord verbergt en hij die het openlijk uit; alsook hij, die zich ´s nachts verbergt en hij, die overdag (openlijk) voortgaat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,11,'Er zijn voor hem (de Boodschapper) bewakers (engelen) v��r en achter hem; zij bewaken hem door het gebod van Allah. Voorzeker, Allah verandert de toestand van een volk niet voordat zij hetgeen in hun hart is veranderen. En wanneer Allah een volk wenst te straffen, is er geen afwenden mogelijk, noch hebben zij een helper naast Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,12,'Hij is het, Die u de bliksem toont vrees en hoop veroorzakende en Hij doet zware wolken ontstaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,13,'En de donder verkondigt Zijn glorie met de lof die Hem toekomt, en de engelen doen het uit ontzag voor Hem en Hij zendt de bliksem en treft er mede, wie Hij wil; nog steeds redetwisten zij over Allah. terwijl Hij streng is in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,14,'Tot Hem is het ware gebed. En degenen, die zij buiten Hem aanroepen, verhoren hen in het geheel niet, doch zij zijn als iemand die zijn handen uitstrekt naar het water, opdat het zijn mond zal bereiken, maar het kan hem nooit bereiken. En het aanroepen der ongelovigen gaat slechts verloren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,15,'En wie in de hemelen en op aarde is, onderwerpt zich willens of onwillens aan Allah en hun schaduwen doen ´s morgens en ´s avonds hetzelfde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,16,'Zeg: \"Wie is de Heer der hemelen en der aarde?\" Zeg: \"Allah.\" Zeg: \"Hebt gij naast Hem dan helpers genomen, die voor zich over goed noch kwaad macht hebben?\" Zeg: \"Kunnen de blinde en de ziende gelijk zijn?\" Of kan de duisternis gelijk zijn aan het licht? Of schrijven zij aan Allah medegoden toe die iets, op Zijn schepping lijkende hebben geschapen, zodat beide scheppingen hun gelijk voorkomen? Zeg: \"Allah is de Schepper aller dingen en Hij is de Ene, de Opperste.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,17,'Hij zendt water van de hemel neder, zodat stromen overeenkomstig hun afmeting vloeien en de vloed zwellend schuim draagt. En van hetgeen zij (de mensen) in het vuur verhitten om sieraden en gereedschappen te vervaardigen komt een soortgelijk schuim. Zo licht Allah de waarheid en de valsheid toe. Wat nu het schuim betreft, het gaat als uitschot weg, maar wat betreft hetgeen de mensen tot nut strekt, dit blijft op aarde. Zo geeft Allah de gelijkenissen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,18,'Er zal voor degenen die aan hun Heer gehoor geven het goede zijn, en degenen, die Hem geen gehoor geven - deze zouden, indien zij al hetgeen op aarde is en het gelijke er aan toegevoegd, bezaten, het gaarne als losprijs aanbieden. Dezen zijn het die een boze afrekening zullen ontvangen en hun tehuis is de hel. En dit is een slechte rustplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,19,'Is dan hij die weet, dat hetgeen u van uw Heer is geopenbaard de waarheid is, gelijk aan hem die blind is? Alleen degenen die met begrip zijn begiftigd trekken er lering uit,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,20,'Degenen, die Allah´s verbond vervullen en dit niet breken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,21,'En degenen, die verbinden, wat Allah bevolen heeft verbonden te worden en die hun Heer vrezen en de kwade afrekening duchten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,22,'En degenen, die volharden in het zoeken naar de gunst van hun Heer en het gebed houden en van hetgeen waarvan Wij hen hebben voorzien, heimelijk en openlijk weggeven en die het kwade met het goede afwenden, dezen zijn het die de beloning en het goede tehuis zullen ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,23,'Tuinen der eeuwigheid. Zij en degenen van hun vaderen en hun echtgenoten en hun kinderen rechtvaardig zijn zullen deze binnengaan. En engelen zullen van iedere poort tot hen komen, (zeggende):');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,24,'\"Vrede zij over u, omdat gij geduldig waart; ziet, hoe uitstekend is het uiteindelijke tehuis.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,25,'En degenen, die het verbond van Allah breken nadat zij het hadden bevestigd en hetgeen Allah heeft bevolen verenigd te zijn, afsnijden en op aarde wanorde stichten, hen treft de vloek en zij zullen een slecht tehuis hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,26,'Allah vergroot en vermindert de voorziening voor wie Hem behaagt. En zij (de mensen) verheugen zich in het tegenwoordige leven, terwijl het tegenwoordige leven slechts een (kortstondig) vermaak is vergeleken met het volgende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,27,'En degenen die niet geloven, zeggen: \"Waarom is hem (de profeet) geen teken van zijn Heer nedergezonden?\" Zeg: \"Allah laat diegene dwalen die Hij wil en leidt tot Zichzelf degene die zich bekeert.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,28,'Degenen die geloven, en wier hart rust vindt in de gedachtenis aan Allah. Ziet toe! in het gedenken van Allah kunnen de harten rust vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,29,'Degenen die geloven en goede werken doen - voor hen is geluk en een uitstekende plaats van terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,30,'Zo hebben Wij u tot een volk gezonden - aan hetwelk andere volkeren zijn voorafgegaan - opdat gij hun hetgeen Wij u hebben geopenbaard, moogt verkondigen doch zij verwerpen de Barmhartige. Zeg: \"Hij is mijn Heer; er is geen God naast Hem. In Hem leg ik mijn vertrouwen en tot Hem is mijn terugkeer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,31,'En als er een Koran was, waarmede de bergen konden worden verzet, de aarde kon worden gespleten, of de doden tot spreken konden worden gebracht, (zouden zij er nog niet in geloven). \"Neen, de zaak berust geheel bij Allah!\" Zijn de gelovigen het niet te weten gekomen dat, indien Allah het wilde, Hij het gehele mensdom zou hebben geleid? En de ongelovigen zullen onophoudelijk door rampen getroffen worden wegens hun daden, of het zult bij hun huizen neerkomen, totdat de belofte van Allah tot stand komt. Voorzeker, Allah faalt niet in Zijn belofte.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,32,'Voorzeker boodschappers werden v��r u ook bespot, maar Ik schonk uitstel aan de ongelovigen. Dan greep Ik hen en hoe (vreselijk) was Mijn straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,33,'Zal Hij, Die over elke ziel waakt ten aanzien van hetgeen zij verdient (hen dan laten gaan)? Toch kennen zij medegoden aan Allah toe. Zeg: \"Noemt hen.\" Zoudt gij Hem willen inlichten over hetgeen Hem op aarde onbekend was? Of is het slechts een ledig gezegde? Neen, maar het plan der ongelovigen is voor hen schoonschijnend gemaakt en zij worden van de juiste weg teruggehouden. En hij, die Allah laat dwalen zal geen helper vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,34,'Er is voor hen een straf in het tegenwoordige leven; doch de straf van het Hiernamaals is gewis zwaarder en zij zullen tegen Allah geen verdediger hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,35,'Het beeld van de Hemel die de godvrezenden is beloofd, is, dat er stromen in vloeien, en dat zijn fruit en schaduw eeuwigdurend zijn. Dit is het loon van de rechtvaardig en maar het loon van de ongelovigen is het Vuur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,36,'En degenen, wie Wij het Boek hebben gegeven, verheugen zich in hetgeen u is geopenbaard. En er zijn sommige der partijen die er een gedeelte van ontkennen. Zeg: \"Het is mij bevolen, Allah te aanbidden en niets met Hem te vereenzelvigen. Tot Hem roep ik en tot Hem is mijn terugkeer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,37,'En zo hebben Wij het als een duidelijk oordeel geopenbaard. En als gij, nadat kennis tot u is gekomen hun boze wensen volgt, zult gij aan Allah vriend, noch beschermer hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,38,'En Wij zonden inderdaad boodschappers v��r u en Wij gaven hun vrouwen en kinderen. En het is een boodschapper niet mogelijk een teken te brengen dan door het gebod van Allah. Voor elke periode is er een (Goddelijk) besluit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,39,'Allah doet te niet wat Hij wil en bevestigt wat Hij wil en bij Hem is de oorsprong van het Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,40,'Of Wij u sommige der dingen doen zien waarmede Wij hen hebben bedreigd, of u doen sterven - op u rust (alleen) de verkondiging (der boodschap) en op Ons de verrekening.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,41,'Zien zij niet dat Wij tot hun land komen, het van de buitenste zijden (grenzen) verminderend Allah besluit en niemand kan Zijn besluit omverwerpen. En Hij is vlug in het vergelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,42,'En degenen, die v��r hen waren, verzonnen plannen, maar (het slagen van) alle plannen berust bij Allah. Hij weet wat elke ziel verdient en de ongelovigen zullen weldra weten voor wie de uiteindelijke woonplaats is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,13,43,'De ongelovigen zeggen: \"Gij zijt geen gezant.\" Zeg: \"Allah, alsmede hij die kennis van het Boek bezit zijn toereikend als getuigen tussen u en mij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,1,'Alif Laam Raa. Dit is een Boek dat Wij u hebben geopenbaard, opdat gij de mensen door het gebod van hun Heer uit de duisternis tot het licht moogt brengen op het pad van de Almachtige, de Geprezene;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,2,'Van Allah, aan Wie wat er ook in de hemelen en op aarde is, toebehoort. Maar wee de ongelovigen wegens een strenge straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,3,'Die het tegenwoordige leven boven het Hiernamaals verkiezen en (anderen) van het pad van Allah afhouden het krom wensend - dezen zijn het die ver afgedwaald zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,4,'Wij zonden geen boodschapper dan met de taal van zijn volk, zodat hij (het) hun duidelijk moge maken. Dan laat Allah dwalen wie Hij wil en leidt wie Hij wil. Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,5,'En Wij zonden Mozes met Onze tekenen, zeggende: \"Breng uw volk uit de duisternis tot het licht en herinner hen aan de dagen van Allah.\" Daarin zijn voorzeker tekenen voor ieder die geduldig en dankbaar is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,6,'En toen Mozes tot zijn volk zeide: \"Gedenk Allah´s gunst aan u toen Hij u van Pharao´s volk redde, dat u met een smartelijke foltering kwelde, uw zonen doodde en uw vrouwen spaarde; daarin was een grote beproeving van uw Heer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,7,'En toen uw Heer verklaarde: \"Als gij dankbaar zijt zal ik u rneer geven, maar als gij ondankbaar zijt is Mijn straf inderdaad streng.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,8,'En Mozes zeide: \"Als gij ondankbaar zijt, gij en al degenen die op aarde zijn, voorwaar, Allah is Zichzelf - genoeg, Geprezen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,9,'Zijn de tijdingen niet tot u gekomen van degenen die v��r u waren, het volk van Noach en van Aad en Samoed en degenen (die) na hen (kwamen)? Niemand behalve Allah kent ze. Hun boodschappers kwamen met duidelijke tekenen tot hen, maar zij deden hen zwijgen en zeiden: \"Wij geloven niet in hetgeen, waarmede gij zijt gezonden en wij zijn zeker in twijfel over hetgeen, waartoe gij ons roept.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,10,'Hun boodschappers antwoordden: \"Bestaat er twijfel over Allah, Schepper der hemelen en der aarde? Hij roept u, opdat Hij uw zonden moge vergeven en u uitstel moge verlenen voor een vastgestelde periode.\" Zij zeiden: \"Gij zijt slechts mensen als wij; gij wenst ons afkerig te maken van hetgeen onze vaderen aanbaden. Brengt ons daarom een duidelijk bewijs.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,11,'Hun boodschappers zeiden tot hen: \"Wij zijn inderdaad stervelingen zoals gij, maar Allah bewijst gunsten aan wie van Zijn dienaren Hij wil. Het is niet aan ons u een bewijs te brengen, dan door het gebod van Allah. En in Allah behoren de gelovigen te vertrouwen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,12,'\"En waarom zouden wij niet in Allah vertrouwen wanneer Hij ons onze wegen heeft getoond? En wij zullen voorzeker al het kwaad dat gij ons doet met geduld dragen. Laat daarom allen die willen vertrouwen, in Allah hun vertrouwen stellen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,13,'En de ongelovigen zeiden tot hun boodschappers: \"Wij zullen u voorzeker uit het land verdrijven, tenzij gij tot onze godsdienst wederkeert.\" Toen zond hun Heer hun de openbaring: \"Wij zullen de onrechtvaardigen zeker vernietigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,14,'\"En Wij zullen u zeker na hen in het land vestigen. Dit is voor hem die vreest v��r Mij te staan en die Mijn waarschuwing vreest.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,15,'Zij vroegen om een oordeel en (dientengevolge) ging elke hoogmoedige vijand te gronde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,16,'Voor hem is de hel en hij zal worden gedwongen kokend water te drinken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,17,'Hij zal het met kleine teugen drinken en zal het ternauwernood kunnen slikken. En de dood zal van elke kant tot hem komen en toch zal hij niet sterven. En daarnaast zal er een zware kastijding zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,18,'De toestand dergenen die in hun Heer niet geloven, is, dat hun werken als as zijn waarop de wind hevig waait op een stormachtige dag. Zij zullen over hetgeen zij verdienen geen macht bezitten. Dit is inderdaad de volstrekte ondergang.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,19,'Ziet gij niet dat Allah de hemelen en de aarde in waarheid schiep? Als Hij het wil kan Hij u verdelgen en een nieuwe schepping voortbrengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,20,'Dit is inderdaad niet moeilijk voor Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,21,'Zij zullen allen voor Allah verschijnen, dan zullen de zwakken tot de hoogmoedigen zeggen: \"Wij waren voorzeker uw volgelingen; kunt gij ons dan tegen Allah´s straf niet helpen?\" Zij zullen zeggen: \"Als Allah ons had geleid, hadden wij u zeker geleid. Het is voor ons gelijk of wij ongeduld tonen of wel geduldig blijven, want er is voor ons geen toevlucht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,22,'Wanneer de zaak is beslist zal Satan zeggen: \"Allah deed u een ware belofte, ik echter beloofde u en faalde, maar ik had geen macht over u dan dat ik u riep en gij mij gehoorzaamdet. Verwijt mij daarom niet, maar beschuldigt uzelf. Ik kan u niet bijstaan noch kunt gij mij bijstaan. Ik verwerp dat gij mij voordien met Allah hebt vereenzelvigd. Er zal voor de onrechtvaardigen gewis een smartelijke straf zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,23,'En de gelovigen die goede werken doen, zullen in tuinen worden toegelaten waardoor rivieren stromen, daarin zullen zij vertoeven door het gebod van hun Heer. Hun groet daarin zal \"Vrede\" zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,24,'Ziet gij niet hoe Allah de gelijkenis van een goed woord geeft? Het is als een goede boom, waarvan de wortel hecht is en zijn takken reiken tot in de hemel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,25,'Deze brengt door het gebod van zijn Heer zijn vrucht voort in ieder jaargetijde. En Allah geeft de gelijkenissen voor de mensen, opdat zij lering mogen trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,26,'En een slecht woord is als een slechte boom die ontworteld ter aarde ligt en geen vaste grond meer heeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,27,'Allah versterkt degenen, die geloven in het tegenwoordige leven en in het Hiernamaals met het bevestigende woord en Allah laat de onrechtvaardigen dwalen. En Allah doet, wat Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,28,'Ziet gij niet degenen, die Allah´s gunst in ondankbaarheid veranderden en hun volk in het huis van verderf brachten?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,29,'Dat is de hel. Daarin zullen zij branden en dit is een boze rustplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,30,'En zij hebben medegoden aan Allah toegekend om (de mensen) van Zijn weg af te leiden. Zeg: \"Vermaakt u een poosje, daarna is uw terugkeer voorzeker naar het Vuur.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,31,'Zeg tot mijn gelovige dienaren dat zij het gebed behoren te onderhouden en van hetgeen Wij hun hebben gegeven heimelijk en openlijk besteden, voordat er een dag komt, waarop er handel noch vriendschap zal zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,32,'Allah is Hij, Die de hemelen en de aarde schiep en water uit die wolken doet nederkomen en er vruchten voor uw onderhoud mee voortbrengt en Hij heeft de schepen in uw dienst gesteld, opdat zij door Zijn gebod over de zee mogen varen en Hij heeft de rivieren eveneens in uw dienst gesteld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,33,'En Hij heeft ook de zon en de maan, die beiden hun werk voortdurend verrichten alsmede de nacht en de dag in uw dienst gesteld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,34,'En Hij gaf u al hetgeen gij van Hem vraagt en als gij de gunsten van Allah telt, zult gij ze stellig niet kunnen opsommen. Voorwaar, de mens is zeer onrechtvaardig, zeer ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,35,'En toen Abraham zeide: \"Mijn Heer maak deze stad (oord van) vrede en weerhoud mij en mijn kinderen van het aanbidden van afgoden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,36,'Mijn Heer, zij hebben inderdaad vele van de mensen op een dwaalspoor gebracht. Wie mij daarom ook volgt hij is stellig van mij en wat betreft hem die mij niet gehoorzaamt - Gij zijt voorzeker Vergevensgezind, Genadevol.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,37,'\"Onze Heer, ik heb sommige van mijn kinderen in een onvruchtbaar dal dicht bij Uw heilig huis (de Kabah) gevestigd, onze Heer, opdat zij het gebed mogen houden. Stem het hart der mensen gunstig voor hen en voorzie hen van vruchten opdat zij dankbaar mogen zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,38,'\"Onze Heer, Gij weet voorzeker hetgeen wij verbergen en hetgeen wij bekend maken. Er is niets op aarde of in de hemel voor Allah verborgen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,39,'\"Alle lof behoort aan Allah, Die mij in weerwil van ouderdom Isma�l en Izaak heeft gegeven Waarlijk mijn Heer is de Verhoorder van het gebed.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,40,'\"Mijn Heer maak mij en mijn kinderen onderhouders van het gebed. Onze Heer, aanvaard mijn gebed.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,41,'\"Onze Heer, vergeef mij en mijn ouders en de gelovigen op de Dag waarop de afrekening zal plaatsvinden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,42,'Denk niet dat Allah achteloos is omtrent hetgeen de onrechtvaardigen doen. Hij geeft hun slechts uitstel tot de Dag waarop zij zullen staren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,43,'Met opgeheven hoofd zich voorthaastend, terwijl zij hun blik niet kunnen afwenden en hun hart ledig is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,44,'En waarschuw de mensen voor de Dag waarop kastijding over hen zal komen; dan zullen de onrechtvaardigen zeggen: \"Onze Heer, schenk ons uitstel voor een korte periode. Wij zullen Uw roep beantwoorden en de boodschappers volgen.\" \"Hebt gij voorheen niet gezworen, dat er voor u geen ondergang was?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,45,'En gij vertoeft thans in de woonplaatsen van degenen die zichzelf onrecht aandeden en het was u duidelijk geworden hoe Wij met hen handelden terwijl Wij de voorbeelden voor u hadden gegeven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,46,'En zij hadden hun plannen reeds gesmeed maar hun plannen zijn bij Allah, al waren hun plannen z� dat er bergen door zouden worden verzet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,47,'Denk derhalve niet dat Allah zal falen Zijn belofte aan Zijn boodschappers te houden: Allah is voorzeker Almachtig, Heer der Vergelding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,48,'De dag (zal komen) waarop de aarde en de hemel door een andere aarde en hemel zullen worden vervangen; en zij (de mensen) allen voor Allah, de Ene, de Opperste zullen verschijnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,49,'En op die Dag zult gij de schuldigen in kettingen geklonken zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,50,'Hun kleren zullen van pek zijn en het Vuur zal hun gezicht omhullen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,51,'Opdat Allah elke ziel moge vergelden voor hetgeen zij heeft gedaan. Voorzeker, Allah is snel in het vergelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,14,52,'Dit is een aankondiging voor de mensen opdat zij er door mogen worden gewaarschuwd en opdat zij mogen weten dat Hij de Enige God is en opdat degenen die begrip hebben er lering uit mogen trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,1,'Alif Laam Raa. Dit zijn de verzen van het Boek, de duidelijke Koran.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,2,'De ongelovigen zullen dikwijls wensen, dat zij Moslims waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,3,'Laat hen eten en zich vermaken en laat hun ijdele hoop hen achteloos maken; zij zullen het weldra te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,4,'En Wij hebben nooit een stad verwoest of het besluit er toe was bekend gemaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,5,'Geen volk kan zijn vastgestelde tijd vooruitlopen noch kunnen zij daarbij achterblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,6,'En dezen zeggen: \"O, gij, tot wie de vermaning is nedergezonden, gij zijt voorzeker bezeten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,7,'\"Waarom brengt gij ons geen engelen indien gij tot de waarachtigen behoort?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,8,'Wij zenden alleen engelen neder met de werkelijkheid en dan wordt hun (de ongelovigen) geen uitstel geschonken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,9,'Voorwaar, Wij hebben deze vermaning (de Koran) nedergezonden en voorzeker Wij zullen er de Waker over zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,10,'En v��r u zonden Wij reeds (boodschappers) onder de oude stammen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,11,'Maar er kwam nooit een boodschapper tot hen of zij bespotten hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,12,'Zo doen Wij dat in het hart der schuldigen binnendringen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,13,'Zij geloven er niet in, hoewel er het voorbeeld der vroegere volkeren is geweest.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,14,'En indien Wij een deur van de hemel voor hen zouden openen waar zij door zouden klimmen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,15,'Dan zouden zij zeker zeggen: \"Onze ogen zijn slechts beneveld; neen wij zijn veeleer een betoverd volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,16,'En Wij hebben aan de hemel voorzeker banen (van sterren) gemaakt en hem voor aanschouwers versierd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,17,'En Wij hebben hem tegen elke vervloekte Satan beschermd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,18,'Maar indien iemand steelsgewijze luistert, vervolgt hem een heldere vlam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,19,'En Wij hebben de aarde uitgespreid, er hechte bergen op geplaatst en Wij doen er allerlei noodzakelijke dingen in de juiste maat op groeien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,20,'Waarvan Wij voor u en degenen die gij niet onderhoudt bestaansmiddelen verstrekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,21,'Er is niets of de schatten er van zijn bij Ons en Wij zenden deze slechts in bepaalde mate neder.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,22,'En Wij zenden bestuivende winden, daarna zenden Wij water uit de wolken neder en geven het u dan te drinken en gij zijt niet degenen die het vergaart.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,23,'En voorwaar, Wij zijn het, die leven geven en doen sterven en Wij zijn de Erfgenaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,24,'En Wij kennen degenen die onder u vooruitgaan en Wij kennen degenen die achterblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,25,'Voorzeker uw Heer zal hen allen verzamelen. Voorwaar, Hij is Alwijs, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,26,'Waarlijk Wij schiepen de mens uit droge, klinkende klei, uit zwarte modder in vorm gewrocht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,27,'En Wij hadden voorheen de djinn uit vlammend vuur geschapen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,28,'Toen uw Heer tot de engelen zeide: \"Ik ga de mens uit droge, klinkende klei scheppen, uit leem gewrocht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,29,'\"Wanneer Ik hem daaruit heb gevormd en hem Mijn geest heb ingeblazen, valt dan in onderdanigheid voor hem neder.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,30,'De engelen onderwierpen zich allen tezamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,31,'Maar Iblies weigerde tot degenen te behoren die zich onderwierpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,32,'Hij zeide: \"O Iblies, wat hapert u dat gij niet onder degenen zijt die zich onderwerpen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,33,'Hij antwoordde: \"Ik ga mij niet onderwerpen aan de mens, die Gij uit droge, klinkende klei hebt geschapen, uit leem gemaakt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,34,'God zeide: \"Ga dan heen, gij zijt voorzeker verworpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,35,'\"Mijn vloek zal tot de Dag des Oordeels op u rusten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,36,'Hij zeide: \"Mijn Heer, schenk mij dan uitstel tot de Dag waarop zij zullen worden opgewekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,37,'God zeide: \"U wordt uitstel verleend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,38,'\"Tot de Dag van de bekende tijd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,39,'Hij antwoordde: \"Mijn Heer, daar Gij mij verloren hebt geacht, zal ik voor hen (de dingen) op aarde schoonschijnend maken en hen allen doen dwalen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,40,'\"Met uitzondering van Uw oprechte dienaren onder hen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,41,'God zeide: \"Dit is een pad, rechtstreeks tot Mij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,42,'\"Gij zult over Mijn dienaren zeker geen macht hebben, met uitzondering van de dwalenden die u volgen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,43,'\"En de hel is zeker de beloofde plaats voor hen allen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,44,'\"Zij heeft zeven poorten en elke poort heeft een gedeelte hunner toegewezen gekregen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,45,'Voorwaar, de rechtschapenen zullen te midden van tuinen met bronnen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,46,'\"Gaat er met vlede en veiligheid binnen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,47,'En Wij zullen alle wrok uit hun hart uitroeien, op tronen zullen zij als broeders tegenover elkander zitten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,48,'Vermoeidheid zal hen daar niet raken noch zullen zij er van worden verdreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,49,'Zeg tot Mijn dienaren dat Ik voorzeker Vergevensgezind, Genadevol ben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,50,'En dat Mijn straf de pijnlijke straf is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,51,'En vertel hun van Abrahams gasten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,52,'Toen zij bij hem binnentraden zeiden zij \"Vrede\", hij antwoordde: \"Voorwaar, wij vrezen u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,53,'Zij zeiden: \"Vreest niet, wij geven u blijde tijding over een zoon, die met kennis zal zijn begiftigd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,54,'Hij zeide: \"Geeft gij mij blijde tijding hoewel de ouderdom mij heeft achterhaald? Wat is het dan, waarover gij mij blijde tijding geeft?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,55,'Zij zeiden: \"Wij hebben u inderdaad in waarheid blijde tijding gegeven, behoor dus niet tot hen die wanhopen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,56,'Hij zeide: \"Wie kunnen aan de genade van hun Heer wanhopen, dan de dwalenden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,57,'Hij zeide: \"Wat is uw taak, o gij boodschappers?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,58,'Zij zeiden: \"Wij zijn naar een schuldig volk gezonden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,59,'Doch wat de familie van Lot betreft, hen zullen Wij allen redden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,60,'\"Behalve zijn vrouw. Wij hebben besloten, dat zij tot degenen zal behoren die achterblijven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,61,'Toen de boodschappers tot de familie van Lot kwamen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,62,'Zeide hij: \"Voorwaar, gij zijt een groep vreemdelingen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,63,'Zij zeiden: \"Neen, wij zijn met hetgeen waarover zij (de ongelovigen) twijfelden tot u gekomen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,64,'\"En wij zijn met de waarheid tot u gekomen en wij spreken zeker de waarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,65,'Ga daarom gedurende de nacht met uw familie weg en volg achter hen. En laat niemand uwer omkijken en gaat waarheen u is bevolen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,66,'En Wij deelden hem dit gebod mede dat hun levenswortel tegen de morgen zou worden afgesneden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,67,'En de mensen der stad kwamen verheugd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,68,'Hij zeide: \"Dit zijn mijn gasten maakt mij daarom niet te schande.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,69,'\"En vreest Allah en onteert mij niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,70,'Zij zeiden; \"Hebben wij u niet verboden de mensen (te ontvangen)?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,71,'Hij zeide: \"Dit zijn mijn dochters als gij iets wilt doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,72,'Bij uw leven, dezen zwerven in hun bedwelming blindelings rond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,73,'Dus overviel de straf hen bij zonsopgang.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,74,'En Wij keerden de stad ondersteboven en Wij deden brokken klei over hen regenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,75,'Hierin zijn voorzeker tekenen voor hen die onderzoeken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,76,'En zij ligt aan een bestaande weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,77,'Hierin is voorzeker een teken voor hen die (willen) geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,78,'En de mensen van het Woud waren eveneens onrechtvaardig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,79,'Wij straften hen daarom. En zij liggen beiden aan een open hoofdweg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,80,'En ook het volk van de Hidjr verloochende de boodschappers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,81,'En Wij gaven hun Onze tekenen, maar zij keerden er zich van af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,82,'En zij hieuwen tot veiligheid huizen in de bergen uit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,83,'Toch greep de straf hen in de morgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,84,'En al hetgeen zij hadden vervaardigd baatte hen niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,85,'En Wij hebben de hemelen en de aarde en al hetgeen er tussen is in waarheid geschapen en het Uur zal zeker komen. Wend u daarom op passende wijze (van hen) af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,86,'Voorwaar, uw Heer is de Schepper, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,87,'En Wij hebben u inderdaad de zeven dikwijls herhaalde verzen en de grote Koran gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,88,'Richt uw ogen niet naar hetgeen Wij sommige groepen hunner (aan goeds) hebben geschonken noch treur over hen en wees zachtmoedig jegens de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,89,'En zeg: \"Ik ben inderdaad een duidelijk waarschuwer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,90,'Want wij hebben besloten (de straf) tegen de samenzweerders te zenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,91,'Die de Koran verloochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,92,'Bij uw Heer, Wij zullen hen voorzeker allen ondervragen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,93,'Over hetgeen zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,94,'Verkondig daarom openlijk hetgeen u is bevolen en wend u van de afgodendienaren af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,95,'Wij zijn u zeker toereikend tegen degenen die bespotten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,96,'Die andere goden met Allah vereenzelvigen; maar zij zullen het weldra te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,97,'En Wij weten inderdaad dat uw boezem benauwd wordt vanwege hetgeen zij zeggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,98,'Maar verheerlijk uw Heer met de lof die Hem toekomt en behoor tot degenen die zich ter aarde werpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,15,99,'En aanbid uw Heer totdat de dood u bereikt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,1,'Het gebod van Allah is komende, verhaast het daarom niet. Heilig is Hij en verheven boven al hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,2,'Hij zendt door Zijn gebod engelen met een Openbaring neder tot wie van Zijn dienaren Hij wil (zeggende): \"Waarschuwt, dat er buiten Mij geen God is, vreest daarom Mij alleen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,3,'Hij heeft de hemelen en de aarde in waarheid geschapen. Verheven is Hij boven al hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,4,'En Hij heeft de mens uit een levenskiem geschapen, maar ziet deze is een openlijke redetwister.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,5,'En het vee heeft Hij geschapen, waarvan gij warmte en nut hebt terwijl gij er ook van als voedsel gebruikt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,6,'En er is schoonheid in voor u wanneer gij het ´s avonds naar huis drijft en wanneer gij het ´s morgens laat weiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,7,'En zij dragen uw lasten naar een land, dat gij niet zonder grote moeilijkheid (voor uzelf) zoudt kunnen bereiken. Voorzeker, uw Heer is Liefderijk, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,8,'En paarden en muildieren en ezels (heeft Hij) geschapen opdat gij er op moogt rijden en tot sieraad (voor u). En Hij zal ook wat gij nog niet kent, scheppen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,9,'En bij Allah berust het, de rechte weg (te tonen) en er zijn wegen die afwijken. En als Hij wilde, zou Hij u allen hebben geleid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,10,'Hij is het, Die water voor u uit de wolken zendt, gij hebt er drank van en het doet bomen groeien, waarmede gij uw vee voedert.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,11,'En Hij doet daarmede koren voor u groeien, de olijf, de dadelpalm, de druiven en allerlei andere vruchten. Daarin is voorzeker een teken voor een volk dat nadenkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,12,'Hij heeft door Zijn gebod de nacht, de dag, de zon, de maan en de sterren in uw dienst gesteld. Voorzeker daarin zijn tekenen voor een volk dat overweegt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,13,'En in de dingen, die Hij in verscheidene kleuren op aarde voor u heeft geschapen is voorzeker een teken voor een volk dat er lering uit wil trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,14,'En Hij is het, Die de zee tot uw beschikking heeft gesteld opdat gij er vers vlees van moogt eten en er sieraden uit moogt nemen die gij draagt. En gij ziet er de schepen over varen opdat gij van Zijn overvloed moogt zoeken en dankbaar moogt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,15,'En Hij heeft hechte bergen op de aarde geplaatst opdat gij niet geschokt zult worden en rivieren en paden opdat gij de juiste weg moogt inslaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,16,'En merktekenen en door de sterren vinden zij (de mensen) de juiste richting.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,17,'Is dan Hij, Die schept gelijk aan iemand die niet schept? Wilt gij dan er geen lering uit trekken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,18,'En indien gij de gunsten Van Allah wilt opsommen, kunt gij dat stellig niet doen. Voorzeker, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,19,'En Allah weet wat gij verbergt en wat gij openbaart.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,20,'Maar degenen, die zij naast Allah aanroepen, scheppon niets, want zij zijn zelf geschapen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,21,'Dood en niet levend, en zij weten niet wanneer zij zullen worden opgewekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,22,'Uw God is E�n God. En zij die in het Hiernamaals niet geloven hun hart is vervreemd (van waarheid) en zij zijn hoogmoedig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,23,'Allah weet ongetwijfeld wat zij verbergen en wat zij openbaren. Hij heeft de hovaardigen voorzeker niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,24,'En wanneer er tot hen wordt gezegd: \"Wat heeft uw Heer geopenbaard?\", zeggen zij: \"Het zijn slechts fabelen der ouden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,25,'Dat zij op de Dag der Opstanding hun last ten volle mogen dragen en een gedeelte der last van degenen die zij zonder kennis doen dwalen. Ziet! slecht is hetgeen zij dragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,26,'Degenen, die v��r hen waren, smeedden ook plannen, maar Allah vernietigde hun gebouw tot in de grondvesten zodat het dak van boven op hen viel; en de straf kwam over hen vanwraar zij het niet vermoedden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,27,'Dan zal Hij hen op de Dag der Opstanding vernederen en Hij zal zeggen: \"Waar zijn Mijn medegoden, ter wille van wie gij placht te strijden?\" Degenen, die met kennis zign begiftigd zullen antwoorden: \"Schande en kwelling zullen deze Dag voorzeker over de ongelovigen zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,28,'\"Degenen, die de engelen doen sterven terwijl zij hun ziel onrecht aandoen zullen onderdanigheid aanbieden (en zeggen): \"Wij deden geen kwaad.\" Neen, Allah weet, wat gij deedt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,29,'Gaat daarom de poorten der hel binnen en vertoeft er in. Het tehuis der hovaardigen is slecht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,30,'En wordt er tot degenen, die rechtvaardig handelden gezegd: \"Wat heeft uw Heer geopenbaard?\", dan zullen zij zeggen: \"Het beste.\" Er is voor degenen, die goed doen, goeds in deze wereld doch het tehuis van het Hiernamaals is nog beter. Het tehuis der godvrezenden is inderdaad uitstekend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,31,'Tuinen der eeuwigheid zullen zij binnengaan, waardoor rivieren vloeien. Zij zullen er in ontvangen wat zij wensen. Zo beloont Allah de rechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,32,'Tot degenen, die de engelen doen sterven terwijl zij rein zijn, wordt gezegd: \"Vrede zij u. Gaat de hemel binnen voor hetgeen gij deedt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,33,'Zij (de ongelovigen) wachten op niets anders dan dat de engelen over hen komen of dat het gebod van uw Heer zal worden uitgevoerd. Degenen, die v��r hen waren deden dat evenzo. Allah deed hun geen onrecht aan, maar zij deden zichzelf onrecht aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,34,'Het boze dat zij deden trof hen en wat zij bespotten overviel hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,35,'De afgodendienaren zeggen: \"Als Allah het zo had gewild zouden wij niets buiten Hem hebben aanbeden, wij noch onze vaderen; noch zouden wij iets buiten Zijn wil hebben verboden.\" Degenen, die v��r hen waren handelden evenzo. Maar zijn de boodschappers voor iets anders verantwoordelijk dan voor de duidelijke verkondiging?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,36,'En voorzeker Wij wekten onder elk volk een boodschapper op, \"Aanbidt Allah en vermijdt de boze.\" Toen waren er sommigen onder hen die Allah leidde en er waren sommigen die bleven dwalen. Reist daarom op aarde rond en ziet wat het einde was der loochenaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,37,'Als gij (profeet) begerig zijt dat zij geleid zullen worden, weet dan dat Allah voorzeker degenen niet leidt, die (zich zelve) doen dwalen. Voor dezulken zijn er geen helpers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,38,'En zij zweren bij Allah hun sterkste eden, dat Allah de doden niet zal doen herrijzen. Waarlijk het is een ware belofte maar de meeste mensen weten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,39,'Opdat Hij het hun duidelijk moge maken waarover zij verschilden en dat de ongelovigen mogen weten dat zij leugenaars waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,40,'Wanneer Wij iets willen, dan zeggen Wij slechts: \"Wees\", en het wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,41,'En degenen, die (hun) huizen ter wille van Allah hebben verlaten, nadat hun onrecht was aangedaan, Wij zullen hun voorzeker een goed tehuis in de wereld geven; waarlijk de beloning van het Hiernamaals is groter; wisten zij het slechts!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,42,'(Voor) hen, die geduldig zijn en hun vertrouwen in hun Heer stellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,43,'En Wij zonden v��r u, slechts mannen aan wie Wij een openbaring gaven - vraagt daarom aan degenen, die de vermaning bezitten als gij het niet weet - met duidelijke tekenen en geschriften.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,44,'En Wij hebben de vermaning tot u gezonden, opdat gij aan het mensdom moogt uitleggen hetgeen tot hen werd nedergezonden, zodat zij mogen nadenken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,45,'Voelen degenen die boze plannen verzinnen, zich er dan veilig tegen dat Allah hen in de grond zal doen verzinken, of dat de straf over hen zal komen vanwaar zij het niet bemerken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,46,'Of dat Hij hen in hun handel en wandel zal treffen, zonder dat zij het kunnen verijdelen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,47,'Of dat Hij hen geleidelijk ten onder zal brengen? Uw Heer is inderdaad Medelijdend, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,48,'Hebben zij niet gezien dat de schaduwen van al hetgeen Allah heeft geschapen zich van rechts en links bewegen en zich voor Allah nederwerpen terwijl zij nederig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,49,'En wat ook in de Hemelen is en welk schepsel ook op aarde bestaat onderwerpt zich aan Allah alsmede de engelen, en zij (allen) tonen geen hoogmoed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,50,'Zij vrezen hun Heer boven hen en doen wat hun bevolen wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,51,'Allah heeft gezegd: \"Neemt geen twee goden. Er is slechts E�n God. Vreest daarom Mij alleen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,52,'En aan Hem behoort hetgeen in de hemelen en op aarde is en Hem is voortdurende gehoorzaamheid verschuldigd. Wilt gij dan iets buiten Allah vrezen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,53,'Welke zegeningen gij ook ontvangt, zij komen van Allah. En wanneer een kwelling over u komt, is het tot Hem dat gij om hulp roept.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,54,'Wanneer Hij dan uw kwelling van u verwijdert, ziet, dan vereenzelvigt een deel uwer andere (Goden) met hun Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,55,'Zodat zij ondankbaar zijn voor hetgeen Wij hun hebben geschonken. Geniet dan en weldra zult gij te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,56,'En zij bestemmen een gedeelte van wat Wij hun hebben geschonken voor datgene, waarvan zij geen kennis hebben. Bij Allah, gij zult zeker ondervraagd worden over al hetgeen gij hebt verzonnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,57,'En zij schrijven dochters aan Allah toe - Heilig is Hij - en zichzelf wat zij wensen (zonen).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,58,'En wanneer aan ��n hunner (de geboorte) van een meisje wordt gemeld, verduistert zijn gezicht en hij is vol toorn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,59,'Hij verbergt zich voor het volk vanwege het slechte nieuws dat hem is aangekondigd; zal hij haar in weerwil van schande behouden of haar in het stof begraven? Voorwaar, slecht is hetgeen zij besluiten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,60,'Het kenteken van degenen die niet in het Hiernamaals geloven is slecht, terwijl Allah´s kenteken het beste is, Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,61,'En indien Allah de mensen voor hun onrechtvaardigheid zou straffen, zou Hij geen levend schepsel op aarde achterlaten, maar Hij geeft hun uitstel tot een vastgestelde termijn, en wanneer hun tijd is gekomen kunnen zij deze niet voor een enkel uur uitstellen of vervroegen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,62,'En zij schrijven aan Allah toe waar zij niet van houden (dochters); hun tong spreekt leugen, nl. dat hun het beste gewordt. Ongetwijfeld komt het vuur hun toe waaraan zij zullen worden overgeleverd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,63,'Bij Allah, Wij zonden (boodschappers) tot de volkeren die v��r u waren; maar Satan deed hun werken voor hen schoon schijnen. Daarom is hij nu (in deze wereld) hun vriend en (in het Hiernamaals) zullen zij een smartelijke straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,64,'En Wij hebben alleen dit Boek tot u nedergezonden, opdat gij hun hetgeen waarover zij verschillen moogt uitleggen en tevens als leiding en barmhartigheid voor de mensen die geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,65,'En Allah heeft water uit de hemel nedergezonden en er de aarde na haar dood mee opgewekt. Daarin is voorzeker een teken voor een volk, dat wil luisteren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,66,'Ook het vee bevat voorzeker een les voor u. Wij geven u van hetgeen in hun buik is, van tussen het uitwerpsel en het bloed, n.l. melk, zuiver en aangenaam voor degenen die drinken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,67,'En van de vrucht der dadelpalmen en druiven maakt gij een bedwelmende drank en een goed voedsel. Voorwaar, daarin is een teken voor een volk dat zijn verstand gebruikt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,68,'En uw Heer heeft de bij bezield, (zeggende): \"Maakt huizen in de heuvels en in de bomen en in hetgeen men bouwt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,69,'\"Eet dan van alle soorten vruchten en volgt onderdanig de wegen van uw Heer.\" Er komt uit hun buik een vloeistof (honing) van verschillende tinten voort waarin genezing is voor de mens. Voorzeker, daarin is een teken voor een volk dat nadenkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,70,'En Allah schept u, dan doet Hij u sterven, en er zijn sommigen onder u die een hoge ouderdom bereiken, waardoor zij na kennis te hebben vergaard, niets meer weten. Voorzeker, Allah is Alwetend, Almachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,71,'En Allah heeft sommigen uwer boven anderen in levensonderhoud bevoorrecht. Maar degenen die Hij bevoordeelde geven hun bezit niet aan hun ondergeschikten, zodat deze er gelijk in zullen worden. Willen zij de gunst van Allah dan verloochenen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,72,'En Allah heeft uit uw midden echtgenoten voor u gemaakt en heeft u van uw echtgenoten kinderen en kleinkinderen geschonken en u van goede dingen voorzien. Willen zij dan in valse dingen geloven en de gunst van Allah verloochenen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,73,'En zij aanbidden naast Allah dingen (afgoden) die over hun levensonderhoud van de hemelen of van de aarde in het geheel niet beschikken, noch enige macht bezitten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,74,'Sehrijf daarom geen gelijken aan Allah toe. Voorzeker Allah weet (alles), en gij weet niets.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,75,'Allah geeft de gelijkenis van een slaaf, die nergens macht over heeft; en van iemand die Wij van een ruim levensonderhnud hebben voorzien, die er heimelijk en openlijk van besteedt. Zijn zij gelijk? Alle lof komt Allah toe! Maar de meesten hunner weten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,76,'En Allah geeft een gelijkenis van twee mannen: een hunner is stom, heeft nergens macht over en is een last voor zijn meester; waar hij hem ook heenzendt, hij brengt (hem) niets goeds mee. Kan deze gelijk zijn aan hem die rechtvaardigheid gelast en die zelf op het rechte pad is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,77,'En aan Allah behoort het Onzichtbare van de hemelen en van de aarde. En het geval van het Uur is als een oogwenk, neen, het is nog sneller. Voorzeker, Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,78,'En Allah bracht u terwijl gij niets wist, uit de baarmoeder van uw moeder voort en gaf u oren, ogen en hart, opdat gij dankbaar moogt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,79,'Zien zij niet, dat de vogelen in het gewelf van de hemel in onderdanigheid worden gehouden? Niemand houdt ze tegen dan Allah. Voorwaar, daarin zijn tekenen voor een volk dat wil geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,80,'En Allah heeft van uw huizen een rustplaats voor u gemaakt, ook heeft Hij van de huiden van het vee woonplaatsen voor u gemaakt die gij licht vindt, op de tijd waarop gij reist en op de tijd waarop gij halt maakt; en van hun wol, hun vachten en hun haar maakt gij meubelen en gebruiksartikelen, voor een (bepaalde) tijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,81,'En Allah heeft van hetgeen Hij heeft geschapen dingen voor u gemaakt die schaduw geven, en Hij heeft in de bergen schuilplaatsen voor u gemaakt; Hij heeft klederen voor u gemaakt die u tegen hitte beschermen en harnassen die u in uw oorlogen beschermen. Zo volmaakt Hij Zijn gunsten aan u, opdat gij u moogt onderwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,82,'Maar indien zij zich afwenden zijt gij (de profeet) alleen voor de duidelijke verkondiging verantwoordelijk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,83,'Zij erkennen de gunst van Allah en toch verloochenen zij deze; de meesten hunner zijn ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,84,'En de dag, waarop Wij uit elk volk een getuige zullen opwekken zal het degenen die niet geloven, niet worden toegestaan (zich te verontschuldigen), noch zal hun worden toegestaan naar Gods gunst te dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,85,'En wanneer degenen die kwaad verrichten de straf in werkelijkheid zien, zal deze voor hen niet worden verlicht noch zal hun uitstel worden verleend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,86,'En wanneer de afgodendienaren hun afgoden zullen zien, zullen zij zeggen: \"Onze Heer, dezen zijn onze goden, die wij buiten u aanbaden.\" Maar zij (afgoden) zullen tegenwerpen: \"Gij zijt voorzeker leugenaars.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,87,'En op die dag zullen zij aan Allah onderwerping aanbieden en al hetgeen zij verzinnen zal hun falen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,88,'Degenen die verwerpen en anderen van de weg van Allah afhouden - Wij zullen straf bij hun straf voegen omdat zij onheil stichtten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,89,'En (gedenk) de dag waarop Wij onder elk volk een getuige tegen hen uit hun midden zullen verwekken en u (profeet) als getuige tegen dezen zullen brengen. Wij hebben u het Boek nedergezonden, alles verklarend, als leiding, barmhartigheid en blijde tijding voor hen die zich onderwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,90,'Voorwaar, Allah gelast u goed met goed (te vergelden) en wel te doen aan anderen en te geven als aan verwanten; en verbiedt onbetamelijkheid, kwaad en opstand. Hij raadt u aan dat gij er lering uit trekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,91,'En vervult het verbond met Allah, wanneer gij een verbond sluit; en breekt geen eden na hun bekrachtiging, terwijl gij Allah tot uw Borg hebt gemaakt. Voorzeker, Allah weet wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,92,'En weest niet zoals zij die haar garen in stukken breekt nadat zij het sterk heeft gemaakt. Gij maakt uw eden onderling tot een middel van bedrog, uit vrees dat het ene volk machtiger dan het andere zou worden. Voorzeker, Allah beproeft u daarmee en op de Dag der Opstanding zal Hij het u duidelijk maken waarover gij verschildet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,93,'En als Allah had gewild, zou Hij u voorzeker tot ��n volk hebben gemaakt; maar Hij laat hem die wil, dwalen en leidt hem die dit wenst, en gij zult zeker worden ondervraagd betreffende hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,94,'En maakt uw eden niet tot een middel van bedrog onder elkander; anders zal uw voet uitglijden nadat hij stevig heeft gestaan en gij zult het kwade ondergaan omdat gij ook anderen van het pad van Allah hebt afgehouden; en er zal voor U een strenge straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,95,'En verkoopt het verbond van Allah niet voor een geringe prijs. Hetgeen bij Allah is, is voorzeker beter voor u, wist gij het slechts.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,96,'Hetgeen gij hebt, zal voorbijgaan maar hetgeen bij Allah is, is blijvend. En Wij zullen degenen die standvastig zijn, voorzeker hun beloning geven naar het beste van wat zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,97,'Die juist handelt, hetzij man of vrouw en een gelovige is, hun zullen Wij voorzeker een goed leven schenken; en gewis zullen Wij hen belonen naar hun beste werken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,98,'En wanneer gij de Koran voordraagt, zoekt dan uw toevlucht tot Allah tegen Satan de verworpene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,99,'Voorzeker hij heeft geen macht over degenen die geloven en die vertrouwen in hun Heer stellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,100,'Zijn macht heerst alleen over degenen die met hem vriendschap aanknopen en die anderen met God vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,101,'En wanneer Wij het ene teken in plaats van het andere brengen - en Allah weet het beste wat Hij openbaart - zeggen zij: \"Gij verzint slechts.\" Neen de meesten hunner weten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,102,'Zeg: \"De Geest van heiligheid heeft het van uw Heer met waarheid nedergebracht, opdat Hij degenen die geloven, moge versterken en als leiding en blijde tijding voor hen die zich onderwerpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,103,'En Wij weten inderdaad dat zij zeggen dat het slechts een man is, die hem (de profeet) onderwijst. De taal van hem die zij bedoelen is vreemd, terwijl dit de duidelijke Arabische taal is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,104,'Degenen die in de tekenen van Allah niet geloven, Allah zal hen voorzeker niet leiden en er zal voor hen een smartelijke straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,105,'Voorzeker slechts zij verzinnen leugens die in de tekenen van Allah niet geloven; zij zijn de leugenaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,106,'Wie Allah verwerpt, na te hebben geloofd - behalve hij die wordt gedwongen terwijl zijn hart in het geloof vrede blijft vinden - en zijn hart voor het ongeloof opent, op hem rust Allah´s toorn; en er zal een grote straf voor hem zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,107,'Dit komt doordat zij het tegenwoordige leven boven het Hiernamaals hebben verkozen, en omdat Allah het ongelovige volk niet leidt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,108,'Dezen zijn het op wier hart, oren en ogen Allah een zegel heeft gelegd. En dezen zijn de achtelozen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,109,'Dit zullen ongetwijfeld in het Hiernamaals de verliezers zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,110,'Dan is uw Heer voorzeker voor degenen die ontvluchten, nadat zij worden vervolgd en ter wille van Allah hun best doen en geduld tonen, Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,111,'De dag waarop elke ziel voor zichzelf komt pleiten, dan zal elke ziel ten volle worden vergoed voor hetgeen zij deed en haar zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,112,'En Allah geeft de gelijkenis van een stad, die in rust en vrede was en wier voorziening in overvloed van alle kanten tot haar kwam; maar zij was ondankbaar voor de gunsten van Allah en daarom deed Allah honger en vrees over haar komen voor hetgeen zij deed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,113,'En er was inderdaad een boodschapper uit hun midden tot hen gekomen maar zij verloochenden hem, en daarom achterhaalde hen de straf, terwijl zij onrecht begingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,114,'Eet daarom van de wettige goede dingen waarvan Allah u heeft voorzien; en weest dankbaar voor de gunst van Allah, indien gij Hem alleen aanbidt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,115,'Hij heeft alleen het gestorvene, bloed, varkensvlees en hetgeen waarover de naam van een ander dan Allah is aangeroepen voor u verboden. Maar voor hem, die door noodzaak wordt gedreven (om te eten) terwijl hij niet wil, noch de grens wil overschrijden, is Allah voorzeker Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,116,'En zegt niet - vanwege de leugens die uw tong spreekt - \"Dit is wettig en dat is onwettig.\", om een leugen tegen Allah te verzinnen. Degenen, die een leugen tegen Allah verzinnen, slagen nooit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,117,'Een kort vermaak, maar er zal een smartelijke straf voor hen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,118,'En Wij verboden voordien de Joden al hetgeen Wij u hebben vermeld. En Wij deden hun geen onrecht aan doch zij handelden onrechtvaardig jegens zichzelf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,119,'Uw Heer is voorzeker - voor degenen die in onwetendheid kwaad doen, en daarna berouw hebben en goed maken - Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,120,'Abraham was inderdaad een voorbeeld van deugd, oprecht, gehoorzaam aan Allah en hij behoorde niet tot de afgodendienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,121,'Dankbaar voor Zijn gunsten; Hij verkoos hem en leidde hem naar het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,122,'En Wij schonken hem het goede in deze wereld en in het Hiernamaals zal hij zeker tot de rechtvaardigen behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,123,'Dan hebben Wij u (Mohammed) geopenbaard, \"Volg de weg van Abraham, de oprechte, die geen afgodendienaar was.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,124,'De Sabbat was alleen aan degenen opgelegd, die daaromtrent van mening verschilden; en op de Dag der Opstanding zal uw Heer voorzeker onder hen rechten omtrent hetgeen waarover zij verschillen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,125,'Roep tot de weg van uw Heer met wijsheid en goede raad en redetwist met hen op een gepaste wijze. Voorzeker uw Heer weet het beste wie van Zijn weg is afgedwaald; en Hij kent degenen goed die juist geleid zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,126,'En indien gij vergeldt, doe dit dan naar mate u onrecht werd aangedaan; maar als gij geduld toont dan is dat voorzeker het beste voor degenen die geduldig zign.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,127,'En wees geduldig, voorwaar uw geduld is alleen met de hulp van Allah (mogelijk). En treur niet over hen (de ongelovigen), noch maak u ongerust over hun plannen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,16,128,'Voorwaar, Allah is met degenen, die (God) vrezen en goeddoen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,1,'Heilig is Hij Die Zijn dienaar bij nacht voerde van de Heilige Moskee naar de Verre Moskee welker omgeving Wij hebben gezegend, opdat Wij hem enkele Onzer tekenen zouden tonen. Voorwaar, Hij is de Alhorende, de Alziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,2,'Wij gaven Mozes het Boek en maakten het tot een richtsnoer voor de kinderen van Isra�l, zeggende: \"Neemt niemand buiten Mij als Voogd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,3,'\"O, nageslacht dergenen die Wij met Noach (in de Ark) droegen! Hij was inderdaad een dankbare dienaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,4,'En Wij maakten aan de kinderen van Isra�l in het Boek bekend: \"Voorwaar, tweemaal zult gij op de aarde verderf teweeg brengen en voorzeker zult gij uitermate aanmatigend worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,5,'Toen dan ook de tijd voor de eerste van de twee bedreigingen kwam, zonden Wij Onze dienaren, toegerust met grote macht tegen u uit, die de huizen binnendrongen; dit was een belofte die in vervulling ging.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,6,'Nadien gaven Wij u macht over hen en Wij hielpen u met rijkdommen en kinderen, en maakten u groter in getal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,7,'(Zeggende) \"Indien gij goed doet, doet gij goed voor uzelf; en indien gij kwaad doet, is het tegen uzelf. En toen de tijd was gekomen voor de tweede (bedreiging), zonden Wij (andere volkeren) om u met schande te treffen zodat zij de Moskee zouden binnendringen zoals zij er de eerste keer binnen gingen om alles wat zij veroverd hadden te verwoesten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,8,'\"Het kan zijn dat uw Heer u barmhartigheid zal tonen; doch indien gij terugkeert, zullen Wij ook terugkeren en Wij hebben de hel tot een kerker voor de ongelovigen gemaakt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,9,'Voorzeker, deze Koran voert tot datgene wat juist is; en geeft aan gelovigen die goede werken verrichten de blijde tijding, dat zij een grote beloning zullen ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,10,'En dat Wij voor degenen die niet geloven in het Hiernamaals een smartelijke straf zullen bereiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,11,'De mens vraagt om het kwade gelijk hij om het goede vraagt; en de mens is haastig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,12,'En Wij hebben de nacht en de dag gemaakt tot twee tekenen, het teken van de nacht hebben Wij donker en het teken van de dag hebben Wij licht gemaakt, opdat gij overvloed moogt zoeken van uw Heer en opdat gij de jaren kunt tellen en (de tijd kunt) berekenen. En Wij hebben alles duidelijk verklaard.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,13,'En de werken van ieder mens hebben Wij om zijn hals gehangen; en op de Dag der Verrijzenis zullen Wij voor hem een boek brengen en hij zal het opengeslagen zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,14,'\"Lees het boek. Uw eigen ziel is op deze dag als rekenaar tegen uzelf voldoende.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,15,'Degene die de rechte weg volgt, volgt deze slechts voor zijn eigen heil en hij die dwaalt, dwaalt alleen tegen zichzelf. En geen lastdrager zal de last dragen van een ander. En Wij straffen nimmer voordat Wij een boodschapper hebben gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,16,'En wanneer Wij Ons voornemen een stad te verwoesten, zenden Wij Ons gebod tot haar machthebbers, maar zij overtreden dit, derhalve wordt de verordening tegen haar van kracht, en verwoesten Wij haar geheel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,17,'Hoevele geslachten hebben Wij niet verdelgd na Noach! Voldoende kent en ziet uw Heer de zonden van Zijn dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,18,'Voor een ieder die het wereldse verkiest haasten Wij ons het te verschaffen aan wie Wij willen en wat Wij willen, daarna kennen Wij hem de hel toe waarin hij zal branden, vernederd en verworpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,19,'En een ieder die het Hiernamaals begeert en er naar streeft zoals er naar gestreefd behoort te worden terwijl hij een gelovige is, deze is het wiens streven zal worden beloond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,20,'Aan iedereen - zowel aan dezen als genen - verstrekken Wij onze gaven. De gaven van uw Heer zijn niet beperkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,21,'Zie, hoe Wij sommigen hunner hebben doen uitblinken boven anderen; voorwaar, het Hiernamaals is groter in waardigheid en uitmuntendheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,22,'Stel geen andere god naast Allah, anders zult gij vernederd en verlaten nederzitten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,23,'Uw Heer heeft u bevolen, zeggende: \"Aanbidt niemand anders dan Mij en betoont vriendelijkheid jegens de ouders. Indien ��n hunner bij u een hoge leeftijd bereikt of beiden doen dit, zeg dan nimmer tot hen \"Foei\" noch stoot hen af, doch spreek tot hen een welgevallig woord.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,24,'En wees teder voor hen in erbarming. En zeg: \"Mijn Heer, ontferm u over hen daar zij mij opvoedden toen ik jong was.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,25,'Uw Heer weet het best, wat in uw gedachten is; indien gij goed zijt dan voorwaar is Hij Vergevensgezind jegens degenen die zich bekeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,26,'Geef de verwanten, de armen en de reiziger het hun toekomende, maar verkwist niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,27,'Voorwaar, de verkwisters zijn de broeders der duivelen en de duivel is ondankbaar jegens zijn Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,28,'En indien gij u van hen afwendt zoekende de barmhartigheid van uw Heer waarop gij hoopt, spreek tot hen een vriendelijk woord.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,29,'En houd uw hand niet op uw zak, noch open haar al te wijd, anders zult gij nederzitten in zelfverwijt en spijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,30,'Voorwaar, uw Heer vergroot en beperkt het levensonderhoud voor wie het Hem behaagt. Voorzeker Hij kent en ziet Zijn dienaren goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,31,'En doodt uw kinderen niet uit vrees voor armoede. Wij zijn het die in hun behoeften en in de uwe voorzien. Voorwaar, hen te doden is een grote zonde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,32,'En houdt u verre van overspel; want het is een afschuwelijke zaak en een slechte weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,33,'En doodt niemand die Allah heilig heeft verklaard, tenzij het met recht geschiedt. En wie onrechtvaardig is gedood, aan diens erfgenaam hebben Wij zeker gezag verleend, doch laat hem bij het doden niet buitensporig zijn, want hij wordt (door de wet) gesteund.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,34,'En raakt het eigendom van de wees niet aan dan op de beste wijze tot hij zijn meerderjarigheid heeft bereikt. En vervult het verbond; want gij zult omtrent het verbond worden ondervraagd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,35,'En geeft volle maat wanneer gij meet en weegt met een zuivere weegschaal; dat is goed en uiteindelijk het beste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,36,'En volgt niet datgene waarvan gij geen kennis bezit. Voorwaar, het oor, oog en het hart - al deze zullen worden ondervraagd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,37,'En wandel niet hoogmoedig op aarde rond want gij kunt de aarde niet doen splijten, noch kunt gij de bergen in hoogte evenaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,38,'Het kwade van dit alles is verwerpelijk in de ogen van uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,39,'Dit is hetgeen uw Heer u van de wijsheid heeft geopenbaard. En stel naast Allah geen andere god aan, anders zult gij in zelfverwijt verworpen in de Hel terechtkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,40,'Heeft dan uw Heer u bevoorrecht met zonen en Zelf dochters gekozen uit het midden der engelen? Voorzeker gij spreekt een groot woord.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,41,'Wij hebben het in deze Koran herhaaldelijk uiteengezet, opdat zij er lering uit zouden trekken, doch dit doet hen slechts in afkeer toenemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,42,'Zeg: \"Waren er zoals gij zegt andere goden met Hem geweest, dan zouden dezen ongetwijfeld een weg hebben gezocht naar de Heer van de Troon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,43,'Heilig is Hij, hoog verheven, boven hetgeen zij zeggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,44,'De zeven hemelen en de aarde en degenen die daarin vertoeven prijzen Zijn heerlijkheid. En daar is niets dat Hem niet met de lof die Hem toekomt verheerlijkt; doch gij begrijpt hun verheerlijking niet. Voorwaar, Hij is Verdraagzaam, Vergevensgezind.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,45,'En wanneer gij de Koran voorleest, plaatsen Wij tussen u en degenen die niet in het Hiernamaals geloven een verborgen sluier;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,46,'En Wij leggen een bedekking over hun hart en doofheid in hun oren zodat zij het niet kunnen begrijpen. En wanneer gij in de Koran uw Heer - de Enige - noemt, wenden zij u in afkeer de rug toe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,47,'Wij weten het best waar zij op letten terwijl zij naar u luisteren, en wanneer zij in het geheim beraadslagen en wanneer de onrechtvaardigen zeggen: \"Gij volgt slechts een betoverd man.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,48,'Zie, wat voor gelijkenissen zij over u vertellen; zij zijn zelf afgedwaald en kunnen de weg niet meer vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,49,'En zij zeggen: \"Zullen wij, wanneer wij tot beenderen en stof vergaan zijn, werkelijk als een nieuwe schepping worden opgewekt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,50,'Zeg: \"Weest steen of ijzer,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,51,'\"Of een andere schepping die naar uw gedachte het moeilijkst, is.\" Dan zullen zij zeggen: \"Zeg, wie zal ons dan doen herleven?\" Zeg: \"Hij Die u de eerste maal heeft geschapen.\" Dan zullen zij het hoofd schudden tegen u en vragen: \"Wanneer zal dit geschieden?\" Zeg, \"Waarschijnlijk is het nabij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,52,'De Dag waarop Hij u zal roepen zult gij Hem met de lof die Hem toekomt antwoorden en gij zult denken dat gij slechts een korte wijle hebt vertoefd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,53,'En zeg tot Mijn dienaren dat zij spreken wat het beste is. Voorwaar, Satan sticht onenigheid onder hen. Voorwaar, Satan is de mens een verklaarde vijand.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,54,'Uw Heer kent u het best. Indien het Hem behaagt zal Hij u barmhartigheid tonen of straffen, maar Wij hebben U niet als voogd over hen gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,55,'En uw Heer kent het best al hetgeen in de hemelen en op aarde is. En Wij hebben sommige profeten boven de anderen doen uitmunten en aan David hebben Wij Zaboer (de Psalmen) geschonken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,56,'Zeg, \"Roept degenen aan die gij u naast Hem inbeeldt; maar dezen hebben geen macht om het kwaad van u te verwijderen, of het te veranderen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,57,'Zij roepen zelf hun Heer aan, Zijn nabijheid zoekend, zelfs de meest nabijzijnden, op Zijn barmhartigheid hopend en Zijn straf vrezend. Voorwaar, de straf van uw Heer dient te worden gevrees??.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,58,'Er is geen stad of Wij zullen die voor de Dag der Opstanding verdelgen of streng straffen. Dit staat in het Boek geschreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,59,'En niets weerhoudt Ons van het zenden van tekenen, behalve dat de vroegere volkeren ze hebben verloochend. En Wij gaven aan de Samoed de kamelin als een zichtbaar teken doch zij deden haar kwaad; Wij zenden slechts tekenen om te waarschuwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,60,'En toen Wij tot u zeiden: \"Voorzeker, uw Heer heeft het volk in Zijn hand.\" Wij gaven het visioen dat Wij u toonden slechts als een beproeving voor de mensen, evenals de gevloekte boom in de Koran. En Wij waarschuwen hen, doch het doet hen slechts in grotere overtreding toenemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,61,'En toen Wij tot de engelen zeiden: \"Betuigt eer aan Adam,\" betuigden zij eer, behalve Iblies. Hij zeide: \"Moet ik mij ter aarde werpen voor iemand die Gij geschapen hebt uit klei?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,62,'En hij zeide: \"Hebt Gij hem boven mij ge�erd? Indien Gij mij tot de Dag der Opstanding uitstel verleent, zal ik voorzeker zijn nakomelingen mij doen volgen, op enkelen na.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,63,'Hij zeide: \"Ga heen! en wie onder hen u zal volgen, de hel zal voorwaar een ruime vergelding voor u allen zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,64,'\"En bekoor met uw stem wie gij kunt en spoor uw ruiterij en uw voetvolk tegen hen aan en wees hun deelgenoot in hun weelde en hun kinderen, en doe hun beloften,\" - maar Satan geeft slechts bedriegelijk beloften -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,65,'Voorzeker over Mijn dienaren zult gij geen macht hebben. En voldoende is uw Heer als Beschermer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,66,'Uw Heer is Hij Die de schepen voor u over de zee stuwt, opdat gij Zijn overvloed moogt zoeken. Voorwaar, Hij is Genadig jegens u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,67,'En indien een ongeluk op zee u treft, verdwijnen u degenen die gij aanroept, behalve Hij. Doch wanneer Hij u veilig aan land brengt wendt gij u af. Want de mens is zeer ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,68,'Gevoelt gij u er dan veilig voor, dat Hij u zal verdelgen op het land of dat Hij een hevige storm tegen u zal doen opkomen? Gij zult dan voor u geen beschermer vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,69,'Of weet gij dan zo zeker dat Hij u daarin niet voor de tweede maal zal terugzenden en dan een stormwind tegen u doen opkomen en u verdrinken wegens uw ongeloof, zodat gij daarin geen helper voor u tegen Ons zult vinden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,70,'En inderdaad hebben Wij de kinderen van Adam ge�erd en hen gedragen over land en zee, en hun van het goede gegeven en hen verheven boven velen dergenen die Wij hebben geschapen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,71,'(Gedenk) de Dag waarop Wij elk volk met zijn leider zullen oproepen. Zij die hun boek in de rechter hand ontvangen, zullen hun boek lezen en hen zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,72,'Maar wie blind is geweest in deze wereld zal blind zijn in het Hiernamaals; hij is ver afgedwaald van de rechte weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,73,'En voorzeker zij zouden u (de profeet) willen afleiden van hetgeen Wij u hebben geopenbaard, opdat gij iets anders over Ons mocht verzinnen; dan zouden zij u zeker tot vriend hebben genomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,74,'En indien Wij u niet hadden gesterkt zoudt gij aan hen een weinig gehoor gegeven hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,75,'Dan zouden Wij u een dubbele straf in dit leven en in het Hiernamaals hebben doen ondergaan en gij zoudt voor u geen helper tegen Ons hebben kunnen vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,76,'Zij trachten u, door u vrees in te boezemen, uit het land te verdrijven; dan zullen zij daarna (na uw vertrek) nog slechts korte tijd in rust blijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,77,'(Dit was Onze) handelwijze met Onze boodschappers die Wij v��r u zonden; en gij zult geen verandering vinden in Onze wijze van handelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,78,'Houd het gebed bij het verbleken van de zon tot aan het donker van de nacht; en het reciteren bij de dageraad. Voorwaar, van het reciteren bij de dageraad wordt getuigd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,79,'Blijf gedurende een deel van de nacht vrijwillig wakker (voor het gebed). Waarschijnlijk zal uw Heer u een verheven rang verschaffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,80,'En zeg: \"O mijn Heer, laat mijn intrede een goede intrede en mijn uitgang een goede uitgang zijn. En schenk,mij van U een gezag dat tot hulp zou kunnen strekken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,81,'En zeg: \"Waarheid is gekomen en leugen is verdwenen. En de leugen is inderdaad onderhevig om te verdwijnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,82,'En van de Koran openbaren Wij hetgeen een geneesmiddel en een genade is voor de gelovigen; doch voor de onrechtvaardigen vergroot het slechts het verlies.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,83,'En wanneer Wij de mens gunsten bewijzen wendt hij zich af en gaat terzijde en wanneer kwaad hem achterhaalt wordt hij wanhopig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,84,'Zeg: \"Ieder handelt op zijn eigen wijze maar uw Heer weet het goed, wie op het rechte pad het best zijn geleid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,85,'En zij stellen u vragen betreffende de Geest. Zeg: \"De Geest is op bevel van mijn Heer: en er is u slechts een weinig kennis van gegeven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,86,'En als Wij wilden, zouden Wij hetgeen Wij u hebben geopenbaard zeker weg kunnen nemen, dan zoudt gij daarin tegen Ons geen helper vinden;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,87,'Doch (dit is) een barmhartigheid van uw Heer, voorwaar, Zijn genade jegens u is groot.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,88,'Zeg: \"Indien de mens en de djinn samenspannen, teneinde het gelijke van deze Koran voort te brengen, zullen zij het gelijke daarvan niet kunnen voortbrengen ook al zouden zij elkanders helpers zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,89,'En voorzeker Wij hebben voor de mensen in deze Koran allerlei gelijkenissen herhaaldelijk vermeld, doch de meeste mensen tonen slechts ondankbaarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,90,'En zij zeggen: \"Wij zullen in u stellig niet geloven voordat gij voor ons een bron doet ontspringen aan de aarde.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,91,'\"Of tenzij gij een tuin hebt met dadelpalmen en wijnranken en in het midden daarvan stromen doet vloeien.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,92,'\"Of tenzij gij de hemel in stukken op ons doet nedervallen zoals gij hebt beweerd of tenzij gij Allah en de engelen v��r ons brengt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,93,'\"Of tenzij gij een huis hebt van goud, of tenzij gij ten hemel stijgt, maar wij zullen in uw hemelvaart niet geloven tenzij gij ons een boek nederzendt dat wij kunnen lezen.\" Zeg: \"Glorie zij mijn Heer: ik ben slechts mens en boodschapper!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,94,'En niets heeft de mensen belet te geloven toen de leiding tot hen kwam dan het feit dat zij zeiden: \"Heeft Allah een mens als boodschapper gezonden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,95,'Zeg: \"Hadden er op aarde engelen in vrede en rust rondgelopen dan zouden Wij ongetwijfeld uit de hemel een engel als boodschapper tot hen hebben gezonden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,96,'Zeg: \"Voldoende is Allah als getuige tussen u en mij; voorwaar Hij weet en ziet alles betreffende Zijn dienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,97,'En hij die Allah leidt, is goed geleid, doch voor hem die Hij laat dwalen zult gij buiten Hem geen helper vinden. En Wij zullen hen verzamelen op de Dag der Opstanding, op hun aangezicht, blind, stom en doof voorover liggend. Hun verblijfplaats zal de hel zijn; telkenmale als het Vuur afneemt, zullen Wij de vlam voor hen aanwakkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,98,'Dat is hun vergelding, daar zij Onze woorden verwierpen en zeiden: \"Zullen wij indien wij beenderen en stof zijn geworden werkelijk worden opgewekt in een nieuwe schepping?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,99,'Zien zij niet in, dat Allah, Die de hemelen en de aarde schiep, bij machte is hun evenbeeld te scheppen? Hij heeft voor hen een termijn vastgesteld waaromtrent geen twijfel bestaat. Doch de onrechtvaardigen tonen slechts ondankbaarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,100,'Zeg: \"Indien gij de schatten der barmhartigheid van mijn Heer bezat zoudt gij ze zeker terughouden uit vrees dat ze uitgeput zouden worden. Waarlijk, de mens is vrekkig.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,101,'En voorwaar, wij schonken Mozes negen duidelijke tekenen. Vraag dit aan de kinderen van Isra�l. Toen hij tot hen kwam, zeide Pharao tot hem: \"Ik geloof, O Mozes, dat gij een betoverd mens zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,102,'Hij zeide: \"Voorzeker gij weet dat niemand anders dan de Heer der Hemelen en der aarde deze tekenen heeft gezonden; en ik ben zeker dat gij, o Pharao, te gronde gaat.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,103,'Derhalve besloot hij hem uit het land te verwijderen; doch Wij deden hem en die met hem waren allen tezamen verdrinken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,104,'En Wij zeiden na hem tot de kinderen van Isra�l: \"Blijft gij in het land en wanneer de laatste belofte komt zullen Wij u allen tezamen brengen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,105,'En voorwaar, Wij hebben dit geopenbaard en met waarheid is hij (de Koran) nedergedaald. En Wij hebben u slechts als een brenger van blijde tijdingen en als waarschuwer gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,106,'En Wij hebben u de Koran verduidelijkt opdat gij hem geleidelijk aan de mensheid mocht verkondigen en Wij hebben hem in gedeelten gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,107,'Zeg: \"Hetzij gij er wel of niet in gelooft, degenen aan wie voordien kennis was geschonken werpen zich met hun aangezicht ter aarde wanneer deze hun wordt voorgelezen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,108,'En zeggen: \"Glorie zij onze Heer. De belofte van onze Heer moest worden vervuld.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,109,'Wenend vallen zij op hun aangezicht neder en het vermeerdert hun nederigheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,110,'Zeg: \"Roept Allah aan of roept Rahmaan aan, bij welke naam gij Hem ook noemt, Hij heeft de schoonste namen.\" En zeg uw gebed niet te luid en evenmin te zacht, doch zoek een middenweg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,17,111,'Zeg: \"Alle lof komt Allah toe Die Zich geen zoon heeft genomen en Die geen mededinger heeft in Zijn Koninkrijk noch heeft Hij enige helper wegens zwakheid.\" En verkondig Zijn Grootheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,1,'Alle lof behoort aan Allah, Die het Boek aan Zijn dienaar heeft geopenbaard, gaaf en volmaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,2,'Volmaakt (in leiding), om te waarschuwen voor Zijn gestrenge kastijding en de gelovigen die goede werken verrichten de blijde tijding te brengen dat zij een uitstekende beloning zullen ontvangen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,3,'Die zij zullen smaken in eeuwigheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,4,'En om diegenen te waarschuwen, die zeggen: \"Allah heeft Zich een zoon genomen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,5,'Zij hebben er geen kennis van en hun vaderen evenmin. Erg is het woord, dat uit hun mond komt. Zij zeggen slechts onwaarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,6,'Misschien zult gij uit droefheid over hen sterven, omdat zij niet in deze Boodschap geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,7,'Voorwaar, Wij hebben al hetgeen op aarde is tot haar sieraad gemaakt om te beproeven, wie van hen van goede werken is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,8,'En zie! al hetgeen daarop is, zullen Wij tot dode stof veranderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,9,'Denkt gij dat de lieden van de Spelonk en van de Inscriptie geen wonder onder Onze tekenen waren?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,10,'Toen de jongelingen hun toevlucht zochten in de Spelonk, zeiden zij: \"Onze Heer, verleen ons Uw genade en bereid ons een weg naar vrede en voorspoed uit onze beproeving.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,11,'Derhalve zonderden Wij hen in de Spelonk af voor een aantal jaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,12,'Daarna wekten Wij hen op, om te beproeven welke der twee partijen wijzer was, naar de tijd dat zij daar hadden vertoefd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,13,'Wij zullen u hun geschiedenis in waarheid verhalen. Zij waren jongelingen die in hun Heer geloofden en Wij gaven hun meer leiding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,14,'En Wij versterkten hun hart toen zij opstonden en zeiden: \"Onze Heer is de Heer der hemelen en der aarde. Nimmer zullen wij een andere god aanroepen naast Hem, anders zouden wij inderdaad een grote dwaasheid begaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,15,'\"Dit ons volk heeft goden genomen naast Hem. Waarom brengen zij voor hen geen duidelijk bewijs? En wie is onrechtvaardiger, dan hij die een leugen over Allah verzint?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,16,'\"Wanneer gij u van hen en van hetgeen zij nevens Allah aanbidden verwijdert, neemt dan uw toevlucht tot de Spelonk en uw Heer zal Zijn barmhartigheid jegens u vermeerderen en uw aangelegenheden gunstig doen verlopen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,17,'En wanneer de zon opgaat zult gij haar zich zien verwijderen rechts van de Spelonk en wanneer zij ondergaat, ziet gij haar zich naar links afwenden, daartussen in de holte van (de Spelonk) bevonden zij zich. Dit zijn de tekenen van Allah. Hij die door Allah wordt geleid, wordt juist geleid doch degene, die Hij laat dwalen, voor hem zult gij stellig geen vriend en leidsman vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,18,'Gij denkt dat zij wakker zijn, terwijl zij slapen en Wij zullen hen zich naar links en rechts doen wenden, terwijl hun hond met zijn voorpoten uitgestrekt op de drempel ligt. Indien gij een blik op hen werpt, zult gij U zeker van hen afwenden en vluchten, met ontzag vervuld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,19,'En Wij deden hen ontwaken, zodat zij elkander konden ondervragen. Een van hen zeide: \"Hoelang hebt gij hier vertoefd?\" Anderen zeiden: \"Wij zijn een dag of een gedeelte van een dag gebleven.\" Nog anderen zeiden: \"Uw God weet het best, hoe lang gij hier gebleven zijt. (Het is beter) ��n van ons met deze zilveren munt naar de stad te zenden en laat hij zien, wat het beste voedsel is en hiervan levensmiddelen meebrengen en laat hij zich vriendelijk gedragen en niemand omtrent ons inlichten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,20,'\"Want indien zij over jullie te weten komen, zullen zij jullie stenigen, of trachten jullie te bekeren tot hun godsdienst en jullie zullen nimmer kunnen slagen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,21,'Dit hebben wij hun bekend gemaakt, opdat zij zouden weten, dat de belofte van Allah waarheid is en dat er omtrent het Uur geen twijfel bestaat. Alsdan redetwisten de mensen over hen, zeggende: \"Richt een gedenkteken voor hen op.\" Hun Heer weet wat het beste is. Degenen, die de overhand behielden, zeiden: \"Wij zullen voorzeker een bedehuis boven hen (boven hun graf) oprichten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,22,'Sommigen zullen zeggen: \"Er waren er drie en de vierde was hun hond.\" En sommigen zullen zeggen: \"Er waren er vijf en de zesde was hun hond,\" gissende in het wilde weg en sommigen zullen zeggen: \"Er waren er zeven, de achtste was hun hond.\" Zeg: \"Mijn Heer kent hun getal het beste. Niemand kent hen, enkelen uitgezonderd.\" Redetwist dus niet over hen er diep op ingaande en vraag evenmin van ��n hunner inlichtingen over hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,23,'En zeg niet over iets: \"Ik zal het morgen doen,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,24,'Zonder (er bij te zeggen): \"Indien het Allah behaagt.\" En wanneer gij het vergeet, gedenk dan uw Heer en zeg: \"Ik hoop, dat mijn Heer mij nog dichter dan thans naar de rechte weg zal leiden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,25,'En zij bleven driehonderd jaar in hun Spelonk en voegden er negen aan toe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,26,'Zeg: \"Allah weet het best, hoelang zij daar vertoefden.\" Hem behoren de geheimen der hemelen en der aarde, hoe Ziende is Hij en hoe Horende! Zij hebben geen vriend buiten Hem en aan Zijn koninkrijk laat Hij niemand deelnemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,27,'En verkondig hetgeen u door Uw Heer is geopenbaard in het Boek. Er is niemand, die Zijn woorden kan veranderen en gij zult geen toevlucht vinden buiten Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,28,'Blijf bij degenen die hun Heer ´s morgens en ´s avonds aanroepen en die Zijn welbehagen zoeken en laat uw ogen niet van hen afdwalen door het zoeken van de praal dezer wereld en gehoorzaam niet aan hem, wiens hart Wij achteloos hebben gemaakt voor de gedachte aan Ons, noch degene die zijn begeerte volgt en wiens geval het ergste is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,29,'Zeg: \"Het is de waarheid van uw Heer: laat daarom geloven die geloven wil en niet geloven, die niet wil.\" Voorwaar, wij hebben de boosdoeners een Vuur bereid, welks omheining hen zal insluiten. Indien zij om hulp roepen, zullen zij worden begoten met water als gesmolten lood, dat hun gezicht zal verbranden. Hoe verschrikkelijk is de drank en hoe vreselijk de rustbank.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,30,'Wat betreft degenen die geloven en goede werken doen, voorwaar, wij doen de beloning der goeden niet verloren gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,31,'Voor dezulken zijn de Tuinen der eeuwigheid, waardoor beken vloeien. Zij zullen daarin worden getooid met armbanden van goud en zullen groene gewaden van fijne zijde en zwaar brocaat dragen, terwijl zij op tronen zullen liggen. Hoe goed is de beloning en hoe schoon is de rustplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,32,'En geef hun de gelijkenis der twee mannen. Voor een hunner maakten Wij twee wijngaarden, omgeven met dadelpalmen en daartussen legden Wij korenvelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,33,'Elk der tuinen bracht vruchten voort en bleef niet in gebreke. En door beide deden Wij rivieren stromen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,34,'En hij had overvloed,en zeide tijdens een gesprek tot zijn gezel: \"Ik ben rijker dan gij, aan bezit en in getal.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,35,'En hij ging zijn tuin binnen, terwijl hij onrechtvaardig was tegenover zichzelf. Hij zeide: \"Ik denk niet, dat dit ooit zal vergaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,36,'\"Noch denk ik dat het Uur zal komen. Indien ik tot mijn Heer word teruggebracht, zal ik voorzeker een betere plaats vinden dan dit.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,37,'Zijn gezel redetwistte en zeide: \"Gelooft gij niet in Hem, Die u schiep uit stof, daarna uit een levenskiem en u dan vormde tot een volledig mens?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,38,'\"Wat mij betreft, het is Allah Die mijn Heer is, ik zal niemand met mijn Heer vereenzelvigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,39,'\"Waarom zeidet gij niet, toen gij de tuin binnentraadt: ´Het is zoals het Allah behaagt, er is geen God dan Allah?´ indien gij mij als uw mindere in rijkdom en nakomelingen ziet,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,40,'\"Waarschijnlijk zal mijn Heer mij iets beters geven dan uw tuin en bliksemstralen uit de hemel doen nederdalen op de uwe, waardoor deze grond kaal wordt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,41,'\"Of het water er van in de grond doen zinken, waardoor gij niet in staat zult zijn, het te bereiken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,42,'En zijn fruit werd vernietigd en hij begon zijn handen te wringen wegens hetgeen hij aan de tuin had besteed, terwijl het latwerk eveneens was neergestort en hij zeide: \"Had ik maar niemand met mijn Heer vereenzelvigd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,43,'En hij had geen leger om hem tegen Allah te helpen, noch kon hij zich verdedigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,44,'De bescherming komt alleen van Allah, de Ware. Hij is de Beste in het belonen en de Beste in het verrekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,45,'Geef hun de gelijkenis van het leven dezer wereld: het is als Wij water uit de hemel nederzenden, waardoor de planten der aarde volop groeien en daarna verdrogen zij en breken in stukken die de wind verspreidt. Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,46,'Rijkdom en kinderen zijn een sieraad van het leven dezer wereld, maar blijvende goede werken, zijn beter bij uw Heer tot beloning en hoop.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,47,'En (gedenk) de dag waarop Wij de bergen zullen verzetten en gij de aarde zult zien oprijzen en Wij hen (de mensen) zullen verzamelen en niemand hunner zullen Wij achterlaten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,48,'En zij zullen in rijen tot uw Heer worden gebracht. (Hij zal zeggen) Nu zijt gij tot Ons gekomen zoals Wij u in den beginne hebben geschapen. Doch gij dacht dat Wij nimmer een Uur voor u zouden vaststellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,49,'En het Boek zal worden voorgelegd; dan zult gij de schuldigen zien vrezen wegens hetgeen daarin staat en zij zullen zeggen: \"Wee ons! Wat voor een boek is dit! Het slaat klein noch groot over, doch het somt alles op.\" En zij zullen al hetgeen zij deden voor zich zien en uw Heer zal niemand onrecht aandoen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,50,'(Gedenk de tijd) toen Wij tot de engelen zeiden: \"Buigt voor Adam\", zij bogen, doch Iblies niet. Hij was ��n der djinn, derhalve was hij ongehoorzaam aan het gebod van zijn Heer. Zult gij hem en zijn nageslacht tot vrienden nemen, terwijl zij uw vijanden zijn? Slecht is het loon der onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,51,'Ik riep hen niet om te getuigen van de schepping der hemelen en der aarde, noch van hun eigen schepping noch neem Ik degenen die misleiden ooit tot helpers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,52,'(Gedenk) de dag waarop Hij zal zeggen: \"Roept degenen waarvan gij beweerdet dat zij Mijn deelgenoten waren.\" Dan zullen zij hen (de afgoden) aanroepen, doch dezen zullen hun niet antwoorden; en Wij zullen een scheiding tussen hen maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,53,'En de schuldigen zullen het Vuur zien en weten dat zij daarin zullen vallen; zij zullen daar niet aan ontkomen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,54,'Voorwaar, Wij hebben in deze Koran voor de mensen allerlei gelijkenissen vermeld, doch de mens is in vele dingen zeer twistziek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,55,'En niets belet de mensen te geloven wanneer de leiding tot hen komt en hun Heer vergiffenis te vragen, dan (dat zij vragen) dat de weg der voorvaderen over hen kome of dat de straf voor hun ogen kome.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,56,'Wij zenden de boodschappers slechts als dragers van de blijde tijding en als waarschuwers. De ongelovigen twisten met leugens om daardoor de Waarheid te niet te doen. En zij houden Mijn tekenen en al hetgeen waarmee zij zijn bedreigd, voor scherts.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,57,'En wie is onrechtvaardiger dan hij die herinnerd wordt aan de tekenen van zijn Heer, doch zich er van afwendt en vergeet, hetgeen zijn handen hebben verricht? Voorwaars Wij hebben sluiers over hun hart gelegd zodat zij niet begrijpen en doofheid in hun oren. Indien gij hen derhalve tot de leiding roept, willen zij de rechte weg niet volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,58,'Doch uw Heer is Vergevensgezind, Barmhartig. Indien Hij hen ter verantwoording zou roepen voor hetgeen zij hebben verdiend, dan zou Hij ongetwijfeld hun straf hebben verhaast. Neen, voor hen is een vastgestelde tijd waaraan zij niet kunnen ontkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,59,'En deze steden! Wij vernietigden ze toen zij ongerechtigheden bedreven. En Wij stelden een bepaalde tijd vast voor hun verdelging.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,60,'En (gedenk de tijd) toen Mozes zeide tot zijn dienaar: \"Ik zal het niet opgeven voordat ik de samenvloeiing van twee zee�n heb bereikt, al moet ik eeuwenlang voortgaan;\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,61,'En toen zij de plek bereikten waar de beide (zee�n) samenkwamen, vergaten zij hun vis en deze zwom snel weg in de zee.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,62,'En toen zij verder gingen, zeide hij tot zijn dienaar: \"Breng ons het ochtendmaal. Waarlijk, vermoeidheid heeft ons bevangen, vanwege onze reis.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,63,'Hij antwoordde: \"Zie, toen wij ons op de rots begaven vergat ik de vis - en slechts Satan deed mij vergeten er over te spreken - en de vis vond op bewonderenswaardige wijze zijn weg naar de zee.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,64,'Hij zeide: \"Dat is waarnaar wij hebben gezocht.\" Derhalve keerden beiden op hun schreden terug.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,65,'Daar vonden zij een Onzer dienaren, aan wie Wij Onze barmhartigheid hadden bewezen en wie Wij van Onze kennis hadden geschonken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,66,'Mozes zeide tot hem: \"Mag ik u volgen dat gij mij onderwijst in de leiding, die u is gegeven?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,67,'Hij antwoordde: \"Gij kunt geen geduld hebben met mij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,68,'\"Want hoe kunt gij geduldig zijn over dingen die uw begrip te boven gaan?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,69,'Hij zeide: \"Indien het Gode behaagt, zult gij mij geduldig vinden en ik zal aan uw bevel niet ongehoorzaam zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,70,'Hij zeide: \"Welaan dan, indien gij mij wenst te volgen stel mij nergens vragen over eer ik zelf daaromtrent tot u spreek.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,71,'Aldus vertrokken beiden totdat zij in een boot stapten en hij maakte er een gat in. Waarop Mozes uitriep: \"Hebt gij er een gat in gemaakt teneinde de opvarenden er van te doen verdrinken? Voorwaar, gij hebt iets gruwelijks bedreven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,72,'Hij antwoordde: \"Had ik u niet gezegd dat gij stellig geen geduld met mij zoudt kunnen tonen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,73,'Mozes zeide: \"Maak mij geen verwijt omdat ik het vergeten ben en maak het mij niet moeilijk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,74,'Zij reisden dus verder tot dat zij een knaap ontmoetten en hij deze doodsloeg. Mozes zeide: \"Hebt gij een onschuldige gedood die niemand had vermoord? Voorwaar, gij hebt een afkeurenswaardige daad begaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,75,'Hij antwoordde: \"Zei ik u niet dat gij nimmer in staat zoudt zijn mij met geduld te vergezellen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,76,'Mozes zeide: \"Indien ik u wederom iets vraag houd mij dan niet in uw gezelschap, dan hebt gij zeker een verontschuldiging van mijn kant.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,77,'Aldus vervolgden zij hun weg totdat zij bij de inwoners ener stad kwamen aan wie zij om eten vroegen, doch dezen weigerden hun gastvrijheid te betonen. Nu vonden zij daar een muur, die op het punt stond in te storten en hij herstelde deze. Mozes zeide: \"Indien gij wildet, hadt gij er loon voor kunnen vragen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,78,'Hij zeide: \"Dit is de scheiding tussen u en mij. Ik zal u thans de verklaring geven van datgene waarvoor gij geen geduld kondet tonen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,79,'\"Wat de boot betreft, deze behoorde aan arme lieden die op de rivier werkten, en ik verkoos haar onbruikbaar te maken want achter hen was een koning die alle (goede) schepen met geweld in beslag wilde nemen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,80,'\"En wat de jongeling betreft, zijn ouders waren gelovigen en wij vreesden dat hij schande over hen zou brengen door zijn opstandigheid en ongeloof.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,81,'\"Derhalve wensten wij dat hun Heer hun in zijn plaats een ander kind zou schenken dat reiner en zachtmoediger zou zijn (dan hij).\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,82,'\"En wat de muur betreft, deze behoorde aan twee weesjongens in de stad en daaronder lag hun schat (begraven), hun vader was een rechtvaardig man derhalve behaagde het uw Heer dat zij volwassen zouden worden en dan hun schat zouden opgraven als een genade van uw Heer, en dit alles deed ik niet uit mezelf. Dit is de verklaring van datgene waarvoor gij geen geduld kondet tonen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,83,'Men vraagt u betreffende Zol-Qarnain. Zeg: \"Ik zal u zijn verhaal vertellen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,84,'Wij vestigden zijn macht op aarde en schonken hem de middelen (en het vermogen) alles te volbrengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,85,'En hij volgde een weg,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,86,'totdat hij het verste punt in de richting van de ondergaande zon bereikte, en deze in een bron van modderig water zag ondergaan, waarbij hij een (ongelovig) volk aantrof. Wij zeiden: \"O, Zol-Qarnain, bestraf hen of behandel hen met vriendelijkheid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,87,'Hij zeide: \"Wat betreft degene die kwaad doet, hem zullen wij straffen; daarna zal hij worden teruggebracht tot zijn Heer die hem straffen zal met een gestrengere straf.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,88,'\"Doch wat hem betreft die gelooft en oprecht handelt, hij zal een goede beloning ontvangen, en Wij zullen hem op Ons bevel alle gemakken verschaffen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,89,'Vervolgens ging hij een andere weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,90,'Totdat hij het land van de rijzende zon bereikte, en ontdekte dat zij over een volk opging voor hetwelk Wij geen beschutting er tegen hadden verschaft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,91,'Zo was het, en Wij hadden volledig kennis van wat hij bezat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,92,'Vervolgens ging hij weer een andere weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,93,'Totdat hij tussen twee bergen kwam, waar hij een volk aantrof dat amper een woord verstond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,94,'Zij zeiden: \"O Zol-Qarnain, Gog en Magog stichten onheil op aarde, mogen wij u dan schatting betalen mits gij een afscheiding tussen hen en ons opricht?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,95,'Hij antwoordde: \"De macht waarmee mijn Heer mij heeft bekleed is beter, doch gij kunt mij met lichamelijke kracht helpen. Ik zal tussen u en hen een sterke afscheiding oprichten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,96,'\"Brengt mij blokken ijzer.\" (Zij deden dit) totdat hij de ruimte tussen de beide rotsen had opgevuld; toen zeide hij: \"Blaast.\" totdat (het ijzer) wit gloeiend werd, nu zeide hij: \"Brengt mij gesmolten koper, opdat ik het er overheen giete.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,97,'Derhalve waren zij (Gog en Magog) niet (meer) in staat er overheen te klimmen, noch waren zij bij machte er doorheen te graven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,98,'Hij zeide: \"Dit is een genade van mijn Heer. Maar wanneer de belofte van mijn Heer vervuld zal worden, zal Hij dit uiteen doen vallen. En de belofte van mijn Heer is werkelijkheid,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,99,'En op die Dag zullen Wij sommigen hunner tegen anderen laten opstaan en de bazuin zal worden geblazen. Dan zullen Wij hen allen tezamen verzamelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,100,'En Wij zullen op die dag de hel aan de ongelovigen tonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,101,'Wier ogen gesluierd waren voor de herinnering aan Mij, en die zelfs niet konden horen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,102,'Denken de ongelovigen dat zij Mijn dienaren tot beschermers kunnen nemen buiten Mij? Voorwaar Wij hebben de hel bereid tot een onthaal voor de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,103,'Zeg: \"Zullen wij u verhalen omtrent degenen die het grootste verlies in hun werken zullen lijden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,104,'Diegenen, wier streven gericht is op het leven dezer wereld en denken dat zij een bijzonder goed werk verrichten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,105,'Dezen zijn het die de tekenen van hun Heer en de ontmoeting met Hem verwerpen. Derhalve zijn hun werken verloren gegaan en op de Dag der Verrijzenis zullen Wij geen weegschaal voor hen oprichten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,106,'De hel is hun beloning wegens hun ongeloof en de spot die zij met Mijn Tekenen en Mijn boodschappers bedreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,107,'Voorwaar, de gelovigen die goede werken doen, zullen de tuinen van het Paradijs tot onthaal hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,108,'Daarin zullen zij vertoeven en zij zullen niet wensen daaruit weg te gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,109,'Zeg: \"Al ware de oceaan inkt voor de Woorden van mijn Heer, zo zou de oceaan zijn uitgeput eer de Woorden van mijn Heer ten einde komen - zelfs al zouden Wij er evenveel ter aanvulling toevoegen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,18,110,'Zeg: \"Ik ben slechts een mens gelijk gij, doch mij wordt geopenbaard dat uw God slechts ��n God is. Laat daarom degene, die op de ontmoeting met zijn Heer hoopt, goede daden verrichten en bij de aanbidding van zijn Heer niemand anders met Hem vereenzelvigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,1,'Kaaf, Haa, Jaa, ´Ain, Saad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,2,'Dit is een vermelding van de barmhartigheid van uw Heer, betoond aan Zijn dienaar, Zacharia.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,3,'Toen hij zijn Heer in het verborgene aanriep,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,4,'Zeide hij: \"Mijn Heer, het gebeente in mij is zwak geworden en mijn hoofd glanst met grijze haren, niettemin ben ik niet wanhopig, mijn Heer, bij mijn aanroep tot U.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,5,'\"Maar ik vrees mijn bloedverwanten na mij; mijn vrouw is onvruchtbaar, geef mij een opvolger van U.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,6,'\"Opdat hij mij en het Huis van Jacob tot erfgenaam moge zijn. En maak hem, mijn Heer, U welgevallig.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,7,'(God antwoordde) \"O Zacharia, Wij brengen u blijde tijding omtrent een zoon wiens naam Jahja (Johannes) zal zijn. Wij hebben voordien niemand aan hem gelijk gemaakt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,8,'Hij zeide: \"Mijn Heer, hoe kan mij een zoon geworden, terwijl mijn vrouw onvruchtbaar is en ik de uiterste grens des ouderdoms heb bereikt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,9,'Hij zeide: \"Het zij zo, Uw Heer zegt: ´Het is gemakkelijk voor Mij, Ik heb u voordien geschapen toen gij niets waart.´\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,10,'Hij zeide: \"Mijn Heer, geef mij een teken.\" (God) zei: \"Uw teken is dat gij voor drie opeenvolgende dagen en nachten tot niemand zult spreken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,11,'Aldus kwam hij uit de kamer tot zijn volk en beduidde hen God in de morgen en in de avond te verheerlijken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,12,'\"O Jahja (Johannes), houd u krachtig aan het Boek.\" Wij schonken hem wijsheid, terwijl hij nog een kind was,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,13,'En zachtmoedigheid van Ons en reinheid. En hij was vroom,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,14,'Vriendelijk en goed voor zijn ouders. En hij was trots noch opstandig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,15,'Vrede was met hem op de dag zijner geboorte, en op zijn sterfdag, en zal eveneens met hem zijn op de dag waarop hij weer tot leven zal worden gewekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,16,'En vermeld Maria in het Boek. Toen zij zich van haar volk terugtrok in een op het Oosten uitziende plaats,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,17,'En zich aan hlm blikken onttrok, zonden Wij Onze Geest tot haar en hij verscheen aan haar in de gestalte van een volmaakte man.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,18,'Zij zeide: \"Ik neem mijn toevlucht tot de Barmhartige tegen u, laat mij met rust, indien gij (God) vreest.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,19,'Hij antwoordde: \"Ik ben slechts een boodschapper van uw Heer opdat ik u een reine zoon moge schenken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,20,'Zij zeide: \"Hoe kan ik een zoon ontvangen terwijl geen man mij heeft aangeraakt en ik evenmin onkuisheid heb bedreven?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,21,'Hij zeide: \"Het is zo naar uw Heer zegt, ´het is gemakkelijk voor Mij,´\" opdat Wij hem tot een teken voor de mensen maken, een genade Onzerzijds; het is een besloten zaak.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,22,'En zij ontving hem en trok zich met hem terug in een ver afgelegen oord.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,23,'En de smarten der bevalling dreven haar naar de voet van een palmboom. Zij zeide: \"O, liever zou ik v��r dit geschiedde gestorven en in de vergetelheid geraakt zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,24,'Dan riep (Gods boodschapper) haar van beneden toe, zeggende: \"Treur niet. Uw Heer heeft een beekje aan uw voet doen ontstaan;\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,25,'\"En schud de stam van de palmboom naar u toe, deze zal verse, rijpe dadels op u doen neervallen;\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,26,'\"Eet en drink en koel uw oog. En indien gij iemand ziet, beduid hem dan: ´Ik heb de Barmhartige gelofte gedaan te vasten; derhalve zal ik heden met niemand spreken.´\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,27,'Alsdan bracht zij het kind tot haar volk. Dit zeide: \"O Maria, gij hebt iets vreemds gedaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,28,'\"O Zuster van A�ron, uw vader was geen verdorven man noch was uw moeder een onkuise vrouw.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,29,'Dan wees zij naar het kind. Zij zeiden: \"Hoe kunnen wij tot een wiegekind spreken?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,30,'Hij (Jezus) zeide: \"Ik ben een dienaar van Allah. Hij heeft mij het Boek gegeven en mij tot een profeet gemaakt;\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,31,'\"Hij heeft mij gezegend waar ik mij ook moge bevinden; en heeft mij het gebed en het geven van aalmoezen zolang ik leef opgelegd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,32,'\"En dat ik gehoorzaam zou zijn jegens mijn moeder. Hij heeft mij noch een onderdrukker, noch een slecht mens gemaakt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,33,'\"Vrede was met mij op de dag mijner geboorte en zal met mij zijn op de dag van mijn dood en evenzo op de dag dat ik ten leven zal worden opgewekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,34,'Aldus was Jezus, de zoon van Maria. En (dit is) het ware woord waaraan zij twijfelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,35,'Het past niet bij Allah Zich een zoon te verwekken, Heilig is Hij. Wanneer Hij een beslissing neemt, zegt Hij daartoe slechts: \"Wees\", en het wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,36,'\"Voorwaar, Allah is mijn Heer en uw Heer. Aanbidt Hem derhalve, dit is de rechte weg.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,37,'Doch (sommige) partijen verschillen (hierover) onderling van mening; maar wee de ongelovigen bij hun aanwezigheid op de grote Dag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,38,'Hoe helder zal hun horen en hun zien zijn op die Dag wanneer zij tot Ons zullen komen. Waarlijk, de onrechtvaardigen zijn in duidelijke dwaling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,39,'En waarschuw hen voor de Dag der Smart wanneer het oordeel zal worden geveld. Thans zijn zij achteloos en geloven niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,40,'Wij zijn het, Die de aarde en alles wat zich daarop bevindt zullen erven en tot Ons zullen zij worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,41,'En vermeld Abraham in het Boek. Hij was een waarheidslievend profeet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,42,'Toen hij tot zijn vader zeide: \"O mijn vader, waarom aanbidt gij hetgeen hoort noch ziet, noch u op enigerlei wijze kan baten?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,43,'\"O mijn vader, er is inderdaad kennis tot mij gekomen die niet tot u is gekomen, volg mij daarom, ik zal u naar een pad leiden dat effen en recht is.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,44,'\"O mijn vader, dien Satan niet want Satan is weerspannig tegen de Barmhartige;\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,45,'\"O mijn vader, ik vrees dat de straf van de Barmhartige u zal treffen en dat gij dan een gezel van Satan zult worden,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,46,'Antwoordde hij: \"Verzaakt gij mijn goden, o Abraham? Indien gij niet ophoudt, zal ik u zeker uitbannen. Laat mij een tijd met rust.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,47,'Abraham zeide: \"Vrede zij met u. Ik zal mijn Heer om vergiffenis voor u smeken. Hij is mij inderdaad genadig.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,48,'\"En ik zal mij verre houden van u en van hetgeen gij nevens Allah aanroept, en ik zal tot mijn Heer bidden; waarschijnlijk zal ik in mijn gebed tot mijn Heer niet worden teleurgesteld.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,49,'Toen hij zich van hen en van hetgeen zij nevens Allah aanbaden, had losgemaakt, schonken Wij hem Isa�c en Jacob en maakten elk hunner profeet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,50,'En Wij schonken hun Onze barmhartigheid en een verheven en goede naam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,51,'En vermeld Mozes in het Boek. Voorwaar hij was een uitverkorene, boodschapper en profeet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,52,'Wij riepen hem van de rechter zijde van de Berg (Sina�), en deden hem tot Ons naderen om met hem te spreken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,53,'En Wij schonken hem, door Onze barmhartigheid zijn broeder A�ron als profeet en helper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,54,'En gedenk Isma�l in het Boek. Hij was getrouw aan zijn belofte En hij was (eveneens) een boodschapper - profeet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,55,'Hij placht zijn volk gebeden en aalmoezen aan te bevelen en zijn Heer had welbehagen in hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,56,'En vermeld Idries in het Boek Hij was een waarheidslievend profeet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,57,'En Wij verhieven hem tot een hoge plaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,58,'Dezen zijn het over wie Allah Zijn zegeningen heeft uitgestort; namelijk de profeten van het nageslacht van Adam en van degenen die Wij met Noach droegen (in de ark) en van het nageslacht van Abraham en Isra�l; en zij behoren tot degenen die Wij leidden en uitverkoren. Toen de tekenen van de Weldadige hun werden voorgelezen vielen zij buigend en wenend neder.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,59,'Hen volgden de bozen op, die het gebed verwaarloosden, en hun hartstochten gehoor gaven. Weldra zullen zij hun ondergang tegemoet gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,60,'Maar zij die berouw hebben en geloven en goede werken verrichten, zullen het paradijs binnengaan en zij zullen geenszins schade lijden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,61,'Tuinen der eeuwigheid, dat is een belofte van het Onzienlijke, welke de Barmhartige aan Zijn dienaren heeft gedaan. Voorwaar, Zijn belofte zal zeker worden vervuld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,62,'Zij zullen daarin geen ijdel gesprek horen: slechts \"vrede\", en ´s morgens en ´s avonds zullen zij hun levensonderhoud ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,63,'Aldus is het paradijs dat Wij als erfenis geven aan Onze dienaren, die rechtvaardig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,64,'\"Wij (engelen) dalen slechts neder op bevel van uw Heer. Aan Hem behoort al hetgeen v��r ons is en al hetgeen achter ons is en al hetgeen er tussen ligt; en uw Heer vergeet nimmer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,65,'Hij is de Heer der hemelen en der aarde en al hetgeen hier tussen is. Dien Hem derhalve en wees volhardend in Zijn aanbidding. Kent gij Zijn gelijke?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,66,'En de mens zegt: \"Zal ik wanneer ik dood ben, dan tot leven worden terug gebracht?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,67,'Herinnert de mens zich dan niet dat Wij hem voorheen hebben geschapen toen hij nog niets was?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,68,'En bij uw Heer, Wij zullen hen en de duivelen zeker verzamelen: dan zullen Wij hen op de knie�n rondom de hel plaatsen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,69,'Dan zullen Wij zeker uit elke groep diegenen onder hen uitkiezen die het opstandigst waren tegen de Weldadige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,70,'En voorzeker, Wij weten het best wie onder hen het meest verdienen daarin te branden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,71,'Er is niemand onder u of hij zal er toe komen - dit is een door uw Heer vastgesteld besluit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,72,'Dan zullen Wij de rechtvaardigen redden en de bozen op hun knie�n daarin achterlaten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,73,'En wanneer Onze duidelijke tekenen aan hen worden voorgehouden zeggen de ongelovigen tot de gelovigen: \"Welke van de twee partijen neemt de beste plaats in en welke is beter als kring?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,74,'Hoevele geslachten hebben Wij niet v��r hen verdelgd, die een groter bezit hadden en een beter uiterlijk!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,75,'Zeg: \"De Weldadige geeft degenen die dwalen uitstel totdat zij zullen zien hetgeen waarmee zij worden bedreigd, - zij het de kastijding of het Uur - daarna zullen zij weten wie de slechtste plaats inneemt en wie zwakkere strijdkrachten heeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,76,'En Allah vermeerdert leiding voor degenen die leiding volgen. De blijvende goede werken geven een betere beloning en (vormen) de beste toevlucht bij uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,77,'Hebt gij hem dan gezien die Onze tekenen verwerpt en zegt: \"Mij zullen zeker rijkdommen en kinderen worden geschonken?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,78,'Heeft hij toegang tot het Onzienlijke gehad of heeft hij een belofte uit de hand van de Weldadige ontvangen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,79,'Neen, hetgeen hij zegt tekenen Wij aan en Wij zullen de straf voor hem vermeerderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,80,'En Wij zullen al hetgeen waarover hij spreekt erven en hij zal alleen tot Ons komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,81,'Zij hebben andere goden naast Allah genomen, opdat dezen een bron van macht voor hen mogen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,82,'Stellig niet! Integendeel zij (de afgoden) zullen hun aanbidding ontkennen en hun tegenstanders blijken te zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,83,'Ziet gij niet dat Wij duivelen over de ongelovigen hebben losgelaten om hen aan te sporen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,84,'Wees daarom niet gehaast tegenover hen, Wij zullen voor hen de juiste (vergelding) voorbereiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,85,'Ten dage waarop Wij de godvrezenden in groepen zullen verzamelen tot de Barmhartige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,86,'Zullen Wij de schuldigen als een dorstige kudde naar de hel drijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,87,'Zij zullen geen voorspraak hebben behalve degenen die van de Weldadige een belofte hebben ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,88,'En zij zeggen: \"De Barmhartige heeft zich een zoon genomen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,89,'Gij hebt voorzeker een lastering uitgesproken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,90,'De hemelen dreigen vaneen te scheuren, en de aarde te splijten en de bergen in stukken te vallen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,91,'Daar zij aan de Barmhartige een zoon hebben toegekend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,92,'Terwijl de Barmhartige te verheven is om een zoon te hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,93,'Er is niemand in de hemelen en op de aarde die niet als een dienaar tot de Barmhartige zal komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,94,'Voorwaar, Hij kent hen en heeft hen allen precies geteld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,95,'En op de Dag der Opstanding zal elk hunner alleen tot Hem komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,96,'Degenen die geloven en goede daden doen - aan hen zal de Barmhartige liefde betonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,97,'Aldus hebben Wij hem (de Koran) gemakkelijk voor uw tong gemaakt, opdat gij er mede goede tijdingen aan de godvruchtigen moogt geven en een twistziek volk er door moogt waarschuwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,19,98,'En hoevele geslachten hebben Wij v��r hen niet vernietigd? Kunt gij een enkeling hunner zien of een voetstap van hen horen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,1,'Taa Haa.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,2,'Wij hebben u de Koran niet geopenbaard opdat gij (er door) ongelukkig zoudt worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,3,'Doch als een vermaning voor hem die (God) vreest.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,4,'Een openbaring van Hem, Die de aarde en de verheven hemelen heeft geschapen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,5,'De Barmhartige, Die Zich nederzette op de Troon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,6,'Hem behoort al hetgeen in de hemelen en op aarde is, eveneens hetgeen er tussen ligt en hetgeen zich onder de grond bevindt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,7,'Of gij het woord luide verkondigt (of fluistert), Hij kent het geheime en verborgene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,8,'Allah, - er is geen God dan Hij. Hij heeft de schoonste eigenschappen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,9,'Hebt gij de geschiedenis van Mozes gehoord?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,10,'Toen hij een vuur zag, zeide hij tot de zijnen: \"Blijft hier, ik bespeur een vuur; misschien zal ik u daarvan een vuurbrand kunnen brengen of door het vuur de weg vinden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,11,'En toen hij het (vuur) naderde werd hij aangeroepen: \"O Mozes\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,12,'\"Voorwaar, Ik ben uw Heer, ontdoe u van uw schoeisel; want gij zijt in de heilige vallei van Towa.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,13,'\"Ik heb u uitverkoren; luister dus naar hetgeen wordt geopenbaard.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,14,'\"Voorwaar, Ik ben Allah; er is geen God behalve Ik, aanbid Mij derhalve en verricht het gebed tot Mijn gedachtenis.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,15,'\"Zie, het Uur komt. Ik zal het onthullen opdat elke ziel de beloning zal ontvangen waarnaar zij streeft.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,16,'\"Laat degene die er niet in gelooft en zijn eigen neigingen volgt, u er niet van afwenden; anders zoudt gij verloren gaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,17,'\"En wat hebt gij in uw rechter hand, O Mozes?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,18,'Hij antwoordde: \"Dit is mijn staf waarop ik leun, en waarmee ik bladeren afsla voor mijn kudde; ik gebruik hem ook voor andere doeleinden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,19,'Hij zeide: \"Werp hem neer o Mozes.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,20,'Dus wierp hij hem neer, en zie, het was een kronkelende slang.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,21,'God zeide: \"Raap hem op en vrees niet. Wij zullen hem in zijn vroegere staat herstellen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,22,'\"En leg uw hand onder uw arm, zij zal wit worden zonder ziekte. Nog een teken (is dit)!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,23,'\"Opdat Wij u Onze grotere tekenen mogen tonen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,24,'\"Ga naar Pharao; hij heeft inderdaad de perken overschreden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,25,'Hij zeide: \"Mijn Heer, verruim mijn borst,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,26,'\"En maak mij mijn taak lichter,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,27,'\"En ontdoe de knoop in mijn tong,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,28,'\"Opdat zij (de mensen) mijn woorden mogen verstaan,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,29,'\"Geef mij een helper uit mijn familie,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,30,'\"A�ron, mijn broeder;\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,31,'\"Vergroot mijn kracht door hem,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,32,'\"En laat hem mijn arbeid delen,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,33,'\"Opdat wij U veel mogen verheerlijken,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,34,'\"En U zeer indachtig mogen zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,35,'\"Voorzeker Gij doorziet ons.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,36,'God zeide: \"Uw verzoek is ingewilligd, o Mozes.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,37,'\"En bij een andere gelegenheid bewezen Wij u ook een gunst.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,38,'\"Toen Wij uw moeder openbaarden:\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,39,'\"´Plaats hem in het kistje en werp dit in de rivier, dan zal de rivier het op de oever werpen, zodat een vijand van Mij en van hem, hem zal opnemen´. En Ik omhulde u met Mijn liefde; opdat gij zoudt worden grootgebracht voor Mijn oog.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,40,'\"Toen uw zuster voorbijkwam en zeide: ´Zal ik u iemand noemen die hem zal verzorgen?´ Aldus schonken Wij u terug aan uw moeder opdat haar oog zou worden verfrist en zij niet zou treuren. En gij dooddet een man, doch Wij verlosten u van smart. En Wij beproefden u op verschillende manieren. En gij vertoefdet jaren te midden van het volk van Midian. Dan zijt gij, o Mozes, herwaarts gekomen zoals besloten was.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,41,'\"En Ik heb u uitverkoren voor Mijzelf.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,42,'\"Gaat, gij en uw broeder, met Mijn tekenen, en verwaarloost niet Mijner indachtig te zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,43,'\"Gaat gij beiden tot Pharao, want hij is alle perken te buiten gegaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,44,'Doch spreekt tot hem op welwillende wijze, opdat hij er lering uit moge trekken, of vrezen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,45,'Zij antwoordden: \"Onze Heer, wij vrezen dat hij tegenover ons gewelddadig zal zijn of opstandig zal worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,46,'Hij (Allah) zei: \"Vreest niet, want Ik ben met u. Ik hoor en Ik zie.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,47,'\"Gaat dus naar hem toe en zegt: ´Wij zijn de boodschappers van uw Heer; laat derhalve de kinderen van Isra�l met ons weggaan, en doe hun geen leed aan. Wij hebben u, voorwaar, een teken gebracht van uw Heer; vrede rust op hem die de leiding volgt;´\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,48,'\"´Het is ons geopenbaard dat kastijding zal komen over hem, die loochent en zich afwendt.´\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,49,'Pharao zeide: \"Wie is uw Heer, o Mozes?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,50,'Hij antwoordde: \"Onze Heer is Hij, Die aan alles een eigen vorm gaf en het daarna leidde.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,51,'Hij (Pharao) zeide: \"Hoe staat het met vroegere geslachten?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,52,'\"De kennis daarvan is bij mijn Heer in een Boek. Mijn Heer dwaalt, noch vergeet,\" zeide Mozes.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,53,'Hij is het Die u de aarde heeft gegeven tot een wieg en wegen voor u heeft doen ontstaan en Die regen doet nederdalen uit de hemel, waardoor Hij allerlei planten voortbrengt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,54,'(Zeggende): \"eet hiervan en weidt uw vee.\" Voorwaar, hierin liggen tekenen voor degenen die verstand bezitten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,55,'Uit de aarde hebben Wij u geschapen en daarin zullen Wij u doen terugkeren en daaruit zullen Wij u weer opwekken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,56,'En Wij toonden (Pharao) Onze tekenen, doch hij loochende deze en weigerde deze (te geloven).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,57,'Hij zeide: \"Zijt gij tot mij gekomen, o Mozes, om ons door uw toverkunst uit ons land te verdrijven?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,58,'\"Voorzeker, wij zullen gelijkwaardige toverkunst tegenover (de uwe) stellen; maak derhalve een afspraak met ons die wij noch gij zullen verzuimen na te komen op een plaats (voor beiden) gelijk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,59,'Hij zeide: \"Uw afspraak zal plaats vinden op de dag van het feest en laat het volk bijeenkomen in de voormiddag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,60,'Daarop trok Pharao zich terug en stelde zijn plan vast en kwam vervolgens (op de bijeenkomst).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,61,'Mozes zeide tot hen: \"Wee u; verzint geen leugen over Allah, anders zal Hij u door een kastijding verdelgen. Hij die een leugen verzint, slaagt nimmer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,62,'Vervolgens redetwistten zij (de tegenstanders) onder elkander over hun aangelegenheden en pleegden geheim overleg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,63,'Zij zeiden: \"Deze twee zijn zeker tovenaars die u met behulp van hun toverkunst uit uw land wensen te verdrijven en uw schone kultuur te vernietigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,64,'\"Beraamt derhalve uw plan en treedt dan eensgezind naar voren. En voorwaar hij die op deze dag zegeviert, zal zeker slagen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,65,'Zij zeiden: \"O Mozes, werpt gij, of zullen wij de eersten zijn om te werpen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,66,'Hij zeide: \"Neen, werpt gij.\" Dan ziet, het scheen hem wegens hun toverkunst toe, dat hun koorden en staven zich voortbewogen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,67,'En Mozes sloeg de angst om het hart.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,68,'Wij zeiden: \"Vrees niet, want gij zijt de overwinnaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,69,'\"Werp hetgeen in uw rechter hand is; het zal wat zij hebben voortgebracht verslinden, want hetgeen zij hebben gemaakt is slechts toverkunst. En een tovenaar slaagt nooit waar hij ook moge komen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,70,'En de tovenaars werden plat ter aarde geworpen, zich nederbuigend. Zij zeiden: \"Wij geloven in de Heer van A�ron en Mozes.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,71,'Pharao zeide tot hen: \"Gelooft gij in Hem eer ik u daartoe verlof geef? Hij moet uw meester zijn die u in de toverkunst heeft onderwezen. Daarom zal ik uw handen en voeten aan de tegenovergestelde kant afhakken en ik zal u voorzeker aan de stammen van palmbomen kruisigen; en gij zult met zekerheid weten wie van ons gestrenger en langduriger is in het straffen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,72,'Zij zeiden: \"In geen geval zullen wij u verkiezen boven de duidelijke tekenen die tot ons zijn gekomen, en boven Hem Die ons geschapen heeft. Doet derhalve wat gij wilt; gij kunt alleen over het leven dezer wereld beslissen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,73,'\"Voorzeker, wij hebben geloofd in onze Heer opdat Hij ons onze zonden en de tovenarij die gij ons hebt gedwongen te bedrijven, moge vergeven. Allah is de Beste, de Bestendigste.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,74,'Voorwaar hij die tot zijn Heer komt als schuldige, hem wacht de (straf der) hel: hij zal daarin sterven noch leven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,75,'Doch die als gelovigen tot Hem komen en goede werken hebben verricht, zullen de hoogste graden der gelukzaligheid ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,76,'Tuinen der eeuwigheid waar doorheen rivieren stromen en waarin zij voor eeuwig zullen vertoeven. En dat is de beloning dergenen die zich louteren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,77,'Wij openbaarden Mozes: \"Voer Mijn dienaren weg in de nacht en baan voor hen een droge weg door de zee. Gij behoeft niet te vrezen, dat gij zult worden ingehaald, noch zult gij angstig zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,78,'Alsdan achtervolgde hen Pharao met zijn leger en toen overspoelde de zee hen allen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,79,'En Pharao voerde zijn volk op een dwaalspoor, hij leidde hen niet op de rechte weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,80,'\"O kinderen van Isra�l, Wij bevrijdden u van uw vijand en Wij gingen met u een verbond aan, aan de rechter zijde van de Berg (Sina�) en zonden manna en kwartels op u neder.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,81,'\"Eet van de goede dingen die Wij u hebben verschaft en overtreedt niet hier in, anders zal Mijn toorn op u nederdalen en degene op wie Mijn toorn nederdaalt gaat ten onder.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,82,'\"Maar voorzeker, Ik ben Vergevensgezind jegens hem die berouw heeft en gelooft en het goede doet en het richtsnoer volgt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,83,'\"En wat heeft u van uw volk haastig doen weggaan, o Mozes?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,84,'Hij zeide: \"Zij volgen in mijn spoor, en ik heb mij tot U gehaast, Mijn Heer, opdat Gij welbehagen in mij moogt hebben.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,85,'(Allah) zeide: \"Wij hebben uw volk in uw afwezigheid beproefd en Saamiri heeft hen misleid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,86,'Mozes keerde daarop verontwaardigd en bedroefd tot zijn volk terug. Hij zeide: \"O mijn volk, heeft uw Heer u dan geen schone belofte gedaan? Kwam de vastgestelde tijd u dan te lang voor, of verlangdet gij dat de toorn van uw Heer op u zou nederdalen dat gij uw belofte aan mij hebt gebroken?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,87,'Zij antwoordden: \"Wij hebben niet uit eigen beweging onze belofte aan u gebroken, doch wij waren belast met een lading sieraden van het volk, derhalve wierpen wij deze weg, en dat heeft Saamiri voorgesteld.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,88,'Dan maakte deze voor het volk een kalf - een beeld, dat een loeiend geluid voortbracht. En men zeide: \"Dit is uw God en de God van Mozes,\" doch hij is hem vergeten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,89,'Konden zij dan niet zien dat het (kalf) hun geen antwoord gaf en geen macht had om hun kwaad of goed te doen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,90,'En inderdaad had A�ron reeds tot hen gezegd: \"O mijn volk, voorzeker gij zijt daarmee op de proef gesteld. Voorwaar uw Heer is de Barmhartige; volgt mij derhalve en gehoorzaamt mijn bevel.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,91,'Zij antwoordden: \"Wij zullen in geen geval ophouden het (kalf) te aanbidden voordat Mozes tot ons is teruggekeerd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,92,'Hij (Mozes) zeide: \"O A�ron, wat belette u, toen gij hen zaagt dwalen,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,93,'Mij te volgen? Hebt gij dan mijn gebod veronachtzaamd?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,94,'Hij antwoordde: \"O zoon van mijn moeder, grijp mij niet bij mijn baard noch bij mijn hoofd.\" Ik was beducht dat gij zoudt zeggen: ´Gij hebt een scheuring teweeg gebracht onder de kinderen van Isra�l en hebt niet op mijn woord gewacht.´\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,95,'Hij (Mozes) zeide: \"En wat hebt gij te zeggen, o Saamiri?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,96,'Hij zeide: \"Ik zag wat zij niet konden zien. Ik volgde de voetstappen van de boodschapper naar mijn beste vermogen, doch dat heb ik thans opgegeven. Aldus heeft. mijn ziel het voor mij vergemakkelijkt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,97,'Mozes zeide: \"Ga dan heen, gedurende heel uw leven zult gij zeggen: ´Raak mij niet aan,´ en bovendien is er voor u een straf (bereid) waaraan gij niet zult ontkomen. Aanschouw thans uw god waarvan gij een toegewijd aanbidder zijt geworden. Wij zullen hem verbranden en daarna in zee strooien.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,98,'Uw God is slechts Allah, naast Wie er geen God is. Hij omvat alle dingen in Zijn kennis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,99,'Zo vermeldden Wij u (Mohammed) de tijdingen van het voorafgaande, waarin Wij u een vermaning Onzerzijds hebben gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,100,'Wie zich er van zal afwenden zal op de Dag der Opstanding de last hiervan dragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,101,'Daaronder zullen zij blijven en deze last zal voor hen op de Dag der Herrijzenis ondraaglijk worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,102,'De Dag waarop de bazuin zal worden geblazen zullen Wij de zondigen bijeenverzamelen en hun ogen zullen zonder licht zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,103,'Zij zullen met elkander op zachte toon spreken en zeggen: \"Gij zijt slechts tien (dagen) gebleven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,104,'Wij weten wat zij zullen zeggen wanneer de beste hunner beweert: \"Gij zijt slechts ��n dag gebleven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,105,'Zij (de ongelovigen) vragen u betreffende de bergen. Zeg: \"Mijn Heer zal ze verpulveren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,106,'\"En Hij zal haar (de aarde) als een lege vlakte laten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,107,'\"Waarop gij generlei inzinking of verhoging zult zien.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,108,'Op die Dag zullen zij de oproeper volgen, die recht op zijn doel afgaat; alle stemmen zullen voor de Barmhartige worden verzacht en gij zult een gedempt geluid gefluistere horen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,109,'Op die Dag zal voorspraak niet van nut zijn behalve van hem aan wie de Barrnhartige verlof geeft en wiens woord Hem welgevallig is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,110,'Hij weet al hetgeen v��r hen en al hetgeen achter hen is, maar zij kunnen het met hun kennis niet omvatten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,111,'Alle gezichten zullen zich verootmoedigen in tegenwoordigheid van de Levende, de Uitzichzelf - Bestaande. Voorzeker, hij die ongerechtigheid begaat zal verloren gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,112,'Maar hij die goede werken verricht en gelovig is, behoeft geen ongerechtigheid of verlies te vrezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,113,'Aldus hebben Wij het (Boek) als een duidelijke Koran nedergezonden en Wij hebben daarin duidelijk waarschuwingen herhaaldelijk uiteengezet, opdat men (God) moge vrezen en opdat het hen tot nadenken moge brengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,114,'Verheven zij Allah, de Ware Koning. En haast u niet met de Koran eer de openbaring er van aan u voltooid is en zeg: \"O mijn Heer, doe mij toenemen in kennis.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,115,'En waarlijk wij gaven voorheen Adam een bevel, doch hij vergat het en Wij vonden in hem geen voornemen daartoe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,116,'En toen Wij tot de engelen zeiden: \"Bewijst Adam eer,\" bewezen zij allen eer, doch niet Iblies. Hij weigerde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,117,'Daarom zeiden Wij: \"O Adam, deze is voor u en uw vrouw een vijand; laat hij u derhalve niet uit de tuin verdrijven, anders zult gij ongelukkig worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,118,'\"(Daarin is voorraad voor u) opdat gij er niet zult hongeren noch naakt zult zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,119,'\"En dat gij er geen dorst zult lijden noch zult blootgesteld zijn aan de hitte van de zon.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,120,'Doch Satan fluisterde hem kwaad in, hij zeide: \"O Adam, zal ik u voeren tot de Boom der Eeuwigheid, en een koninkrijk dat nimmer zal vergaan?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,121,'Zo aten beiden er van, waardoor hun schaamte hun duidelijk werd en zij zich begonnen te bekleden met bladeren uit de tuin. En Adam was ongehoorzaam aan het gebod van zijn Heer, derhalve leed hij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,122,'Alsdan verkoos zijn Heer hem, vergaf hem en leidde hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,123,'Hij (God) zeide: \"Gaat allen tezamen hier vandaan, want gij zult elkander tot vijanden zijn. En indien er leiding van Mij tot u komt dan zal een ieder die Mijn leiding volgt, noch dwalen noch ongelukkig zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,124,'Doch degene die zich van Mijn gedachtenis zal afwenden, zal in benarde omstandigheden leven en op de Dag der Opstanding zullen Wij hem blind doen opstaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,125,'Hij zal zeggen: \"Mijn Heer waarom hebt Gij mij blind doen opstaan, terwijl ik kon zien?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,126,'God zal zeggen: \"Aldus kwamen Onze tekenen tot u en gij hebt er geen acht op geslagen en insgelijks zal op deze Dag op u geen acht worden geslagen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,127,'Op deze wijze vergelden Wij hem die buitensporig is en niet gelooft in de tekenen van zijn Heer; en de straf van het Hiernamaals is zeker gestrenger en langer van duur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,128,'Is het hun (bewoners van Mekka) dan niet duidelijk hoevele geslachten Wij v��r hen hebben verdelgd, in wier woonplaatsen zij wandelen? Voorwaar, daarin liggen tekenen voor degenen die met rede zijn begaafd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,129,'En ware het niet om een woord dat reeds van uw Heer was uitgegaan over een vastgestelde termijn, dan zou de straf al gekomen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,130,'Verdraag (Mohammed) lijdzaam hetgeen zij zeggen en verheerlijk uw Heer met de lof die Hem toekomt voor het opgaan der zon en voor haar ondergang en verheerlijk Hem in de uren van de nacht en op de gedeelten van de dag, opdat gij gelukkig moogt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,131,'En wend uw ogen niet naar hetgeen Wij hebben toebedeeld van de heerlijkheid dezer wereld aan verschillenden hunner, om hen daardoor te beproeven. De voorziening van uw Heer is beter en van langer duur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,132,'En spoor uw volk aan tot gebed en wees daarin volhardend. Wij vragen geen levensonderhoud van u, Wij onderhouden u. En het einde is voor de godvruchtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,133,'En zij (de ongelovigen) zeggen: \"Waarom brengt hij ons geen teken van zijn Heer?\" Is er dan geen duidelijk teken tot hen gekomen in hetgeen in de vroegere geschriften staat?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,134,'En indien Wij hen voor zijn (van de profeet) komst met een straf hadden verdelgd, zouden zij ongetwijfeld hebben gezegd: \"Onze Heer, waarom hebt Gij ons geen boodschapper gezonden, dan hadden wij Uw geboden kunnen volgen eer wij vernederd en onteerd werden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,20,135,'Zeg: \"Een ieder wacht; wacht gij derhalve ook en weldra zult gij te weten komen wie de mensen van het rechte pad zijn en wie de rechte weg volgen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,1,'Voor de mensen is de afrekening dichterbij gekomen en toch wenden zij zich in achteloosheid af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,2,'Er komt geen nieuwe Vermaning tot hen van hun Heer of zij luisteren er naar terwijl zij er mee spelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,3,'En hun hart is achteloos. En de onrechtvaardigen plegen overleg in het geheim zeggende: \"Is deze (Mohammed) niet slechts een mens als gij? Wilt gij dan de tovenarij met open ogen tegemoet gaan?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,4,'Zeg: \"Mijn Heer, weet wat in de hemel en op aarde wordt gezegd; Hij is de Alhorende, de Alwetende.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,5,'\"Neen,\" zeggen zij, \"verwarde dromen; neen, hij heeft het verzonnen; neen, hij is een dichter. Laat hem ons een teken brengen zoals de vroegere (profeten) dit hebben gebracht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,6,'V��r hen (bewoners van Mekka) heeft nooit een stad geloofd die Wij vernietigden; zullen deze dan wel geloven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,7,'En v��r u zonden Wij slechts mannen aan wie Wij een openbaring hadden gezonden - Vraagt degenen, die de Vermaning bezitten, indien gij het niet weet. -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,8,'En Wij maakten hun lichaam niet zodanig dat zij geen voedsel behoefden te gebruiken, evenmin dat zij voor eeuwen konden blijven leven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,9,'Aldus vervulden Wij aan hen Onze belofte, en Wij redden hen en degenen die Wij wilden; doch Wij verdelgden de buitensporigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,10,'Wij hebben u een Boek (de Koran) nedergezonden waardoor gij tot aanzien kunt komen, wilt gij dan met begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,11,'Hoe menige stad vol van ongerechtigheid hebben Wij vernietigd en na haar hebben Wij een ander volk verwekt!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,12,'En toen zij Onze straf bemerkten, ziet, toen sloegen zij er voor op de vlucht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,13,'\"Vlucht niet en keert terug tot de genoegens die u waren veroorloofd en tot uw woningen opdat gij ondervraagd zult worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,14,'Zij antwoordden: \"Wee ons, voorzeker, wij waren onrechtvaardig.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,15,'En hun geroep hield niet op totdat Wij hen nedermaaiden en uitblusten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,16,'Wij schiepen de hemel en de aarde en al hetgeen er tussen is, niet tot vermaak.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,17,'Indien Wij een spel hadden willen doen, dan zouden Wij met Onszelf hebben gespeeld, maar dit doen Wij niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,18,'Neen, Wij stellen de waarheid tegenover de valsheid zodat de eerste de laatste het hoofd breekt en ziet, zij vergaat. En wee u, wegens hetgeen gij beweert.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,19,'Hem behoort wat in de hemelen en op aarde is, en degenen die zich in Zijn tegenwoordigheid bevinden, zijn niet te trots om Hem te aanbidden, noch worden zij dit moede;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,20,'Zij verheerlijken Hem dag en nacht, en zij verslappen hierin nimmer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,21,'Hebben zij (de afgodendienaars) goden genomen van de aarde die de doden kunnen opwekken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,22,'Indien er naast Allah andere Goden waren in (de hemel en op aarde) zouden dezen voorzeker tot chaos zijn vervallen. Verheven is Allah, de Heer van de Troon, boven hetgeen zij zeggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,23,'Hij kan niet worden ondertraagd betreffende hetgeen Hij doet, doch zij zullen worden ondervraagd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,24,'Hebben zij Goden buiten Hem genomen? Zeg: \"Komt met uw bewijzen.\" Hier is de verkondiging dergenen die v��r mij waren. Doch de meesten hunner kennen de waarheid niet en zij zijn er afkerig van.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,25,'En Wij zonden geen boodschapper v��r u zonder hem te openbaren: \"Voorzeker er is geen God buiten Mij, aanbidt derhalve Mij alleen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,26,'En zij zeggen: \"De Barmhartige heeft Zich een zoon genomen.\" Heilig is Hij. Neen, zij zijn slechts ge�erde dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,27,'Zij spreken niet eer Hij het beveelt, en zij handelen volgens Zijn gebod.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,28,'Hij weet wat v��r hen is, en wat achter hen is, zij zijn voor niemand voorspraak behalve voor degene die Hem behaagt en zij sidderen uit eerbied voor Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,29,'En wie hunner zou zeggen: \"Ik ben een God naast Hem,\" die zouden Wij met de hel vergelden. Aldus vergelden Wij de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,30,'Hebben de ongelovigen niet ingezien dat de hemel en de aarde gesloten waren en dat Wij ze dan hebben geopend? En al hetgeen leeft, hebben Wij uit water gemaakt. Willen zij dan toch niet geloven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,31,'En Wij hebben op aarde onwrikbare bergen geplaatst, opdat zij niet met hen (de mensen) zouden beven; en Wij hebben er wijde wegen gemaakt, opdat zij de juiste richting zouden volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,32,'En Wij hebben de hemel gemaakt tot een welbeschermd dak; niettemin wenden zij zich af van deze tekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,33,'En Hij is het, Die de nacht en de dag schiep. Ook de zon en de maan, elk hunner beweegt zich langs een (vaste) baan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,34,'Wij hebben aan niemand v��r u een eeuwig leven geschonken. Indien gij sterft, zouden zij hier dan voor eeuwig kunnen blijven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,35,'Iedere ziel zal de dood ondergaan en Wij beproeven u met kwaad en goed en tot Ons zult gij terugkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,36,'Wanneer de ongelovigen u zien, spotten zij slechts met u, zij zeggen: \"Is dit degene die kwaad spreekt van uw Goden?\" terwijl zij het zijn die de verkondiging van de Barmhartige verwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,37,'De mens is met een haastige natuur geschapen. Ik zal u Mijn tekenen tonen, doch vraagt Mij niet ze te verhaasten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,38,'En zij zeggen: \"Wanneer zal deze belofte worden vervuld, indien gij waarachtig zijt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,39,'O, wisten de ongelovigen maar de tijd wanneer zij niet bij machte zullen zijn het Vuur van hun gezicht of van hun rug te weren en niet zullen worden geholpen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,40,'Neen, onverwachts zal het hen achterhalen en het zal hen verbijsteren; en zij zullen niet bij machte zijn het te voorkomen, noch zal hun uitstel worden gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,41,'Voorzeker werden de boodschappers v��r u ook bespot, maar degenen die hen bespotten, werden door het bespotte getroffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,42,'Zeg: \"Wie beschermt u dag en nacht behalve de Barmhartige?\" Neen, zij wenden zich af van de gedachtenis aan hun Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,43,'Hebben zij goden die hen kunnen beschermen tegen Ons? Zij kunnen zichzelf niet helpen, noch worden zij door Ons bijgestaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,44,'Neen, Wij hebben deze (mensen) en hun vaderen een voorziening gegeven totdat het leven hun verlengd werd. Zien zij met dat Wij het land (der ongelovigen) bezoeken, het van de buitenzijde af besnoeiend? Zullen zij dan de overhand hebben?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,45,'Zeg: \"Ik waarschuw u slechts door Openbaring.\" Doch de doven horen de roep niet wanneer zij worden gewaarschuwd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,46,'En indien een ademtocht der kastijding van uw Heer hen raakt, zullen zij ongetwijfeld zeggen: \"Wee ons, wij waren inderdaad onrechtvaardigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,47,'En Wij zullen weegschalen der gerechtigheid instellen op de Dag der Opstanding, zodat geen enkele ziel in enig opzicht onrecht zal worden aangedaan. En al was het slechts het gewicht van een mosterdzaadje, Wij zullen het naar voren brengen en Wij zijn voldoende als Rekenaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,48,'En Wij schonken Mozes en A�ron het Onderscheid, tot een licht en een gedachtenis voor de godvrezenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,49,'Die hun Heer in het verborgene vrezen en het Uur duchten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,50,'En dit (de Koran) is een gezegende verkondiging die Wij hebben nedergezonden: zult gij deze dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,51,'En voorheen schonken Wij aan Abraham zijn rechtschapenheid en Wij kenden hem goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,52,'Toen hij tot zijn vader en tot zijn volk zeide: \"Wat zijn deze beelden waaraan gij zo gehecht zijt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,53,'Antwoordden zij: \"Wij vonden dat onze vaderen deze aanbaden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,54,'Hij zeide: \"Voorwaar, gij met uw vaderen verkeert in duidelijke dwaling.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,55,'Zij zeiden: \"Hebt gij ons de waarheid gebracht, of speelt gij slechts met ons?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,56,'Hij antwoordde: \"Neen, uw Heer is de Heer van de hemelen en van de aarde, Die deze schiep en ik leg getuigenis er van af.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,57,'En, bij Allah, ik zal tegen uw afgoden een plan beramen nadat gij hun uw rug hebt toegewend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,58,'Alsdan brak hij ze in stukken, behalve de grootste daarvan, opdat zij zich tot hem zouden wenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,59,'(Toen zij dit zagen) zeiden zij: \"Wie heeft dit onze Goden aangedaan? Voorwaar, hij moet een boosdoener zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,60,'Enigen hunner zeiden: \"Wij hoorden een jonge man over hen spreken; hij heet Abraham.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,61,'Zij zeiden: \"Brengt hem dan voor de ogen des volks, opdat zij kunnen getuigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,62,'Zij vroegen: \"Hebt gij dit onze Goden aangedaan, o Abraham?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,63,'Hij antwoordde: \"Iemand heeft het gedaan; dit is de grootste van hen. Vraagt hen of zij kunnen spreken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,64,'Toen kwamen zij tot inkeer en zeiden (bij zichzelf) \"Gij zijt zelf de boosdoeners.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,65,'En zij lieten (beschaamd) het hoofd hangen, \"Gij weet wel dat deze niet kunnen spreken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,66,'Hij zeide: \"Aanbidt gij dan in plaats van Allah datgene wat u geenszins kan baten noch schaden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,67,'\"Schande over u en over hetgeen gij buiten Allah aanbidt. Hebt gij dan geen verstand?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,68,'Zij zeiden: \"Verbrandt hem en helpt uw goden indien gij iets wilt doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,69,'Wij zeiden: \"O vuur, wees koel en onschadelijk voor Abraham.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,70,'En zij wensten hem kwaad te doen doch Wij deden hen de grootste verliezers zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,71,'En Wij redden hem en Lot en voerden hen naar het land dat Wij zegenden voor alle volkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,72,'En Wij schonken hem Iza�k en Jacob als kleinzoon en Wij maakten hen allen rechtvaardig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,73,'En Wij maakten hen tot leiders die de mensen leidden op Ons bevel en Wij zonden een Openbaring tot hen, die aanspoorde, goede werken te doen, het gebed te onderhouden en aalmoezen te geven. En zij aanbaden Ons alleen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,74,'En aan Lot schonken Wij wijsheid en kennis. En Wij bevrijdden hem uit de stad die gruwelijk handelde. Zij waren inderdaad een boos en opstandig volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,75,'En Wij namen hem in Onze barmhartigheid op, want hij was een der rechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,76,'En toen Noach voordien riep, verhoorden Wij zijn gebed en redden hem en zijn gezin uit de grote ramp.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,77,'En Wij stonden hem bij tegen degenen die Onze tekenen verloochenden. Zij waren voorzeker een slecht volk; derhalve verdronken Wij hen allen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,78,'En toen David en Salomo rechtspraken betreffende het veld waar de geiten van zekere mensen bij nacht graasden, waren Wij Getuige van hun oordeel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,79,'Wij schonken Salomo begrip van de zaak en aan elk hunner schonken Wij wijsheid en kennis. En Wij noopten de bergen en de vogels om samen met David Gods heerlijkheid te loven. En Wij waren het, Die dat deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,80,'En Wij leerden hem de kunst, mali�nkolders voor u te maken, opdat deze u zouden beschermen tegen aanvallen. Zult gij dan niet erkentelijk zijn?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,81,'En Wij maakten de geweldige wind aan Salomo onderdanig. Deze blies om zijnentwille in de richting van het land dat Wij hadden gezegend. En Wij bezitten kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,82,'En Wij maakten onder de duivels, die voor hem doken en daarnaast andere arbeid verrichtten en Wij waren het die over hen waakten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,83,'En (gedenk) Job toen hij tot zijn Heer riep, zeggende: \"Kwelling heeft mij terneer geworpen en Gij zijt de Genadigste der genadigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,84,'Wij verhoorden daarom zijn gebed en bevrijdden hem van moeilijkheden en gaven hem de zijnen en het gelijke er van daarnevens, als een bewijs Onzer barmhartigheid en als een herinnering voor de vromen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,85,'En Isma�l en Idries en Zol-Kifl; allen behoorden tot de standvastigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,86,'En Wij namen hen op in Onze genade want zij behoorden tot de rechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,87,'En Zonnoen (Jonas) toen hij in toorn heenging en dacht dat Wij geen macht over hem hadden en in de duisternis uitriep, zeggende: \"Er is geen God dan Gij. Heilig zijt Gij. Ik behoorde inderdaad tot de onrechtvaardigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,88,'Wij verhoorden toen zijn gebed en namen zijn droefenis van hem weg. En aldus verlossen Wij de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,89,'En Zacharia, toen hij tot zijn Heer riep, zeggende: \"Mijn Heer, laat mij niet alleen en Gij zijt de Beste der erfgenamen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,90,'Toen verhoorden Wij zijn gebed en beloofden hem Johannes en Wij maakten zijn vrouw geschikt (een kind te krijgen). Zij plachten met elkander te wedijveren in goede werken en zij riepen Ons in hoop en vrees aan en waren nederig voor Ons.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,91,'En (gedenk) haar, die haar kuisheid bewaarde; Wij bliezen haar Onze geest in en Wij maakten haar en haar zoon tot een teken voor alle volkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,92,'Voorwaar, dit is uw gemeenschap: ��n gemeenschap; en Ik ben uw Heer, aanbidt Mij derhalve.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,93,'Doch zij (hun volgelingen) hebben hun eenheid verbroken; toch zullen zij allen tot Ons terugkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,94,'Wie ook goede werken verricht en een gelovige is, voor diens ijver zal geen ondankbaarheid zijn. Wij zullen dit voorzeker in zijn voordeel boekstaven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,95,'En voor een stad die Wij verdelgd hebben, is het een onherroepelijk gebod, dat het (volk) niet zal terugkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,96,'Zelfs wanneer Gog en Magog zullen worden losgelaten en zij elke hoogte zullen overschrijden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,97,'En als de ware Belofte nadert, dan ziet, de ogen der ongelovigen zullen verstard zijn. (Zij zullen zeggen): \"O! wee ons, wij waren hier inderdaad onachtzaam over, neen, wij waren onrechtvaardigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,98,'Voorwaar, gij met hetgeen gij buiten Allah aanbidt, zult de brandstof der hel zijn. Daartoe zult gij komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,99,'Indien dezen werkelijk Goden waren geweest zouden zij niet daarin zijn gegaan; nu zullen allen er in verblijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,100,'Daarin zullen zij weeklagen en niets horen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,101,'Voorzeker degenen, aan wie door Ons tevoren de belofte van een goede beloning is gedaan, dezen zullen er ver van verwijderd worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,102,'Geen gerucht daarvan (van de hel) zullen zij horen, en zij zullen voor eeuwig vertoeven in hetgeen hun zielen begeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,103,'De grote ontzetting zal hen niet beangstigen en de engelen zullen hen tegemoet komen, zeggende: \"Dit is de Dag die u was beloofd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,104,'De Dag, waarop Wij de hemelen zullen oprollen zoals een schrijver zijn geschriften oprolt. Gelijk Wij de schepping eerst begonnen, aldus zullen Wij haar terugbrengen - een Belofte van Ons; voorwaar Wij zullen deze nakomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,105,'En voordien hebben Wij na de Vermaning in de geschriften geschreven dat Mijn rechtvaardige dienaren de aarde zullen erven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,106,'Voorzeker, hierin ligt een boodschap voor mensen die God dienen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,107,'En Wij hebben u (Mohammed) slechts als genade voor de werelden gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,108,'Zeg: \"Voorzeker, mij is geopenbaard dat uw God slechts een enig God is, zult gij u dan aan Hem onderwerpen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,109,'Maar indien zij zich afwenden, zeg dan: \"Ik heb u allen gelijkelijk ingelicht en ik weet niet of hetgeen waar gij mee bedreigd wordt, nabij of ver is.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,110,'\"Voorwaar, Hij weet wat openlijk besproken wordt en Hij weet hetgeen gij verbergt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,111,'\"En ik weet niet of het voor u een beproeving is of een voordeel voor een bepaalde tijd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,21,112,'Zeg: \"Mijn Heer, oordeel in waarheid.\" En \"Onze Heer is de Barmhartige Wiens hulp moet worden ingeroepen tegen hetgeen gij zegt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,1,'O volk, vrees uw Heer, want de schok van het Uur is een verschrikkelijk iets.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,2,'De Dag waarop elke zogende vrouw haar zuigeling zal vergeten en elke zwangere vrouw zich zal ontdoen van haar dracht; en gij zult mensen bedwelmd zien, terwijl zij niet dronken zijn, doch de kastijding van uw Heer is gestreng.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,3,'En onder de mensen zijn er sommigen, die over Allah redetwisten zonder kennis en elke opstandige Satan volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,4,'Voor ieder die hem tot vriend neemt is verordend, dat hij hem zal verleiden en naar de straf van het Vuur voeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,5,'O mensen, indien gij in twijfel verkeert over de Opstanding, bedenkt, dat Wij u hebben geschapen uit stof, daarna uit een levenskiem, dan van een klonter bloed, daarna uit een klomp vlees, volkomen en onvolkomen in maaksel, opdat Wij het u duidelijk maken. En Wij laten wat Ons behaagt gedurende een vastgestelde tijd in de baarmoeder blijven, dan brengen Wij u als zuigelingen voort, dan (doen Wij u opgroeien) zodat gij volwassen wordt. En daar zijn er onder u die door de dood worden achterhaald en anderen die zulk een hoge ouderdom bereiken, dat zij, na geweten te hebben, niets meer weten. En gij ziet de aarde levenloos, doch wanneer Wij er regen op doen nederdalen, beweegt zij zich, zwelt op en brengt iedere mooie soort planten voort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,6,'Dit is zo omdat Allah de Waarheid is en omdat Hij het is Die de doden tot leven wekt en omdat Hij over alle dingen macht heeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,7,'Voorzeker het Uur nadert, daaraan is geen twijfel; Allah zal al degenen die in de graven zijn, opwekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,8,'En onder de mensen zijn er die over Allah redetwisten zonder kennis, richtsnoer of verlichtend Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,9,'Zich hooghartig afkerend ten einde anderen af te leiden van Allah´s weg. Voor hem is er schande in deze wereld en op de Dag der Verrijzenis zullen Wij hem de straf van het branden doen ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,10,'\"Dit is wegens hetgeen uw handen hebben vooruit gezonden; want Allah is niet onrechtvaardig jegens Zijn dienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,11,'En onder de mensen zijn er die Allah weifelend aanbidden. Indien het hun wel gaat, zijn zij daarmede tevreden, maar indien zo iemand een beproeving ten deel valt, keert hij terug tot zijn vroegere wandel. Hij verliest deze wereld zowel als het Hiernamaals. Dat is een duidelijk verlies.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,12,'Hij roept naast Allah datgene aan, wat hem schaden noch baten kan. Dat is een vergaande dwaling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,13,'Hij roept degene aan, die eerder schaadt dan baat. Voorwaar slecht is de beschermer en waarlijk slecht de metgezel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,14,'Voorwaar, Allah zal hen die geloven en goede werken verrichten, tuinen doen binnengaan waardoor rivieren stromen; Allah doet wat Hem behaagt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,15,'Laat hij die denkt dat Allah hem (de profeet) in deze wereld of in het Hiernamaals niet zal helpen, op de een of andere wijze ten hemel gaan en (Gods hulp) tegenhouden; laat hem dan zien of zijn plan datgene verwijderen kan, wat zijn toorn opwekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,16,'En aldus hebben Wij hem duidelijke tekenen gezonden, en Allah zal voorzeker leiden wie Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,17,'Voorzeker de gelovigen, de Joden, de Sabianen, de Christenen, de Magi�rs en de afgodendienaren, Allah zal tussen hen richten op de Dag der Opstanding, want Allah is Getuige over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,18,'Hebt gij dan niet gezien dat alles zich voor Allah nederwerpt, wat in de hemelen en op aarde is, de zon, de maan, de sterren, de bergen, de bomen, het vee en een groot deel der mensen; maar toch valt nog velen de kastijding ten deel. En die Allah vernedert, kan niemand verheffen. Voorwaar, Allah doet wat Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,19,'Hier zijn twee tegenstanders die redetwisten over hun Heer. Voor de ongelovigen zullen gewaden van Vuur worden gesneden en over hun hoofd zal kokend water worden uitgegoten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,20,'Waardoor hun ingewanden alsmede hun huiden zullen worden verteerd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,21,'En hen zullen ijzeren roeden wachten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,22,'Telkens wanneer zij er uit (uit de hel) wensen te gaan, zullen zij er in terug worden gedreven; men zal zeggen: \"Proeft gij de straf van het branden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,23,'Doch Allah zal degenen die geloven en goede werken verrichten tuinen doen binnentreden waardoor rivieren stromen. Zij zullen daarin worden getooid met armbanden van goud en parels, en hun gewaden zullen van zijde zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,24,'En zij zullen naar het reine woord en het pad van de Geprezene worden geleid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,25,'Voorzeker degenen die niet geloven en mensen afhouden van de weg van Allah en van de Heilige Moskee (te Mekka) - die Wij gelijk voor alle mensen hebben aangewezen, hetzij degene die er in (de stad) vertoeft of (de vreemdeling) die van buiten komt - en hij die in de Moskee onrechtvaardig naar goddeloosheid streeft - hem zullen Wij een pijnlijke straf doen ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,26,'En toen Wij Abraham de plaats voor het Huis (de Kaaba) aanwezen zeggende: \"Vereenzelvig niets met Mij, en houd Mijn Huis rein voor degenen die de rondgang verrichten en degenen die opstaan (voor gebed) en neerbuigen en zich ter aarde werpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,27,'En verkondig de bedevaart aan de mensen. Zij zullen te voet of op magere kamelen van verre tot u komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,28,'Opdat zij van hun voordeel getuigenis afleggen en de naam van Allah uitspreken gedurende de vastgestelde dagen over het vee waarvan Hij hen heeft voorzien. Eet dan daarvan en spijzigt de behoeftigen in nood.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,29,'Laat hen dan hun vuilheid verwijderen en hun geloften vervullen en een omgang maken om het oude Huis (Kaaba).\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,30,'Zo zij het. Wie dus de heilige geboden van Allah eert, het zal voor hem goed zijn in de ogen van zijn Heer. En wettig voor u is alle vee behalve hetgeen u anderszins is verkondigd. Vermijdt derhalve de onreinheid der afgodsbeelden en vermijdt het valse woord.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,31,'Oprecht zijnde voor Allah, niets met Hem vereenzelvigende. En wie iets met Allah vereenzelvigt, het is alsof hij van een hoogte valt en de vogels hem wegrukken of de wind hem wegblaast naar een afgelegen plaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,32,'Zo zij het. En wie de heilige tekenen van Allah vereert, voorwaar, dat is de oprechtheid des harten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,33,'Daar is in de offeranden een profijt voor u voor een vastgestelde tijd, daarna is hun plaats bij het oude Huis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,34,'En voor elk volk hebben Wij handelingen van offer en wijdingen vastgesteld, opdat zij de naam van Allah mogen uitspreken over het vee dat Hij hun heeft gegeven. Uw God is dus E�n God, weest daarom onderdanig aan Hem. En geef blijde tijding aan de ootmoedigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,35,'Wier harten vervuld zijn van vrees wanneer Allah´s naam wordt genoemd, en die geduldig dragen al hetgeen hun overkomt, die het gebed onderhouden, en geven van hetgeen Wij hun hebben toebedeeld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,36,'En onder de heilige tekenen van Allah hebben Wij voor u de offerkamelen aangewezen. In hen is er veel voordeel voor u. Spreekt daarom de naam van Allah over hen uit terwijl zij in rijen staan opgesteld. En wanneer ze op hun zij neervallen, eet er van en voedt de rijken en de armen. Aldus hebben Wij hen aan u dienstbaar gemaakt, opdat gij dankbaar moogt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,37,'Hun vlees noch hun bloed bereikt Allah, doch uw godsvrucht bereikt Hem. Aldus heeft Hij hen aan u dienstbaar gemaakt, opdat gij Allah moogt verheerlijken wegens hetgeen waartoe Hij u heeft geleid. En geef blijde tijding aan de goeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,38,'Voorwaar, Allah verdedigt de gelovigen. Voorzeker, Allah heeft niemand lief die oneerlijk, ondankbaar is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,39,'Toestemming om te vechten is gegeven aan degenen tegen wie gevochten wordt, omdat hun onrecht is aangedaan, voorzeker Allah heeft de macht hen bij te staan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,40,'Degenen die ten onrechte uit hun huizen werden verdreven alleen omdat zij zeiden: \"Onze Heer is Allah.\" - En indien Allah sommige mensen niet met behulp van anderen tegenhield, zouden ongetwijfeld kloosters, kerken, synagogen en moskee�n, waarin dikwijls de naam van Allah wordt herdacht, afgebroken zijn. Allah zal ongetwijfeld degene ondersteunen die Hem helpt - Allah is inderdaad Sterk, Almachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,41,'Degenen die, indien Wij hen op aarde vestigen, het gebed verrichten en de Zakaat betalen en het goede bevelen en het kwade verbieden. En het eindbesluit in alles berust bij Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,42,'Indien zij u (Mohammed) verloochenen, v��r hen heeft het volk van Noach en Aad en Samoed ook verloochend;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,43,'En het volk van Abraham en het volk van Lot;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,44,'En de inwoners van Midian eveneens. En Mozes werd ook verloochend. Maar Ik schonk de ongelovigen uitstel, daarna greep Ik hen, en hoe (groot) was toen Mijn afkeer!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,45,'Hoe menige stad hebben Wij verdelgd, terwijl deze vol ongerechtigheid was, zodat de daken er van zijn ingestort en hoe menige bron en opgetrokken paleis werd verlaten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,46,'Hebben zij dan niet in het land gereisd zodat zij hart moesten hebben waarmee zij konden begrijpen en oren om er mee te horen? Voorzeker, het zijn niet de ogen die blind zijn doch het hart in (hun) borst is blind.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,47,'En zij dringen bij u aan de straf te verhaasten, doch Allah zal nimmer Zijn Belofte breken. Voorwaar bij uw Heer is ��n dag gelijk duizend jaren van uw berekening.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,48,'Hoe vele steden heb Ik uitstel verleend, hoewel zij vol ongerechtigheden waren. Daarna greep Ik hen en tot Mij is de terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,49,'Zeg: \"O mensdom, ik ben slechts een duidelijke waarschuwer voor u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,50,'Degenen, die geloven en goede werken verrichten, voor hen is er vergiffenis en een eerzaam levensonderhoud.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,51,'Doch degenen die trachten Onze woorden krachteloos te maken, zullen de bewoners van het Vuur zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,52,'Nimmer zonden Wij een boodschapper of een profeet v��r u of, wanneer hij (zijn boodschap) verkondigde, kwam de duivel er tussen. Doch Allah doet hetgeen Satan inblaast te niet. Dan bevestigt Allah Zijn woorden en Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,53,'Zodat Hij hetgeen Satan inblaast tot een beproeving moge maken voor degenen in wier hart een ziekte is en wier hart verhard is - voorzeker, de onrechtvaardigen zijn in groot verzet -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,54,'En opdat degenen aan wie kennis is gegeven mogen weten dat het (de verkondiging) de waarheid is van uw Heer, opdat zij er in mogen geloven en hun hart nederig voor Hem moge worden. Waarlijk Allah leidt degenen die geloven naar het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,55,'En de ongelovigen zullen er over in twijfel blijven tot onverwachts het Uur hen achterhaalt, of de straf van een rampzalige Dag over hen komt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,56,'Op die Dag zal het koninkrijk van Allah zijn. Hij zal onder hen richten. Zij die geloven en goede werken verrichten, zullen in tuinen van zaligheid vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,57,'Doch die niet geloven en Onze tekenen verloochenen zullen een schandelijke straf ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,58,'Degenen die hun huizen verlaten terwille van Allah, en dan sneuvelen of sterven, voorwaar voor hen zal Allah een goede voorziening verschaffen. En voorzeker Allah is de Beste der Voorzieners.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,59,'Gewis zal Hij hen een plaats doen binnengaan waarmee zij zeer tevreden zullen zijn. Allah is inderdaad Alwetend, Verdraagzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,60,'Zo zal het zijn. En wie vergeldt in de mate waarin hem onrecht is aangedaan en men doet hem dan opnieuw onrecht, hem zal Allah voorzeker bijstaan. Waarlijk, Allah is Begenadigend, Vergevensgezind.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,61,'Dat is omdat Allah de nacht doet overgaan in de dag en de dag doet overgaan in de nacht, en omdat Allah Alhorend, Alziende is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,62,'Dat is omdat Allah de Waarheid is en hetgeen zij aanroepen nevens Hem vals is. Voorzeker Allah is de Hoge, de Grote.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,63,'Hebt gij niet gezien, dat Allah water uit de hemel nederzendt en de aarde daardoor groen wordt? Allah is inderdaad Aldoordringend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,64,'Aan Hem behoort al hetgeen in de hemelen en op aarde is. En Allah is inderdaad Zichzelf-genoeg, Geprezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,65,'Hebt gij niet gezien, dat Allah al hetgeen op aarde is in uw dienst heeft gesteld, en dat de schepen op Zijn bevel de zee�n doorkruisen? En Hij weerhoudt de hemel ervan op aarde te vallen behalve met Zijn toestemming. Waarlijk, Allah is Liefderijk en Genadevol voor de mensen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,66,'Hij is het, Die u leven schonk. Hij zal u doen sterven, daarna zal Hij u wederom tot leven opwekken. Waarlijk de mens is uiterst ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,67,'Voor elk volk hebben Wij wijdingen vastgesteld die zij moeten volgen; laat hen daarom niet met u er over redetwisten; doch nodigt hen tot uw Heer, waarlijk gij volgt de juiste leiding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,68,'Doch indien zij met u redetwisten, zeg dan: \"Allah weet het beste wat gij doet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,69,'\"Allah zal onder u richten op de Dag der Opstanding over datgene waarover gij van mening verschildet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,70,'Weet gij niet dat Allah al hetgeen in de hemelen en op aarde is, kent? Voorwaar dat is vastgesteld in een Boek, dat is gemakkelijk voor Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,71,'En zij aanbidden naast Allah, waartoe Hij geen machtiging heeft nedergezonden, en waaromtrent zij geen kennis bezitten. En voor degenen die kwaad bedrijven is er geen helper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,72,'En wanneer Onze duidelijke tekenen aan hen worden voorgedragen zult gij afkeuring bespeuren op het gezicht der ongelovigen. Bijna zouden zij degenen, die Onze tekenen aan hen verhalen, aanvallen. Zeg: \"Zal ik u over iets ergers dan dat inlichten? Het Vuur, Allah heeft het beloofd aan de ongelovigen. En dat is een slechte bestemming.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,73,'O mensen, een gelijkenis wordt gegeven, luistert er naar. Voorzeker, degenen die grij in plaats van Allah aanbidt kunnen zelfs geen vlieg scheppen, al zouden zij daar allen toe samenwerken. En indien een vlieg iets van hen zou wegnemen, zouden zij dat niet kunnen terugnemen. Zwak is zowel de zoeker als de gezochte.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,74,'Zij achten Allah niet met de achting die Hem verschuldigd is. Voorzeker, Allah is Sterk, Almachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,75,'Allah kiest boodschappers uit het midden der engelen, eveneens uit het midden der mensen. Voorzeker, Allah is Alhorend, Alziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,76,'Hij weet hetgeen v��r hen en hetgeen achter hen is en naar Allah worden alle zaken teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,77,'O, gij die gelooft, buigt u neder en werpt u ter aarde, en aanbidt uw Heer, en doet goed, opdat gij moogt slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,22,78,'En strijdt voor de zaak van Allah zoals er voor behoort te worden gestreden. Hij heeft u verkozen en heeft u in de godsdienst geen lasten opgelegd - dit is het geloof van uw vader Abraham. Hij heeft u Moslims genoemd voorheen en in dit Boek, opdat Onze boodschapper getuige over u zij, en dat gij getuige moogt zijn over de mensheid. Onderhoudt het gebed, betaalt de Zakaat en houdt u aan Allah vast. Hij is uw Beschermer. Een uitmuntend Meester en een uitnemend Helper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,1,'Inderdaad voorspoedig zijn de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,2,'Die ootmoedig zijn in hun gebeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,3,'En die al hetgeen ijdel is, schuwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,4,'En die aktief zijn in het betalen van Zakaat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,5,'En die hun vleselijke lusten beheersen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,6,'Behalve met hun vrouwen of hetgeen hun rechterhand bezit, want dan treft hen geen verwijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,7,'Doch degenen die deze perken te buiten gaan, zullen overtreders zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,8,'Zij die zorgzaam zijn voor het hun toevertrouwde en voor hun overeenkomsten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,9,'En die hun gebeden in acht nemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,10,'Dezen zijn de erfgenamen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,11,'Die het paradijs zullen erven. Zij zullen daarin vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,12,'Voorwaar, Wij scheppen de mens uit een uittreksel van klei;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,13,'Dan plaatsen Wij hem als een kleine levenskiem in een veilige plaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,14,'Vervolgens vormen Wij de levenskiem tot een klonter bloed; daarna vormen Wij het geronnen bloed tot een (vormeloze) klomp; dan vormen Wij beenderen uit deze (vormeloze) klomp; daarna bekleden Wij deze beenderen met vlees; vervolgens ontwikkelen Wij het tot een nieuwe schepping. Gezegend zij Allah, de Beste Schepper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,15,'Voorzeker daarna sterft gij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,16,'En op de Dag der Verrijzenis zult gij worden opgewekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,17,'En boven u hebben Wij zeven wegen gemaakt, en nimmer veronachtzamen Wij de schepping.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,18,'Wij zenden water uit de hemel neer in bepaalde hoeveelheid en Wij doen deze in de aarde blijven en voorzeker zijn Wij ook in staat die weer weg te nemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,19,'En Wij brengen daarmede tuinen van dadelpalmen en wijnstokken voor u voort, waarvan gij overvloedig fruit hebt; en gij eet daarvan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,20,'En Wij brengen een boom voort die groeit uit de berg Sina�: deze brengt olie en een saus voort voor hen die het willen nuttigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,21,'En in het vee is eveneens een les voor u. Wij geven u te drinken van de melk die in hun buik is en gij trekt er talrijke voordelen van en eveneens eet gij er van.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,22,'Daarop zowel als op schepen wordt gij gedragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,23,'En Wij zonden Noach tot zijn volk, en hij zeide: \"O mijn volk, dien Allah. Gij hebt geen andere God buiten Hem. Wilt gij dan niet vrezen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,24,'En de hoofden van zijn volk, die ongelovig waren, zeiden: \"Hij is slechts een mens zoals gij, hij zou zich boven u willen verheffen. En indien het Allah had behaagd, had Hij voorzeker engelen nedergezonden. Wij hebben nooit van zulk (een boodschapper) onder onze voorvaderen gehoord.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,25,'Hij is slechts een bezetene; wacht daarom een korte wijle, (ongetwijfeld zal hem iets overkomen).\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,26,'Noach zeide: \"O mijn Heer, help mij, want zij hebben mij verloochend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,27,'Toen openbaarden Wij hem: \"Bouw de Ark onder Onze ogen en in overeenstemming met Onze openbaring. En wanneer Ons bevel komt en de oppervlakte der aarde overstroomt, neem dan aan boord twee (exemplaren) van wat nodig is en uw gezin, behalve degenen tegen wie het woord reeds is uitgevaardigd. En spreek Mij niet over de onrechtvaardigen, want zij zullen worden verdronken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,28,'\"En wanneer gij de Ark zult hebben betrokken - gij en degenen die met u zijn zeg dan: \"Alle lof behoort aan Allah, Die ons van een boosaardig volk heeft gered.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,29,'En zeg: \"Mijn Heer, verleen mij een gezegende landing, want Gij zijt de Beste Landingshulp.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,30,'Voorwaar, hierin zijn tekenen, en waarlijk Wij stellen (de mensen) op de proef.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,31,'Toen verwekten Wij een ander geslacht na hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,32,'En Wij zonden onder hen een boodschapper uit hun midden, die zeide: \"Dient Allah, gij hebt geen andere God dan Hem. Wilt gij dan niet vrezen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,33,'En de hoofden van zijn volk, die ongelovig waren en die de ontmoeting in het Hiernamaals loochenden en wie Wij in dit leven overvloed (van het goede der aarde) hadden gegeven, zeiden: \"Dit is slechts een mens, zoals gij. Hij eet van hetgeengij eet en drinkt van hetgeen gij drinkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,34,'En indien gij een man gelijk aan uzelf gehoorzaamt dan zijt gij zeker verloren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,35,'Belooft hij u dat wanneer gij dood zijt en stof en beenderen zijt geworden, gij weder zult worden opgewekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,36,'Verre, verre is hetgeen u wordt beloofd!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,37,'Er is geen ander leven buiten ons tegenwoordige leven; wij leven en sterven en zullen niet worden opgewekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,38,'Hij is niet anders dan een mens die een leugen heeft verzonnen over Allah; wij zullen in hem stellig niet geloven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,39,'Hij zeide: \"Mijn Heer, help mij, want zij hebben mij verloochend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,40,'(God) zeide: \"Binnen korte tijd zullen zij zeker spijt krijgen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,41,'Terecht greep hen daarom de rukwind en Wij maakten hen als wrakhout. Vervloekt zij het onrechtvaardige volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,42,'Toen verwekten Wij na hen andere geslachten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,43,'Geen volk kan zijn vastgestelde tijd overschrijden, evenmin kunnen zij die uitstellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,44,'Dan zonden Wij Onze boodschappers de een na de ander. Telkens wanneer een Boodschapper tot een volk kwam, verloochenden zij hem. Dus deden Wij hen elkander opvolgen en maakten hen tot legenden. Vervloekt zij het volk dat niet wil geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,45,'Dan zonden Wij Mozes en zijn broeder A�ron met Onze tekenen en een duidelijk gezag');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,46,'Tot Pharao en zijn opperhoofden; zij toonden hoogmoed en waren een aanmatigend volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,47,'En zij zeiden: \"Moeten wij geloven in twee mannen aan ons gelijk terwigl hun volk onze slaaf is?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,48,'Derhalve verloochenden zij hen en zij behoorden tot degenen die vernietigd werden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,49,'En wij schonken Mozes het Boek opdat zij (de kinderen Isra�ls) leiding mochten volge.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,50,'En Wij bestemden de zoon van Maria en zijn moeder tot een teken en schonken hun toevlucht op een hoog plateau met groene weiden en bronnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,51,'O gij boodschappers, eet van hetgeen rein is en verricht goede werken. Voorwaar Ik weet goed wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,52,'En weet dat uw gemeenschap ��n gemeenschap is en dat Ik uw Heer ben. Neemt Mij derhalve tot uw Beschermer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,53,'Maar zij hebben hun godsdienst onder elkander verdeeld, elke partij verheugt zich over hetgeen zij bezit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,54,'Laat hen daarom voor een tijd aan hun onwetendheid over.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,55,'Denken zij dat vanwege de rijkdom en de zonen waarmee Wij hen helpen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,56,'Wij Ons haasten hun goed te doen? Neen, zij begrijpen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,57,'Voorwaar, degenen die sidderen van ontzag voor hun Heer,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,58,'En degenen die geloven in de tekenen van hun Heer,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,59,'En degenen die hun Heer geen deelgenoten toeschrijven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,60,'En degenen die weggeven hetgeen zij (kunnen) geven terwijl hun hart is vervuld van vrees. omdat zij tot hun Heer zullen terugkeren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,61,'Dezen zijn het die zich haasten en wedijveren in het doen van goede werken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,62,'Wij belasten geen ziel boven haar vermogen. Bij Ons is een boek, dat de waarheid spreekt en hun zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,63,'Maar hun hart is onverschillig jegens dit Boek, en buitendien hebben zij bezigheden waarmee zij voortgaan;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,64,'Totdat, wanneer Wij degenen hunner die in weelde leven met straf grijpen, ziet, dan jammeren zij allen om hulp.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,65,'Klaag niet op deze Dag, want gij zult door Ons niet worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,66,'Mijn woorden werden u verkondigd, doch gij placht u af te keren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,67,'Hovaardig, in dwaasheid er over pratende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,68,'Hebben zij dan niet over het Woord nagedacht, of is er iets tot hen gekomen dat niet tot hun voorvaderen kwam?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,69,'Of hebben zij hun boodschapper niet erkend dat zij hem niet aanvaarden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,70,'Of zeggen zij: \"Hij is krankzinng?\" Neen, hij heeft hun de Waarheid gebracht maar de meesten hunner houden niet van de Waarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,71,'En indien de Waarheid hun wensen had gevolgd, voorwaar dan zouden de hemelen en de aarde en al hetgeen daarin is, in wanorde zijn geraakt. Neen, Wij hebben hun een vermaning gezonden doch zij wenden zich ervan af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,72,'Of vraagt gij van hen enige beloning? Doch de beloning van uw Heer is beter en Hij is de beste Voorziener.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,73,'En gij roept hen, voorzeker, tot het rechte pad;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,74,'Maar degenen, die in het Hiernamaals niet geloven dwalen inderdaad van dit pad af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,75,'En indien Wij ons over hen ontfermden en hun kwaal verlichtten, zouden zij toch blindelings in hun overtreding volharden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,76,'Wij troffen hen door een straf, doch zij werden niet nederig voor hun Heer noch werden zij ootmoedig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,77,'Maar, wanneer Wij voor hen de poort der gestrenge straf openen, dan zullen zij tot wanhoop vervallen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,78,'(Allah) is het, Die oren, en ogen en hart voor u heeft geschapen, doch gij betoont weinig dank.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,79,'En Hij is het, Die u heeft vermenigvuldigd op aarde en tot Hem zult gij worden verzameld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,80,'En Hij is het, Die leven schenkt en de dood veroorzaakt en in Zijn handen is de wisseling van nacht en dag. Wilt gij dan niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,81,'Doch zij zeggen hetzelfde als de voormalige volkeren zeiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,82,'Zij zeggen: \"Zullen wij, wanneer wij dood zijn en tot beenderen en stof geworden, dan inderdaad weer worden opgewekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,83,'\"Dit werd ons en onze voorvaderen ook beloofd maar het zijn slechts fabelen der ouden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,84,'Zeg: \"Wie behoort de aarde toe en al hetgeen daarop is, als gij het weet?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,85,'Zij zullen zeggen: \"Aan Allah.\" Zeg: \"Wilt gij er dan geen lering uit trekken?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,86,'Zeg: \"Wie is de Heer der zeven hemelen en de Heer van de Grote Troon?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,87,'Zij zullen zeggen: \"Allah.\" Zeg: \"Wilt gij Hem dan niet tot uw Beschermer nemen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,88,'Zeg: \"Wie is het in Wiens hand de heerschappij over alle dingen is - en Die beschermt doch tegen Wie er geen bescherming is, - als gij het weet?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,89,'Zij zullen antwoorden: \"Dit behoort aan Allah.\" Zeg: \"Waarom wordt gij dan misleid?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,90,'Wij hebben hun de Waarheid gebracht en zij zijn zeker leugenaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,91,'Allah heeft zich geen zoon genomen, noch is er enige God naast Hem, anders zou elke God hetgeen Hij schiep, voor zich houden, en sommigen hunner zouden zeker anderen hebben overwonnen. Verheven is Allah boven al hetgeen zij beweren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,92,'Kenner van het ongeziene en het geziene. Hij is verheven boven hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,93,'Zeg: \"Mijn Heer, indien Gij mij datgene zoudt laten zien waarmee zij bedreigd worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,94,'Mijn Heer, plaats mij dan niet te midden van het onrechtvaardige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,95,'En voorzeker, Wij hebben de macht u datgene te laten zien waarmee Wij hen bedreigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,96,'Verdrijf het kwade met het beste. Wij zijn op de hoogte van hetgeen zij zeggen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,97,'En zeg: \"Mijn Heer, bij U zoek ik mijn toevlucht tegen de inblazingen der duivelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,98,'En bij U mijn Heer zoek ik mijn toevlucht, opdat zij niet bij mij komen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,99,'Wanneer de dood tot een hunner komt, zegt deze smekend: \"Mijn Heer, zend mij terug.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,100,'Opdat ik recht doe in hetgeen ik heb achtergelaten.\" (Dan wordt er gezegd): \"In geen geval; het is slechts een woord dat hij uit.\" En achter hen is een hindernis tot de Dag waarop zij gewekt zullen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,101,'En wanneer de bazuin wordt geblazen zal er die Dag geen verwantschap tussen hen bestaan, noch zal de een naar de ander vragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,102,'Dan zullen zij slagen, wier schalen zwaar zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,103,'Doch zij, wier werken licht zijn - dit zijn degenen die hun ziel benadeelden - zullen in de hel vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,104,'Het Vuur zal hun gezicht branden en zij zullen er in verschrompelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,105,'(Er zal gezegd worden): Werden Mijn woorden U niet verkondigd? Maar gij placht ze te verloochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,106,'Zij zullen antwoorden: \"O, onze Heer onze tegenspoed heeft ons overweldigd en wij waren een dwalend volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,107,'Onze Heer, neem ons daaruit; indien wij in het (kwade) terugvallen dan zijn wij stellig onrechtvaardig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,108,'Hij zal zeggen: \"Blijft daarin vernederd en spreekt niet tot Mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,109,'Waarlijk, er was een gedeelte van Mijn dienaren dat placht te zeggen: \"O, onze Heer, wij hebben geloofd, vergeef ons daarom en wees Barmhartig jegens ons. En Gij zijt de Beste der barmhartigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,110,'Maar gij maaktet hen ten spot totdat dezen u Mijn gedachtenis deden vergeten omdat gij hen placht uit te lachen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,111,'Inderdaad heb Ik hen heden beloond wegens hun geduld. Voorzeker, zij zijn de overwinnaars.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,112,'Hij (God) zal vragen: \"Hoeveel jaren zijt gij op de aarde gebleven?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,113,'Zij zullen antwoorden: \"Wij bleven een dag of een deel van een dag. Vraag dus degenen die rekening houden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,114,'Hij (Allah) zal zeggen: \"Gij bleeft een korte tijd, hadt gij het maar geweten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,115,'Dacht gij, dat Wij u tevergeefs schiepen en dat gij niet tot Ons zult worden teruggebracht?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,116,'Verheven is Allah, de ware Koning. Er is geen God behalve Hij, de Heer van de aanzienlijke Troon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,117,'En diegene die naast Allah een andere god aanroept heeft daar geen bewijs voor: en de vergelding ervan berust bij zijn Heer. Voorzeker de ongelovigen slagen nooit!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,23,118,'En zeg: \"O mijn Heer, vergeef en wees Barmhartig, want Gij zijt de Barmhartigste der barmhartigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,1,'Dit is een hoofdstuk, dat Wij hebben geopenbaard en verplichtend gesteld en Wij hebben er duidelijke tekenen in nedergezonden opdat gij er lering uit moogt trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,2,'Geselt iedere echtbreekster en echtbreker met honderd slagen. En laat medelijden met hen u van de gehoorzaamheid aan Allah niet afhouden indien gij in Allah en de Laatste Dag gelooft. En laat een groep gelovigen getuige zijn van hun bestraffing.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,3,'De echtbreker zal alleen een echtbreekster of een afgodendienares huwen, en met de echtbreekster zal alleen een echtbreker of een afgodendienaar huwen. En dit is de gelovigen verboden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,4,'En zij, die kuise vrouwen beschuldigen en geen vier getuigen brengen - geselt hen met tachtig slagen en aanvaardt hun getuigenis nooit meer, want dezen zijn overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,5,'Met uitzondering van hen die daarna berouw tonen en zich verbeteren; waarlijk, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,6,'En betreffende degenen die hun vrouwen beschuldigen en die buiten zich geen getuigen hebben, - laat ieder hunner vier maal in de naam van Allah zweren dat hij voorzeker de waarheid spreekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,7,'En de vijfde maal zal hij zeggen: dat Allah´s vloek op hem ruste als hij tot de leugenaars behoort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,8,'Maar het zal de straf van haar afwenden indien zij viermaal in de naam van Allah getuigt en zegt, dat hij tot de leugenaars behoort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,9,'En de vijfde maal zal zij zeggen: dat de toorn van Allah over haar zij als hij (haar man) de waarheid spreekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,10,'En ware het niet door Allah´s genade en Zijn barmhartigheid voor u (dan waart gij verloren gegaan). Voorzeker Allah is Berouwaanvaardend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,11,'Waarlijk, zij die de lastering voortbrachten waren een grote groep uit uw midden; beschouwt dit niet als een kwaad voor u - integendeel het is goed voor u. Elk hunner zal de straf voor de zonde die hij heeft begaan, ontvangen en hij, die onder hen het voornaamste deel ervan op zich nam zal een grotere straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,12,'Waarom dachten de gelovige mannen en vrouwen, toen zij dit hoorden geen goed over hun eigen mensen en zeiden: \"Dit is een openlijke lastering?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,13,'Waarom brachten zij geen vier getuigen (om dit te bewijzen)? Daar zij geen getuigen hebben medegebracht zijn zij in de ogen van Allah leugenaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,14,'En ware het niet door Allah´s genade en Zijn barmhartigheid jegens u in deze wereld en in het Hiernamaals, zo zou u wegens hetgeen gij hebt begaan, een grote straf hebben getroffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,15,'Toen gij het van elkander hoordet en gij zeidet, waarvan gij geen kennis bezat, dacht gij dat het onbeduidend was, terwijl het in de ogen van Allah belangrijk was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,16,'Waarom hebt gij niet gezegd toen gij het hoordet: \"Het betaamt ons niet om erover te spreken. Heilig zijt Gij, dit is een grote lastering!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,17,'Allah waarschuwt u om nooit tot iets dergelijks terug te vallen indien gij gelovigen zijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,18,'En Allah legt u de geboden uit; Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,19,'Zij die graag willen dat onbetamelijkheid zich onder de gelovigen moge verspreiden, zullen in deze wereld en in het Hiernamaals een pijnlijke straf ondergaan. Allah weet, en gij weet niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,20,'En ware het niet door Allah´s genade en Zijn barmhartigheid voor u en dat Allah Liefderijk en Genadevol is, (dan zoudt gij verloren zijn gegaan).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,21,'O gij die gelooft, volgt de voetstappen van Satan niet. Wie de voetstappen van Satan volgt die zal hij zeker onzedelijkheid en boosheid beyelen. En ware het niet door Allah´s genade en Zijn barmhartigheid voor u geweest, dan zou niemand uwer ooit rein zijn geworden, maar Allah reinigt wie Hij wil. Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,22,'En laat hen, die rijkdommen en overvloed onder u bezitten niet ophouden te geven aan verwanten en behoeftigen en hun die hun huizen terwille van Allah hebben verlaten. Laten zij vergeven en over het hoofd zien. Wenst gij niet dat Allah u zou vergeven? Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,23,'Zij die kuise, gelovige vrouwen, die geen kwaad kennen, belasteren, zijn in deze wereld en in het Hiernamaals vervloekt. Voor hen is er een grote kastijding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,24,'Op de Dag waarop hun tong, hun handen en hun voeten tegen hen zullen getuigen over hetgeen zij hebben bedreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,25,'Op die Dag zal Allah hun de hun toekomende vergelding ten volle geven, en zij zullen weten dat alleen Allah de duidelijke Waarheid is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,26,'Slechte vrouwen zijn voor de slechte mannen, en de slechte mannen zijn voor de slechte vrouwen. En goede vrouwen zijn voor de goede mannen en de goede mannen zijn voor de goede vrouwen, dezen hebben niets uitstaande met hetgeen anderen zeggen. Er is voor hen vergiffenis en een eerbare voorziening.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,27,'O gij die gelooft, gaat geen andere huizen dan de uwe binnen zonder de bewoners er van te waarschuwen en te begroeten. Dat is beter voor u, opdat gij er lering uit zult trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,28,'En indien gij niemand daarin vindt, gaat ze niet binnen voordat gij toestemming krijgt. En als er tot u wordt gezegd: \"Gaat terug,\" gaat dan terug want dit is voegzamer voor u. Allah is op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,29,'Het is voor u geen zonde indien gij onbewoonde huizen, waarin uw goederen staan, binnengaat. Allah weet wat gij openlijk doet en wat gij verbergt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,30,'Zeg tot de gelovige mannen dat zij hun ogen neergeslagen houden en dat zij hun passies beheersen. Dat is reiner voor hen. Voorzeker, Allah is wel op de hoogte van hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,31,'En zeg tot de gelovige vrouwen dat zij ook haar ogen neergeslagen houden en hun passies beheersen, en dat zij haar schoonheid niet tonen dan hetgeen ervan zichtbaar moet zijn, en dat zij haar hoofddoeken over haar boezem laten hangen, en dat zij haar schoonheid niet tonen behalve aan haar echtgenoot of haar vader of de vader van haar echtgenoot, of haar zonen of de zonen van haar echtgenoot, of haar broeders, of de zonen van haar broeders, of de zonen van haar zusters of haar vrouwen, of haar slaven, of zulke mannelijke bedienden die geen geslachtsdrang hebben, of de jonge kinderen die van de naaktheid van een vrouw niets afweten. En laat haar niet met haar voeten slaan, opdat hetgeen zij van haar schoonheid bedekken openbaar moge worden. En wendt u allen tezamen tot Allah, o gelovigen, opdat gij moogt slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,32,'En huwt uw weduwen en de deugdzamen onder uw mannelijke of vrouwelijke slaven. Indien zij arm zijn, zal Allah hen uit Zijn overvloed verrijken, want Allah is milddadig, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,33,'En laat degenen, die geen mogelijkheid tot trouwen vinden, zich kuis houden totdat Allah hen uit Zijn overvloed verrijkt. En de slaven die een acte van vrijmaking wensen, voorziet hen daarvan indien gij enig goed in hen ziet; en geeft hun van de rijkdommen van Allah, die Hij u heeft geschonken. En dwingt uw slavinnen, terwijl zij kuis wensen te zijn, niet tot ontucht om de goederen van het tegenwoordige leven te zoeken. Maar indien iemand haar dwingt, dan zal Allah na die dwang (voor haar) Vergevensgezind, Barmhartig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,34,'En Wij hebben duidelijke tekenen tot u nedergezonden, en de voorbeelden van hen die v��r u zijn geweest en een raadgeving voor de godvrezenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,35,'Allah is het Licht van de hemelen en de aarde. De gelijkenis van Zijn Licht is als een nis waarin een lamp staat. De lamp is door een glas omsloten; het glas is als een schitterende ster. Het wordt, aangestoken met olie van een gezegende boom, een olijfboom, die van het Oosten noch van het Westen is, welks olie bijna zou lichten, zelfs al raakte vuur haar niet. Licht op Licht. Allah leidt tot Zijn Licht wie Hij wil. - Allah geeft gelijkenissen voor de mensen; Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,36,'In huizen waaromtrent Allah heeft verordent dat zij zullen verrijzen, waarin Zijn naam zal worden herdacht, daarin verheerlijken Hem ´s morgens en ´s avonds,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,37,'Mensen die noch door handel noch door zaken achteloos worden om Allah te gedenken, het gebed te houden en de Zakaat te betalen, zij vrezen de Dag waarop harten en ogen zich zullen afwenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,38,'Opdat Allah hen voor de beste hunner daden moge belonen en hun ruim moge bedelen uit Zijn overvloed. Allah geeft zonder maat aan wie Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,39,'Maar de daden der ongelovigen zijn als een luchtspiegeling op een vlakte. De dorstige denkt dat het water is, wanneer hij er bij komt ontdekt hij echter dat het niets is. Maar hij vindt Allah in zijn nabijheid, Die hem zijn rekening ten volle vereffent; en Allah is snel in het afrekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,40,'Of als duisternis in een diepe zee, bedekt door golf boven golf waarboven wolken zijn: duisternis boven duisternis. Wanneer men zijn hand uitstrekt kan men haar bijna niet zien; en hij, wie Allah geen licht geeft, voor hem is er geen licht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,41,'Ziet gij niet, dat alles in de hemelen en op aarde, ook de vogels met hun uitgespreide vleugels Allah verheerlijken? Een ieder kent zijn eigen bidden en lofzang. En Allah weet goed wat zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,42,'Aan Allah behoort het koninkrijk der hemelen en der aarde en tot Allah is de terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,43,'Hebt gij niet gezien dat Allah de wolken voortdrijft, ze dan verzamelt en daarna ophoopt zodat gij regen uit hun midden ziet voortkomen? En Hij zendt van de hemel neder (wolken als) bergen waarin zich hagel bevindt en Hij treft daarmee wie Hij wil en wendt het af van wie Hij wil. De glans van de bliksem neemt het gezicht bijna weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,44,'Allah wisselt dag en nacht af. Daarin is zeker een les voor degenen die ogen hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,45,'En Allah heeft elk dier uit water geschapen. Sommigen hiervan gaan op hun buik, anderen op twee poten en nog anderen op vier poten. Allah schept wat Hij wil. Voorzeker Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,46,'Wij hebben duidelijke tekenen nedergezonden. En Allah leidt naar het rechte pad wie Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,47,'En zii zeggen: \"Wij geloven in Allah en in de boodschapper en wij gehoorzamen.\" Maar daarna wenden sommigen hunner zich af. En dezen zijn geen gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,48,'En wanneer zij tot Allah en Zijn boodschapper worden geroepen opdat hij over hen moge recht spreken, ziet! een deel hunner wendt zich af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,49,'Maar indien het recht aan hun zijde was, dan zouden zij ijlings naar hem toe komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,50,'Is er een ziekte in hun hart? Of twijfelen zij, of vrezen zij dat Allah en Zijn boodschapper onrechtvaardig jegens hen zullen zijn? Neen, zij zijn zelf de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,51,'Wanneer de gelovigen tot Allah en Zijn boodschapper worden geroepen opdat Hij over hen moge recht spreken, zeggen zij slechts: \"Wij horen en wij gehoorzamen.\" Dezen zijn het die zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,52,'En wie Allah en Zijn boodschapper gehoorzaamt en Allah vreest en godvruchtig is jegens Hem, dezulken zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,53,'En zij zweren hun plechtigste eden bij Allah; dat zij indien gij hen beveelt, zeker zullen oprukken. Zeg: \"Zweert niet: (maar) werkelijke gehoorzaamheid (is nodig).\" Voorzeker Allah is goed op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,54,'Zeg: \"Gehoorzaamt Allah en gehoorzaamt de boodschapper. Maar indien gij u afwendt is hij slechts verantwoordelijk voor datgene waarmee hij is belast, en gij zijt slechts verantwoordelijk voor datgene waarmee gij zijt belast. En indien gij hem gehoorzaamt, zult gij geleid worden. En de plicht van de boodschapper is slechts de duidelijke verkondiging.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,55,'Allah heeft aan degenen onder u die geloven en goede werken verrichten beloofd, dat Hij hen voorzeker tot stedehouders op aarde zal stellen, zoals Hij degenen die v��r hen waren tot stedehouders maakte en dat Hij de godsdienst, die Hij voor hen heeft gekozen, zeker zal bevestigen, en dat Hij hun na hun vrees, vrede en veiligheid zal geven; Mij zullen zij aanbidden en niets met Mij vereenzelvigen. Maar wie daarna het geloof verwerpen, zullen overtreders zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,56,'En houdt het gebed en betaalt de Zakaat en gehoorzaamt de boodschapper, opdat gij barmhartigheid moogt ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,57,'Denkt niet, dat degenen die niet geloven, op aarde kunnen ontsnappen, hun tehuis is de hel, en deze is inderdaad een slechte toevlucht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,58,'O gij die gelooft, laten uw slaven en degenen uwer die de geslachtsrijpheid nog niet hebben bereikt driemaal uw toestemming vragen (bij u te mogen komen), v��r het morgengebed, wanneer gij wegens de middaghitte u van uw klederen ontdoet, en na het avondgebed. Drie priv�-tijden voor u. Op andere tijden is het noch voor u, noch voor hen hinderlijk, want sommigen uwer moeten met anderen omgaan; aldus maakt Allah u de geboden duidelijk; Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,59,'En wanneer de kinderen onder u geslachtsrijpheid bereiken, moeten ook zij verlof vragen evenals ouderen dan zij om toestemming vragen. Zo maakt Allah u Zijn geboden duidelijk; Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,60,'Bejaarde vrouwen die geen hoop op het huwelijk koesteren - op haar rust geen schuld als zij zonder haar schoonheid te tonen, zich van kledingstukken ontdoen. Maar als zij zich inhouden is dit beter voor haar. Allah is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,61,'Het doet de blinden, de lammen, de zieken of uzelven geen kwaad, dat gij in uw eigen huizen eet of in de huizen van uw broeders, of in de huizen van uw zusters, of in de huizen van uw vaders broeders, of in de huizen van uw vaders zusters, of in de huizen van uw moeders broeders, of in de huizen van uw moeders zusters, of in dat huis waarvan gij de sleutel in uw bezit hebt, of in het huis van een uwer vrienden. Het doet u geen kwaad of gij tezamen of afzonderlijk eet. Wanneer gij de huizen betreedt groet dan elkander met een groet van uw Heer, die vol van zegen en reinheid is. Zo maakt Allah u de geboden duidelijk, opdat gij het moogt begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,62,'Zij alleen zijn gelovigen die in Allah en Zijn boodschapper geloven, en die, wanneer zij wegens iets dat voor allen belangrijk is, bij hem (de profeet) zijn, zich niet verwijderen voordat zij hem om toestemming hebben gevraagd. Zij die u om verlof vragen zijn degenen die werkelijk in Allah en Zijn boodschapper geloven. Wanneer zij daarom uw toestemming vragen terwille van hun zaken, geef dan toestemming aan wie hunner gij wilt en vraag voor hen vergiffenis van Allah, voorzeker, Allah is Vergevensgezind, Barmhartig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,63,'Behandelt de uitnodiging van de boodschapper onder u niet zoals gij de uitnodiging van elkander behandelt. Allah kent degenen uwer die wegsluipen en zich verbergen. Laat daarom degenen die tegen Zijn gebod ingaan, zich in acht nemen opdat hen geen rampspoed overkome of een pijnlijke straf hen achterhale.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,24,64,'Luistert! aan Allah behoort wat in de hemelen en op aarde is. Hij kent uw toestand goed. En de Dag waarop zij tot Hem zullen worden teruggebracht, zal Hij hen onderrichten over hetgeen zij deden. Waarlijk Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,1,'Gezegend is Hij, die de Forqaan (het onderscheid) aan Zijn dienaar heeft neder gezonden, opdat hij een waarschuwer moge zijn voor alle volkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,2,'Aan Wie het Koninkrijk der hemelen en der aarde toebehoort, Hij heeft zich geen zoon genomen noch heeft Hij een mededinger in Zijn Koninkrijk, Hij heeft alles geschapen, en het de juiste maat gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,3,'Toch hebben zij (de mensen) naast Hem goden genomen die niets kunnen scheppen, doch zelf geschapen zijn, en die geen macht hebben om zichzelf goed of kwaad te doen, noch macht hebben over dood, leven of opstanding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,4,'De ongelovigen zeggen: \"Dit is niets dan een leugen, die hij (de Profeet) verzonnen heeft en andere mensen hebben hem er bij geholpen.\" Maar zij uiten daarmee onrechtvaardigheid en leugen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,5,'En zij zeggen: \"Dit zijn fabelen der ouden; hij heeft ze laten neerschrijven en zij worden hem ´s morgens en ´s avonds voorgezegd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,6,'Zeg: \"Hij, Die de geheimen der hemelen en der aarde kent, heeft het nedergezonden. Waarlijk, Hij is Vergevensgezind, Genadevol.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,7,'En zij zeggen: \"Wat voor boodschapper is deze die voedsel gebruikt en op straat wandelt? Waarom is geen engel tot hem nedergezonden om met hem een waarschuwer te zijn?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,8,'Ofwel een schat had tot hem nedergeworpen moeten worden of hij had een tuin moeten hebben om (de vruchten) er van te eten.\" En de onrechtvaardigen zeggen: \"Gij volgt slechts een betoverd man!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,9,'Zie, wat voor verhalen zij over u doen, zij zijn verdwaald en kunnen de rechte weg niet vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,10,'Gezegend is Hij Die, indien Hij het wil u iets beters dan dat alles zal schenken - tuinen, waar doorheen rivieren stromen, en ook paleizen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,11,'Neen, zij verloochenen het Uur en voor degenen die dat Uur verloochenen hebben Wij een laaiend Vuur bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,12,'Wanneer de hel hen vanuit de verte zal zien, zullen zij het woeden en razen horen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,13,'En wanneer zij, aan elkander geketend, op een kleine ruimte daarvan zullen worden geworpen, zullen zij daar om vernietiging roepen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,14,'\"Roept niet ��nmaal om vernietiging doch roept er keer op keer om.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,15,'Zeg: \"Is dit beter of de tuin der eeuwigheid die de rechtvaardigen is beloofd? Deze zal hun loon en hun toevlucht zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,16,'Zij zullen er alles in ontvangen waar zij naar verlangen en zij zullen er blijvend vertoeven. Dit is een belofte van uw Heer waar om gevraagd mag worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,17,'En de Dag waarop Hij de ongelovigen en degenen die zij naast Allah aanbidden zal verzamelen, zal Hij vragen: \"Waart gij het, die deze mijn dienaren deedt dwalen, of dwaalden zij zelf van het rechte pad af?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,18,'Zij zullen antwoorden: \"Ere zij U; Het betaamde ons niet andere beschermers dan U te nemen, maar Gij hebt hen en hun vaderen doen genieten totdat zij de aanmaning vergaten en een verloren volk werden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,19,'Zo hebben dezen wat gij zegt, verloochend, zodat gij de straf niet kunt afwenden of hen helpen. En wie onder u onrechtvaardig is, Wij zullen hem een zware straf doen ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,20,'En Wij zonden nooit boodschappers v��r u, of zij gebruikten voedsel en liepen op straat. En Wij gebruiken sommigen uwer tot een beproeving voor anderen. Zult gij geduldig zijn? Want uw Heer is Alziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,21,'Zij die Onze ontmoeting niet verwachten zeggen: \"Waarom zijn geen engelen tot ons nedergezonden? of waarom kunnen wij onze Heer niet zien?\" Voorzeker, zij schatten zich te hoog en zijn de perken ver te buiten gegaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,22,'De Dag waarop zij de engelen zullen zien, zal er geen goed nieuws zijn voor de schuldigen; dan zullen zij zeggen: \"Ware er slechts een grote afscheiding (tussen ons).\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,23,'En Wij zullen ons tot hun werken wenden en zullen deze als stof verstrooien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,24,'De bewoners van het paradijs zullen op die Dag er beter aan toe zijn betreffende het tehuis, en beter betreffende de rustplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,25,'En de Dag waarop de hemel met de wolken zal worden gespleten en de engelen worden nedergezonden in grote aantallen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,26,'Het ware Koninkrijk zal op die Dag aan de Genadevolle behoren, maar het zal voor de ongelovigeneen moeilijke Dag zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,27,'De Dag waarop de onrechtvaardige op zijn handen zal bijten zal hij zeggen: \"O, had ik de weg met de boodschapper maar gevolgd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,28,'O. wee! Had ik nooit zo iemand als vriend genomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,29,'Hij deed mij van de herinnering afdwalen nadat zij tot mij was gekomen.\" En Satan laat de mens in de steek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,30,'En de boodschapper zal zeggen: \"O, mijn Heer, mijn volk heeft deze Koran verzaakt!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,31,'Zo maken Wij voor elke profeet een vijand van onder de zondaren; uw Heer is voldoende als Leider en Helper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,32,'En de ongelovigen zeggen: \"Waarom werd de Koran niet ineens aan hem geopenbaard?\" Zo is het, opdat Wij daarmee uw hart mogen versterken. En Wij hebben hem duidelijk en geleidelijk uiteengezet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,33,'En zij stellen u geen vraag of Wij geven u de waarheid en een uitmuntende uitleg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,34,'Zij die vernederd naar de hel zullen worden gebracht, verkeren in een slechte toestand, en zij zijn het meest van het rechte pad afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,35,'Wij gaven Mozes het Boek (der Wet) en stelden zijn broeder A�ron tot helper aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,36,'En Wij zeiden: \"Gaat samen naar het volk dat Onze Tekenen verloochent.\" Daarna vernietigden Wij hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,37,'En het volk van Noach: toen dit de boodschappers verloochende, verdronken Wij het en Wij maakten het tot een teken voor het mensdom. En Wij hebben een pijnlijke straf voor de onrechtvaardigen bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,38,'En herinnert u Aad en Samoed en het volk van de Bron en vele andere geslachten tussen hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,39,'Wij gaven aan ieder hunner allerlei voorbeelden en Wij vernietigden allen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,40,'En zij komen voorzeker de stad voorbij, waarop een boze regen was gevallen. Zien zij die (plaats) dan niet? Neen, zij verwachten de Opstanding niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,41,'Wanneer zij u zien maken zij u slechts tot een bespotting. \"Is hij het, die Allah als boodschapper heeft gezonden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,42,'Hij had ons inderdaad bijna van onze Goden doen afdwalen, als wij jegens hen niet standvastig waren gebleven.\" Maar zij zullen weldra te weten komen, wanneer zij de straf zullen aanschouwen, wie het meest afgedwaald is van het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,43,'Hebt gij hem gezien, die zijn eigen begeerte als zijn God aanneemt? Wilt gij dan een beschermer over hem zijn?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,44,'Denkt gij dat de meesten hunner horen of begrijpen? Zij zijn slechts als vee - neen, zij zijn verder afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,45,'Hebt gij niet gezien hoe uw Heer de schaduw verlengt? - En indien Hij het had gewild, kon Hij haar onbeweeglijk hebben gemaakt - Dan hebben Wij de zon tot een leider er van gemaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,46,'Daarna trekken Wij haar langzaam tot Ons terug.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,47,'En Hij is het, Die de nacht tot een bedekking voor u heeft gemaakt en de slaap voor rust, en de dag voor het opstaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,48,'En Hij is het, Die de winden als blijde aankondiging voor Zijn barmhartigheid uitzendt en Wij zenden zuiver water uit de wolken neer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,49,'Opdat Wij daarmee leven mogen schenken aan een dor land, en het ook als drank geven aan Onze schepping - aan vee en mensen in grote getale.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,50,'En Wij herhalen dit voor hen opdat zij er lering uit mogen trekken, maar de meeste mensen weigeren alles, behalve ondankbaarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,51,'Als Wij het hadden gewild konden Wij zeker in elke stad een waarschuwer hebben verwekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,52,'Dus volg de ongelovigen niet, en voer met (de Koran) een grote strijd tegen hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,53,'En Hij is het die twee wateren heeft doen stromen, het ene zoet en het andere zout, en tussen hen heeft Hij een afscheiding en een versperring geplaatst.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,54,'En Hij is het Die de mens uit water heeft geschapen en heeft hem verwanten gegeven door afstamming en huwelijk; uw Heer is Almachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,55,'Toch aanbidden zij naast Allah datgene dat hen helpen noch schaden kan. De ongelovige is een helper tegen zijn Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,56,'En Wij hebben u slechts als drager van blijde tijdingen en als waarschuwer gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,57,'Zeg: \"Ik vraag van u geen vergoeding er voor, behalve dat hij, die dit wil, de weg naar zijn Heer moge inslaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,58,'En stel uw vertrouwen in de Levende, Die niet sterft, en verheerlijk Hem met de lof die Hem toekomt. Hij is goed op de hoogte met de zonden van Zijn dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,59,'Hij, Die de hemelen en de aarde en alles wat er tussen is, in zes dagen schiep, zette Zich dan op de Troon. Hij is de Barmhartige. Vraag dus iemand die meer over Hem weet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,60,'En wanneer er tot de ongelovigen wordt gezegd: \"Werpt u neder voor de Barmhartige,\" zeggen zij: \"En wie is de Barmhartige? Zullen wij ons nederwerpen voor degene die gij ons gelast?\" En dit vermeerdert slechts hun afkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,61,'Gezegend is Hij, Die de sterren, de stralende zon en de glanzende maan aan de hemel heeft geplaatst.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,62,'En Hij is het Die de nacht en de dag heeft ingesteld die elkander opvolgen; dit is voor hen die er lering uit willen trekken, of hun dankbaarheid betonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,63,'En de dienaren van de Barmhartige zijn zij, die zachtmoedig op aarde wandelen en als de onwetenden hen aanspreken, zeggen zij: \"Vrede\".');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,64,'En zij, die de nacht doorbrengen zich voor hun Heer ter aarde werpende en voor Hem staande.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,65,'Terwijl zij zeggen:\"Onze Heer, wend de straf der hel van ons af want de straf daarvan is een voortdurende kwelling.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,66,'Zij is inderdaad slecht als rustplaats en als tehuis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,67,'En zij, die, als zij iets besteden, noch spilzuchtig noch vrekkig zijn, maar evenwichtig blijven tussen beide in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,68,'En zij die geen andere goden naast Allah aanroepen noch iemand doden, wat Allah heeft verboden, tenzij met recht, noch overspel plegen; en hij die dat doet zal een straf ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,69,'De straf zal hem verdubbeld worden op de Dag der Opstanding, en hij zal daar vernederd in vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,70,'Met uitzondering van hen die berouw hebben en geloven en goede daden doen, voor dezulken zal Allah de slechte daden in goede daden veranderen, want Allah is Vergevensgezind, Barmhartig!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,71,'En hij die berouw heeft, en het goede doet, wendt zich voorzeker berouwvol tot Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,72,'En zij, die niet leugenachtig getuigen en als zi; iets ijdels voorbijgaan, er edelmoedig aan voorbijgaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,73,'En zij, die, wanneer zij door tekenen van hun Heer gewaarschuwd worden, daarbij niet doof en blind nedervallen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,74,'En zij die zeggen: \"Onze Heer, maak onze echtgenoten en kinderen tot troost der ogen, en maak ons tot voorbeeld voor de godvruchtigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,75,'Dit zijn diegenen die beloond zullen worden met de hoogste plaats (in het paradijs) - omdat zij standvastig waren - waar zij zullen worden ontvangen met begroeting en vrede.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,76,'Daarin zullen zij verblijven; uitstekend is dit als verblijf en als rustplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,25,77,'Zeg: \"Mijn Heer zou niets om U geven als gij niet bidt. Gij hebt de waarheid verloochend en weldra zal de straf (u) worden opgelegd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,1,'Taa Sien Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,2,'Dit zijn de verzen van het duidelijke Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,3,'Wellicht zult gij ten dode toe treuren omdat zij niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,4,'Als Wij het willen, kunnen Wij hun een teken van de hemel nederzenden, zodat hun hoofd er zich voor zal nederbuigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,5,'Maar er komt van de Barmhartige geen nieuwe vermaning tot hen of zij wenden zich er van af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,6,'Voorzeker zij hebben dit verloochend, maar weldra zullen de tijdingen hun bereiken van hetgeen zij bespotten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,7,'Zien zij niet op aarde - hoeveel voortreffelijke soorten Wij daarop hebben doen groeien?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,8,'Daarin is inderdaad een teken; maar de meesten onder hen willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,9,'En voorzeker uw Heer is de Machtige, de Genadige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,10,'Toen uw Heer tot Mozes riep: \"Ga naar het onrechtvaardige volk,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,11,'Het volk van Pharao. Zullen zij (Mij) niet vrezen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,12,'Zeide hij: \"Mijn Heer, ik vrees, dat zij mij zullen verloochenen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,13,'En mijn boezem vernauwt zich en mijn tong is niet welsprekend; zend daarom (bericht) aan A�ron (om mij te helpen).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,14,'Bovendien hebben zij een aanklacht van misdaad tegen mij, dus vrees ik dat zij mij zullen doden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,15,'Hij (God) zeide: \"In geen geval, gaat dan met Onze tekenen; Wij zijn met u en zullen horen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,16,'\"Gaat dus naar Pharao en zegt: ´Wij zijn de boodschappers van de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,17,'Laat de kinderen Isra�ls met ons meegaan´.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,18,'Hij (Pharao) zeide: \"Voedden wij u niet onder ons op toen gij een kind waart? En gij bleeft onder ons vele jaren van uw leven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,19,'En gij weet wat gij deedt, terwijl gij ondankbaar waart.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,20,'Hij (Mozes) zeide: \"Ik deed dit, toen ik nog tot de dwalenden behoorde.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,21,'\"Daarom vluchtte ik van u omdat ik u vreesde; maar mijn Heer heeft mij wijsheid geschonken en mij tot een boodschapper gemaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,22,'Is dit de gunst die gij mij in herinnering brengt, dat gij de kinderen van Isra�l tot slaven hebt gemaakt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,23,'Pharao zeide: \"En wie is de Heer der Werelden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,24,'Mozes antwoordde: \"De Heer der hemelen en der aarde en van alles wat er tussen is, als gij het wilt geloven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,25,'Pharao zeide tot degenen die om hem heen waren: \"Hoort gij het niet?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,26,'Mozes zeide: \"Uw Heer, en de Heer uwer voorvaderen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,27,'Pharao zeide: \"Waarlijk, de boodschapper die tot u is gezonden, is krankzinnig.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,28,'Mozes zeide: \"Hij is de Heer van het Oosten en van het Westen en van alles wat daar tussen is, indien gij wilt begrijpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,29,'Pharao zeide tot hem: \"Indien gij een andere God aanneemt dan mij zal ik u zeker in de gevangenis werpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,30,'Mozes antwoordde: \"Ofschoon ik u een duidelijk teken breng?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,31,'Pharao zeide: \"Breng het dan als gij tot de waarachtigen behoort.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,32,'Daarop wierp Mozes zijn staf neder, en ziet! deze werd een zichtbare slang.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,33,'En hij strekte zijn hand uit, en ziet! zij was wit voor de toeschouwers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,34,'Pharao zeide tot de vooraanstaanden om zich heen: \"Dit is inderdaad een bedreven tovenaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,35,'Hij wenst u uit uw land te verdrijven door zijn tovenarij. Wat raadt gij mij dan aan?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,36,'Zij zeiden: \"Geef hem en zijn broeder uitstel en zend aankondigers naar de steden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,37,'Die u iedere bedreven tovenaar zullen brengen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,38,'Dus werden de tovenaars verzameld op de bepaalde tijd op een vastgestelde dag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,39,'En er werd tot het volk gezegd: \"Wilt gij u ook verzamelen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,40,'Opdat wij de tovenaars mogen volgen als zij overwinnaars zijn?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,41,'En toen de tovenaars kwamen, vroegen zij aan Pharao, \"Zal er een beloning voor ons zijn als wij de overwinnaars worden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,42,'Hij antwoordde: \"Ja zeker, bovendien zult gij tot de gunstelingen behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,43,'Mozes zeide tot hen: \"Werpt neder hetgeen gij te werpen hebt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,44,'Toen gooiden zij hun touwen en hun roeden, en zeiden: \"Bij de macht van Pharao, wij zullen de overhand krijgen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,45,'Daarna wierp Mozes zijn staf neder en ziet! deze slokte alles wat zij hadden gemaakt op.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,46,'Daarop wierpen de tovenaars zich op de grond neder.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,47,'En riepen uit: \"Wij geloven in de Heer der Werelden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,48,'De Heer van Mozes en A�ron.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,49,'Pharao zeide tot hen: \"Gelooft gij in hem voordat ik u toestemming geef? Hij is zeker uw leider die u tovenarij heeft onderwezen. Maar gij zult het weldra te weten komen. Ik zal zeker uw handen en uw voeten van links en rechts afhakken, en u allen doen kruisigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,50,'Zij antwoordden: \"Dat geeft niet; wij zullen voorzeker tot onze Heer terugkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,51,'Wij hopen dat onze Heer ons onze zonden zal vergeven, want wij zijn de eersten der gelovigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,52,'En Wij openbaarden aan Mozes, zeggende: \"Neemt Mijn dienaren mede in de nacht, want gij zult worden achtervolgd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,53,'En Pharao zond herauten naar de steden, zeggende,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,54,'\"Dit is slechts een kleine groep,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,55,'Toch hebben zij ons vertoornd;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,56,'En wij zijn een ten volle bewapende menigte.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,57,'Daarom verbanden Wij hen uit hun tuinen en bronnen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,58,'En schatten en voortreffelijke woning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,59,'Zo geschiedde het; en Wij gaven die als een erfenis aan de kinderen van Isra�l.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,60,'En zij (Egyptenaren) vervolgden hen bij zonsopgang;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,61,'En toen de twee scharen elkander zagen, zeiden de metgezellen van Mozes: \"Wij worden zeker ingehaald.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,62,'\"In geen geval!\" zeide hij. \"Mijn Heer is met mij. Hij zal mij leiden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,63,'Toen openbaarden Wij aan Mozes: \"Tref de zee met uw staf.\" Waarop zij vaneen week en elk gedeelte was als een grote berg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,64,'En Wij lieten de anderen naderbij komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,65,'En Wij redden Mozes en allen die met hem waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,66,'Daarna verdronken Wij de anderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,67,'Hierin is zeker een teken maar de meesten onder hen willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,68,'Voorwaar, uw Heer is de Almachtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,69,'En verkondig aan het volk het verhaal van Abraham.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,70,'Toen hij tot zijn vader en zijn volk zeide: \"Wat aanbidt gij?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,71,'Zeiden zij: \"Wij aanbidden (onze) goden en wij zullen hun toegewijd blijven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,72,'Hij zeide: \"Horen zij u als gij hen aanroept?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,73,'Baten of schaden zij u?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,74,'Zij antwoordden: \"Maar wij vonden dat onze vaderen hetzelfde deden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,75,'Hij zeide: \"Ziet gij dan, wat gij aanbidt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,76,'Gij en uw voorvaderen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,77,'Zij zijn vijanden van mij behalve de Heer der Werelden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,78,'Die mij heeft geschapen en Hij is het, Die mij leidt;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,79,'En Die mij voedsel en drank geeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,80,'En Die mij geneest wanneer ik ziek ben;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,81,'En Die mij zal doen sterven en daarna weer tot het leven terugroepen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,82,'En Die, hoop ik, mij mijn tekortkomingen zal vergeven op de Dag des Oordeels.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,83,'\"Mijn Heer schenk mij wijsheid en voeg mij bij de rechtvaardigen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,84,'En geef mij een goede naam onder de komende geslachten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,85,':En maak mij een der erfgenamen van de Tuin der Zaligheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,86,'En vergeef mijn vader, want hij behoorde tot de dwalenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,87,'En verneder mij niet op de Dag waarop de mensen zullen worden opgewekt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,88,'De Dag waarop rijkdom noch kinderen zullen baten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,89,'Maar slechts hij, die met een toegewijd hart tot Allah komt, (zal baat vinden).\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,90,'En het paradijs zal nabij worden gebracht voor de rechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,91,'En de hel zal worden onthuld voor de dwalenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,92,'En er zal tot hen worden gezegd: \"Waar zijn zij die gij aanbadt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,93,'Naast Allah? Kunnen zij u helpen of zichzelf helpen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,94,'Dan zullen zij hals over kop in (de hel) worden geworpen, zij en de dwalenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,95,'En de scharen van Iblies, allen tezamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,96,'Terwijl zij daarin onder elkander twisten, zullen zij (tegen de afgoden) zeggen:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,97,'\"Bij Allah, wij waren klaarblijkelijk in dwaling,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,98,'Toen wij u gelijk stelden aan de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,99,'En slechts de schuldigen deden ons dwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,100,'En wij hebben nu geen bemiddelaar,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,101,'Noch een boezemvriend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,102,'Indien er voor ons een terugkeer (naar de aarde) was, zouden wij tot de gelovigen behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,103,'Hierin is waarlijk een teken, maar de meesten onder hen willen het niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,104,'En voorwaar, uw Heer is de Machtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,105,'Het volk van Noach verloochende de boodschappers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,106,'Toen hun broeder, Noach, tot hen zeide: \"Wilt gij niet (God) vrezen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,107,'\"Waarlijk, ik ben voor u een getrouwe boodschapper,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,108,'Vreest daarom Allah, en gehoorzaamt mij,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,109,'En ik vraag u er geen beloning voor: Mijn loon is bij de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,110,'Vreest daarom Allah en gehoorzaamt mij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,111,'Zij antwoordden: \"Zullen wij u geloven terwijl slechts de onaanzienlijken u volgen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,112,'Hij (Noach) zeide: \"En wat weet ik er van wat zij deden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,113,'Hun rekening is alleen bij mijn Heer, als gij het slechts wist!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,114,'\"En ik ga de gelovigen niet verdrijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,115,'Ik ben niets dan een duidelijke waarschuwer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,116,'Zij zeiden: \"Als gij niet ophoudt, o Noach, zult gij voorzeker worden gestenigd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,117,'Hij zeide: \"Mijn Heer, mijn volk heeft mij verloochend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,118,'Oordeel daarom beslissend tussen hen en mij; en red mij en de gelovigen die met mij zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,119,'Daarom redden Wij hem en degenen die met hem in de geladen ark waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,120,'Daarna verdronken Wij degenen die achterbleven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,121,'Hierin is voorwaar een teken, maar de meesten hunner willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,122,'Waarlijk uw Heer is de Machtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,123,'De Aad verloochenden de boodschappers,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,124,'Toen hun broeder Hoed tot hen zeide: \"Zult gij niet godvruchtig worden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,125,'\"Waarlijk, ik ben tot u een getrouwe boodschapper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,126,'Daarom vreest Allah en gehoorzaamt mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,127,'En ik vraag u er geen beloning voor; mijn loon is slechts bij de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,128,'\"Bouwt gij monumenten op elke hoge plaats om u te vermaken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,129,'En bouwt gij kastelen, alsof gij voor eeuwig zult leven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,130,'En als gij iemand aangrijpt, grijpt gij hem aan als geweldenaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,131,'Vreest Allah en gehoorzaamt mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,132,'Ja, vreest Hem, Die alles wat gij weet aan u geschonken heeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,133,'Hij heeft u overvloedig vee en kinderen geschonken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,134,'En tuinen en bronnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,135,'Ik vrees voor u inderdaad de straf van een grote Dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,136,'Zij antwoordden: \"Het is ons hetzelfde of gij predikt of niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,137,'Dit is niets dan een verzinsel der ouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,138,'En wij zullen niet worden gestraft.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,139,'Daarom verloochenden zij hem en Wij vernietigden hen. Daarin is waarlijk een teken, maar de meesten hunner willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,140,'En voorwaar uw Heer is de Machtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,141,'Het geslacht van Samoed verloochende de boodschappers eveneens.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,142,'Toen hun broeder, Salih, tot hen zeide, \"Wilt gij niet godvruchtig worden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,143,'Waarlijk ik ben voor u een getrouwe boodschapper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,144,'Vreest daarom Allah en gehoorzaamt mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,145,'En ik vraag u er geen beloning voor. Mijn beloning is slechts bij de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,146,'Zult gij met rust worden gelaten tussen de dingen die hier zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,147,'Tussen tuinen en bronnen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,148,'En korenvelden en dadelpalmen vol vruchten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,149,'En de huizen, welke gij met grote handigheid uit de bergen maakt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,150,'Daarom vreest Allah en gehoorzaamt mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,151,'En gehoorzaamt niet aan het bevel der buitensporigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,152,'Die onheil op aarde stichten, en zich niet beteren,\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,153,'Zeiden zij: \"Gij zijt betoverd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,154,'Gij zijt slechts een mens zoals wij, toon ons dan een teken, als gij tot de waarachtigen behoort.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,155,'Hij (Salih) zeide: \"Hier is een kamelin, zij heeft haar beurt om te drinken en gij hebt uw beurt om te drenken op een vastgestelde tijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,156,'En doe haar geen kwaad anders zal de straf van een grote Dag u achterhalen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,157,'Doch zij verlamden haar en daarna hadden zij er spijt van.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,158,'Maar de straf achterhaalde hen. Voorwaar daarin is een teken maar de meesten hunner willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,159,'Uw Heer is de Machtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,160,'Ook het volk van Lot verloochende de boodschappers,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,161,'Toen hun broeder Lot tot hen zeide: \"Wilt gij niet rechtvaardig worden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,162,'Waarlijk, ik ben u een getrouwe boodschapper,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,163,'Vreest daarom Allah en gehoorzaamt mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,164,'En ik vraag u er geen beloning voor. Mijn beloning is slechts bij de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,165,'\"Nadert gij van alle schepselen de mannen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,166,'En verlaat gij uw vrouwen, die uw Heer voor u heeft geschapen? Neen, gij zijt een volk dat de perken te buiten gaat.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,167,'Zij zeiden: \"Als gij niet ophoudt, o Lot, zult gij zeker worden verbannen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,168,'Hij zeide: \"Waarlijk, ik veracht uw handelwijze.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,169,'\"Mijn Heer, red mij en mijn familie van hetgeen zij doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,170,'Daarom redden Wij hem en zijn hele gezin.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,171,'Behalve een oude vrouw die achterbleef.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,172,'Daarna vernietigden Wij de anderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,173,'En Wij deden een regen op hen regenen, en vreselijk was de regen voor hen, die waren gewaarschuwd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,174,'Daarin is waarlijk een teken maar de meesten hunner willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,175,'En voorwaar, uw Heer is de Machtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,176,'Het volk van het woud verloochende ook de boodschappers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,177,'Toen Shoaib tot hen zeide: \"Wilt gij niet godvruchtig worden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,178,'Waarlijk, ik ben voor u een getrouwe boodschapper,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,179,'Daarom vreest Allah en gehoorzaamt mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,180,'En ik vraag u er geen beloning voor. Mijn beloning is slechts bij de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,181,'Geeft de volle maat en behoort niet tot hen die minder geven (bedriegers).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,182,'En weegt met de zuivere weegschaal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,183,'En doet de mensen in hetgeen hun toekomt niet te kort, noch handelt verderfelijk door onheil te stichten op aarde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,184,'En vreest Hem, Die u en de vroegere geslachten schiep.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,185,'Zij zeiden: \"Gij zijt slechts betoverd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,186,'En gij zijt niets meer dan een man zoals wij en wij denken dat gij tot de leugenaars behoort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,187,'Doe dan stukken van de hemel op ons vallen als gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,188,'Hij zeide: \"Mijn Heer weet het beste wat gij doet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,189,'En zij verloochenden hem. Daarna achterhaalde hen de straf van de dag der overschaduwing. Dat was waarlijk de straf van een grote dag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,190,'Voorwaar, daarin is een teken maar de meesten hunner willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,191,'Waarlijk uw Heer is de Almachtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,192,'Voorwaar dit Boek is een openbaring van de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,193,'De Heilige Geest (Gabri�l) heeft het nedergebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,194,'In uw hart, opdat gij de waarschuwer moogt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,195,'In duidelijke Arabische taal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,196,'En het is zeker in de geschriften der vroegere volkeren (vermeld).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,197,'Is het geen teken voor hen dat de geleerden onder de kinderen van Isra�l het weten?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,198,'Indien Wij het aan een vreemdeling hadden geopenbaard');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,199,'En hij had het hun voorgelezen, dan zouden zij er nooit in hebben geloofd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,200,'Zo hebben Wij het (ongeloof) in de harten der zondaren doen binnendringen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,201,'Zij zullen er niet in geloven voordat zij de smartelijke straf zien;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,202,'Maar deze zal plotseling over hen komen terwijl zij het niet bemerken;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,203,'En zij zullen zeggen: \"Wordt ons geen uitstel gegeven?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,204,'Vragen zij dan Onze straf te verhaasten?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,205,'Zeg: \"Als Wij hun toest,aan jaren te genieten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,206,'En hun dan datgene zou overkomen, waaabmede zij werden bedreigd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,207,'Zou al hetgeen hun werd geschonken hun toch niet baten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,208,'En Wij vernietigden nooit een stad, zonder dat er voor haar een Waarschuwer was geweest,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,209,'Om te vermanen. Wij waren nooit onrechtvaardig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,210,'De duivelen hebben (de Koran) niet nedergebracht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,211,'Noch is het passend voor hen, noch zijn zij in staat, dat te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,212,'Voorzeker het horen (hiervan) is hun ontnomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,213,'Roep daarom naast Allah geen andere god aan, anders zult gij gestraft worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,214,'En waarschuw uw naaste familieleden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,215,'En wend u met zachtmoedigheid tot de gelovigen die u volgen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,216,'Indien zij u dan niet gehoorzamen, zeg: \"Ik heb niets uitstaande met hetgeen gij doet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,217,'En stel uw vertrouwen in de Almachtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,218,'Die u ziet wanneer gij opstaat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,219,'En uw bewegingen onder hen die zich nederwerpen (in aanbidding).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,220,'Want Hij is de Alhorende, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,221,'Zal ik u mededelen op wie de duivelen nederdalen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,222,'Zij dalen op elke verstokte leugenaar en zondaar neder.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,223,'En zij verlenen gehoor aan (geruchten) en velen hunner zijn zelf leugenaars,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,224,'En de dichters! de dwalenden volgen hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,225,'Hebt gij niet gezien hoe zij in elk dal radeloos rondlopen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,226,'En wat zij zeggen doen zij niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,26,227,'Behalve zij die geloven en goede werken doen, en Allah vaak gedenken, en zich verdedigen nadat hun onrecht is aangedaan, maar de onrechtvaardigen zullen weldra weten welke wending hun zaken zullen nemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,1,'Taa Sien. Dit zijn de verzen van de Koran, het duidelijke Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,2,'Een richtsnoer en goed nieuws voor de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,3,'Die het gebed houden en de Zakaat betalen, en een vast geloof hebben in het Hiernamaals.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,4,'Voorzeker, die niet in het Hiernamaals geloven, hun daden hebben Wij voor hen schoonschijnend gemaakt, daarom lopen zij blindelings rond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,5,'Zij zullen zeker een zware straf krijgen en zij zullen in het Hiernamaals de grootste verliezers zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,6,'Voorwaar, aan u wordt de Koran overgedragen door de Alwijze, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,7,'Gedenk toen Mozes tot zijn familieleden zeide: \"Ik zie een vuur. Ik zal u daarvan enig bericht brengen of ik breng wat vuur mee opdat gij u moogt verwarmen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,8,'En toen hij er bij kwam, riep een stem hem toe: \"Gezegend is hij, die in het vuur is en gezegend is hij die er dichtbij is, glorie zij Allah, de Heer der Werelden!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,9,'O Mozes, Ik ben Allah, de Machtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,10,'Werp uw staf neder.\" Maar toen hij de staf zich als een slang zag bewegen, wendde hij zich af en wilde zich niet omkeren. (En Allah zeide) \"O Mozes, vrees niet, voorwqwaar bij Mij vrezen de boodschappers niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,11,'Noch degene die kwaad doet en daarna het kwade door goed vereffent; want waarlijk, Ik ben dan Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,12,'En stop uw hand in uw boezem, zij zal zonder enige schade wit te voorschijn komen. Dit behoort tot de negen tekenen voor Pharao en zijn volk; want zij zijn een opstandig volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,13,'Maar toen Onze verlichtende tekenen tot hen kwamen, zeiden zij: \"Dit is openbare tovenarij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,14,'En zij verwierpen deze onrechtvaardig en aanmatigend terwijl hun zielen er van overtuigd waren. Ziet, hoe kwaad het einde was van de onruststokers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,15,'En Wij gaven kennis aan David en Salomo, en zij zeiden: \"Alle eer behoort aan Allah, Die ons boven vele van Zijn gelovige dienaren heeft verheven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,16,'En Salomo volgde David op en hij zeide: \"O gij mensen, ons is de taal der vogelen onderwezen, en ons werd alles geschonken. Dit is inderdaad Gods openbare gunst.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,17,'Er waren legers voor Salomo verzameld van djinn, mensen en vogelen en zij werden in slagorde opgesteld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,18,'Toen zij tot het dal van de mieren kwamen, zei een mier: \"O gij mieren, gaat uw woningen binnen opdat Salomo en zijn scharen u niet verpletteren zonder dit te bemerken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,19,'Daarop glimlachte hij, zich verbazend over haar woorden en hij zeide: \"Mijn Heer, doe mij dankbaar zijn voor Uw gunst, die Gij mij en mijn ouders hebt bewezen en laat mij het goede doen dat U behaagt en laat mij door Uw barmhartigheid tot Uw rechtvaardige dienaren behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,20,'En hij overzag de vogelen, en zeide: \"Hoe kan het zijn dat ik de hop niet zie? Is hij onder de afwezigen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,21,'Ik zal hem zeker streng straffen of ik zal hem doden, als hij mij geen duidelijke uitleg geeft.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,22,'En hij duurde niet lang, totdat deze kwam en zeide: \"Ik heb datgene gezien wat gij niet weet; en ik heb over Saba betrouwbare inlichtingen meegebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,23,'Ik vond een vrouw, die daar regeerde en haar is alles gegeven en zij heeft een machtige troon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,24,'Ik vond, dat zij en haar volk de zon aanbaden in plaats van Allah en Satan heeft hun werken voor schoonschijnend gemaakt en heeft hun de weg versperd, zodat zij geen rechte leiding volgen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,25,'Zij aanbidden Allah niet, Die hetgeen in de hemelen en op aarde verborgen is aan het licht brengt en Die weet wat gij verbergt en wat gij toont.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,26,'Allah! Er is geen God naast Hem, de Heer van de Grote Troon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,27,'Salomo zeide: \"Wij zullen zien of gij de waarheid hebt gesproken of dat gij tot de leugenaars behoort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,28,'Ga met deze mijn brief en leg hem voor hen neder, trek u dan van hen terug, en zie welk antwoord zij terugzenden.´´');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,29,'Zij (de Koningin) zeide: \"Gij, leiders, er is aan mij een nobele brief afgeleverd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,30,'Hij is van Salomo en luidt: \"In naam van Allah, de Barmhartige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,31,'Weest niet laatdunkend tegenover mij maar komt tot mij in onderworpenheid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,32,'Zij zeide: \"Gij leiders, geeft mij raad in de zaak die voor mij ligt, ik beslis niets totdat gij er mee instemt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,33,'Zij antwoordden: \"Wij hebben de macht en wij bezitten een grote dapperheid in de oorlog, maar de zaak is in uw handen; overdenk daarom wat gij zult bevelen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,34,'Zij zeide: \"Voorzeker, koningen verwoesten een stad als zij er (met geweld) binnen trekken en maken de hoogsten van het volk tot de laagsten. Zo handelen dezen (zeker ook met ons).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,35,'Maar ik ga hun geschenken sturen en afwachten waarmede de afgevaardigden terugkeren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,36,'Toen de gezant (der koningin) tot Salomo kwam, zeide deze: \"Schenkt gij mij rijkdommen? Maar datgene wat Allah mij geschonken heeft is beter dan wat Hij u heeft gegeven. Neen, gij verheft u op uw gaven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,37,'Gaat tot hen terug, want wij zullen zeker tot hen komen met scharen waartegen zij geen macht zullen hebben, wij zullen hen met ontering daaruit (de stad) verdrijven en zij zullen vernederd worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,38,'Hij (Salomo) zeide: \"O edelen, welke onder u zal mij een passende troon voor haar brengen voordat zij onderdanig tot mij komt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,39,'Een dappere van de djinn zeide: \"Ik zal deze tot u brengen voordat gij van uw kamp opstaat en zeker heb ik daar macht over en ik ben betrouwbaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,40,'Iemand, die kennis van het geschrift had zeide: \"Ik zal hem tot u brengen v��r uw bode terugkeert,\" en toen Salomo de troon naast zich zag geplaatst, zeide hij: \"Dit is bij de gratie van mijn Heer, opdat Hij mij moge beproeven of ik dankbaar of ondankbaar ben. En wie dankbaar is, is dankbaar voor het welzijn van zijn eigen ziel, maar wie ondankbaar is, waarlijk mijn Heer is Zichzelf-genoeg, Ge�erd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,41,'En hij (Salomo) zeide: \"Zorgt dat haar eigen troon haar tegenstaat. Wij zullen zien of zij de rechte weg volgt of dat zij behoort tot degenen die van de rechte weg worden afgeleid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,42,'En toen zij kwam, werd haar gevraagd: \"Is uw troon als deze?\" Zij antwoordde: \"Hij is als het ware dezelfde.\" En ons is voordien kennis gegeven en wij zijn reeds onderdanig geworden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,43,'Hij (Salomo) weerhield haar van het aanbidden van hetgeen zij in plaats van Allah aanbad; want zij behoorde tot een ongelovig volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,44,'Er werd tot haar gezegd \"Ga het paleis binnen.\" En toen zij het zag, dacht zij dat het een massa water was, en zij raakte in verwarring. Hij zeide: \"Het is een paleis dat geplaveid is met glas.\" Zij zeide: \"Mijn Heer, ik heb mijn ziel inderdaad onrecht aangedaan; en ik onderwerp mij met Salomo aan Allah, de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,45,'En Wij zonden zeker tot de Samoed hun broeder Salih, die zeide: \"Aanbidt Allah.\" Maar ziet, zij werden in twee partijen gesplitst die met elkander twistten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,46,'Hij zeide: \"O mijn volk, waarom wenst gij het kwade te verhaasten boven het goede? Waarom vraagt gij geen vergiffenis aan Allah, opdat u barmhartigheid betoond moge worden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,47,'Zij antwoordden: \"Wij voorzien kwaad wegens u en degenen die met u zijn.\" Hij zeide: \"Uw kwade verwachting is bij Allah. Neen, gij zijt een volk dat beproefd wordt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,48,'En er waren negen personen in de stad die onrust in het land stichtten en zich niet wilden verbeteren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,49,'Zij zeiden: \"Zweert tot elkander bij Allah, dat wij zeker Salih en zijn familie in de nacht zullen aanvallen en daarna zullen wij tot zijn bloedverwanten zeggen: \"Wij waren geen getuigen van de vernietiging van zijn familie en wij spreken zeker de waarheid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,50,'En zij smeedden een plan, en Wij maakten ook een plan (tegen hen) maar zij bemerkten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,51,'Ziet dan hoe het einde van hun plan was; Wij vernietigden hen en hun volk, allen tezamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,52,'En dit zijn hun ingestorte huizen omdat zij onrechtvaardig waren. Daarin is voorwaar een teken voor een volk, dat begrijpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,53,'En Wij redden hen die geloofden en godvrezend waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,54,'En Lot, toen hij tot zijn volk zeide: \"Begaat gij onzedelijkheid tegen beter weten in?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,55,'Nadert gij wellustig de mannen in plaats van de vrouwen? Neen, gij zijt een onwetend volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,56,'Maar het antwoord van zijn volk was niets anders dan dat zij zeiden: \"Verdrijft Lot´s familie uit uw stad want zij zijn mensen, die zich rein willen houden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,57,'Daarom redden Wij hem en zijn familie behalve zijn vrouw; Wij deden haar tot de achterblijvenden behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,58,'En Wij deden een regen over hen komen, en vreselijk was de regen voor de gewaarschuwden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,59,'Zeg: \"Alle lof behoort aan Allah en vrede zij met Zijn uitverkoren dienaren. Is Allah beter of wat zij met Hem vereenzelvigen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,60,'Hij Die de hemelen en de aarde schiep en water uit de hemelen nederzendt waarmee Hij prachtige tuinen doet groeien? Gij zoudt hun bomen niet kunnen doen groeien. Is er een God naast Allah? Neen, zij zijn een volk dat het spoor bijster is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,61,'Hij Die de aarde tot een rustplaats maakte, er rivieren in plaatste en er hechte bergen op zette en een dam tussen de beide zee�n? Is er een God naast Allah? Neen, de meesten hunner (willen) het niet weten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,62,'Hij Die de wanhopige verhoort als deze Hem aanroept, en het kwade wegneemt en u opvolgers op aarde maakt? Is er een God naast Allah? Hoe weinig lering trekt gij er uit!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,63,'Hij, Die u leidt in het duister van het land en van de zee, en Die u winden zendt als boodschappers van blijde tijdingen (regen) door Zijn barmhartigheid? Is er een God naast Allah? Verheven is Allah boven hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,64,'Hij Die de schepping voortbrengt en dat dan herhaalt, en u (voedsel) voorziet uit de hemel en de aarde? Is er een God naast Allah?\" Zeg: \"Geeft uw bewijs hiervoor als gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,65,'Zeg: \"Niemand in de hemelen en op aarde kent het onzienlijke, behalve Allah; en zij weten niet wanneer zij zullen worden opgewekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,66,'Neen, hun kennis heeft geheel gefaald betreffende het Hiernamaals, neen, zij twijfelen er aan, neen, zij zijn er blind voor.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,67,'En de ongelovigen zeggen: \"Zullen wij wanneer wij en onze vaderen tot stof zijn geworden inderdaad worden opgewekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,68,'Er was ons voorheen reeds mede gedreigd, - ons en onze voorvaderen; dit zijn niets dan fabelen der ouden!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,69,'Zeg tot hen: \"Reist op aarde en ziet hoe het einde der zondaren was.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,70,'En treur niet om hen, noch wees bezorgd voor hun samenzwering.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,71,'En zij zullen zeggen: \"Wanneer zal deze bedreiging worden vervuld als gij de waarheid spreekt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,72,'Zeg: \"Het kan zijn, dat een gedeelte van de straf die gij wildet verhaasten, reeds dicht bij u is gekomen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,73,'En waarlijk, uw Heer is goedertieren voor de mensen maar de meesten hunner zijn ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,74,'En zeker uw Heer weet alles wat hun hart verbergt en wat het openbaart.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,75,'En er is niets in de hemelen of op aarde verborgen, of het staat in een duidelijk boek opgeschreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,76,'Waarlijk deze Koran legt aan de kinderen van Isra�l veel uit van hetgeen waaromtrent zij verschillen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,77,'En voorwaar, het is richtsnoer en barmhartigheid voor de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,78,'En voorwaar uw Heer zal over hen beslissen door Zijn gebod en Hij is de Almachtige, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,79,'Stel dan uw vertrouwen in Allah; voorzeker gij bezit de duidelijke Waarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,80,'Waarlijk, gij kunt de doden, noch de doven de oproep doen horen als Zij zich verwijderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,81,'Noch kunt gij de blindeen van hun dwaling afleiden. Gij kunt alleen hen doen horen die in Onze tekenen willen geloven en zich onderwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,82,'En wanneer het Woord voor hun bewaarheid wordt, zullen Wij een dier uit de aarde te voorschijn brengen dat hen zal verwonden, omdat de mensen niet in Onze tekenen geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,83,'En op de Dag waarop Wij van elk volk degenen zullen verzamelen die Onze tekenen loochenden, zullen zij in groepen worden bijeengehouden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,84,'Wanneer zij komen, zal Hij zeggen: \"Hebt gij Onze tekenen geloochend voordat gij deze trachttet te begrijpen of wat deedt gij?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,85,'En het oordeel zal op hen vallen wegens hun ongerechtigheid en zij zullen niet kunnen spreken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,86,'Hebben zij niet gezien dat Wij de nacht hebben ingesteld opdat zij er in mogen rusten, en de dag om licht te geven. Daarin zijn voorwaar tekenen voor een volk dat gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,87,'En de Dag, waarop de bazuin zal worden geblazen, zullen zij (allen) die in de hemelen en ook zij die op aarde zijn, schrikken, behalve degenen die Allah wil (sparen). En allen zullen nederig tot Hem komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,88,'En gij ziet de bergen en gij denkt ze onbewegelijk terwijl zij als de wolken voorbijgaan. Dit is Allah´s werk Die alles volmaakt heeft geschapen. Voorwaar, Hij is goed op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,89,'Wie een goede daad doet, zal een betere beloning hebben dan deze en zij zullen op die Dag veilig zijn voor schrik.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,90,'Maar zij die een slechte daad begaan, zullen op hun aangezicht in het Vuur worden nedergeworpen. \"Gij wordt slechts beloond voor hetgeen gij hebt gedaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,91,'Zeg: \"Het is mij geboden alleen de Heer dezer stad die Hij heilig heeft verklaard te aanbidden, en aan Hem behoren alle dingen, en het is mij geboden tot de Moslims te behoren');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,92,'En de Koran te verkondigen.´´ Wie daarom leiding volgt, volgt haar ten eigen bate. En zeg tegen hem die dwaalt: \"Ik ben slechts een waarschuwer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,27,93,'En zeg: \"Alle eer behoort aan Allah, Hij zal u Zijn tekenen tonen en gij zult ze kennen.\" En uw Heer is niet onachtzaam omtrent hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,1,'Taa Sien Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,2,'Dit zijn de verzen van het duidelijke Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,3,'Wij dragen u het verhaal van Mozes en Pharao voor, in waarheid, ten bate van een volk dat wil geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,4,'Waarlijk, Pharao handelde aanmatigend in het land en deed het volk er van in partijen scheiden; van een groep die hij als zwak beschouwde doodde hij de zonen en spaarde de vrouwen. Zeker, hij behoorde tot de onheilstichters.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,5,'En Wij wensten hun die op aarde als zwak beschouwd werden een gunst te bewijzen door hen tot erfgenamen en leiders te maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,6,'En hen te vestigen op aarde; om Pharao en Hamaan en hun scharen datgene te tonen waarvoor zij vreesden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,7,'En Wij openbaarden aan de moeder van Mozes: \"Zoog hem; en indien gij voor hem vreest, werp hem dan in de rivier en vrees noch treur; want Wij zullen hem aan u teruggeven en zullen hem tot een boodschapper maken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,8,'En Pharao´s familie nam hem op zodat hij voor hen een vijand en een smart zou worden; want Pharao en Hamaan en hun scharen waren boosdoeners.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,9,'En Pharao´s vrouw zeide: \"(Dit kind is) een troost voor de ogen voor u en voor mij. Dood hem niet. Hij kan nuttig voor ons zijn of wij kunnen hem als zoon opnemen.\" Maar zij doorzagen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,10,'En het hart der moeder van Mozes werd vrij (van angst). Zij had het bijna onthuld als Wij haar hart niet gesterkt hadden om tot de gelovigen te behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,11,'En zij zeide tot zijn zuster: \"Ga hem achterna.\" Zij sloeg hem van verre gade en de anderen bemerkten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,12,'En Wij hadden hem de minnen voordien verboden. Daarom zeide zij (zijn zuster): \"Zal ik u een familie noemen die hem voor u zal grootbrengen en die voor hem welwillend zal zijn?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,13,'Zo gaven Wij hem aan zijn moeder terug opdat haar oog getroost mocht worden en opdat zij niet behoefde te treuren en opdat zij mocht weten dat de belofte van Allah waar is. Maar de meeste mensen kennen (de Waarheid) niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,14,'En toen hij volwassen werd en zijn volle kracht had bereikt, gaven wij hem wijsheid en kennis; zo belonen Wij hen die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,15,'En hij ging de stad binnen op een tijdstip waarop de bewoners achteloos waren, en hij vond er twee vechtende mannen, de ene van zijn eigen volk en de andere van zijn vijanden. En hij die van zijn volk was zocht hulp tegen hem die tot zijn vijanden behoorde. Daarom stompte Mozes hem zodat deze stierf. Hij zeide: \"Dit is Satan´s werk, en deze is inderdaad een vijand en openbare verleider.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,16,'Hij zeide: \"Mijn Heer, ik heb mijn eigen ziel onrecht gedaan, bescherm mij.\" Daarom vergaf Hij hem; want Hij is de Vergevensgezinde, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,17,'Hij zeide: \"Mijn Heer, door de gunsten die Gij mij hebt bewezen zal ik nooit de schuldigen ondersteunen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,18,'En in de morgen was hij in de stad, vrezend, op zijn hoede; en ziet! hij, die de vorige dag zign hulp had gezocht riep wederom tot hem om hulp. Mozes zeide tot hem: \"Gij zijt voorzeker stellig een dwalende.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,19,'En toen hij hem wilde grijpen die een vijand van beiden was, zeide deze: \"O Mozes, wilt gij mij ook doden, zoals gij gisteren een man gedood hebt? Gij wenst slechts een geweldenaar te worden in het land en wilt geen vredestichter zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,20,'En er kwam een man aangehold van het andere einde der stad, zeggende: \"O Mozes, waarlijk, de leiders beraadslagen om u te doden. Ga daarom weg, ik ben u welgezind.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,21,'Daarop ging hij heen, vrezende en op zijn hoede. Hij bad: \"Mijn Heer, verlos mij van het kwaadaardige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,22,'En toen hij zijn gezicht naar Midian keerde, zeide hij: \"Ik hoop dat mijn Heer mij naar de rechte weg zal leiden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,23,'En toen hij bij de bron van Midian aankwam, vond hij daar een groep mannen die hun vee drenkten. En hij vond naast hen twee vrouwen die (haar kudden) terughielden. Mozes zeide tot haar: \"Wat scheelt u?\" Zij antwoordden: \"Wij kunnen niet drenken, totdat de herders hun kudden terugnemen want onze vader is een zeer oude man.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,24,'Daarop drenkte hij voor haar. Daarna ging hij opzij in de schaduw, en zeide: \"Mijn Heer, ik heb behoefte aan wat Gij mij voor goeds moogt nederzenden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,25,'En een der twee vrouwen kwam verlegen naar hem toelopen. Zij zeide: \"Mijn vader roept u opdat hij u moge belonen omdat gij voor ons gedrenkt hebt.\" Dan, toen hij tot hem kwam en hem het verhaal vertelde, zeide hij: \"Vrees niet, gij zijt een onrechtvaardig volk ontvlucht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,26,'Een der twee vrouwen zeide: \"O, mijn vader neem hem in dienst, want de beste man die gij kunt huren is hij, die sterk, vertrouwenswaardig is.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,27,'En hij zeide: \"Ik zou u een dezer twee dochters van mij uithuwen, mits gij acht jaren voor mij werkt. En als gij er tien voltooit dan zou dit uit uw vrije wil geschieden. En ik zal u geen moeilijkheden opleggen; gij zult vinden, als Allah het wil, dat ik tot de rechtvaardigen behoor.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,28,'Mozes antwoordde: \"Dat is een overeenkomst tussen u en mij. Welke van de twee termijnen ik ook vervul, er zal mij geen onrecht worden aangedaan; en Allah is Getuige van hetgeen wij zeggen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,29,'Toen Mozes de termijn had voltooid, en met zijn familie op reis ging, bemerkte hij een vuur in de richting van de berg Sina�. Hij zeide tot zijn familie: \"Wacht hier, ik zie een vuur, misschien kan ik u nieuws of wat vuur daarvan brengen opdat gij u moogt verwarmen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,30,'En toen hij er bij kwam werd hij door een stem van de rechterzijde van het dal geroepen, op de heilige plaats van uit de boom: \"O Mozes, voorwaar, Ik ben Allah, de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,31,'Werp uw staf neder.\" En toen hij hem zag bewegen als een slang, vluchtte hij en keerde niet om. \"O Mozes, kom en vrees niet, want gij behoort tot hen die veilig zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,32,'\"Steek uw hand in uw boezem; zij zal zonder ziekte wit te voorschijn komen - en wees niet bang voor gevaar - dit zijn twee tekenen van uw Heer aan Pharao en zijn leiders. Waarlijk, zij zijn een opstandig volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,33,'Hij (Mozes) zeide: \"Mijn Heer, ik doodde een man onder hen, en ik vrees dat zij mij nu zullen doden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,34,'Maar mijn broeder A�ron is beter bespraakt dan ik, zend hem daarom met mij als helper, opdat hij moge getuigen van mijn waarheid, want ik vrees dat zij mij zullen verloochenen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,35,'God zeide: \"Wij zullen uw arm door uw broeder versterken en Wij zullen u beiden macht geven zodat zij u niet zullen kunnen bereiken. Door Onze tekenen zult gij beiden en zij die u volgen overwinnaars worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,36,'En toen Mozes met Onze duidelijke tekenen tot hen kwam, zeiden zij: \"Dit is niets dan verzonnen tovenarij, en wij hoorden nooit van iets dergelijks onder onze voorvaderen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,37,'Mozes zeide: \"Mijn Heer weet het beste wie de leiding van Hem heeft gebracht en voor wie de gelukkige beloning van het tehuis zal zijn. Waarlijk, de onrechtvaardigen zullen nooit slagen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,38,'En Pharao zeide: \"O leiders, ik erken geen God voor u naast mij; stook voor mij een vuur O Hamaan, om stenen van klei te bakken en bouw een toren, opdat ik moge opklimmen naar de God van Mozes want waarlijk ik beschouw hem als een leugenaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,39,'En hij en zijn legers handelden ten onrechte aanmatigend in het land. En zij dachten, dat zij nooit naar Ons zouden worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,40,'Daarom grepen Wij hem en zijn scharen en wierpen hen midden in de zee. Zie dan hoe slecht het einde der boosdoeners was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,41,'En Wij gaven hun leiders die tot het Vuur uitnodigen; en op de Dag der Opstanding zullen zij niet worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,42,'En Wij deden hen in deze wereld door een vloek achtervolgen, en op de Dag der Opstanding zullen zij de verachten zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,43,'En Wij gaven het Boek aan Mozes nadat Wij de vroegere geslachten hadden vernietigd als een duidelijk bewijs voor de mensen en als een leiding en een genade, opdat zij er lering uit mochten trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,44,'En gij (Mohammed) waart niet aan de westelijke kant (van de berg) toen Wij Mozes de geboden mededeelden, noch waart gij onder de aanwezigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,45,'Maar Wij brachten vele geslachten na Mozes voort en het leven werd voor hen verlengd. En gij waart geen bewoner onder het volk van Midian, die Onze tekenen aan hen voordroeg, maar Wij waren het, Die boodschappers stuurden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,46,'En gij waart niet aan de bergkant toen Wij (naar Mozes) riepen. Maar uit barmhartigheid van uw Heer zijt gij gezonden, opdat gij een volk naar hetwelk geen waarschuwer kwam v��r u moogt waarschuwen opdat zij er lering uit mogen trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,47,'En indien een ramp over hen zou komen wegens hetgeen zij verdienen, mogen zij niet meer zeggen: \"Onze Heer, waarom zondt Gij ons geen boodschapper, opdat wij Uwe tekenen hadden kunnen volgen en onder de gelovigen konden zijn?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,48,'Maar toen de Waarheid van Ons tot hen kwam, zeiden zij: \"Waarom is hem niet hetzelfde gegeven als aan Mozes werd gegeven?\" Verwierpen zij datgene niet, wat Mozes voorheen was gegeven? Zij zeiden: \"Twee tovenaars die elkander ondersteunen.\" En dezen zeggen: \"Wij geloven in beiden niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,49,'Zeg: \"Brengt dan een Boek van Allah dat een betere leiding geeft dan dit, ik zal dat volgen, indien gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,50,'Maar als zij u niet antwoorden, weet dan dat zij slechts hun eigen begeerten volgen. En wie dwaalt meer dan hij die zijn eigen neigingen volgt zonder de leiding van Allah? Voorwaar, Allah leidt de onrechtvaardige mensen niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,51,'En Wij hebben inderdaad het Woord voortdurend aan hen nedergezonden opdat zij er lering uit mogen trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,52,'Zij aan wie Wij het Boek voordien gaven, geloven er in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,53,'En als het aan hen is voorgedragen, zeggen zij: \"Wij geloven er in. Voorwaar, het is de Waarheid van onze Heer. Inderdaad, wij hadden ons zelf reeds onderworpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,54,'Hun beloning zal hun tweemaal worden gegeven want zij zijn standvastig geweest en omdat zij het kwade met het goede weren, en mededelen van hetgeen waarmee Wij hen hebben voorzien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,55,'Wanneer zij ijdele gesprekken horen, wenden zij zich er van af en zeggen: \"Aan ons onze werken en aan u uw werken. Vrede zij met u. Wij zoeken de onwetenden niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,56,'Waarlijk, gij zult hen die gij wilt niet kunnen leiden, maar Allah leidt wie Hij wil; en Hij kent hen het beste die geleid willen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,57,'En zij (de bewoners van Mekka) zeggen: \"Als wij de leiding met u zouden volgen, zouden wij van ons land worden weggevoerd.\" Hebben Wij voor hen geen veilig heiligdom opgericht waarheen allerlei vruchten worden gebracht als een voorziening van Ons? Maar de meesten onder hen begrijpen het niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,58,'En hoeveel steden hebben Wij niet vernietigd die trots waren op hun middelen van bestaan! En ginds waren hun woonplaatsen die tot op enkele na niet meer bewoond zijn geworden. En Wij zijn het Die de erfgenamen werden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,59,'En uw Heer is niet zo, dat Hij steden vernietigt, voordat Hij in de hoofdstad een boodschapper heeft verwekt die hun Ons woord verkondigt; noch verwoesten Wij steden tenzij de bewoners er van onrechtvaardig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,60,'En wat u gegeven is dient slechts als middel van bestaan voor het tegenwoordige leven en ter versiering er van; en hetgeen bij Allah is, is beter en van langere duur. Wilt gij dit niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,61,'Staat hij aan wie Wij een goede belofte hebben gedaan, die hij (vervuld) zal zien, gelijk met degenen, die Wij van de goederen dezer wereld hebben gegeven en zal hij dan op de Dag der Opstanding naar de hel worden gebracht?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,62,'En op die Dag zal God hen roepen en zeggen: \"Waar zijn Mijn deelgenoten die gij u beweerdet te zijn?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,63,'Zij, tegen wie het Woord van kracht zal worden, zullen zeggen: \"Onze Heer, dit zijn degenen die wij deden dwalen. Wij deden hen dwalen zoals wij dwaalden. Wij betuigen onze onschuld aan U. Wij waren het niet die zij aanbaden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,64,'En er zal worden gezegd: \"Roept uw afgoden aan.\" En zij zullen hen aanroepen maar deze zullen hen niet horen. Terwijl zij de straf zullen zien. Hadden zij slechts de leiding gevolgd!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,65,'En op die Dag zal Hij tot hen roepen en zeggen: \"Welk antwoord gaaft gij (aan Onze) boodschappers?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,66,'Dan zullen alle uitvluchten op die Dag voor hen duister worden en zij zullen elkander niet kunnen vragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,67,'Maar hij, die berouw heeft, gelooft en goed doet, zal waarschijnlijk tot de geslaagden behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,68,'Uw Heer schept en kiest wat Hij wil, zij (de afgoden) hebben geen keuze. Glorie zij Allah en verheven is Hij boven alles wat zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,69,'En uw Heer weet wat hun harten verbergen en wat zij openbaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,70,'En Hij is Allah; er is geen God naast Hem. Aan Hem behoort alle roem in deze wereld en in het Hiernamaals. Van Hem is het gebod en tot Hem zult gij worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,71,'Zeg: \"Vertelt mij, als Allah de nacht over u doet voortduren tot de Dag der Opstanding, welke God is er naast Allah die u een licht kan brengen? Wilt gij dan niet luisteren?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,72,'Zeg: \"Vertelt mij, als Allah de dag voor u doet voortduren tot de Dag der Opstanding welke God is er dan naast Allah die u een nacht kan brengen waarin gij kunt rusten? Wilt gij dat niet inzien?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,73,'Het is door Zijn barmhartigheid dat Hij nacht en dag voor u heeft ingesteld opdat gij er in moogt rusten en naar Zijn overvloed moogt uitzien, en opdat gij dankbaar moogt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,74,'Gedenkt de dag waarop Hij hen zal oproepen en zeggen: \"Waar zijn Mijn mededingers, die gij u placht te verbeelden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,75,'En Wij zullen uit elk volk een getuige nemen en Wij zullen zeggen: \"Brengt uw bewijs.\" Dan zullen zij weten dat de Waarheid aan Allah behoort. En hetgeen zij plachten te verzinnen zal mislukken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,76,'Korach behoorde voorwaar tot het volk van Mozes, maar hij gedroeg zich aanmatigend tegenover hen. En Wij hadden hem zoveel schatten gegeven dat zijn sleutels zeker een last waren geweest voor een groep sterke mannen. Toen zijn volk tot hem zeide: \"Poch niet, want Allah houdt niet van degenen die pochen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,77,'Maar zoek door hetgeen Allah u heeft gegeven het tehuis van het Hiernamaals; en vergeet uw deel aan de wereld niet, en doe goed (aan anderen) zoals Allah u goed gedaan heeft; en schep geen wanorde op aarde, want Allah heeft hen, die onheil stichten, niet lief.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,78,'Hij antuoordde: \"Mij werd het alleen door mijn kennis gegeven.\" Wist hij niet dat Allah v��r hem vele geslachten had vernietigd die machtiger waren dan hij en groter in aantal? En de schuldigen worden niet gevraagd omtrent hun zonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,79,'Hij bleef verschijnen voor zijn volk met pracht en praal. Zij, die het leven dezer wereld wensten, zeiden: \"O, ware ons hetzelfde gegeven als Korach. Waarlijk, hij is bezitter van een groot fortuin.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,80,'Maar zij, aan wie kennis was gegeven, zeiden: \"Wee u, de beloning van Allah is beter voor degenen die geloven en goede werken doen; en het zal niemand worden geschonken behalve hun die geduldig zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,81,'Dan deden Wij hem en zijn huis in de aarde verzinken; en hij had geen partij om hem tegen Allah te helpen noch kon hij zich verdedigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,82,'En zij, die zijn plaats de vorige dag hadden begeerd, begonnen (de volgende dag) te zeggen: \"O wee, Allah vergroot en verkleint de voorziening voor wie Hij wil van Zijn dienaren. Indien Allah ons niet genadig was geweest zou Hij ons ook in de aarde hebben doen verzinken. Wee, de ondankbaren slagen nooit.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,83,'Daar is het tehuis van het Hiernamaals! Wij geven het degenen die op aarde geen zelfverheffing wensen, noch wanorde stichten, en het einde is voor de godvruchtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,84,'Zij die goed doen worden er beter voor beloond, maar zij die kwaad doen, worden slechts vergolden naar datgene wat zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,85,'Voorwaar, Hij, Die de verkondiging van de Koran u oplegde, zal u tot de plaats van terugkeer brengen. Zeg: \"Mijn Heer weet het beste wie de ware leiding heeft gebracht en wie op een openlijk dwaalspoor is.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,86,'En gij hadt niet verwacht dat het Boek (de Koran) aan u zou worden geopenbaard; maar het is een barmhartigheid van uw Heer; wees daarom nooit een ondersteuner der ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,87,'En laten zij u niet afwenden van de woorden van Allah nadat zij tot u zijn nedergezonden; en roep anderen tot uw Heer, en behoor niet tot de afgodendienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,28,88,'En roep naast Allah geen andere God aan. Er is geen God naast Hem. Alles is vergankelijk behalve Zijn Aangezieht. Aan Hem is de heerschappij en tot Hem zult glg worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,1,'Alif Laam Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,2,'Denken de mensen dat zij (met rust) zullen worden gelaten, alleen omdat zij zeggen: \"Wij geloven\" zonder dat zij zullen worden beproefd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,3,'Wij beproefden degenen die v��r hen waren. Daarom zal Allah ook hen die waarachtig zijn, onderscheiden en de leugenaars kenbaar maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,4,'Of denken zij, die slechte daden doen, dat zij Ons zullen ontsnappen? Hun oordeel is verkeerd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,5,'Wie de ontmoeting met Allah verwacht (wete dat) Allah´s vastgestelde tijd gewis komt. En Hij is de Alhorende, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,6,'En wie streeft, streeft slechts voor zichzelf; want Allah is Onafhankelijk van alle werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,7,'Waarlijk Wij zullen de fouten dergenen die geloven en goede daden verrichten bedekken en hun de beste beloning geven voor wat zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,8,'En Wij hebben de mens geboden zijn ouders goed te doen; en indien zij trachten u er toe te brengen dat gij iets met Mij vereenzelvigt waarvan gij geen kennis hebt, gehoorzaam hen dan niet. Tot Mij is uw terugkeer, en Ik zal u vertellen wat gij deedt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,9,'En zij die geloven en goede werken doen, hen zullen Wij zeker onder de rechtevaardigen toelaten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,10,'Onder de mensen zijn er die zeggen: \"Wij geloven in Allah\", maar als zij vervolgd worden om Allah´s zaak, zien zij de vervolging van de mens als een straf van Allah. Maar als hulp van uw Heer komt, zeggen zij: \"Voorzeker, wij waren met U.\" Weet Allah niet het best wat in het innerlijk aller schepselen is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,11,'Allah zal de gelovigen zeker onderscheiden en Hij zal de huichelaars gewis kenbaar maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,12,'En de ongelovigen zeggen tot de gelovigen: \"Volgt onze weg, wij zullen uw zonden dragen.\" Doch zij kunnen niets van hun zonden dragen. Zij zijn zeker leugenaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,13,'Voorzeker zij zullen hun eigen last dragen en andere last buiten hun eigen last. En waarlijk zij zullen op de Dag der Opstanding worden ondervraagd over hetgeen zij plachten te verzinnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,14,'Voorwaar, Wij zonden Noach tot zijn volk, en hij verbleef onder hen duizend jaar op vijftig jaar na. En de zondvloed achterhaalde hen terwijl zij onrechtvaardig waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,15,'Maar Wij redden hem en de deelgenoten der ark, en Wij maakten dit tot een teken voor alle volkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,16,'En Wij zonden Abraham, en hij zeide tot zijn volk: \"Aanbid Allah en vrees Hem. Dat zal voor u het beste zijn indien gij het begrijpt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,17,'\"Gij aanbidt naast Allah slechts afgoden en gij verzint leugens. Zij die gij naast Allah aanbidt hebben geen macht over uw onderhoud. Zoekt daarom levensonderhoud van Allah en aanbidt Hem en weest Hem dankbaar want tot Hem zult gij worden teruggebracht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,18,'En als gij verloochent, andere geslachten v��r u verloochenden ook. En op de boodschapper rust slechts de duidelijke overbrenging (van de boodschap).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,19,'Zien zij niet hoe Allah de schepping verwekt, en daarna herhaalt? Dat is zeker gemakkelijk voor Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,20,'Zeg: \"Trek op aarde rond en zie hoe Allah de schepping begint en daarna de latere schepping verwekt.\" Waarlijk, Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,21,'Hij straft wie Hij wil en Hij toont barmhartigheid aan wie Hem behaagt en tot Hem zult gij worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,22,'Nimmer kunt gij de plannen van Allah in de hemel of op aarde verijdelen; noch hebt gij een enkele vriend of helper naast Allah.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,23,'Zij, die in de tekenen van Allah en de ontmoeting met Hem niet geloven, wanhopen aan Zijn barmhartigheid; dezen zullen een smartelijke straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,24,'Het antwoord van zijn volk was slechts: \"Doodt of verbrandt hem.\" Maar Allah redde hem van het vuur. Daarin zijn zeker tekenen voor een volk dat wil geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,25,'Hij (Abraham) zeide: \"Gij hebt naast Allah slechts afgoden voor u gekozen terwille van vriendschap onder elkander in het tegenwoordige leven. Doch op de Dag der Opstanding zult gij elkander verwerpen, en elkander vervloeken. En uw tehuis zal het Vuur zijn; en gij zult geen helper hebben.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,26,'En Lot geloofde in hem. Abraham zeide: \"Ik vlucht naar mijn Heer; want Hij is de Almachtige, de Alwijze.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,27,'En Wij gaven hem Izaak en Jacob, en Wij plaatsten het profetenambt en het Boek onder zijn nageslacht, en Wij gaven hem zijn beloning in dit leven en in het Hiernamaals zal hij zeker tot de rechtvaardigen behoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,28,'En toen Lot tot zijn volk zeide: \"Gij verricht een gruweldaad die niemand onder het mensdom ooit v��r u heeft begaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,29,'Nadert gij mannen met wellust en rooft gij op de weg, en begaat gij zelfs gruweldaden in uw bijeenkomsten?\" Maar het antwoord van zijn volk was niet anders dan dat zij zeiden: \"Breng de straf van Allah over ons als gij de waarheid spreekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,30,'Hij (Lot) zeide: \"Help mij mijn Heer, tegen het volk dat onheil sticht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,31,'En toen onze boodschappers Abraham het nieuws brachten, zeiden zij: \"Wij willen het volk dezer stad vernietigen; want haar inwoners zijn onrechtvaardigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,32,'Hij zeide: \"Maar Lot is daar.\" Zij zeiden: \"Wij weten wel wie daar woont. Wij zullen hem en zijn familie sparen, behalve zijn vrouw, die achter zal blijven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,33,'En toen Onze boodschappers tot Lot kwamen was hij verdrietig wegens hen en voelde zich daardoor in moeilijkheid. En zij zeiden: \"Vrees niet, noch treur. Voorzeker, wij zullen u en uw familie redden, behalve uw vrouw die tot de achterblijvenden behoort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,34,'Wij zullen gewis een straf van de hemel op de bewoners dezer stad nederzenden, wegens hun overtredingen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,35,'En Wij lieten hier een duidelijk teken achter voor een volk, dat begrijpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,36,'En tot Midian, (zonden Wij) hun broeder Shoaib, die zeide: \"O mijn volk, dien Allah en vrees de laatste dag en wandel niet op aarde onheil stichtende.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,37,'Maar zij verloochenden hem. Daarom overviel hen een hevige aardbeving en zij lagen in hun huizen plat tegen de grond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,38,'En (Wij vernietigden) ook de Aad en de Samoed; en dit kunt gij aan hun woonplaatsen duidelijk zien. Satan deed hun daden hun goed voorkomen, en weerhield hen van het pad ofschoon zij het duidelijk konden zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,39,'Wij vernietigden eveneens Korach en Pharao en Hamaan. Mozes kwam tot hen met duidelijke tekenen, maar zij handelden hoogmoedig op aarde; toch konden zij Ons niet ontsnappen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,40,'Zo grepen Wij iedereen om zijn zonden en onder hen waren er, die een rukwind achterhaalde, en onder hen waren er die de aardbeving overviel, en onder hen waren er die Wij in de aarde deden verzinken, en onder hen waren er die Wij deden verdrinken. Allah was het niet, die hen onrecht aandeed, maar zij handelden onrechtvaardig jegens zichzelf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,41,'De gelijkenis van hen, die helpers verkiezen naast Allah, is als de gelijkenis van de spin die zich een huis maakt: en het zwakste der huizen is zeker het huis van de spin, als zij het slechts wisten!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,42,'Voorwaar, Allah weet wat zij naast Hem aanroepen: en Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,43,'En dit zijn gelijkenissen die Wij voor de mensen geven, maar alleen zij die kennis bezitten begrijpen ze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,44,'Allah schiep de hemelen en de aarde in waarheid Daarin is zeker een teken voor hen die willen geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,45,'Verkondig hetgeen u in het Boek is geopenbaard, en onderhoud uw gebed. Voorwaar, het gebed weerhoudt van ondeugd en kwaad. En Allah gedachtig te zijn is inderdaad het hoogste. Allah weet wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,46,'En twist met de mensen van het Boek slechts op de goede wijze; doch zeg tegen de onrechtvaardigen: \"Wij geloven in hetgeen ons is geopenbaard en hetgeen u is geopenbaard; en onze God en uw God is E�n; en aan Hem onderwerpen wij ons.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,47,'En aldus hebben Wij u het Boek nedergezonden en zij wie Wij het Boek hebben gegeven geloven daarin; en ook onder de (Mekkanen) zijn er die er in geloven. En het zijn alleen de ondankbaren die Onze tekenen verwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,48,'En voordien placht gij geen boek te lezen, noch met uw rechter hand te schrijven anders zouden de leugenaars aan de (echtheid) ervan hebben kunnen twijfelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,49,'Neen, het zijn duidelijke tekonen in het hart van hen aan wie kennis is gegeven. En alleen de onrechtvaardigen verwerpen Onze tekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,50,'Toch zeggen zij: \"Waarom zijn hem geen tekenen van zijn Heer nedergezonden?\" Zeg: \"De tekenen zijn bij Allah alleen, en ik ben slechts een duidelijke waarschuwer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,51,'Is het niet genoeg voor hen dat Wij u het Boek hebben geopenbaard dat aan hen wordt voorgelezen? Voorwaar, hierin is barmhartigheid en aanzien voor een volk dat gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,52,'Zeg, \"Allah is voldoende als Getuige tussen u en mij. Hij weet alles wat in de hemelen en op aarde is. Zij die in de leugen geloven en Allah verwerpen zijn de verliezers.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,53,'Zij vragen u de straf te verhaasten; en indien er geen termijn was genoemd zou de straf reeds over hen zijn gekomen; toch zal deze hen zeker onverwachts overvallen terwijl zij het niet voorzien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,54,'Zij vragen u de straf te verhaasten; maar waarlijk de hel zal de ongelovigen omringen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,55,'Op de Dag waarop de straf hen zal overweldigen van boven en van onder hun voeten, zal Hij zeggen: \"Ondergaat wat gij hebt bedreven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,56,'O Mijn gelovige dienaren Mijn aarde is uitgestrekt, aanbidt derhalve Mij alleen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,57,'Elk mens moet de dood ondergaan, daarna zult gij tot Ons worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,58,'Zij, die geloven en goede werken doen, hen zullen Wij zeker huisvesten in verheven woningen van het paradijs waarin rivieren stromen. Zij zullen er in vertoeven. Hoe voortreffelijk is de beloning dergenen die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,59,'Die standvastig zijn, en hun vertrauwen in hun Heer stellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,60,'En hoeveel dieren zijn er die hun eigen onderhoud niet meedragen! Allah zorgt voor hen en voor u, en Hij is de Alhorende, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,61,'En als gij hen vraagt: \"Wie heeft de hemelen en de aarde geschapen, en de zon en de maan in dienst gesteld?\" zullen zij gewis zeggen, \"Allah\". Hoe worden zij dan (van het goede pad) afgewend?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,62,'Allah vergroot en bekrimpt het levensonderhoud voor wie Hij wil van Zijn dienaren. Voorzeker, Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,63,'En als gij hun vraagt: \"Wie zendt water uit de hemel neder en geeft er leven door aan de aarde na haar dood?\", zullen zij gewis zeggen: \"Allah\". Zeg: \"Alle roem behoort aan Allah.\" Maar de meesten hunner begrijpen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,64,'Het tegenwoordige leven is niets dan een leeg vermaak en een spel, maar het tehuis van het Hiernamaals, dat is het werkelijke Leven, als zij dit slechts konden begrijpen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,65,'En wanneer zij aan boord van een schip gaan, roepen zij Allah aan, oprecht zijnde in gehoorzaamheid aan Hem. Maar wanneer Hij hen veilig aan wal brengt, zie, zij schrijven deelgenoten aan Hem toe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,66,'Zodat zij datgene, wat Wij hun hebben geschonken verloochenen en zich vermaken. Maar zij zullen het weldra te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,67,'Hebben zij niet gezien, dat Wij een veilig Heiligdom (Makka) hebben gemaakt, terwijl overal om hen heen mensen worden weggerukt? Geloven zij dan aan een leugen en ontkennen zij de gunsten van Allah?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,68,'En wie is onrechtvaardiger dan hij, die een leugen verzint over Allah, of de Waarheid verloochent wanneer zij tot hem komt? Is er geen woning in de hel voor de ongelovigen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,29,69,'En zij, die naar Ons streven, - Wij zullen hen zeker op Onze wegen leiden. Voorwaar, Allah is met hen die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,1,'Alif Laam Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,2,'De Romeinen zijn verslagen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,3,'In het nabijzijnde land, maar zij zullen na hun nederlaag zeker overwinnen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,4,'Binnen een negental jaren - van Allah is het gebod daarv��r en daarna - en op die Dag zullen de gelovigen zich verheugen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,5,'In de hulp van Allah, Hij helpt wie Hij wil; en Hij is de Almachtige, de Genadige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,6,'(Dit is) Allah´s Belofte; Allah breekt zijn Belofte niet, maar de meeste mensen beseffen dit niet;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,7,'Zij kennen slechts de schijn van het wereldse leven, en zij zijn zorgeloos over het Hiernamaals.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,8,'Hebben zij over zichzelf niet nagedacht? Allah heeft de hemelen en de aarde en alles wat daartussen is niet geschapen, dan in waarheid en voor een vastgestelde tijd. Toch geloven velen onder de mensen niet in de ontmoeting met hun Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,9,'Hebben zij niet op aarde gereisd, zodat zij mochten zien hoe het einde was van degenen die v��r hen waren? Zij waren sterker in macht dan dezen, zij maakten het land vruchtbaar en bebouwden het, meer dan dezen het deden. En hun boodschappers kwamen tot hen met duidelijke tekenen. En Allah was het niet Die hun onrecht aandeed, maar zij waren het die hun eigen ziel onrecht aandeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,10,'Dan was het einde bitter voor hen die kwaad deden, omdat zij de tekenen van Allah loochenden, en er over spotten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,11,'Allah brengt de schepping teweeg; dan herhaalt Hij haar; daarna zult gij tot Hem worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,12,'En de Dag, waarop het Uur zal komen, zullen de schuldigen wanhopig worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,13,'Geen hunner afgoden zal voor hen een bemiddelaar zijn; en zij zullen hun afgoderij verwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,14,'Op de Dag, waarop het Uur zal komen, zullen zij worden gescheiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,15,'Dan zullen zij die geloven en goede werken verrichtten in een tuin gelukkig worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,16,'Maar zij die niet geloofden en Onze tekenen en de ontmoeting in het Hiernamaals verwierpen, zullen voor straf te staan komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,17,'Glorie zij Allah, wanneer gij de avond ingaat en wanneer gij de ochtend ingaat -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,18,'En aan Hem behoort alle roem in de hemelen en op aarde - bij nacht en des daags.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,19,'Hij brengt de levenden uit de doden voort en Hij brengt de doden uit de levenden voort; en Hij geeft de aarde leven na haar dood, en evenzo zult gij worden voortgebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,20,'En tot Zijn tekenen behoort, dat Hij u uit stof schiep; en ziet! gij zijt mensen die zich kunnen verspreiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,21,'En dit is onder Zijn tekenen, dat Hij uit uw midden echtgenoten voor u schiep, opdat gij er rust in moogt vinden, en Hij heeft liefde en tederheid onder u geplaatst. Daarin zijn zeker tekenen voor een volk, dat nadenkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,22,'En tot Zijn tekenen behoort ook de schepping der hemelen en der aarde, en de verscheidenheid van uw talen en (huids) - kleuren. En dit zijn voorzeker tekenen voor degenen, die willen begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,23,'En tot Zijn tekenen behoort uw slapen ´s nachts en uw zoeken naar Zijn overvloed overdag. Daarin zijn zeker tekenen voor een volk, dat luistert.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,24,'En tot Zijn tekenen behoort eveneens dat Hij u de bliksem toont als vrees en hoop. En dat Hij water uit de hemel nederzendt waarmede hij de aarde doet herleven na haar dood. Hierin zijn zeker tekenen voor een volk, dat wil begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,25,'En dit is onder Zijn tekenen, dat de hemelen en de aarde in stand blijven door Zijn gebod. Dan, wanneer Hij u eenmaal van de aarde zal roepen, ziet! zult gij gaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,26,'En aan Hem behoort een ieder, die in de hemelen en op aarde is; allen zijn Hem gehoorzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,27,'En Hij is het, Die de schepping voortbrengt en haar daarna herhaalt, dit is gemakkelijk voor Hem. En voor Hem zijn de verhevenste attributen in de hemelen en op aarde, en Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,28,'Hij geeft een gelijkenis uit uzelf. Hebt gij onder uw ondergeschikten deelgenoten in hetgeen waarvan Wij u hebben voorzien, zodat gij dienaangaande gelijken wordt en vreest gij hen, zoals gij elkander vreest? - Zo leggen Wij de tekenen uit aan een volk dat begrijpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,29,'Neen, de onrechtvaardigen volgen hun eigen neigingen zonder enige kennis. En wie kan hen leiden, die Allah liet dwalen? Voor hen zullen er geen helpers zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,30,'Daarom, richt uw aangezicht oprecht tot de (ware) godsdienst, overeenkomstig de natuur naar welke Allah de mensen heeft geschapen. - De schepping van Allah kent geen verandering. - Dat is het ware geloof. Maar de meeste mensen weten het niet. -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,31,'U tot Hem bekerende, vreest Hem en leeft het gebed na, en behoort niet tot de afgodendienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,32,'Noch tot degenen die hun godsdienst verdelen en secten vormen, terwijl elke partij zich verheugt in wat zij heeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,33,'En als een ramp over de mensen komt roepen zij hun Heer aan, zich tot Hem bekerende; en als Hij hen van Zijn barmhartigheid heeft doen smaken, ziet! dan schrijft een gedeelte hunner aan hun Heer medegoden toe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,34,'Zodat zij ondankbaar worden voor hetgeen Wij hun hebben geschonken. Vermaakt u dan voor een wijle, maar gij zult het weldra te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,35,'Hebben Wij enig gezag tot hen nedergezonden, dat spreekt over hetgeen zij met Hem vereenzelvigen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,36,'En wanneer Wij de mensen barmhartigheid doen smaken, verheugen zij zich daarin; maar als een kwaad hen overkomt door hun eigen werken, ziet! dan wanhopen zij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,37,'Hebben zij niet ingezien, dat Allah de voorziening vergroot en bekrimpt voor wie Hij wil? Daarin zijn waarlijk tekenen voor een volk dat wil geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,38,'Geeft, de verwanten, de behoeftigen, de reiziger wat hun toekomt. Dat is het beste voor degenen die het Aangezicht van Allah zoeken, dezen zijn het die zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,39,'Wat gij ook uitleent met rente opdat het moge toenemen door hetgeen andere (mensen) bezitten; het neemt niet toe bij Allah; maar wat gij in Zakaat geeft, Allah´s welbehagen zoekend, dezen zullen hun bezit vermeerderd zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,40,'Hij is Allah, Die u schept en dan voor u zorgt en daarna doet Hij u sterven en dan zal Hij u doen herleven. Is er een onder uw afgoden, die iets dergelijks kan doen? Heilig is Hij en verheven boven hetgeen zij (met Hem) vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,41,'Verderf is gekomen over land en zee door hetgeen de handen der mensen hebden gewrocht, zodat Hij hen een gedeelte van hun daden zou doen smaken, opdat zij zich bekeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,42,'Zeg: \"Reist op aarde en ziet hoe het einde was van degenen die voordien waren. De meesten hunner waren afgodendienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,43,'Richt uw aaneezicht tot de juiste godsdienst, voordat de Dag komt, die door niemand afgewend kan worden buiten Allah. Op die Dag zal het mensdom worden gescheiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,44,'Hij die verwerpt, zijn ongeloof zal tegen hem zijn, en zij die goede daden verrichten, bereiden dit voor hun eigen ziel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,45,'Opdat Hij hen, die geloven en goede werken doen, moge belonen uit Zijn overvloed. Voorzeker, Hij heeft de ongelovigen niet lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,46,'En ��n onder Zijn tekenen is dat Hij de winden met blijde vooruitzichten zendt, opdat Hij u Zijn genade moge doen smaken, en opdat de schepen op Zijn gebod mogen varen, opdat gij moogt zoeken naar Zijn overvloed en opdat gij dankbaar zult zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,47,'Waarlijk, Wij zonden boodschappers v��r u naar hun volkeren. Zij brachten hun duidelijke bewijzen. Dan straften Wij degenen die zondigden. En het was Onze plicht de gelovigen te helpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,48,'Hij is Allah, Die de winden zendt welke de wolken doen oprijzen. Dan verspreidt Hij ze in de lucht zoals Hij wil, dan hoopt Hij ze laag boven laag op en gij ziet regen uit hun midden stromen. En wanneer Hij deze doet vallen op wie van Zijn dienaren Hij wil, ziet! verheugen zij zich.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,49,'Ofschoon zij voordien, voordat hij (de regen) over hen was nedergezonden, vertwijfelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,50,'Beschouw daarom de kentekenen van Allah´s barmhartigheid: hoe Hij de aarde doet herleven na haar dood. Voorwaar, Dezelfde zal de doden opwekken; Hij heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,51,'En indien Wij een (droge) wind hadden gezonden en zij de aarde zagen geel worden, zouden zij daarna zeker ondankbaarheid hebben betoond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,52,'En gij kunt de doden niet doen horen, noch kunt gij de doven de roep doen horen wanneer zij u hun rug toekeren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,53,'Noch kunt gij de blinden uit hun dwaling leiden. Gij kunt slechts diegene doen horen die in Onze tekenen zouden willen geloven, zodat zij zich onderwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,54,'Het is Allah, Die u in een staat van zwakheid schept, dan na de zwakte kracht geeft en na de kracht (weer) zwakte en ouderdom. Hij schept wat Hij wil. Hij is de Alwetende, de Almachtige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,55,'De Dag waarop het Uur zal komen zullen de schuldigen zweren, dat zij slechts een uur hebben geleefd - zo werden zij bedrogen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,56,'Maar zij wie wijsheid en geloof was gegeven, zullen zeggen: \"Volgens het Boek van Allah zijt gij inderdaad tot de Dag der Opstanding gebleven. En dit is de Dag der Opstanding, maar gij wist het niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,57,'Daarom zullen de uitvluchten op die Dag de onrechtvaardigen niet baten; noch zal hun verontschuldiging worden aangenomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,58,'Waarlijk, Wij hebben in deze Koran allerlei gelijkenissen voor de mensen gegeven; voorzeker, als gij hun een teken brengt, zullen de ongelovigen zeggen: \"Gij zijt slechts leugenaars.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,59,'Zo verzegelt Allah het hart van hen, die niet willen weten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,30,60,'Wees geduldig voorzeker, de Belofte van Allah is waar; en laat hen die geen zekerheid hebben u niet doen wankelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,1,'Alif Laam Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,2,'Dit zijn verzen van het Boek vol van Wijsheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,3,'Een leiding en een genade voor de goeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,4,'Zij, die het Gebed naleven en de Zakaat betalen, en die in het Hiernamaals vast geloven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,5,'Zij zijn het, die de leiding van hun Heer volgen, en zij zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,6,'En onder de mensen is iemand die door ijdele praatjes zonder kennis (anderen) van Allah´s pad wil doen afdwalen en er mee de spot drijft; voor zulken zal er een vernederende straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,7,'En wanneer Onze woorden aan hem worden voorgedragen, wendt hij zich verachtelijk af alsof hij ze niet hoorde en zijn oren verstopt waren. Kondig hem daarom een pijnlijke straf aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,8,'Voorzeker, die geloven en goede werken doen, zullen gezegende tuinen hebben,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,9,'Waarin zij zullen vertoeven, zo is Allah´s Belofte; Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,10,'Hij heeft de hemelen geschapen zonder enige voor u zichtbare pilaren en Hij heeft op aarde hechte bergen gemaakt opdat zij niet beven met u, en Hij heeft er allerlei dieren over verspreid. Wij hebben water uit de wolken nedergezonden en hebben allerlei edele soorten daarin (de aarde) doen groeien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,11,'Dit is de Schepping van Allah. Toont mij nu wat degenen hebben geschapen die naast Hem (worden gesteld). Neen, de onrechtvaardigen verkeren klaarblijkelijk in dwaling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,12,'En Wij schonken wijsheid aan Loqmaan, zeggende: \"Wees Allah dankbaar, want hij die dankbaar is, is dankbaar voor zichzelf, en die ondankbaar is: Allah is Zichzelf-genoeg, Geprezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,13,'Toen Loqmaan tot zijn zoon, terwijl hij hem raad gaf, zeide: \"O mijn lieve zoon, ken geen medegoden aan Allah toe; afgoderij is inderdaad een grote ongerechtigheid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,14,'Wij hebhen de mens op het hart gedrukt betreffende zijn ouders, zijn moeder droeg hem in zwakte op zwakte, en zijn zogen nam twee jaren in beslag. Zeg Mij en uw ouders dank, tot Mij is de terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,15,'Maar indien (uw ouders) trachten u iets met Mij te doen vereenzelvigen, waarvan gij geen kennis hebt, gehoorzaam hen niet. Doch leef met hen samen in de wereld op een behoorlijke wijze en volg de weg van hem die zich tot Mij richt. Dan zult gij tot Mij terugkeren en Ik zal u inlichten over hetgeen gij deedt. -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,16,'\"O mijn lieve zoon! Al zou het het gewicht van een mosterdzaadje zijn, en al zou het zich in een rots bevinden of in de hemelen of op aarde, Allah zal het zeker openbaar maken. Voorwaar, Allah is Aldoordringend, Alkennend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,17,'O mijn lieve zoon, verricht het gebed en beveel het goede aan en verbied het kwade en verdraag geduldig wat u ook overkome. Dit is een ernstige zaak.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,18,'En keer uw gelaat niet (in verachting) van de mensen af noch wandel in hoogmoed op aarde; want Allah heeft de hoogmoedige noch de pocher lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,19,'En loop met gewone stap en verzacht uw stem; want de meest onaangename stem is het gebalk van een ezel.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,20,'Hebt gij niet gezien, dat Allah alles wat in de hemelen en op aarde is in uw dienst heeft gesteld en Zijn gunsten rijkelijk aan u heeft geschonken, zowel uiterlijk als innerlijk? En onder de mensen zijn er, die over Allah twisten, zonder kennis of enige leiding of een verlichtend Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,21,'En als er tot hen wordt gezegd: \"Volgt hetgeen Allah heeft geopenbaard,\" zeggen zij: \"Neen, wij zullen datgene volgen wat wij onze vaderen zagen volgen.\" Zelfs al zou Satan hen tot de straf van het branden hebben uitgenodigd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,22,'Maar hij, die zich aan Allah onderwerpt en het goede doet, heeft inderdaad een sterk houvast gegrepen. Bij Allah rust het einde aller dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,23,'En zij die niet geloven, laat hun ongeloof u niet verdrieten. Tot Ons zullen zij wederkeren en Wij zullen hen inlichten over wat zij deden; Allah weet heel goed wat in hun innerlijk is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,24,'Wij zullen hen voor een poosje zich laten vermaken; daarna zullen Wij hen tot een strenge straf voortdrijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,25,'En als gij hun vraagt: \"Wie schiep de hemelen en de aarde?\" zullen zij gewis antwoorden: \"Allah\". Zeg: \"Alle roem behoort aan Allah.\" Maar de meesten hunner weten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,26,'Aan Allah behoort al hetgeen in de hemelen en op aarde is, voorzeker Allah is Zichzelf-genoeg, Geprezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,27,'En als alle bomen op aarde pennen waren en de oceaan, met nog zeven oceanen aangevuld (inkt was), de woorden van Allah zouden niet kunnen worden uitgeput. Voorwaar, Allah is Almachtig, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,28,'O mensen uw Schepping en uw Opstanding zijn slechts als die van een enkele ziel. Voorwaar, Allah is Alhorend, Alziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,29,'Hebt gij niet gezien, dat Allah de nacht in de dag doet overgaan en de dag in de nacht en dat hij de zon en de maan in dienst heeft gesteld? Elk loopt voor een vastgestelde tijd; Allah is goed op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,30,'Dit is omdat Allah de Waarheid en wat zij naast Hem aanroepen vals is, en omdat Allah de Verhevene, de Grote is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,31,'Hebt gij niet gezien, dat de schepen op zee varen door de gunst van Allah, opdat Hij u Zijn tekenen moge tonen? Voorzeker daarin zijn tekenen voor een ieder, die geduldig en dankbaar is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,32,'En wanneer de golven hen als schaduwen omhullen, roepen zij Allah oprecht zijnde in gehoorzaamheid aan; maar wanneer Hij hen veilig aan land brengt, volgen slechts enigen hunner de rechte weg. En niemand verloochent Onze tekenen behalve de trouweloze, de ondankbare.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,33,'O mensen, vreest uw Heer, en ducht de Dag waarop geen vader zijn zoon iets zal baten, noch de zoon zijn vader van enig nut zal kunnen zijn. Allah´s belofte is zeker waar. Laat daarom het wereldse leven u niet misleiden, noch laat de Verleider u omtrent Allah bedriegen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,31,34,'Voorwaar, Allah alleen bezit de kennis van het Uur. Hij zendt de regen neder en Hij weet wat zich in de baarmoeder bevindt. Geen ziel weet wat zij morgen zal doen, en geen ziel weet in welk land zij zal sterven. Voorwaar, Allah is Alwetend, Alkennend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,1,'Alif Laam Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,2,'De openbaring van het Boek is zonder twijfel van de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,3,'Zeggen zij: \"Hij heeft het verzonnen?\" Neen, het is de Waarheid van uw Heer, opdat gij een volk moogt waarschuwen onder hetwelk v��r u geen waarschuwer is geweest, opdat zij geleid mogen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,4,'Allah is het, Die de hemelen en de aarde en hetgeen er tussen is in zes dagen schiep; daarna zette Hij Zich op de Troon. Gij hebt geen helper of bemiddelaar buiten Hem. Wilt gij dan geen lering (hieruit) trekken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,5,'Hij ordent het bestel van de hemel tot de aarde, daarna zal deze tot Hem opstijgen in een dag, waarvan de duur naar uw berekening duizend jaar is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,6,'Alzo is de Kenner van het onzichtbare en het zichtbare, de Almachtige, de Genadevolle,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,7,'Die de schepping van alles voltooide. En Hij begon de schepping van de mens uit klei.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,8,'Daarna maakte Hij zijn nageslacht uit een uittreksel van een nietige vloeistof.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,9,'Dan vormde Hij hem en ademde hem van Zijn geest in. En Hij gaf u oren, ogen en hart. Maar gij betoont weinig dankbaarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,10,'En zij zeggen: \"Zullen wij, wanneer wij in de aarde verloren zijn, opnieuw worden geschapen?\" Neen, zij geloven niet in de ontmoeting met hun Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,11,'Zeg: \"De doodsengel, aan wie gij toevertrouwd zijt, zal uw ziel nemen; dan zult gij tot uw Heer worden teruggebracht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,12,'O, kondet gij het slechts zien wanneer de schuldigen hun hoofd zullen buigen voor hun Heer, zeggende: \"Onze Heer, wij hebben gezien en wij hebben gehoord, zend ons nu terug opdat wij goede werken mogen verrichten; voorzeker wij zijn thans overtuigd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,13,'Indien Wij het wilden, zouden Wij aan elke ziel haar leiding kunnen geven, maar Mijn woord werd bewaarheid: \"Ik zal de hel met djinn en mensen allen tezamen vullen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,14,'Ondergaat daarom (de straf) omdat gij de ontmoeting van deze Dag vergat. Voorzeker nu hebben Wij u vergeten. Ondergaat de duurzame straf voor hetgeen gij deedt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,15,'Slechts zij geloven in Onze tekenen, die, wanneer zij er aan herinnerd worden, zich met het gelaat ter aarde werpen en hun Heer verheerlijken met de lof die Hem toekomt, en die niet hoogmoedig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,16,'Zij verwijderen zich van hun bed, hun Heer in vrees en hoop aanroepende en zij doen wel met hetgeen Wij hun hebben geschonken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,17,'Maar niemand weet welke verkwikking der ogen voor hen verborgen is gehouden als beloning voor wat zij hebben gedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,18,'Zou dan de gelovige gelijk zijn aan hem die ongehoorzaam is? Zij zijn (stellig) niet gelijk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,19,'Zij, dje geloven en goede werken doen, zullen Tuinen hebben tot verblijf, als onthaal voor hetgeen zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,20,'Maar het tehuis v an de ongehoorzamen zal het Vuur zijn. Telkens wanneer zij er uit willen komen, zullen zij er weer in worden teruggedreven en hun zal worden gezegd: \"Ondergaat de straf van het Vuur die gij loochendet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,21,'Wij zullen hen inderdaad de lichtere straf doen ondergaan v��r de grotere straf opdat zij zich mogen bekeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,22,'En wie is onrechtvaardiger dan hij die door het teken van zijn Heer wordt vermaand en zich er toch van afwendt? Wij zullen de schuldigen beslist straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,23,'Voorzeker, Wij gaven Mozes het Boek - twijfel dus niet aan de ontmoeting met Hem - en Wij maakten dit tot een richtsnoer voor de kinderen van Isra�l.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,24,'En Wij stelden leiders uit hun midden aan, die het volk door Ons gebod leidden, zolang zij standvastig waren en in Onze woorden een hecht geloof hadden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,25,'Voorwaar, uw Heer zal op de Dag der Opstanding onder hen richten over hetgeen waaromtrent zij onderling verschillen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,26,'Komen zij niet tot inzicht (door het feit) dat Wij zovele geslachten v��r hen hebben vernietigd in wier woonplaatsen zij nu rondlopen? Daarin zijn zeker tekenen. Willen zij dan niet luisteren?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,27,'Hebben zij niet gezien dat Wij het water naar het droge land voeren en daardoor oogsten voortbrengen waarvan zij en hun vee eten? Willen zij dan niet inzien?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,28,'En zij (de ongelovigen) zeggen: \"Wanneer zal deze beslissing plaats vinden als gij de waarheid spreekt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,29,'Zeg: \"Op de Dag der Beslissing zal het geloof der ongelovigen hen niet baten, noch zal hun uitstel worden verleend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,32,30,'Wend u daarom van hen af, en wacht; voorzeker zij wachten ook.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,1,'O Profeet, zoek bescherming bij Allah en gehoorzaam de ongelovigen en de huichelaars niet. Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,2,'Volg hetgeen u is geopenbaard van uw Heer. Voorwaar, Allah is goed op de hoogte van alles wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,3,'En stel uw vertrouwen in Allah, want Allah is als beschermer voldoende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,4,'Allah heeft voor geen man twee harten in zijn binnenste gemaakt, noch heeft Hij uw vrouwen van wie gij wegblijft door haar moeder te noemen, tot uw moeders gemaakt, noch heeft Hij uw aangenomen zonen tot uw (werkelijke) zonen gemaakt. Dat is slechts een woord dat men uit, maar Allah spreekt de waarheid, en Hij wijst de weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,5,'Noemt hen bij hun vaders naam dat is billijker in de ogen van Allah. Maar als gij hun vader niet kent, dan zijn zij uw broeders in het geloof en uw vrienden, en er is geen zonde voor u in datgene waarin gij u vergist, maar wel in hetgeen uw hart zich heeft voorgenomen. Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,6,'De Profeet is dichter bij de gelovigen dan zij zelven, en zijn vrouwen zijn hun moeders. En bloedverwanten zijn nader bij elkander volgens het Boek van Allah, dan de gelovigen en de Mohadjirien tenzij gij uw vrienden een gunst bewijst. Dit is in het Boek neergeschreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,7,'En toen Wij met de profeten een verbond sloten: met u, met Noach, Abraham, Mozes, en Jezus de zoon van Maria, sloten wij een hecht verbond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,8,'Opdat Hij de waarachtigen over hun waarachtigheid moge ondervragen. En voor de ongelovigen heeft Hij een pijnlijke straf bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,9,'O, gij die gelooft, herinnert u Allah´s gunst, aan u bewezen, toen er legers tegen u opkwamen en Wij tegen hen een wind zonden en legers die gij niet zaagt. En Allah ziet wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,10,'Toen zij over u kwamen van boven en van beneden, en toen uw ogen staarden en het hart in de keel klopte, en gij over Allah allerlei gedachten koesterdet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,11,'Toen werden de gelovigen beproefd en zij werden hevig geschokt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,12,'En toen de huichelaars en zij in wier hart een ziekte is, zeiden: \"Wat Allah en Zijn boodschapper ons beloofden was slechts bedrog.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,13,'En toen een gedeelte van hen zei: \"O volk van Jasrab (Madinah), gij kunt hier geen stand houden, keert daarom terug.\" En een gedeelte vroeg zelfs om toestemming van de Profeet, zeggende: \"Onze huizen staan aan de vijand bloot.\" Deze waren echter niet blootgesteld, zij wensten slechts te vluchten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,14,'Als men uit de omgeving bij hen zou binnendringen en hun zou worden gevraagd, onlusten te veroorzaken, zouden zij dat terstond hebben gedaan en zij zoudlen slechts weinig hebben getalmd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,15,'Waarlijk, zij hadden reeds vroeger een verbond gesloten dat zij hun rug niet zouden tonen. En er zal (hun) gevraagd worden over Allah´s verbond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,16,'Zeg: \"Vlucht zal u stellig geen voordeel brengen als gij voor de dood of voor het gevecht vlucht; slechts korte tijd zult gij genieten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,17,'Zeg: \"Wie is het, die u tegen Allah kan beschermen indien Hij u met kwaad wil treffen of barmhartigheid betonen? En zij zullen voor zich buiten Allah vriend noch helper vinden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,18,'Allah kent degenen onder u die de mensen tegenhouden, en hen, die tegen hun broeders zeggen: \"Komt naar ons toe,\" en die zich weinig met de oorlog bemoeien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,19,'Zij zijn terughoudend in hun hulp voor u. Maar als het gevaar komt, ziet gij hen naar u kijken met rollende ogen als van iemand die bezwijmt bij de doodsstrijd. En als de vrees is weggevaagd, treffen zij u met hun scherpe tong door hun zucht naar rijkdommen. Zulken hebben niet oprecht geloofd; daarom heeft Allah hun werken vruchteloos gemaakt. Dit is gemakkelijk voor Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,20,'Zij denken, dat de bondgenoten niet zijn vertrokken; en als de bondgenoten zouden wederkomen, zouden zij gaarne onder de zwervende Arabieren in de woestijn willen zijn, nieuws over u vragende. En als zij onder u waren, zouden zij weinig vechten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,21,'Voorwaar, gij hebt in de Profeet van Allah een prachtig voorbeeld voor ieder die Allah en de laatste Dag vreest, en die Allah vaak herdenkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,22,'En toen de gelovigen de scharen zagen, zeiden zij: \"Dit is wat Allah en Zijn boodschapper ons beloofden; en Allah en Zijn boodschapper spraken de waarheid.\" En dit vermeerderde slechts hun geloof en deed hun onderwerping toenemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,23,'Er zijn mensen onder de gelovigen die trouw gebleven zijn aan het verbond dat zij met Allah hebben gesloten. Er zijn enigen onder hen die hun eed hebben gehouden, en anderen die nog wachten en geenszins veranderd zijn;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,24,'Zodat Allah de waarachtigen voor hun oprechtheid moge belonen en de huichelaars straffen zoals Hij wil, of Zich tot hen in barmhartigheid wenden. Voorwaar, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,25,'Allah weerhield de ongelovigen in hun woede; zij verwierven geen voordeel. En Allah was toereikend (als Beschermer) voor de gelovigen in de slag. Allah is Sterk, Almachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,26,'En Hij deed de mensen van het Boek die hen (de vijand) hielpen uit hun vestingen komen en vervulde hun hart met ontzetting. Gij dooddet sommigen en gij naamt anderen gevangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,27,'En Hij deed u hun land, huizen en hun rijkdommen erven en ook een land waarop gij nog nooit een voet had gezet. Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,28,'O profeet! Zeg aan uw vrouwen, \"Als gij het leven dezer wereld en zijn luister wenst, komt dan, ik zal u een geschenk geven en u op een grootmoedige manier vrij laten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,29,'Maar indien gij Allah en Zijn boodschapper en het tehuis van het Hiernamaals wenst, dan heeft Allah waarlijk voor degenen onder u die goed doen, een grote beloning.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,30,'O vrouwen van de profeet! Als iemand onder u schuldig is aan een openbaar onbetamelijk gedrag zal haar straf worden verdubbeld. En dit is gemakkelijk voor Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,31,'Maar wie van u aan Allah en Zijn boodschapper gehoorzaamt en goede werken doet, haar zullen Wij dubbel belonen en Wij hebben voor haar een waardige voorziening bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,32,'O vrouwen van de profeet, gij zijt niet zoals een andere vrouw. Indien gij godvruchtig zijt, spreekt dan niet op een verleidelijke manier, anders zal hij in wiens hart ziekte is, verwachtingen koesteren; maar spreekt een oprechte taal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,33,'Blijft in uw huizen en stelt uw schoonheid niet ten toon als in de vroegere dagen der onwetendheid; leeft het gebed na, en betaalt de Zakaat en gehoorzaamt Allah en Zijn boodschapper. O huisgenoten, Allah wenst alleen onreinheid van u te verwijderen, en u schoon en zuiver te maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,34,'En herinnert u de woorden van Allah en de wijsheid die in uw huizen wordt verkondigd; want Allah is Aldoordringend, Alkennend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,35,'Voorwaar, de Moslims en de Moslima´s en de gelovige mannen en vrouwen, de gehoorzame mannen en vrouwen, de waarachtige mannen en vrouwen, de standvastige mannen en vrouwen, de mannen en de vrouwen die nederig zijn, de mannen en de vrouwen die aalmoezen geven, de mannen en de vrouwen die vasten, de mannen en de vrouwen die hun kuisheid bewaren, de mannen en de vrouwen die Allah vaak gedenken - voor zulken heeft Allah vergiffenis en een grote beloning bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,36,'En het betaamt de gelovige man of vrouw niet, wanneer Allah en Zijn boodschapper over een zaak hebben beslist, dat er voor hen een keuze zou zijn in die zaak. En wie Allah en Zijn boodschapper niet gehoorzaamt, is zeker klaarblijkelijk afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,37,'En herinnert u, toen gij tot hem, wie Allah gunsten had bewezen en wie gij ook gunsten had bewezen, zeidet: \"Behoud uw vrouw voor u en vrees Allah.\" Gij verborgt in uw hart wat Allah aan het licht zou brengen, en gij vreesdet de mensen terwijl Allah er meer recht op heeft dat gij Hem zoudt vrezen. Toen Zaid van haar scheidde, verenigden Wij haar met u in de echt, opdat er voor de gelovigen geen bezwaar mocht zijn ten opzichte van de vrouwen van hun aangenomen zonen, als zij van haar zijn gescheiden. Allah´s gebod moet worden nageleefd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,38,'Er moet voor de profeet geen bezwaar zijn betreffende hetgeen Allah voor hem geordend heeft. Dit is ook de handelwijze van Allah met hen die v��rdien zijn heengegaan - en het gebod van Allah is een vastgestelde verordening.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,39,'Degenen, die Allah´s boodschappen brengen, vrezen Hem en buiten Allah niemand. Allah is Toereikend om te verrekenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,40,'Mohammed is niet de vader van ��n uwer mannen, maar de boodschapper van Allah en het zegel der profeten; Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,41,'O, gij die gelooft! Gedenkt Allah veelvuldig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,42,'En prijst Zijn Heiligheid ´s morgens en ´s avonds.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,43,'Hij is het Die u zegent en ook Zijn engelen doen dit, opdat Hij u van de duisternissen tot het licht moge leiden. En Hij is voor de gelovigen Genadig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,44,'De Dag waarop zij Hem zullen ontmoeten zal hun groet \"Vrede\" zijn. En Hij heeft hun een eervolle beloning bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,45,'O, profeet. Wij hebben u als getuige, drager van blijde tijdingen en waarschuwer gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,46,'En als een roeper tot Allah door Zijn gebod, en als een stralende zon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,47,'Verkondig derhalve aan de gelovigen het blijde nieuws dat zij van Allah grote genade zullen ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,48,'En gehoorzaam de ongelovigen en de huichelaars niet en sla geen acht op hun grievende taal, stel uw vertrouwen in Allah, want Allah is Toereikend als Beschermer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,49,'O, gij die gelooft! Als gij een gelovige vrouw huwt en daarna van haar scheidt voordat gij haar hebt aangeraakt dan behoeft zij om uwentwille geen wachtperiode te berekenen. Schenkt haar daarom een gave en laat haar op een grootmoedige wijze vrij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,50,'O profeet, Wij hebben voor u uw vrouwen wettig gemaakt, aan wie gij haar huwelijksgiften hebt gegeven, en degenen die uw rechterhand bezit van haar, die Allah u als een oorlogsbuit heeft gegeven en de dochters van uw ooms en tantes van vaderszijde en de dochters van uw ooms en tantes van moederszijde die met u emigreerden, en elke gelovige vrouw indien zij zich aan de profeet toevertrouwt als de profeet haar wenst te huwen; dit is slechts voor u en niet voor de gelovigen. Wij hebben reeds kenbaar gemaakt wat Wij omtrent hun (gelovige) vrouwen en degenen die hun rechterhand bezit, hebben verordend, opdat er geen blaam u aankleve. Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,51,'Gij moogt verlaten wie gij wilt en tot u nemen wie gij wilt, er rust geen blaam op u wanneer gij haar terugneemt van wie gij u afzijdig hebt gehouden. Dit is het meest passend om hen verust te stellen, zodat zij niet treuren en allen tevreden mogen zijn met hetgeen gij haar geeft. En Allah weet wat in uw hart is; Allah is Alwetend, Verdraagzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,52,'Het is u hierna niet toegestaan vrouwen te huwen noch haar voor andere vrouwen te ruilen, zelfs al behaagt u haar schoonheid, met uitzondering van haar die uw rechterhand mocht bezitten. En Allah houdt de wacht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,53,'O, gij die gelooft! Gaat de huizen van de profeet niet binnen tenzij gij uitgenodigd wordt tot een maaltijd, doch niet wachtend tot deze gereed is. Wanneer gij zijt uitgenodigd, komt dan binnen; en wanneer gij gegeten hebt vertrekt dan en blijft niet praten. Dat is lastig voor de profeet; hij is verlegen voor u, maar Allah aarzelt niet om de waarheid (te zeggen). En als gij haar (zijn vrouwen) om iets vraagt, vraagt het dan van achter het gordijn. Dat is reiner voor uw hart en haar hart. En het past u niet de boodschapper van Allah lastig te vallen, noch dat gij ooit zijn vrouwen na hem zoudt huwen. Dat zou in de ogen van Allah inderdaad een grote (belediging) zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,54,'Of gij iets openbaar maakt of verbergt, waarlijk Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,55,'Er rust op haar (uw vrouwen) geen schuld als zij zich tonen aan haar vaders of haar zonen of haar broeders of de zonen van haar broeders, of de zonen van haar zusters en hun vrouwen of hun ondergeschikten. Maar vreest Allah. Voorwaar, Allah is Getuige van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,56,'Allah en Zijn engelen zenden zegeningen over de profeet. O, gij die gelooft, zendt zegeningen over hem en wenst hem vrede met alle eerbied toe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,57,'Betreffende hen, die Allah en Zijn boodschapper lastig vallen, Allah heeft hen in deze wereld en in het Hiernamaals vervloekt en heeft een vernederende straf voor hen bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,58,'En zij, die gelovige mannen en vrouwen lastig vallen zonder dat dezen er schuld aan hebben, dragen voorzeker de schuld van laster en een openlijke zonde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,59,'O profeet! Zeg aan uw vrouwen en uw dochters en de vrouwen der gelovigen dat zij een gedeelte van haar omslagdoeken over haar (hoofd) laten hangen. Dit is beter, opdat zij mogen worden onderscheiden en niet lastig worden gevallen. En Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,60,'Indien de huichelaars en degenen in wier hart een ziekte is en degenen die opschudding in de stad veroorzaken, niet ophouden, zullen Wij u zeker tegen hen in beweging brengen; dan zullen zij slechts voor een korte tijd in uw nabijheid mogen vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,61,'Vervloekt zijn zij; waar zij zich ook bevinden zullen zij worden gegrepen en gedood.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,62,'Voorwaar, zo was Allah´s handelwijze met degenen die voordien zijn heengegaan en in Allah´s handelwijze zult gij geen verandering vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,63,'De mensen vragen u over het Uur. Zeg: \"De kennis er van is slechts bij Allah,\" gij weet het niet; het kan zijn dat het Uur nabij is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,64,'Allah heeft de ongelovigen zeker vervloekt en heeft een laaiend Vuur voor hen bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,65,'Daarin zullen zij voor lange tijd vertoeven en zullen vriend noch helper vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,66,'De Dag waarop hun gezicht zich in het Vuur zal wentelen zullen zij zeggen: \"O, hadden wij slechts Allah en Zijn boodschapper gehoorzaamd!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,67,'En zij zullen zeggen: \"Onze Heer, wij gehoorzaamden onze leiders en onze grote mannen maar zij deden ons van de rechte weg afdwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,68,'Onze Heer, geef hun een dubbele straf en vloek hen met een zware vloek.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,69,'O, gij die gelooft! weest niet zoals degenen die Mozes ergerden! Allah echter zuiverde hem van hetgeen zij zeiden. En hij was in aanzien bij Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,70,'O, gij die gelooft! Vreest Allah en spreekt de waarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,71,'Hij zal uw werken goed voor u maken en u uw zonden vergeven. En wie Allah en Zijn boodschapper gehoorzaamt, heeft zeker een grote overwinning behaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,72,'Voorwaar, Wij boden de hemelen, de aarde en de bergen aan, hun (iets) toe te vertrouwen, maar zij weigerden dit te dragen en vreesden er voor, maar de mens nam het op zich. Inderdaad, hij is zeer onrechtvaardig (jegens zichzelf), onwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,33,73,'Het gevolg er van is dat Allah huichelachtige mannen en vrouwen, en afgodendienaren en afgodendienaressen zal straffen. En Allah wendt zich in barmhartigheid tot gelovige mannen en vrouwen, en Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,1,'Alle roem komt Allah toe aan Wie alles behoort wat in de hemelen en op aarde is en Hem komt alle Lof toe in het Hiernamaals; Hij is de Alwijze, de Alkennende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,2,'Hij weet alles wat de aarde binnengaat en wat er uit voortkomt en wat van de hemel nederdaalt en wat er naar opstijgt; Hij is de Barmhartige, de Vergevensgezinde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,3,'De ongelovigen zeggen: \"Het Uur zal niet over ons komen.\" Zeg: \"Ja, bij mijn Heer, de Kenner van het onzichtbare, het zal beslist over u komen!\" Voor Hem is niets in de hemelen of op de aarde verborgen, zelfs niet het gewicht van een atoom, noch bestaat er iets, groter of minder dan dit of het staat in een duidelijk Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,4,'Opdat Hij degenen. die geloven en goede werken doen, moge belonen. Zulken zijn het die vergiffenis en een eervol onderhoud zullen ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,5,'Maar zij die Onze woorden trachten te verijdelen, zullen een pijnlijke straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,6,'En zij, wie kennis is gegeven, zien, dat alles wat u van uw Heer is geopenbaard, de Waarheid is en leidt op het pad van de Almachtige, de Geprezene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,7,'En de ongelovigen zeggen: \"Zullen wij u een man aanwijzen die u meedeelt dat wanneer gij door bederf uiteen valt, gij dan opnieuw zult worden geschapen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,8,'\"Heeft hij een leugen uitgedacht over Allah, of is hij een waanzinnige?\" Neen, zij die niet in het Hiernamaals geloven zullen onder de straf lijden, en zij zijn ver afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,9,'Hebben zij dan niet gezien naar hetgeen v��r hen en achter hen, van de hemel en van de aarde is? Indien het Ons behaagde konden Wij de aarde met hen doen inzinken, of een deel van de hemel op hen doen nedervallen. Hierin is waarlijk een teken voor elke dienaar, die zich tot God wendt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,10,'En voorwaar, Wij schonken David overvloed van Ons Zelf en zeiden: \"O gij bergen, alsmede gij vogelen, verheerlijkt Allah met hem!\" En Wij maakten het ijzer week voor hem,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,11,'Zeggende: \"Maak u mali�nkolders van volle lengte en meet de schalmen af en verricht goede werken: want Ik zie alles wat gij doet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,12,'En aan Salomo onderwierpen Wij de wind; de ochtendreis en de avondreis daarvan was een maand, en Wij deden een stroom van gesmolten koper voor hem vloeien. Er waren enige onder de djinn die door het gebod van zijn Heer onder hem werkten, en Wij zeiden: indien iemand van hen zich van Ons gebod zou afkeren, zouden Wij hem de straf van het brandend Vuur doen ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,13,'Zij maakten voor hem wat hij wenste, paleizen en standbeelden, en waterreservoirs en grote pannen: \"Betoon, O Huis van David, dankbaarheid,\" doch slechts weinigen van Mijn dienaren zijn dankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,14,'En toen Wij zijn (Salomo´s) dood hadden veroorzaakt, deed niets hen (djinn) zijn dood beseffen, dan een worm der aarde die zijn staf (macht) opvrat en toen die nederviel, bemerkten de dijnn duidelijk, dat, indien zij het onzichtbare gekend hadden, zij niet zolang in een toestand van vernederende kwelling zouden zijn gebleven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,15,'En voorzeker, er was voor Saba een teken in hun woonplaatsen, twee tuinen aan de rechter- en aan de linkerhand; er werd gezegd: \"Eet van de spijzen van uw Heer en weest Hem dankbaar. Een schone stad en een vergevende Heer!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,16,'Maar zij wendden zich af; daarom zonden Wij een sterke overstroming over hen en Wij veranderden hun tuinen in twee tuinen met bitter fruit en tamarisken en weinig lotusbloemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,17,'Daarmee vergolden Wij hen wegens hun ondankbaarheid, en Wij straften niemand behalve de ondankbaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,18,'En Wij plaatsten tussen hen en de steden die Wij hadden gezegend, bloeiende steden die aanzienlijk waren, en Wij maakten het reizen tussen die steden gemakkelijk; \"Reist er dagen en nachten veilig doorheen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,19,'Maar zij zeiden: \"Onze Heer, maak langere afstanden tussen onze reizen.\" En zij deden zich daarmee onrecht aan, daarom maakten Wij hen tot sagen en legenden, terwijl Wij hen volledig hadden verpletterd. Daarin zijn zeker tekenen voor een ieder die geduldig en dankbaar is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,20,'En Iblies bewees inderdaad de waarheid van zijn mening over hen en zij volgden hem, behalve een deel der ware gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,21,'En hij had over hen geen macht, maar Wij wilden degenen, die in het Hiernamaals geloofden van hen onderscheiden die er aan twijfelden. En uw Heer houdt de wacht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,22,'Zeg: \"Roept degenen aan, waarvan gij beweert dat zij Goden zijn buiten Allah. Zij hebben zelfs geen macht over het gewicht van een atoom in de hemelen of op aarde noch hebben zij enig aandeel aan beiden, noch heeft Hij een enkele helper onder hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,23,'Geen voorspraak geldt bij Hem, behalve voor degenen aan wie Hij het toestaat, tot zij, wanneer de vrees van hun hart wordt weggenomen, zeggen: \"Wat zeide uw Heer?\" Zij zullen antwoorden: \"De Waarheid.\" En Hij is de Hoogverhevene, de Grote.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,24,'Zeg: \"Wie geeft u uw levensonderhoud van de hemelen en de aarde?\" Zeg: \"Allah.\" Zijn wij of gij op het rechte pad of in klaarblijkelijke dwaling?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,25,'Zeg: \"Gij zult niet worden ondervraagd omtrent wat wij misdeden, noch zullen wij worden ondervraagd omtrent hetgeen gij doet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,26,'Zeg: \"Onze Heer zal ons allen tezamen brengen; dan zal Hij onder ons richten met rechtvaardigheid. Hij is de Rechter, de Alwetende.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,27,'Zeg: \"Toont mij hen, die gij met Hem vereenzelvigt! Geenszins! Hij is Allah, de Almachtige, de Alwijze.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,28,'En Wij hebben u slechts gezonden als een brenger van blijde tijdingen en een waarschuwer voor het gehele mensdom; maar de meeste mensen begrijpen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,29,'En zij zeggen: \"Wanneer zal deze belofte worden vervuld als gij de waarheid spreekt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,30,'Antwoord: \"Er is voor u een vastgestelde tijd, gij kunt geen uur eerder gaan of langer blijven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,31,'En de ongelovigen zeggen: \"Wij zullen stellig aan deze verkondiging niet geloven, noch in hetgeen er aan voorafging.\" En kondet gij slechts zien wanneer de onrechtvaardigen voor hun Heer zullen worden gebracht, terwijl zij de schuld op elkander zullen werpen. De zwakken zullen tot de hoogmoedigen zeggen: \"Waart gij niet geweest dan zouden wij zeker tot de gelovigen behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,32,'De hoogmoedigen zullen tot de zwakkelingen zeggen: \"Waren wij het die u van de leiding afwendden, nadat zij tot u was gekomen? Neen, gij draagt zelf de schuld hiervan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,33,'En de zwakkelingen zullen tot de hoogmoedigen zeggen: \"Maar het kwam door uw listige plannen, bij dag en nacht beraamd, waarbij gij ons geboodt niet in Allah te geloven en gelijken aan Hem op werpen.\" En zij zullen berouw tonen wanneer zij de straf zullen zien. En Wij zullen zware kettingen leggen om de halzen der ongelovigen. Zij zullen slechts worden gestraft voor hetgeen zij deden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,34,'En Wij zonden geen waarschuwer tot een stad of de rijken er van zeiden: \"Waarlijk, wij willen niet geloven in hetgeen waarmee gij zijt gezonden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,35,'En zij zeiden: \"Wij hebben meer rijkdommen en kinderen en wij zullen niet worden gestraft.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,36,'Zeg: \"Waarlijk, mijn Heer vergroot en bekrimpt het levensonderhoud voor wie Hij wil, maar de meeste mensen begrijpen het niet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,37,'Noch uw rijkdommen noch uw kinderen kunnen u tot Onze nabijheid brengen, maar zij die geloven en goede werken doen, zullen een veelvuldige beloning ontvangen, voor hetgeen zij deden en zullen veilig zijn in verheven woningen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,38,'En zij, die Onze woorden trachten krachteloos te maken zullen de straf ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,39,'Waarlijk, mijn Heer vergroot en bekrimpt het levensonderhoud voor wie Hij wil van Zijn dienaren. En wat gij ook (weldadig) besteedt, Hij zal het teruggeven en Hij is de beste Voorziener.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,40,'En de Dag waarop Hij hen allen tezamen zal verzamelen, zal Hij tot de engelen zeggen: \"Plachten dezen u te aanbidden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,41,'Zij zullen antwoorden: \"Glorie zij U! Gij zijt onze Vriend, niet zij. Neen, zij aanbaden de djinn; in hen geloofden de meesten hunner.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,42,'(God zal zeggen) \"Heden hebt gij geen macht om elkander goed of kwaad te doen.\" En Wij zullen tot de onrechtvaardigen zeggen: \"Ondergaat de straf van het Vuur die gij placht te verloochenen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,43,'En wanneer Onze duidelijke woorden aan hen zijn verkondigd, zeggen zij: \"Dit is slechts een man, die u van hetgeen uw vaderen aanbaden, wenst af te leiden.\" En zij zeggen: \"Dit is slechts een verzonnen leugen.\" En de ongelovigen zeggen van de Waarheid als deze tot hen komt, \"Dit is niets dan zuiver tovenarij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,44,'En Wij gaven hun geen boek dat zij bestudeerden, noch zonden Wij hun een waarschuwer v��r u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,45,'Zij, die v��r hen waren verloochenden ook - en zij hebben zelfs geen tiende bereikt van hetgeen Wij hun gaven - zij verloochenden Mijn Boodschappers en hoe (streng) was dan Mijn afkeuring.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,46,'Zeg: \"Ik raad u aan slechts ��n ding te doen; dat gij paarsgewijze en alleen voor Allah staat en dan nadenkt. En (gij zult weten) dat er geen krankzinnigheid in uw metgezel is; hij is voor u slechts een waarschuwer v��r een strenge straf (komt).\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,47,'Zeg: \"Welk loon ik ook van u vraag, dat is voor u. Mijn loon is bij Allah; en Hij is Getuige van alle dingen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,48,'Zeg: \"Waarlijk, mijn Heer verspreidt de Waarheid. Hij is de Kenner van al het onzichtbare.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,49,'Zeg: \"De Waarheid is gekomen en de leugen zal niet meer beginnen, noch wederkeren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,50,'Zeg: \"Als ik dwaal, dwaal ik slechts door mijzelf; en indien ik goed geleid ben, is het door hetgeen mijn Heer mij heeft geopenbaard. Voorwaar, Hij is de Alhorende, de Nabijzijnde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,51,'Kondet gij (hen) maar zien, wanneer zij schrikken! Dan zal er geen ontvluchten zijn als zij van nabij worden gegrepen!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,52,'Dan zullen zij zeggen: \"Wij geloven er in!\" Maar hoe zal het bereiken er van voor hen mogelijk zijn van z� ver,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,53,'Terwijl zij voorheen hebben verworpen? En zij uiten gissingen omtrent het onzichtbare van een verre plaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,34,54,'En er is een hinderpaal (afscheiding) gemaakt tussen hen en hetgeen zij verlangen zoals met hun gelijken v��r hen was gedaan. Zij verkeerden inderdaad in een verontrustende twijfel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,1,'Alle lof komt Allah toe, de Schepper der hemelen en der aarde, Die de engelen tot boodschappers maakt met twee, drie en vier vleugelen. En Hij voegt aan de schepping toe wat Hij wil; want Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,2,'Wat Allah de mens aan barmhartigheid schenkt, is door niemand tegen te houden; en wat Hij terug houdt, kan buiten Hem, niemand schenken; Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,3,'O mensen, herinnert u de gunst van Allah jegens u. Is er een andere Schepper buiten Allah die u levensonderhoud geeft van de hemelen en de aarde? Er is geen God naast Hem. Waarheen wordt gij dan afgewend?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,4,'En indien zij u verloochenen (bedenk dan) dat de boodschappers v��r u ook werden verloochend; maar tot Allah worden alle dingen teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,5,'O mensen, de belofte van Allah is voorzeker waarachtig. Laat het tegenwoordige leven u daarom niet misleiden, noch laat de aarts-bedrieger u van Allah afleiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,6,'Voorwaar, Satan is een vijand van u, behandelt hem daarom als vijand. Hij roept zijn volgelingen slechts opdat zij bewoners van het brandende Vuur mogen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,7,'Er is een strenge straf voor hen die niet geloven. Maar er is vergiffenis en een grote beloning voor de gelovigen die goede werken doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,8,'Hij wiens boze daden schoonschijnend zijn gemaakt, zodat hij deze als goed beschouwt (kan de leiding vinden). Zeker, Allah laat dwalen wie Hij wil en leidt wie Hij wil. Laat uw ziel dus niet wegkwijnen uit verdriet over hen. Voorzeker, Allah weet wat zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,9,'En het is Allah Die de winden zendt zodat zij wolken doen opstijgen, dan drijven Wij deze (de wolken) naar een verdord land en geven leven aan de aarde na haar dood. Zo is de Opstanding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,10,'Wie eer wenst (wete), dat alle eer aan Allah behoort. Tot Hem stijgt het reine woord en de goede daad verheft het (tot Hem). En zij, die slechte plannen maken, hun wacht een strenge straf en hun plan zal te niet worden gedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,11,'Allah schiep u uit stof, dan uit een levenskiem, daarna maakte Hij u tot paren. En geen vrouw wordt zwanger of brengt voort, zonder dat Hij het weet. En niemands leven wordt verkort of verlengd zonder dat het in het Boek is vermeld. Voorzeker, dit is gemakkelijk voor Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,12,'De twee wateren zijn niet gelijk; het ene zoet, smakelijk en goed om te drinken, en het andere zout en bitter. En uit elk eet gij vlees en vindt gij sieraden die gij draagt. En gij ziet er schepen die de golven door klieven opdat gij van Zijn overvloed moogt zoeken, en opdat gij dankbaar zult zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,13,'Hij dompelt de nacht in de dag en de dag in de nacht. En Hij heeft de zon en de maan in dienst gesteld; elk volgt haar baan, voor een vastgestelde termijn. Alzo is Allah, uw Heer, van Hem is het Koninkrijk en zij, die gij buiten Hem aanroept, bezitten niets.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,14,'Als gij hen aanroept, zullen zij uw roep niet horen en indien zij uw roep horen, zullen zij u niet kunnen antwoorden. En op de Dag der Opstanding zullen zij uw afgoderij verwerpen. Niemand kan u (omtrent de waarheid) inlichten zoals de Alkennende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,15,'O, gij mensen, gij zijt afhankelijk van Allah, maar Allah is de Onafhankelijke, de Geprezene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,16,'Als Hij het wilde, zou Hij u kunnen wegnemen en een nieuwe schepping voortbrengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,17,'Dat is voor Allah niet moeilijk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,18,'Geen lastdragende kan de last van een ander dragen en indien een zwaarbelaste (een ander) roept tot (verlichting van) zijn last, zal er niets van kunnen worden overgenomen, zelfs al is hij een bloedverwant; gij kunt slechts hen waarschuwen, die hun Heer in het verborgene vrezen en het gebed onderhouden. En wie zich reinigt, reinigt zich alleen in zijn eigen belang en tot Allah is de terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,19,'De blinde is niet gelijk aan de ziende;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,20,'Noch is de duisternis gelijk aan het licht;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,21,'Noch zijn de schaduw en de hitte gelijk;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,22,'Noch zijn de levenden gelijk aan de doden. Voorzeker, Allah doet hen horen die Hij wil, maar gij kunt degenen die in hun graven zijn, niet doen horen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,23,'Gij (profeet) zijt slechts een waarschuwer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,24,'Voorwaar, Wij hebben u met de Waarheid gezonden als drager van blijde tijdingen en als waarschuwer; en er is geen volk waaronder zich geen boodschapper heeft bevonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,25,'Indien dezen u verloochenen, verloochenden ook zij, die voor hen waren. Hun boodschappers kwamen tot hen met duidelijke bewijzen en met de Geschriften en met een verlichtend Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,26,'Dan greep Ik de ongelovigen aan en hoe (vreselijk) was Mijn afkeuring!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,27,'Hebt gij niet gezien, dat Wij water van de hemel nederzenden en dat Wij daardoor vruchten voortbrengen van verschillende kleuren (en soorten); en in de bergen zijn streken van wit en rood, van donker of ravenzwart en van nog verschillende tinten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,28,'Ook mensen, beesten, vee zijn van verschillende kleur. Alleen Zijn dienaren die kennis bezitten, vrezen Allah. Voorwaar, Allah is Almachtig, Vergevensgezind.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,29,'Waarlijk, zij die het Boek (de Koran) van Allah voordragen en het gebed naleven en heimelijk of openlijk geven van hetgeen Wij hun hebben geschonken, mogen hopen op een winst die nooit zal vergaan;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,30,'Opdat Hij hun de volle beloning moge geven, er uit Zijn overvloed aan toevoegende. Hij is zeker Vergevensgezind, Waarderend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,31,'En hetgeen Wij u hebben geopenbaard van het Boek is de Waarheid, vervullend hetgeen voordien (geopenbaard) was. Voorzeker, Allah kent en doorziet Zijn dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,32,'Dan gaven Wij het Boek als erfdeel aan diegenen Onzer dienaren die Wij uitkozen. En onder hen zijn er die zich zelven te kort doen, anderen die de middenweg bewandelen en nog anderen die in goedheid en deugd uitmunten naar Allah´s gebod. Dat is de grote genade.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,33,'In tuinen der eeuwigheid zullen zij binnengaan, zij zullen er in worden getooid met gouden armbanden en met paarlen; en hun kleding zal van zijde zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,34,'En zij zullen zeggen: \"Alle lof zij Allah, Die droefheid van ons heeft weggenomen. Onze Heer is voorzeker Vergevensgezind; Waarderend.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,35,'\"Hij, Die ons door Zijn genade deze verbligfplaats heeft toegewezen, waarin ons geen last, noch vermoeienis raakt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,36,'Maar voor de ongelovigen is het Vuur der hel. Voor hen zal de dood niet worden verordend opdat zij mochten sterven, noch zal de straf er van voor hen worden verlicht. Alzo straffen Wij iedere ondankbare.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,37,'En zij zullen er in schreeuwen, zeggende: \"Onze Heer, haal ons er uit, wij zullen goede werken doen, anders dan wij vroeger deden.\" (Men zal hun antwoorden): \"Gaven Wij u niet een leven, lang genoeg dat wie wilde nadenken, daarin kon nadenken, bovendien kwam een waarschuwer tot u. Ondergaat daarom de straf, want voor de boosdoeners is er geen helper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,38,'Voorwaar, Allah kent de geheimen der hemelen en der aarde. Waarlijk, Hij weet wat in de harten leeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,39,'Hij is het, Die u tot stedehouders op aarde heeft gemaakt. Hij die niet gelooft, zijn ongeloof zal tegen hem zijn en het ongeloof der ongelovigen doet hen slechts in weerzinwekkendheid toenemen in de ogen van hun Heer, en het ongeloof der ongelovigen doet hen slechts toenemen in verlies.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,40,'Zeg: \"Licht (mij) in over de goden, die gij naast Allah aanroept- Toont mij aan hetgeen zij van de aarde hebben geschapen. Of hebben zij een aandeel aan de hemelen?\" Hebben Wij hun een Boek gegeven waaruit zij een bewijs hebben? Neen, de boosdoeners beloven elkander slechts bedrog.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,41,'Voorzeker, Allah houdt de hemelen en de aarde in stand opdat zij niet vergaan. En indien zij uit elkaar zouden vallen, zou niemand buiten Hem ze bij elkander kunnen houden. Voorwaar, Hij is Verdraagzaam, Vergevensgezind.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,42,'Zij zweren bij Allah hun plechtigste eden, dat indien een waarschuwer tot hen zou komen, zij de leiding beter zouden volgen dan andere volkeren. Maar toen een waarschuwer tot hen kwam, deed het hen slechts in afkeer toenemen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,43,'Evenals in aanmatiging op aarde en in het smeden van boze plannen. Maar het slechte komplot is een val voor hen alleen die het maken. Verwachten zij slechts de handelwijze waarmee de vroegere (volkeren) werden behandeld? Gij zult in de handelwijze van Allah nooit een verandering aantreffen, noch zult gij de handelwijze van Allah ooit gewijzigd vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,44,'Hebben zij niet op aarde gereisd en gezien hoe het einde was van degenen die v��r hen leefden? En dezen waren sterker in macht dan zij. Er is niets in de hemelen en op aarde dat Allah kan overweldigen, Hij is de Alwetende, de Almachtige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,35,45,'En indien Allah de mensen zou straffen voor hetgeen zij doen, zou Hij geen schepsel op de oppervlakte ervan achterlaten; maar Hij schenkt hun uitstel tot een vastgestelde tijd; en wanneer die vastgestelde tijd komt: voorwaar Allah kent Zijn dienaren goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,1,'Jaa Sien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,2,'Bij de Koran, die vol van Wijsheid is,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,3,'Gij zijt inderdaad ��n der boodschappers');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,4,'Op het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,5,'Dit is een openbaring van de Almachtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,6,'Opdat gij een volk moogt waarschuwen welks vaderen niet zijn gewaarschuwd en dat achteloos leeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,7,'Het Woord heeft zich reeds bewaarheid ten opzichte van de meesten hunner, want zij geloven niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,8,'Wij hebben om hun hals ijzeren banden gelegd die tot aan hun kin reiken, zodat hun hoofd omhoog geheven blijft,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,9,'En Wij hebben een hinderpaal v��r hen en een hinderpaal achter hen geplaatst en Wij hebben hen gesluierd, zodat zij niet kunnen zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,10,'En het is hun hetzelfde of gij hen waarschuwt of niet; zij willen niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,11,'Gij kunt slechts hem waarschuwen die de vermaning zou willen volgen en de Barmhartige in het verborgene vrezen. Geef hem daarom blijde tijdingen van vergiffenis en een ruime beloning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,12,'Voorzeker, Wij zijn het Die de doden doen herleven, en wat zij doen, optekenen evenals de sporen die zij nalaten en Wij hebben alle dingen in een duidelijk boek geschreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,13,'Geef hun de gelijkenis van de bewoners ener stad , to en de boodschappers tot haar kwamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,14,'Wij zonden tot hen twee boodschappers maar zij verloochenden dezen waarop wij hen met een derde versterkten en zij zeiden: \"Waarlijk, wij zijn tot u gezonden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,15,'Zij (de bewoners) antwoordden: \"Gij zijt slechts mensen zoals wij en de Barmhartige heeft u niets geopenbaard; gij liegt slechts.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,16,'Zij zeiden: \"Onze Heer weet dat wij inderdaad tot u zijn gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,17,'Op ons rust slechts de duidelijke verkondiging (der boodschap).\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,18,'Het volk zeide: \"Waarlijk, wij beschouwen u als een slecht voorteken; als gij niet ophoudt, zullen wij u gewis stenigen en een pijnlijke straf zal zeker onzerzijds over u komen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,19,'Zij antwoordden: \"Uw onheil is bij u. Zegt gij dit omdat gij vermaand zijt? Neen, gij zijt een volk dat alle perken te buiten gaat.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,20,'En er kwam een man aanhollen van het verste gedeelte der stad; hij zeide: \"O mijn volk, volg de boodschappers;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,21,'Volg hen, die van u geen beloning vragen en die goed geleid zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,22,'En welke reden heb ik, dat ik Hem, Die mij schiep en tot Wie gij zult worden teruggebracht, niet zou aanbidden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,23,'Zal ik anderen tot goden nemen naast Hem? Indien de Barmhartige kwaad met mij zou voorhebben, zou hun bemiddeling mij niets baten noch kunnen zij mij redden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,24,'Dan zou ik inderdaad in openlijke dwaling verkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,25,'Ik geloof in uw Heer, luistert daarom naar mij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,26,'Er werd gezegd: \"Ga het paradijs binnen.\" Hij riep uit: \"O, als mijn volk slechts wist,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,27,'Hoe mijn Heer mij vergiffenis heeft geschonken en mij tot een der ge�erden heeft gemaakt!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,28,'En Wij zonden na hem geen schare (van engelen) uit de hemel neder (tot zijn volk) noch zenden Wij die ooit (op die wijze) neder.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,29,'Het was slechts een enkele kreet en ziet; zij waren als uitgeblust.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,30,'Wee, over de mensen: er komt geen boodschapper tot hen of zij bespotten hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,31,'Hebben zij niet gezien, hoeveel geslachten Wij v��r hen hebben vernietigd, die niet tot hen terugkeren?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,32,'Maar gewis, allen zullen tezamen voor Ons worden gebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,33,'En de dorre aarde is voor hen een teken; Wij doen deze herleven en brengen graan uit haar voort, waarvan zij eten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,34,'En Wij hebben er tuinen van dadelpalmen en druiven aangelegd en Wji deden er bronnen ontspringen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,35,'Opdat zij van de vruchten daarvan mogen eten, en genieten van hetgeen hun handen toebereiden. Willen zij dan niet dankbaar zijn?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,36,'Glorie zij Hem, Die alles in paren schiep van hetgeen op aarde groeit en van hen zelf en van hetgeen zijn nog niet kennen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,37,'En voor hen is de nacht een teken. Wij nemen de dag weg en ziet! zij zijn in duisternis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,38,'En de zon beweegt zich naar haar bestemming. Dat is het gebod van de Almachtige, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,39,'En voor de maan hebben Wij fasen bepaald tot zij als een oude tak van een palmboom wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,40,'De zon mag de maan niet achterhalen noch kan de nacht de dag voorbijstreven. Zij zweven elk in hun eigen baan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,41,'En het is voor hen een teken, dat Wij hun nakomelingen in het geladen schip dragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,42,'En Wij zullen voor hen nog iets dergelijks scheppen, waarop zij zullen varen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,43,'En indien Wij willen, zullen Wij hen doen verdrinken, er zal dan voor hen geen helper zijn noch kunnen zij gered worden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,44,'Dan door Onze barmhartigheid en als tijdelijk genot (voor hen op aarde).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,45,'En wanneer er tot hen wordt gezegd: \"Behoedt u tegen hetgeen v��r u is en hetgeen achter u is, opdat u barmhartigheid moge worden betoond.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,46,'Maar er komt geen teken tot hen van de tekenen van hun Heer, of zij wenden er zich van af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,47,'En wanneer er tot hen wordt gezegd: \"Besteedt van hetgeen Allah u heeft geschonken,\" zeggen de ongelovigen tot de gelovigen, \"Moeten wij hem voeden? Indien het Allah behaagde zou Hij hem hebben kunnen voeden. Gij verkeert slechts in een klaarblijkelijke dwaling.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,48,'En zij zeggen: \"Wanneer zal deze Belofte worden vervuld, als gij de waarheid spreekt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,49,'Zij wachten slechts op een plotselinge straf die hen zal overkomen terwijl zij nog aan het redetwisten zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,50,'En zij zullen geen testament meer kunnen maken noch zullen zij tot hun families terugkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,51,'En de bazuin zal worden geblazen, en ziet! zij zullen zich vanuit hun graven naar hun Heer haasten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,52,'Zij zullen zeggen: \"O wee ons, wie heeft ons van onze slaapplaatgen gewekt? Dit is hetgeen de Barmhartige heeft beloofd, en de boodschappers spraken de waarheid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,53,'Het zal slechts een kreet zijn en ziet! zij zullen allen voor Ons worden gebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,54,'En op die Dag zal geen ziel onrecht worden aangedaan, noch zult gij worden beloond, behalve overeenkomstig uw daden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,55,'Voorwaar, op die Dag zullen de bewoners van de Hemel in (een groot) werk hun geluk vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,56,'Zij en hun echtgenoten zullen zich in de schaduw op tronen nedervlijen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,57,'Zij zullen daar vruchten hebben en alles waar zij om vragen ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,58,'Het woord van de Genadevolle Heer zal (klinken) \"Vrede (vrede).\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,59,'(En Hij zal zeggen): \"Houdt u op deze dag terzijde, o gij schuldigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,60,'\"Gelastte Ik u niet, o gij kinderen van Adam, dat gij Satan niet zoudt dienen, daar hij een openlijke vijand van u is,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,61,'Maar dat gij Mij zoudt dienen?\" Dat was het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,62,'Toch deed hij een groot gedeelte uwer dwalen. Hadt gij dan geen verstand?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,63,'\"Dit is de hel waarmede gij werdt bedreigd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,64,'Gaat daar thans binnen, omdat gij haar placht te loochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,65,'Op die Dag zullen Wij hun mond verzegelen, maar hun handen zullen tot ons spreken en hun voeten zullen getuigenis afleggen van alles wat zij hebben bedreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,66,'En als Wij het hadden gewild, konden Wij het licht in hun ogen hebben gedoofd; dan zouden zij zich naar het pad hebben willen haasten. Maar hoe konden zij zien?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,67,'En indien Wij wilden, zouden Wij hen op hun plaatsen hebben doen verstijven zodat zij noch v��r- noch achteruit konden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,68,'En wie Wij een lang leven schenken, doen Wij achteruitgaan in kracht. Willen zij dan niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,69,'En Wij hebben hem (de profeet) het dichten niet geleerd, noch is het voor hem passend, dit is slechts een vermaning en een duidelijke verkondiging;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,70,'Opdat de levenden mogen worden gewaarschuwd en opdat het oordeel tegen de ongelovigen gerechtvaardigd moge zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,71,'Hebben zij niet gezien, dat onder de dingen die Onze handen gemaakt hebben, Wij vee hebben geschapen, waar zij meesters over zijn?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,72,'En Wij hebben het aan hen dienstbaar gemaakt, zodat sommige rijdieren zijn, en sommige tot voedsel strekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,73,'En zij hebben er voordelen van en dranken. Willen zij dan niet dankbaar zijn?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,74,'En zij hebben andere goden naast Allah genomen, hopende dat zij mogen worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,75,'Dezen kunnen hen niet helpen maar zij zullen als een schare tegen hen worden gebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,76,'Laat daarom hun spraak u niet verdrieten. Voorwaar, Wij weten wat zij verbergen en wat zij tonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,77,'Heeft de mens niet begrepen dat Wij hem hebben geschapen uit een levenskiem? Doch ziet, hij is klaarblijkelijk een redetwister!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,78,'En hij zet Ons verhalen voor en vergeet zijn eigen ontstaan. Hij zegt: \"Wie kan de beenderen doen herleven als zij vergaan zijn?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,79,'Zeg: \"Hij, Die hen voor de eerste keer schiep zal hen doen herleven; Hij heeft kennis van de gehele schepping.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,80,'Hij is het, Die uit een groene boom voor u vuur voortbrengt, en ziet, gij steekt er (uw brandstof) van aan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,81,'\"Is Hij, Die de hemelen en de aarde schiep, niet in staat hun gelijken te scheppen?\" Ja, inderdaad Hij is de Schepper, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,82,'Voorwaar, wanneer Hij Zich iets voorneemt is Zijn gebod slechts: \"Wees\", en het wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,36,83,'Glorie zij daarom Hem, in wiens hand de oppermacht over alle dingen is! En tot Hem zult gij worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,1,'Bij hen, die zich in rijen scharen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,2,'En bij hen die berispen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,3,'En bij de verkondigers der Vermaning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,4,'Voorwaar, (voorwaar), uw God is ��n (enig God),');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,5,'Heer der hemelen en der aarde en van alles wat er tussen is, de Heer van het Oosten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,6,'Wij hebben de laagste hemel met sterren versierd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,7,'Als bescherming tegen iedere opstandige Satan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,8,'Zij kunnen van de verheven bijeenkomst niets horen en zij worden van elke kant verdreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,9,'Als verworpenen en er is voor hen een voortdurende straf;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,10,'Maar hij die steelsgewijze opvangt, hem achtervolgt een heldere vlam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,11,'Vraag hun (de ongelovigen) of zij moeilijker zijn te scheppen, dan andere (dingen) die Wij hebben geschapen. Voorzeker, Wij hebben hen uit vaste klei geschapen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,12,'Neen, gij verwondert u en zij spotten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,13,'En wanneer zij vermaand worden, trekken zij er geen lering uit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,14,'En wanneer zij een teken zien, bespotten zij het.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,15,'En zij zeggen: \"Dit is niets dan een klaarblijkelijke tovenarij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,16,'\"Zullen wij wanneer wij dood zijn en stof en beenderen zijn geworden, worden opgewekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,17,'En onze voorvaderen ook?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,18,'Zeg: \"Ja, terwijl gij vernederd zult zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,19,'Er zal slechts ��n roep zijn en ziet, zij zullen beginnen te zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,20,'Dan zullen zij zeggen: \"Wee ons! Dit is de Dag der vergelding.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,21,'(Allah zal zeggen:) \"Dit is de Dag der Beslissing die gij placht te verloochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,22,'Verzamelt de onrechtvaardigen, hun metgezellen en hetgeen zij aanbaden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,23,'Naast Allah. Leidt hen dan naar het pad van het Vuur;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,24,'Doch houdt hen staande want zij moeten worden ondervraagd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,25,'\"Wat scheelt u dat gij elkander niet helpt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,26,'Neen, op die Dag zullen zij onderworpen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,27,'Sommigen hunner zullen zich tot anderen wenden, elkander wederkerig ondervragend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,28,'Zij zullen zeggen: \"Voorwaar, gij placht ons op de goede weg tegen te houden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,29,'Zij zullen antwoorden: \"Neen, gij waart zelf geen gelovigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,30,'En wij hadden geen macht over u, maar gij waart een overtredend volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,31,'Nu is het woord van onze Heer omtrent ons werkelijkheid geworden. Wij zullen gewis (de straf) smaken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,32,'En wij deden u dwalen omdat wij zelf in dwaling waren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,33,'Waarlijk, op die Dag zullen zij allen deelgenoten zijn in de straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,34,'Zo behandelen Wij de schuldigen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,35,'Voorzeker toen er tot hen werd gezegd: \"Er is geen God naast Allah\", waren zij vanmatigend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,36,'En zeiden: \"Zullen wij onze Goden voor die waanzinnige dichter opgeven?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,37,'Neen, hij is met de Waarheid gekomen en heeft die van de (vroegere) boodschappers bevestigd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,38,'Gij zult de pijnlijke straf gewis ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,39,'En gij zult slechts worden vergolden voor hetgeen gij deedt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,40,'Maar de uitverkoren dienaren van Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,41,'Zullen een bekende voorziening ontvangen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,42,'Zij zullen vruchten ontvangen, en worden ge�erd,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,43,'In tuinen van gunsten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,44,'Op rustbanken. tegenover elkander.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,45,'En een beker zal hun worden rondgereikt uit een stromende bron.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,46,'Helder, smakelijk voor de drinkenden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,47,'Waardoor geen dronkenschap zal ontstaans noch zullen zij er door worden uitgeput.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,48,'En naast hen zullen vrouwen zijn van bescheiden blik met mooie ogen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,49,'Rein, alsof zij zorgvuldig bewaarde eieren waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,50,'En enigen hunner zullen zich tot anderen wenden, elkander ondervragend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,51,'Een hunner zal zeggen: \"Ik had een metgezel,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,52,'Die placht te zeggen: \"Bevestigt gij inderdaad,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,53,'Dat wanneer wij dood zijn en tot stof en beenderen geworden, ons inderdaad wordt vergolden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,54,'Hij zal vragen: \"Wilt gij opzien?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,55,'Dan zal hij kijken en hem in het midden van het Vuur zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,56,'Hij zal zeggen: \"Bij Allah, gij deedt mij ook bijna te niet gaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,57,'\"En ware het niet door de gunst van mijn Heer, ik zou ook tot hen behoren die daar aanwezig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,58,'Zullen wij niet sterven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,59,'Na onze eerste dood, noch worden gestraft?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,60,'Voorwaar, dit is de opperste zegepraal.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,61,'Laat daarom de werkers voor zo iets werken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,62,'Is dit een beter onthaal of de boom van Zaqqoem?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,63,'Voorzeker, wij hebben deze tot een beproeving voor de onrechtvaardigen gemaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,64,'Het is een boom die uit de bodem der hel ontspringt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,65,'De trossen er van zijn als de koppen van duivels.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,66,'En zij zullen er zeker van eten en er hun buik mee vullen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,67,'Dan zullen zij bovendien een drank van kokend water ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,68,'Daarna zal hun terugkeer zeker naar het Vuur zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,69,'Zij vonden inderdaad hun voorvaderen in dwaling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,70,'En zij haastten zich in hun voetstappen voort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,71,'En voorzeker dwaalden v��r hen velen der ouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,72,'En Wij hadden waarschuwers tot hen gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,73,'Ziet dan hoe het einde was van hen die waren gewaarschuwd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,74,'Met uitzondering der uitverkoren dienaren van Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,75,'Noach riep Ons aan, en hoe uitmuntend zijn Wij in het verhoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,76,'Wij redden hem en zijn familie uit de grote nood;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,77,'En Wji maakten zijn nakomelingen tot de overlevenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,78,'En Wij lieten voor hem onder de komende geslachten (de groet):');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,79,'\"Vrede zij Noach onder de volkeren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,80,'Zo belonen Wij inderdaad hen die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,81,'Hij was voorzeker ��n Onzer gelovige dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,82,'Dan deden Wij de anderen verdrinken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,83,'En voorwaar, tot zijn partij behoorde Abraham;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,84,'Toen hij tot zijn Heer kwam met een deemoedig hart;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,85,'En hij tot zijn vader en tot zijn volk zeide: \"Wat aanbidt gij?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,86,'Kiest gij valse goden naast Allah?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,87,'Hoe denkt gij over de Heer der Werelden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,88,'En hij (Abraham) redetwistte over de sterren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,89,'En zei: \"Ik ben er ziek van.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,90,'En zij wendden zich van hem af en gingen weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,91,'En hij ging heimelijk tot hun goden en zeide: \"Waarom eet gij niet,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,92,'Wat scheelt u, dat gij niet spreekt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,93,'Dan begon hij hen met de rechter hand te slaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,94,'En zij (de afgodendienaren) haastten zich naar hem toe.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,95,'Hij zeide: \"Aanbidt gij hetgeen gij zelf hebt uitgebeeld,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,96,'Terwijl Allah u en uw handwerk heeft geschapen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,97,'Zij zeiden: \"Laat ons een omheining bouwen en hem in het vuur werpen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,98,'En zij hadden een komplot tegen hem gesmeed, maar Wij vernederden hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,99,'Hij zeide: \"Ik ga naar mijn Heer, Die zal mij leiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,100,'Mijn Heer, schenk mij een nakomeling die goed zal zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,101,'Dan gaven Wij hem de blijde tijding van een verdraagzame zoon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,102,'En toen deze de knapenleeftijd bereikte, zeide hij: \"O mijn lieve zoon, ik heb in een droom gezien, dat ik u heb te offeren. Zie, wat zegt gij daarvan?\" Deze antwoordde: \"O mijn vader doe zoals u bevolen is, gij zult mij, indien Allah het wil, zeker geduldig vinden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,103,'En toen zij zich beiden aan (Gods bevel) hadden onderworpen, en hij hem plat op zijn voorhoofd had gelegd,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,104,'Riepen Wij hem toe: \"O Abraham,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,105,'Gij hebt de droom reeds vervuld. Zo belonen Wij inderdaad degenen, die goed doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,106,'Dit was voorzeker een grote beproenng.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,107,'En Wij verlosten hem door een groot offer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,108,'En Wij lieten voor hem onder de komende geslachten (de groet):');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,109,'\"Vrede zij Abraham.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,110,'Zo belonen Wij hen die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,111,'Voorwaar, hij was ��n Onzer gelovige dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,112,'Wij gaven hem het blijde nieuws van Iza�k, een profeet onder de rechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,113,'En Wij zegenden hem en Iza�k. En er zijn er onder hun nageslacht die goed doen en anderen die zichzelf openlijk onrecht aandoen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,114,'Wij bewezen inderdaad gunsten aan Mozes en A�ron.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,115,'En Wij redden hen beiden en hun volk uit een grote nood;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,116,'En Wij hielpen hen (tegen de Egyptenaren) en zij waren het die de overwinning verkregen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,117,'En Wij gaven hun het duidelijke boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,118,'En leidden hen op het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,119,'Wij lieten voor hen, onder de komende geslachten (de groet):');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,120,'\"Vrede zij Mozes en A�ron.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,121,'Voorzeker zo belonen Wij degenen die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,122,'Voorwaar zij behoorden tot Onze gelovige dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,123,'En Elias was ��k een der boodschappers');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,124,'Toen hij tot zijn volk zeide, \"Wilt gij niet godvruchtig zijn?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,125,'Wilt gij Ba�l aanroepen en de beste Schepper verzaken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,126,'Allah, uw Heer en de Heer uwer voorvaderen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,127,'Maar zij verloochenden hem en zij zullen zeker worden overgeleverd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,128,'Met uitzondering der uitverkoren dienaren van Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,129,'En Wij lieten voor hem onder de komende geslachten (de groet):');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,130,'\"Vrede zij Elias.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,131,'Voorzeker zo belonen Wij degenen, die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,132,'Voorwaar, hij was ��n Onzer gelovige dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,133,'En Lot was voorzeker ��k een der boodschappers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,134,'Toen Wij hem en zijn familieleden redden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,135,'Met uitzoudering van zijn vrouw die tot de achterblijvenden beboorde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,136,'En Wij vernietigden de anderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,137,'En gij gaat hen (de plaats waar dezen woonden) zeker ´s morgens voorbij');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,138,'En ´s avonds. Wilt gij dan niet begrijpen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,139,'En Jonas was voorzeker ook een der boodchappers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,140,'Toen hij in het geladen schip vluchtte,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,141,'En hij lootte en werd (overboord) geworpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,142,'Een grote vis slokte hem op terwijl hij zelfverwijt had.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,143,'Indien hij niet behoorde tot hen die Ons verheerlijken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,144,'Dan zou hij in diens buik zijn gebleven tot de Dag der Opstanding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,145,'Wij wierpen hem op een kaal strand terwijl hij ziek was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,146,'En Wij lieten een pompoen voor hem opgroeien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,147,'En Wij zonden hem als boodschapper tot honderdduizend of meer mensen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,148,'En zij geloofden, daarom gaven Wij hun voor een korte tijd de voorziening (van dit leven).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,149,'Vraag hun nu of hun Heer dochters heeft terwijl zij zonen hebben?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,150,'Hebben Wij de engelen als vrouwelijke wezens geschapen, terwijl zij getuigen waren?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,151,'Welnu, door hun verzinsel zeggen zij:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,152,'\"Allah heeft verwekt.\" Maar zij zijn stellig leugenaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,153,'\"Heeft Hij dochters gekozen boven zonen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,154,'Wat scheelt u? Hoe oordeelt gij?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,155,'Wilt gij dan niet nadenken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,156,'Of hebt gij een duidelijk bewijs?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,157,'Toont dan uw Boek, indien gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,158,'En zij beweren een bloedverwantschap tussen Hem en de djinn, terwijl de djinn zeer goed weten, dat zij voor Hem zullen worden gebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,159,'Verheven is Allah boven hetgeen zij zeggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,160,'Met uitzondering van de uitverkoren dienaren van Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,161,'Voorwaar, gij en wat gij aanbidt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,162,'Gij kunt niemand verleiden tegen Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,163,'Behalve hem die het Vuur zal binnengaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,164,'En er is niet ��n onzer of hij heeft een vaste plaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,165,'Waarlijk wij zijn degenen die in rijen gerangschikt zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,166,'En voorzeker wij verheerlijken (God).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,167,'En zij plachten te zeggen:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,168,'\"Als wij een vermaning hadden gehad van de ouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,169,'Zouden wij zeker Allah´s uitverkoren dienaren zijn geworden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,170,'Toch verwerpen zij deze, maar zij zullen het weldra te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,171,'En waarlijk, Ons woord aangaande Onze dienaren, de boodschappers, is reeds uitgesproken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,172,'Voorzeker, zij zijn het die geholpen zullen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,173,'En Onze schare is gewis overwinnaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,174,'Wend u daarom voor een wijle van hen af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,175,'En sla hen gade; want zij zullen het weldra inzien');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,176,'Willen zij dan Onze straf verhaasten?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,177,'Maar wanneer deze op hun land nederdaalt zal de dag slecht zijn voor degenen, die werden gewaarschuwd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,178,'Wend u daarom voor een wijle van hen af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,179,'En let op, zij zullen het weldra inzien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,180,'Verheven is uw Heer, de Heer van Roem en Macht, boven hetgeen zij zeggen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,181,'En vrede zij de boodschappers!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,37,182,'En alle roem behoort aan Allah, de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,1,'Saad. Bij de Koran vol van aanzien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,2,'Maar de ongelovigen zijn in valse trots en strijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,3,'Hoevele geslachten hebben Wij vernietigd v��r hen! Zij schreeuwden het uit, toen er voor ontkomen geen tijd meer was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,4,'En dezen verwonderen zich, omdat een waarschuwer uit hun midden tot hen is gekomen; en de ongelovigen zeggen. \"Dit is een tovenaar en een leugenaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,5,'Heeft hij van vele Goden ��n God gemaakt? Dit is voorzeker iets eigenaardigs.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,6,'En de leiders onder hen zeggen: \"Gaat voort en houdt u aan uw Goden. Dit is voorzeker gewenst.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,7,'Wij hebben hieromtrent in de laatste godsdienst niets gehoord. Dit is niets anders dan een verzinsel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,8,'Aan hem is onder ons de vermaning gezonden?\" Neen, zij twijfelen aan Mijn vermaning, zij hebben Mijn straf nog niet ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,9,'Bezitten zij de schatten der barmhartigheid van uw Heer, de Machtige, de Milddadige?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,10,'Of is het koninkrijk der hemelen en der aarde en alles wat er tussen is van hen? Laat hen dan hun middelen vermeerderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,11,'Zij zijn een leger van bondgenoten dat zal worden verslagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,12,'V��r hen loochende het volk van Noach, en Aad en Pharao - de heer der scharen -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,13,'En de Samoed, en het volk van Lot, e: en de woudbewoners; dezen waren bond genoten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,14,'Allen verloochenden de boodschappers, daarom werd Mijn straf verwerkelijkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,15,'En dezen wachten slechts op een enkele roep waarvoor geen uitstel zal zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,16,'Zij zeggen: \"Onze Heer, geef ons spoedig ons deel v��r de Dag der Afrekening.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,17,'Verdraag hetgeen zij zeggen en gedenk onze dienaar David, heer van macht; voorzeker hij was altijd tot God geneigd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,18,'Wij onderwierpen de bergen om met hem (God) te verheerlijken bij avond en ochtend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,19,'En de vogelen die tezamen verzameld waren, waren hem allen gehoorzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,20,'En Wij versterkten zijn koninkrijk en gaven hem wijsheid en een beslissend oordeel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,21,'En heeft het verhaal van de tegenstanders u bereikt? Hoe zij over de muur van zijn kamer klommen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,22,'Hoe zij bij David binnenkwamen en hij hen vreesde. Zij zeiden: \"Vrees niet, wij zijn twee procesvoerders, waarvan de ene de ander onrecht heeft aangedaan; spreek daarom recht tussen ons in waarheid en handel niet onrechtvaardig en leid ons naar het rechte pad.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,23,'\"Deze is mijn broeder; hij heeft negen en negentig ooien, en ik heb maar ��n ooi. Toch zegt hij: ´Geef haar aan mij´ en hij was mij in het dispuut de baas.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,24,'David zeide: \"Voorzeker, hij heeft u onrecht aangedaan door uw ooi te eisen naast zijn eigen ooien. En voorzeker, vele der mededingers doen elkaar onrecht aan, met uitzondering van hen, die geloven en goede werken doen: en zij zijn slechts weinigen.\" En David bemerkte, dat Wij hem hadden beproefd, daarom vroeg hij om vergiffenis van zijn Heer en zich tot Hem wendend, viel hij in gebed neder.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,25,'Daarom gaven Wij hem bescherming en inderdaad had hij een dichte toenadering en een voortreffelijk toevlucht tot Ons.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,26,'(Wij zeiden): \"O David, Wij hebben u als stedehouder op aarde aangewezen, spreek daarom recht over de mensen naar waarheid en volg (hun) begeerten niet, anders zullen zij u van de weg van Allah afleiden.\" Degenen, die van de weg van Allah afdwalen zullen gewis een strenge straf ontvangen, omdat zij de Dag des Oordeels vergeten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,27,'En Wij hebben de hemelen en de aarde en alles wat er tussen is niet tevergeefs geschapen - Dat is het vermoeden der ongelovigen. En wee de ongelovigen vanwege het Vuur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,28,'Zullen Wij degenen, die geloven en goede werken doen op dezelfde wijze behandelen als de onheilstichters op aarde of moeten Wij de godvruchtigen en de bozen gelijk stellen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,29,'Het Boek dat Wij aan u hebben geopenbaard is vol van zegeningen, laat hen dus over zijn verzen nadenken en laat de verstandigen er lering uit trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,30,'En aan David schonken Wij Salomo; een voortreffelijke dienaar, die altijd (tot Ons) geneigd was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,31,'Herinnert u, toen er renpaarden van het edelste ras en vlug ter been op een avond voor hem werden gebracht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,32,'Dat hij zeide: \"Ik houd van goede dingen vanwege de gedachtenis aan mijn Heer.\" Toen zij (de zon) door een sluier verborgen waren, zei hij:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,33,'\"Brengt ze naar mij terug.\" Toen begon hij ze over hun benen en nek te strijken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,34,'Voorzeker Wij beproefden Salomo en op zijn troon zetten Wij een zielloos lichaam. En hij (Salomo) wendde zich tot (God).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,35,'En zeide: \"O mijn Heer, vergeef mij, en schenk mij een koninkrijk hetwelk na mij voor niemand anders is; zeker, Gij zijt de Milddadige.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,36,'Wij onderwierpen de wind aan hem, die op zijn gebod zachtjes waaide waarheen hij wilde,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,37,'En deskundigen en allerlei bouwers en duikers,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,38,'Alsook anderen, die met ketenen geboeid waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,39,'Wij zeiden: \"Dit is Onze gave. Wees vrijgevig of spaarzaam, er zal daarover geen oordeel zjin.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,40,'En hij had inderdaad een dichte toenadering tot Ons en een voortreffelijke toevlucht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,41,'Herinnert u Onze dienaar Job, toen hij tot zijn Heer riep: \"Satan heeft mij met kommer en smart geslagen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,42,'Wij zeiden: \"Spoor uw rijdier met uw voet aan, hier is koel water om u er mee te wassen en ook om te drinken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,43,'Wij schonken hem zijn familie en evenveel bovendien, als een barmhartigheid van Ons en als les voor mensen van begrip.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,44,'(En Wij zeiden:) \"Neem een handvol gedroogde stengels in uw hand en sla er mee, en breek uw eed niet.\" Wij vonden hem standvastig. Hij was een voortreffelijke dienaar en altijd tot Ons geneigd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,45,'En gedenk Onze dienaren Abraham, Izaak en Jacob, de bezitters van macht en inzicht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,46,'Wij verkozen hen in het bijzonder - ter vormaning betreffende het laatste tehuis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,47,'En waarlijk, zij zijn in Onze ogen de uitverkorenen en de goeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,48,'En gedenk Isma�l, Eliza en Zolkifl; zij behoren allen tot de besten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,49,'Dit is een aanmaning. En voor de godvruchtigen zal zeker een voortreffelijke toevlucht zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,50,'Tuinen der eeuwigheid met de poorten wijd voor hen open;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,51,'Op tronen rustend zullen zij daarin om overvloedig vruchten en drank roepen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,52,'En bij hen zullen vrouwen zijn, die haar blikken weerhouden, metgezellen van gelijke leeftijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,53,'Dit is hetgeen u beloofd is voor de Dag des Oordeels.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,54,'Voorwaar dit is Onze voorziening die nooit uitgeput zal zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,55,'Dit is (voor de gelovigen). Maar voor de opstandigen zal er een slechte plaats van terugkeer zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,56,'De hel! daarin zullen zij branden, het is een slechte rustplaats,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,57,'Deze! Laat hen daarom een kokende en een ijskoude drank proeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,58,'En meer dergelijke van verschillende soorten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,59,'Hier is een groep van uw volgelingen die er samen met u ingestort zal worden. (Zij zullen zeggen:) \"Geen welkom voor hen, zij moeten in het Vuur branden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,60,'Zij zullen antwoorden: \"Wee, gij zijt het, voor wie geen welkom is. Gij hebt dit voor ons bereid. En het is een slechte plaats!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,61,'Zij zullen zeggen: \"Onze Heer, wie dit voor ons bereid heeft, voeg hem een dubbele straf in het Vuur toe.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,62,'En zij zullen zeggen: \"Hoe komt het dat wij de mensen die wij onder de bozen rekenden, niet meer zien?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,63,'\"Hebben wij hen ten onrechte bespot of zien onze ogen hen niet?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,64,'Voorzeker, het onderlinge redetwisten van de mensen in het Vuur is de waarheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,65,'Zeg: \"Ik ben slechts een waarschuwer; en er is geen God naast Allah, de Ene, de Onweerstaanbare;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,66,'De Heer van de hemelen en de aarde, en alles wat er tussen is, de Machtige, de Vergevensgezinde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,67,'Zeg: \"Het is een belangrijke mededeling,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,68,'Doch gij wendt u er van af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,69,'Ik heb geen kennis van de verheven vergadering toen zij onderling redetwistten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,70,'Slechts dit is aan mij geopenbaard dat ik een duidelijke waarschuwer ben.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,71,'Toen uw Heer tot de engelen zeide: \"Ik ga de mens uit klei scheppen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,72,'En wanneer Ik hem heb gevormd en hem van Mijn geest heb ingeademd, werpt u dan in gehoorzaamheid voor hem neder.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,73,'Derhalve vielen alle engelen neder,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,74,'Doch Iblies niet, hij toonde hoogmoed en behoorde tot de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,75,'God zeide: \"O Iblies, wat heeft u verhinderd te buigen voor hem, die Ik met Mijn Hand heb geschapen? Zijt gij te trots of behoort gij tot de (hoog) verhevenen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,76,'Hij zeide: \"Ik ben beter dan hij, Gij hebt mij uit vuur en hem uit klei geschapen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,77,'God zeide: \"Ga dan hier vandaan, voorzeker gij zijt de verworpene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,78,'En Mijn vloek zal op u rusten tot de Dag des Oordeels.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,79,'Hij zeide: \"O mijn Heer, vergun mij dan uitstel tot de Dag waarop zij zullen worden opgewekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,80,'God zeide: \"U wordt uitstel verleend,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,81,'Tot de Dag van de bepaalde tijd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,82,'Hij zeide: \"Bij Uw eer, ik zal hen allen zeker doen dwalen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,83,'Behalve Uw oprechte dienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,84,'God zeide: \"Dit is de waarheid en Ik zeg de waarheid,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,85,'Dat Ik de hel zeker met u en allen die u volgen, zal vullen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,86,'Zeg: \"Ik vraag u er geen loon voor, noch breng ik u in moeilijkheden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,87,'Dit is slechts een vermaning voor de werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,38,88,'En na een wijle zult gij de tijding er van te weten komen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,1,'De openbaring van dit Boek is van Allah, de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,2,'Voorwaar, Wij hebben u het Boek met waarheid geopenbaard; aanbid daarom Allah, oprecht zijnde jegens Hem in onderwerping.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,3,'Ziet, aan Allah alleen komt oprechte gehoorzaamheid toe. En degenen, die naast Hem anderen als beschermers nemen, zeggende: \"Wij aanbidden dezen slechts opdat zij ons in Allah´s nabijheid brengen.\" Voorzeker, Allah zal onder hen uitspraak doen betreffende datgene waarin zij verschillen. Voorwaar, Allah leidt een ondankbare leugenaar niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,4,'Had Allah een zoon begeerd dan zou Hij uit wat Hij geschapen heeft kunnen nemen, wie Hij wilde. Heilig is Hij. Hij is Allah, de Ene, de Opperste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,5,'Hij schiep de hemelen en de aarde in waarheid. Hij doet de nacht over de dag komen, en de dag over de nacht; en Hij heeft de zon en de maan in dienst gesteld; elk loopt een vaste baan. Voorzeker, Hij is de Almachtige, de Vergevensgezinde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,6,'Hij schiep u uit ��n ziel, dan maakte Hij daaruit echtgenoten; en Hij zond voor u acht stuks vee in paren neder. Hij vormde u in de baarmoeder van uw moeder, schepping naast schepping, in drievoudige duisternis. Zo is Allah uw Heer, van Hem is het koninkrijk. Er is geen God naast Hem. Waardoor wordt gij dan afgeleid?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,7,'Indien gij ondankbaar zijt; Allan is zeker onafhankelijk van u. Maar Hij houdt niet van ondankbaarheid onder Zijn dienaren. En als gij dankbaar zijt, zal Hij in u behagen hebben - En geen lastdragende kan de last van een ander dragen - Dan is uw terugkeer tot uw Heer: en Hij zal u mededelen wat gij deedt. Zeker, Hij weet goed wat in uw innerlijk is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,8,'Wanneer een mens wordt benadeeld, roept hij zijn Heer aan, zich tot Hem wendend. Dan, wanneer Hij hem een gunst bewijst van Zichzelf, vergeet de mens waarvoor hij eerst (God) aanriep en stelt medegoden naast Allah, om de mensen van Zijn weg af te leiden. Zeg: \"Geniet door uw ongeloof voor een wijle, gij behoort zeker tot de bewoners van het Vuur.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,9,'Is hij, die God vereert in de uren der nacht, nederknielende en staande, die voor het Hiernamaals vreest en hoopt op de barmhartigheid van zijn Heer. Zeg: \"Zijn zij die weten gelijk aan hen die niet weten?\" Maar alleen de verstandigen trekken er lering uit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,10,'Zeg: \"O, Mijn gelovige dienaren, vreest uw Heer.\" Voor hen, die in dit leven goed doen, is het goede. En Allah´s aarde is ruim. Voorwaar, aan de standvastigen zal hun beloning zonder berekening worden uitbetaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,11,'Zeg: \"Het is mij bevolen Allah te aanbidden oprecht zijnde in onderwerping aan Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,12,'En mij is bevolen de eerste der Moslims te zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,13,'Zeg: \"Indien ik mijn Heer niet gehoorzaam, vrees ik de straf van de grote Dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,14,'Zeg: \"Allah is het Die ik aanbid, oprecht zijnde in gehoorzaamheid tot Hem.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,15,'Zeg: \"Aanbidt wie gij wilt buiten Hem. Op de Dag der Opstanding zullen zij de verliezers zijn, die zichzelf en hun familie hebben benadeeld.\" Ziet toe, dit is het duidelijke verlies.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,16,'Zij zullen lagen van Vuur over en onder zich hebben. Hiertegen waarschuwt Allah Zijn dienaren: \"O Mijn dienaren, vreest Mij derhalve.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,17,'En zij die vermijden valse goden te aanbidden, en zich tot Allah wenden - voor hen zijn er blijde tijdingen. Daarom geef blijde tijding aan Mijn dienaren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,18,'Die naar het Woord luisteren en dit het best naleven. Zij zijn het die Allah geleid heeft, en zij zijn de verstandigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,19,'Kunt gij hem, tegen wie de uitspraak van straf is bevestigd, en die in het Vuur is redden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,20,'Maar voor hen die hun Heer vrezen zijn woningen boven elkaar gebouwd, waaronder rivieren stromen. Dit is Allah´s belofte en Allah breekt Zijn belofte niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,21,'Hebt gij niet gezien, dat Allah water van de hemel nederzendt en het in de aarde doet binnendringen tot (het vormen van) bronnen? Ook worden er oogsten voortgebracht, die in hun kleuren vari�ren. Daarna drogen deze uit en gij ziet ze geel worden; dan doet Hij ze in stukken breken. Voorwaar daarin is een les voor de mensen van begrip.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,22,'Hij wiens hart Allah voor de Islam heeft verruimd, is in het licht van zijn Heer. Wee dan degenen, wier hart verhard is bij de gedachtenis aan Allah! Waarlijk, zij verkeren klaarblijkelijk in dwaling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,23,'Allah heeft de beste verkondiging geopenbaard, een Boek (de Koran), overeenkomstig met zichzelf, vaak herhalend (vermaningen) waarbij de huid van hen die hun Heer vrezen ineenkrimpt, daarna ontspant zich hun huid en hun hart wordt zacht bij de gedachte aan Allah. Dit is de leiding van Allah, Hij leidt daarmee wie Hij wil. En wie Allah laat dwalen, zal geen leider hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,24,'Is (deze beter) die voor zijn persoon bescherming zoekt op de Dag der Opstanding voor de vreselijke straf? - terwijl tot de onrechtvaardigen zal worden gezegd: \"Ondergaat (nu) wat gij verdiendet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,25,'Ook degenen die v��r hen waren, verloochenden, daarom kwam de straf over hen zonder dat zij bemerkten van waar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,26,'En Allah vernederde hen in het leven dezer wereld; doch de straf van het Hiernamaals zal zeker groter zijn. Als zij slechts wisten!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,27,'En voorzeker, Wij hebben allerlei gelijkenissen voor de mensen in deze Koran vermeld, opdat zij er lering uit trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,28,'Dit is een duidelijke verkondiging zonder afwijking opdat men godvruchtig moge worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,29,'Allah geeft een gelijkenis; een man die aan meer aanspraak-hebbenden toebehoort, die het met elkander oneens zijn, en een man die geheel aan ��n man toebehoort. Zijn zij beiden in dezelfde toestand? Alle roem behoort aan Allah. Maar de meesten onder hen begrijpen dit niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,30,'Waarlijk gij zult sterven en zij zullen ook sterven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,31,'Dan zult gij op de Dag der Opstanding met elkander redetwisten in het bijzijn van uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,32,'Wie is onrechtvaardiger dan hij, die een leugen over Allah verzint of de Waarheid verloochent wanneer zij tot hem komt? Is er voor de ongelovigen geen plaats in de hel?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,33,'En hij die de Waarheid brengt of deze bevestigt - dezen zijn de godvruchtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,34,'Zij zullen van hun Heer ontvangen wat zij begeren; dat is de beloning dergenen die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,35,'Allah zal het slechtste wat zij deden van hen verwijderen en hun het loon geven beter dan hetgeen zij verdienden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,36,'Is Allah niet toereikend voor Zijn dienaar? En zij trachten u te doen vrezen voor hen (de afgoden) die buiten Allah zijn. Voor hem die Allah laat dwalen is er geen leider.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,37,'En wie Allah leidt zal niemand kunnen doen afdwalen. Is Allah niet Machtig, de Heer der Vergelding?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,38,'Indien gij hun vraagt: \"Wie heeft de hemelen en de aarde geschapen?\", zullen zij voorzeker antwoorden: \"Allah\". Zeg: \"Vertelt mij (dan) wat gij naast Allah aanroept, kunnen zij, indien Allah mij zou willen benadelen, Zijn schade verwijderen? Of als Hij mij barmhartigheid wil tonen, kunnen zij Zijn barmhartigheid dan tegenhouden?\" Zeg: \"Allah is mij voldoende. In Hem zullen de vertrouwenden hun vertrouwen stellen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,39,'Zeg: \"O, mijn volk, handel op uw plaats, ik ben ook werkzaam, maar weldra zult gij weten');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,40,'Tot wie de vernederende kastijding komt en op wie de blijvende straf nederdaalt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,41,'Voorwaar, Wij hebben u ten bate der mensen het Boek met Waarheid nedergezonden. Hij die deze leiding volgt, volgt haar ten bate van zijn eigen ziel en wie dwaalt, dwaalt ten nadele van haar. En gij zijt geen voogd over hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,42,'Allah neemt de zielen van de mensen op wanneer zij sterven en ook van hen die niet sterven tijdens hun slaap. Dan houdt Hij die, die Hij ten dode heeft opgeschreven en zendt de overigen tot een bepaalde tijd (in het lichaam) terug. Hierin zijn stellig tekenen voor een volk dat nadenkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,43,'Hebben zij bemiddelaars naast Allah genomen? Vraag: \"Zelfs indien zij (de afgoden) nergens macht over hebben en zonder verstand zijn?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,44,'Zeg: \"Van Allah is iedere voorspraak. Hem behoort het koninkrijk der hemelen en der aarde en naar Hem zult gij worden teruggebracht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,45,'En wanneer Allah wordt genoemd als de Enige, dan krimpt het hart dergenen die in het Hiernamaals niet geloven samen, maar wanneer degenen naast Hem genoemd worden, ziet, zij verheugen zich.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,46,'Zeg: \"O, Allah! Schepper der hemelen en der aarde! Kenner van het onzichtbare en het zichtbare! Slechts Gij oordeelt onder Uw dienaren over datgene waarin zij verschillen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,47,'En indien de onrechtvaardigen bezaten al hetgeen op de aarde is en nog eens zoveel daarbij, zullen zij dit op de Dag der Opstanding als losprijs voor de vreselijke straf willen aanbieden. En wat zij nooit dachten zal hun door Allah onthuld worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,48,'De straf voor het kwaad dat zij bedreven zal hun duidelijk worden en wat zij plachten te bespotten zal hen omringen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,49,'Wanneer nu de mens tegenspoed treft, roept hij Ons aan. Als Wij hem dan Onze gunst bewijzen zegt hij: \"Dit is mij alleen wegens mijn verdiensten geschonken.\" Neen, het is slechts een beproeving; maar de meesten onder hen beseffen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,50,'Ook hun voorgangers zeiden hetzelfde, toch baatte hen hetgeen zij deden niet;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,51,'En het kwaad dat zij deden trof hen en wat de onrechtvaardigen onder dezen doen, zal hen ook treffen en zij kunnen Ons niet ontsnappen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,52,'Weten zij niet dat Allah de voorziening vermeerdert of vermindert voor wie Hij wil; voorwaar, daarin zijn tekenen voor een volk dat wil geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,53,'Zeg: \"O mijn dienaren die tegen u zelf buitensporig zijt geweest, wanhoopt niet aan de barmhartigheid van Allah, voorzeker Allah vergeeft alle zonden, waarlijk, Hij is de Vergevensgezinde, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,54,'\"Wendt u tot uw Heer en onderwerpt u aan Hem voordat de straf over u komt, want dan zult gij niet meer worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,55,'\"En volgt het beste dat u geopenbaard is door uw Heer voordat de straf onverwachts over u komt terwijl gij het (naderen er van) niet bemerkt;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,56,'Opdat geen ziel moge zeggen: \"O wat een spijt heb ik over hetgeen waarin ik te kort schoot tegenover Allah! En inderdaad behoorde ik tot de spotters.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,57,'Of opdat zij niet moge zeggen: \"Had Allah mij geleid dan zou ik zeker tot de godvruchtigen hebben behoord.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,58,'Of opdat zij de straf ziende, niet moge zeggen: \"Ik wilde dat er voor mij een terugkeer was, dan zou ik tot de goeden behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,59,'(God zal antwoorden): \"Neen, Mijn tekenen kwamen tot u, doch gij verloochendet deze, gij waart hoogmoedig en behoordet tot de ongelovigen.´´');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,60,'En op de Dag der Opstanding zult gij de gezichten van hen die over Allah leugens uitten zwart zien. Is er in de hel geen tehuis voor de hoogmoedigen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,61,'Allah zal de godvruchtigen vanwege hun geloof redden. Geen kwaad zal over hen komen noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,62,'Allah is de Schepper van alles en de Voogd over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,63,'Aan Hem behoren de schatten der hemelen en der aarde; en zij die de tekenen van Allah verwerpen zijn de verliezers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,64,'Zeg: \"O, gij onwetenden, beveelt gij mij iets buiten Allah te aanbidden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,65,'En voorwaar, aan u zoals aan hen die v��r u waren, is geopenbaard: \"Als gij deelgenoten aan God toeschrijft, zal uw werk stellig vruchteloos blijken en gij zult zeker tot de verliezers behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,66,'Neen, dient Allah alleen en behoort tot de dankbaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,67,'Zij waarderen Allah niet volgens Zijn Waardigheid. De gehele aarde zal in Zijn greep zijn op de Dag der Opstanding, en de hemelen zullen worden opgerold in Zijn hand. Glorie zij Hem en verheven is Hij boven hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,68,'En de bazuin zal worden geblazen en allen die in de hemelen en op aarde zijn, zullen bezwijmen, behalve degenen die Allah wil. Dan zal er nogmaals worden geblazen en ziet! Zij zullen staande herrijzen en wachten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,69,'En de aarde zal door het licht van haar Heer schitteren, en het Boek zal nedergelegd worden en de profeten en de getuigen zullen worden gebracht en er zal tussen hen met rechtvaardigheid geoordeeld worden en hun zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,70,'Elke ziel zal volledig worden beloond voor hetgeen zij deed. En Hij weet het beste wat zij volbrachten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,71,'En de ongelovigen zullen naar de hel worden gedreven, wanneer zij deze bereiken, zullen de poorten worden geopend en haar wachters zullen tot hen zeggen: \"Kwamen er geen boodschappers van uit uw midden tot u, de tekenen van uw Heer verkondigende en u waarschuwende voor de komst van deze Dag?\" Zij zullen antwoorden: \"Ja zeker!\" Maar nu is de uitspraak van de straf tegen de ongelovigen van kracht geworden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,72,'Er zal worden gezegd: \"Gaat de poorten der hel binnen om er in te vertoeven, slecht is de verblijfplaats voor de hoogmoedigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,73,'En degenen die hun Heer vreesden zullen in groepen naar de Hemel worden geleid; wanneer zij die bereiken zullen de poorten worden geopend en zijn bewakers zullen tot hen zeggen: \"Vrede zij u! Weest gelukkig en gaat binnen om er voor (altijd) te verblijven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,74,'Zij zullen zeggen: \"Alle lof behoort aan Allah, Die Zijn belofte aan ons heeft vervuld en ons het land als erfenis heeft gegeven om daarin te vertoeven, waar wij ook willen.\" Hoe voortreffelijk is het loon der (rechtvaardige) werkers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,39,75,'En gij zult de engelen om de Troon zien dringen, hun Heer lovende met de roem, die Hem toekomt. En er zal tussen hen met Waarheid worden geoordeeld. En er zal worden gezegd: \"Alle lof behoort aan Allah, de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,1,'Haa Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,2,'De openbaring van dit Boek is van Allah, de Almachtige, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,3,'De Vergever der zonden, de Aanvaarder van berouw, de Gestrenge in het straffen, de Heer van genade. Er is geen God buiten Hem. Tot Hem is de terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,4,'Niemand betwist de woorden van Allah behalve de ongelovigen. Laat hun bedrijvigheid in het land u niet bedriegen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,5,'Het volk van Noach voor hen en andere groepen na hen verloochenden ook en elk volk besloot zijn boodschapper te vangen en twistte door leugen om de Waarheid er mee te niet te doen. Dan greep Ik hen en hoe verschrikkelijk was Mijn straf!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,6,'Zo werd het woord van uw Heer bewaarheid ten opzichte van de ongelovigen: dat zij de bewoners van het Vuur zouden zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,7,'Zij, die de Troon dragen en zij die er omheen staan verheerlijken hun Heer met de lof die Hem toekomt en zij geloven in Hem en vragen vergiffenis voor de gelovigen, zeggende: \"Onze Heer, Gij omvat alle dingen in Uw barmhartigheid en kennis. Vergeef daarom hen die berouw tonen en Uw weg volgen; en behoed hen voor de straf der hel,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,8,'Onze Heer, en doe hen de tuinen der Eeuwigheid ingaan, die Gij hun hebt beloofd, alsook de deugdzamen onder hun ouders, hun echtgenoten en hun kinderen. Zeker, Gij zijt de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,9,'\"En behoed hen voor het kwade; en een ieder die Gij op die Dag voor het kwade behoedt, hem betoont Gij zeker barmhartigheid. En dat is de grootste zegepraal.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,10,'De ongelovigen zullen worden toegesproken: \"Het misnoegen van Allah was groter dan uw eigen misnoegen toen gij tot het geloof werd geroepen doch gij dit verwierpt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,11,'Zij zullen zeggen: \"Onze Heer, Gij deedt ons tweemaal sterven en Gij hebt ons tweemaal in het leven teruggeroepen en wij bekennen onze zonden. Is er nu een uitweg?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,12,'Dit kwam omdat gij niet geloofdet toen Allah de Ene werd genoemd, maar toen Hem medegoden werden toegeschreven, geloofdet gij. Nu behoort het oordeel aan Allah, de Allerhoogste, de Allergrootste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,13,'Hij is het Die u Zijn tekenen toont en voorziening voor u van de hemel nederzendt; maar niemand trekt er lering uit behalve hij die zich (tot God) wendt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,14,'Roept alleen Allah aan, oprecht zijnde in gehoorzaamheid tot Hem, hoewel de ongelovigen er tegen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,15,'Verheven boven alle graden is de Heer van de Troon. Hij zendt het woord door Zijn gebod aan wie Hij wil van Zijn dienaren, opdat hij moge waarschuwen voor de Dag der Ontmoeting.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,16,'De Dag waarop zij naar voren zullen komen - zal niets van hen voor Allah verborgen zijn. \"Van Wie is het Koninkrijk op deze Dag?\" \"Van Allah, de Ene, de Onweerstaanbare.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,17,'\"Op deze Dag zal elke ziel worden beloond voor hetgeen zij heeft verdiend. Geen onrecht zal geschieden op deze Dag. Voorzeker, Allah is snel in het beoordelen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,18,'Waarschuw hen voor de naderende Dag, wanneer het hart in de keel klopt terwijl zij vol verdriet zullen zijn. De onrechtvaardigen zullen geen boezemvrienden hebben, noch enige bemiddelaar naar wie zal worden geluisterd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,19,'Hij kent de oneerlijkheid der ogen en datgene wat de harten verbergen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,20,'En Allah richt naar waarheid, maar degenen die zij aanroepen naast Hem kunnen in het geheel niet richten. Voorzeker, Allah is de Alhorende, de Alziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,21,'Hebben zij niet over de aarde gereisd en gezien wat het einde was van hen die v��r hen waren? Zij waren machtiger dan dezen in kracht en in hun sporen op aarde. Toch greep Allah hen voor hun zonden en zij hadden niemand om hen tegen Allah te beschermen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,22,'Dat kwam omdat hun boodschappers tot hen kwamen met duidelijke tekenen, doch zij verwierpen ze; daarom greep Allah hen. Voorzeker, Hij is Machtig, Streng in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,23,'En Wij zonden Mozes met Onze tekenen en een duidelijk gezag,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,24,'Tot Pharao en Hamaan en Korach, maar zij zeiden: \"Hij is een tovenaar en de grootste leugenaar.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,25,'En toen hij (Mozes) met Waarheid van Ons tot hen kwam, zeiden zij: \"Doodt de zonen der gelovigen met hem en ontziet hun vrouwen.\" Maar het plan der ongelovigen is ijdel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,26,'En Pharao zeide: \"Laat mij Mozes doodslaan en laat hem dan zijn Heer aanroepen. Ik vrees dat hij uw godsdienst zal veranderen of in het land onrust zal stoken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,27,'En Mozes zeide: \"Ik zoek toevlucht bij mijn Heer en uw Heer, tegen elke laatdunkende die aan de Dag des Oordeels niet gelooft.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,28,'En een gelovig man uit het volk van Pharao die zijn geloof verborg, zeide: \"Wilt gij een man doden omdat hij zegt: ´Mijn Heer is Allah´; terwijl hij tot u gekomen is met duidelijke tekenen van uw Heer? Is hij een leugenaar, dan rust zijn leugen op hem; maar als hij oprecht is, dan zal iets van datgene, waarmee hij u bedreigt, u overkomen. Voorzeker, Allah leidt hem die buitensporig en een grote leugenaar is, niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,29,'O mijn volk heden hebt gij de oppermacht en gij zijt de hoogsten in het land. Maar wie zal ons beschermen tegen de straf van Allah als zij over ons komt?\" Pharao zeide: \"Ik wijs u alleen dat aan wat ik zelf zie en ik leid u slechts naar het pad der rechtschapenheid.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,30,'En de gelovige zeide: \"O mijn volk, ik vrees voor u een gebeurtenis zoals op de Dag der bondgenoten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,31,'Zoals hoe geval was bij het volk van Noach, en Aad en Samoed en degenen die na hen waren. Allah wil Zijn dienaren geen onrecht aandoen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,32,'En o mijn volk, ik vrees voor u de Dag van het geweeklaag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,33,'Een Dag waarop gij u zult afwenden om te vluchten. Dan zult gij geen beschermer hebben tegen Allah. En hij die Allah laat dwalen zal geen leider hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,34,'En voordien kwam Jozef tot u met duidelijke tekenen, maar gij bleeft twijfeles aan hetgeen hij u bracht doch toen hij stierf zeidet gjj: \"Allah zal na hem geen boodschapper meer zenden.\" Alzo laat Allah de buitensporigen en de twijfelaars dwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,35,'Degenen die twisten over de tekenen van Allah zonder dat enig gezag (daarover) tot hen kwam; dit is afkeurenswaardig in de ogen van Allah en de gelovigen. Alzo verzegelt Allah het hart van iedere hoogmoedige en onderdrukker.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,36,'En Pharao zeide: \"O Hamaan, bouw mij een toren opdat ik de toegangswegen moge naderen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,37,'De toegangswegen der hemelen, opdat ik de God van Mozes moge bereiken ofschoon ik zeker weet dat hij een leugenaar is.\" Zo werd voor Pharao zijn slechte daad schoonschijnend gemaakt, hij werd van het rechte pad afgeleid en Pharao´s plan eindigde slechts in ondergang.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,38,'En de gelovige zeide: \"O, mijn volk, volg mij, ik zal u op het pad van leiding voeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,39,'O mijn volk, dit leven dezer wereld is slechts een voorbijgaand genoegen; en het Hiernamaals is het blijvende tehuis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,40,'Wie kwaad doet zal naar evenredigheid hiervan worden vergolden; maar wie goed doet, man of vrouw, en gelovig is zal het paradijs binnengaan; daarin zullen zij van alles worden voorzien, zonder berekening.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,41,'En O mijn volk, hoe komt het toch dat ik u tot redding roep en gij mij tot het Vuur wilt leiden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,42,'Gij nodigt mij uit, Allah te verwerpen en iets met Hem te vereenzelvigen waarvan ik geen kennis heb. En ik roep u tot de Almachtige, de Vergevensgezinde.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,43,'\"Zeker, datgene waartoe gij mij uitnodigt heeft geen macht in deze wereld of in het Hiernamaals; voorwaar, onze terugkeer is tot Allah, en de overtreders zullen de bewoners van het Vuur zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,44,'Weldra zult gij u herinneren wat ik u zeg. En ik vertrouw mijn zaak aan Allah toe. Voorwaar, Allah ziet Zijn dienaren door en door.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,45,'Daarom beschermde Allah hem voor het kwade hunner plannen, en een zware straf kwam over het volk van Pharao;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,46,'Aan het Vuur zullen zij morgen en avond worden blootgesteld. En de Dag waarop het Uur zal komen, zal er worden gezegd: \"Doet Pharao´s volk de strengste straf ondergaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,47,'En wanneer zij met elkander in het Vuur zullen twisten, zullen de zwakken tot de trotsen zeggen: \"Voorzeker, wij waren uw volgelingen; wilt gij dan nu een gedeelte van het Vuur van ons wegnemen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,48,'Zij die trots waren zullen zeggen: \"Wij zijn er allen in. Allah heeft nu over Zijn dienaren recht gesproken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,49,'En degenen die in het Vuur zijn zullen tot de bewaarders der hel zeggen: \"Bidt uw Heer, een dag van onze straf te verlichten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,50,'Zij zullen antwoorden: \"Kwamen uw boodschappers niet tot u met duidelijke bewijzen?\" Zij zullen zeggen: \"Ja zeker.\" De bewaarders zullen antwoorden: \"Bidt dan.\" Maar het bidden der ongelovigen is nutteloos.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,51,'Voorwaar, Wij helpen Onze boodschappers en de gelovigen in het leven dezer wereld en op de Dag waarop de getuigen zullen opstaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,52,'De Dag, waarop de verontschuldiging van de onrechtvaardigen niets zal baten en voor hen zal de vloek en het kwade tehuis zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,53,'En Wij gaven Mozes de leiding, en deden de kinderen van Isra�l het Boek erven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,54,'Als richtsnoer en aanmaning voor mensen van begrip.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,55,'Heb geduld, voorzeker, Allah´s belofte is waar. En vraag bescherming tegen uw zonde en eert uw Heer ´s morgens en ´s avonds met de lof die Hem toekomt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,56,'Zij die over de tekenen van Allah twisten zonder dat hun het gezag daartoe verleend is, hebben in hun innerlijk niets dan trots, die zij niet kunnen verwerkelijken. Zoekt daarom uw toevlucht bij Allah. Waarlijk, Hij is de Alhorende, de Alziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,57,'Voorzeker, de schepping der hemelen en der aarde is groter dan de schepping der mensen maar de meeste mensen beseffen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,58,'De blinden en de zienden zijn niet gelijk; noch zijn zij, die geloven en goede werken doen gelijk aan hen die kwaad doen. Gering is de lering die gij hieruit trekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,59,'Het Uur zal zeker komen, daaraan is geen twifel; toch geloven de meeste men sen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,60,'En uw Heer zegt: \"Aanbidt Mij; Ik zal uw gebed verhoren. Maar zij die te hoogmoedig zijn om Mij te aanbidden, zullen veracht de hel binnengaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,61,'Allah is Degene Die de nacht voor u aanwees opdat gij er in moogt rusten en de dag om u licht te geven. Voorwaar, Allah is vol genade voor de mensen, toch zijn de meeste mensen ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,62,'Zo is Allah uw Heer, de Schepper aller dingen. Er is geen God naast Hem. Waarheen wordt gij dan afgewend?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,63,'Zo worden degenen, die de tekenen van Allah verloochenen, afgeleid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,64,'Allah is het, Die de aarde voor u als een rustplaats heeft gemaakt en de hemelen als gewelf, Die u gevormd heeft en u een schone vorm heeft gegeven en u van goede dingen heeft voorzien. Dit is Allah uw Heer. Gezegend is Allah, de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,65,'Hij is de Levende, er is geen God naast Hem. Aanbidt daarom Hem alleen, oprecht zijnde in gehoorzaamheid tot Hem. Alle lof behoort aan Allah, de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,66,'Zeg: \"Het is mij verboden diegenen te aanbidden die gij naast Allah aanroept daar er duidelijke bewjizen van mijn Heer tot mij zijn gekomen; en het is mij geboden mij te onderwerpen aan de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,67,'Hij is het Die u uit stof schiep, dan uit een levenskiem en uit een klonter bloed, vervolgens brengt Hij u voort als een kind, dan bereikt gij de volwassenheid, daarna wordt gij oud. Sommigen sterven eerder, en anderen onder u zullen een vastgestelde tijd bereiken; opdat gij tot inzicht komt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,68,'Hij is het Die leven geeft en doet sterven. En wanneer Hij iets besluit, zegt Hij slechts: \"Wees\", en het wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,69,'Hebt gij degenen niet gezien, die over de tekenen van Allah redetwisten? Hoe worden zij afgewend!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,70,'Degenen die het Boek en hetgeen waarmee Wij Onze boodschappers zonden, verloochenden, zullen weldra (de waarheid) te weten komen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,71,'Wanneer zij met boeien en kettingen om hun hals zullen worden gesleept');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,72,'In kokend water; dan zullen zij in het vuur worden geworpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,73,'Dan zal er tot hen worden gezegd: \"Waar zijn (de afgoden), die gij met Allah hadt vereenzelvigd?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,74,'\"Naast Allah?\" Zij zullen zeggen: \"Zij zijn verloren gegaan. Neen, wij plachten voorheen niets te aanbidden.\" Zo laat Allah de ongelovigen dwalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,75,'Er zal tot hen worden gezegd: \"Dit is omdat gij op aarde ten onrechte placht te jubelen en omdat gij hoovaardig waart.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,76,'\"Gaat de poorten der hel binnen daarin vertoevende. Kwaad is nu het tehuis voor de laatdunkenden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,77,'Heb daarom geduld, Allah´s belofte is zeker waar. Of Wij u de straf waarmede Wij hen bedreigen gedeeltelijk tonen of u (v��r dien) doen sterven, zij zullen toch tot Ons worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,78,'En Wij zonden boodschappers v��r u, sommigen van hen hebben Wij vermeld en anderen hebben Wij niet genoemd en geen boodschapper kan een teken brengen zonder Allah´s gebod. En wanneer Allah´s gebod komt, wordt er in waarheid geoordeeld en dan gaan de leugenaars verloren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,79,'Het is Allah, Die u vee heeft gegeven, opdat gij op sommige dieren moogt rijden en andere als voedsel gebruiken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,80,'En gij hebt andere voordelen van hen - zodat gij door hen elke behoefte die in uw innerlijk is, tevreden moogt stellen. En door hen (te land) en op schepen (ter zee) wordt gij gedragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,81,'En Hij toont u Zijn tekenen; welke van de tekenen van Allah wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,82,'Hebben zij niet op aarde gereisd en gezien wat het einde was van degenen die voor hen waren? Zij waren groter in aantal dan dezen en machtiger in kracht, en in de sporen die zij op aarde achterlieten. Maar alles wat zij verwierven baatte hen niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,83,'En toen hun boodschappers met duidelijke tekenen tot hen kwamen, namen zij genoegen met de kennis die zij bezaten. en de straf waarover zij spotten, verstrikte hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,84,'En toen zij Onze straf zagen zeiden zij: \"Wij geloven in Allah als de Enige en wij verwerpen alles wat wij vroeger met Hem plachten te vereenzelvigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,40,85,'Maar nadat zij Onze straf hadden gezien kon hun geloof hun niet meer baten. Dit is Allah´s wet die haar loop neemt ten opzichte van Zijn dienaren en zo gingen de ongelovigen verloren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,1,'Haa Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,2,'Een openbaring van de Barmhartige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,3,'Een Boek waarvan de verzen zijn verklaard als duidelijke verkondiging voor mensen die kennis bezitten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,4,'Als drager van goede tijding en als waarschuwer. Maar de meesten hunner wenden zich af, zodat zij niet luisteren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,5,'Zij zeggen: \"Onze harten zijn gesluierd voor datgene waartoe gij ons roept en er is doofheid in onze oren en tussen u en ons is een scherm. Daarom ga door met uw werk, wij werken ook.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,6,'Zeg: \"Ik ben slechts een mens zoals gij. Het is mij geopenbaard dat uw God slechts ��n God is; weest derhalve oprecht jegens Hem en vraagt vergiffenis van Hem.\" En wee de afgodendienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,7,'Die geen Zakaat geven en aan het Hiernamaals niet geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,8,'Wat hen betreft, die geloven en goede werken doen, zij zullen zeker een loon ontvangen dat nooit zal ophouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,9,'Zeg: \"Verwerpt gij werkelijk Hem Die de aarde in twee dagen schiep? En richt gij gelijken aan Hem op, hoewel Hij de Heer der Werelden is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,10,'Hij heeft de bergen daarop gesteld en heeft deze gezegend en er op (de aarde) de voedingsmiddelen bepaald, in vier dagen, gelijkelijk voor de zoekenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,11,'Dan wendde Hij Zich tot de hemel terwijl deze een soort damp was en zeide hiertegen en tot de aarde: \"Komt beiden, willens of onwillens.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,12,'Zij zeiden: \"Wij komen gewillig.\" Zo voltooide Hij hen als de zeven hemelen in twee dagen, en Hij wees elke hemel zijn werk aan. En Wij versierden de laagste hemel met lichten ter bescherming. Dat is de verordening van de Almachtige, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,13,'Maar indien zij zich afwenden, zeg dan: \"Ik waarschuw u voor een bliksemstraal, zoals de bliksem die Aad en Samoed achterhaalde. \"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,14,'Toen hun boodschappers van v��r hen en achter hen tot hen kwamen, zeggende: \"Aanbidt niets dan Allah\", zeiden zij: \"Als onze Heer het had gewild, zou Hij beslist engelen hebben nedergezonden. Derhalve verwerpen wij datgene waarmede gij gezonden zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,15,'Maar de Aad handelden ten onrechte laatdunkend op aarde en zeiden: \"Wie is machtiger dan wij?\" Wisten zij niet dat Allah, Die hen schiep machtiger was dan zij? Doch zij plachten Onze tekenen te verwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,16,'Daarom zonden Wij tegen hen een razende wind gedurende verscheidene noodlottige dagen, opdat Wij hen in dit leven de straf der vernedering mochten doen ondergaan. De straf van het Hiernamaals zal zeker nog vernederender zijn en zij zullen niet worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,17,'En wat de Samoed betreft, Wij gaven leiding, maar zij verkozen blindheid boven het rechte pad, daarom trof hen de bliksem van de straf der vernedering, voor hetgeen Zij hadden verdiend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,18,'En Wij redden de gelovigen, die godvruchtig waren;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,19,'Op de dag waarop Allah´s vijanden, in groepen verdeeld naar het Vuur zullen worden gebracht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,20,'Tot zij het bereiken, zullen hun oren, ogen en huiden tegen hen getuigenis afleggen over wat zij plachten te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,21,'En zij zullen tot hun huiden zeggen: \"Waarom getuigt gij tegen ons?\" Deze zullen antwoorden: \"Allah Die alles heeft doen spreken - deed ook ons spreken. En Hij is het Die u de eerste keer schiep en gij zijt tot Hem teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,22,'Gij waart niet in staat u te verschuilen, opdat uw oren, uw ogen en uw huiden geen getuigenis tegen u zouden afleggen, maar gij dacht, dat Allah onbekend was met het geen gij deedt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,23,'En deze gedachte van u, die gij over uw Heer koesterdet, heeft u tot verderf gebracht, daarom behoort gii tot de verliezers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,24,'Indien zij nu volharden, is het Vuur hun tehuis; en als zij om verontschuldiging vragen, behoren zij niet tot hen aan wie deze wordt verleend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,25,'Wij stelden gezellen (duivelen) voor hen aan, die hetgeen v��r hen en achter hen was schoonschijnend maakten, en het woord werd tegen hen van kracht, met de volkeren van djinn en mensen die v��r hen leefden. Zeker, zij waren verliezers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,26,'En de ongelovigen zeggen: \"Luistert niet naar deze Koran, maar maakt leven daarbij opdat gij de overhand moogt krijgen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,27,'Maar Wij zullen zeker de ongelovigen een strenge straf doen toekomen en Wij zullen hun slechtste daden vergelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,28,'Dat is het loon van Allah´s vijanden: het Vuur. Daar zullen zij een langdurig tehuis hebben; een vergelding, omdat zij Onze tekenen niet erkenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,29,'En de ongelovigen zullen zeggen: \"Onze Heer, toon ons degenen der djinn en der mensen die ons deden dwalen, opdat wij hen onder onze voeten mogen plaatsen zodat zij tot de vernederden behoren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,30,'Voorzeker zij, die zeggen: \"Onze Heer is Allah,\" en daarin standvastig blijven, op hen zullen de engelen nederdalen: \"Vreest niet, noch treurt; maar verheugt u over het paradijs dat u wordt beloofd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,31,'\"Wij zijn uw vrienden in dit leven en in het Hiernamaals. Daarin zult gij alles krijgen wat uw ziel zal wensen, en daarna zult gij alles hebben waarom gij vraagt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,32,'Als onthaal van de Vergevensgezinde, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,33,'En wie spreekt beter woord dan hij die mensen tot Allah uitnodigt en goede werken doet en zegt: \"Waarlijk, ik behoor tot de Moslims.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,34,'Het goede en kwade zijn niet gelijk. Daarom weerstaat (het kwade) door hetgeen best is. Dan ziet, degene met wie gij vijandschap hebt, hij zal als uw boezemvriend worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,35,'Maar het is niemand gegeven behalve de geduldigen noch is het iemand gegeven behalve zij die een grote gave hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,36,'En als een ophitsing van Satan u treft, zoek dan toevlucht tot Allah. Waarlijk, Hij is de Alhorende, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,37,'En onder Zijn tekenen zijn de dag en de nacht, de zon en de maan; derhalve werpt u niet neder voor de zon of de maan maar werpt u neder voor Allah Die hen schiep, indien gij Hem wilt aanbidden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,38,'Maar al tonen zij (de ongelovigen) hoogmoed, degenen die bij uw Heer zijn, verheerlijken Hem dag en nacht, en zij vervelen zich nooit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,39,'Dit behoort tot Zijn tekenen, dat gij de aarde droog en verschroeid ziet, maar wanneer Wij er water op nederzenden, beweegt zij zich en zet uit. Zeker Hij, Die haar leven geeft, zal ook de doden opwekken. Voorwaar, Hij heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,40,'Voorzeker, zij die Onze tekenen verdraaien zijn niet voor Ons verborgen. Is dan hij die in het Vuur geworpen wordt beter dan degene die veilig blijft op de Dag der Opstanding? Doet wat gij wilt. Voorwaar, Hij ziet alles wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,41,'Voorzeker, zij die niet in de Vermaning geloven als deze tot hen komt (zijn de verliezers); waarlijk het is een machtig Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,42,'Geen valsheid kan het beroeren, van voren noch van achteren. Het is een Openbaring van de Alwijze, de Geprezene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,43,'Er is niets tot u gezegd behalve het geen aan de boodschappers v��r u was gezegd. Uw Heer is de Heer der vergevensgezindheid en der pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,44,'Indien Wij deze Koran in een vreemde taal hadden gegeven, zouden zij zeker hebben gezegd: \"Waarom zijn zijn verzen niet duidelijk gemaakt? Is Arabisch en niet-Arabisch gelijk?\" Zeg: \"Het is een leiding en een genezing voor de gelovigen.\" Maar de ongelovigen hebben doofheid in hun oren en het is duister voor hen. Zij worden aangeroepen vanaf een verre plaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,45,'En Wij gaven Mozes het Boek, maar men verschilde er over van mening; en indien het woord van uw Heer er niet aan was voorafgegaan zou er zeker over hen geoordeeld zijn, want waarlijk zij verkeerden er in een verontrustende twijfel over.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,46,'Wie goed doet, doet dit voor zijn eigen ziel; en wie kwaad bedrijft, het is er tegen. En uw Heer is in het geheel niet onrechtvaardig jegens Zijn dienaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,47,'Naar Hem alleen wordt de kennis van het Uur verwezen. En geen vruchten komen voort uit hun bloemscheden noch wordt een enkele vrouw zwanger noch wordt zij verlost, dan met Zijn kennis. En de Dag waarop Hij tot hen zal roepen: \"Waar zijn Mijn medegoden?\" zullen zij antwoorden: \"Wij verklaren U, dat niemand van ons getuige is.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,48,'En de afgoden welke zij voorheen plachten aan te roepen, gaan voor hen verloren en zij zullen weten dat zij gee toevluchtsoord hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,49,'De mens wordt niet moe het goede te vragen; maar als het kwade hem treft vertwijfelt hij en wordt wanhopig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,50,'En als Wij hem barmhartigheid tonen nadat enige kommer over hem is gekomen, zal hij zeker zeggen: \"Dit komt mij toe en ik denk niet dat het Uur zal plaatsvinden. Maar indien ik tot mijn Heer word teruggebracht zal ik zeker bij Hem het allerbeste vinden.\" Doch Wij zullen stellig de ongelovigen alles vertellen wat zij deden en Wij zullen hen zeker een zware straf doen ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,51,'Wanneer Wij gunsten aan de mens verlenen wendt hij zich af en gaat terzijde, maar wanneer het kwade hem raakt, ziet! dan offert hij lange, lange gebeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,52,'Zeg: \"Bedenkt u: Als dit (de Koran) van Allah is en gij verwerpt het - wie begaat grotere dwaling dan hij die zich hevig daartegen verzet?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,53,'Weldra zullen Wij hun Onze tekenen in henzelf en over afgelegen streken tonen, tot het hun duidelijk wordt dat dit de Waarheid is. Is het niet genoeg dat uw Heer Getuige over alle dingen is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,41,54,'Ziet toe! Zij zijn in twijfel over de ontmoeting met hun Heer. Voorwaar, Hij omvat alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,1,'Haa Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,2,'Ain Sien Qaaf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,3,'Zo heeft Allah, de Machtige, de Wijze aan u en aan degenen die v��r u waren, geopenbaard.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,4,'Aan Hem behoort hetgeen in de hemelen en op aarde is en Hij is de Hoogste, de Grootste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,5,'Het is nabij dat de hemelen zullen worden uiteengescheurd boven hen, maar de engelen verheerlijken hun Heer met de lof die Hem toekomt en vragen vergiffenis voor hen die op aarde zijn. Ziet toe! Allah is de Vergevensgezinde, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,6,'En degenen, die naast Hem beschermers tot zich nemen, over hen waakt Allah: maar gij (profeet) zijt geen voogd over hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,7,'Zo hebben Wij u de Koran in het Arabisch geopenbaard, opdat gij de Moeder der steden (Makka) en al het omringende moogt waarschuwen; dus waarschuwt (hen) voor de Dag der Verzameling waaromtrent geen twijfel is. Een deel zal in het paradijs zijn, en een ander deel in het laaiend Vuur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,8,'Indien Allah wilde kon Hij hen tot een enkel volk hebben gemaakt, maar Hij laat in Zijn barmhartigheid toe wie Hij wil. Doch de onrechtvaardigen zullen geen beschermer of helper hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,9,'Hebben zij naast Hem besehermers tot zich genomen terwijl Allah de Besehermer is? Hij maakt de doden levend en heeft macht over alle dingen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,10,'En waarover gij ook moogt verschillen, de beslissing ervan rust bij Allah. Zeg: \"Zo is Allah, mijn Heer. In Hem stel ik mijn vertrouwen, en tot Hem wend ik mij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,11,'Hij is de Schepper der hemelen en der aarde. Hij heeft u tot paren gemaakt, evenals het vee, te uwen behoeve. Daardoor vermenigvuldigt Hij u. Er is niets aan Hem gelijk en Hij is de Alhorende, de Alziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,12,'Aan Hem behoren de schatten van de hemelen en de aarde. Hij vergroot en bekrimpt de voorziening voor wie Hij wil. Hij heeft voorzeker kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,13,'Hij schreef u dezelfde godsdienst voor, die Hij aan Noach oplegden en die Wij bovendien aan u openbaren en die Wij Abraham, Mozes en Jezus oplegden: \"Bevestigt deze godsdienst en weest er niet in verdeeld.\" Voor de afgodendienarenis dat moeilijk waartoe gij hen roept. Allah kiest voor Zich wie Hij wil en leidt hem die zich (in berouw) tot Hem wendt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,14,'En zij waren slechts verdeeld, nadat de kennis tot hen was gekomen, door zelfzuchtige afgunst onder elkander. En ware het niet dat een Woord reeds van uw Heer was uitgegaan voor een vastgestelde tijd, dan zou de zaak voorzeker tussen hen geoordeeld zijn. En waarlijk, zij die het Boek erfden, na hen, zijn er in een verontrustende twijfel over.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,15,'Nodig hen daarom hiertoe uit. En wees standvastig zoals u is geboden en volg hun slechte begeerten niet, maar zeg: \"Ik geloof in elk Boek dat Allah heeft neder gezonden en het is mij geboden rechtvaardig tegenover u te handelen. Allah is onze Heer en uw Heer. Aan ons onze werken en aan u uw werken. Laat er geen twist tussen u en ons bestaan. Allah zal ons tezamen brengen en tot Hem is de terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,16,'En zij die over Allah twisten nadat zij Hem aanvaard hebben, hun twist is waardeloos in de ogen van hun Heer; er is toorn over hen en er zal een strenge straf voor hen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,17,'Allah is het Die dit Boek (de Koran) met de Weegschaal in waarheid heeft nedergezonden. Wat weet gij waarschijnlijk is het Uur nabij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,18,'De ongelovigen vragen het te verhaasten maar de gelovigen vrezen er voor en weten dat het de Waarheid is. Ziet toe! Zij die over het Uur redetwisten zijn ver afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,19,'Allah is Zorgzaam tegenover Zijn dienaren. Hij zorgt voor wie Hij wil. Hij is de Sterke, de Machtige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,20,'Wie de oogst van het Hiernamaals wenst, diens oogst doen Wij toenemen, doch wie naar de oogst der wereld verlangt ook hem geven Wij daarvan, maar hij zal in het Hiernamaals geen deel hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,21,'Hebben zij (afgodendienaren) dan medegoden, die hun een godsdienst hebben voorgeschreven welke Allah verboden heeft? Ware Ons gebod voor het laatste gericht niet uitgevaardigd,dan zou de zaak onder hen geoordeeld zijn geweest. Want de onrechtvaardigen zullen zeker een pijnlijke straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,22,'Gij zult de onrechtvaardigen in vrees zien voor hetgeen zij hebben verdiend op de Dag des Oordeels en het zal hen zeker treffen. Maar degenen, die geloven en goede werken doen, zullen in de tuinen van het paradijs zijn. Zij zullen bij hun Heer alles vinden wat zij wensen. Dat is de grote genade.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,23,'Dit is het waarvan Allah aan Zijn dienaren die geloven en goede werken doen, de blijde tijdingen geeft. Zeg: \"Ik vraag u geen loon voor (mijn prediking), behalve liefde van verwanten.\" En hij die het goede verricht zullen Wij in goedheid doen toenemen. Voorzeker, Allah is Vergevensgezind, Waarderend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,24,'Zeggen zij: \"Hij heeft een leugen over Allah verzonnen?\" Als Allah het wilde kon Hij uw hart verzegelen. Maar Allah zal de leugen uitvagen en de Waarheid door Zijn woord bevestigen. Voorzeker, Hij weet wat in de harten is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,25,'Hij Die berouw aanvaardt van Zijn dienaren en (hun) zonden vergeeft. Hij weet wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,26,'En Hij verhoort de gelovigen die goede werken doen en geeft nog meer uit Zijn overvloed maar de ongelovigen zullen een strenge straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,27,'Indien Allah de voorziening voor Zijn dienaren zou hebben vergroot, zouden zij op aarde verderf hebben veroorzaakt: Hij zendt echter met mate neder zoals Hij dat wil. Hij kent en ziet Zijn dienaren inderdaad goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,28,'Hij is het, Die regen nederzendt en Zijn barmhartigheid uitspreidt nadat men daaraan gewanhoopt heeft. Hij is de Beschermer, de Geprezene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,29,'En onder Zijn tekenen is de Schepping der hemelen en der aarde, en der levende wezens die Hij daarin heeft verspreid. En Hij heeft macht hen te verzamelen wanneer Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,30,'Welke ramp u ook overkomt, het is door hetgeen uw handen hebben gewrocht. Doch Hij vergeeft vele dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,31,'En gij kunt op aarde niet ontkomen noch hebt gij een enkele vriend of helper buiten Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,32,'En onder Zijn tekenen zijn de schepen als bergen op zee.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,33,'Als Hij wil kan Hij de wind stillen zodat zij bewegingloos staan op de oppervlakte daarvan! Daarin zijn voorzeker tekenen voor elke geduldige, dankbare (mens).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,34,'Of Hij kan ze vernietigen: wegens hetgeen zij (de mensen) verdienen - maar Hij vergeeft veel. -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,35,'Opdat zij die over de tekenen van Allah redetwisten, mogen inzien dat zij geen toevlucht hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,36,'Wat u is gegeven is slechts een voorziening voor dit leven, en hetgeen bij Allah is, is beter en van langere duur voor de gelovigen die in hun Heer vertrouwen stellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,37,'Voor degenen die de zwaarste zonden en gruweldaden vermijden en die wanneer zij vertoornd zijn, vergeven;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,38,'En voor degenen die naar hun Heer luisteren en hun gebeden houden en wier manier van handelen een zaak van wederzijds overleg is en voor degenen die geven van hetgeen waarmee Wij hen hebben voorzien;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,39,'En voor degenen die, als een aanval hen treft, zich verdedigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,40,'Doch de vergelding van het kwade is het daaraan gelijke; maar wie vergeeft en verbetering voor ogen houdt, zijn loon rust bij Allah. Voorzeker, Hij houdt niet van de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,41,'Maar er is geen verwijt tegen hen die zich verdedigen nadat hun onrecht is aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,42,'Het verwijt is slechts tegen hen, die de mensen onrecht aandoen en ten onrechte in het land opstand veroorzaken. Dezen zullen een pijnlijke straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,43,'En hij die geduldig is en vergeeft, - dat is voorzeker een (teken) van een sterk karakter.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,44,'Hij die Allah laat dwalen, zal buiten Hem geen beschermer hebben. En gij zult de onrechtvaardigen zien die, wanneer zij de straf zullen aanschouwen, zeggen: \"Is er geen weg tot terugkeer?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,45,'En gij zult hen aan het Vuur zien blootgesteld, door schande vernederd, terwijl zij er met neergeslagen ogen naar kijken. De gelovigen zullen zeggen: \"De verliezers zijn inderdaad zij die zichzelf en hun familie op de Dag der Opstanding hebben verloren.\" Ziet toe! de onrechtvaardigen zullen een blijvende straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,46,'Zij hebben buiten Allah geen vrienden die hen kunnen helpen. En er is voor hen die Allah laat dwalen geen uitweg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,47,'Luistert naar uw Heer voordat Allah´s Dag komt die niemand zal kunnen tegenhouden. Op die Dag zal er voor u geen toevlucht zijn, noch enige kans op ontkenning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,48,'Maar indien zij zich afwenden hebben Wij u niet als wachter over hen gezonden. Het is alleen uw plicht de boodschap over te brengen. En waarlijk, wanneer Wij de mens Onze barmhartigheid betuigen, verheugt hij zich er in. Maar indien hun een kwaad overkomt door hetgeen hun handen hebben bedreven, dan voorzeker, is de mens ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,49,'Aan Allah behoort het koninkrijk der hemelen en der aarde. Hij schept wat Hij wil. Hij schenkt vrouwelijke en mannelijke kinderen aan wie Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,50,'Of Hij mengt ze, mannelijk en vrouwelijk en Hij maakt onvruchtbaar wie Hij wil. Voorwaar, Hij is Alwetend, Almachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,51,'Het is voor een mens niet mogelijk dat Allah tot hem zou spreken anders dan door ingeving of van achter een sluier of door een boodschapper te zenden om door Zijn gebod te openbaren wat Hij wil. Voorwaar, Hij is de Verhevene, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,52,'En zo hebben Wij u een woord door ons gebod geopenbaard. Gij wist niet wat het Boek noch wat het geloof was. Maar Wij maakten het tot een licht waarbij Wij leiding verlenen aan diegenen Onzer dienaren die Wij willen. Voorzeker, gij leidt de mens zeker naar het rechte pad,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,42,53,'Het pad van Allah, aan Wie hetgeen in de hemelen en op aarde is, toebehoort. Ziet toe, tot Allah is de terugkeer van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,1,'Haa Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,2,'Bij het duidelijke Boek;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,3,'Voorzeker, Wij hebben het tot een duidelijke verkondiging gemaakt, opdat gij het moogt begrijpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,4,'En voorwaar, dit is in het Boek der Boeken bij Ons, verheven, vol van wijsheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,5,'Zullen Wij u dit dan niet in herinnering brengen, omdat gij een buitensporig volk zijt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,6,'Hoevele profeten hebben Wij tot de vroegere geslachten gezonden!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,7,'En er kwam tot hen nooit een profeet of zij bespotten hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,8,'Daarom vernietigden Wij de sterksten onder hen ofschoon het voorbeeld van de vroegere volkeren reeds voorafgegaan was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,9,'En indien gij hun vraagt: \"Wie schiep de hemelen en de aarde?\" zullen zij zeker zeggen: \"De Machtige, de Alwetende.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,10,'Die de aarde voor u als wieg heeft gemaakt en uw wegen daarop (aangaf), opdat gij de goede weg moogt volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,11,'En Die water in juiste maat van de hemel nederzendt, waardoor Wij een dood land doen herleven. Zo zult ook gij worden opgewekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,12,'En Die alles in paren schiep en u schepen heeft gegeven en dieren waarop gij rijdt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,13,'Opdat gij stevig op hun rug moogt zitten en dan, wanneer gij er stevig op zit, de gunst van uw Heer moogt gedenken en zeggen: \"Glorie zij Hem, Die dit in onze dienst heeft gesteld want wij konden die zelf niet onderwerpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,14,'En voorzeker wij moeten tot onze Heer wederkeren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,15,'En sommigen maken van Zijn dienaren gelijken aan Allah. Waarlijk de mens is klaarblijkelijk ondankbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,16,'Heeft Hij uit de wezens die Hij schiep dochters genomen en u met zonen ge�erd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,17,'Doch wanneer aan een hunner nieuws wordt gegeven van hetgeen hij over de Barmhartige vertelt, is hij toornig en wordt zijn gelaat donker.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,18,'(Schrijft gij iemand aan God toe) die omhangen met sieraden wordt grootgebracht en die zich bij een twist moeilijk kan uiten?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,19,'En zij maakten de engelen, die dienaren zijn van de Barmhartige, tot vrouwelijke wezens. Waren zij dan van hun schepping getuige? Hun getuigenis zal worden opgetekend en zij zullen tot rekenschap worden geroepen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,20,'Zij zeggen: \"Indien de Barmhartige had gewild zouden wij hen niet hebben aanbeden.\" Zij hebben daar in het geheel geen kennis van, zij vermoeden slechts.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,21,'Hebben Wij hun ooit te voren een Boek gegeven waar zij zich aan vasthouden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,22,'Neen, zij zeggen: \"Wij zagen onze vaderen een godsdienst volgen en wij richten ons naar hun voetstappen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,23,'En evenzo zonden Wij geen waarschuwer naar een stad v��r u of de rijken hiervan zeiden: \"Wij zagen onze vaderen een godsdienst volgen, en wij treden in hun voetstappen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,24,'Zij (de boodschappers) zeiden: \"Hoewel wij u een betere leiding brengen dan hetgeen gij uw vaderen hebt zien volgen?\" Zij zeiden: \"Waarlijk, wij verwerpen datgene waarmede gij gezonden zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,25,'Daarom straften Wij hen; ziet dan hoe het einde der loochenaars was,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,26,'En (gedenkt) hoe Abraham tot zijn vader en zijn volk zeide: \"Ik heb voorzeker iets uitstaande met hetgeen gij aanbidt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,27,'Doch Hij, Die mij schiep zal mij zeker leiden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,28,'En Hij maakte dit een blijvende leer voor zijn nakomelingen, opdat zij zich mochten bekeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,29,'Waarlijk, Ik liet dezen en hun vaderen in welvaart leven totdat de Waarheid en een welsprekende boodschapper, die alles verduidelijkte, tot hen kwam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,30,'Maar nu de Waarheid tot hen is gekomen, zeggen zij: \"Dit is tovenarij en wij zullen er niet in geloven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,31,'En men zegt: \"Waarom is deze Koran niet aan een groot man uit de twee steden geopenbaard?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,32,'Delen deze de barmhartigheid van uw Heer uit? Wij zijn het, Die in het tegenwoordige leven middelen van bestaan onder hen uitdelen en Wij verheffen sommigen hunner boven anderen in graden, opdat sommigen hunner anderen te werk mogen stellen. En de barmhartigheid van uw Heer is beter dan hetgeen zij vergaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,33,'Ware er niet (het gevaar) dat alle mensen ��n groep zouden vormen, Wij zouden voor degenen die de Barmhartige verwerpen, daken voor hun huizen en trappen waarop zij naar boven konden lopen van zilver hebben gemaakt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,34,'En deuren voor hun huizen; en rustbanken, waarop zij konden rusten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,35,'En versieringen. Maar dat alles is niets dan een voorziening voor het tegenwoordige leven, doch het Hiernamaals bij uw Heer is voor de godvruchtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,36,'En wie zich van de aanbidding van de Barmhartige afkeert, achter hem zetten Wij een satan, die zijn metgezel wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,37,'En voorwaar, deze leidt hem van de rechte weg af, en toch denkt hij dat hij juist geleid wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,38,'Wanneer zo iemand bij Ons komt, zegt hij tegen zijn metgezel: \"O, ware er tussen u en mij een afstand van het Oosten naar het Westen geweest. Wat is dit een boze metgezel!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,39,'Indien gij onrechtvaardig handeldet, zal het u heden niet baten dat gij samen dezelfde straf ondergaat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,40,'Kunt gij dan de doven doen horen en de blinden en degenen die klaarblijkelijk dwalen, leiden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,41,'En indien Wij u wegnemen (uit hun midden) zullen Wij hen gewis bestraffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,42,'En indien Wij u datgene tonen waarmede Wij hen bedreigen dan voorzeker hebben Wij macht over hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,43,'Houd u daarom vast aan hetgeen u is geopenbaard; gij zijt voorzeker op het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,44,'Waarlijk, het is een eer voor u en voor uw volk en gij zult weldra (daarover) worden ondervraagd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,45,'En vraagt aan Onze boodschappers die Wij v��r u zonden: \"Stelden wij naast de Barmhartige andere goden om te worden aanbeden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,46,'Wij zonden Mozes met Onze tekenen naar Pharao en zijn leiders, en hij zeide: \"Ik ben waarlijk een boodschapper van de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,47,'Maar toen hij met Onze tekenen tot hen kwam, ziet, bespotten zij hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,48,'En Wij toonden hun geen teken of het ene was groter dan het andere en Wij deden hen een straf ondergaan opdat zij zich mochten bekeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,49,'En zij zeiden (tot Mozes): \"O, gij tovenaar, bid voor ons tot uw Heer overeenkomstig het verdrag dat Hij met u heeft gesloten, wij zullen zeker de leiding volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,50,'Maar toen Wij de straf van hen wegnamen, ziet, zij braken hun woord.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,51,'En Pharao riep tot zijn volk: \"O, mijn volk! Behoort het koninkrijk van Egypte niet aan mij toe? En stromen deze rivieren niet op mijn bevel? Kunt, gij dat niet inzien?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,52,'Of ben ik niet beter dan deze onaanzienlijke man die zich nauwelijks kan uitdrukken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,53,'Waarom zijn hem dan geen armbanden van goud geschonken of komen engelen niet in processie met hem?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,54,'Zo maakte hij zijn volk tot dwazen en zij gehoorzaamden hem. Zij waren inderdaad een overtredend volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,55,'Toen zij Ons vertoornden, straften Wij hen en verdronken hen allen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,56,'Wij deden hen vergaan en maakten dit tot een voorbeeld voor de komende (geslachten).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,57,'En wanneer de zoon van Maria als voorbeeld wordt genoemd, ziet, uw volk rijst op en keerde zich of in ofschuw.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,58,'En zij roepen: \"Zijn onze goden beter of is hij beter?\" Zij zeggen dit tot u alleen om te twisten. Waarlijk zij zijn een twistziek volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,59,'Hij (Jezus) is niets dan een dienaar wie Wij Onze gunst schonken en Wij stelden hem tot voorbeeld voor de kinderen van Isra�l.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,60,'En indien Wij het wilden, konden Wij engelen uit uw midden tot opvolgers op aarde maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,61,'Maar dit is een teken van het Uur. Twijfelt er daarom niet aan, maar volgt Mij. Dit is het rechte pad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,62,'En laat Satan u niet verleiden. Voorzeker, hij is voor u een openlijke vijand.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,63,'Toen Jezus met duidelijke bewijzen kwam, zeide hij: \"Waarlijk ik ben met wijsheid tot u gekomen opdat ik u iets van hetgeen waarover gij onderling verschilt duidelijk moge maken. Vreest daarom Allah en gehoorzaamt mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,64,'Voorwaar, Allah is mijn Heer en uw Heer. Dient Hem daarom. Dit is het rechte pad.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,65,'Maar vele groepen uit hun midden werden onenig. Wee de onrechtvaardigen wegens de straf van een smartelijke Dag!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,66,'Zij wachten slechts tot het Uur plotseling over hen komt, terwijl zij het niet voorzien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,67,'Vrienden zullen op die Dag elkanders vijanden zijn. Doch de godvruchtigen:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,68,'\"O Mijn dienaren, geen vrees zal op deze Dag over u komen noch zult gij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,69,'Die in Onze tekenen geloofdet en onderdanig waart,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,70,'Gaat het paradijs binnen, gji en uw echtgenoten, gelukkig zijnde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,71,'Er zullen gouden schalen en bekers worden rondgereikt en er zal daarin alles zijn wat de zielen zich wensen en waar de ogen van genieten. En gij zult daarin vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,72,'Dit is de Tuin, die u is gegeven (als beloning) voor hetgeen gij deedt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,73,'Er is daarin een overvloed van fruit voor u waarvan gij kunt eten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,74,'De schuldigen zullen gewis de kastijding der hel blijven ondergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,75,'En deze zal voor hen niet verlicht worden en zij zullen daarin vertwijfelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,76,'Wij deden hun geen onrecht, doch zij waren tet die zichzelf onrecht plachten te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,77,'En zij zullen schreeuwen: \"O, Malik, laat uw Heer een einde aan ons maken.\" Deze zal antwoorden: \"Gij moet blijven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,78,'Wij brachten u zeker de Waarheid maar de meesten uwer waren er afkerig van.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,79,'Hebben zij een richting bepaald? Dan doen Wij dat ook.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,80,'Denken zij dat Wij hun heimelijk overleg en hun beraadslaging niet horen? Ja zeker! Onze boodschappers bij hen schrijven alles op.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,81,'Indien de Barmhartige een zoon had, dan zou ik de eerste der aanbidders zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,82,'Verheven is de Heer der hemelen en der aarde, de Heer van de Troon, boven al hetgeen zij vertellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,83,'Laat hen praten en zich vermaken totdat de Dag komt die hun is beloofd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,84,'En Hij is God in de hemel en op aarde en Hij is de Alwijze, de Alwetende,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,85,'En zalig is Hij, Wie het Koninkrijk der hemelen en der aarde en alles, wat er tussen is, toebehoort, en bij Hem is de kennis van het Uur, en tot Hem zult gij worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,86,'En degenen die zij naast Allah aanroepen bezitten geen macht tot bemiddeling, behalve hij, die de Waarheid getuigt; en dat weten zij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,87,'En indien gji hun vraagt: \"Wie schiep hen?\", zullen zij zeker zeggen: \"Allah\". Waarheen worden zij dan afgewend?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,88,'En zijn (des Profeten) gezegde: \"O, mijn Heer, dit is een volk dat niet gelooft.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,43,89,'Wend u dan van hen af en zeg: \"Vrede\": en weldra zullen zij (hun dwaijling) te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,1,'Haa Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,2,'Bij het duidelijke Boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,3,'Waarlijk, Wij openbaarden het in een gezegende nacht en Wij zijn de Waarschuwer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,4,'Waarin al het wijze tot in bijzonderheden is uitgelegd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,5,'Door Ons gebod. - Voorzeker, Wij zenden (de profeten)');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,6,'Als een barmhartigheid van uw Heer; waarlijk, Hij is de Alhorende, de Alwetende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,7,'Van de Heer der hemelen en der aarde en alles wat er tussen is, indien gij er vertrouwen in stelt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,8,'Er is geen God naast Hem. Hij doet leven en sterven, uw Heer en de Heer uwer voorvaderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,9,'Doch zij vermaken zich door te twijfelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,10,'Maar wacht op de Dag waarop de hemel een zichtbare damp zal voortbrengen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,11,'Die het volk zal omhullen. Dit zal een pijnlijke straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,12,'\"Onze Heer, neem de marteling van ons weg; waarlijk wij zijn gelovigen,\" (zullen zij zeggen).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,13,'Hoe kan er lering voor hen zijn, terwijl er tot hen een boodschapper is gekomen, die alles duidelijk maakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,14,'En zij hebben zich van hem afgewend zeggende: \"Hij is (door mensen) onderwezen, een bezetene.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,15,'Wij zullen de straf voor een wijle wegnemen, maar zij zullen stellig terugvallen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,16,'De de dag, waarop Wij hen met een machtige greep aanvallen, zullen Wij hen zeker straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,17,'Wij hebben het volk van Pharao reeds v��r hen beproefd en er kwam een eerwaardige boodschapper tot hen zeggende:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,18,'\"Geeft mij de dienaren van Allah, voorwaar, ik ben voor u een betrouwbare Boodschapper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,19,'En verheft u niet tegen Allah. Zeker, ik kom tot u met duidelijk gezag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,20,'En ik zoek toevlucht tot mijn en uw Heer, vrezende dat gij mij zult stenigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,21,'En laat mij alleen, indien gij mij niet gelooft.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,22,'Toen bad hij tot zijn Heer: \"Dit is inderdaad een zondig volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,23,'God zeide: \"Trek met Mijn dienaren ´s nachts weg want gij zult gewis worden achtervolgd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,24,'En doorwaad de zee terwijl deze rustig is. Voorzeker, zij zijn een schare die zal verdrinken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,25,'Hoevele tuinen en bronnen lieten zij achter!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,26,'En de korenvelden en de schone plaatsen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,27,'En de aangename dingen, waarvan zij genoten!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,28,'Zo was het, maar Wij deden ze een ander volk erven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,29,'De hemel en de aarde weenden niet om hen noch werd hun uitstel gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,30,'En zo redden Wij de kinderen van Isra�l van een vernederende kwelling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,31,'Door Pharao: want hij was trots en ��n der buitensporigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,32,'En Wij verkozen hen doelbewust boven andere volkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,33,'En Wij gaven hun tekenen, waar een duidelijke beproeving in lag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,34,'Deze mensen (de ongelovigen) zeggen:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,35,'\"Het is slechts onze enige dood want wij zullen niet worden opgewekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,36,'Breng onze voorvaderen dan terug indien gij de waarheid spreekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,37,'Zijn zij beter of het volk van Tobba of zij die v��r hen zijn geweest? Wij vernietigden hen omdat zij schuldig waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,38,'En Wij hebben de hemelen en de aarde en alles wat er tussen is niet als spel geschapen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,39,'Wij schiepen ze slechts in waarheid, maar de meesten hunner begrijpeen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,40,'Voorwaar, de Dag der beslissing is voor hen allen de vastgestelde tijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,41,'De Dag waarop een vriend de vriend niets zal baten noch zullen zij worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,42,'Met uitzondering van hen, die Allah genadig zal zijn. Voorwaar, Hij is de Almachtige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,43,'De boom van Zaqqoem,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,44,'Zal het voedsel voor de zondaar zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,45,'Als gesmolten koper zal het in de buik koken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,46,'Gelijk het koken van ziedend water.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,47,'\"Grijpt hem en sleurt hem in het midden van het laaiend Vuur;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,48,'Giet daarna als marteling kokend water op zijn hoofd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,49,'Proef dit! Voorzeker gij waart eens de machtige, de eerwaardige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,50,'Dit is inderdaad datgene waaraan gij twijfeldet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,51,'Voorwaar, de rechtvaardigen zullen in een woning van vrede en veiligheid zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,52,'Tussen tuinen en bronnen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,53,'Gekleed in fijne zijde en zwaar goudlaken naar elkander toegekeerd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,54,'Zo zal het zijn. En Wij zullen hen met schone meisjes die grote, mooie ogen hebben, verenigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,55,'Zij zullen daar naar alle fruitsoorten vragen (en deze ontvangen) in vrede en veiligheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,56,'Zij zullen daarin geen andere dood smaken na de eerste dood. En Hij heeft hen voor de straf van het laaiend Vuur behoed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,57,'Als een genade van uw Heer. Dit is de grootste zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,58,'Wij hebben hem (de Koran) gemakkelijk voor uw tong gemaakt, opdat men er lering uit moge trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,44,59,'Wacht daarom, zij wachten ook.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,1,'Haa Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,2,'De openbaring van dit Boek is van Allah, de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,3,'Voorwaar, in de hemelen en op aarde zijn tekenen voor de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,4,'En in de schepping van uzelf en alle medeschepselen, die Hij verspreidt (over de aarde), zijn tekenen voor een volk dat zekerheid van geloof wil hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,5,'En in de wisseling van nacht en dag en de voorziening die Allah uit de hemel nederzendt waardoor Hij de aarde doet herleven na haar dood en in de verandering van de winden, zijn eveneens tekenen voor een volk, dat zijn verstand gebruikt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,6,'Dit zijn de tekenen van Allah, die wij naar waarheid aan u voordragen. In welk woord buiten Allah en Zijn tekenen zullen zij dan geloven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,7,'Wee elke zondige leugenaar,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,8,'Die de woorden van Allah, die hem worden voorgedragen, hoort en niettemin minachtend ze trotseert alsof hij ze niet hoorde. - Geef hem tijding van een pijnlijke straf. -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,9,'En die, wanneer hij van Onze tekenen kennis krijgt er mee spot. Voor dezulken is er een vernederende straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,10,'Zij hebben de hel in het vooruitzicht; hetgeen zij verwierven zal hen niet baten noch de afgoden die zij buiten Allah tot beschermers namen, terwijl zij een grote straf zullen ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,11,'Dit is de leiding. En voor degenen die de tekenen van hun Heer verwerpen, is de kwelling van een pijnlijke straf gereed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,12,'Allah is Hij, Die de zee in uw dienst heeft gesteld, zodat schepen er op varen door Zijn gebod opdat gij naar Zijn overvloed zult zoeken en dat gij dankbaar moogt zjin.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,13,'En Hij heeft alles van Hem afkomstig in de hemelen en op aarde aan u onderworpen. Daarin zijn zeker tekenen voor een volk, dat nadenkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,14,'Zeg tegen de gelovigen, dat zij diegenen, die de dagen van Allah niet vrezen, moeten vergeven, zodat Hij Zelf het volk moge vergelden voor hetgeen zij verrichten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,15,'Wie goed doet, doet dat ten voordele van zijn eigen ziel: en wie kwaad doet, doet dat tegen zijn eigen ziel. Ten slotte zult gij tot uw Heer worden teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,16,'Wij gaven het Boek en de heerschappij en het profetenambt aan de kinderen van Isra�l en Wij hadden hen van goede dingen voorzien: Wij begunstigden hen boven de andere volkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,17,'En Wij gaven hun duidelijke uitleg over de godsdienst. En zij werden onenig slechts nadat kennis tot hen was gekomen door onderlinge afgunst. Voorwaar, uw Heer zal op de Dag der Opstanding over hen uitspraak doen omtrent datgene waarover zij het met elkaar oneens waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,18,'Dan hebben Wij u (o Mohammed) een duidelijke weg gewezen; volg die daarom, maar volg de begeerten der onwetenden niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,19,'Voorwaar, zij zullen u niets tegen Allah baten. En voorzeker, de onrechtvaardigen zijn vrienden onder elkander, maar Allah is de Vriend der godvruchtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,20,'Dit zijn de duidelijke bewijzen voor de mensen en een richtsnoer en barmhartigheid aan een volk dat zekerheid van geloof heeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,21,'Verbeelden diegenen die slechte daden doen, dat Wij hen zullen behandelen zoals hen, die geloven en goede werken verrichten, zodat hun leven en hun dood gelijk zullen zijn? Verkeerd is hun oordeel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,22,'Allah heeft de hemelen en de aarde in waarheid geschapen, zo dat elke ziel voor hetgeen zij verdient vergolden moge worden en hun zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,23,'Hebt gij hem gezien, die zijn eigen begeerte tot zijn God maakt, en die Allah liet dwalen, ondanks zijn kennis, en wiens oren en wiens hart Hij heeft verzegeld en op wiens ogen Hij een sluier heeft gelegd? Wie zal hem buiten Allah kunnen leiden? Wilt gij dan geen lering hieruit trekken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,24,'En zij zeggen: \"Er is niets dan dit tegenwoordige leven, wij leven en sterven; alleen de tijd vernietigt ons.\" Maar zij hebben daaromtrent geen kennis, zij vermoeden slechts.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,25,'En wanneer Onze duidelijke woorden aan hen worden voorgedragen, is hun enige tegenwerping: \"Brengt onze vaderen terug, als gij de waarheid spreekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,26,'Zeg: \"Het is Allah, Die u leven geeft en u daarna doet sterven, daarna zal Hij u tezamen verzamelen op de Dag der Opstanding waarover geen twijfel is. Maar de meeste mensen begrijpen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,27,'Aan Allah behoort de heerschappij der hemelen en der aarde; de Dag waarop het Uur aanbreekt, zullen zij die leugens volgen, vergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,28,'En gij zult ieder volk zien knielen. Elk volk zal tot zijn boek worden geroepen en er zal tot hen worden gezegd: \"Heden zult gij voor hetgeen gij deedt worden beloond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,29,'\"Dit is Ons Boek: het spreekt tot u; met waarheid. Wij hebben opgetekend, wat gij deedt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,30,'Maar wat hen betreft, die geloofden en goede daden verrichtten, hun Heer zal hen in Zijn barmhartigheid toelaten. Dat is de openlijke zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,31,'Maar tot de ongelovigen (zal gezegd worden): \"Werden Mijn woorden niet aan u voorgedragen? Doch gij waart hoogmoedig en werdt een schuldig volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,32,'En toen er werd gezegd: \"De belofte van Allah is zeker waar en aan het Uur is geen twijfel,\" zeidet gij: \"Wij weten niet wat het Uur is: wij vermoeden het slechts en zijn er niet zeker van.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,33,'En het boze hunner daden zal hun duidelijk worden en hetgeen zij plachten te bespotten zal hen omringen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,34,'En er zal worden gezegd: \"Deze Dag zullen Wij u vergeten zoals gij de ontmoeting met deze Dag vergeten hebt. Uw toevlucht is (slechts) het Vuur en gij hebt daar geen helpers.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,35,'\"Dit is omdat gij de tekenen van Allah bespottet, daardoor heeft het leven der wereld u misleid.\" Daarom zullen zij op die Dag niet uit het Vuur worden genomen, noch zal hun verontschuldiging worden toegestaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,36,'Alle lof komt Allah toe, de Heer der hemelen en der aarde; de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,45,37,'Hem behoort de Grootheid in de hemelen en op aarde: en Hij is de Machtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,1,'Haa Miem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,2,'De openbaring van dit Boek is van Allah, de Machtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,3,'Wij hebben de hemelen en de aarde en alles wat er tussen is niet anders dan in waarheid geschapen en voor een vastgestelde tijd; maar de ongelovigen wenden zich af van hetgeen, waardoor zij zijn gewaarschuwd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,4,'Zeg: \"Weet gij wat gij naast Allah aanroept? Toont mij, welk deel zij van de aarde hebben geschapen. Of hebben zij aandeel aan de hemelen? Brengt mij een boek, dat v��r dit is geopenbaard of een spoor van kennis, indien gij de waarheid spreekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,5,'Wie is verder afgedwaald dan hij die buiten Allah (afgoden) aanroept, die tot de Dag der Opstanding hem nooit zullen kunnen antwoorden en die niet wet,en dat men hen aanroept?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,6,'En wanneer de mensen worden verzameld zullen dezen (de afgoden) hun vijanden worden en hun aanbidding ontkennen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,7,'En wanneer Onze duidelijke woorden aan hen worden medegedeeld, zeggen degenen, die de Waarheid, toen zij tot hen kwam, verwierpen: \"Dit is klaarblijkelijk tovenarij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,8,'Zeggen zij: \"Hij heeft dit verzonnen,\" Zeg: \"Als ik het heb verzonnen, kunt gij mij tegen Allah niets baten. Hij weet het beste, wat gij daaromtrent zegt. Hij is voldoende als Getuige tussen u en mij. Hij is de Vergevensgezinde, de Genadevolle.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,9,'Zeg: \"Ik ben geen nieuwe boodschapper, noch weet ik wat met u of mij zal geschieden. Ik volg alleen hetgeen mij is geopenbaard; en ik ben slechts een duidelijke waarschuwer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,10,'\"Ziet, indien hij van Allah is en gij hem verwerpt, hoewel een getuige vanuit de kinderen Isra�ls (Mozes) heeft getuigd van een aan hem gelijke en hij geloofde (in hem) maar gij zijt hoovaardig? Voorwaar Allah leidt het onrechtvaardige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,11,'En de ongelovigen zeggen over de gelovigen: \"Indien dit goed was, zouden zij ons daarin niet voorgegaan zijn. \" En omdat zij de rechte weg niet hebben gevolgd, zeggen zij: \"Dit is een oude leugen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,12,'En voordien was het Boek van Mozes een leiding en een barmhartigheid: en dit Boek (de Koran) is bevestigend in duidelijke taal, om de onrechtvaardigen te waarschuwen en als verblijdend nieuws voor de goeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,13,'Voorwaar, zij, die zeggen: \"Onze Heer is Allah\", en dan standvastig blijven - over hen zal geen vrees komen, noch zullen zij treuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,14,'Dezen zijn de bewoners van het paradijs: zij zullen daarin vertoeven als beloning voor hetgeen zij plachten te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,15,'En Wij hebben de mens vriendelijkheid jegens zijn ouders geboden. Zijn moeder draagt hem met ongemak en baart hem met smart. En zijn dragen en spenen nemen dertig maanden in beslag totdat, wanneer hij zijn volle kracht bereikt heeft en veertig jaren wordt, hij zegt: \"Mijn Heer, stel mij in staat, dat ik dankbaar moge zijn voor de gunsten die Gij mij en mijn ouders hebt bewezen en dat ik het goede moge doen, dat U behaagt. En laat mijn nakomelingen rechtvaardig zijn. Ik wend mij tot U: en waarlijk, ik behoor tot de Moslims.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,16,'Van dezulken aanvaarden Wij de goede werken die zij verrichten en Wij zien hun slechte daden over het hoofd. Zij behoren tot de bewoners van het paradijs, volgens de ware belofte die hun was toegezegd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,17,'- Maar degene, die tot zijn ouders zegt: \"Foei gij beiden! Dreigt gij mij dat ik opgewekt zal worden, terwijl geslachten reeds v��r mij zijn vergaan\" En beiden roepen tot Allah om hulp: \"Wee u! Geloof: want de belofte van Allah is waar.\" Maar hij (de zoon) zegt: \"Dit zijn slechts fabelen der ouden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,18,'Dezulken zijn het tegen wie het woord van kracht is geworden, tezamen met de volkeren van de djinn en de mensen die v��r hen zijn heengegaan, dat zij zeker de verliezers zullen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,19,'En voor allen zijn er graden overeenkomstig hetgeen zij doen, opdat Allah hun daden volledig moge belonen en hun zal geen onrecht worden aangedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,20,'De Dag, waarop de ongelovigen aan het Vuur zullen worden blootgesteld, zal er tot hen worden gezegd: \"Gij buittet uw goede dingen in het leven der wereld uit en gij hebt het genoten. Deze Dag zult gij met de straf der vernedering worden vergolden omdat gij ten onrechte op aarde hoogmoedig en opstandig waart.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,21,'En gedenk de broeder van Aad, toen hij zijn volk in de zandheuvels waarschuwde - en er zijn waarschuwers v��r en na hem geweest - \"Dient Allah alleen, want ik vrees de straf van een grote Dag voor u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,22,'Toen zeiden zij: \"Zijt gij tot ons gekomen om ons van onze goden afvallig te maken? Breng hetgeen waarmee gij ons bedreigt dan over ons, als gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,23,'Hij zeide: \"De kennis daarvan is slechts bij Allah, ik breng waarmede ik ben gezonden aan u over, maar ik zie dat gij een onwetend volk zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,24,'Toen zij een wolk naar hun valleien zagen komen, zeiden zij: \"Dit is een wolk, die ons regen zal geven.\" Neen, dat is hetgeen gij zocht te verhaasten, een wind, die een smartelijke straf bevat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,25,'Deze zal alles door het gebod van zijn Heer vernietigen. En het kwam met hen z� ver dat slechts hun lege woningen waren te zien. Zo straffen Wij het schuldige volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,26,'En Wij hadden hen stevig gevestigd in hetgeen waarin Wij u niet hebben gevestigd en Wij hadden hun oren, ogen en een hart gegeven. Maar hun oren, noch hun ogen noch hun hart baatten hen iets, daar zij de tekenen van Allah verwierpen en hetgeen waarover zij plachten te spotten, (de straf) omringde hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,27,'En waarlijk, Wij hebben om hen heen de steden vernietigd, en Wij hebben de tekenen uitgelegd opdat zij zich mochten bekeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,28,'Waarom hielpen degenen, die zij buiten Allah tot goden hadden aaagenomen, om in Zijn nabijheid, te komen, hen dan niet? Neen, zij faalden hier geheel in. -Dat was hun leugen - en wat zij plachten te verzinnen (faalde eveneens).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,29,'En toen Wij een aantal van de djinn naar u deden komen, die de Koran wensten te horen en, toen zij bij u kwamen, zeiden zij: \"Weest stil\" en toen het (de prediking) be�indigd was, gingen zij naar hun volk terug en waarschuwden dit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,30,'Zij zeiden: \"O, ons volk, wij hebben een Boek horen voorlezen, dat na Mozes nedergezonden is, en dat het voorafgaande vervult, het leidt tot de Waarheid en tot de rechte weg.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,31,'\"O, ons volk, geef gehoor aan Allah´s verkondiger en geloof in hem. Hij (God) zal u uw zonden vergeven en u voor een pjinlijke straf behoeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,32,'En wie aan Allah´s verkondiger geen gehoor geeft kan op aarde niet ontvluchten, noch kan hij een enkele beschermer naast Hem hebben. Zulken verkeren in openlijke dwaling.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,33,'Hebben zij niet ingezien dat Allah, Die de hemelen en de aarde schiep en niet moe werd door hen te scheppen, macht heeft de doden te doen herleven? Ja, inderdaad, Hij heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,34,'En de Dag, waarop de ongelovigen aan het Vuur zullen worden blootgesteld zal er worden gezegd: \"Is dit niet de waarheid?\" Zij zullen antwoorden: \"Ja zeker, bij onze Heer.\" Hij zal zeggen: \"Ondergaat dan de straf omdat gij (ons woord) verwierpt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,46,35,'Wees daarom geduldig (o profeet) zoals de boodschappers, die mannen van karaktervastheid waren, en wees omtrent hen niet haastig. De Dag, waarop zij zullen zien waarmede zij worden bedreigd, zal het hun toeschijnen alsof zij slechts een uur van een dag hadden geleefd (in deze wereld). De verkondiging is aan u en niemand wordt vernietigd dan het overtredende volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,1,'Zij, die (de Waarheid) verwerpen en mensen van Allah´s weg afleiden, hun werk zal Hij vruchteloos maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,2,'Maar zij, die geloven en goede werken doen en in hetgeen aan Mohammed is ge openbaard, geloven - dit is de Waarheid van hun Heer - hun fouten zal Hij van hen wegnemen en hun toestand verbeteren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,3,'Dat is omdat de ongelovigen de leugen volgen, terwijl de gelovigen de Waarheid van hun Heer volgen. Zo deelt Allah aan de mensen hun toestand mede.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,4,'Wanneer gij de ongelovigen (in oorlog) ontmoet, treft dan hun nek en wanneer gij overwinnaar zijt, bindt hen dan vast. En wanneer de oorlog opgehouden is, laat hen dan vrij uit gunst of voor een losprijs. Zo zij het. En indien Allah wilde, had Hij hen Zelf kunnen bestraffen. Doch Hij wilde sommigen uwer door anderen op de proef stellen. En degenen die terwille van Allah worden gedood, hun werken zal Hij zeker niet vruchteloos maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,5,'Hij zal hen leiden en hun toestand verbeteren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,6,'En hen in het paradijs, dat Hij hun heeft bekend gemaakt, toelaten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,7,'O gij, die gelooft, indien gij de zaak van Allah steunt zal Hij u helpen en standvastig doen blijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,8,'Maar de ongelovigen wacht vernietiging en Hij zal hun werken vruchteloos maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,9,'Dat is omdat zij, hetgeen Allah heeft geopenbaard, haten, daarom maakte Hij hun werken vruchteloos.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,10,'Hebben zij op aarde niet gereisd en gezien wat het einde was van degenen, die v��r hen waren? Allah vernietigde hen geheel en hetzelfde zal voor de ongelovigen gelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,11,'Dat is, omdat Allah de Beschermer is van de gelovigen en voor de ongelovigen is er geen Beschermer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,12,'Voorwaar, Allah zal hen die geloven en goede werken doen in het paradijs toelaten, waardoorheen rivieren vloeien: terwijl de ongelovigen zich vermaken en eten zoals het vee; het Vuur zal hun tehuis zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,13,'En hoevele steden die sterker waren dan de stad die u heeft uitgedreven, hebben Wij vernietigd, en zij hadden geen helper!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,14,'Zijn zij die op een duidelijk bewijs van hun Heer steunen als zij voor wie hun slechte daden schoonschijnend zijn gemaakt en die hun eigen begeerten volgen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,15,'Het beeld van het paradijs dat aan de godvruchtigen is beloofd: er zijn daarin stromen water dat niet bederft; en stromen melk waarvan de smaak niet verandert en stromen wijn, smakelijk voor degenen die drinken en rivieren van zuivere honing. En zij zullen er allerlei vruchten in hebben en vergiffenis van hun Heer. Kunnen zij gelijk zijn aan degenen die in het Vuur vertoeven en die kokend water te drinken krijgen zodat het hun ingewanden verscheurt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,16,'En sommigen onder hen luisteren naar u doch wanneer zij van u weggaan, zeggen zij tot hen aan wie kennis is gegeven: \"Wat zeide hij zo juist?\" Allah heeft hun hart verzegeld, zij volgen hun eigen neigingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,17,'Maar van hen die de leiding volgen vermeerdert Hij de leiding en schenkt hun rechtvaardigheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,18,'Zij (de ongelovigen) wachten op niets dan het Uur dat onverwachts over hen kan komen. De tekenen er van zijn reeds gekomen, maar hoe zal voor hen de herinnering zijn wanneer het (Uur) werkelijk tot hen komt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,19,'Weet, dat er buiten Allah geen God bestaat en vraag bescherming voor uw tekortkoming en voor die van gelovige mannen en vrouwen. Allah kent de plaats uwer handelingen en uw rustplaats.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,20,'En de gelovigen zeggen: \"Waarom is er geen Soerah geopenbaard?\" Maar wanneer een beslissende Soerah wordt geopenbaard en daarin over vechten wordt gesproken, zult gij hen in wier hart een ziekte is naar u zien kijken als iemand die bezwijmt in de dood. Maar voor hen ware het beter,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,21,'Gehoorzaamheid (te betonen) en goede woorden (te spreken). En wanneer de zaak is beslecht, is het voor hen beter indien zij Allah trouw blijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,22,'Zult gij dan niet door u af te wenden verderf in het land brengen en uw familiebanden verbreken?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,23,'Dezen zijn het, die Allah heeft vervloekt, zodat Hij hen doof heeft gemaakt en hun ogen verblind.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,24,'Willen zij dan niet over de Koran nadenken, of zijn er sloten op hun hart?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,25,'Waarlijk, voor hen die hun rug omkeren nadat de leiding hun duidelijk is geworden, heeft Satan het gemakkelijk gemaakt en hun verlangens opgewekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,26,'Dat is doordat zij tot degenen die haten wat Allah heeft geopenbaard, zeggen: \"Wij willen u in sommige zaken gehoorzamen.\" Maar Allah kent hun geheimen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,27,'En hoe (zal het zjin) wanneer de engelen bij de dood hun ziel zullen nemen, hun aangezicht en hun rug treffend?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,28,'Omdat zij datgene volgen wat Allah vertoornt en haten wat Hem behaagt, daarom heeft Hij hun werken vruchteloos gemaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,29,'Denken zij wier hart ziek is, dat Allah hun boosaardigheden niet aan het licht zou brengen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,30,'En indien Wij wilden, konden Wij hen (de huichelaars) aan u tonen, zodat gij hen aan hun merkteken zoudt kennen. Maar gij zult hen gewis aan hun woorden herkennen. En Allah heeft kennis van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,31,'En Wij zullen u zeker beproeven totdat Wij diegenen onder u onderscheiden die ijverig streven en standvastig zijn. En Wij zullen uw feiten aan u openbaar maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,32,'Voorzeker, zij die niet geloven en (anderen) van Allah´s pad afleiden en die de boodschapper tegenwerken, nadat de leiding hun duidelijk is geworden, zullen Allah stellig niet schaden doch Hij zal hun werken vruchteloos maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,33,'O. gij die gelooft, gehoorzaamt Allah en de boodschapper en maakt uw werken niet nutteloos.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,34,'Waarlijk, de ongelovigen, die van het pad van Allah afleiden en sterven, terwiil zij ongelovig zijn, Allah zal hen zeker niet vergeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,35,'Weifelt daarom niet noch roept om vrede, want gij zult de overhand hebben. Allah is met u en Hij zal uw daden niet teniet doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,36,'Het leven dezer wereld is slechts een spel en een (ijdel) vermaak, en indien gij gelooft en (God) vreest, zal Hij u belonen en zal u niet om uw (gehele) bezit vragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,37,'Want indien Hij dit van u zou vragen en er op zou aandringen, zoudt gij vrekkig worden en Hij zou uw boosaardigheden aan het licht brengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,47,38,'Ziet, gij zijt het, die geroepen wordt ter wille van Allah (een deel van uw vermogen) te geven, maar er zijn sommigen onder u die vrekkig zijn. En wie vrekkig is, is dit slechts tegen zichzelf. Allah is Zichzelf - genoeg en gij zijt nooddruftig. En indien gij u (van de Waarheid) afwendt, zal Hij een ander volk in uw plaats brengen en dezen zullen uw gelijken niet zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,1,'Voorwaar, Wij hebben u een klaarblijkelijke overwinning verleend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,2,'Zodat Allah u tegen uw voorafgaande en toekomstige (aan u toegeschrevene) zonden moge behoeden en dat Hij Zijn gunst aan u moge vervolmaken en u op het juiste pad moge leiden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,3,'En dat Allah u met een machtige hulp moge ondersteunen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,4,'Hij is het, Die rust in het hart der gelovigen heeft nedergezonden, opdat zij geloof aan hun geloof mogen toevoegen - en aan Allah behoren de scharen der hemelen en der aarde en Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,5,'Zodat Hij de gelovige mannen en vrouwen in tuinen moge toelaten waar doorheen rivieren vloeien om daarin te vertoeven, en hun feilen van hen moge wegnemen; dat is in de ogen van Allah de grootste zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,6,'En dat Hij de huichelaars en huichelaarsters en de afgodendienaren en -dienaressen moge straffen, die boze gedachten jegens Allah koesteren. Over dezulken zal tegenspoed en de toorn van Allah komen. Hij heeft hen vervloekt en de hel voor hen gereed gemaakt. En dit is een slechte be stemming.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,7,'Aan Allah behoren de scharen der hemelen en der aarde; Allah is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,8,'Wij hebben u als getuige en drager van blijde. tijdingen en als waarschuwer gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,9,'Opdat gji in Allah en Zijn boodschapper zoudt geloven, hem steunen en eren en Hem ´s morgens en ´s avonds zoudt verheerlijken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,10,'Voorwaar, zij die u trouw zweren, zweren trouw aan Allah; Allah´s hand rust op hun handen. Doch wie zijn eed schendt, doet dit tot zijn eigen nadeel en wie zijn belofte aan Allah vervult, Hij zal hem een grote beloning geven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,11,'Degenen onder de bewoners der woestijn die achterbleven, zullen tot u zeggen: \"Onze bezittingen en onze gezinnen hielden ons bezig (zodat wij u niet konden helpen); vraag daarom voor ons vergiffenis.\" Zij zeggen met hun tong hetgeen niet in hun hart is. Zeg: \"Wie kan u iets baten tegen Allah indien Hij voor u voordeel of nadeel beoogt?\" Neen, Allah is goed onderricht van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,12,'Neen, gij dacht dat de boodschapper en de gelovigen nooit tot hun gezinnen zouden terugkeren en dat was voor uw hart schoonschjinend gemaakt doch gij hadt een slechte gedachte gekoesterd, daarom werdt gij een volk dat ten gronde gaat.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,13,'En voor degenen, die niet in Allah en Zijn boodschapper geloven hebben Wij voorzeker een laaiend Vuur bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,14,'Van Allah is het koninkrijk der hemelen en der aarde. Hij vergeeft en straft wie Hij wil. En Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,15,'Zij, die werden achtergelaten zullen zeggen, wanneer gij zoudt uitgaan op een expeditie waarbij gij gemakkelijk buit kunt maken: \"Sta ons toe u te volgen.\" Zij wensen de uitspraak van Allah te veranderen. Zeg: \"Gij zult ons zeker niet volgen. Allah heeft het voorheen zo bepaald.\" Dan zullen zij zeggen: \"Neen, doch gij benijdt ons.\" Neen, zij begrijpen slechts weinig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,16,'Zeg tot de Arabieren der woestijn die werden achtergelaten: \"Gij zult tegen een volk van geduchte krijgslieden worden opgeroepen om te vechten totdat zij zich overgeven. Dan, als gij gehoorzaamt, zal Allah u een goede beloning geven, maar indien gij u omkeert zoals gij voorheen deedt, zal Hij u door een pijnlijke straf kastijden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,17,'Er rust geen schuld op de blinde, noch op de lamme, noch op de zieke. En wie Allah en Zijn boodschapper gehoorzaamt, hem zal Hij in tuinen toelaten waar doorheen rivieren stromen: maar wie zich omkeert, hem zal Hij door een smartelijke straf straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,18,'Voorzeker, Allah had aan de gelovigen welgevallen, toen zij u onder de boom trouw zwoeren en Hij wist wat in hun hart was en Hij zond op hen kalmte neder en Hij beloonde hen met een spoedige overwinning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,19,'En een grote buit die zij konden bemachtigen. En Allah is Almachtig, Alwijs;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,20,'Allah heeft u een grote buit beloofd die gij moogt nemen en Hij heeft u deze voorlopig gegeven en heeft de handen der vijanden van u weerhouden opdat het een teken moge zijn voor de gelovigen en dat Hij u op het rechte pad moge leiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,21,'En een andere overwinning, die gij nog niet hebt kunnen behalen, maar Allah heeft deze in zijn hand; Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,22,'Indien de ongelovigen u bestrijden zullen zij u zeker de rug toekeren; daarbij zullen zij beschermer noch helper vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,23,'Zo is de handelwijze van Allah zoals die ook vroeger is geweest want gij zult in Allah´s handelwijze geen verandering vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,24,'En Hij is het, Die in het dal van Makka hun handen van u en uw handen van hen afhield, nadat Hij u de overwinning over hen had gegeven. En Allah ziet hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,25,'Zij zijn het die niet geloofden en u van de Heilige Moskee afhielden en de offeranden verhinderden hun bestemming te bereiken. En ware het niet om de gelovige mannen en vrouwen die gij niet kent en die gij hadt kunnen vertrappen zodat wegens hen een blaam aan u kon hebben gekleefd zonder dat gij het wist, (zou Hij u hebben toegestaan te vechten, maar Hij deed dat niet) opdat Hij in Zijn barmhartigheid zou opnemen wie Hij wil. Als zij gescheiden waren zouden Wij de ongelovigen onder hen voorzeker met een smartelijke straf hebben gestraft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,26,'Toen de ongelovigen verwaandheid in hun hart verborgen- de verwaandheid der onwetendheid - zond Allah Zijn kalmte over Zijn boodschapper en over de gelovigen neder en deed hen het woord der rechtvaardigheid nakomen. En zij hadden er recht op en waren het waardig. Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,27,'Voorwaar, Allah vervulde het visioen van Zijn boodschapper naar waarheid. Voorzeker gij zult de Heilige Moskee (te Makka) in vrede binnengaan met haar geknipt of geschoren zonder vrees. Dus Hij wist wat u onbekend was en Hij heeft u hiervoor een nabijzijnde overwinning toegezegd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,28,'Hij is het, Die Zijn boodschapper met leiding en de godsdienst der Waarheid heeft gezonden, opdat Hij hem moge doen zegevieren over alle (andere) godsdiensten. En Allah is als Getuige voldoende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,48,29,'Mohammed is de boodschapper van Allah. En zij, die met hem zijn, zijn hard tegen de ongelovigen en zachtmoedig onder elkander. Gij ziet hen zich buigen en nederwerpen (in gebed), Allah´s genade en Zijn welbehagen zoekende - Op hun aangezicht zijn de sporen van het zich ter aarde werpen. Dit is hun beschrijving in de Torah. En hun beschrijving in het Evangelie is als het zaad van koren, dat zijn scheut uitspruit, en dien versterkt, waardoor zij dik wordt en op eigen stengel komt te staan, tot vreugde der zaaiers en woede der ongelovigen. Allah heeft aan de gelovigen die goede werken doen, vergiffenis en een grote beloning beloofd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,1,'O, gij die gelooft, weest niet voorbarig bij Allah en Zijn boodschapper, maar vreest Allah. Voorwaar Hij is Alhorend, Alwetend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,2,'O gij gelovigen, verheft uw stem niet boven de stem van de profeet en spreekt niet hardop tot hem, zoals gij hardop tot elkander spreekt, opdat uw werken niet vruchteloos mogen worden zonder dat gij het bemerkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,3,'Waarlijk, zij die hun stem verzachten in het bijzijn van de boodschapper van Allah, zijn degenen wier hart Allah tot rechtvaardigheid heeft gezuiverd. Voor hen is er vergiffenis en een grote beloning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,4,'Zij, die van buiten de huizen uit naar u schreeuwen - de meesten hunner hebben geen verstand.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,5,'Als zij geduld tonen totdat gij tot hen komt, zou het beter voor hen zijn. Maar Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,6,'O gij gelovigen, indien een slecht persoon u nieuws brengt, onderzoekt het nauwkeurig opdat gij sommige mensen niet in onwetendheid schaadt en naderhand spijt krijgt van hetgeen gij hebt gedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,7,'En weet, dat de boodsehapper van Allah onder u is; als hij in verschillende opzichten aan uw wensen gehoor zou geven zoudt gij zeker in moeilijkheden zijn geraakt, maar Allah heeft in uw hart het geloof dierbaar en schoon gemaakt, en ongeloof, buitensporigheid en ongehoorzaamheid afkeurenswaardig. Zo zijn zij, die het rechte pad volgen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,8,'Door de genade en gunst van Allah. Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,9,'Indien twee partijen van gelovigen beginnen te vechten treft dan een schikking onder hen, maar indien ��n hunner tegen de andere in overtreding is, bestrijdt dan de overtredende partij totdat zij tot de verordening van Allah terugkeert. En indien zij terugkomt, sluit dan een rechtvaardige vrede en behandelt hen billijk. Voorwaar, Allah heeft de rechtvaardigen lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,10,'De gelovigen zijn voorzeker broeders. Bewaart daarom vrede onder uw broeders en weest godvruchtig opdat u barmhartigheid moge worden betoond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,11,'O, gij die gelooft! Laat een volk het andere volk dat waarschijnlijk beter is dan zij, niet bespotten, noch vrouwen andere vrouwen, die misschien beter zijn dan zij. En belastert elkander niet, noch noemt elkaar bij scheldnamen. Kwaad is (het geven van) een slechte naam na de aanvaarding van het geloof, en zij die geen berouw tonen zijn de onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,12,'O, gij die gelooft! Vermijdt in het algemeen verdenking want achterdoeht is een zonde. En spionneert niet, noch belastert elkander. Lust iemand onder u het vlees van zijn dode broeder? Gij verafschuwt het zekerlijk. Vreest Allah voorzeker, Allah is Berouwaanvaardend, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,13,'O, mensdom! Wij hebben u uit man en vrouw geschapen en Wij hebben u tot volkeren en stammen gemaakt, opdat gij elkander moogt kennen. Voorzeker, de godvruchtigste onder u is de eerwaardigste bij Allah. Voorwaar, Allah is Alwetend, Alkennend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,14,'De bewoners der woestijn zeggen: \"Wij geloven.\" Zeg: \"Gij gelooft nog niet, maar zegt liever: Wij hebben ons onderworpen want het geloof is uw hart nog niet binnengedrongen. Maar indien gij Allah en Zijn boodschapper gehoorzaamt, zal Hij u van uw goede daden niets afnemen. \" Voorzeker, Allah is de Vergevensgezinde, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,15,'De ware gelovigen zijn slechts degenen, die in Allah en Zijn boodschapper geloven en daarna niet twijfelen, doch met hun bezittingen en persoon voor de zaak van Allah strijden. Zij zijn de waarachtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,16,'Zeg: \"Wilt gij Allah uw geloof doen weten, terwijl Hij weet wat in de hemelen en op aarde is; waarlijk Allah heeft kennis van alle dingen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,17,'Zij achten het een gunst voor u dat zij de Islam hebben omhelsd. Zeg: \"Acht het aannemen van de Islam geen gunst voor mij. Neen, Allah heeft u een gunst bewezen doordat Hij u tot het geloof heeft geleid als gij waarachtig zijt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,49,18,'Voorwaar, Allah kent de geheimen der hemelen en der aarde. Waarlijk Allah ziet al hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,1,'Qaaf. Bij de glorierijke Koran.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,2,'Maar zij verwonderen zich dat er uit e hun midden een waarschuwer tot hen kwam. En de ongelovigen zeggen: \"Dit is een zonderling iets!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,3,'Zullen wij in het leven worden geroepen wanneer wij dood gaan en stof zijn geworden? Zulk een terugkeer is onmogelijk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,4,'Wij weten wat de aarde van hen verteert en bij Ons is een Boek dat alles bewaart.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,5,'Neen, zij hebben de Waarheid verloochend toen deze tot hen kwam, derhalve zijn zij in een verwarde toestand geraakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,6,'Zien zij niet naar de hemel boven hen hoe Wij deze hebben opgericht en versierd en dat dezelve geen gebreken heeft?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,7,'En de aarde - Wij hebben haar uitgespreid en stevige bergen er op gevestigd en Wij hebben er elk prachtig gewas op doen groeien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,8,'Als inzicht en les voor iedere dienaar die zich er toe wendt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,9,'En Wij zenden water vol zegeningen uit de hemel neder en Wij brengen daarmee tuinen en graan voort waarvan kan worden geoogst');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,10,'En hoge palmbomen met bloeikolve over elkander gegroeid');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,11,'Als voorziening voor Onze dienaren en Wij verkwikken daarmee een dood land. - Zo zal ook de Opstanding zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,12,'V��r hen verloochende ook het volk van Noach, de mensen van de Bron en het volk van Samoed,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,13,'Het volk van Aad, en Pharao en de broeders van Lot eveneens,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,14,'En de Bosbewoners, en het volk van Tobba, elk hunner verloochende de boodschapper. Daarom ging de bedreiging in vervulling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,15,'Zijn Wij dan uitgeput door de eerste schepping? Neen, zij zijn in twijfel omtrent de nieuwe schepping.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,16,'En voorzeker, Wij hebben de mens geschapen en Wij weten alles wat zijn Ik hem toefluistert. En Wij zijn nader tot hem dan zijn halsader.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,17,'Wanneer de twee (engelen) die te boek stellen, schrijven, zit de een aan de rechter-, de andere aan de linkerzijde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,18,'Hij uit geen woord of er is een bewaker bij hem, die altijd klaar staat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,19,'En de bezwijming des doods komt waarlijk. \"Dit is hetgeen gij wildet ontvrluchten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,20,'En er zal op de bazuin worden geblazen. \"Dit is de Dag der Bedreiging.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,21,'En iedere ziel zal tezamen komen met een geleider en een getuige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,22,'Er zal worden gezegd: \"Gij waart hieromtrent achteloos. Nu hebben Wij uw sluier van u weggenomen en uw oog ziet deze Dag scherp.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,23,'En zijn metgezel zal zeggen: \"Dit is hetgeen bij mij gereed is.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,24,'\"Werpt, werpt in de hel elke ondankbare vijand.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,25,'\"Die het goede belette, de overtreder, de twijfelaar,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,26,'\"Die een andere God naast Allah oprichtte, doet hem de strenge marteling ondergaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,27,'Zijn metgezel zal zeggen: \"O, onze Heer, ik maakte hem niet opstandig maar hij was te ver afgedwaald.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,28,'God zal antwoorden: \"Redetwist niet in Mijn tegenwoordigheid, terwijl Ik u de waarschuwing vooraf heb gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,29,'Het vonnis door Mij geveld kan niet worden veranderd en Ik ben in het geheel niet onrechtvaardig jegens Mijn dienaren.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,30,'Op die Dag zullen Wij tot de hel zeggen: \"Zijt gij gevuld?\" En zij zal antwoorden: \"Is er nog iets?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,31,'En de Hemel zal dicht bij de rechtvaardigen worden gebracht en niet ver verwijderd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,32,'Dit is hetgeen was beloofd voor een ieder die zich bekeerde en die waakzaam was,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,33,'Die de Barmhartige in het verborgene vreesde en met een berouwvol hart tot Hem kwam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,34,'Gaat hier in vrede binnen. Dit is de Dag der Eeuwigheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,35,'Voor hen zal daarin zijn wat zij wensen en bij Ons is nog meer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,36,'Maar hoevele geslachten hebben Wij (niet) v��r hen vernietigd, die machtiger in gezag waren dan dezen! Zij trokken door het land, maar was er een toevluchtsoord voor hen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,37,'Daarin is voorwaar een vermaning voor hem die een hart heeft of die luistert en oplettend is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,38,'En voorwaar, Wij schiepen de hemelen en de aarde en alles wat er tussen is in zes dagen en geen vermoeidheid raakte Ons.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,39,'Heb dus geduld met wat zij zeggen en verheerlijk uw Heer met de lof die Hem toekomt, v��r zonsop- en ondergang.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,40,'En verheerlijk Hem ´s nachts en na de gebeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,41,'En luister! De Dag, waarop de omroeper vanuit een dichtbijzijnde plaats zal roepen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,42,'De Dag, waarop zij de kreet in werkelijkheid zullen horen, dat zal de Tijd zijn van het voor den dag komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,43,'Voorwaar, Wij zijn het die leven geven en de dood veroorzaken, en tot Ons is de terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,44,'De Dag, waarop de aarde onder hen vaneen zal splijten, is het verzamelen gemakkelijk voor Ons.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,50,45,'Wij weten het beste wat zij zeggen en gij zijt er niet om hen te dwingen. Vermaan dus met de Koran hem die Mijn bedreiging vreest.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,1,'(Wij roepen als getuigen) degenen die wijd en zijd verspreiden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,2,'En degenen die de last dragen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,3,'En degenen die rustig voortgaan,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,4,'En degenen die de zaak uitdelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,5,'Voorzeker, hetgeen u is beloofd, is waar,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,6,'En voorwaar, het gericht zal zeker plaats hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,7,'Bij de hemelen vol van paden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,8,'Waarlijk gij hebt uiteenlopende meningen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,9,'Daarvan wordt afgewend wie zich (van het ware geloof) afwendt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,10,'Vervloekt zijn zij die vermoedens uiten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,11,'Die onachtzaam zijn in onwetendheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,12,'Zij vragen: \"Wanneer zal de Tijd des Gerichts zijn?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,13,'Het zal op de Dag zijn, wanneer zij in het Vuur zullen worden beproefd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,14,'\"Ondergaat uw beproeving. Dit is hetgeen gij verhaasttet.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,15,'Maar de rechtvaardigen zullen te midden van tuinen en bronnen verkeren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,16,'Nemend hetgeen hun Heer zal geven omdat zij voorheen goed plachten te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,17,'Gedurende de nacht sliepen zij weinig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,18,'Tijdens de morgenstond zochten zij vergiffenis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,19,'En van hun rijkdommen was een deel voor de bedelaars en ook voor degenen die niet konden bedelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,20,'En er zijn tekenen op aarde voor hen die zekerheid van geloof willen hebben,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,21,'En ook in uzelf, wilt gij dat niet inzien?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,22,'En in de hemel is uw onderhoud en hetgeen u is beloofd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,23,'Bij de Heer van de hemel en de aarde - dit is inderdaad de waarheid zoals gij spreekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,24,'Heeft het verhaal van Abrahams geeerde gasten u bereikt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,25,'Toen zij bij hem binnentraden en zeiden: \"Vrede\", antwoordde hij: \"Vrede\". Hij zeide (bij zichzelven): \"Vreemde mensen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,26,'Maar hij ging rustig naar zijn gezin en bracht een (toebereid) vet kalf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,27,'En plaatste het voor hen. Hij zeide: \"Wilt gij niet eten?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,28,'Daarop begon hij hen te vrezen. Zij zeiden: \"Vrees niet\" en zij gaven hem blijde tijding over een wijze zoon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,29,'Toen kwam zijn vrouw, in verbijstering en sloeg de hand voor het gezicht en zeide: \"Een verwelkte, bejaarde vrouw!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,30,'\"Uw Heer heeft het zo gezegd,\" zeiden zij. \"Voorzeker, Hij is de Alwijze, de Alwetende.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,31,'Abraham zeide: \"Wat is uw taak, o boodsehappers?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,32,'Zij antwoordden: \"Wij zijn naar een schuldig volk gezonden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,33,'Om brokken klei op hen neder te zenden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,34,'Door uw Heer gemerkt (ter verdelging) voor de buitensporigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,35,'De gelovigen die daarin waren lieten Wij (veilig) weggaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,36,'Maar Wij vonden er slechts ��n huis der Moslims.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,37,'En Wij lieten daarin een teken achter voor hen, die de pijnlijke straf vrezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,38,'En in Mozes (is eveneens een teken), toen Wij hem tot Pharao zonden met openlijk gezag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,39,'Maar deze wendde zich af om zijn macht en zeide: \"Een tovenaar of een waanzinnige.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,40,'Daarom grepen Wij hem en zijn scharen en wierpen hen in de zee, waardoor hij zelfverwijt kreeg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,41,'En er was een teken in de Aad, toen Wij een orkaan tegen hen zonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,42,'Deze liet van hetgeen hij teisterde niets over of hij maakte het als as,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,43,'En er was een teken in de Samoed toen er tot hen werd gezegd: \"Vermaakt u voor een wijle.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,44,'Maar zij overtraden het gebod van hun Heer. Daarom achterhaalde hen de bliksem terwijl zij er naar keken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,45,'En zij konden niet opstaan noch konden zij zich hiertegen beschermen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,46,'En in het volk van Noach (is ook een teken), voorwaar zij waren een ongehoorzaam volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,47,'Voorzeker Wij bouwden de hemel door Onze macht en waarlijk Wij zin het, Die hem hebben uitgebreid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,48,'En Wij hebben de aarde uitgespreid en hoe uitmuntend hebben Wij dit gedaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,49,'En Wij hebben alles in paren geschapen opdat gij er lering uit moogt trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,50,'Haast u daarom tot Allah. Waarlijk ik ben voor u een duidelijke waarschuwer van Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,51,'En werpt geen andere God op naast Allah, waarlijk ik ben voor u een duidelijke waarschuwer van Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,52,'En er kwam tot degenen, die v��r hen waren, geen boodschapper of zij zeiden: \"Dit is een tovenaar of een bezetene!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,53,'Hebben zij elkander er toe aangespoord? Neen, zij zijn een opstandig volk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,54,'Wend u daarom van hen af en u zal niets worden verweten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,55,'Maar ga door met het vermanen want de vermaning helpt degenen die willen geloven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,56,'En ik heb de djinn en de mensen slechts tot Mijn aanbidding geschapen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,57,'Ik wens van hen geen onderhoud noch wens Ik dat zij Mij zullen voeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,58,'Voorzeker, Allah is de grootste Voorziener, de Almachtige, de Alsterke.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,59,'Voorzeker het lot der onrechtvaardigen is gelijk aan dat van hun gezellen. Laat hen derhalve niet wensen dit te verhaasten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,51,60,'Wee over de ongelovigen vanwege de Dag waarmede zij worden bedreigd!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,1,'Bij de Berg');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,2,'En bij het geschreven Boek,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,3,'Op uitgebreide perkament.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,4,'En bij het veelbezochte huis');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,5,'En bij het hoogverheven dak');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,6,'En bij de boordevolle oceaan,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,7,'Voorzeker, de straf van uw Heer zal worden voltrokken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,8,'Er is niemand die haar kan afwenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,9,'De Dag waarop de hemel in beweging zal komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,10,'En de bergen zullen vergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,11,'Dan wee op die Dag de loochenaars,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,12,'Die zich in ijdel gesprek vermaken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,13,'De Dag waarop zij in het Vuur der hel zullen worden geslingerd:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,14,'(Men zal zeggen:) \"Dit is het Vuur dat gij placht te loochenen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,15,'Is dit dan toverkunst of ziet gij niet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,16,'Brandt daarin; en het zal voor u hetzelfde zijn, of gij geduld of ongeduld toont. U is slechts vergolden voor hetgeen gij placht te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,17,'Voorwaar, de godvruchtigen zullen in tuinen en gelukzaligheid zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,18,'Genietende van de gaven, die hun Heer hun heeft geschonken en hun Heer heeft hen voor de marteling van het Vuur behoed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,19,'Eet en drinkt met genoegen wegens hetgeen gij placht te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,20,'(U) op tronen nedervlijend die in rijen zijn gerangschikt. En Wij zullen hen met schone meisjes verenigen die grote, mooie ogen hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,21,'En met de gelovigen zullen Wij hun nageslacht, dat hun in het geloof volgt, verenigen. En Wij zullen zeker niets aan hun werken afdoen. Elk mens is onderpand voor zijn daden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,22,'En Wij zullen hun een overvloed van fruit en vlees schenken, volgens hun wensen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,23,'Daar zullen zij elkander een beker van hand tot hand reiken waarin ijdelheid noch zonde zal zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,24,'En er zullen knapen rondgaan alsof zij welbewaakte paarlen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,25,'En zij zullen zich vragend tot elkander wenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,26,'Zij zullen zeggen: \"Voorheen vreesden wij ter wille van onze families.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,27,'Maar Allah is ons genadig geweest en heeft ons voor de marteling van de brandende wind behoed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,28,'Wij plachten voorheen Hem te aanbidden. Voorzeker, Hij is de Goede, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,29,'Waarschuw daarom (o, profeet). Bij de gratie van uw Heer zijt gij noch een waarzegger noch een bezetene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,30,'Zeggen zij: \"Hij is een dichter en wij wachten of te zijner tijd een ramp over hem komt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,31,'Zeg: \"Wacht! Ik wacht ook met u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,32,'Is het hun verstand, dat hun dit oplegt of zijn zij een opstandig volk?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,33,'Of zeggen zij: \"Hij heeft het verzonnen\"? - Neen, zij willen niet geloven -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,34,'Laat hen dan een woord hieraan gelijk naar voren brengen, als zij waarachtig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,35,'Zijn zij door niets geschapen of zijn zij (hun eigen) schepper?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,36,'Schiepen zij de hemelen en de aarde? Neen, zij willen geen zekerheid hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,37,'Bezitten zij de schatten van uw Heer of zijn zij de bewaarders hiervan?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,38,'Hebben zij een ladder naar de hemel waardoor zij kunnen luisteren? Laat hun luisteraar dan openlijk gezag tonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,39,'Heeft Hij (Allah) dochters terwijl gij zonen hebt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,40,'Vraagt gij loon van hen, zodat zij onder schulden gebukt gaan?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,41,'Bezitten zij het onzichtbare, zodat zij het kunnen neerschrijven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,42,'Willen zij een plan smeden (tegen u)? Maar de ongelovigen zullen door hun eigen plan worden gevangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,43,'Hebben zij een andere God buiten Allah? Allah is verheven boven hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,44,'En indien zij een stuk van de hemel zien vallen, zullen zij zeggen \"Opgehoopte wolken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,45,'Laat hen daarom, totdat zij hun Dag ontmoeten waarop zij in onmacht zullen neervallen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,46,'De Dag, waarop hun samenzwering hen niets zal baten noch zullen zij worden geholpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,47,'En voorwaar, voor de onrechtvaardigen is hiervoor een straf. Maar de meesten hunner beseffen het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,48,'Wacht daarom geduldig op het oordeel van uw Heer. want gij zijt onder Onze ogen en verheerlijk uw Heer wanneer gij opstaat met de lof die Hem toekomt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,52,49,'En verheerlijk Hem ´s nachts en na het verbleken der sterren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,1,'Bij de ster wanneer zij valt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,2,'Uw metgezel is noch afgedwaald noch afgeweken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,3,'Noch spreekt hij naar eigen begeerte.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,4,'Het is slechts de Openbaring die wordt nedergezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,5,'Hij, die grote macht heeft, onderwees hem,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,6,'Die kracht bezit. Zo is hij volmaakt geworden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,7,'En hij staat aan de hoogste horizon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,8,'Hij naderde en kwam steeds nader.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,9,'En werd als de spanning van twee bogen, Ja, nog dichter bij,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,10,'En Hij (Allah) openbaarde aan Zijn dienaar hetgeen Hij wilde openbaren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,11,'Het hart loog niet over wat het zag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,12,'Wilt gij dan met hem redetwisten over hetgeen hij heeft gezien?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,13,'En voorzeker, hij zag hem ook bij een andere nederdaling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,14,'Bij de Lotusboom waar niemand voorbij mag gaan,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,15,'Waarnaast de Tuin van Verblijf is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,16,'Toen het goddelijke Licht de Lotusboom overstraalde');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,17,'Wendde zijn oog zich niet af, noch ging het de grens te buiten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,18,'Voorwaar, hij zag de grote tekenen van zijn Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,19,'Ziet, de Laat en de Ozza,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,20,'En een ander, de derde, Manaat?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,21,'\"Zijn voor u de mannelijke wezens en voor Hem de vrouwelijke?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,22,'Dat is dan een onrechtvaardige verdeling;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,23,'Dit zijn slechts namen die gij uitgedacht hebt - gij en uw vaderen - waarvoor Allah geen gezag heeft nedergezonden. Zij volgen slechts hun vermoedens en begeerten. En voorzeker de leiding van hun Heer is nu tot hen gekomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,24,'Krijgt de mens alles waarnaar hij verlangt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,25,'Neen, aan Allah behoren het Hiernamaals en deze wereld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,26,'En hoevele engelen zijn er niet in de hemelen wier voorspraak van geen nut zal zijn, behalve nadat Allah verlof heeft gegeven aan wie Hij wil en wie Hem behaagt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,27,'Zij, die niet in het Hiernamaals geloven geven de engelen vrouwelijke namen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,28,'Maar zij hebben daar geen kennis van. Zij volgen alleen een vermoeden en het vermoeden kan tegen de waarheid niets baten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,29,'Wend u daarom van hem af die zich van de gedachtenis aan Ons afwendt, en die niets wenst dan het leven dezer wereld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,30,'Zo ver reikt hun kennis. Voorwaar, uw Heer kent het beste degene die van Zijn pad afdwaalt en Hij kent het beste degene die Zijn leiding volgt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,31,'En aan Allah behoort hetgeen in de hemelen en hetgeen op aarde is, opdat Hij degenen die slecht deden moge vergelden voor hetgeen zij hebben gewrocht en opdat Hji degenen die goed doen, met het beste moge belonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,32,'Zij, die behalve kleine feilen, de ergste zonden en slechtheden vermijden - voorwaar, uw Heer is de Heer der Alomvattende Vergiffenis. Hij kende u toen H. u uit aarde deed ontstaan en toen gij een embryo waart in de baarmoeder uwer moeder. Prijst daarom uzelf niet om reinheid. Hij kent de godvruchtigen het beste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,33,'Ziet gij hem die zich afwendt (van het rechte pad)');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,34,'En die weinig geeft en vrekkig is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,35,'Bezit hij de kennis van het onzichtbare, zodat hij kan zien?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,36,'Is hem niet verteld over hetgeen in de geschriften van Mozes staat,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,37,'En van Abraham, die de geboden hield?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,38,'Dat geen drager van last de last van een ander zal dragen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,39,'En dat de mens niet meer kan krijgen dan hetgeen waarnaar hij streeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,40,'En dat zijn streven spoedig zal worden opgemerkt;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,41,'Dan zal hij er volledig voor worden beloond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,42,'En dat alles uiteindelijk tot uw Heer komt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,43,'En dat Hij het is, Die doet lachen en wenen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,44,'En dat Hij het is, Die de dood veroorzaakt en het leven geeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,45,'En dat Hij de twee echtgenoten schept, de vrouwelijke en de mannelijke');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,46,'Uit een levenskiem wanneer deze uitgegoten wordt:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,47,'En dat de volgende opwekking (tot leven) op Hem rust:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,48,'En dat Hij het is Die voldoening en rijkdom geeft');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,49,'En dat Hij de Heer van Sirius is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,50,'En dat Hij de oude (stam van Aad) vernietigde');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,51,'En Samoed, en Hij spaarde (hen) niet,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,52,'Evenals het volk van Noach v��rdien; waarlijk zij waren uiterst onrechtvaardig en opstandig');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,53,'En Hij bracht de verwoeste steden ten val,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,54,'Zodat hetgeen bedekken kon, hen bedekte.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,55,'Over welke gaven van uw Heer wilt gij dan redetwisten?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,56,'Deze waarschuwer is gelijk aan de vroegere waarschuwers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,57,'Het Uur nadert,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,58,'Niemand behalve Allah kan het ontsluieren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,59,'Verwondert gij u dan over deze aankondiging?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,60,'En lacht gij in plaats van te wenen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,61,'Terwijl gij achteloos zijt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,53,62,'Werpt u voor Allah neder en aanbidt (Hem).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,1,'Het Uur is nabij, en de Maan is opengespleten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,2,'Maar als zij (de ongelovigen) een teken zien wenden zij zich er van af en zeggen: \"Een voortdurende toverkunst.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,3,'Zij verloochenen en volgen hun eigen begeerten. Maar elke verordening (Gods) zal plaats hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,4,'En er zijn reeds tijdingen tot hen gekomen waarin een waarschuwing ligt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,5,'Volmaakte wijsheid; maar de waarschuwingen helpen hen niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,6,'Wend u daarom van hen af. De Dag waarop de aankondiger hen zal roepen tot iets onaangenaams,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,7,'Dan zullen zij met nedergeslagen ogen uit hun graven komen als verstrooide sprinkhanen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,8,'Zich naar de omroeper haastend. De ongelovigen zullen zeggen \"Dit is een moeilijke dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,9,'V��r hen verloochende het volk van Noach, zij verloochenden Onze dienaar en zeiden: \"Een waanzinnige.\" En hij werd verdreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,10,'Daarom bad hij tot zijn Heer: \"Ik ben gewis verslagen, sta mij bij.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,11,'Toen openden Wij de poorten van de hemel voor het stromende water.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,12,'En Wij spleten de aarde door bronnen, waar door de wateren elkander ontmoetten volgens een vastgesteld plan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,13,'En Wij droegen hem op iets, bestaande uit planken en spijkers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,14,'Het dreef onder Onze ogen voort als een beloning voor hem, die verworpen was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,15,'En Wij maakten dit tot een teken. Is er iemand die er lering uit trekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,16,'Hoe vreselijk was Mijn straf en Mijn waarschuwing!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,17,'En Wij hebben inderdaad de Koran gemakkelijk gemaakt ter vermaning. Is er iemand die er lering uit trekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,18,'Aad verloochende eveneens. Hoe (ernstig) was Mijn straf en Mijn waarschuwing!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,19,'Wij zonden een woedende wind tegen hen, op een kwade, onvergetelijke dag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,20,'Die mensen wegtrok als waren zij de stammen van ontwortelde palmbomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,21,'Hoe groot was toen Mijn straf en Mijn waarschuwing!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,22,'En Wij hebben inderdaad de Koran gemakkelijk gemaakt ter vermaning. Is er iemand die er lering uit trekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,23,'Ook (het volk van) Samoed verloochende de waarschuwers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,24,'En zij zeiden: \"Moeten wij een man uit ons midden volgen? Dan zouden wij inderdaad verdwaald en krankzinnig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,25,'Is de vermaning hem alleen gegeven? Neen, hij is een grote leugenaar en misdadiger.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,26,'Morgen zullen zij weten wie de grote leugenaar en misdadiger is!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,27,'Wij zullen de kameel zenden om hen op de proef te stellen. Let daarom op hen en heb geduld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,28,'En zeg hun, dat het water tussen hen is verdeeld en dat de tijd van elke drinkbeurt in acht moet worden genomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,29,'Maar zij riepen hun metgezel, deze nam het (kameel) en verlamde het.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,30,'Hoe vreselijk was toen Mijn straf en Mijn waarschuwing!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,31,'Wij zonden een enkele straf tegen hen en zij werden als droog, vertrapt stro.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,32,'En Wij hebben inderdaad de Koran gemakkelijk gemaakt ter vermaning. Is er iemand die er lering uit trekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,33,'Het volk van Lot verloochende de waarschuwers ook.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,34,'En Wij zonden een storm van stenen over hen allen met uitzondering van de familie van Lot, die Wij bij de dageraad verlosten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,35,'Als een gunst van Ons. Zo belonen Wij hen die dank betuigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,36,'En Lot had hen inderdaad voor Onze straf gewaarschuwd doch zij trokken de waarschuwingen in twijfel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,37,'En zij trachtten hem van zijn gasten af te keren. Daarom verblindden Wij hun ogen en zeiden: \"Ondergaat nu Mijn straf en Mijn waarschuwing.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,38,'En de volgende morgen vroeg kwam er een blijvende straf over hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,39,'\"Ondergaat nu Mijn straf en Mijn waarschuwing.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,40,'En Wij hebben inderdaad de Koran gemakkelijk gemaakt ter vermaning. Is er iemand die er lering uit trekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,41,'Er kwamen ook waarschuwers tot het volk van Pharao.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,42,'Zij verwierpen al Onze tekenen, daarom grepen Wij hen gelijk het grijpen van een krachtige en machtige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,43,'Zijn uw ongelovigen beter dan dezen? Of zijt gij vrijgesteld in de geschriften?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,44,'Zeggen zij: \"Wij zijn een overwinnende schare?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,45,'De scharen zullen allen op de vlucht worden gejaagd en zij zullen hun rug tonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,46,'Neen, het Uur is hun vastgestelde tijd en het Uur zal uiterst rampzalig en bitter zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,47,'Voorzeker, de overtreders zullen in dwaling verkeren en zich in een vlammend Vuur bevinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,48,'De Dag, waarop zij met hun aangezicht in het Vuur zullen worden gesleurd, zal er tot hen worden gezegd: \"Voelt de aanraking der hel.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,49,'Voorwaar, Wij hebben alles naar maat geschapen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,50,'En Ons gebod komt in ��n oogwenk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,51,'En Wij hebben inderdaad uw gelijken vernietigd. Is er iemand die er lering uit trekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,52,'En al hetgeen zij deden staat in de geschriften.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,53,'En alles, groot of klein, is nedergeschreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,54,'Voorwaar, de rechtvaardigen zullen te midden van tuinen en rivieren zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,54,55,'Op de juiste plaats in de tegenwoordigheid van de Almachtige Koning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,1,'De Barmhartige');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,2,'Heeft de Koran onderwezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,3,'Hij heeft de mens geschapen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,4,'En heeft hem de uiteenzetting (er van) geleerd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,5,'De zon en de maan doorlopen hun banen volgens het plan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,6,'En planten en bomen aanbidden Hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,7,'Hij heeft de hemel hoog er boven verheven en een evenwicht bepaald');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,8,'Opdat gij het evenwicht niet zoudt verstoren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,9,'Houdt de weegschaal naar recht en doet aan de maat niet tekort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,10,'En Hij heeft de aarde voor Zijn schepselen gemaakt:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,11,'Daarop zijn vruchten en palmbomen met scheden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,12,'En gebolsterd graan en geurige bloemen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,13,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,14,'Hij schiep de mens uit droge klei, als aardewerk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,15,'En Hij schiep de djinn uit de vlam van Vuur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,16,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,17,'De Heer der twee Oosten en de Heer der twee Westen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,18,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,19,'Hij heeft de twee zee�n gescheiden, die elkander eens zullen ontmoeten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,20,'Daartussen is een versperring geplaatst welke zij niet kunnen passeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,21,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,22,'Er komen paarlen en koraal uit beide (zee�n) vandaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,23,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,24,'En van Hem zijn de bergenhoge schepen op zee.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,25,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,26,'Al hetgeen is, zal vergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,27,'En er blijft alleen het Aangezicht van uw Heer, de Bezitter van Heerlijkheid en Eer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,28,'Welke van de gunsten van uw Heer uilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,29,'Van Hem smeken allen, die in de hemelen en op aarde zijn, (gunsten) af. Elk dag toont Hij een andere Heerlijkheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,30,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,31,'Wij zullen spoedig met u afrekenen, o gij twee volkeren!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,32,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,33,'O, groep van djinn en mensen; als gij de grenzen der hemelen en der aarde wilt overschrijden, probeert dit dan. Doch gij zult dit zonder gezag stellig niet kunnen doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,34,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,35,'Er zullen vurige vlammen en gesmolten koper tegen u worden gezonden en gij zult u niet kunnen verweren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,36,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,37,'En wanneer de hemel uiteengespleten en rosssig wordt als een roodgeverfde huid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,38,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,39,'Op die Dag zullen mens noch djinn worden ondervraagd over hun zonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,40,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,41,'De schuldigen zullen aan hun kenmerken worden herkend en zij zullen worden gegrepen bij haren en voeten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,42,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,43,'Dit is de hel door de schuldigen verloochend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,44,'Zij zullen daar tussen vuur en fel kokend water rondgaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,45,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,46,'Maar er zullen voor hem die het verschijnen voor zijn Heer vreest, twee tuinen zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,47,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,48,'Van verschillende soort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,49,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,50,'In beide zullen twee fonteinen stromen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,51,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,52,'Daarin zullen alle vruchten tweesoortig zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,53,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,54,'Zij zullen zich nedervlijen op divans met tapijten waarvan de voeringen van dikke zijde zullen zijn. En het fruit der tuinen zal dicht bij de hand liggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,55,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,56,'Daarin zullen kuise meisjes zijn met zedige blik, door mens noch djinn ooit aangeraakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,57,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,58,'Als waren zij robijnen en koralen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,59,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,60,'De beloning van goedheid kan niet anders dan goedheid zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,61,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,62,'En naast deze twee zijn er nog twee tuinen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,63,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,64,'Donkergroen van gebladerte,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,65,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,66,'Daarin zullen ook twee bronnen zijn die water in overvloed spuiten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,67,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,68,'In beide zullen er vruchten, dadels en granaatappels zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,69,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,70,'Daarin zullen goede en schone meisjes zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,71,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,72,'Schonen in paviljoenen gehuisvest.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,73,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,74,'Die v��r hen mensen noch djinn hebben aangeraakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,75,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,76,'Rustend op groene kussens en prachtige tapijten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,77,'Welke van de gunsten van uw Heer wilt gij dan ontkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,55,78,'Gezegend zij de naam van uw Heer, de Bezitter van Heerlijkheid en Eer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,1,'Als de Gebeurtenis plaats vindt');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,2,'Zal er niets dit plaatsvinden kunnen tegenhouden -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,3,'Enigen zal het vernederen, anderen zal het verheffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,4,'Wanneer de aarde hevig zal worden geschokt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,5,'En de bergen verbrijzeld,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,6,'Zullen deze als stof worden verstrooid,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,7,'En gij zult in drie soorten worden verdeeld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,8,'De mensen aan de rechter kant - hoe (gelukkig zijn) de mensen aan de rechter kant!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,9,'En de mensen aan de linker kant - hoe (ongelukkig) zijn de mensen aan de linker kant!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,10,'De voorbijstrevenden (in het geloof) zullen de eersten zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,11,'Dezen zijn de gunstelingen die God dicht zullen naderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,12,'In tuinen van verrukking.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,13,'Het zijn een groot aantal van de eersten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,14,'En weinigen uit later tijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,15,'Op sofa´s doorvlochten met goud en edelgesteenten');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,16,'Daarop nederliggende, naar elkander toegewend!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,17,'Daar zullen jonge mannen onder hen rondgaan die niet zullen verouderen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,18,'Met bekers, kannen en kopjes gevuld uit een zilveren bron -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,19,'Zij zullen daarvan geen hoofdpijn krijgen noch zullen zij dronken worden -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,20,'En met fruit dat zij het liefst hebben -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,21,'En met vlees van vogelen dat zij begeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,22,'En er zullen schonen zijn met grote, mooie ogen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,23,'Als verscholen paarlen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,24,'Als beloning voor hetgeen zij plachten te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,25,'Zij zullen daar geen ijdele gesprekken of zondige taal horen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,26,'Doch het woord \"vrede, vrede.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,27,'En zij die rechts zullen staan - hoe (gelukkig) zijn deze die rechts staan!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,28,'Zij zullen zich bevinden tussen doornloze lotusbomen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,29,'En trossen bananen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,30,'En dekkende schaduwen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,31,'En stromende wateren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,32,'En overvloedig fruit,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,33,'Noch afgesneden, noch verboden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,34,'En edele vrouwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,35,'Voorwaar, Wij hebben dezen tot een wonderligke schepping gemaakt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,36,'Wij maakten haar maagden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,37,'Beminnelijk, van gelijke leeftijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,38,'Tot degenen aan de rechter kant.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,39,'(Behoort) een groot aantal van de eersten (gelovigen).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,40,'En een groot aantal uit latere tijden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,41,'De mensen aan de linker kant - hoe (ongelukkig) zijn degenen die aan de linker kant staan!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,42,'Te midden van verschroeiende winden en kokend water.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,43,'En in de schaduw van zwarte rook,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,44,'Noch koel, noch verfrissend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,45,'Voordien waren zij inderdaad in weelde (op aarde),');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,46,'En volhardden in grote zonde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,47,'En zij plachten te zeggen: \"Als wij dood zijn en stof en beenderen zijn geworden, zullen wij inderdaad herrijzen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,48,'En ook onze voorvaderen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,49,'Zeg: \"Ja, de vroegeren en de lateren');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,50,'Zullen tezamen worden verzameld op de vastgestelde tijd van een bepaalde Dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,51,'Dan, o gij, die waart verdwaald en hebt verloochend,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,52,'Gij zult. zeker van de boom van Zaqqoem eten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,53,'En zult er uw buik mee vullen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,54,'En daama kokend water drinken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,55,'(Drinkende,) zoals dorstige kamelen drinken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,56,'Dit zal hun onthaal zijn op de Dag des Gerichts.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,57,'Wij schiepen u, maar waarom wildet gij deWaarheid niet erkennen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,58,'Zeg mij wat gij verwekt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,59,'Schept gij het of zijn Wij de Schepper er van?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,60,'Wij hebben de dood onder u verordend en Wij kunnen niet worden tegengehouden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,61,'Om anderen als gij in uw plaats te stellen en u in een toestand te brengen die gij niet kent.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,62,'En zeker kent gij de eerste schepping. Waarom trekt gij er dan geen lering uit?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,63,'Hebt gij gezien wat gij zaait?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,64,'Doen Wij het groeien of doet gij dat?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,65,'Als Wij het willen, kunnen Wij dat alles tot stof maken, dan blijft gij jammeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,66,'(Zeggende): \"Wij zijn beladen met borgstelling,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,67,'Meer nog, wij zijn van alles beroofd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,68,'Ziet, het water dat gij drinkt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,69,'Zijt gij het die het uit de wolken nederzendt, of zijn Wij de Zender?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,70,'Indien Wij het willen, kunnen Wij het bitter maken. Waarom zijt gij dan niet dankbaar?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,71,'En zeg mij; het vuur dat gij aansteekt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,72,'Zijt gij het die de boom er voor doet groeien of zijn Wij het?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,73,'Wij hebben het tot een aanmaning en een weldaad gemaakt voor de reizigers in de wildernissen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,74,'Daarom verheerlijk de naam van uw Heer, de Verhevene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,75,'En Ik roep het verschieten der sterren tot getuige');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,76,'En inderdaad is dat een grote eed, indien gij het beseft -;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,77,'Voorzeker, dit is (de) verheven Koran,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,78,'Een beschermd Boek,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,79,'Dat niemand zal aanraken behalve zij die zich louteren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,80,'Een Openbaring van de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,81,'Veracht gij dan deze aankondiging?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,82,'En verzekert gij door de ontkenning ervan uw levensonderhoud?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,83,'Waarom dan, wanneer de ziel van (de stervende) zijn keel bereikt');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,84,'En gij ziet toe - op dat ogenblik');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,85,'Zijn Wij dichter bij hem dan gij, maar gij ziet dit niet,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,86,'Waarom dan, als gij niet onderdanig zijt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,87,'Brengt gij haar niet terug indien gij waarachtig zijt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,88,'Als hij nu behoort tot degenen, die dicht bij God zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,89,'Dan is voor hem geluk en geur en een tuin van verrukking;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,90,'En indien hij behoort tot degenen aan de rechter kant,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,91,'Dan luidt het \"Vrede zij u\" van degenen aan de rechter kant.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,92,'Maar als hij behoort tot de dwalenden die (de Waarheid) hadden verloochend,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,93,'Dan is voor hem een onthaal op kokend water');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,94,'En branden in de hel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,95,'Voorzeker dit is de werkelijkheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,56,96,'Verheerlijk daarom de naam van uw Heer, de Verhevene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,1,'Wat er ook in de hemelen en op aarde is, verheerlijkt Allah; Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,2,'Van Hem is het koninkrijk der hemelen en der aarde. Hij doet sterven en leven en Hij heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,3,'Hij is de Eerste en de Laatste, de Zich Manifesterende en de Verborgene, en Hij heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,4,'Hij is het Die de hemelen en de aarde in zes dagen schiep; daarna zette Hij zich op de Troon neder. Hij weet wat de aarde ingaat en wat er uit voortkomt, en wat van de hemelen nederkomt en wat er naar toe opstijgt. Hij is met u waar gij ook zijn moogt, want Allah ziet alles wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,5,'Van Hem is het koninkrijk der hemelen en der aarde en naar Allah worden alle dingen teruggebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,6,'Hij laat de nacht in de dag overgaan en de dag in de nacht: en Hij is de Kenner van het innerlijk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,7,'Gelooft in Allah en Zijn boodschapper en geeft weg van datgene waarvan Hij u erfgenamen heeft gemaakt. En zij onder u die geloven en besteden (als weldaad) zullen een grote beloning ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,8,'Wat scheelt u dat gij niet in Allah gelooft, terwijl de boodschapper u roept om in uw Heer te geloven en Hij een verbond met u heeft gesloten, indien gij gelovig zijt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,9,'Hij is het Die duidelijke tekenen nederzendt aan Zijn dienaar om u van de Duisternissen in het Licht te brengen en voorwaar, Allah is Liefderijk Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,10,'Waarom geeft gij niet terwille van Allah, terwijl aan Allah de erfenis van de hemelen en de aarde behoort? Degenen onder u die (geld) besteedden en streden v��r de overwinning zijn niet gelijk maar hoger in rang dan degenen die nadien (geld) besteedden en streden. En Allah heeft aan allen het goede beloofd. En Allah is op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,11,'Ieder die met Allah een goede lening sluit - Hij zal deze voor hem vermenigvuldigen en hem zal bovendien een voortreffelijke beloning ten deel vallen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,12,'En de Dag waarop gij de gelovige mannen en vrouwen zult zien, hun licht v��r hen en aan hun rechter handen uitstralende; verblijdend nieuws is er voor u op deze Dag! Tuinen waar doorheen rivieren stromen, waarin gij zult vertoeven. Dat is de opperste zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,13,'Op de Dag, waarop huichelaars en huichelaarsters tot de gelovigen zullen zeggen: \"Laat ons iets van uw licht nemen,\" zal er gezegd worden: \"Gaat terug en zoekt licht.\" Dan zal er tussen hen een muur worden opgericht met een poort er in. Aan de binnenkant zal barmhartigheid zijn en aan de buitenkant zal straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,14,'(De huichelaars zullen tot de gelovigen) roepen: \"Waren wij niet met u?\" Zij zullen antwoorden: \"Ja, maar gij hebt uzelf in verzoeking laten brengen en gewacht en getwijfeld en uw begeerte bedroog u, totdat de verordening van Allah kwam. En de bedrieger bedroog u ten opzichte van Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,15,'Derhalve zal op deze Dag geen losgeld van u worden aangenomen, noch van degenen die ongelovig waren. Uw tehuis zal het Vuur zijn; dat is uw vriend en het is een slechte bestemming!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,16,'Is voor de gelovigen de tijd nog niet aangebroken dat hun hart nederig worde om Allah gedachtig te zijn en de Waarheid (op te nemen), die nedergedaald is? En laten zij niet worden zoals zij die het Boek v��rdien ontvingen - voor dezen was de termijn (te) lang geworden waardoor hun hart werd verhard en velen van hen ongehoorzaam werden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,17,'Weet, dat Allah de aarde doet herleven na haar dood. Wij hebben de tekenen duidelijk voor u verklaard, opdat gij begrijpen moogt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,18,'De mannen en vrouwen die aalmoezen geven en degenen die met Allah een goede lening sluiten - deze zal voor hen vermenigvuldigd worden, bovendien zullen zij een eervolle beloning ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,19,'En zij, die in Allah en Zijn boodschappers geloven, zijn de waarachtigen en de martelaren in de ogen van hun Heer; zij zullen hun beloning en hun licht ontvangen. Maar zij die Onze boodschappen verwierpen en verloochenden, zullen de bewoners der hel zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,20,'Weet, dat het wereldse leven, alleen spel, vermaak, praalvertoon, pochelij onder elkander, wedijver in vermeerdering van rijkdom en kinderen, is als de regen waardoor het plantenleven de kwekers verblijdt. Dan droogt het op, gij ziet het geel worden en vergaan. En in het Hiernamaals is er een strenge straf en Allah´s vergiffenis en welbehagen. En het leven dezer wereld is niets anders dan een zaak van begoocheling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,21,'Wedijvert om vergiffenis van uw Heer (te verkrijgen) en voor het paradijs, waarvan de breedte gelijk is aan de breedte tussen hemel en aarde, bereid voor degenen, die in Allah en Zijn boodschappers geloven. Dat is de genade van Allah. Hij schenkt deze aan wie Hij wil en Allah is de Heer van grote genade.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,22,'Er gebeurt geen ongeluk op aarde of aan uzelf zonder dat het is opgetekend in het Boek voordat Wij het openbaren. Voorzeker - dat is gemakkelijk voor Allah -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,23,'Opdat gij niet moogt treuren over hetgeen gij verloren hebt noch juichen over hetgeen Hij u heeft gegeven, want Allah heeft geen pocher of opschepper lief');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,24,'(Noch degenen,) die vrekkig zijn en de mensen aansporen vrekkig te worden en wie zich van Hem afwendt; voorzeker Allah is Zichzelf-genoeg, Geprezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,25,'Voorwaar, Wij zonden Onze boodschappers met duidelijke bewijzen en openbaarden hun het Boek en de Weegschaal opdat het mensdom rechtvaardig moge zijn. Wij hebben ijzer nedergezonden, waardoor grote strijd doch ook grote voordelen voor het mensdom ontstaan, opdat Allah degenen moge onderscheiden, die in het ongeziene Hem en Zijn boodschappers helpen. Zeker, Allah is Sterk, Almachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,26,'En Wij zonden Noach en Abraham, en Wij plaatsten in hun nageslacht het profetenambt en het Boek. En enigen van hen waren op het rechte pad, maar de meesten hunner waren overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,27,'Dan deden Wij Onze boodschappers in hun voetsporen treden en Wij deden Jezus, de zoon van Maria, opvolgen en Wij gaven hem het Evangelie. En Wij legden zachtmoedigheid en barmhartigheid in het hart zijner volgelingen. Doch het kloosterleven schreven Wij hun niet voor, maar zij vonden dit zelf uit om Allah´s welbehagen te zoeken. Zij namen dit echter niet in acht zoals het behoorde. Toen gaven Wij de gelovigen onder hen een beloning, maar velen onder hen waren overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,28,'O gij gelovigen, vreest Allah en gelooft in Zijn boodschapper. Hij zal u een dubbel aandeel van Zijn barmhartigheid geven en u een licht verschaffen waarin gij wandelen zult en Hij zal u vergeven: - Voorwaar, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,57,29,'Opdat de mensen van het Boek mogen weten, dat zij geen macht hebben over de genade van Allah - Voorzeker de genade is in Allah´s handen, Hij geeft deze aan wie Hij wil. En Allah is de Heer van grote genade.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,1,'Allah heeft het woord gehoord van degene die met u aangaande haar man twistte en tot Allah klaagde. En Allah heeft uw gesprek gehoord. Voorwaar, Allah is Alhorend, Alziende.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,2,'Degenen onder u, die hun vrouwen moeders noemen - dezen zijn hun moeders niet; hun moeders zijn alleen degenen die hen baarden, - en voorzeker zij zeggen iets onbetamelijks en een leugen; doch Allah is Verdraagzaam, Vergevensgezind.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,3,'Degenen, die hun vrouwen moeders noemen en willen terugnemen wat zij zeiden, moeten hiervoor een slaaf bevrijden voordat zij elkander aanraken. Dit is een vermaning voor u. En Allah is goed op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,4,'Maar wie geen slaaf vindt, laat hem twee achtereenvolgende maanden vasten, voordat zij elkander aanraken. En wie dat niet doen kan, moet zestig arme mensen voeden. Dit is een bevel, opdat gij moogt geloven aan Allah en Zijn boodschapper. Dit zijn de verordeningen van Allah; en er is een pijnlijke straf voor de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,5,'Degenen, die tegen Allah en Zijn boodschapper ingaan, zullen zeker vernederd worden zoals degenen die hen vooraf gingen vernederd werden; want Wij hebben reeds duidelijke tekenen nedergezonden. En de ongelovigen zullen een onterende straf ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,6,'De Dag, waarop Allah hen allen tezamen zal opwekken, zal Hij hun over alles wat zij deden, inlichten. Allah heeft het opgetekend, terwijl zij het vergeten zijn. En Allah is Getuige van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,7,'Ziet gij niet, dat Allah alles weet wat in de hemelen en op aarde is? Er is geen geheim gesprek van drie (personen) zonder dat Hij de vierde is, noch van vijf, zonder dat Hij de zesde is, noch van minder noch van meer, zonder dat Hij met hen is, waar zij ook mogen zijn. Dan zal Hij hun op de Dag der Opstanding mededelen wat zij deden. Voorzeker, Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,8,'Hebt gij degenen niet waargenomen, wie de geheime samenzwering was verboden maar die daarna terugkeerden naar hetgeen hun verboden was en heimelijk beraadslagen in zonde, overtreding en ongehoorzaamheid jegens de boodschapper? En als zij tot u komen, groeten zij u met een groet, waar Allah u niet mee begroet; maar onder elkander zeggen zij: \"Waarom straft Allah ons niet voor hetgeen uw (tegen de profeet) zeggen?\" Genoegzaam voor hen is de hel waarin zij zullen branden; en deze is een slechte bestemming!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,9,'O. gij die gelooft, als gij tezamen beraadslaagt, spreekt dan niet over zonde, overtreding en ongehoorzaamheid jegens de boodschapper, maar beraadslaagt over deugd en rechtvaardigheid, en vreest Allah tot Wie gij zult worden verzameld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,10,'Geheime samenzwering gaat alleen uit van Satan, opdat hij verdriet moge veroorzaken aan de gelovigen maar het kan hun niet schaden dan met Allah´s toelating. Laat dus de gelovigen in Allah hun vertrouwen stellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,11,'O, gij die gelooft, als er u gezegd wordt: \"Maakt plaats in vergaderingen, maakt dan plaats; Allah zal rijkelijk plaats voor u maken. En als er gezegd wordt \"Staat op\" staat dan op; Allah zal de gelovigen onder u en hen die kennis werd gegeven in rang verheffen. En Allah is goed op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,12,'O, gij die gelooft, indien gij de boodschapper (in het bijzonder) wiltraadplegen, geeft dan een liefdegift v��r uw raadpleging. Dat is beter voor u en reiner. Maar als gij niets bezit dan is Allah Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,13,'Zijt gij bezorgd inzake het geven van liefdegiften voor uw bijzondere raadpleging? Indien gij dat niet doet en Allah heeft zich met barmhartigheid tot u gewend, houdt dan het Gebed en betaalt de Zakaat en gehoorzaamt Allah en Zijn boodschapper. En Allah is goed op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,14,'Hebt gij degenen niet gezien, die zich bevrienden met een volk, waarop Allah vertoornd was? Zij zijn noch de uwen noch de hunnen, zij zweren bij de leugen tegen beter weten in.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,15,'Allah heeft voor hen een zware straf bereid. Slecht is inderdaad hetgeen zij doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,16,'Zij hebben van hun eden een schild gemaakt en zij leiden anderen van het pad van Allah af; voor hen zal er een vernederende straf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,17,'Noch hun bezittingen, noch hun kinderen zullen hen tegen Allah iets baten, dit zijn de bewoners van het Vuur en zij zullen daarin vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,18,'De Dag waarop Allah hen allen zal opwekken, zullen zij tot Hem zweren zoals zij dit tot u deden en zij zullen denken dat zij iets bereiken. Ziet toe, zij zijn zeker leugenaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,19,'Satan heeft hen volledig in zijn macht, en heeft hen de gedachtenis aan Allah doen vergeten. Zij behoren tot Satans partij. Ziet toe, Satans partij is de verliezer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,20,'Waarlijk, degenen die Allah en Zijn Boodschapper tegenwerken zullen worden vernederd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,21,'Allah heeft verordend: \"Voorwaar Ik en Mijn boodschappers zullen zegevieren.\" Voorzeker Allah is Sterk, Almachtig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,58,22,'Gij zult geen mensen vinden die in Allah en de Laatste Dag geloven, terwijl zij iemand liefhebben die Allah en Zijn boodschapper tegenwerkt, zelfs al waren dezen hun vader of hun kinderen, of hun broeders, of hun verwanten. Dezen zijn degenen, in wier hart Allah geloof heeft ingegrift en die Hij gesterkt heeft met Zijn Geest. En Hij zal hen toelaten in tuinen waardoor rivieren stromen. Daarin zullen zij vertoeven. Allah heeft welbehagen in hen en zij hebben welbehagen in Hem. Zij behoren tot Allah´s partij. Voorwaar, Allah´s partij zal zegevieren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,1,'Alles wat in de hemelen en op aarde is, verheerlijkt Allah; en Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,2,'Hij is het Die de ongelovigen onder de mensen van het Boek, uit hun huizen zette bij de eerste verbanning. Gij dacht niet dat zij zouden weggaan en zij dachten dat hun vestingen hen zouden beschermen tegen Allah. Maar Allah kwam tot hen, vanwaar zij Hem niet verwachtten, en wierp schrik in hun hart, zodat zij hun huizen met hun eigen handen en met die van de gelovigen vernielden. Trekt er daarom een lering uit, o gij die ogen hebt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,3,'En indien Allah hun geen verbanning voorgeschreven had, zou Hij hen zeker in deze wereld (nog zwaarder) hebben bestraft. En voor hen is in het Hiernamaals de straf van het Vuur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,4,'Dat is omdat zij Allah en Zijn boodschapper tegenwerkten - en hij die Allah tegenwerkt - waarlijk, Allah is streng in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,5,'Welke palmbomen gij ook hebt nedergehouwen of op hun wortels hebt laten staan, het was met Allah´s toelating, opdat Hij de overtreders mocht vernederen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,6,'Hetgeen Allah van hen als buit aan, Zijn boodschapper heeft gegeven daarvoor spoordet gij noch paard noch kamelen aan; maar Allah geeft macht aan Zijn boodschappers over wie Hij wil. En Allah heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,7,'Wat Allah aan Zijn boodschapper heeft gegeven als buit van het volk van de stadsgebieden, is voor Allah en Zijn boodschapper en voor de naaste familieleden en de wezen en de armen en de reiziger, opdat het niet alleen in omloop moge zijn tussen de rijken onder u. En wat de boodschapper u ook moge geven, neemt het en wat Hij u ook verbiedt, onthoudt u daarvan. En vreest Allah, zeker, Allah is streng in het straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,8,'Een deel behoort aan de arme vluchtelingen die van hun huizen en hun eigendommen zijn verdreven, terwijl zij de genade van Allah en Zijn welbehagen zochten en Allah en Zijn boodschapper hielpen; dit zijn de waarachtigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,9,'En degenen die zich in de stad hebben gehuisvest en(anderen) v��rgingen in het geloof, hebben diegenen lief, die tot hen de toevlucht nemen, en gevoelen geen behoefte in hun hart aan hetgeen hun gegeven wordt, zij geven anderen de voorkeur boven zichzelf, al verkeren zij zelf in armoede. En wie voor zijn eigen vrekkigheid wordt behoed, hij is voorzeker geslaagd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,10,'En degenen die na hen kwamen, zeggen: \"Onze Heer, vergeef ons en onze broeders, die ons voorafgingen in het geloof, en laat geen wrok in ons hart blijven tegen de gelovigen. Onze Heer! Gij zijt inderdaad Liefderijk, Genadevol.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,11,'Hebt gij de huichelaars gezien? Zij zeggen tegen hun ongelovige broeders onder de mensen van het Boek: \"Indien gij verdreven wordt, zullen wij zeker met u medegaan, en wij zullen nooit iemand ten (nadele van) uw zaak gehoorzamen en als gij wordt aangevallen zullen wij u beslist helpen.\" Maar Allah is getuige dat zij leugenaars zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,12,'Als zij (de ongelovigen) verbannen zouden worden, zouden (de huichelaars) nooit met hen medegaan en als zij aangevallen zouden worden, zouden zij hen nooit helpen. En indien zij hielpen zouden zij zeker op de vlucht slaan en dan zullen zij niet geholpen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,13,'Voorzeker zij hebben meer angst in hun hart voor u (Moslims) dan voor Allah. Dat is omdat zij een volk zijn dat niet begrijpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,14,'Zij zullen u niet bestrijden zelfs allen tezamen, tenzij in versterkte steden of achter muren, ofschoon zij onderling grote dapperheid tonen. Gij denkt dat zij eensgezind zijn maar hun harten zijn verdeeld. Dat is omdat zij een volk zijn dat niet begrijpt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,15,'Evenals voor degenen die kort v��r hen het kwade gevolg hunner daden ondergingen, is er voor hen een pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,16,'Evenals Satan, wanneer hij tegen de mens zegt: \"Verwerp (de waarheid)\"; maar wanneer deze haar verwerpt zegt hij: \"Ik heb niets met u uitstaande, voorzeker, ik vrees Allah, de Heer der Werelden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,17,'Daarom zal het einde van beiden wezen, dat zij samen in het Vuur zullen vertoeven; dit is het loon der onrechtvaardigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,18,'O gij die gelooft, vreest Allah; en laat iedere ziel acht geven op hetgeen zij voor morgen voorbereidt. En vreest Allah, voorzeker Allah is op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,19,'En weest niet als degenen die Allah vergaten, zodat Hij hun hun eigen ziel heeft doen vergeten. Zij zijn de overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,20,'De bewoners van het Vuur en de bewoners van het paradijs zijn niet gelijk: de bewoners van het paradijs zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,21,'Indien Wij deze Koran op een berg hadden doen neerkomen, dan hadt gij de berg zich zien vernederen en splijten uit vrees voor Allah. Deze gelijkenissen zetten Wij aan de mensen voor opdat zij er over nadenken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,22,'Hij is Allah, naast Wie er geen God is, de Kenner van het onzienlijke en het zienlijke, Hij is de Barmhartige, de Genadevolle.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,23,'Hij is Allah, naast Wie er geen God is, de Koning, de Heilige, de Brenger van Vrede, de Schenker van Veiligheid, de Beschermer, de Machtige, de Krachtige, Bezitter van Grootheid. Verheven is Allah boven hetgeen zij met Hem vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,59,24,'Hij is Allah, de Schepper, de Maker, de Vormer. Hij heeft de schoonste namen. Alles wat in de hemelen en op aarde is verheerlijkt Hem en Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,1,'O gij die gelooft, neemt Mijn vijanden en uw vijanden niet tot vrienden! Biedt gij hun vriendschap aan, hoewel zij de Waarheid die tot u is gekomen hebben verworpen en de boodschapper en uzelf verdrijven, omdat gij in Allah uw Heer gelooft? Indien gij optreedt om voor Mijn zaak te strijden en Mijn welbehagen te zoeken, zoudt gij hun dan in het geheim vriendschap betuigen? En Ik weet het beste wat gij verbergt en wat gij openbaar maakt. En wie van u zo handelt, is zeker van de rechte weg afgedwaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,2,'Als zij de overhand over u krijgen zullen zij als vijanden tegenover u handelen, en zij zullen hun handen en tong naar u uitsteken om u kwaad te berokkenen, en zij wensen vurig dat gij ongelovigen zult worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,3,'Noch uw familiebanden noch uw kinderen zullen u op de Dag der Opstanding iets baten. Hij zal over u beslissen. En Allah ziet alles wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,4,'Er is een goed voorbeeld voor u in Abraham en degenen die met hem waren toen zij tegen hun volk zeiden: \"Wij hebben niets uitstaande met u en hetgeen gij buiten Allah aanbidt. Wij verwerpen u en er is tussen u en ons eeuwige vijandschap en haat ontstaan, tenzij gij in Allah, de Enige gelooft.\" - uitgezonderd het woord van Abraham tot zijn vader: \"Ik zal zeker om vergiffenis voor u vragen, ik heb niets van Allah ten uwen behoeve. - Onze Heer, in U stellen wij ons vertrouwen en tot U wenden wij ons, en naar U is de terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,5,'Onze Heer, maak ons niet tot een voorwerp van beproeving voor de ongelovigen en vergeef ons o, Heer, voorzeker Gij, Gij zijt de Almachtige, de Alwijze.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,6,'Voorzeker, zij zijn een goed voorbeeld voor een ieder onder u die Allah en de Laatste Dag vreest. En wie zich (van de Waarheid) afwendt, - waarlijk, Allah is zich zelf genoeg, Geprezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,7,'Het is mogelijk dat Allah liefde zal kweken tussen u en diegene van hen met wie gij in vijandschap verkeert; want Allah is Almachtig en Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,8,'Allah verbiedt u niet, degenen, die niet tegen u om de godsdienst hebben gevochten, noch u uit uw huizen hebben verdreven, goed te doen en rechtvaardig te behandelen; voorzeker, Allah heeft de rechtvaardigen lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,9,'Maar Allah verbiedt u vriendschap te betonen aan degenen, die tegen u gevochten hebben om de godsdienst, en die u uit uw huizen hebben verdreven of geholpen hebben u te verdrijven. En wie hun ook vriendschap aanbiedt, dezen zijn de boosdoeners.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,10,'O, gij die gelooft wanneer gelovige vrouwen tot u komen als vluchtelingen, beproeft haar (geloof); Allah kent hun geloof het beste. Als gij dan vindt dat zij gelovig zijn, zendt haar niet terug naar de ongelovigen. Deze vrouwen zijn voor hen niet wettig, noch zijn de ongelovigen wettig voor deze vrouwen. Maar betaalt (aan de echtgenoten) wat zij besteed hebben. En het is geen zonde voor u haar te huwen als gij haar haar huwelijksgift hebt gegeven. En houdt niet vast aan huwelijksbanden met ongelovige vrouwen; maar vraagt om hetgeen gij besteed hebt; en laten zij vragen om hetgeen zij besteed hebben. Dat is het gebod van Allah. Hij spreekt recht over u. En Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,11,'En als enig bezit door uw vrouwen van u overgaat in de handen der ongelovigen geeft dan in het omgekeerde geval aan diegenen, wier vrouwen zijn weggegaan hetzelfde als z. aan hun vrouwen besteed hadden. En vreest Allah in Wie gij gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,12,'O profeet! Wanneer gelovige vrouwen tot u komen, haar eed van trouw aan u afleggende: dat zij niets met Allah zullen vereenzelvigen, en dat zij noch zullen stelen, noch overspel plegen, noch hun kinderen doden, noch laster die zij moedwillig hebben verzonnen, zullen uiten, noch ongehoorzaam zullen zijn aan u in wat recht is, neem dan haar trouw aan en vraag vergiffenis voor haar van Allah. Waarlijk, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,60,13,'O gij die gelooft, bevriendt u niet met een volk op hetwelk Allah vertoornd is; zij wanhopen aan het Hiernamaals zoals de ongelovigen wanhopen aan hen, die in de graven liggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,1,'Wat zich ook in de hemelen en op de aarde bevindt, verheerlijkt Allah; Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,2,'O gij die gelooft, waarom zegt gij hetgeen gij niet doet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,3,'Het is afkeurenswaardig bij Allah dat gij zegt hetgeen gij niet doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,4,'Voorzeker, Allah heeft diegenen lief die terwille van Hem strijden in geordende gelederen, alsof zij een hechte muur vormen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,5,'En toen Mozes tegen zijn volk zeide: \"O mijn volk, waarom ergert gij mij, wetende dat ik Allah´s boodschapper voor u ben?\" En toen zij afdwaalden deed Allah hun hart zich afwenden, want Allah leidt het opstandige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,6,'En toen Jezus, zoon van Maria, zeide: \"O kinderen van Isra�l, Ik ben Allah´s boodschapper voor u, datgene bevestigend wat v��r mij in de Torah was, en een blijde tijding gevende van een boodschapper die na mij komen zal, zijn naam zal Ahmad zijn.\" En als hij tot hen komen zal met duidelijke bewijzen zullen zij zeggen: \"Dit is louter bedrog.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,7,'Wie is onrechtvaardiger dan hij die leugen over Allah verzint, terwijl hij opgeroepen wordt tot de Islam? Allah leidt het onrechtvaardige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,8,'Zij wensen Allah´s licht door hun mond te doven, maar Allah zal Zijn licht vervolmaken, hoewel de ongelovigen er afkerig van zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,9,'Hij is het Die Zijn boodschapper heeft gezonden met leiding en de godsdienst der Waarheid, opdat hij deze moge doen zegevieren over alle andere godsdiensten, al zijn de afgodendienaren er afkerig van.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,10,'O gij die gelooft, zal ik u inlichten over een handel die u zal redden van een pijnlijke straf?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,11,'Dat gij in Allah en Zijn boodschapper gelooft en voor de zaak van Allah met uw bezit en uw persoon strijdt. Dat is beter voor u als gij het weet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,12,'Hij zal u uw zonden vergeven en u in tuinen leiden waar doorheen rivieren stromen en tot reine woningen toelaten in tuinen der Eeuwigheid. Dat is de grote zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,13,'En nog meer waarnaar gij verlangt: hulp van Allah en een spoedige overwinning. En geef blijde tijding aan de gelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,61,14,'O, gij die gelooft, weest Allah´s helpers, zoals toen Jezus, zoon van Maria, tot zijn discipelen zeide: \"Wie zijn mijn helpers terwille van Allah?\" De discipelen antwoordden: \"Wij zijn Allah´s helpers!\" Toen geloofde een gedeelte van de kinderen Isra�ls, terwijl een ander deel niet geloofde maar Wij hielpen de gelovigen tegen hun vijand en zij werden overwinnaars.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,1,'Alles wat zich in de hemelen en op aarde bevindt verheerlijkt Allah, de Koning, de Heilige, de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,2,'Hij is het Die onder de ongeletterden een boodschapper heeft verwekt die Zijn tekenen onder hen verkondigt en hen zuivert en hun het Boek en de wijsheid onderwijst, ofschoon zij voorheen in openbare dwaling verkeerden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,3,'En ook anderen die dezen (gelovigen) nog niet hebben ontmoet. Hij is de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,4,'Dat is Allah´s genade, Hij schenkt haar aan wie Hij wil; en Allah is de Heer van grote genade.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,5,'Degenen die belast zijn met de Torah en deze niet naleven, zijn als een ezel die boeken draagt. Slecht is de staat van het volk dat de tekenen van Allah verwerpt. En Allah leidt het onrechtvaardige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,6,'Zeg: \"O gij Joden als gij denkt dat gij met uitsluiting van andere mensen de vrienden van Allah zijt, wenst dan de dood als gij de waarheid spreekt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,7,'Maar zij zullen deze nooit wensen vanwege hetgeen hun handen hebben uitgevoerd. En Allah kent de onrechtvaardigen goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,8,'Zeg: \"De dood waarvoor gij vlucht zal u zeker treffen. Dan zult gij tot de Kenner van het onzichtbare en zichtbare teruggebracht worden, en Hij zal u inlichten over hetgeen gij placht te doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,9,'O, gij die gelooft! Wanneer op Vrijdag de oproep tot het gebed is uitgezonden, haast u dan Allah gedachtig te zijn en verlaat de handel. Dit is beter voor u indien gij het weet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,10,'En als het gebed ge�indigd is, verspreidt u dan over het land en zoekt naar Allah´s genade, en gedenkt Allah vaak, opdat gij moogt slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,62,11,'Maar indien zji koopwaar of enig vermaak zien, gaan zij er haastig heen en laten u staan. Zeg: \"Hetgeen bij Allah is, is beter dan vermaak en handel, en Allah is de beste Onderhouder.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,1,'Wanneer de huichelaars tot u komen, zeggen zij: \"Wij getuigen dat gij inderdaad de boodschapper van Allah zijt.\" Allah weet dat gij Zijn boodschapper zijt, en Allah getuigt dat de huichelaars inderdaad leugenaars zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,2,'Zij hebben hun eden tot een schild gemaakt; zo leiden zij mensen van Allah´s weg af. Hetgeen zij doen is zeker slecht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,3,'Dat is omdat zij het geloof omhelsden en daarna verwierpen. Derhalve is een zegel op hun hart gedrukt en zij begrijpen niet (meer).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,4,'En wanneer gij hen ziet, behaagt hun uterlijk u en indien zij spreken luistert gij naar hen. Zij lijken op aangeklede stukken hout. Zij denken dat ieder gerucht tegen hen is. Zij zijn (uw) vijanden, neemt u daarom voor hen in acht. Allah´s vloek zij over hen! Hoe ver zijn zij afgewend (van de Waarheid)!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,5,'En wanneer er tot hen wordt gezegd: \"Komt, de boodschapper van Allah zal voor u om vergiffenis vragen,\" dan wenden zij hun hoofd af en gij ziet hen zich hoogmoedig terugtrekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,6,'Het is hetzelfde of gij wel of niet voor hen om vergiffenis vraagt, Allah zal hen stellig niet vergeven. Voorzeker, Allah leidt het opstandige volk niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,7,'Zij zijn het die zeggen, \"Besteedt niets voor degenen die met de boodschapper van Allah zijn zodat deze weglopen\"- terwijl aan Allah de schatten der hemelen en der aarde behoren; doch de huichelaars begrijpen dit niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,8,'Zij zeggen: \"Als wij naar Madinah terugkeren zal de aanzienlijkste er zeker de minste uitdrijven;\" maar eer behoort aan Allah, Zijn boodschapper en de gelovigen; de huichelaars echter weten het niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,9,'O, gij die gelooft, laat uw rijkdommen en uw kinderen u niet afleiden van de gedachtenis aan Allah. En wie dat doet behoort tot de verliezers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,10,'En besteedt uit datgene waarvan Wij u voorzien hebben voordat de dood ��n uwer overvalt en deze zegt: \"Mijn Heer! Waarom hebt Gij mij niet voor een wijle uitstel verleend, opdat ik aalmoezen zou kunnen geven en tot de rechtvaardigen behoren?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,63,11,'En Allah geeft niemand uitstel wanneer zijn tijd is gekomen; en Allah is volkomen op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,1,'Wat er ook in de hemelen en op aarde is, verheerlijkt Allah; Hem is het Koninkrijk en de Lof, want Hij heeft macht over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,2,'Hij is het Die u geschapen heeft; maar sommigen uwer zijn ongelovig en sommigen uwer zijn gelovig; en Allah ziet hoe gij handelt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,3,'Hij schiep de hemelen en de aarde in waarheid, en Hij heeft u gevormd en een schone gedaante gegeven, en tot Hem is aller terugkeer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,4,'Hij weet wat in de hemelen en op aarde is, Hij weet wat gij verbergt en wat gij openbaar maakt; en Allah weet alles wat in het innerlijk is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,5,'Heeft het verhaal u niet bereikt van degenen die vroeger ongelovig waren? Zo ondergingen zij het kwade gevolg van hun gedrag, en hen wacht een pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,6,'Deze (gingen onder) omdat hun boodschappers met duidelijke bewijzen tot hen kwamen, maar zij zeiden: \"Zullen stervelingen ons leiden?\" Daarom verwierpen zij (de Waarheid) en wendden zich af, Allah toonde Zijn zelfgenoegzaamheid, want AIlah is Zichzelf-genoeg, Geprezen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,7,'De ongelovigen denken dat zij niet zullen worden opgewekt. Zeg: \"Ja, bij mijn Heer, gij zult zeker herrijzen; dan zult gij worden onderricht omtrent hetgeen gij deedt. En dat is gemakkelijk voor Allah.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,8,'Gelooft daarom in Allah en Zijn boodschapper, en in het Licht dat Wij nedergezonden hebben. En Allah is op de hoogte van hetgeen gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,9,'Wanneer Hij u voor de Dag der Verzameling zal bijeenroepen, zal dit de tijd voor onthulling der gebreken zijn. En hij die gelooft in Allah en recht doet, - hem zal Hij zuiveren van zijn fouten en Hij zal hun tot tuinen toegang geven waardoor rivieren stromen, om daarin voor eeuwig te vertoeven. Dat is de grote zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,10,'Maar wie Onze tekenen verwerpen en loochenen, zullen de bewoners van het Vuur zijn, daarin zullen zij vertoeven, en dat is een slechte bestemming!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,11,'Er gebeurt geen ongeluk zonder toelating van Allah. En wie in Allah gelooft, - Hij leidt zijn hart. - En Allah heeft kennis van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,12,'Gehoorzaamt dus aan Allah en gehoorzaamt de boodschapper. Maar indien gij u afwendt dan berust op Onze boodschapper alleen, de boodschap duidelijk over te brengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,13,'Allah! Er is geen God dan Hij; laat de gelovigen daarom in Allah hun vertrouwen stellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,14,'O, gij gelovigen, er zijn onder uw echtgenoten en kinderen die uw vijanden zijn, neemt u dus voor hen in acht. En indien gij verontschuldigt en door de vingers ziet en vergeeft; dan is Allah Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,15,'Uw rijkdommen en uw kinderen zijn slechts een beproeving; doch bij Allah is er een grote beloning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,16,'Weest godvruchtig naar vermogen, luistert, gehoorzaamt en geeft weg, dat is beter voor u. En degenen die voor eigen vrekkigheid zijn behoed zullen slagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,17,'Indien gij een goede lening met Allah sluit, zal Hij deze voor u vermenigvuldigen en Hij zal u vergeven; want Allah is Waarderend, Verdraagzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,64,18,'De Kenner van het onzienlijke en het zienlijke, de Almachtige, de Alwijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,1,'O, profeet, indien gij van de vrouwen scheidt, scheidt dan van haar voor de vastgestelde periode en berekent de periode, en vreest Allah uw Heer. Verdrijft haar niet uit haar vertrekken, noch behoeven zij uit zichzelf weg te gaan (v��r de bepaalde termijn) tenzij zij zich openlijk onbetamelijk gedragen. Dit zijn Allah´s vastgestelde grenzen; en wie de door Allah bepaalde grenzen overschrijdt doet zeker zijn eigen ziel onrecht aan. Gij weet niet; misschien zal Allah daarna iets beters teweegbrengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,2,'Als zij dan haar termijn bereikt hebben, neemt haar op een vriendelijke manier terug, of scheidt van haar op een behoorlijke wijze en roept twee rechtvaardigen vanuit uw midden tot getuigen en laat dit een ware getuigenis zijn voor Allah. Dit is een vermaning voor hem die in Allah en de laatste Dag gelooft. En voor hem die Allah vreest, zal Hij een uitweg bereiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,3,'En Hij zal hem onderhouden vanwaar gij het niet verwacht. En voor hem, die zijn vertrouwen in Allah stelt, is Allah toereikend. Voorwaar, Allah volbrengt Zijn voornemen, Hij heeft voor alles een maatstaf bepaald.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,4,'En indien gij twijfelt aangaande diegenen uwer vrouwen, die geen menstruatie meer verwachten, haar (wacht) periode is drie maanden, hetzelfde geldt ook voor degenen die haar menstruatie nog niet hebben gehad. En de wachtperiode voor de zwangeren duurt tot zij verlost zijn. En degenen die Allah vrezen, zal Hij van het nodige voorzien door Zijn gebod.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,5,'Dat is het bevel van Allah dat Hij u heeft geopenbaard. En wie Allah vreest, van hem zal Hij zijn fouten wegnemen en zijn loon zal vergroot worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,6,'Herbergt haar (van wie gij scheidt) in de huizen waar gij vertoeft, overeenkomstig uw middelen; en doet haar geen kwaad om het haar moeilijk te maken. En als zij zwanger zijn, onderhoudt haar tot zij verlost zijn. En als zij haar kind voor u zogen geeft haar vergoeding en beraadslaagt tezamen in vriendelijkheid; maar als gij het lastig voor elkander maakt laat dan een andere vrouw het kind zogen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,7,'Laat hij die overvloed heeft geven uit zijn overvloed. En laat hij wiens middelen beperkt zijn, geven overeenkomstig hetgeen Allah hem heeft gegeven. Allah belast geen ziel boven hetgeen Hij haar heeft gegeven. Allah zal weldra verlichting verlenen na ongemak.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,8,'Hoe vele steden kwamen niet in opstand tegen het gebod van hun Heer en van Zijn boodschappers! Wij riepen ze dan tot een strenge verantwoording en kastijdden haar met strenge kastijding.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,9,'Zo ondervonden zij het kwade gevolg van hun gedrag en het einde (hiervan) was de ondergang.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,10,'Allah heeft hun een strenge straf bereid; vreest daarom Allah, o gij mensen van verstand, die gelooft! Allah heeft inderdaad een vermaning tot u nedergezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,11,'Een boodschapper, die aan u de duidelijke woorden van Allah voordraagt, opdat hij degenen die geloven en goede daden verrichten uit de duisternis in het licht moge brengen; en wie in Allah gelooft en goed doet, hem zal Hij in tuinen toelaten waar doorheen rivieren stromen om daarin voor eeuwig te vertoeven. Allah heeft hem inderdaad een voortreffelijk onderhoud geschonken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,65,12,'Allah is Hij Die de zeven hemelen schiep, en van de aarde desgelijks. Het gebod daalt in hun midden neder, opdat gij moogt weten dat Allah macht heeft over alle dingen, en dat Allah alle dingen in zijn kennis omvat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,1,'1. O Prophet! warum untersagst du (dir) das, was Allah dir erlaubt hat? Suchst du deinen Frauen zu gefallen? Und Allah ist allverzeihend, barmherzig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,2,'Allah heeft de annulatie van uw eden voor u verplichtend gesteld en Allah is uw Beschermer en Hij is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,3,'Toen de profeet een woord aan een zijner vrouwen toevertrouwde en zij het daarna ruchtbaar maakte (aan een andere), deelde Allah hem dit mede. Hij maakte een deel er van bekend en verzweeg een deel ervan. En toen hij het haar vertelde, zeide zij: \"Wie gaf u hiervan kennis? \" Hij zeide: \"De Alwetende, de van alles op de hoogte, heeft mij er bericht van gegeven.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,4,'Als gij beide (vrouwen) u tot Allah wendt en uw hart is reeds hiertoe geneigd (dan is het wel) - Maar indien gij samenspant tegen hem (de profeet), dan is Allah zeker zijn Beschermer, bovendien zijn Gabri�l, de rechtvaardigen onder de gelovigen en de engelen zijn helpers.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,5,'Indien hij van u scheidt, is het mogelijk dat zijn Heer hem betere vrouwen dan u zal geven, die Moslim zijn en onderdanig, gelovig, gehoorzaam, berouwvol, vroom, gewend te vasten, weduwen of maagden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,6,'O gij die gelooft, redt u zelf en uw gezinnen van het Vuur, welks brandstof mensen en stenen zijn, waarover engelen zijn, hard en streng, die Allah niet ongehoorzaam zijn in hetgeen Hij hun beveelt, en volvoeren wat hun wordt geboden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,7,'O, gij ongelovigen, verontschuldigt u vandaag niet! U zal slechts vergolden worden voor hetgeen gij placht te doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,8,'O gij gelovigen, wendt u tot Allah in oprecht berouw. Het kan zijn dat uw Heer uw fouten van u zal verwijderen en u in tuinen toelaten waar doorheen rivieren stromen, op de Dag waarop Allah de profeet alsmede de gelovigen niet zal vernederen. Hun licht zal v��r hen en van hun rechter handen uitgaan. Zij zullen zeggen: \"Onze Heer, volmaak ons licht voor ons en vergeef ons; want Gij hebt macht over alle dingen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,9,'O profeet, strijd tegen de ongelovigen en de huichelaars en wees streng tegen hen. Hun woning is de hel en dit is een kwade bestemming!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,10,'Allah vergelijkt de ongelovigen met de vrouw van Noach en met die van Lot. Zij behoorden aan twee Onzer rechtvaardige dienaren maar zij waren hun ontrouw. Daarom baatten haar echtgenoten haar niet tegen Allah, en er werd tot hen gezegd: \"Gaat het Vuur in tezamen met degenen die er binnengaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,11,'En Allah vergelijkt de gelovigen met de vrouw van Pharao toen zij zeide: \"Mijn Heer! bouw voor mij een huis bij U in het Paradijs, verlos mij van Pharao en zijn daden en verlos mij van het onrechtvaardige volk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,66,12,'En met Maria, de dochter van Imraan, die haar kuisheid bewaarde; Toen ademden Wij haar Onze geest in - zij geloofde in het Woord van haar Heer en Zijn Boeken en behoorde tot de gehoorzamen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,1,'Gezegend is Hij in Wiens hand het Koninkrijk is en Die macht heeft over alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,2,'Die de dood en het leven heeft ingesteld, opdat Hij u moge beproeven wie onder u zich het beste gedraagt; en Hij is de Almachtige, de Vergevensgezinde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,3,'Hij Die de zeven hemelen opeenvolgend heeft geschapen. Gij kunt geen tekort zien in de schepping van de Barmhartige. Kijk dan nog eens; ziet gij een enkel gebrek?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,4,'Kijk dan weer eens en dan nog eens, uw blik zal vermoeid en verzwakt tot u terugkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,5,'En voorwaar, Wij hebben de naastbije hemel met lampen versierd, Wij hebben hem tot een middel gemaakt om de satans te verdrijven en voor hen hebben Wij de straf van het razende Vuur bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,6,'En voor degenen die niet in hun Heer geloven is de straf der hel (bereid), en dit is een slechte bestemming.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,7,'Wanneer zij er in worden geworpen, zullen zij haar van woede horen zieden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,8,'Zij zal bijna barsten van woede. Telkens als een groep er in geworpen wordt, zullen de bewakers er van (der hel) hun vragen: \"Kwam er geen waarschuwer tot u?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,9,'Zij zullen zeggen: \"Zeker, de waarschuwer kwam tot ons, maar wij verwierpen hem, en zeiden: \"Allah heeft niets geopenbaard; gij verkeert slechts in grote dwaling.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,10,'En zij zullen zeggen: \"Indien wij maar geluisterd hadden en ons verstand hadden gebruikt, zouden wij ons niet onder de bewonerg van het laaiende Vuur bevinden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,11,'Dan zullen zij hun zonden bekennen; maar de bewoners van het Vuur zijn verre (van genade).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,12,'Waarlijk, degenen die hun Heer in het verborgene vrezen, zullen vergiffenis en een grote beloning ontvangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,13,'Hetzij gij uw woorden verbergt of openbaar maakt, Hij weet, wat in (uw) binnenste is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,14,'Zou Hij Die schiep niet alles weten? Hij is Aldoordringend, Alkennend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,15,'Hij is het Die de aarde aan u onderworpen heeft; wandelt dus op haar paden en geniet van haar gaven. En tot Hem zal de Opstanding zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,16,'Voelt gij u veilig voor Hem Die in de Hemel is, dat Hij u niet zal doen verzwelgen als de aarde plotseling begint te schudden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,17,'Voelt gij u veilig voor Hem Die in de Hemel is, dat Hij niet tegen u een orkaan zal zenden? Dan zult gij weten, hoe (mijn) waarschuwing was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,18,'En voorzeker loochenden zij die v��r u waren ook (de boodschap). Hoe (ernstig) was dan Mijn afkeuring!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,19,'Hebben zij de vogelen niet boven hun (hoofden) gezien, die hun vleugels uitspreiden en in- eenvouwen? Niemand behalve de Barmhartige houdt ze tegen, waarlijk, Hij ziet alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,20,'Waar is uw leger dat u buiten Allah om zou kunnen helpen? De ongelovigen zijn omhuld door bedrog.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,21,'Of wie is er die voor u wil zorgen indien Hij Zijn voorziening terughoudt? Neen, zij volharden in opstandigheid en afkerigheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,22,'Is hij die gebogen loopt, beter geleid of hij die rechtop het rechte pad bewandelt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,23,'Zeg: \"Hij is het, Die u schiep, en u oren, ogen en hart gaf; weinig dank betuigt gij er voor.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,24,'Zeg: \"Hij is het Die u vermenigvuldigt op aarde en tot Hem zult gij bijeen verzameld worden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,25,'En zij zeggen: \"Wanneer zal deze belofte vervuld worden, als gij de waarheid spreekt?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,26,'Zeg: \"De kennis daarvan ligt alleen bij Allah en ik ben slechts een duideliike waarschuwer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,27,'Maar als zij de straf van nabij zullen zien, zal het gezicht der ongelovigen zich verduisteren en er zal gezegd worden: \"Dit is wat gij placht te vragen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,28,'Zeg: \"Vertel mij, indien Allah mij en degenen die met mij zijn, zou vernietigen - veeleer zal Hij ons genadig zijn - wie zal de ongelovigen tegen een pijnlijke straf kunnen beschermen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,29,'Zeg: \"Hij is de Barmhartige, in Hem geloven wij en in Hem stellen wij ons vertrouwen. En gij zult weldra weten wie in klaarblijkelijke dwaling verkeert.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,67,30,'Zeg: \"Vertel mij, indien uw water diep in de aarde wegzakt, wie zal u dan helder stromend water kunnen brengen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,1,'Noen. Bij de pen, en bij hetgeen zij schrijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,2,'Gij zijt, bij de gratie van uw Heer, geen krankzinnige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,3,'En voorzeker er is een loon voor u dat niet zal ophouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,4,'En gij staat zeker op hoog zedelijk peil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,5,'En gij zult zien en zij (de ongelovigen) zullen ook zien,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,6,'Wie van u bezeten is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,7,'Zeker, uw Heer weet het beste wie van Zijn weg afdwaalt en Hij kent het beste degenen die de leiding volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,8,'Dus gehoorzaam de loochenaars niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,9,'Zij zouden willen dat gij meegaande waart, dan zouden zij ook meegaande kunnen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,10,'En geef geen gehoor aan een verachtelijke eedaflegger,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,11,'Lasteraar, achterklapper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,12,'Tegenhouder van het goede, overtreder, zondaar,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,13,'Laatdunkend, bovendien een berucht misdadiger,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,14,'Omdat hij rijkdommen en kinderen bezit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,15,'Wanneer Onze woorden aan hem worden voorgedragen, zegt hij: \"Fabelen der oudeu.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,16,'Wij zullen hem op de neus brandmerken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,17,'Voorwaar, Wij zullen hen (de ongelovigen) op de proef stellen zoals Wij de eigenaars van een tuin beproefden toen zij zwoeren dat zij zeker het fruit daarvan in de vroege morgen zouden plukken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,18,'En zij maakten geen voorbehoud.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,19,'Toen kwam er van uw Heer een bezoeking over hen, terwijl zij sliepen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,20,'Waardoor (de tuin) werd als een gemaaid veld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,21,'Toen riepen zij tot elkander in de morgen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,22,'Zeggende: \"Gaat vroeg naar uw veld indien gij het fruit wilt plukken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,23,'En zij gingen fluisterend met elkander op weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,24,'\"Laat heden geen arme bij u binnen komen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,25,'En zij gingen vroeg in de morgen uit, (denkende) dat zij de macht hadden om het te verhinderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,26,'Maar toen zij de tuin zagen, zeiden zij: \"Voorwaar, wij zijn verdwaald!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,27,'Neen, wij zijn beroofd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,28,'De beste onder hen sprak: \"Zeide ik niet tot u: ´Waarom looft gij (God) niet?´\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,29,'Nu riepen zij uit: \"Glorie zij U, onze Heer! Voorzeker wij waren onrechtvaardig.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,30,'Toen gingen zij elkaar beschuldigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,31,'En zeiden: \"Wee ons, wij waren inderdaad overtreders.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,32,'Het kan zijn dat onze Heer ons een betere tuin dan deze zal geven, wij wenden ons tot onze Heer.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,33,'Zo is de straf (voor dit leven). En voorwaar, de straf van het Hiernamaals zal nog groter zijn, konden zij dit maar begrijpen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,34,'Inderdaad, voor de rechtvaardigen zijn er verrukkelijke tuinen bij hun Heer!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,35,'Zullen Wij dan degenen die zich onderwerpen even als de schuldigen behandelen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,36,'Wat is er met u? Hoe oordeelt gij?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,37,'Hebt gij een Boek waarin gij leest?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,38,'Dat gij alles waarnaar gij verlangt zult verkrijgen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,39,'Of hebt gij enige verdragen met Ons gesloten tot de Dag der Opstanding zodat gij dan alles zult hebben wat gij zult willen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,40,'Vraag hun, wie van hen daar borg voor is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,41,'Of hebben zij soms deelgenoten? Laten zij dan deze naar voren brengen als zij de waarheid spreken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,42,'Op de Dag, waarop men beangstigd wordt, zullen zij geroepen worden te prostreren, maar zij zullen dat niet kunnen doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,43,'Hun ogen zullen terneergeslagen zijn en vernedering zal hen overvallen, want zij werden tot het prostraat Sadjdah geroepen toen hun niets ontbrak (en zij deden het niet).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,44,'Laat Mij en degenen die deze aankondiging loochenen, alleen. Wij zullen hen stap voor stap (de vernietiging) doen naderen, op een wijze die zij niet kennen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,45,'En Ik geef hun uitstel; want Mijn opzet is sterk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,46,'Vraagt gij van hen een beloning voor u zelf zodat zij onder schuld gebukt gaan?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,47,'Of hebben zij kennis van het onzienlijke, zodat zij het kunnen opschrijven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,48,'Wacht geduldig op het gebod van uw Heer en wees niet als de man van de vis toen hij (Allah) aanriep terwijl hij misnoegd was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,49,'Als een gunst van zijn Heer hem niet had bereikt dan zou hji zeker op een dorre kust geworpen zijn, terwijl hij vernederd werd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,50,'Maar zijn Heer verkoos hem en maakte hem tot ��n der goeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,51,'En de ongelovigen wanneer zij het vermaan horen willen u met hun blikken gaarne ten val brengen; en zij zeggen: \"Hij is zeker krankzinnig.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,68,52,'Neen, het (Boek) is niets dan een vermaning voor de werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,1,'Datgene wat plaats zal hebben');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,2,'Wat is het dat plaats zal hebben?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,3,'Gij weet niet wat plaats zal hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,4,'De Samoed alsook de Aad loochenden de ramp.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,5,'Wat de Samoed betreft, dezen werden door een overweldigende straf vernietigd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,6,'En de Aad werden door een felle, geweldige wind vernietigd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,7,'Die Hij zeven nachten en acht dagen achtereenvolgens over hen liet woeden, zodat gij hadt kunnen zien hoe het volk er door neergeworpen werd, alsof zij gevallen palmboomstammen waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,8,'Kunt gij enige overblijfselen van hen vinden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,9,'Ook Pharao, en degenen die v��r hem waren, en de steden die verwoest werden begingen grote zonde;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,10,'En zij gehoorzaamden de boodschapper van hun Heer niet, daarom greep Hij hen met een vaste greep.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,11,'Ziet, toen de wateren stegen, droegen Wij u de ark binnen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,12,'Opdat Wij dit tot een les voor u mochten maken en opdat degene die deze (gebeurtenis) kan onthouden zich deze moge herinneren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,13,'En wanneer een enkele stoot op de bazuin zal worden geblazen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,14,'En de aarde en de bergen van hun plaats zullen worden opgeheven en terstond zullen worden verbrijzeld,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,15,'Op die Dag zal de grote gebeurtenis plaats vinden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,16,'En de hemelen zullen uiteen splijten, zodat deze op die Dag zwak zullen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,17,'En de engelen zullen op de zijden ervan staan. En op die Dag zullen acht engelen de troon van uw Heer boven zich houden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,18,'Dan zult gij worden bloot gelegd en geen uwer geheimen zal verborgen blijven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,19,'En hij, aan wie zijn boek in de rechter hand wordt gegeven, zal zeggen: \"Komt, leest mijn boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,20,'Voorzeker, ik wist dat ik mijn afrekening tegemoet moest gaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,21,'Deze zal dan een heerlijk leven krijgen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,22,'In een verheven tuin,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,23,'Waarvan het fruit gemakkeljik bereikbaar zal zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,24,'\"Eet en drinkt smakelijk als loon voor hetgeen gij in vroeger dagen hebt gedaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,25,'Maar, hij wiens boek in de linker hand wordt gegeven, zal zeggen: \"O was mijn boek mij maar niet gegeven!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,26,'En had ik maar niet geweten wat mijn oordeel was!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,27,'O, had de dood maar aan mij een einde gemaakt!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,28,'Mijn rijkdom heeft mij niet gebaat,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,29,'Mijn macht is van mij weg gegaan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,30,'Grijpt hem en boeit hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,31,'Werpt hem dan in de hel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,32,'Bindt hem vervolgens met een ketting vast waarvan de lengte zeventig armlengten bedraagt;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,33,'Want hij geloofde niet in Allah, de Grote.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,34,'Noch moedigde hij aan, de armen te spijzigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,35,'Daarom heeft hij hier geen vriend;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,36,'Noch voedsel, behalve spoelsel van wonden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,37,'Dat niemand dan de zondaren zal gebruiken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,38,'Neen, Ik zweer bij alles wat gij ziet,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,39,'En bij alles wat gij niet ziet,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,40,'Dit is voorzeker de boodschap die een eerwaardige boodschapper heeft gebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,41,'Het is geen woord van een dichter; nietig is hetgeen gij gelooft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,42,'Noch is het de uiting van een waarzegger; gering is de lering, die gij er uit trekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,43,'Het is een Openbaring van de Heer der werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,44,'En indien hij enige woorden in Onze naam had uitgedacht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,45,'Dan zouden Wij hem zeker bij de rechter hand hebben gegrepen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,46,'En daarna zijn levensader hebben afgesneden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,47,'En geen uwer zou ons van hem hebben kunnen tegenhouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,48,'Voorwaar, het is een vermaning voor de godvrezenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,49,'En voorzeker, Wij weten dat er onder u loochenaars zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,50,'Waarlijk, de ongelovigen zullen er wroeging over hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,51,'En voorwaar, het is de ware zekerheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,69,52,'Verheerlijk daarom de naam van uw Heer, de Luisterrijke.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,1,'Men vraagt naar de straf, die straks zal vallen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,2,'Over de ongelovigen, die niemand kan weerhouden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,3,'Van Allah, de Heer der wegen die omhoog leiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,4,'De engelen en de geest gaan tot Hem op, in een Dag waarvan de maat vijftig duizend jaren is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,5,'Heb daarom gepast geduld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,6,'Zij (de ongelovigen) zien (de straf) ver weg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,7,'Maar Wij zien die nabij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,8,'De Dag waarop de hemelen als gesmolten koper zullen worden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,9,'En de bergen als zachte, gekleurde wol,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,10,'En een vriend zal een vriend niet vragen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,11,'Hoewel zij elkander kunnen zien. Op die Dag zal de schuldige zich gaarne van de straf willen vrijkopen door zijn kinderen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,12,'En zijn vrouw en zijn broeder,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,13,'En zijn familieleden die hem een toevlucht waren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,14,'En allen die op aarde zijn, om zich te redden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,15,'Stellig niet! Waarlijk het is een laaiend Vuur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,16,'Het zal zijn huid afschroeien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,17,'Het zal hem opeisen, die zich afwendt en wegloopt');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,18,'En rijkdommen verzamelt, en deze (gierig) terughoudt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,19,'Voorwaar, de mens is geschapen met een ongeduldige aard.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,20,'Als hem kwaad overkomt, is hij vol weeklagen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,21,'Maar als hem goed wedervaart, is hij inhalig,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,22,'Behalve degenen die bidden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,23,'En in hun gebeden volharden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,24,'En degenen in wier rijkdommen een vastgesteld deel is');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,25,'Voor de bedelaar en voor hem die niet bedelen kan');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,26,'En degenen die de Dag des Oordeels aannemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,27,'En degenen die de straf van hun Heer vrezen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,28,'Voorwaar, er is geen beveiliging voor de straf van hun Heer -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,29,'En degenen die onthouding betrachten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,30,'- Uitgezonderd met hun vrouwen en degenen die zij bezitten, waarvoor hen geen blaam treft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,31,'Maar degenen die buiten deze (voorschriften) handelen zijn overtreders -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,32,'En degenen die het hun toevertrouwde bewaren en hun verdragen nakomen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,33,'En degenen die oprecht zijn in hun getuigenissen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,34,'En degenen die hun gebeden naleven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,35,'Zij zijn het die in de tuinen zullen worden ge�erd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,36,'Maar wat scheelt de ongelovigen die zich naar u toe spoeden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,37,'Van rechts en links in groepen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,38,'Verwacht elk hunner de tuin van verrukking binnen te gaan?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,39,'Stellig niet! Wij zijn het Die hen hebben geschapen uit hetgeen zij weten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,40,'Maar neen! Ik zweer als Heer van het Oosten en het westen dat Wij macht hebben,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,41,'In hun plaats betere (volkeren) dan zij voort te brengen en Wij kunnen (daarin) niet worden verhinderd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,42,'Laten zij zich aan ijdele gesprekken overgeven en zich vermaken tot zij de Dag tegemoet gaan welke hun beloofd is,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,43,'De Dag waarop zij zich uit hun graven zullen haasten alsof zij zich naar een bepaald doel spoeden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,70,44,'Met hun ogen nedergeslagen; schande zal hen bedekken. Zo is de Dag die hun beloofd is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,1,'Wij zonden Noach tot zijn volk, \"Waarschuw uw volk voordat een smartelijke straf over hen komt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,2,'Noach zeide: \"O mijn volk! Waarlijk ik ben een duidelijke waarschuwer voor u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,3,'Aanbidt daarom Allah, vreest Hem en gehoorzaamt mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,4,'Hij zal u uw zonden vergeven en u uitstel verlenen tot een bepaalde termijn; voorwaar, de termijn van Allah kan, wanneer hij komt, niet worden uitgesteld, als gij dit slechts wist!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,5,'Hij zeide: \"Mijn Heer, ik heb mijn volk dag en nacht geroepen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,6,'Maar mijn roepen heeft slechts hun afkeer vermeerderd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,7,'En telkens wanneer ik hen riep, opdat Gij hen zoudt vergeven stopten zij hun vingers in de oren, bedekten zich met hun kleren, volhardden (in hun ongeloof) en gedroegen zich laatdunkend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,8,'Toen riep ik hen luide,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,9,'En verkondigde hun in het openbaar; ook sprak ik tot hen in het verborgene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,10,'En ik zeide: \"Zoekt vergiffenis van uw Heer, want Hij is de Vergevensgezinde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,11,'Hij zal regen voor u nederzenden in overvloed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,12,'En Hij zal uw rijkdommen en kinderen vermeerderen, en Hij zal u tuinen en rivieren schenken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,13,'Wat scheelt u, dat gij geen Wijsheid van Allah verwacht?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,14,'En Hij heeft u door verschillende stadia heen geschapen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,15,'\"Hebt gij niet gezien, hoe Allah de zeven opeenvolgende hemelen schiep?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,16,'En hoe Hij de maan daarin als licht heeft geplaatst en de zon als een stralende lamp!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,17,'En Allah heeft u voortgebracht vanuit de aarde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,18,'Vervolgens zal Hij u daarheen doen terugkeren, en u daaruit opnieuw doen verrijzen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,19,'En Allah heeft de aarde voor u uitgespreid');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,20,'Zodat gij de brede wegen er van doorkruist.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,21,'Noach zeide: \"Mijn Heer, zij gehoorzamen mij niet, en volgen iemand wiens bezit en kinderen slechts tot zijn ondergang hebben bijgedragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,22,'En zij hebben een vreselijk plan gesmeed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,23,'En zeggen tegen elkander: ´Verlaat uw goden nooit. Verlaat noch Wodd, noch Sowa, noch Jaghoes en Jaoeq en Nasr.´');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,24,'En zij hebben velen doen dwalen, en Gij doet de onrechtvaardigen slechts in dwaling toenemen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,25,'Daarom werden zij vanwege hun zonden verdronken en in het Vuur gedreven. Zij konden daar voor zich geen helpers vinden tegen Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,26,'En Noach had gezegd: \"Mijn Heer, laat in het land geen huis der ongelovigen achterblijven;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,27,'Want als Gij hen achterlaat zullen zij Uw dienaren op een dwaalspoor leiden en zij zullen niets dan een onzedelijk en ondankbaar nageslacht voortbrengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,71,28,'Mijn Heer, vergeef mij, en mijn ouders, en hem die gelovend mijn huis binnentreedt, ook de gelovige mannen en vrouwen; en doe de onrechtvaardigen slechts in verderf toenemen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,1,'Zeg: \"Het is aan mij geopenbaard dat een groep der djinn heeft geluisterd (naar de Koran), en zij zeiden: ´Waarlijk, wij hebben een wonderbaarlijke verkondiging gehoord!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,2,'Die tot rechtschapenheid leidt; daarom hebben wij er in geloofd, en wij zullen stellig niemand met onze Heer vereenzelvigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,3,'En de Majesteit van onze Heer is hoog verheven. Hij heeft noch echtgenote noch zoon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,4,'En voorzeker, de dwaas onder ons placht over Allah leugen te spreken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,5,'Doch wij hadden gemeend dat mensen en djinn nooit een leugen over Allah zouden uiten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,6,'Voorzeker, waren er enige mensen die toevlucht bij sommige djinn zochten, waardoor zij hun zonden vermeerderden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,7,'En zij meenden inderdaad, zoals gij meendet, dat Allah nooit een boodschapper zou zenden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,8,'En wij trachtten de hemel te bespieden en wij vonden deze vol sterke wachters en vlammen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,9,'En voorzeker, wij plachten op enige plaatsen te zitten om de gesprekken te beluisteren. Maar wie nu luistert, vindt een vlam die op hem wacht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,10,'Wij weten daardoor niet of voor degenen die op aarde zijn, een ramp wordt bedoeld of dat hun Heer hen op het goede pad wil leiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,11,'Er zijn onder ons die rechtvaardig zijn en er zijn onder ons die anders zijn en wij volgen verschillende wegen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,12,'En wij beseffen dat wij Allah´s (plan) op aarde onmogelijk kunnen verijdelen, noch kunnen wij Hem door de vlucht ontlopen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,13,'En toen wij de leiding hoorden, geloofden wij er in. En hij, die gelooft in zijn Heer, heeft geen vrees voor verlies of onrecht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,14,'En er zijn onder ons Moslims en er zijn onder ons die van de rechte weg zijn afgeweken. En zij die zich onderwerpen - hebben de rechte weg gezocht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,15,'En zij die van de rechte weg afwijken, zullen brandstof der hel zijn.´\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,16,'Indien zij zich aan het rechte pad houden zullen Wij hun water in overvloed te drinken geven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,17,'Om hen daarmee op de proef te stellen. En wie zich van de gedachte aan zijn Heer afwendt, Hij zal hem een toenemende straf toedienen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,18,'En zeg: \"Alle bedehuizen behoren aan Allah; roept daarom niemand naast Allah aan.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,19,'En toen de dienaar van Allah opstond om Hem te aanbidden, vielen zij hem bijna aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,20,'Zeg: \"Ik bid alleen tot mijn Heer en ik vereenzelvig niemand met Hem.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,21,'Zeg: \"Ik heb (uit mijzelf) geen macht u goed of kwaad te doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,22,'Zeg: \"Voorzeker, niemand kan mij tegen Allah beschermen, noch kan ik een andere schuilplaats vinden buiten Hem -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,23,'(Mij is) slechts de verkondiging van Allah´s boodschap opgedragen.\" En voor degenen die Allah en Zijn boodschapper niet gehoorzamen is het Vuur der hel, waarin zij lange tijd zullen vertoeven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,24,'Tot zij de straf zien waarmee zij worden bedreigd, maar dan zullen zij ook weten wie zwakkere helpers en kleiner aantal heeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,25,'Zeg hun: \"Ik weet niet of hetgeen waarmede gij bedreigd wordt nabij is of wel dat mijn Heer het zal uitstellen voor een lange tijd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,26,'Hij is de Kenner van het onzienlijke en Hij geeft niemand overvloedig kennis van Zijn geheimen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,27,'Behalve hem die Hij als boodschapper kiest. Dan doet Hij een wacht v��r hem en achter hem gaan,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,72,28,'Opdat Hij moge weten dat zij (Zijn boodschappers) de boodschappen van hun Heer hebben overgebracht. En Hij omvat alles wat met hen is - en Hij heeft alles berekend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,1,'O, gij die u omwikkelt!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,2,'Sta op in de nacht voor korte tijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,3,'De helft er van of minder dan dat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,4,'Of maak het iets langer - en zeg de Koran duidelijk en aandachtig op.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,5,'Waarlijk, Wij dragen u een gewichtig Woord op.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,6,'Voorwaar, des nachts opstaan is de zekerste weg en geeft het Woord krachtige uitwerking.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,7,'Gij hebt inderdaad gedurende de dag langdurige bezigheden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,8,'Daarom gedenk de naam van uw Heer, en geef u met volle toewijding aan Hem over.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,9,'Hij is de Heer van het Oosten en het Westen, er is geen andere God naast Hem; neem Hem daarom tot uw Beschermer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,10,'En verdraag met geduld alles wat zij (de ongelovigen) zeggen; en verlaat hen op gepaste wijze.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,11,'En laat Mij alleen met degenen die loochenen, de bezitters van rijkdom en geef hun een wijle uitstel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,12,'Voorzeker, bij Ons zijn zware boeien en een laaiend Vuur,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,13,'En voedsel dat verstikt, en pijnlijke straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,14,'Er zal een Dag komen waarop de aarde en de bergen zullen beven, en de bergen in een hoop mul zand zullen veranderen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,15,'Waarlijk, Wij hebben tot u een boodschapper gezonden, die een getuige tegen u is, geljik Wij een boodschapper tot Pharao zonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,16,'Maar Pharao gehoorzaamde de boodschapper niet, daarom grepen Wij hem met een verschrikkelijke greep aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,17,'Hoe zult gij u, indien gij het ware geloof verwerpt, beveiligen voor de Dag, waarop de kinderen grijze haren zullen krijgen (van schrik).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,18,'En waarbij de hemel uiteen zi splijten, en Zijn belofte zal worden vervuld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,19,'Dit is zeker een vermaning. Dus moge hij die wil, de weg tot zijn Heer inslaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,73,20,'Waarlijk uw Heer weet dat gij bijna twee-derde van de nacht staat (te bidden), somsdehelft of ook wel een derde er van, en eveneens doet dit een deel van degenen die met u zijn. En Allah bepaalt de maat van dag en nacht. Hij weet, dat gij het niet kunt volhouden, en daarom heeft Hij Zich in barmhartigheid tot u gewend. Zegt dan zoveel van de Koran op als u gemakkelijk valt. Hij weet dat er enigen onder u ziek kunnen zijn, en anderen op reis door het land trekken, zoekende naar Allah´s genade, en weer anderen strijdend voor Allah´s zaak. Zegt er dus zoveel van (de Koran) op, als u gemakkelijk valt en onderhoudt het gebed, en betaalt de Zakaat, en sluit met Allah een goede lening. En wat goeds gij voor u uitzendt, gij zult betere en grotere beloning bij Allah vinden. En zoekt vergiffenis van Allah, voorwaar, Allah is Vergevensgezind, Genadevol.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,1,'O gij die u omhult!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,2,'Sta op en waarschuw,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,3,'En verkondig de Grootheid van uw Heer,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,4,'En reinig uw hart.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,5,'En vlied de onreinheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,6,'Bewijs geen gunsten om u daardoor te verrijken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,7,'En wees geduldig ter wille van uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,8,'Want als de bazuin wordt geblazen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,9,'Die Dag zal een moeilijke dag zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,10,'Niet gemakkelijk voor de ongelovigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,11,'Laat Mij alleen met hem die Ik schiep.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,12,'Ik heb hem overvloedig bezit gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,13,'En zonen die bij hem zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,14,'En ik verschafte hem elk gemak.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,15,'Toch verlangt hij dat Ik hem nog meer zal geven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,16,'Stellig niet; want hij was vijandig tegenover Onze boodschappen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,17,'Hem zal Ik een zware straf opleggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,18,'Ziet! Hij dacht na en hij besloot!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,19,'Vervloekt zij hij, hoe besloot hij!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,20,'Nogmaals, vervloekt zij hij! Hoe be sloot hij!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,21,'Toen keek hij (om zich heen),');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,22,'Daarna fronste hij zijn voorhoofd en keek nors.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,23,'Dan keerde hij zich om en toonde zich hovaardig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,24,'Hij zeide: \"Dit is niets dan een nagebootste tovenarij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,25,'Dit is slechts het woord van een mens.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,26,'Weldra zal Ik hem in het Vuur werpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,27,'En wat weet gij wat het Vuur der hel is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,28,'Het ontziet niets, noch laat het iets (onverteerd) achter,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,29,'Het verschroeit het gezicht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,30,'Daarover waken er negentien (engelen).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,31,'En Wij hebben niets dan engelen tot wachters van het Vuur gemaakt. En Wij hebben hun getal niet vastgesteld, dan tot beproeving der ongelovigen, opdat wie het Boek is gegeven zekerheid mogen verkrijgen en dat de gelovigen in geloof mogen toenemen en opdat de mensen van het Boek en de gelovigen niet zullen twijfelen. En dat degenen in wier hart een ziekte is en degenen die ongelovig zijn, mogen zeggen: \"Wat bedoelt Allah met deze gelijkenis?\" Zo laat Allah dwalen wie Hij wil en leidt wie Hij wil. Niemand kent de legerscharen van uw Heer dan Hij. Dit is niets dan een vermaning voor de mensheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,32,'Neen, bij de maan,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,33,'En de nacht als zij heengaat');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,34,'En de dageraad wanneer zij gloort,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,35,'Waarlijk, het is een der grootste tijdingen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,36,'Een waarschuwing voor de mensen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,37,'Aan degene onder u, die vooruit wenst te gaan of degene die wil achterblijven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,38,'Elke ziel is als een pand voor hetgeen zij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,39,'Doch degenen aan de rechter hand');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,40,'In tuinen (wonende) vragen zij:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,41,'Aan de schuldigen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,42,'\"Wat heeft u in de hel gebracht?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,43,'Zij zullen antwoorden: \"Wij behoorden niet tot hen die plachten te bidden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,44,'Noch voedden wij de armen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,45,'En wij plachten ijdele gesprekken te voeren met hen die ijdele gesprekken voerden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,46,'En wij plachten de Dag des Oordeels te loochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,47,'Totdat de dood ons overviel.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,48,'De tussenkomst van bemiddelaars zal hen daarom niets baten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,49,'Wat scheelt hun dat zij zich van de vermaning afwenden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,50,'Als bange ezels,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,51,'Vluchtende voor een leeuw?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,52,'Neen, ieder van hen wenst dat hem opengeslagen bladzijden zullen worden getoond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,53,'Voorwaar, zij vrezen het Hiernamaals niet!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,54,'Neen, waarlijk, dit is een vermaning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,55,'Die wil, trekke er lering uit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,74,56,'Doch zij zullen er geen lering uit trekken tenzij Allah het wil. Hij is Waardig, dat men Hem vreest, en Hij is de Heer der vergiffenis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,1,'Neen! Ik roep de Dag der Opstanding tot getuige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,2,'Neen! Ik roep de zichzelf beschuldigende ziel tot getuige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,3,'Denkt de mens dat Wij zijn beenderen niet kunnen verzamelen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,4,'Zeker; Wij hebben de macht hem te herstellen tot in zijn vingertoppen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,5,'Maar de mens wenst in ´t vervolg slecht te handelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,6,'Hij vraagt: \"Wanneer is de Dag der Opstanding?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,7,'Maar als het oog verblind wordt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,8,'En de maan verduisterd zal zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,9,'En de zon en de maan zullen samen gebracht worden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,10,'Op die Dag zal de mens zeggen: \"Waarheen te vluchten?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,11,'Neen! Geen schuilplaats!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,12,'Slechts bij uw Heer zal dan uw toevlucht zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,13,'De mens zal op die Dag worden onderricht over hetgeen hij vooruitzond of achterliet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,14,'Neen, de mens is een bewijs tegen zichzelf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,15,'Zelfs al biedt hij (zijn) verontschuldigingen aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,16,'Beweeg uw tong er niet mede om deze (woorden) haastig (opte nemen!)');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,17,'Het verzamelen en het verkondigen er van rust op Ons.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,18,'Wanneer Wij dus (de Openbaring) verkondigd hebben volg dan de verkondiging.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,19,'Daarna rust de verklaring er van op Ons.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,20,'Neen, maar gij (mensen) hebt dit leven lief.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,21,'En gij geeft het Hiernamaals prijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,22,'Op die Dag zullen sommige gezichten verlicht zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,23,'Opziende naar hun Heer;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,24,'En andere gezichten zullen op die Dag somber zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,25,'Wetende dat een vreselijke ramp hen spoedig zal overkomen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,26,'Ja! Als de ziel van de stervende tot de keel zal opstijgen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,27,'En er zal worden gezegd: \"Wie is de geneesheer?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,28,'Dan weet hij dat hij scheiden moet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,29,'En wrijft (in doodsangst) het ene been tegen het andere.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,30,'Dan wordt (hij) tot uw Heer gedreven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,31,'Want hij (mens) nam de Waarheid niet aan, noch bad hij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,32,'Doch hij verloochende (de profeet) en wendde zich af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,33,'Dan ging hij trots naar zijn familie terug.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,34,'\"Wee u! Wee dus over u.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,35,'\"Wee u nogmaals en nog eens wee!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,36,'Denkt de mens dat hij zonder doel zal worden gelaten?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,37,'Was hij niet een kleine levenskiem die werd uitgestort?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,38,'Dan werd hij een klonter bloed daarna schiep en vervolmaakte Hij hem.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,39,'Daarvan (de kiem) maakt Hij een paar, man en vrouw.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,75,40,'Is Hij dan niet bij machte de doden te doen herleven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,1,'Voorzeker, er is voor de mens een tijdperk geweest toen hij geen vermeldenswaardig ding was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,2,'Wij hebben de mens uit een gemengde levenskiem geschapen en hebben hem horende en ziende gemaakt om hem op de proef te stellen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,3,'Wij hebben hem de weg getoond, hij moge dankbaar of wel ondankbaar zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,4,'Voorwaar, Wij hebben voor de ongelovigen ketenen, ijzeren halsbanden en een laaiend Vuur bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,5,'Maar de deugdzamen drinken uit een beker (een drank) gemengd met Kamfer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,6,'De dienaren van Allah drinken uit een bron, welke zij in overvloed doen stromen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,7,'Zij vervullen de gelofte, en vrezen een Dag waarvan het kwaad verstrekkend is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,8,'En zij geven voedsel, uit liefde voor Hem, aan de armen, de wees en de gevangenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,9,'(Zeggende): \"Wij voeden u slechts ter wille van Allah. Wij verlangen geen beloning noch dank van u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,10,'Wij vrezen van onze Heer een moeilijke en drukkende Dag.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,11,'Daarom zal Allah hen voor het kwade van die Dag beschermen en zal hun blijdschap en geluk schenken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,12,'En Hij zal hen voor hun standvastigheid belonen met een tuin en kleren van zijde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,13,'Zich daarin nedervlijende op sofa´s zullen zij het noch te koud noch te warm hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,14,'En de schaduw der bomen zal dicht over hen zijn en de trossen fruit zullen gemakkelijk bereikbaar worden gemaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,15,'En zilveren vaten zullen aan hen worden rondgereikt, en bekers');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,16,'Kristalhelder, uit zilver, in de juiste maat vervaardigd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,17,'En daarin zal hun een drank worden gegeven, vermengd met Gember.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,18,'Van een bron genaamd: Salsabiel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,19,'En jonge mensen, die niet verouderen, zullen om hen rondgaan (om hen te bedienen). Wanneer gij hen ziet, denkt gij dat zij verstrooide paarlen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,20,'En waarheen gij ook kijkt, zult gij een zaligheid voelen en een groot koninkrijk aanschouwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,21,'Zij zullen klederen van fijne groene zijde en zwaar brocaat dragen en zilveren armbanden. En hun Heer zal hun een zuivere drank geven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,22,'(Hij zal zeggen): \"Dit is uw loon, omdat uw streven waardevol was.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,23,'Voorwaar, Wij hebben de Koran aan u bij gedeelten geopenbaard.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,24,'Wees daarom geduldig volgens het gebod van uw Heer en gehoorzaam niemand die onder hen zondig of ongelovig is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,25,'En gedenk de naam van uw Heer ´s morgens en ´s avonds.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,26,'En aanbid Hem gedurende (een deel) van de nacht en prijs Zijn eer gedurende een groot deel ervan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,27,'Waarlijk, de ongelovigen houden van de voorbijgaande wereld en denken niet aan de zware Dag (des Oordeels).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,28,'Wij zijn het Die hen geschapen hebben en hun lichaamsbouw hebben gesterkt. En indien Wij willen, kunnen Wij hen door gelijksoortige schepselen vervangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,29,'Voorwaar, dit is een vermaning. Wie het daarom wenst, kieze een weg die tot zijn Heer leidt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,30,'En gij zult niets anders willen dan hetgeen Allah wil. Voorwaar, Allah is Alwetend, Alwijs.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,76,31,'Hij laat tot Zijn barmhartigheid ingaan wie Hij wil, en voor de onrechtvaardigen heeft Hij een pijnlijke straf bereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,1,'Bij de met goedheid gezondenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,2,'En bij hen die verbrijzelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,3,'En bij hen, die heinde en ver verspreiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,4,'En bij hen die goed onderscheiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,5,'En bij hen die de vermaning toedienen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,6,'Om tot verontschuldiging te brengen en te waarschuwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,7,'Voorwaar, hetgeen u is beloofd moet gebeuren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,8,'Dus, als de sterren verduisterd zullen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,9,'En als de hemelen geopend zullen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,10,'En als de bergen verstrooid zullen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,11,'En als de gezanten verzameld zullen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,12,'Tot welke Dag is dit einde uitgesteld?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,13,'Tot de Dag der beslissing.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,14,'En wat weet gij ervan wat de Dag der beslissing is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,15,'Wee op die Dag, degenen die loochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,16,'Hebben Wij de vroegere (ongelovigen) niet vernietigd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,17,'Wij zullen daarom die van latere tijden hen doen volgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,18,'Zo behandelen Wij de schuldigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,19,'Wee op die Dag degenen die loochenen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,20,'Schiepen Wij u niet uit een kleine levenskiem');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,21,'Die Wij op een veilige plaats bewaarden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,22,'Voor een bepaalde tijd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,23,'Zo hebben Wij bepaald. Hoe voortreffelijk zijn Wij in het bepalen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,24,'Wee op die Dag degenen die loochenen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,25,'Hebben Wij de aarde niet gemaakt om');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,26,'De levenden en de doden te kunnen bevatten?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,27,'En hebben Wij er geen hoge bergen op geplaatst en u zoet (zuiver) watergegeven om te drinken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,28,'Wee op die Dag degenen die loochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,29,'Men zal zeggen: \"Gaat naar (de straf) welke gij loochendet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,30,'Begeeft u tot een schaduw van drie takken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,31,'Die geen koelte geeft, noch beschermt tegen de vlam.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,32,'Ziet! Het (Vuur der hel) gooit vonken op als kastelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,33,'Alsof zij kamelen van een gele kleur waren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,34,'Wee op die Dag degenen die loochenen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,35,'Dit is een Dag waarop zij (de schuldigen) niet mogen spreken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,36,'Noch zal hun worden toegestaan verontschuldigingen aan te bieden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,37,'Wee op die Dag degenen die loochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,38,'Dit is de Dag der beslissing; Wij hebben u en degenen die vroeger leefden bijeengebracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,39,'Indien gij nu enig plan hebt gebruikt het dan tegen Mij.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,40,'Wee op die Dag degenen die loochenen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,41,'De godvruchtigen zullen te midden van schaduwen en bronnen wonen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,42,'En fruit ontvangen, zoals zij zich mogen wensen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,43,'(Men zal zeggen): \"Eet en drinkt met smaak als beloning voor hetgeen gij placht te doen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,44,'Voorwaar, zo belonen Wij degenen die goed doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,45,'Wee op die Dag degenen die loochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,46,'\"Eet en vermaakt u een poosje (in dit leven). Voorzeker, gij zijt de schuldigen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,47,'Wee op die Dag degenen die loochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,48,'En als er tot hen wordt gezegd: \"Buigt u neder!\" dan buigen zij zich niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,49,'Wee op die Dag degenen die loochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,77,50,'In welk woord buiten dit zullen zij dan geloven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,1,'Waarover vragen zij?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,2,'Over de grote aankondiging,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,3,'Waaromtrent zij (van mening) verschillen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,4,'Waarlijk, zij zullen het spoedig te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,5,'Nogmaals zij zullen het weldra te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,6,'Hebben Wij de aarde niet als een bed gespreid?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,7,'En de bergen als palen opgezet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,8,'En hebben Wij u niet in paren geschapen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,9,'En hebben Wij uw slaap niet tot rusten bestemd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,10,'En hebben Wij de nacht niet als een mantel gemaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,11,'En hebben Wij de dag niet voor (het zoeken) naar levensonderhoud gemaakt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,12,'En hebben Wij niet zeven sterke (hemelen) boven u gebouwd;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,13,'En daarin een stralende lamp geplaatst?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,14,'En zenden Wij niet vanuit de wolken regen neder die voortstroomt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,15,'Opdat Wij daardoor graan en plantengroei voortbrengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,16,'En weelderige tuinen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,17,'Voorzeker, de Dag der beslissing is bepaald;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,18,'De Dag waarop de bazuin wordt geblazen; dan zult gij in scharen komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,19,'En de hemel wordt geopend en zal vele poorten hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,20,'En de bergen verdwijnen en worden tot een luchtspiegeling.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,21,'Voorzeker de hel ligt in een hinderlaag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,22,'Een tehuis voor de opstandigen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,23,'Die daarin lange tijd zullen vertoeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,24,'Zij zullen daar geen koelte hebben en geen dronk smaken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,25,'Behalve kokend water en een stinkende vloeistof die verschrikkelijk koud is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,26,'Een passende vergelding (voor hun daden).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,27,'Zij verwachtten geen rekening.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,28,'En verwierpen Onze tekenen geheel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,29,'En Wij hebben alles in een boek neergeschreven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,30,'Smaakt dus de straf! Wij zullen u slechts hierin doen toenemen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,31,'Voorwaar, er is triomf voor de rechtvaardigen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,32,'Beschutte tuinen en wijnbergen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,33,'En jeugdige gezellen, gelijk in leeftijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,34,'En een gevulde beker.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,35,'Zij horen daar geen ijdele gesprekken noch leugens!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,36,'Een beloning van uw Heer, een toereikende gave,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,37,'Van de Heer der hemelen en der aarde en van alles wat daar tussen is, de Barmhartige. Niemand zal Hem kunnen aanspreken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,38,'De Dag waarop de Geest en de (andere) engelen in gelederen opgesteld staan, zullen zij niet spreken, met uitzondering van hem aan wie de Barmhartige het toestaat en die alleen zal spreken wat recht is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,39,'Die Dag is de werkelijkheid. Daarom, laat hij die het wil een toevlucht bij zijn Heer zoeken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,78,40,'Voorwaar, Wij hebben u voor een straf die nabij is gewaarschuwd; de Dag waarop de mens zal zien wat hij heeft uitgevoerd en (waarop) de ongelovige zal zeggen: \"O, ware ik maar stof geweest!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,1,'Bij hen die zich volledig inspannen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,2,'En bij hen die hun werk met vreugde verrichten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,3,'En bij hen die snelle vorderingen maken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,4,'En bij hen die de eersten willen zijn');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,5,'En bij hen die de zaak regelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,6,'De Dag waarop de bevende (aarde) zal beven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,7,'Hierop zal volgen, wat volgen moet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,8,'Op die Dag zullen de harten kloppen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,9,'En de ogen zullen nedergeslagen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,10,'Zij (de ongelovigen) zeggen: \"Zullen wij werkelijk tot onze vroegere toestand worden teruggebracht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,11,'Zelfs al zijn wij vergane beenderen geworden?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,12,'Zij zeggen: \"Dan zou deze opstanding een ondergang zijn.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,13,'Daar is slechts ��n dreigende roep.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,14,'En ziet, zij zijn opgewekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,15,'Heeft het verhaal van Mozes u niet bereikt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,16,'Toen zijn Heer hem in het heilige dal van Towa toeriep, (zeggende):');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,17,'\"Ga naar Pharao; want hij is opstandig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,18,'En zeg tot hem: Zoudt gij u willen reinigen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,19,'En ik zal u tot uw Heer leiden opdat gij Hem moogt vrezen.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,20,'Toen toonde hij hem (Pharao) het grote teken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,21,'Maar deze verwierp het en gehoorzaamde niet;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,22,'Maar wendde zich daarna haastig af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,23,'En hij (Pharao) verzamelde de zijnen en riep uit:');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,24,'(Zeggende), \"Ik ben uw Heer de Allerhoogste.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,25,'Daarop greep Allah hem aan met een voorbeeldige straf voor de toekomst en voor die tijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,26,'Waarlijk daarin is een les voor hem die vreest.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,27,'Zijt gij moeilijker te scheppen dan de hemel die Hij heeft gebouwd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,28,'Hij verhief hem hoog en maakte hem volmaakt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,29,'En Hij maakte de nacht donker en bracht het daglicht voort;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,30,'En ook de aarde spreidde hij uit.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,31,'Daaruit bracht Hij water en weide voort.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,32,'En Hij maakte de bergen onwrikbaar.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,33,'Een voorziening voor u en voor uw vee.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,34,'Maar als de grote ramp zal komen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,35,'De Dag waarop de mens zich zal herinneren hetgeen hij heeft gedaan,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,36,'En de hel zal zichtbaar gemaakt worden voor hem die ziet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,37,'Dan zal (voor hem) die opstandig is geweest,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,38,'En die het leven dezer wereld verkoos,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,39,'Brandend Vuur zijn tehuis zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,40,'Doch voor hem die vreesde voor zijn Heer te staan, en die zijn ziel van begeerten onthield,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,41,'Zal het paradijs zeker zijn verblijf zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,42,'Zij vragen u omtrent het Uur: \"Wanneer zal het komen?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,43,'Maar datgene waarmede gij u bezighoudt');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,44,'De uitkomst daarvan is bij uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,45,'Gij zijt slechts een waarschuwer voor hem die vreest.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,79,46,'Op de dag waarop zij dit zullen zien, (zal het zijn) alsof zij slechts een avond of een morgen (op de aarde) hadden vertoefd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,1,'Hij (de profeet) fronste (zijn voorhoofd) en wendde zich af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,2,'Omdat er een blinde man tot hem kwam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,3,'(Mens) wat weet gij? Misschien wilde hij zich laten louteren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,4,'Of hij kon om raad komen, en die raad zou hem van nut kunnen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,5,'Maar aan hem, die onverschillig is');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,6,'Schenkt gij uw aandacht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,7,'Hoewel gij er niet voor aansprakelijk zijt als hij zich niet loutert.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,8,'Maar hij die zich tot u haast,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,9,'En Allah vreest,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,10,'Voor hem zijt gij onverschillig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,11,'Neen! Voorwaar, het is een vermaning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,12,'Dus, wie het wil, laat hem er lering uit trekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,13,'(Dit is) in verheven geschriften,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,14,'Hoogstaand en rein,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,15,'In de handen van schrijvers,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,16,'Edel, deugdzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,17,'Wee de mens! Hoe ondankbaar is hij!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,18,'Waaruit heeft Hij hem geschapen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,19,'Uit een kleine levenskiem schept Hij hem en stelt zijn verhoudingen vast.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,20,'Dan effent Hij de weg voor hem,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,21,'Dan doet Hij hem sterven en geeft hem aan het graf over,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,22,'Dan, wanneer Hij wil, zal Hij hem weer opwekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,23,'Neen, hij heeft hetgeen Hij hem gebood, niet volbracht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,24,'Laat nu de mens naar zijn voedsel zien;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,25,'Hoe Wij water doen neerstromen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,26,'Dan de aarde splijten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,27,'En graan daaruit doen groeien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,28,'Ook druiven en groenten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,29,'En de olijfboom en de dadelpalm.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,30,'En tuinen, dicht beplant.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,31,'En vruchten en weiden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,32,'Voorziening voor u en uw vee!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,33,'Maar als de oorverdovende roep komt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,34,'De Dag waarop een man van zijn broeder vlucht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,35,'En van zijn moeder en zijn vader,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,36,'En van zijn vrouw en zijn kinderen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,37,'Op die Dag zal een ieder een aangeiegenheid hebben die hem bezig zal houden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,38,'Op die Dag zullen sommige gezichten stralend zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,39,'Lachend, vrolijk!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,40,'En op andere gezichten zal op die Dag stof liggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,41,'Duisternis zal hen bedekken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,80,42,'Dat zijn de ongelovigen, de slechten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,1,'Wanneer de zon wordt omhuld,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,2,'En wanneer de sterren dof worden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,3,'En wanneer de bergen verdwijnen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,4,'En wanneer de drachtige kamelen worden verlaten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,5,'En wanneer de dieren worden bijeengegaard,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,6,'En wanneer de ze�en worden geledigd,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,7,'En wanneer de mensen worden verenigd,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,8,'En wanneer er over het gedode kind (verantwoording) zal worden gevraagd');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,9,'Voor welke misdaad het gedood werd,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,10,'En wanneer geschriften worden verspreid,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,11,'En wanneer de Hemel wordt opengelegd,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,12,'En wanneer de hel wordt ontstoken,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,13,'En wanneer het paradijs nabij wordt gebracht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,14,'Dan zal ieder ziel weten wat zij heeft voorbereid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,15,'En Ik roep tot getuige datgene wat terugkeert,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,16,'Zijn loop volgt en ondergaat,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,17,'En de nacht wanneer deze heengaat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,18,'En de dageraad als deze aanbreekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,19,'Dat is voorzeker de boodschap van een edele boodschapper,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,20,'Vol van macht, bevestigd door de Heer van de Troon,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,21,'Die gehoorzaamd moet worden en vertrouwenswaardig is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,22,'En uw metgezel is niet krankzinnig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,23,'En hij zag hem (Gabri�l) aan de heldere horizon.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,24,'En hij is geen vrek wat het onzienlijke aangaat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,25,'En dit is niet het woord van Satan de vervloekte.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,26,'Waarheen richt gij u dan?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,27,'Dit is niets dan een vermaning voor de werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,28,'Voor hem onder u die oprecht wil wandelen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,81,29,'En gij zult niets willen behalve wat Allah wil, de Heer der Werelden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,1,'Wanneer de hemel wordt gespleten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,2,'En wanneer de sterren verstrooid worden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,3,'En wanneer de ze�en worden geledigd,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,4,'En wanneer de graven worden geopend,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,5,'Zal iedere ziel weten wat zij heeft vooruitgezonden en wat zij achterwege heeft gelaten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,6,'O mens, wat heeft u bedrogen omtrent uw Heer, de Genadige,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,7,'Die u schiep, daarna voltooide en u de juiste verhoudingen gaf?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,8,'Hij heeft u gevormd in een vorm, die Hem behaagde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,9,'Neen, gij loochent het Oordeel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,10,'Maar voorzeker er zijn bewakers over u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,11,'Eerwaarde schrijvers,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,12,'Die weten wat gij doet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,13,'Voorwaar, de deugdzamen zijn omringd door zegeningen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,14,'En de slechten zijn omringd door de hel,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,15,'Daarin zullen zij verbranden op de Dag des Oordeels;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,16,'En zij zullen er niet aan kunnen ontsnappen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,17,'En wat weet gij er van wat de Dag des Oordeels is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,18,'Nogmaals, wat weet gij er van wat de Dag des Oordeels is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,82,19,'De Dag waarop een ziel iets vermag voor een andere ziel! Op die Dag berust het gebod alleen bij Allah.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,1,'Wee hen die anderen tekort doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,2,'Wanneer zij voor zichzelf wegen, nemen zij volle maat;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,3,'Indien zij voor anderen uitmeten of afwegen, geven zij minder (dan behoort).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,4,'Weten zulke mensen niet dat zij zullen herrijzen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,5,'Op een grote Dag,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,6,'De Dag, waarop de mensheid voor de Heer der Werelden zal staan?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,7,'Neen! Het gedenkschrift over de bozen is in Sidjdjien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,8,'En wat weet gij er van wat Sidjdjien is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,9,'Het is een geschreven boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,10,'Wee, op die Dag de loochenaars,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,11,'Die de Dag des Oordeels loochenen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,12,'En niemand behalve de zondige overtreder loochent die (Dag),');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,13,'Die zegt, als Onze woorden aan hem worden voorgedragen: \"Fabelen der ouden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,14,'Neen, maar hetgeen zij plachten te verdienen heeft zich als roest aan hun hart gehecht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,15,'Neen, zij zullen die Dag zeker van hun Heer worden uitgesloten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,16,'Voorwaar, dan zullen zij in de hel branden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,17,'En er zal tot hen worden gezegd: \"Dit is hetgeen gij placht te loochenen!\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,18,'Neen, het gedenkschrift der deugdzamen is voorzeker in \"Illijjien.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,19,'En wat weet gij er van wat\"Illijjien\" is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,20,'Een geschreven boek.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,21,'De nabij (God) zijnden zullen het zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,22,'Voorwaar, de deugdzamen onder zegeningen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,23,'Op hoge sofa´s zullen zij elkander aanschouwen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,24,'Gij zult in hun gezicht de glans der gelukzaligheid herkennen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,25,'Hun wordt zuivere verzegelde wijn te drinken gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,26,'Welks zegel muskus is. En laat degenen die wedijveren, hiervoor wedijveren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,27,'En hij zal vermengd worden met water van Tasniem;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,28,'Een bron waaruit de nabij (God) zijnden drinken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,29,'Waarlijk, de schuldigen plachten de gelovigen uit te lachen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,30,'En wanneer zij hen voorbijgingen, knipoogden zij tegen elkander.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,31,'En wanneer zij tot de hunnen terugkeerden, keerden zij opgetogen terug;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,32,'En wanneer zij hen zagen, zeiden zij: \"Dit zijn inderdaad de dwalenden.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,33,'Maar zij waren niet als bewakers over hen gezonden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,34,'Daarom zullen op deze Dag de gelovigen over de ongelovigen lachen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,35,'Op hoge sofa´s zittende zullen zij aanschouwen;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,83,36,'Voorzeker wordt de ongelovigen vergolden voor hetgeen zij plachten te doen!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,1,'Wanneer de hemel vaneen splijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,2,'En zijn Heer gehoorzaamt zoals het hem betaamt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,3,'En wanneer de aarde wordt uitgespreid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,4,'En alles zal uitwerpen wat in haar is, en leeg wordt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,5,'En gehoorzaamt aan haar Heer, zoals het haar betaamt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,6,'(Zal worden gezegd) \"O mens, gij moet ijverig naar uw Heer streven, dan zult gij Hem ontmoeten.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,7,'Wat hem betreft, wie het boek in zijn rechter hand wordt gegeven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,8,'Hij zal waarlijk een gemakkelijke rekening krijgen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,9,'En zal tot de zijnen in vreugde terugkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,10,'Maar hij, wie het boek achter zijn rug wordt gegeven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,11,'Hij zal vernietiging wensen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,12,'En een laaiend Vuur ingaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,13,'Voorzeker, hij was bij de zijnen gelukkig,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,14,'En dacht inderdaad dat hij nooit zou terugkeren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,15,'Ja! Voorzeker, zijn Heer kent hem goed.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,16,'Ja, Ik roep de avondschemering tot getuige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,17,'En de nacht en wat deze omsluiert,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,18,'En de maan als zij vol wordt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,19,'Dat gij zeker van de ene toestand naar de andere overgaat.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,20,'Maar, wat scheelt hen, dat zij niet geloven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,21,'En wanneer de Koran aan hun wordt voorgedragen, werpen zij zich niet ter aarde neer,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,22,'Integendeel, de ongelovigen loochenen (deze).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,23,'Doch Allah weet het beste wat zij denken.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,24,'Kondig hun hiervoor dus een pijnlijke straf aan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,84,25,'Maar voor de gelovigen die goede werken doen, is een oneindige beloning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,1,'Bij de hemel met zijn constellaties.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,2,'En bij de beloofde Dag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,3,'En bij de getuige en hetgeen waarover hij getuigenis aflegt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,4,'Vervloekt zijn degenen die groeven maakten -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,5,'Daarin vuur stookten -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,6,'Ziet! Zij zaten er bij,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,7,'En waren getuigen van wat zij de gelovigen aandeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,8,'En zij wreekten zich slechts op hen omdat zij in Allah geloofden, de Almachtige, de Geprezene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,9,'Aan Wie het koninkrijk der hemelen en der aarde behoort; en Allah is Getuige van alle dingen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,10,'En zij, die de gelovige mannen en vrouwen vervolgen en dan geen berouw hebben, voor hen is de straf der hel, en hen wacht de straf van het branden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,11,'Voorzeker, de gelovigen die goede werken doen, zullen tuinen hebben waardoor rivieren stromen. Dat is de grote zegepraal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,12,'Waarlijk, de greep van uw Heer is hard.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,13,'Hij is het Die schept en weder voortbrengt;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,14,'En Hij is de Vergevende, de Liefderijke;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,15,'De Heer van de Troon, de Roemrijke;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,16,'Uitvoerder van wat Hij wil.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,17,'Heeft het verhaal van de heerscharen u dan niet bereikt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,18,'Van Pharao en de Samoed?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,19,'Ja, maar de ongelovigen loochenen het.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,20,'En Allah omsingelt hen van achteraf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,21,'Voorwaar, het is een glorierijke Koran,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,85,22,'Op een beschermde tafel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,1,'Bij de hemel en bij de morgenster.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,2,'En wat weet gij (er van) wat de morgenster is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,3,'Het is een ster van doordringende helderheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,4,'Er is geen ziel waarover geen wachter is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,5,'Laat de mens derhalve overwegen waaruit hij geschapen werd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,6,'Hij werd uit een stromende vloeistof geschapen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,7,'Welke voortkomt van tussen de ruggegraat en de ribben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,8,'Voorzeker, Hij kan hem (tot het leven) terugroepen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,9,'Op de Dag waarop de geheimen zullen worden geopenbaard.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,10,'Dan zal hij geen kracht en geen helper hebben.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,11,'Bij de wolk die regen geeft.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,12,'En de aarde, die door planten splijt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,13,'Dit is zeker een beslissend woord,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,14,'Het is geen scherts.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,15,'Voorwaar zij smeden een plan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,16,'En ook Ik smeed een (machtiger) plan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,86,17,'Geef derhalve de ongelovigen voor een wijle uitstel,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,1,'Verheerlijk de Naam van uw Heer, de Allerhoogste.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,2,'Die schept en vervolmaakt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,3,'En Die bepaalt en leidt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,4,'En Die het gewas voortbrengt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,5,'En het dan doet verdorren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,6,'Wij zullen u weldra onderwijzen zodat gij het niet vergeet -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,7,'Behalve wat Allah wil - Voorwaar, Hij kent het openlijke en het verborgene.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,8,'En Wij zullen uw weg effenen tot gemak.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,9,'Maak (anderen) daarom indachtig, voorzeker dit is nuttig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,10,'Hij die vreest zal er lering uit trekken;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,11,'Maar de rampzalige zal zich ervan afwenden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,12,'Die het grote Vuur zal binnengaan,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,13,'Waarin hij noch sterven noch leven zal.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,14,'Voorzeker, geslaagd is hij die zich loutert.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,15,'En die de naam van zijn Heer gedenkt en bidt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,16,'Maar gij verkiest het leven dezer wereld,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,17,'Ofschoon het Hiernamaals beter en van langere duur is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,18,'Voorzeker, dit is in vroegere geschriften vermeld,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,87,19,'De geschriften van Abraham en Mozes.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,1,'Heeft het nieuws van de overweldigende (gebeurtenis) u bereikt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,2,'Op die Dag zullen sommige aangezichten terneergeslagen zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,3,'Zwoegend, zich afmattende,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,4,'Zij zullen in een vreselijk Vuur branden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,5,'Hun zal uit een kokende bron te drinken worden gegeven,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,6,'Zij zullen geen voedsel krijgen, behalve van doornen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,7,'Dat noch voedzaam zal zijn noch tegen de honger zal baten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,8,'Op die Dag zullen andere aangezichten verblijd zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,9,'Weltevreden met hun streven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,10,'In een verheven tuin');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,11,'Waarin zij geen ijdele (taal) zullen horen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,12,'Waarin een stromende bron is,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,13,'Waarin hoge rustbanken opgericht zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,14,'En drinkschalen gereed gezet,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,15,'En kussens gerangschikt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,16,'En tapijten uitgespreid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,17,'Zien zij niet naar de wolken, hoe zij gevormd worden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,18,'En naar de hemel, hoe deze hoog verheven werd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,19,'En naar de bergen, hoe zij opgericht werden?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,20,'En naar de aarde, hoe zij uitgespreid werd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,21,'Vermaant hen daarom want gij zijt slechts een vermaner;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,22,'Gij zijt geen waker over hen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,23,'Maar hij die zich afwendt en niet gelooft,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,24,'Allah zal hem straffen met de strengste straf.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,25,'Voorwaar, hun terugkeer is tot Ons.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,88,26,'Dan zullen Wij rekenschap van hen vragen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,1,'Bij de dageraad,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,2,'En de tien nachten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,3,'En het even en het oneven');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,4,'En de nacht als deze vervaagt;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,5,'Daarin is zeker genoeg bewijs voor een man van begrip.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,6,'Weet gij niet hoe uw Heer met de Aad handelde?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,7,'Het volk van Iram dat verheven gebouwen bezat,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,8,'Wier gelijken nog in geen enkele stad zijn voortgebracht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,9,'En met de Samoed die de rotsen in het dal uithieuwen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,10,'En met Pharao, de heer der grote scharen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,11,'Die zich in de steden aan overtreding overgaven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,12,'En veel verderf daarin aanrichtten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,13,'Daarom, deed uw Heer een roede der kastijding over hen nederdalen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,14,'Voorwaar, uw Heer is waakzaam.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,15,'Wat de mens betreft, wanneer zijn Heer hem beproeft door hem te roemen en door hem gunsten te bewijzen, dan zegt hij: \"Mijn Heer heeft mij ge�erd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,16,'Maar wanneer Hij hem beproeft door hem in zijn levensonderhoud te beperken, zegt hij: \"Mijn Heer heeft mij onteerd.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,17,'Neen, maar gij ontziet de wees niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,18,'Noch spoort elkander aan, de armen te voeden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,19,'En gij verslindt het erfdeel in zijn geheel');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,20,'En gij houdt te veel van weelde.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,21,'Neen, wanneer de aarde aan stukken wordt geschud,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,22,'En uw Heer komt en de engelen in rijen gerangschikt zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,23,'Op die Dag zal de hel (hem) worden getoond; op die Dag zal de mens de vermaning willen volgen, maar hoe zal de vermaning hem kunnen baten?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,24,'Hij zal zeggen: \"o had ik (vroeger), voor dit leven iets verricht.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,25,'Niemand straft zoals Hij op die Dag zal straffen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,26,'Noch boeit iemand zoals Hij zal boeien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,27,'Maar gij, o ziel in vrede!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,28,'Keer tot uw Heer terug, verblijd in Allah´s welbehagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,29,'Ga daarom in onder Mijn dienaren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,89,30,'En ga Mijn paradijs binnen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,1,'Ik zweer bij deze stad (Makka),');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,2,'En gij zijt vogelvrij in deze stad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,3,'En bij de vader en wat hij verwekte.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,4,'Voorwaar, Wij hebben de mens geschapen om moeilijkheden (te overwinnen).');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,5,'Denkt hij dat niemand macht over hem heeft?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,6,'Hij zegt: \"Ik heb veel rijkdommen verkwist.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,7,'Denkt hij dat niemand hem ziet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,8,'Hebben Wij hem niet twee ogen gegeven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,9,'En een tong en twee lippen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,10,'Hebben Wij hem dan niet de twee hoofdwegen getoond?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,11,'Maar hij besteeg de heuvel niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,12,'En wat weet gij (er van) wat de heuvel is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,13,'Een slaaf te bevrijden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,14,'Of, op de dag van honger iemand te voeden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,15,'Of een wees die u verwant is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,16,'Of een arme die in het stof rolt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,17,'Bovendien behoort hij (die dit doet) tot hen, die geloven en elkander aansporen tot geduld en die elkander aansporen tot barmhartigheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,18,'Dezen zullen aan de rechter hand zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,19,'Maar zij, die niet in Onze tekenen geloven zullen aan de linker hand zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,90,20,'Een gesloten Vuur zal hen omringen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,1,'Bij de zon en haar licht,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,2,'En bij de maan als zij deze volgt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,3,'En bij de dag wanneer hij dezs onthult');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,4,'En bij de nacht, wanneer hij haar bedekt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,5,'En bij de hemel en de schepping er van.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,6,'En bij de aarde en haar uitgestrektheid,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,7,'En bij de ziel en haar volmaaktheid,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,8,'Hij openbaarde haar wat slecht en wat goed (voor haar) is,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,9,'Voorwaar, geslaagd is hij die haar loutert');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,10,'En voorzeker hij gaat te gronde die haar te gronde richt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,11,'De Samoed verloochenden de boodschap in hun opstandigheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,12,'Toen de ongelukkigste onder hen opstond,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,13,'Zeide de boodschapper van Allah: \"Laat de kamelin van Allah vrij in haar drinken.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,14,'Maar zij verloochenden hem en verlamden haar, daarom vernietigde hun Heer hen volkomen om hun zonden en maakte het land met de grond gelijk.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,91,15,'En Hij vreest de gevolgen hiervan niet.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,1,'Bij de nacht als hij bedekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,2,'En bij de dag wanneer hij schittert,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,3,'En bij de schepping van man en vrouw.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,4,'Voorzeker, uw streven is verschillend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,5,'Wat hem betreft die geeft en God vreest,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,6,'En het goede aanvaardt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,7,'Wij zullen zijn weg effenen tot welslagen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,8,'Maar hij, die vrekkig en onverschillig is,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,9,'En het beste verwerpt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,10,'Wij zullen hem naar moeilijkheden leiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,11,'Wanneer hij te gronde gaat zullen zijn rijkdommen hem niet baten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,12,'Voorwaar, het is aan Ons om te leiden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,13,'En aan Ons is het Hiernamaals en ook deze wereld.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,14,'Daarom waarschuw Ik u voor het laaiend Vuur;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,15,'Niemand zal er binnengaan dan de rampzaligste,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,16,'Die loochent en zich afwendt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,17,'Maar de rechtvaardige zal ver daarvan verwijderd worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,18,'Die zijn rijkdommen weggeeft om zich te louteren.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,19,'En niemand heeft Hem een gunst bewezen waarvoor hij moet worden beloond.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,20,'Maar hij die het welbehagen zoekt van zijn Heer, de Verhevene,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,92,21,'Weldra zal hij tevreden zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,1,'Bij de glorie van de dag.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,2,'En bij de nacht als het donker is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,3,'Uw Heer heeft u niet verlaten, noch is Hij mishaagd over u.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,4,'Voorwaar, het komende uur zal beter zijn voor u dan het vorige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,5,'En voorwaar uw Heer zal u geven, en gij zult tevreden zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,6,'Vond Hij u niet als wees, en beschermde u?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,7,'En vond Hij u niet zoekende en leidde Hij u?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,8,'En vond Hij u niet in armoede en verrijkte u?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,9,'Daarom verdruk de wees niet,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,10,'En snauw de bedelaar niet af.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,93,11,'Maar maak de gunst van uw Heer bekend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,94,1,'Hebben Wij uw borst niet voor u verruimd?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,94,2,'En uw last niet van u weggenomen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,94,3,'Die uw rug bezwaarde?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,94,4,'En uw roem niet verheven?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,94,5,'Voorwaar, zo komt gemak naast ongemak.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,94,6,'Voorwaar, gemak komt naast ongemak.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,94,7,'Wanneer gij verlicht zijt, streef dan verder.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,94,8,'En wend u tot uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,95,1,'Bij de vijg en de olijf,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,95,2,'Bij de berg Sina�,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,95,3,'En bij deze stad van Vrede (Makka),');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,95,4,'Voorzeker, Wij hebben de mens in de beste vorm geschapen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,95,5,'Daarna laten Wij hem vervallen tot het allerlaagste,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,95,6,'Behalve degenen die geloven en goede werken doen; hunner is een oneindige beloning.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,95,7,'Wat is de oorzaak die u het Gericht doet loochenen?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,95,8,'Is Allah niet de Rechter aller rechters ?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,1,'Verkondig de naam van uw Heer, de Schepper.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,2,'Die de mens uit geronnen bloed schiep.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,3,'Verkondig, want uw Heer is de meest Eerbiedwaardige');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,4,'Die (de mens) door middel van de pen onderwees.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,5,'Hij leerde aan de mens datgene wat deze niet kende,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,6,'In het geheel niet. Voorwaar, de mens wordt opstandig,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,7,'Omdat hij zich onafhankelijk denkt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,8,'Voorwaar uw terugkeer is tot uw Heer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,9,'Hebt gij degelle gezien die verbiedt');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,10,'Wanneer onze dienaar bidt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,11,'Zeg mij, als hij de leiding volgt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,12,'Of tot rechtvaardigheid maant.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,13,'Zeg mij, indien hij (de Waarheid) verloochent en zich afwendt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,14,'Weet hij niet dat Allah alles ziet?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,15,'Neen, wanneer hij niet ophoudt, zullen Wij hem zeker bij de haren van zijn voorhoofd grijpen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,16,'Van dat leugenachtige en schuldige voorhoofd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,17,'Laat hij dan zijn raadgevers bij elkaar roepen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,18,'Wij zullen ook Onze wachters bijeen brengen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,96,19,'Neen, gehoorzaam hem niet, maar werp u neder en zoek Zijn nabijheid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,97,1,'Waarlijk, Wij hebben u (de Koran) nedergezonden, in de waardevolle nacht.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,97,2,'Wat weet gij (er van) wat de waardevolle nacht is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,97,3,'De waardevolle nacht is beter dan duizend maanden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,97,4,'Daarin dalen engelen en de Geest door Gods gebod neder (zeggende)');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,97,5,'\"In alles Vrede,\" tot het rijzen van de dageraad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,98,1,'De ongelovigen onder de mensen van het Boek en onder de afgodendienaren konden niet worden bevrijd, v��rdat een duidelijk bewijs tot hen gekomen was,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,98,2,'Een boodschapper van Allah, die aan hen de zuivere bladzijden voordroeg.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,98,3,'Waarin alle geschriften verzameld zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,98,4,'En de mensen van het Boek werden eerst onenig, nadat het duidelijke teken tot hen gekomen was.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,98,5,'En daarin werd hun slechts geboden Allah te aanbidden, oprecht zijnde in gehoorzaamheid jegens Hem, oprecht het gebed te onderhouden en de Zakaat te betalen. Dat is de ware godsdienst.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,98,6,'Voorwaar, de ongelovigen onder de mensen van het Boek en de afgodendienaren zullen in het Vuur der hel geworpen worden, daarin zullen zij verblijven. Zij zijn de slechtste der schepselen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,98,7,'Doch zij die geloven en goede werken doen, zij zijn de beste der schepselen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,98,8,'Hun beloning is bij hun Heer; tuinen der eeuwigheid waardoor rivieren stromen en waarin zij voor altijd zullen vertoeven. Allah zal welbehagen in hen hebben en zij zullen welbehagen in Hem hebben. Dit is voor hem, die zijn Heer vreest.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,99,1,'Wanneer de aarde hevig zal worden geschud,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,99,2,'En zij haar binnenste naar buiten zal keren,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,99,3,'En de mens zal zeggen: \"Wat is er met haar gebeurd?\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,99,4,'Op die Dag zal de aarde haar geschiedenis mededelen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,99,5,'Omdat uw Heer het haar heeft geopenbaard.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,99,6,'Op die Dag zullen de mensen in verschillende groepen te voorschijn komen opdat hun hun werken getoond zullen worden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,99,7,'Wie ter grootte van een atoom goed deed, zal dit aanschouwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,99,8,'En wie ter grootte van een atoom kwaad deed, zal ook dat aanschouwen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,1,'Bij de rossen die snel en snuivend ademen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,2,'Die vonken uit de hoeven slaan,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,3,'En bij de dageraad plotseling een aan val doen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,4,'Daarbij stof opwerpen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,5,'En zo door het midden der vijandelijke menigte zich een weg banen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,6,'Voorwaar, de mens is ondankbaar jegens zijn Heer;');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,7,'En waarlijk, hij is daar zelf getuige van.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,8,'En voorzeker, hij heeft een hevige begeerte naar rijkdommen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,9,'Weet zo iemand dan niet, dat hetgeen in de graven is weder zal worden opgewekt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,10,'En dat het innerlijk zal worden bekend gemaakt?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,100,11,'Dat hun Heer hen op die Dag volkomen kent?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,1,'De ramp.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,2,'Wat is de ramp?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,3,'En wat weet gij (er van) wat de ramp is?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,4,'Een Dag waarop de mensen als motten verstrooid zullen zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,5,'En de bergen als gekaarde wol');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,6,'Dan zal hij, wiens schalen zwaar zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,7,'Een aangenaam leven genieten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,8,'Doch hij, wiens schalen licht zijn,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,9,'Zijn toevlucht zal Hawi´jah zijn.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,10,'En gij weet niet, wat dit is.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,101,11,'Het is een laaiend Vuur.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,102,1,'Jacht naar vermeerdering van rijkdom (en kinderen) maakt u onachtzaam,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,102,2,'Totdat gij in uw graven nederdaalt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,102,3,'Neen - gij zult weldra te weten komen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,102,4,'Nogmaals neen! Gij zult weldra te weten komen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,102,5,'Waarlijk, indien gij de zekerheid van kennis bezit -');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,102,6,'Zult gij zeker de hel zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,102,7,'Ja, dan zult gij haar met zekerheid van blik zien.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,102,8,'Op die Dag zult gij worden ondervraagd over de gaven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,103,1,'Bij de tijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,103,2,'Voorzeker, de mens is te midden van verlies.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,103,3,'Behalve degenen die geloven en goede werken doen, en elkander tot waarheid, en geduld aansporen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,104,1,'Wee iedere leugenaar en lasteraar!');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,104,2,'Die rijkdommen verzamelt en deze telt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,104,3,'Denkende dat zijn schatten hem voor eeuwig zullen behouden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,104,4,'Neen, hij zal zeker in het Verterende Vuur worden geworpen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,104,5,'En wat weet gij er van wat het verterende Vuur betekent?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,104,6,'Het is het Vuur dat Allah heeft aan gewakkerd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,104,7,'Dat boven de harten zal opstijgen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,104,8,'Voorwaar het zal hen omsluiten');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,104,9,'In uitgestrekte rijen van zuilen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,105,1,'Hebt gij niet vernomen, hoe uw Heer de bezitters der olifanten behandelde?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,105,2,'Heeft Hij hun plannen niet teniet gedaan?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,105,3,'Zond Hij geen zwermen vogels op hen neer?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,105,4,'En wierpen deze geen klompen klei?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,105,5,'Dat hen maakte als fijn gekauwd (door het vee) stro?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,106,1,'Ter bescherming van de Qoraishieten,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,106,2,'Ter bescherming op hun zomer- en winterreis.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,106,3,'Laten zij derhalve de Heer van dit Huis aanbidden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,106,4,'Die hen van voedsel tegen honger heeft voorzien en van vrees bevrijd.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,107,1,'Hebt gij hem gezien die deze godsdienst loochent?');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,107,2,'Het is degene die de wees verstoot,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,107,3,'Hij wekt anderen niet op de armen te voeden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,107,4,'En wee degenen die bidden,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,107,5,'En de gebeden achteloos opzeggen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,107,6,'En zij, die er mee te koop lopen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,107,7,'En zich er van weerhouden de behoeftige vriendelijkheid te betonen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,108,1,'Voorwaar, Wij hebben u in overvloed het goede gegeven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,108,2,'Bid daarom tot uw Heer en offer.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,108,3,'Voorzeker, uw vijand zal uitsterven.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,109,1,'Zeg: \"O gij ongelovigen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,109,2,'Ik bid niet aan, wat gij aanbidt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,109,3,'Noch gij bidt aan, wat ik aanbid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,109,4,'Noch wil ik aanbidden, wat gij aanbidt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,109,5,'Nogmaals gij wilt niet aanbidden wat ik aanbid.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,109,6,'Derhalve voor u uw godsdienst en voor mij mijn godsdienst.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,110,1,'Als de hulp van Allah en overwinning komt,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,110,2,'En gij de mensen groepsgewijze ziet binnentreden tot Allah´s godsdienst,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,110,3,'Roem dan uw Heer met de lof, die Hem toekomt en vraag vergiffenis van Hem; voorzeker Hij is Berouwaanvaardend.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,111,1,'De macht van Aboe Lahab en hijzelf zullen vergaan.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,111,2,'Zijn rijkdommen en daden zullen hem niet baten.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,111,3,'Weldra zal hij in een laaiend Vuur branden.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,111,4,'Ook zijn vrouw, de draagster van brandstof,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,111,5,'Om haar hals zal een koord van palmvezels hangen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,112,1,'Zeg: \"Allah is de Enige.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,112,2,'Allah is zichzelf-genoeg, Eeuwig.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,112,3,'Hij verwekte niet, noch werd Hij verwekt.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,112,4,'En niemand is Hem in enig opzicht gelijk.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,113,1,'Zeg: \"Ik zoek mijn toevlucht bij de Heer van de dageraad.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,113,2,'Tegen het kwade van wat Hij heeft geschapen');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,113,3,'En tegen het kwade van de duisternis wanneer deze zich verspreidt');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,113,4,'En tegen het kwade van degenen die vaste banden door boze inblazingen willen ontbinden');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,113,5,'En van het kwade van de benijder wanneer deze benijdt.\"');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,114,1,'Zeg: \"Ik zoek mijn toevlucht bij de Heer der mensen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,114,2,'De Koning der mensen,');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,114,3,'De God der mensen.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,114,4,'Opdat Hij mij bevrijde van het kwade der inblazingen van de duivel.');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,114,5,'Die in het hart der mensen fluistert');
INSERT INTO `dutch` (`qdb`,`sura`,`verse`,`text`) VALUES (15,114,6,'Vanuit het midden der djinn en mensen.\"');
