INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,1,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,1,1,1,N´In the Name of God, the Merciful, the Compassionate´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,1,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,1,2,2,N´Praise belongs to God, the Lord of all Being,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,1,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,1,3,3,N´the All-merciful, the All-compassionate,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,1,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,1,4,4,N´the Master of the Day of Doom.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,1,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,1,5,5,N´Thee only we serve; to Thee alone we pray for succour.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,1,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,1,6,6,N´Guide us in the straight path,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,1,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,1,7,7,N´the path of those whom Thou hast blessed, not of those against whom Thou art wrathful, nor of those who are astray.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,1,1,N´Alif Lam Mim´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,2,2,N´That is the Book, wherein is no doubt, a guidance to the godfearing´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,3,3,N´who believe in the Unseen, and perform the prayer, and expend of that We have provided them;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,4,4,N´who believe in what has been sent down to thee and what has been sent down before thee, and have faith in the Hereafter;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,5,5,N´those are upon guidance from their Lord, those are the ones who prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,6,6,N´As for the unbelievers, alike it is to them whether thou hast warned them or hast not warned them, they do not believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,7,7,N´God has set a seal on their hearts and on their hearing, and on their eyes is a covering, and there awaits them a mighty chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,8,8,N´And some men there are who say, ´We believe in God and the Last Day´; but they are not believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,9,9,N´They would trick God and the believers, and only themselves they deceive, and they are not aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,10,10,N´In their hearts is a sickness, and God has increased their sickness, and there awaits them a painful chastisement for that they have cried lies.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,11,11,N´When it is said to them, ´Do not corruption in the land´, they say, ´We are only ones that put things right.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,12,12,N´Truly, they are the workers of corruption but they are not aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,13,13,N´When it is said to them, ´Believe as the people believe´, they say, ´Shall we believe, as fools believe?´ Truly, they are the foolish ones, but they do not know.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,14,14,N´When they meet those who believe, they say, ´We believe´; but when they go privily to their Satans, they say, ´We are with you; we were only mocking.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,15,15,N´God shall mock them, and shall lead them on blindly wandering in their insolence.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,16,16,N´Those are they that have bought error at the price of guidance, and their commerce has not profited them, and they are not right-guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,17,17,N´The likeness of them is as the likeness of a man who kindled a fire, and when it lit all about him God took away their light, and left them in darkness unseeing´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,18,18,N´deaf, dumb, blind -- so they shall not return;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,19,19,N´or as a cloudburst out of heaven in which is darkness, and thunder, and lightning -- they put their fingers in their ears against the thunderclaps, fearful of death; and God encompasses the unbelievers;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,20,20,N´the lightning wellnigh snatches away their sight; whensoever it gives them light, they walk in it, and when the darkness is over them, they halt; had God willed, He would have taken away their hearing and their sight. Truly, God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,21,21,N´O you men, serve your Lord Who created you, and those that were before you; haply so you will be godfearing;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,22,22,N´who assigned to you the earth for a couch, and heaven for an edifice, and sent down out of heaven water, wherewith He brought forth fruits for your provision; so set not up compeers to God wittingly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,23,23,N´And if you are in doubt concerning that We have sent down on Our servant, then bring a sura like it, and call your witnesses, apart from God, if you are truthful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,24,24,N´And if you do not -- and you will not -- then fear the Fire, whose fuel is men and stones, prepared for unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,25,25,N´Give thou good tidings to those who believe and do deeds of righteousness, that for them await gardens underneath which rivers flow; whensoever they are provided with fruits therefrom they shall say, ´This is that wherewithal we were provided before´; that they shall be given in perfect semblance; and there for them shall be spouses purified; therein they shall dwell forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,26,26,N´God is not ashamed to strike a similitude even of a gnat, or aught above it. As for the believers, they know it is the truth from their Lord; but as for unbelievers, they say, ´What did God desire by this for a similitude?´ Thereby He leads many astray, and thereby He guides many; and thereby He leads none astray save the ungodly´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,27,27,N´such as break the covenant of God after its solemn binding, and such as cut what God has commanded should be joined, and such as do corruption in the land -- they shall be the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,28,28,N´How do you disbelieve in God, seeing you were dead and He gave you life, then He shall make you dead, then He shall give you life, then unto Him you shall be returned?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,29,29,N´It is He who created for you all that is in the earth, then He lifted Himself to heaven and levelled them seven heavens; and He has knowledge of everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,30,30,N´And when thy Lord said to the angels, ´I am setting in the earth a viceroy.´ They said, ´What, wilt Thou set therein one who will do corruption there, and shed blood, while We proclaim Thy praise and call Thee Holy?´ He said, ´Assuredly I know that you know not.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,31,31,N´And He taught Adam the names, all of them; then He presented them unto the angels and said, ´Now tell Me the names of these, if you speak truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,32,32,N´They said, ´Glory be to Thee! We know not save what Thou hast taught us. Surely Thou art the All-knowing, the All-wise.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,33,33,N´He said, ´Adam, tell them their names.´ And when he had told them their names He said, ´Did I not tell you I know the unseen things of the heavens and earth? And I know what things you reveal, and what you were hiding.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,34,34,N´And when We said to the angels, ´Bow yourselves to Adam´; so they bowed themselves, save Iblis; he refused, and waxed proud, and so he became one of the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,35,35,N´And We said, ´Adam, dwell thou, and thy wife, in the Garden, and eat thereof easefully where you desire; but draw not nigh this tree, lest you be evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,36,36,N´Then Satan caused them to slip therefrom and brought them out of that they were in; and We said, ´Get you all down, each of you an enemy of each; and in the earth a sojourn shall be yours, and enjoyment for a time.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,37,37,N´Thereafter Adam received certain words from his Lord, and He turned towards him; truly He turns, and is All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,38,38,N´We said, ´Get you down out of it, all together; yet there shall come to you guidance from Me, and whosoever follows My guidance, no fear shall be on them, neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,39,39,N´As for the unbelievers who cry lies to Our signs, those shall be the inhabitants of the Fire, therein dwelling forever.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,40,40,N´Children of Israel, remember My blessing wherewith I blessed you, and fulfil My covenant and I shall fulfil your covenant; and have awe of Me.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,41,41,N´And believe in that I have sent down, confirming that which is with you, and be not the first to disbelieve in it. And sell not My signs for a little price; and fear you Me.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,42,42,N´And do not confound the truth with vanity, and do not conceal the truth wittingly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,43,43,N´And perform the prayer, and pay the alms, and bow with those that bow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,44,44,N´Will you bid others to piety, and forget yourselves while you recite the Book? Do you not understand?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,45,45,N´Seek you help in patience and prayer, for grievous it is, save to the humble´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,46,46,N´who reckon that they shall meet their Lord and that unto Him they are returning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,47,47,N´Children of Israel, remember My blessing wherewith I blessed you, and that I have preferred you above all beings;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,48,48,N´and beware of a day when no soul for another shall give satisfaction, and no intercession shall be accepted from it, nor any counterpoise be taken, neither shall they be helped.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,49,49,N´And when We delivered you from the folk of Pharaoh who were visiting you with evil chastisement, slaughtering your sons, and sparing your women; and in that was a grievous trial from your Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,50,50,N´And when We divided for you the sea and delivered you, and drowned Pharaoh´s folk while you were beholding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,51,51,N´And when We appointed with Moses forty nights then you took to yourselves the Calf after him and you were evildoers;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,52,52,N´then We pardoned you after that, that haply you should be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,53,53,N´And when We gave to Moses the Book and the Salvation, that haply you should be guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,54,54,N´And when Moses said to his people, ´My people, you have done wrong against yourselves by your taking the Calf; now turn to your Creator and slay one another. That will be better for you in your Creator´s sight, and He will turn to you; truly He turns, and is All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,55,55,N´And when you said, ´Moses, we will not believe thee till we see God openly´; and the thunderbolt took you while you were beholding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,56,56,N´Then We raised you up after you were dead, that haply you should be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,57,57,N´And We outspread the cloud to overshadow you, and We sent down manna and quails upon you: ´Eat of the good things wherewith We have provided you. And they worked no wrong upon Us, but themselves they wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,58,58,N´And when We said, ´Enter this township, and eat easefully of it wherever you will, and enter in at the gate, prostrating, and say, Unburdening; We will forgive you your transgressions, and increase the good-doers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,59,59,N´Then the evildoers substituted a saying other than that which had been said to them; so We sent down upon the evildoers wrath out of heaven for their ungodliness.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,60,60,N´And when Moses sought water for his people, so We said, ´Strike with thy staff the rock´; and there gushed forth from it twelve fountains; all the people knew now their drinking-place. ´Eat and drink of God´s providing, and mischief not in the earth, doing corruption.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,61,61,N´And when you said, ´Moses, we will not endure one sort of food; pray to thy Lord for us, that He may bring forth for us of that the earth produces - green herbs, cucumbers, corn, lentils, onions.´ He said, ´Would you have in exchange what is meaner for what is better? Get you down to Egypt; you shall have there that you demanded.´ And abasement and poverty were pitched upon them, and they were laden with the burden of God´s anger; that, because they had disbelieved the signs of God and slain the Prophets unrightfully; that, because they disobeyed, and were transgressors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,62,62,N´Surely they that believe, and those of Jewry, and the Christians, and those Sabaeans, whoso believes in God and the Last Day, and works righteousness -- their wage awaits them with their Lord, and no fear shall be on them; neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,63,63,N´And when We took compact with you, and raised above you the Mount: ´Take forcefully what We have given you, and remember what is in it; haply you shall be godfearing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,64,64,N´Then you turned away thereafter, and but for the bounty and mercy of God towards you, you had been of the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,65,65,N´And well you know there were those among you that transgressed the Sabbath, and We said to them, ´Be you apes, miserably slinking!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,66,66,N´And We made it a punishment exemplary for all the former times and for the latter, and an admonition to such as are godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,67,67,N´And when Moses said to his people, ´God commands you to sacrifice a cow.´ They said, ´Dost thou take us in mockery?´ He said, ´I take refuge with God, lest I should be one of the ignorant.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,68,68,N´They said, ´Pray to thy Lord for us, that He may make clear to us what she may be.´ He said, ´He says she is a cow neither old, nor virgin, middling between the two; so do that you are bidden.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,69,69,N´They said, ´Pray to thy Lord for us, that He make clear to us what her colour may be.´ He said, ´He says she shall be a golden cow, bright her colour, gladdening the beholders.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,70,70,N´They said, ´Pray to thy Lord for us, that He make clear to us what she may be; cows are much alike to us; and, if God will, we shall then be guided.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,71,71,N´He said, ´He says she shall be a cow not broken to plough the earth or to water the tillage, one kept secure, with no blemish on her.´ They said, ´Now thou hast brought the truth´; and therefore they sacrificed her, a thing they had scarcely done.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,72,72,N´And when you killed a living soul, and disputed thereon -- and God disclosed what you were hiding --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,73,73,N´so We said, ´Smite him with part of it´; even so God brings to life the dead, and He shows you His signs, that haply you may have understanding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,74,74,N´Then your hearts became hardened thereafter and are like stones, or even yet harder; for there are stones from which rivers come gushing, and others split, so that water issues from them, and others crash down in the fear of God. And God is not heedless of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,75,75,N´Are you then so eager that they should believe you, seeing there is a party of them that heard God´s word, and then tampered with it, and that after they had comprehended it, wittingly?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,76,76,N´And when they meet those who believe, they say ´We believe´; and when they go privily one to another, they say, ´Do you speak to them of what God has revealed to you, that they may thereby dispute with you before your Lord? Have you no understanding?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,77,77,N´Know they not that God knows what they keep secret and what they publish?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,78,78,N´And some there are of them that are common folk not knowing the Book, but only fancies and mere conjectures.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,79,79,N´So woe to those who write the Book with their hands, then say, ´This is from God,´ that they may sell it for a little price; so woe to them for what their hands have written, and woe to them for their earnings.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,80,80,N´And they say, ´The Fire shall not touch us save a number of days.´ Say: ´Have you taken with God a covenant? God will not fail in His covenant; or say you things against God of which you know nothing?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,81,81,N´Not so; whoso earns evil, and is encompassed by his transgression -- those are the inhabitants of the Fire; there they shall dwell forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,82,82,N´And those that believe, and do deeds of righteousness -- those are the inhabitants of Paradise; there they shall dwell forever.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,83,83,N´And when We took compact with the Children of Israel: ´You shall not serve any save God; and to be good to parents, and the near kinsman, and to orphans, and to the needy; and speak good to men, and perform the prayer, and pay the alms.´ Then you turned away, all but a few of you, swerving aside.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,84,84,N´And when We took compact with you: ´You shall not shed your own blood, neither expel your own from your habitations´; then you confirmed it and yourselves bore witness.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,85,85,N´Then there you are killing one another, and expelling a party of you from their habitations, conspiring against them in sin and enmity; and if they come to you as captives, you ransom them; yet their expulsion was forbidden you. What, do you believe in part of the Book, and disbelieve in part? What shall be the recompense of those of you who do that, but degradation in the present life, and on the Day of Resurrection to be returned unto the most terrible of chastisement? And God is not heedless of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,86,86,N´Those who have purchased the present life at the price of the world to come -- for them the chastisement shall not be lightened, neither shall they be helped.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,87,87,N´And We gave to Moses the Book, and after him sent succeeding Messengers; and We gave Jesus son of Mary the clear signs, and confirmed him with the Holy Spirit; and whensoever there came to you a Messenger with that your souls had not desire for, did you become arrogant, and some cry lies to, and some slay?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,88,88,N´And they say, ´Our hearts are uncircumcised.´ Nay, but God has cursed them for their unbelief; little will they believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,89,89,N´When there came to them a Book from God, confirming what was with them -- and they aforetimes prayed for victory over the unbelievers -- when there came to them that they recognized, they disbelieved in it; and the curse of God is on the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,90,90,N´Evil is the thing they have sold themselves for, disbelieving in that which God sent down, grudging that God should send down of His bounty on whomsoever He will of His servants, and they were laden with anger upon anger; and for unbelievers awaits a humbling chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,91,91,N´And when they were told, ´Believe in that God has sent down,´ they said, ´We believe in what was sent down on us´; and they disbelieve in what is beyond that, yet it is the truth confirming what is with them. Say: ´Why then were you slaying the Prophets of God in former time, if you were believers?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,92,92,N´And Moses came to you with the clear signs, then you took to yourselves the Calf after him and you were evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,93,93,N´And when We took compact with you, and raised over you the Mount: ´Take forcefully what We have given you and give ear.´ They said, ´We hear, and rebel´; and they were made to drink the Calf in their hearts for their unbelief. Say: ´Evil is the thing your faith bids you to, if you are believers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,94,94,N´Say: ´If the Last Abode with God is yours exclusively, and not for other people, then long for death -- if you speak truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,95,95,N´But they will never long for it, because of that their hands have forwarded; God knows the evildoers;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,96,96,N´and thou shalt find them the eagerest of men for life. And of the idolaters; there is one of them wishes if he might be spared a thousand years, yet his being spared alive shall not remove him from the chastisement. God sees the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,97,97,N´Say: ´Whosoever is an enemy to Gabriel -- he it was that brought it down upon thy heart by the leave of God, confirming what was before it, and for a guidance and good tidings to the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,98,98,N´Whosoever is an enemy to God and His angels and His Messengers, and Gabriel, and Michael - surely God is an enemy to the unbelievers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,99,99,N´And We have sent down unto thee signs, clear signs, and none disbelieves in them except the ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,100,100,N´Why, whensoever they have made a covenant, does a party of them reject it? Nay, but the most of them are unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,101,101,N´When there has come to them a Messenger from God confirming what was with them, a party of them that were given the Book reject the Book of God behind their backs, as though they knew not,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,102,102,N´and they follow what the Satans recited over Solomon´s kingdom. Solomon disbelieved not, but the Satans disbelieved, teaching the people sorcery, and that which was sent down upon Babylon´s two angels, Harut and Marut; they taught not any man, without they said, ´We are but a temptation; do not disbelieve.´ From them they learned how they might divide a man and his wife, yet they did not hurt any man thereby, save by the leave of God, and they learned what hurt them, and did not profit them, knowing well that whoso buys it shall have no share in the world to come; evil then was that they sold themselves for, if they had but known.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,103,103,N´Yet had they believed, and been godfearing, a recompense from God had been better, if they had but known.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,104,104,N´O believers, do not say, ´Observe us,´ but say, ´Regard us´; and give ear; for unbelievers awaits a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,105,105,N´Those unbelievers of the People of the Book and the idolaters wish not that any good should be sent down upon you from your Lord; but God singles out for His mercy whom He will; God is of bounty abounding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,106,106,N´And for whatever verse We abrogate or cast into oblivion, We bring a better or the like of it; knowest thou not that God is powerful over everything?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,107,107,N´Knowest thou not that to God belongs the kingdom of the heavens and the earth, and that you have none, apart from God, neither protector nor helper?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,108,108,N´Or do you desire to question your Messenger as Moses was questioned in former time? Whoso exchanges belief for unbelief has surely strayed from the right way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,109,109,N´Many of the People of the Book wish they might restore you as unbelievers, after you have believed, in the jealousy of their souls, after the truth has become clear to them; yet do you pardon and be forgiving, till God brings His command; truly God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,110,110,N´And perform the prayer, and pay the alms; whatever good you shall forward to your souls´ account, you shall find it with God; assuredly God sees the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,111,111,N´And they say, ´None shall enter Paradise except that they be Jews or Christians.´ Such are their fancies. Say: ´Produce your proof, if you speak truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,112,112,N´Nay, but whosoever submits his will to God, being a good-doer, his wage is with his Lord, and no fear shall be on them, neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,113,113,N´The Jews say, ´The Christians stand not on anything´; the Christians say, ´The Jews stand not on anything´; yet they recite the Book. So too the ignorant say the like of them. God shall decide between them on the Day of Resurrection touching their differences.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,114,114,N´And who does greater evil than he who bars God´s places of worship, so that His Name be not rehearsed in them, and strives to destroy them? Such men might never enter them, save in fear; for them is degradation in the present world, and in the world to come a mighty chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,115,115,N´To God belong the East and the West; whithersoever you turn, there is the Face of God; God is All-embracing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,116,116,N´And they say, ´God has taken to Him a son. Glory be to Him! Nay, to Him belongs all that is in the heavens and the earth; all obey His will --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,117,117,N´the Creator of the heavens and the earth; and when He decrees a thing, He but says to it ´Be,´ and it is.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,118,118,N´And they that know not say, ´Why does God not speak to us? Why does a sign not come to us?´ So spoke those before them as these men say; their hearts are much alike. Yet We have made clear the signs unto a people who are sure´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,119,119,N´We have sent thee with the truth, good tidings to bear, and warning. Thou shalt not be questioned touching the inhabitants of Hell.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,120,120,N´Never will the Jews be satisfied with thee, neither the Christians, not till thou followest their religion. Say: ´God´s guidance is the true guidance.´ If thou followest their caprices, after the knowledge that has come to thee, thou shalt have against God neither protector nor helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,121,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,121,121,N´Those to whom We have given the Book and who recite it with true recitation, they believe in it; and whoso disbelieves in it, they shall be the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,122,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,122,122,N´Children of Israel, remember My blessing wherewith I blessed you, and that I have preferred you above all beings;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,123,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,123,123,N´and beware a day when no soul for another shall give satisfaction, and no counterpoise shall be accepted from it, nor any intercession shall be profitable to it, neither shall they be helped.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,124,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,124,124,N´And when his Lord tested Abraham with certain words, and he fulfilled them. He said, ´Behold, I make you a leader for the people.´ Said he, ´And of my seed?´ He said ´My covenant shall not reach the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,125,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,125,125,N´And when We appointed the House to be a place of visitation for the people, and a sanctuary, and: ´Take to yourselves Abraham´s station for a place of prayer.´ And We made covenant with Abraham and Ishmael: ´Purify My House for those that shall go about it and those that cleave to it, to those who bow and prostrate themselves.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,126,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,126,126,N´And when Abraham said, ´My Lord, make this a land secure, and provide its people with fruits, such of them as believe in God and the Last Day.´ He said, ´And whoso disbelieves, to him I shall give enjoyment a little, then I shall compel him to the chastisement of the Fire -- how evil a homecoming!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,127,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,127,127,N´And when Abraham, and Ishmael with him, raised up the foundations of the House: ´Our Lord, receive this from us; Thou art the All-hearing, the All-knowing;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,128,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,128,128,N´and, our Lord, make us submissive to Thee, and of our seed a nation submissive to Thee; and show us our holy rites, and turn towards us; surely Thou turnest, and art All-compassionate;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,129,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,129,129,N´and, our Lord, do Thou send among them a Messenger, one of them, who shall recite to them Thy signs, and teach them the Book and the Wisdom, and purify them; Thou art the All-mighty, the All-wise.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,130,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,130,130,N´Who therefore shrinks from the religion of Abraham, except he be foolish-minded? Indeed, We chose him in the present world, and in the world to come he shall be among the righteous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,131,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,131,131,N´When his Lord said to him, ´Surrender,´ he said, ´I have surrendered me to the Lord of all Being.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,132,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,132,132,N´And Abraham charged his sons with this and Jacob likewise: ´My sons, God has chosen for you the religion; see that you die not save in surrender.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,133,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,133,133,N´Why, were you witnesses, when death came to Jacob? When he said to his sons, ´What will you serve after me?´ They said, ´We will serve thy God and the God of thy fathers Abraham, Ishmael and Isaac, One God; to Him we surrender.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,134,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,134,134,N´That is a nation that has passed away; there awaits them that they have earned, and there awaits you that you have earned; you shall not be questioned concerning the things they did.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,135,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,135,135,N´And they say, ´Be Jews or Christians and you shall be guided.´ Say thou: ´Nay, rather the creed of Abraham, a man of pure faith; he was no idolater.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,136,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,136,136,N´Say you: ´We believe in God, and in that which has been sent down on us and sent down on Abraham, Ishmael, Isaac and Jacob, and the Tribes, and that which was given to Moses and Jesus and the Prophets, of their Lord; we make no division between any of them, and to Him we surrender.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,137,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,137,137,N´And if they believe in the like of that you believe in, then they are truly guided; but if they turn away, then they are clearly in schism; God will suffice you for them; He is the All-hearing, the All-knowing;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,138,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,138,138,N´the baptism of God; and who is there that baptizes fairer than God? Him we are serving.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,139,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,139,139,N´Say: ´Would you then dispute with us concerning God, who is our Lord and your Lord? Our deeds belong to us, and to you belong your deeds; Him we serve sincerely.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,140,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,140,140,N´Or do you say, \"Abraham, Ishmael, Isaac and Jacob, and the Tribes -- they were Jews, or they were Christians\"?´ Say: ´Have you then greater knowledge, or God? And who does greater evil than he who conceals a testimony received from God? And God is not heedless of the things you do.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,141,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,141,141,N´That is a nation that has passed away; there awaits them that they have earned, and there awaits you that you have earned; you shall not be questioned concerning the things they did.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,142,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,142,142,N´The fools among the people will say, ´What has turned them from the direction they were facing in their prayers aforetime?´ Say: \"To God belong the East and the West; He guides whomsoever He will to a straight path.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,143,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,143,143,N´Thus We appointed you a midmost nation that you might be witnesses to the people, and that the Messenger might be a witness to you; and We did not appoint the direction thou wast facing, except that We might know who followed the Messenger from him who turned on his heels -- though it were a grave thing save for those whom God has guided; but God would never leave your faith to waste - truly, God is All-gentle with the people, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,144,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,144,144,N´We have seen thee turning thy face about in the heaven; now We will surely turn thee to a direction that shall satisfy thee. Turn thy face towards the Holy Mosque; and wherever you are, turn your faces towards it. Those who have been given the Book know it is the truth from their Lord; God is not heedless of the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,145,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,145,145,N´Yet if thou shouldst bring to those that have been given the Book every sign, they will not follow thy direction; thou art not a follower of their direction, neither are they followers of one another´s direction. If thou followest their caprices, after the knowledge that has come to thee, then thou wilt surely be among the evildoers´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,146,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,146,146,N´whom We have given the Book, and they recognize as they recognize their sons, even though there is a party of them conceal the truth and that wittingly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,147,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,147,147,N´The truth comes from thy Lord; then be not among the doubters.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,148,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,148,148,N´Every man has his direction to which he turns; so be you forward in good works. Wherever you may be, God will bring you all together; surely God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,149,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,149,149,N´From whatsoever place thou issuest, turn thy face towards the Holy Mosque; it is the truth from thy Lord. God is not heedless of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,150,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,150,150,N´From whatsoever place thou issuest, turn thy face towards the Holy Mosque; and wherever you may be, turn your faces towards it, that the people may not have any argument against you, excepting the evildoers of them; and fear you them not, but fear you Me; and that I may perfect My blessing upon you, and that haply so you may be guided;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,151,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,151,151,N´as also We have sent among you, of yourselves, a Messenger, to recite Our signs to you and to purify you, and to teach you the Book and the Wisdom, and to teach you that you knew not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,152,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,152,152,N´So remember Me, and I will remember you; and be thankful to Me; and be you not ungrateful towards Me.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,153,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,153,153,N´O all you who believe, seek you help in patience and prayer; surely God is with the patient.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,154,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,154,154,N´And say not of those slain in God´s way, ´They are dead´; rather they are living, but you are not aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,155,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,155,155,N´Surely We will try you with something of fear and hunger, and diminution of goods and lives and fruits; yet give thou good tidings unto the patient´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,156,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,156,156,N´who, when they are visited by an affliction, say, ´Surely we belong to God, and to Him we return´;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,157,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,157,157,N´upon those rest blessings and mercy from their Lord, and those -- they are the truly guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,158,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,158,158,N´Safa and Marwa are among the waymarks of God; so whosoever makes the Pilgrimage to the House, or the Visitation, it is no fault in him to circumambulate them; and whoso volunteers good, God is All-grateful, All-knowing´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,159,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,159,159,N´Those who conceal the clear signs and the guidance that We have sent down, after We have shown them clearly in the Book -- they shall be cursed by God and the cursers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,160,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,160,160,N´save such as repent and make amends, and show clearly -- towards them I shall turn; I turn, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,161,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,161,161,N´But those who disbelieve, and die disbelieving -- upon them shall rest the curse of God and the angels, and of men altogether,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,162,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,162,162,N´therein dwelling forever; the chastisement shall not be lightened for them; no respite shall be given them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,163,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,163,163,N´Your God is One God; there is no god but He, the All-merciful, the All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,164,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,164,164,N´.. Surely in the creation of the heavens and the earth and the alternation of night and day and the ship that runs in the sea with profit to men, and the water God sends down from heaven therewith reviving the earth after it is dead and His scattering abroad in it all manner of crawling thing, and the turning about of the winds and the clouds compelled between heaven and earth -- surely there are signs for a people having understanding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,165,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,165,165,N´Yet there be men who take to themselves compeers apart from God, loving them as God is loved; but those that believe love God more ardently. O if the evildoers might see, when they see the chastisement, that the power altogether belongs to God, and that God is terrible in chastisement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,166,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,166,166,N´when those that were followed disown their followers, and they see the chastisement, and their cords are cut asunder,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,167,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,167,167,N´and those that followed say, ´O if only we might return again and disown them, as they have disowned us!´ Even so God shall show them their works. O bitter regrets for them! Never shall they issue from the Fire.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,168,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,168,168,N´O men, eat of what is in the earth lawful and good; and follow not the steps of Satan; he is a manifest foe to you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,169,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,169,169,N´He only commands you to evil and indecency, and that you should speak against God such things as you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,170,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,170,170,N´And when it is said to them, ´Follow what God has sent down,´ they say, ´No; but we will follow such things as we found our fathers doing.´ What? And if their fathers had no understanding of anything, and if they were not guided?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,171,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,171,171,N´The likeness of those who disbelieve is as the likeness of one who shouts to that which hears nothing, save a call and a cry; deaf, dumb, blind -- they do not understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,172,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,172,172,N´O believers, eat of the good things wherewith We have provided you, and give thanks to God, if it be Him that you serve.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,173,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,173,173,N´These things only has He forbidden you: carrion, blood, the flesh of swine, what has been hollowed to other than God. Yet who so is constrained, not desiring nor transgressing, no sin shall be on him; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,174,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,174,174,N´Those who conceal what of the Book God has sent down on them, and sell it for a little price - they shall eat naught but the Fire in their bellies; God shall not speak to them on the Day of Resurrection neither purify them; there awaits them a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,175,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,175,175,N´Those are they that have bought error at the price of guidance, and chastisement at the price of pardon; how patiently they shall endure the Fire!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,176,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,176,176,N´That, because God has sent down the Book with the truth; and those that are at variance regarding the Book are in wide schism.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,177,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,177,177,N´It is not piety, that you turn your faces to the East and to the West. True piety is this: to believe in God, and the Last Day, the angels, the Book, and the Prophets, to give of one´s substance, however cherished, to kinsmen, and orphans, the needy, the traveller, beggars, and to ransom the slave, to perform the prayer, to pay the alms. And they who fulfil their covenant when they have engaged in a covenant, and endure with fortitude misfortune, hardship and peril, these are they who are true in their faith, these are the truly godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,178,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,178,178,N´O believers, prescribed for you is retaliation, touching the slain; freeman for freeman, slave for slave, female for female. But if aught is pardoned a man by his brother, let the pursuing be honourable, and let the payment be with kindliness. That is a lightening granted you by your Lord, and a mercy; and for him who commits aggression after that -- for him there awaits a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,179,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,179,179,N´In retaliation there is life for you, men possessed of minds; haply you will be godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,180,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,180,180,N´Prescribed for you, when any of you is visited by death, and he leaves behind some goods, is to make testament in favour of his parents and kinsmen honourably -- an obligation on the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,181,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,181,181,N´Then if any man changes it after hearing it, the sin shall rest upon those who change it; surely God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,182,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,182,182,N´But if any man fears injustice or sin from one making testament, and so makes things right between them, then sin shall not rest upon him; surely God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,183,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,183,183,N´O believers, prescribed for you is the Fast, even as it was prescribed for those that were before you -- haply you will be godfearing --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,184,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,184,184,N´for days numbered; and if any of you be sick, or if he be on a journey, then a number of other days; and for those who are able to fast, a redemption by feeding a poor man. Yet better it is for him who volunteers good, and that you should fast is better for you, if you but know;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,185,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,185,185,N´the month of Ramadan, wherein the Koran was sent down to be a guidance to the people, and as clear signs of the Guidance and the Salvation. So let those of you, who are present at the month, fast it; and if any of you be sick, or if he be on a journey, then a number of other days; God desires ease for you, and desires not hardship for you; and that you fulfil the number, and magnify God that He has guided you, and haply you will be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,186,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,186,186,N´And when My servants question thee concerning Me -- I am near to answer the call of the caller, when he calls to Me; so let them respond to Me, and let them believe in Me; haply so they will go aright.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,187,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,187,187,N´Permitted to you, upon the night of the Fast, is to go in to your wives; -- they are a vestment for you, and you are a vestment for them. God knows that you have been betraying yourselves, and has turned to you and pardoned you. So now lie with them, and seek what God has prescribed for you. And eat and drink, until the white thread shows clearly to you from the black thread at the dawn; then complete the Fast unto the night, and do not lie with them while you cleave to the mosques. Those are God´s bounds; keep well within them. So God makes clear His signs to men; haply they will be godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,188,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,188,188,N´Consume not your goods between you in vanity; neither proffer it to the judges, that you may sinfully consume a portion of other men´s goods, and that wittingly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,189,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,189,189,N´They will question thee concerning the new moons. Say: ´They are appointed times for the people, and the Pilgrimage.´ It is not piety to come to the houses from the backs of them; but piety is to be godfearing; so come to the houses by their doors, and fear God; haply so you will prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,190,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,190,190,N´And fight in the way of God with those; who fight with you, but aggress not: God loves not the aggressors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,191,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,191,191,N´And slay them wherever you come upon them, and expel them from where they expelled you; persecution is more grievous than slaying. But fight them not by the Holy Mosque until they should fight you there; then, if they fight you, slay them -- such is the recompense of unbelievers --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,192,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,192,192,N´but if they give over, surely God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,193,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,193,193,N´Fight them, till there is no persecution and the religion is God´s; then if they give over, there shall be no enmity save for evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,194,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,194,194,N´The holy month for the holy month; holy things demand retaliation. Whoso commits aggression against you, do you commit aggression against him like as he has committed against you, and fear you God, and know that God is with the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,195,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,195,195,N´And expend in the way of God; and cast not yourselves by your own hands into destruction, but be good-doers; God loves the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,196,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,196,196,N´Fulfil the Pilgrimage and the Visitation unto God; but if you are prevented, then such offering as may be feasible. And shave not your heads, till the offering reaches its place of sacrifice. If any of you is sick, or injured in his head, then redemption by fast, or freewill offering, or ritual sacrifice. When you are secure, then whosoever enjoys the Visitation until the Pilgrimage, let his offering be such as may be feasible; or if he finds none, then a fast of three days in the Pilgrimage, and of seven when you return, that is ten completely; that is for him whose family are not present at the Holy Mosque. And fear God, and know that God is terrible in retribution.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,197,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,197,197,N´The Pilgrimage is in months well-known; whoso undertakes the duty of Pilgrimage in them shall not go in to his womenfolk nor indulge in ungodliness and disputing in the Pilgrimage. Whatever good you do, God knows it. And take provision; but the best provision is godfearing, so fear you Me, men possessed of minds!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,198,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,198,198,N´It is no fault in you, that you should seek bounty from your Lord; but when you press on from Arafat, then remember God at the Holy Waymark, and remember Him as He has guided you, though formerly you were gone astray.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,199,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,199,199,N´Then press on from where the people press on, and pray for God´s forgiveness; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,200,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,200,200,N´And when you have performed your holy rites remember God, as you remember your fathers or yet more devoutly. Now some men there are who say, ´Our Lord, give to us in this world´; such men shall have no part in the world to come.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,201,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,201,201,N´And others there are who say, ´Our Lord, give to us in this world good, and good in the world to come, and guard us against the chastisement of the fire´;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,202,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,202,202,N´those -- they shall have a portion from what they have earned; and God is swift at the reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,203,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,203,203,N´And remember God during certain days numbered. If any man hastens on in two days, that is no sin in him; and if any delays, it is not a sin in him, if he be godfearing. And fear you God, and know that unto Him you shall be mustered.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,204,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,204,204,N´And some men there are whose saying upon the present world pleases thee, and such a one calls on God to witness what is in his heart, yet he is most stubborn in altercation,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,205,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,205,205,N´and when he turns his back, he hastens about the earth, to do corruption there and to destroy the tillage and the stock; and God loves not corruption;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,206,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,206,206,N´and when it is said to him, ´Fear God´, vainglory seizes him in his sin. So Gehenna shall be enough for him -- how evil a cradling!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,207,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,207,207,N´But other men there are that sell themselves desiring God´s good pleasure; and God is gentle with His servants.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,208,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,208,208,N´O believers, enter the peace, all of you, and follow not the steps of Satan; he is a manifest foe to you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,209,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,209,209,N´But if you slip, after the clear signs have come to you, know then that God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,210,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,210,210,N´What do they look for, but that God shall come to them in the cloud -- shadows, and the angels? The matter is determined, and unto God all matters are returned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,211,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,211,211,N´Ask the Children of Israel how many a clear sign We gave them. Whoso changes God´s blessing after it has come to him, God is terrible in retribution.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,212,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,212,212,N´Decked out fair to the unbelievers is the present life, and they deride the believers; but those who were godfearing shall be above them on the Resurrection Day; and God provides whomsoever He will without reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,213,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,213,213,N´The people were one nation; then God sent forth the Prophets, good tidings to bear and warning, and He sent down with them the Book with the truth, that He might decide between the people touching their differences; and only those who had been given it were at variance upon it, after the clear signs had come to them, being insolent one to another; then God guided those who believed to the truth, touching which they were at variance, by His leave; and God guides whomsoever He will to a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,214,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,214,214,N´Or did you suppose you should enter Paradise without there had come upon you the like of those who passed away before you? They were afflicted by misery and hardship and were so convulsed, that the Messenger and those who believed with him said, ´When comes God´s help?´ Ah, but surely God´s help is nigh.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,215,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,215,215,N´They will question thee concerning what they should expend. Say: ´Whatsoever good you expend is for parents and kinsmen, orphans, the needy, and the traveller; and whatever good you may do, God has knowledge of it.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,216,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,216,216,N´Prescribed for you is fighting, though it be hateful to you. Yet it may happen that you will hate a thing which is better for you; and it may happen that you will love a thing which is worse for you; God knows, and you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,217,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,217,217,N´They will question thee concerning the holy month, and fighting in it. Say: ´Fighting in it is a heinous thing, but to bar from God´s way, and disbelief in Him, and the Holy Mosque, and to expel its people from it -- that is more heinous in God´s sight; and persecution is more heinous than slaying.´ They will not cease to fight with you, till they turn you from your religion, if they are able; and whosoever of you turns from his religion, and dies disbelieving -- their works have failed in this world and the next; those are the inhabitants of the Fire; therein they shall dwell forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,218,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,218,218,N´But the believers, and those who emigrate and struggle in God´s way -- those have hope of God´s compassion; and God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,219,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,219,219,N´They will question thee concerning wine, and arrow-shuffling. Say: ´In both is heinous sin; and uses for men, but the sin in them is more heinous than the usefulness.´ They will question thee concerning what they should expend. Say: ´The abundance.´ So God makes clear His signs to you; haply you will reflect;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,220,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,220,220,N´in this world, and the world to come. They will question thee concerning the orphans. Say: ´To set their affairs aright is good. And if you intermix with them, they are your brothers. God knows well him who works corruption from him who sets aright; and had He willed He would have harassed you. Surely God is All-mighty, All-wise.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,221,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,221,221,N´Do not marry idolatresses, until they believe; a believing slavegirl is better than an idolatress, though you may admire her. And do not marry idolaters, until they believe. A believing slave is better than an idolater, though you may admire him. Those call unto the Fire; and God calls unto Paradise, and pardon, by His leave, and He makes clear His signs to the people; haply they will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,222,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,222,222,N´They will question thee concerning the monthly course. Say: ´It is hurt; so go apart from women during the monthly course, and do not approach them till they are clean. When they have cleansed themselves, then come unto them as God has commanded you.´ Truly, God loves those who repent, and He loves those who cleanse themselves.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,223,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,223,223,N´Your women are a tillage for you; so come unto your tillage as you wish, and forward for your souls; and fear God, and know that you shall meet Him. Give thou good tidings to the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,224,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,224,224,N´Do not make God a hindrance, through your oaths, to being pious and godfearing, and putting things right between men. Surely God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,225,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,225,225,N´God will not take you to task for a slip in your oaths; but He will take you to task for what your hearts have earned; and God is All-forgiving, All-clement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,226,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,226,226,N´For those who forswear their women a wait of four months; if they revert, God is All-forgiving, All-compassionate;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,227,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,227,227,N´but if they resolve on divorce, surely God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,228,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,228,228,N´Divorced women shall wait by themselves for three periods; and it is not lawful for them to hide what God has created in their wombs; if they believe in God and the Last Day. In such time their mates have better right to restore them, if they desire to set things right. Women have such honourable rights as obligations, but their men have a degree above them; God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,229,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,229,229,N´Divorce is twice; then honourable retention or setting free kindly. It is not lawful for you to take of what you have given them unless the couple fear they may not maintain God´s bounds; if you fear they may not maintain God´s bounds, it is no fault in them for her to redeem herself. Those are God´s bounds; do not transgress them. Whosoever transgresses the bounds of God -- those are the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,230,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,230,230,N´If he divorces her finally, she shall not be lawful to him after that, until she marries another husband. If he divorces her, then it is no fault in them to return to each other, if they suppose that they will maintain God´s bounds. Those are God´s bounds; He makes them clear unto a people that have knowledge.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,231,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,231,231,N´When you divorce women, and they have reached their term, then retain them honourably or set them free honourably; do not retain them by force, to transgress; whoever does that has wronged himself. Take not God´s signs in mockery, and remember God´s blessing upon you, and the Book and the Wisdom He has sent down on you, to admonish you. And fear God, and know that God has knowledge of everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,232,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,232,232,N´When you divorce women, and they have reached their term, do not debar them from marrying their husbands, when they have agreed together honourably. That is an admonition for whoso of you believes in God and the Last Day; that is cleaner and purer for you; God knows, and you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,233,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,233,233,N´Mothers shall suckle their children two years completely, for such as desire to fulfil the suckling. It is for the father to provide them and clothe them honourably. No soul is charged save to its capacity; a mother shall not be pressed for her child, neither a father for his child. The heir has a like duty. But if the couple desire by mutual consent and consultation to wean, then it is no fault in them. And if you desire to seek nursing for your children, it is no fault in you provide you hand over what you have given honourably; and fear God, and know that God sees the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,234,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,234,234,N´And those of you who die, leaving wives, they shall wait by themselves for four months and ten nights; when they have reached their term then it is no fault in you what they may do with themselves honourably. God is aware of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,235,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,235,235,N´There is no fault in you touching the proposal to women you offer, or hide in your hearts; God knows that you will be mindful of them; but do not make troth with them secretly without you speak honourable words. And do not resolve on the knot of marriage until the book has reached its term; and know that God knows what is in your hearts, so be fearful of Him; and know that God is All-forgiving, All-clement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,236,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,236,236,N´There is no fault in you, if you divorce women while as yet you have not touched them nor appointed any marriage-portion for them; yet make provision for them, the affluent man according to his means, and according to his means the needy man, honourably -- an obligation on the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,237,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,237,237,N´And if you divorce them before you have touched them, and you have already appointed for them a marriage-portion, then one-half of what you have appointed, unless it be they make remission, or he makes remission in whose hand is the knot of marriage; yet that you should remit is nearer to godfearing. Forget not to be bountiful one towards another. Surely God sees the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,238,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,238,238,N´Be you watchful over the prayers, and the middle prayer; and do you stand obedient to God.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,239,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,239,239,N´And if you are in fear, then afoot or mounted; but when you are secure, then remember God, as He taught you the things that you knew not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,240,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,240,240,N´And those of you who die, leaving wives, let them make testament for their wives, provision for a year without expulsion; but if they go forth, there is no fault in you what they may do with themselves honourably; God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,241,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,241,241,N´There shall be for divorced women provision honourable -- an obligation on the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,242,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,242,242,N´So God makes clear His signs for you; haply you will understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,243,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,243,243,N´Hast thou not regarded those who went forth from their habitations in their thousands fearful of death? God said to them, ´Die!´ Then He gave them life. Truly God is bounteous to the people, but most of the people are not thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,244,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,244,244,N´So fight in God´s way, and know that God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,245,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,245,245,N´Who is he that will lend God a good loan, and He will multiply it for him manifold? God grasps, and outspreads; and unto Him you shall be returned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,246,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,246,246,N´Hast thou not regarded the Council of the Children of Israel, after Moses, when they said to a Prophet of theirs, ´Raise up for us a king, and we will fight in God´s way.´ He said, ´Might it be that, if fighting is prescribed for you, you will not fight?´ They said, ´Why should we not fight in God´s way, who have been expelled from our habitations and our children?´ Yet when fighting was prescribed for them, they turned their backs except a few of them; and God has knowledge of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,247,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,247,247,N´Then their Prophet said to them, ´Verily God has raised up Saul for you as king.´ They said, ´How should he be king over us who have better right than he to kingship, seeing he has not been given amplitude of wealth?´ He said, ´God has chosen him over you, and has increased him broadly in knowledge and body. God gives the kingship to whom He will; and God is All-embracing, All-knowing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,248,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,248,248,N´And their Prophet said to them, ´The sign of his kingship is that the Ark will come to you, in it a Shechina from your Lord, and a remnant of what the folk of Moses and Aaron´s folk left behind, the angels bearing it. Surely in that shall be a sign for you, if you are believers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,249,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,249,249,N´And when Saul went forth with the hosts he said, ´God will try you with a river; whosoever drinks of it is not of me, and whoso tastes it flot, he is of me, saving him who scoops up with his hand.´ But they drank of it, except a few of them; and when he crossed it, and those who believed with him, they said, ´We have no power today against Goliath and his hosts.´ Said those who reckoned they should meet God, ´How often a little company has overcome a numerous company, by God´s leave! And God is with the patient.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,250,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,250,250,N´So, when they went forth against Goliath and his hosts, they said, ´Our Lord, pour out upon us patience, and make firm our feet, and give us aid against the people of the unbelievers!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,251,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,251,251,N´And they routed them, by the leave of God, and David slew Goliath; and God gave him the kingship, and Wisdom, and He taught him such as He willed. Had God not driven back the people, some by the means of others, the earth had surely corrupted; but God is bounteous unto all beings.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,252,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,252,252,N´These are the signs of God We recite to thee in truth, and assuredly thou art of the number of the Envoys.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,253,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,253,253,N´And those Messengers, some We have preferred above others; some there are to whom God spoke, and some He raised in rank. And We gave Jesus son of Mary the clear signs, and confirmed him with the Holy Spirit. And had God willed, those who came after him would not have fought one against the other after the clear signs had come to them; but they fell into variance, and some of them believed, and some disbelieved; and had God willed they would not have fought one against the other; but God does whatsoever He desires.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,254,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,254,254,N´O believers, expend of that wherewith We have provided you, before there comes a day wherein shall be neither traffick, nor friendship, nor intercession; and the unbelievers -- they are the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,255,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,255,255,N´God there is no god but He, the Living, the Everlasting. Slumber seizes Him not, neither sleep; to Him belongs all that is in the heavens and the earth. Who is there that shall intercede with Him save by His leave? He knows what lies before them and what is after them, and they comprehend not anything of His knowledge save such as He wills. His Throne comprises the heavens and earth; the preserving of them oppresses Him not; He is the All-high, the All-glorious.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,256,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,256,256,N´No compulsion is there in religion. Rectitude has become clear from error. So whosoever disbelieves in idols and believes in God, has laid hold of the most firm handle, unbreaking; God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,257,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,257,257,N´God is the Protector of the believers; He brings them forth from the shadows into the light. And the unbelievers -- their protectors are idols, that bring them forth from the light into the shadows; those are the inhabitants of the Fire, therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,258,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,258,258,N´Hast thou not regarded him who disputed with Abraham, concerning his Lord, that God had given him the kingship? When Abraham said, ´My Lord is He who gives life, and makes to die,´ he said, ´I give life, and make to die.´ Said Abraham, ´God brings the sun from the east; so bring thou it from the west.´ Then the unbeliever was confounded. God guides not the people of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,259,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,259,259,N´Or such as he who passed by a city that was fallen down upon its turrets; he said, ´How shall God give life to this now it is dead?´ So God made him die a hundred years, then He raised him up, saying, ´How long hast thou tarried?´ He said, ´I have tarried a day, or part of a day.´ Said He, ´Nay; thou hast tarried a hundred years. Look at thy food and drink -- it has not spoiled; and look at thy ass. So We would make thee a sign for the people. And look at the bones; how We shall set them up, and then clothe them with flesh.´ So, when it was made clear to him, he said, ´I know that God is powerful over everything.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,260,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,260,260,N´And when Abraham said, ´My Lord, show me how Thou wilt give life to the dead,´ He said, ´Why, dost thou not believe?´ ´Yes,´ he said, ´but that my heart may be at rest.´ Said He, ´Take four birds, and twist them to thee, then set a part of them on every hill, then summon them, and they will come to thee running. And do thou know that God is All-mighty, All-wise.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,261,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,261,261,N´The likeness of those who expend their wealth in the way of God is as the likeness of a grain of corn that sprouts seven ears, in every ear a hundred grains. So God multiplies unto whom He will; God is All-embracing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,262,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,262,262,N´Those who expend their wealth in the way of God then follow not up what they have expended with reproach and injury, their wage is with their Lord, and no fear shall be on them, neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,263,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,263,263,N´Honourable words, and forgiveness, are better than a freewill offering followed by injury; and God is All-sufficient, All-clement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,264,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,264,264,N´O believers, void not your freewill offerings with reproach and injury, as one who expends of his substance to show off to men and believes not in God and the Last Day. The likeness of him is as the likeness of a smooth rock on which is soil, and a torrent smites it, and leaves it barren. They have no power over anything that they have earned. God guides not the people of the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,265,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,265,265,N´But the likeness of those who expend their wealth, seeking God´s good pleasure, and to confirm themselves, is as the likeness of a garden upon a hill; a torrent smites it and it yields its produce twofold; if no torrent smites it, yet dew; and God sees the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,266,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,266,266,N´Would any of you wish to have a garden of palms and vines, with rivers flowing beneath it, and all manner of fruit there for him, then old age smites him, and he has seed, but weaklings, then a whirlwind with fire smites it, and it is consumed? So God makes clear the signs to you; haply you will reflect.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,267,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,267,267,N´O believers, expend of the good things you have earned, and of that We have produced for you from the earth, and intend not the corrupt of it for your expending; for you would never take it yourselves, except you closed an eye on it; and know that God is All-sufficient, All-laudable.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,268,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,268,268,N´Satan promises you poverty, and bids you unto indecency; but God promises you His pardon and His bounty; and God is All-embracing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,269,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,269,269,N´He gives the Wisdom to whomsoever He will, and whoso is given the Wisdom, has been given much good; yet none remembers but men possessed of minds.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,270,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,270,270,N´And whatever expenditure you expend, and whatever vow you vow, surely God knows it. No helpers have the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,271,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,271,271,N´If you publish your freewill offerings, it is excellent; but if you conceal them, and give them to the poor, that is better for you, and will acquit you of your evil deeds; God is aware of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,272,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,272,272,N´Thou art not responsible for guiding them; but God guides whomsoever He will. And whatever good you expend is for yourselves, for then you are expending, being desirous only of God´s Face; and whatever good you expend shall be repaid to you in full, and you will not be wronged,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,273,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,273,273,N´it being for the poor who are restrained in the way of God, and are unable to journey in the land; the ignorant man supposes them rich because of their abstinence, but thou shalt know them by their mark -- they do not beg of men importunately. And whatever good you expend, surely God has knowledge of it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,274,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,274,274,N´Those who expend their wealth night and day, secretly and in public, their wage awaits them with their Lord, and no fear shall be on them, neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,275,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,275,275,N´Those who devour usury shall not rise again except as he rises, whom Satan of the touch prostrates; that is because they say, ´Trafficking (trade) is like usury.´ God has permitted trafficking, and forbidden usury. Whosoever receives an admonition from his Lord and gives over, he shall have his past gains, and his affair is committed to God; but whosoever reverts -- those are the inhabitants of the Fire, therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,276,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,276,276,N´God blots out usury, but freewill offerings He augments with interest. God loves not any guilty ingrate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,277,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,277,277,N´Those who believe and do deeds of righteousness, and perform the prayer, and pay the alms -- their wage awaits them with their Lord, and no fear shall be on them, neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,278,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,278,278,N´O believers, fear you God; and give up the usury that is outstanding, if you are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,279,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,279,279,N´But if you do not, then take notice that God shall war with you, and His Messenger; yet if you repent, you shall have your principal, unwronging and unwronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,280,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,280,280,N´And if any man should be in difficulties, let him have respite till things are easier; but that you should give freewill offerings is better for you, did you but know.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,281,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,281,281,N´And fear a day wherein you shall be returned to God, and every soul shall be paid in full what it has earned; and they shall not be wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,282,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,282,282,N´O believers, when you contract a debt one upon another for a stated term, write it down, and let a writer write it down between you justly, and let not any writer refuse to write it down, as God has taught him; so let him write, and let the debtor dictate, and let him fear God his Lord and not diminish aught of it. And if the debtor be a fool, or weak, or unable to dictate himself, then let his guardian dictate justly. And call in to witness two witnesses, men; or if the two be not men, then one man and two women, such witnesses as you approve of, that if one of the two women errs the other will remind her; and let the witnesses not refuse, whenever they are summoned. And be not loth to write it down, whether it be small or great, with its term; that is more equitable in God´s sight, more upright for testimony, and likelier that you will not be in doubt. Unless it be merchandise present that you give and take between you; then it shall be no fault in you if you do not write it down. And take witnesses when you are trafficking one with another. And let not either writer or witness be pressed; or if you do, that is ungodliness in you. And fear God; God teaches you, and God has knowledge of everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,283,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,283,283,N´And if you are upon a journey, and you do not find a writer, then a pledge in hand. But if one of you trusts another, let him who is trusted deliver his trust, and let him fear God his Lord. And do not conceal the testimony; whoso conceals it, his heart is sinful; and God has knowledge of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,284,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,284,284,N´To God belongs all that is in the heavers and earth. Whether you publish what is in your hearts or hide it, God shall make reckoning with you for it. He will forgive whom He will, and chastise whom He will; God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,285,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,285,285,N´The Messenger believes in what was sent down to him from his Lord, and the believers; each one believes in God and His angels, and in His Books and His Messengers; we make no division between any one of His Messengers. They say, ´We hear, and obey. Our Lord, grant us Thy forgiveness; unto Thee is the homecoming.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,2,286,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,2,286,286,N´God charges no soul save to its capacity; standing to its account is what it has earned, and against its account what it has merited. Our Lord, take us not to task if we forget, or make mistake. Our Lord; charge us not with a load such as Thou didst lay upon those before us. Our Lord, do Thou not burden us beyond what we have the strength to bear. And pardon us, and forgive us, and have mercy on us; Thou art our Protector. And help us against the people of the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,1,1,N´Alif Lam Mim´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,2,2,N´God, there is no god but He, the Living, the Everlasting.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,3,3,N´He has sent down upon thee the Book with the truth, confirming what was before it, and He sent down the Torah and the Gospel´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,4,4,N´aforetime, as guidance to the people, and He sent down the Salvation. As for those who disbelieve in God´s signs, for them awaits a terrible chastisement; God is All-mighty, Vengeful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,5,5,N´From God nothing whatever is hidden in heaven and earth.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,6,6,N´It is He who forms you in the womb as He will. There is no god but He, the All-mighty, the All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,7,7,N´It is He who sent down upon thee the Book, wherein are verses clear that are the Essence of the Book, and others ambiguous. As for those in whose hearts is swerving, they follow the ambiguous part, desiring dissension, and desiring its interpretation; and none knows its interpretation, save only God. And those firmly rooted in knowledge say, ´We believe in it; all is from our Lord´; yet none remembers, but men possessed of minds.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,8,8,N´Our Lord, make not our hearts to swerve after that Thou hast guided us; and give us mercy from Thee; Thou art the Giver.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,9,9,N´Our Lord, it is Thou that shall gather mankind for a day whereon is no doubt; verily God will not fail the tryst.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,10,10,N´As for the unbelievers, their riches will not avail them, neither their children, aught against God; those -- they shall be fuel for the Fire´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,11,11,N´like Pharaoh´s folk, and the people before them, who cried lies to Our signs; God seized them because of their sins; God is terrible in retribution.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,12,12,N´Say to the unbelievers: ´You shall be overthrown, and mustered into Gehenna -- an evil cradling!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,13,13,N´There has already been a sign for you in the two companies that encountered, one company fighting in the way of God and another unbelieving; they saw them twice the like of them, as the eye sees, but God confirms with His help whom He will. Surely in that is a lesson for men possessed of eyes.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,14,14,N´Decked out fair to men is the love of lusts -- women, children, heaped-up heaps of gold and silver, horses of mark, cattle and tillage. That is the enjoyment of the present life; but God -- with Him is the fairest resort.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,15,15,N´Say: ´Shall I tell you of a better than that?´ For those that are godfearing, with their Lord are gardens underneath which rivers flow, therein dwelling forever, and spouses purified, and God´s good pleasure. And God sees His servants´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,16,16,N´who say, ´Our Lord, we believe; forgive us our sins, and guard us against the chastisement of the Fire´ --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,17,17,N´men who are patient, truthful, obedient, expenders in alms, imploring God´s pardon at the daybreak.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,18,18,N´God bears witness that there is no god but He -- and the angels, and men possessed of knowledge -- upholding justice; there is no god but He, the All-mighty, the All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,19,19,N´The true religion with God is Islam. Those who were given the Book were not at variance except after the knowledge came to them, being insolent one to another. And whoso disbelieves in God´s signs. God is swift at the reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,20,20,N´So if they dispute with thee, say: ´I have surrendered my will to God, and whosoever follows me. And say to those who have been given the Book and to the common folk: ´Have you surrendered?´ If they have surrendered, they are right guided; but if they turn their backs, thine it is only to deliver the Message; and God sees His servants.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,21,21,N´Those who disbelieve in the signs of God and slay the Prophets without right, and slay such men as bid to justice -- do thou give them the good tidings of a painful chastisement;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,22,22,N´their works have failed in this world and the next; they have no helpers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,23,23,N´Hast thou not regarded those who were given a portion of the Book, being called to the Book of God, that it might decide between them, and then a party of them turned away, swerving aside?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,24,24,N´That, because they said, ´The Fire shall not touch us, except for a number of days´; and the lies they forged has deluded them in their religion.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,25,25,N´But how will it be, when We gather them for a day whereon is no doubt, and every soul shall be paid in full what it has earned, and they shall not be wronged?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,26,26,N´Say: ´O God, Master of the Kingdom, Thou givest the Kingdom to whom Thou wilt, and seizest the Kingdom from whom Thou wilt, Thou exaltest whom Thou wilt, and Thou abasest whom Thou wilt; in Thy hand is the good; Thou art powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,27,27,N´Thou makest the night to enter into the day and Thou makest the day to enter into the night, Thou bringest forth the living from the dead and Thou bringest forth the dead from the living, and Thou providest whomsoever Thou wilt without reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,28,28,N´Let not the believers take the unbelievers for friends, rather than the believers -- for whoso does that belongs not to God in anything -- unless you have a fear of them. God warns you that You beware of Him, and unto God is the homecoming.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,29,29,N´Say: ´Whether you hide what is in your breasts or publish it, God knows it. God knows what is in the heavens and what is in the earth; and God is powerful over everything.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,30,30,N´The day every soul shall find what it has done of good brought forward, and what it has done of evil; it will wish if there were only a far space between it and that day. God warns you that you beware of Him; and God is gentle with His servants.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,31,31,N´Say: ´If you love God, follow me, and God will love you, and forgive you your sins; God is All-forgiving, All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,32,32,N´Say: ´Obey God, and the Messenger.´ But if they turn their backs, God loves not the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,33,33,N´God chose Adam and Noah and the House of Abraham and the House of Imran above all beings, the´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,34,34,N´seed of one another; God hears, and knows.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,35,35,N´When the wife of Imran said, ´Lord, I have vowed to Thee, in dedication, what is within my womb. Receive Thou this from me; Thou hearest, and knowest.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,36,36,N´And when she gave birth to her she said, ´Lord, I have given birth to her, a female.´ (And God knew very well what she had given birth to; the male is not as the female.) ´And I have named her Mary, and commend her to Thee with her seed, to protect them from the accursed Satan.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,37,37,N´Her Lord received the child with gracious favour, and by His goodness she grew up comely, Zachariah taking charge of her. Whenever Zachariah went in to her in the Sanctuary, he found her provisioned. ´Mary,´ he said, ´how comes this to thee?´ ´From God,´ she said. Truly God provisions whomsoever He will without reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,38,38,N´Then Zachariah prayed to his Lord saying, ´Lord, give me of Thy goodness a goodly offspring. Yea, Thou hearest prayer.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,39,39,N´And the angels called to him, standing in the Sanctuary at worship, ´Lo, God gives thee good tidings of John, who shall confirm a Word of God, a chief, and chaste, a Prophet, righteous.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,40,40,N´´Lord,´ said Zachariah, ´how shall I have a son, seeing I am an old man and my wife is barren?´ ´Even so,´ God said, ´God does what He will.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,41,41,N´´Lord,´ said Zachariah, ´appoint to me a sign.´ ´Thy sign,´ God said, ´is that thou shalt not speak, save by tokens, to men for three days. And mention thy Lord oft, and give glory at evening and dawn.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,42,42,N´And when the angels said, ´Mary, God has chosen thee, and purified thee; He has chosen thee above all women.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,43,43,N´Mary; be obedient to thy Lord, prostrating and bowing before Him.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,44,44,N´(That is of the tidings of the Unseen, that We reveal to thee; for thou wast not with them, when they were casting quills which of them should have charge of Mary; thou wast not with them, when they were disputing.)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,45,45,N´When the angels said, ´Mary, God gives thee good tidings of a Word from Him whose name is Messiah, Jesus, son of Mary; high honoured shall he be in this world and the next, near stationed to God.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,46,46,N´He shall speak to men in the cradle, and of age, and righteous he shall be.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,47,47,N´´Lord,´ said Mary, ´how shall I have a son seeing no mortal has touched me?´ ´Even so,´ God said, God creates what He will. When He decrees a thing He does but say to it \"Be,\" and it is.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,48,48,N´And He will teach him the Book, the Wisdom, the Torah, the Gospel,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,49,49,N´to be a Messenger to the Children of Israel saying, \"I have come to you with a sign from your Lord. I will create for you out of clay as the likeness of a bird; then I will breathe into it, and it will be a bird, by the leave of God. I will also heal the blind and the leper, and bring to life the dead, by the leave of God. I will inform you too of what things you eat, and what you treasure up in your houses. Surely in that is a sign for you, if you are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,50,50,N´Likewise confirming the truth of the Torah that is before me, and to make lawful to you certain things that before were forbidden unto you. I have come to you with a sign from your Lord; so fear you God, and obey you me.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,51,51,N´Surely God is my Lord and your Lord; so serve Him. This is a straight path\".´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,52,52,N´And when Jesus perceived their unbelief, he said, ´Who will be my helpers unto God?´ The Apostles said, ´We will be helpers of God; we believe in God; witness thou our submission.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,53,53,N´Lord, we believe in that Thou hast sent down, and we follow the Messenger. Inscribe us therefore with those who bear witness.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,54,54,N´And they devised, and God devised, and God is the best of devisers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,55,55,N´When God said, ´Jesus, I will take thee to Me and will raise thee to Me and I will purify thee of those who believe not. I will set thy followers above the unbelievers till the Resurrection Day. Then unto Me shall you return, and I will decide between you, as to what you were at variance on.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,56,56,N´As for the unbelievers, I will chastise them with a terrible chastisement in this world and the next; they shall have no helpers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,57,57,N´But as for the believers, who do deeds of righteousness, He will pay them in full their wages: and God loves not the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,58,58,N´This We recite to thee of signs and wise remembrance.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,59,59,N´Truly, the likeness of Jesus, in God´s sight, is as Adam´s likeness; He created him of dust, then said He unto him, ´Be,´ and he was.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,60,60,N´The truth is of God; be not of the doubters.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,61,61,N´And whoso disputes with thee concerning him, after the knowledge that has come to thee, say: ´Come now, let us call our sons and your sons, our wives and your wives, our selves and your selves, then let us humbly pray and so lay God´s curse upon the ones who lie.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,62,62,N´This is the true story. There is no god but God, and assuredly God is the All-mighty, the All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,63,63,N´And if they turn their backs, assuredly God knows the workers of corruption.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,64,64,N´Say: ´People of the Book! Come now to a word common between us and you, that we serve none but God, and that we associate not aught with Him, and do not some of us take others as Lords, apart from God.´ And if they turn their backs, say: ´Bear witness that we are Muslims.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,65,65,N´People of the Book! Why do you dispute concerning Abraham? The Torah was not sent down, neither the Gospel, but after him. What, have you no reason?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,66,66,N´Ha, you are the ones who dispute on what you know; why then dispute you touching a matter of which you know not anything? God knows, and you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,67,67,N´No; Abraham in truth was not a Jew, neither a Christian; but he was a Muslim and one pure of faith; certainly he was never of the idolaters.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,68,68,N´Surely the people standing closest to Abraham are those who followed him, and this Prophet, and those who believe; and God is the Protector of the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,69,69,N´There is a party of the People of the Book yearn to make you go astray; yet none they make to stray, except themselves, but they are not aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,70,70,N´People of the Book! Why do you disbelieve in God´s signs, which you yourselves witness?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,71,71,N´People of the Book! Why do you confound the truth with vanity, and conceal the truth and that wittingly?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,72,72,N´There is a party of the People of the Book say, ´Believe in what has been sent down upon those who believe at the beginning of the day, and disbelieve at the end of it; haply they will then return;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,73,73,N´and believe not any but him who follows your religion.´ Say: ´The true guidance is God´s guidance -- that anyone should be given the like of what you have been given, or dispute with you before your Lord.´ Say: ´Surely bounty is in the hand of God; He gives it unto whomsoever He will; and God is All-embracing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,74,74,N´He singles out for His mercy whom He will; God is of bounty abounding.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,75,75,N´And of the People of the Book is he who, if thou trust him with a hundredweight, will restore it thee; and of them is he who, if thou trust him with one pound, will not restore it thee, unless ever thou standest over him. That, because they say, ´There is no way over us as to the common people.´ They speak falsehood against God and that wittingly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,76,76,N´Nay, but whoso fulfils his covenant and fears God, God loves the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,77,77,N´Those that sell God´s covenant, and their oaths, for a little price, there shall be no share for them in the world to come; God shall not speak to them neither look on them on the Resurrection Day, neither will He purify them; and for them awaits a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,78,78,N´And there is a sect of them twist their tongues with the Book, that you may suppose it part of the Book, yet it is not part of the Book; and they say, ´It is from God,´ yet it is not from God, and they speak falsehood against God, and that wittingly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,79,79,N´It belongs not to any mortal that God should give him the Book, the Judgment, the Prophethood, then he should say to men, ´Be you servants to me apart from God.´ Rather, ´Be you masters in that you know the Book, and in that you study.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,80,80,N´He would never order you to take the angels and the Prophets as Lords; what, would He order you to disbelieve, after you have surrendered?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,81,81,N´And when God took compact with the Prophets: ´That I have given you of Book and Wisdom; then there shall come to you a Messenger confirming what is with you -- you shall believe in him and you shall help him; do you agree?´ He said. ´And do you take My load on you on that condition?´ They said, ´We do agree.´ God said, ´Bear witness so, and I shall be with you among the witnesses.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,82,82,N´Then whosoever turns his back after that -- they are the ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,83,83,N´What, do they desire another religion than God´s, and to Him has surrendered whoso is in the heavens and the earth, willingly or unwillingly, and to Him they shall be returned?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,84,84,N´Say: ´We believe in God, and that which has been sent down on us, and sent down on Abraham and Ishmael, Isaac and Jacob, and the Tribes, and in that which was given to Moses and Jesus, and the Prophets, of their Lord; we make no division between any of them, and to Him we surrender.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,85,85,N´Whoso desires another religion than Islam, it shall not be accepted of him; in the next world he shall be among the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,86,86,N´How shall God guide a people who have disbelieved after they believed, and bore witness that the Messenger is true, and the clear signs came to them? God guides not the people of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,87,87,N´Those -- their recompense is that there shall rest on them the curse of God and of the angels and of men, altogether,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,88,88,N´therein dwelling forever; the chastisement shall not be lightened for them; no respite shall be given them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,89,89,N´But those who repent thereafter, and make amends -- God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,90,90,N´Surely those who disbelieve after they have believed and then increase in unbelief -- their repentance shall not be accepted; those are the ones who stray.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,91,91,N´Surely those who disbelieve, and die disbelieving, there shall not be accepted from any one of them the whole earth full of gold, if he would ransom himself thereby; for them awaits a painful chastisement, and they shall have no helpers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,92,92,N´You will not attain piety until you expend of what you love; and whatever thing you expend, God knows of it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,93,93,N´All food was lawful to the Children of Israel save what Israel forbade for himself before the Torah was sent down. Say: ´Bring you the Torah now, and recite it, if you are truthful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,94,94,N´Whoso forges falsehood against God after that, those are the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,95,95,N´Say: ´God has spoken the truth; therefore follow the creed of Abraham, a man of pure faith and no idolater.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,96,96,N´The first House established for the people was that at Bekka, a place holy, and a guidance to all beings.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,97,97,N´Therein are clear signs -- the station of Abraham, and whosoever enters it is in security. It is the duty of all men towards God to come to the House a pilgrim, if he is able to make his way there. As for the unbeliever, God is All-sufficient nor needs any being.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,98,98,N´Say: ´People of the Book, why do you disbelieve in the signs of God? Surely God is witness of the things you do.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,99,99,N´Say: ´People of the Book, why do you bar from God´s way the believer, desiring to make it crooked, yourselves being witnesses? God is not heedless of the things you do.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,100,100,N´O believers, if you obey a sect of those who have been given the Book, they will turn you, after you have believed, into unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,101,101,N´How can you disbelieve, seeing you have God´s signs recited to you, and His Messenger among you? Whosoever holds fast to God, he is guided to a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,102,102,N´O believers, fear God as He should be feared, and see you do not die, save in surrender.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,103,103,N´And hold you fast to God´s bond, together, and do not scatter; remember God´s blessing upon you when you were enemies, and He brought your hearts together, so that by His blessing you became brothers. You were upon the brink of a pit of Fire, and He delivered you from it; even so God makes clear to you His signs; so haply you will be guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,104,104,N´Let there be one nation of you, calling to good, and bidding to honour, and forbidding dishonour; those are the prosperers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,105,105,N´Be not as those who scattered and fell into variance after the clear signs came to them; those there awaits a mighty chastisement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,106,106,N´the day when some faces are blackened, and some faces whitened. As for those whose faces are blackened -- ´Did you disbelieve after you had believed? Then taste the chastisement for that you disbelieved!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,107,107,N´But as for those whose faces are whitened, they shall be in God´s mercy, therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,108,108,N´These are the signs of God We recite to thee in truth, and God desires not any injustice to living beings.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,109,109,N´To God belongs all that is in the heavens and in the earth, and unto Him all matters are returned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,110,110,N´You are the best nation ever brought forth to men, bidding to honour, and forbidding dishonour, and believing in God. Had the People of the Book believed, it were better for them; some of them are believers, but the most of them are ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,111,111,N´They will not harm you, except a little hurt; and if they fight with you, they will turn on you their backs; then they will not be helped.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,112,112,N´Abasement shall be pitched on them, wherever they are come upon, except they be in a bond of God, and a bond of the people; they will be laden with the burden of God´s anger, and poverty shall be pitched on them; that, because they disbelieved in God´s signs, and slew the Prophets without right; that, for that they acted rebelliously and were transgressors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,113,113,N´Yet they are not all alike; some of the People of the Book are a nation upstanding, that recite God´s signs in the watches of the night, bowing themselves,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,114,114,N´believing in God and in the Last Day, bidding to honour and forbidding dishonour, vying one with the other in good works; those are of the righteous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,115,115,N´And whatsoever good you do, you shall not be denied the just reward of it; and God knows the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,116,116,N´As for the unbelievers, their riches shall not avail them, neither their children, against God; those are the inhabitants of the Fire, therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,117,117,N´The likeness of that they expend in this present life is as the likeness of a freezing blast that smites the tillage of a people who wronged themselves, and it destroyed that; God wronged them not, but themselves they wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,118,118,N´O believers, take not for your intimates outside yourselves such men spare nothing to ruin you; they yearn for you to suffer Hatred has already shown itself of their mouths, and what their breasts conceal is yet greater. Now We have made clear to you the signs, if you understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,119,119,N´Ha, there you are; you love them, and they love you not; you believe in the Book, all of it, and when they meet you they say, ´We believe,´ but when they go privily, they bite at you their fingers, enraged. Say: ´Die in your rage; God knows the thoughts in the breasts.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,120,120,N´If you are visited by good fortune, it vexes them; but if you are smitten by evil, they rejoice at it. Yet if you are patient and godfearing, their guile will hurt you nothing; God encompasses the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,121,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,121,121,N´When thou wentest forth at dawn from thy people to lodge the believers in their pitches for the battle -- God is All-hearing, All-knowing --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,122,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,122,122,N´when two parties of you were about to lose heart, though God was their Protector -- and in God let the believers put all their trust --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,123,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,123,123,N´and God most surely helped you at Badr, when you were utterly abject. So fear God, and haply you Will be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,124,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,124,124,N´When thou saidst to the believers, ´Is it not enough for you that your Lord should reinforce you with three thousand angels sent down upon you?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,125,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,125,125,N´Yea; if you are patient and godfearing, and the foe come against you instantly, your Lord will reinforce you with five thousand swooping angels.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,126,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,126,126,N´God wrought this not, save as good tiding to you, and that your hearts might be at rest; help comes only from God the All-mighty, the All-wise;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,127,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,127,127,N´and that He might cut off a part of the unbelievers or frustrate them, so that they turned in their tracks, disappointed.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,128,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,128,128,N´No part of the matter is thine, whether He turns towards them again, or chastises them; for they are evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,129,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,129,129,N´To God belongs all that is in the heavens and earth; He forgives whom He will, and chastises whom He will; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,130,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,130,130,N´O believers, devour not usury, doubled and redoubled, and fear you God; haply so you will prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,131,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,131,131,N´And fear the Fire prepared for the unbelievers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,132,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,132,132,N´and obey God and the Messenger; haply so you will find mercy.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,133,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,133,133,N´And vie with one another, hastening to forgiveness from your Lord, and to a garden whose breadth is as the heavens and earth, prepared for the godfearing´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,134,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,134,134,N´who expend in prosperity and adversity in almsgiving, and restrain their rage, and pardon the offences of their fellowmen; and God loves the good-doers;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,135,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,135,135,N´who, when they commit an indecency or wrong themselves, remember God, and pray forgiveness for their sins -- and who shall forgive sins but God? -- and do not persevere in the things they did and that wittingly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,136,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,136,136,N´Those -- their recompense is forgiveness from their Lord, and gardens beneath which rivers flow, therein dwelling forever; and how excellent is the wage of those who labour!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,137,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,137,137,N´Divers institutions have passed away before you; journey in the land, and behold how was the end of those that cried lies.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,138,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,138,138,N´This is an exposition for mankind, and a guidance, and an admonition for such as are godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,139,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,139,139,N´Faint not, neither sorrow; you shall be the upper ones if you are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,140,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,140,140,N´If a wound touches you, a like wound already has touched the heathen; such days We deal out in turn among men, and that God may know who are the believers, and that He may take witnesses from among you; and God loves not the evildoers;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,141,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,141,141,N´and that God may prove the believers, and blot out the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,142,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,142,142,N´Or did you suppose you should enter Paradise without God know who of you have struggled and who are patient?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,143,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,143,143,N´You were longing for death before you met it; now you have seen it, while you were beholding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,144,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,144,144,N´Muhammad is naught but a Messenger; Messengers have passed away before him. Why, if he should die or is slain, will you turn about on your heels? If any man should turn about on his heels, he will not harm God in any way; and God will recompense the thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,145,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,145,145,N´It is not given to any soul to die, save by the leave of God, at an appointed time. Whoso desires the reward of this world, We will give him of this; and whoso desires the reward of the other world, We will give him of that; and We will recompense the thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,146,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,146,146,N´Many a Prophet there has been, with whom thousands manifold have fought, and they fainted not for what smote them in God´s way, neither weakened, nor did they humble themselves; and God loves the patient.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,147,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,147,147,N´Nothing else they said but, ´Lord, forgive us our sins, and that we exceeded in our affair, and make firm our feet, and help us against the people of the unbelievers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,148,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,148,148,N´And God gave them the reward of this world and the fairest reward of the world to come; and God loves the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,149,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,149,149,N´O believers, if you obey the unbelievers they will turn you upon your heels, and you will turn about, losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,150,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,150,150,N´No; but God is your Protector, and He is the best of helpers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,151,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,151,151,N´We will cast into the hearts of the unbelievers terror, for that they have associated with God that for which He sent down never authority; their lodging shall be the Fire; evil is the lodging of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,152,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,152,152,N´God has been true in His promise towards you when you blasted them by His leave; until you lost heart, and quarrelled about the matter, and were rebellious, after He had shown you that you longed for. Some of you there are that desire this world, and some of you there are desire the next world. Then He turned you from them, that He might try you; and He has pardoned you; and God is bounteous to the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,153,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,153,153,N´When you were going up, not twisting about for anyone, and the Messenger was calling you in your rear; so He rewarded you with grief on grief that you might not sorrow for what escaped you neither for what smote you; and God is aware of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,154,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,154,154,N´Then He sent down upon you, after grief, security -- a slumber overcoming a party of you; and a party themselves had grieved, thinking of God thoughts that were not true such as the pagans thought, saying, ´Have we any part whatever in the affair?´ Say: ´The affair belongs to God entirely.´ They were concealing in their hearts that they show not to thee, saying, ´Ah, if we had had a part in the affair, never would we have been slain here.´ Say: ´Even if you had been in your houses, those for whom slaying was appointed would have sallied forth unto their last couches´; and that God might try what was in your breasts, and that He might prove what was in your hearts; and God knows the thoughts in the breasts.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,155,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,155,155,N´Those of you who turned away the day the two hosts encountered -- Satan made them slip for somewhat they had earned; but God has pardoned them; God is All-forgiving, All-clement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,156,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,156,156,N´O believers, be not as the unbelievers who say to their brothers, when they journey in the land, or are upon expeditions, ´If they had been with us, they would not have died and not been slain´ -- that God may make that an anguish in their hearts. For God gives life, and He makes to die; and God sees the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,157,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,157,157,N´If you are slain or die in God´s way, forgiveness and mercy from God are a better thing than that you amass;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,158,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,158,158,N´surely if you die or are slain, it is unto God you shall be mustered.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,159,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,159,159,N´It was by some mercy of God that thou wast gentle to them; hadst thou been harsh and hard of heart, they would have scattered from about thee. So pardon them, and pray forgiveness for them, and take counsel with them in the affair; and when thou art resolved, put thy trust in God; surely God loves those who put their trust.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,160,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,160,160,N´If God helps you, none can overcome you; but if He forsakes you, who then can help you after Him? Therefore in God let the believers put all their trust.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,161,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,161,161,N´It is not for a Prophet to be fraudulent; whoso defrauds shall bring the fruits of his fraud on the Day of Resurrection; then every soul shall be paid in full what it has earned, and they shall not be wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,162,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,162,162,N´What, is he who follows God´s good pleasure like him who is laden with the burden of God´s anger, whose refuge is Gehenna? An evil homecoming!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,163,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,163,163,N´They are in ranks with God; and God sees the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,164,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,164,164,N´Truly God was gracious to the believers when He raised up among them a Messenger from themselves, to recite to them His signs and to purify them, and to teach them the Book and the Wisdom, though before they were in manifest error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,165,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,165,165,N´Why, when an affliction visited you, and you had visited twice over the like of it, did you say, ´How is this?´ Say: ´This is from your own selves; surely God is powerful over everything.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,166,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,166,166,N´And what visited you, the day the two hosts encountered, was by God´s leave, and that He might know the believers;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,167,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,167,167,N´and that He might also know the hypocrites when it was said of them, ´Come now, fight in the way of God, or repel!´ They said, ´If only we knew how to fight, we would follow you.´ They that day were nearer to unbelief than to belief, saying with their mouths that which never was in their hearts; and God knows very well the things they hide;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,168,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,168,168,N´who said of their brothers (and they themselves held back), ´Had they obeyed us, they would not have been slain.´ Say: ´Then avert death from yourselves, if you speak truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,169,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,169,169,N´Count not those who were slain in God´s way as dead, but rather living with their Lord, by Him provided,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,170,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,170,170,N´rejoicing in the bounty that God has given them, and joyful in those who remain behind and have not joined them, because no fear shall be on them, neither shall they sorrow,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,171,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,171,171,N´joyful in blessing and bounty from God, and that God leaves not to waste the wage of the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,172,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,172,172,N´And those who answered God and the Messenger after the wound had smitten them - to all those of them who did good and feared God, shall be a mighty wage;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,173,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,173,173,N´those to whom the people said, ´The people have gathered against you, therefore fear them´, but it increased them in faith, and they said, ´God is sufficient for us; an excellent Guardian is He.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,174,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,174,174,N´So they returned with blessing and bounty from God, untouched by evil; they followed the good pleasure of God; and God is of bounty abounding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,175,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,175,175,N´That is Satan frightening his friends, therefore do not fear them; but fear you Me, if you are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,176,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,176,176,N´Let them not grieve thee that vie with one another in unbelief; they will nothing hurt God; God desires not to appoint for them a portion in the world to come, and there awaits them a mighty chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,177,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,177,177,N´Those who buy unbelief at the price of faith, they will nothing hurt God; and there awaits them a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,178,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,178,178,N´And let not the unbelievers suppose that the indulgence We grant them is better for them; We grant them indulgence only that they may increase in sin; and there awaits them a humbling chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,179,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,179,179,N´God will not leave the believers in the state in which you are, till He shall distinguish the corrupt from the good, and God will not inform you of the Unseen; but God chooses out of His Messengers whom He will. Believe you then in God and His Messengers; and if you believe and are godfearing, there shall be for you a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,180,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,180,180,N´But as for those who are niggardly with the bounty God has given them, let them not suppose it is better for them; nay, it is worse for them; that they were niggardly with they shall have hung about their necks on the Resurrection Day; and to God belongs the inheritance of the heavens and earth; and God is aware of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,181,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,181,181,N´God has heard the saying of those who said, ´Surely God is poor, and we are rich.´ We shall write down what they have said, and their slaying the Prophets without right, and We shall say, ´Taste the chastisement of the burning --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,182,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,182,182,N´that, for what your hands have forwarded, and for that God is never unjust unto His servants.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,183,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,183,183,N´Those same men said, ´God has made covenant with us, that we believe not any Messenger until he brings to us a sacrifice devoured by fire.´ Say: ´Messengers have come to you before me bearing clear signs, and that you spoke of; why therefore did you slay them, if you speak truly?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,184,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,184,184,N´But if they cry lies to thee, lies were cried to Messengers before thee, who came bearing clear signs, and the Psalms, and the Book Illuminating.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,185,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,185,185,N´Every soul shall taste of death; you she surely be paid in full your wages on the Day of Resurrection. Whosoever is removed from the Fire and admitted to Paradise, shall win the triumph. The present life is but the joy of delusion.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,186,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,186,186,N´You shall surely be tried in your possessions and your selves, and you shall hear from those who were given the Book before you, and from those who are idolaters, much hurt; but if you are patient and godfearing -- surely that is true constancy.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,187,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,187,187,N´And when God took compact with those who had been given the Book: ´You shall make it clear unto the people, and not conceal it.´ But they rejected it behind their backs and sold it for a small price -- how evil was that their selling!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,188,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,188,188,N´Reckon not that those who rejoice in what they have brought, and love to be praised for what they have not done -- do not reckon them secure from chastisement; for them awaits a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,189,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,189,189,N´To God belongs the Kingdom of the heavens and of the earth; and God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,190,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,190,190,N´Surely in the creation of the heavens and earth and in the alternation of night and day there are signs for men possessed of minds´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,191,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,191,191,N´who remember God, standing and sitting and on their sides, and reflect upon the creation of the heavens and the earth: ´Our Lord, Thou hast not created this for vanity. Glory be to Thee! Guard us against the chastisement of the Fire.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,192,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,192,192,N´Our Lord, whomsoever Thou admittest into the Fire, Thou wilt have abased; and the evildoers shall have no helpers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,193,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,193,193,N´Our Lord, we have heard a caller calling us to belief, saying, \"Believe you in your Lord!\" And we believe. Our Lord, forgive Thou us our sins and acquit us of our evil deeds, and take us to Thee with the pious.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,194,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,194,194,N´Our Lord, give us what Thou hast promised us by Thy Messengers, and abase us not on the Day of Resurrection; Thou wilt not fail the tryst.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,195,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,195,195,N´And their Lord answers them: ´I waste not the labour of any that labours among you, be you male or female -- the one of you is as the other. And those who emigrated, and were expelled from their habitations, those who suffered hurt in My way, and fought, and were slain -- them I shall surely acquit of their evil deeds, and I shall admit them to gardens underneath which rivers flow.´ A reward from God! And God with Him is the fairest reward.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,196,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,196,196,N´Let it not delude thee, that the unbelievers go to and fro in the land;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,197,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,197,197,N´a little enjoyment, then their refuge is Gehenna -- an evil cradling!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,198,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,198,198,N´But those who fear their Lord - for them shall be gardens underneath which rivers flow, therein dwelling forever -- a hospitality God Himself offers; and that which is with God is better for the pious.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,199,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,199,199,N´And some there are of the People of the Book who believe in God, and what has been sent down unto you, and what has been sent down unto them, men humble to God, not selling the signs of God for a small price; those -- their wage is with their Lord; God is swift at the reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,3,200,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,3,200,200,N´O believers, be patient, and vie you in patience; be steadfast; fear God; haply so you will prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,1,1,N´Mankind, fear your Lord, who created you of a single soul, and from it created its mate, and from the pair of them scattered abroad many men and women; and fear God by whom you demand one of another, and the wombs; surely God ever watches over you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,2,2,N´Give the orphans their property, and do not exchange the corrupt for the good; and devour not their property with your property; surely that is a great crime.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,3,3,N´If you fear that you will not act justly towards the orphans, marry such women as seem good to you, two, three, four; but if you fear you will not be equitable, then only one, or what your right hands own; so it is likelier you will not be partial.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,4,4,N´And give the women their dowries as a gift spontaneous; but if they are pleased to offer you any of it, consume it with wholesome appetite.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,5,5,N´But do not give to fools their property that God has assigned to you to manage; provide for them and clothe them out of it, and speak to them honourable words.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,6,6,N´Test well the orphans, until they reach the age of marrying; then, if you perceive in them right judgment, deliver to them their property; consume it not wastefully and hastily ere they are grown. If any man is rich, let him be abstinent; if poor, let him consume in reason. And when you deliver to them their property, take witnesses over them; God suffices for a reckoner.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,7,7,N´To the men a share of what parents and kinsmen leave, and to the women a share of what parents and kinsmen leave, whether it be little or much, a share apportioned;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,8,8,N´and when the division is attended by kinsmen and orphans and the poor, make provision for them out of it, and speak to them honourable words.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,9,9,N´And let those fear who, if they left behind them weak seed, would be afraid on their account, and let them fear God, and speak words hitting the mark.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,10,10,N´Those who devour the property of orphans unjustly, devour Fire in their bellies, and shall assuredly roast in a Blaze.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,11,11,N´God charges you, concerning your children: to the male the like of the portion of two females, and if they be women above two, then for them two-thirds of what he leaves, but if she be one then to her a half; and to his parents to each one of the two the sixth of what he leaves, if he has children; but if he has no children, and his heirs are his parents, a third to his mother, or, if he has brothers, to his mother a sixth, after any bequest he may bequeath, or any debt. Your fathers and your sons -- you know not which out of them is nearer in profit to you. So God apportions; surely God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,12,12,N´And for you a half of what your wives leave, if they have no children; but if they have children, then for you of what they leave a fourth, after any bequest they may bequeath, or any debt. And for them a fourth of what you leave, if you have no children; but if you have children, then for them of what you leave an eighth, after any bequest you may bequeath, or any debt. If a man or a woman have no heir direct, but have a brother or a sister, to each of the two a sixth; but if they are more numerous than that, they share equally a third, after any bequest he may bequeath, or any debt not prejudicial; a charge from God. God is All-knowing, All-clement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,13,13,N´Those are God´s bounds. Whoso obeys God and His Messenger, He will admit him to gardens underneath which rivers flow, therein dwelling forever; that is the mighty triumph.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,14,14,N´But whoso disobeys God, and His Messenger, and transgresses His bounds, him He will admit to a Fire, therein dwelling forever, and for him there awaits a humbling chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,15,15,N´Such of your women as commit indecency, call four of you to witness against them; and if they witness, then detain them in their houses until death takes them or God appoints for them a way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,16,16,N´And when two of you commit indecency, punish them both; but if they repent and make amends, then suffer them to be; God turns, and is All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,17,17,N´God shall turn only towards those who do evil in ignorance, then shortly repent; God will return towards those; God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,18,18,N´But God shall not turn towards those who do evil deeds until, when one of them is visited by death, he says, ´Indeed now I repent,´ neither to those who die disbelieving; for them We have prepared a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,19,19,N´O believers, it is not lawful for you to inherit women against their will; neither debar them, that you may go off with part of what you have given them, except when they commit a flagrant indecency Consort with them honourably; or if you are averse to them, it is possible you may be averse to a thing, and God set in it much good.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,20,20,N´And if you desire to exchange a wife in place of another, and you have given to one a hundredweight, take of it nothing. What, will you take it by way of calumny and manifest sin?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,21,21,N´How shall you take it, when each of you has been privily with the other, and they have taken from you a solemn compact?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,22,22,N´And do not marry women that your fathers married, unless it be a thing of the past; surely that is indecent and hateful; an evil way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,23,23,N´Forbidden to you are your mothers and daughters, your sisters, your aunts paternal and maternal, your brother´s daughters, your sister´s daughters, your mothers who have given suck to you, your suckling sisters, your wives´ mothers, your stepdaughters who are in your care being born of your wives you have been in to -- but if you have not yet been in to them it is no fault in you - and the spouses of your sons who are of your loins, and that you should take to you two sisters together, unless it be a thing of the past; God is All-forgiving, All-compassionate;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,24,24,N´and wedded women, save what your right hands own. So God prescribes for you. Lawful for you, beyond all that, is that you may seek, using your wealth, in wedlock and not in licence. Such wives as you enjoy thereby, give them their wages apportionate; it is no fault in you in your agreeing together, after the due apportionate. God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,25,25,N´Any one of you who has not the affluence to be able to marry believing freewomen in wedlock, let him take believing handmaids that your right hands own; God knows very well your faith; the one of you is as the other. So marry them, with their people´s leave, and give them their wages honourably as women in wedlock, not as in licence or taking lovers. But when they are in wedlock, if they commit indecency, they shall be liable to half the chastisement of freewomen. That provision is for those of you who fear fornication; yet it is better for you to be patient. God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,26,26,N´God desires to make clear to you, and to guide you in the institutions of those before you, and to turn towards you; God is All-knowing, All-wise;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,27,27,N´and God desires to turn towards you, but those who follow their lusts desire you to swerve away mightily.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,28,28,N´God desires to lighten things for you, for man was created a weakling.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,29,29,N´O believers, consume not your goods between you in vanity, except there be trading, by your agreeing together. And kill not one another. Surely God is compassionate to you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,30,30,N´But whosoever does that in transgression and wrongfully, him We shall certainly roast at a Fire; and that for God is an easy matter.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,31,31,N´If you avoid the heinous sins that are forbidden you, We will acquit you of your evil deeds, and admit you by the gate of honour.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,32,32,N´Do not covet that whereby God in bounty has preferred one of you above another. To the men a share from what they have earned, and to the women a share from what they have earned. And ask God of His bounty; God knows everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,33,33,N´To everyone We have appointed heirs of that which parents and kinsmen leave, and those with whom you have sworn compact. So give to them their share; God is witness over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,34,34,N´Men are the managers of the affairs of women for that God has preferred in bounty one of them over another, and for that they have expended of their property. Righteous women are therefore obedient, guarding the secret for God´s guarding. And those you fear may be rebellious admonish; banish them to their couches, and beat them. If they then obey you, look not for any way against them; God is All-high, All-great.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,35,35,N´And if you fear a breach between the two, bring forth an arbiter from his people and from her people an arbiter, if they desire to set things right; God will compose their differences; surely God is All-knowing, All-aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,36,36,N´Serve God, and associate naught with Him. Be kind to parents, and the near kinsman, and to orphans, and to the needy, and to the neighbour who is of kin, and to the neighbour who is a stranger, and to the companion at your side, and to the traveller, and to that your right hands own. Surely God loves not the proud and boastful´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,37,37,N´such as are niggardly, and bid other men to be niggardly, and themselves conceal the bounty that God has given them. We have prepared for the unbelievers a humbling chastisement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,38,38,N´and such as expend of their substance to show off to men, and believe not in God and the Last Day. Whosoever has Satan for a comrade, an evil comrade is he.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,39,39,N´Why, what would it harm them, if they believed in God and the Last Day, and expended of that God has provided them? God knows them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,40,40,N´Surely God shall not wrong so much as the weight of an ant; and if it be a good deed He will double it, and give from Himself a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,41,41,N´How then shall it be, when We bring forward from every nation a witness, and bring thee to witness against those?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,42,42,N´Upon that day the unbelievers, those who have disobeyed the Messenger, will wish that the earth might be levelled with them; and they will not conceal from God one tiding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,43,43,N´O believers, draw not near to prayer when you are drunken until you know what you are saying, or defiled -- unless you are traversing a way -- until you have washed yourselves; but if you are sick, or on a journey, or if any of you comes from the privy, or you have touched women, and you can find no water, then have recourse to wholesome dust and wipe your faces and your hands; God is All-pardoning, All-forgiving.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,44,44,N´Hast thou not regarded those who were given a share of the Book purchasing error, and desiring that you should also err from the way?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,45,45,N´God knows well your enemies; God suffices as a protector, God suffices as a helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,46,46,N´Some of the Jews pervert words from their meanings saying, ´We have heard and we disobey´ and ´Hear, and be thou not given to hear´ and ´Observe us,´ twisting with their tongues and traducing religion. If they had said, ´We have heard and obey´ and ´Hear´ and ´Regard us,´ it would have been better for them, and more upright; but God has cursed them for their unbelief so they believe not except a few.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,47,47,N´You who have been given the Book, believe in what We have sent down, confirming what is with you, before We obliterate faces, and turn them upon their backs, or curse them as We cursed the Sabbath-men, and God´s command is done.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,48,48,N´God forgives not that aught should be with Him associated; less than that He forgives to whomsoever He will. Whoso associates with God anything, has indeed forged a mighty sin.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,49,49,N´Hast thou not regarded those who purify themselves? Nay; only God purifies whom He will; and they shall not be wronged a single date-thread.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,50,50,N´Consider how they forge falsehood against God; and that suffices for a manifest sin;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,51,51,N´Hast thou not regarded those who were given a share of the Book believing in demons and idols, and saying to the unbelievers, ´These are more rightly guided on the way than the believers´?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,52,52,N´Those are they whom God has cursed; he whom God has cursed, thou wilt not find for him any helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,53,53,N´Or have they a share in the Kingdom? If that is so, they do not give the people a single date-spot.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,54,54,N´Or are they jealous of the people for the bounty that God has given them? Yet We gave the people of Abraham the Book and the Wisdom, and We gave them a mighty kingdom.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,55,55,N´And some of them there are that believe, and some of them that bar from it; Gehenna suffices for a Blaze!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,56,56,N´Surely those who disbelieve in Our signs -- We shall certainly roast them at a Fire; as often as their skins are wholly burned, We shall give them in exchange other skins, that they may taste the chastisement. Surely God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,57,57,N´And those that believe, and do deeds of righteousness, them We shall admit to gardens underneath which rivers flow, therein dwelling forever and ever; therein for them shall be spouses purified, and We shall admit them to a shelter of plenteous shade.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,58,58,N´God commands you to deliver trusts back to their owners; and when you judge between the people, that you judge with justice. Good is the admonition God gives you; God is All-hearing, All-seeing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,59,59,N´O believers, obey God, and obey the Messenger and those in authority among you. If you should quarrel on anything, refer it to God and the Messenger, if you believe in God and the Last Day; that is better, and fairer in the issue.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,60,60,N´Hast thou not regarded those who assert that they believe in what has been sent down to thee, and what was sent down before thee, desiring to take their disputes to idols, yet they have been commanded to disbelieve in them? But Satan desires to lead them astray into far error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,61,61,N´And when it is said to them, ´Come now to what God has sent down, and the Messenger,´ then thou seest the hypocrites barring the way to thee.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,62,62,N´How shall it be, when they are visited by an affliction for what their own hands have forwarded, then they come to thee swearing by God, ´We sought only kindness and conciliation´?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,63,63,N´Those -- God knows what is in their hearts; so turn away from them, and admonish them, and say to them penetrating words about themselves.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,64,64,N´We sent not ever any Messenger, but that he should he obeyed, by the leave of God. If; when they wronged themselves, they had come to thee, and prayed forgiveness of God, and the Messenger had prayed forgiveness for them, they would have found God turns, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,65,65,N´But no, by thy Lord! they will not believe till they make thee the judge regarding the disagreement between them, then they shall find in themselves no impediment touching thy verdict, but shall surrender in full submission.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,66,66,N´But had We prescribed for them, saying, ´Slay yourselves´ or ´Leave your habitations,´ they would not have done it, save a few of them; yet if they had done as they were admonished it would have been better for them, and stronger confirming them,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,67,67,N´and then We surely would have given them from Us a mighty wage,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,68,68,N´and guided them on a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,69,69,N´Whosoever obeys God, and the Messenger -- they are with those whom God has blessed, Prophets, just men, martyrs, the righteous; good companions they!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,70,70,N´That is the bounty from God; God suffices as One who knows.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,71,71,N´O believers, take your precautions; then move forward in companies, or move forward all together.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,72,72,N´Some of you there are that are dilatory; then, if an affliction visits you, he says, ´God has blessed me, in that I was not a martyr with them.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,73,73,N´But if a bounty from God visits you, he will surely say, as if there had never been any affection between you and him, ´Would that I had been with them, to attain a mighty triumph!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,74,74,N´So let them fight in the way of God who sell the present life for the world to come; and whosoever fights in the way of God and is slain, or conquers, We shall bring him a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,75,75,N´How is it with you, that you do not fight in the way of God, and for the men, women, and children who, being abased, say, ´Our Lord, bring us forth from this city whose people are evildoers, and appoint t us a protector from Thee, and appoint to us from Thee a helper´?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,76,76,N´The believers fight in the way of God, and the unbelievers fight in the idols´ way. Fight you therefore against the friends of Satan; surely the guile of Satan is ever feeble.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,77,77,N´Hast thou not regarded those to whom it was said, ´Restrain your hands, and perform the prayer, and pay the alms´? Then, as soon as fighting is prescribed for them, there is a party of them fearing the people as they would fear God, or with a greater fear, and they say, ´Our Lord, why hast thou prescribed fighting for us? Why not defer us to a near term?´ Say: ´The enjoyment of this world is little; the world to come is better for him who fears God; you shall not be wronged a single date-thread.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,78,78,N´Wherever you may be, death will overtake you, though you should be in raised-up towers. And if a good thing visits them, they say, ´This is from God´; but if an evil thing visits them, they say, ´This is from thee.´ Say: ´Everything is from God.´ How is it with this people? They scarcely understand any tiding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,79,79,N´Whatever good visits thee, it is of God; whatever evil visits thee is of thyself. And We have sent thee to men a Messenger; God suffices for a witness.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,80,80,N´Whosoever obeys the Messenger, thereby obeys God; and whosoever turns his back - We have not sent thee to be a watcher over them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,81,81,N´They say, ´Obedience´; but when they sally forth from thee, a party of them meditate all night on other than what thou sayest. God writes down their meditations; so turn away from them, and put thy trust in God; God suffices for a guardian.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,82,82,N´What, do they not ponder the Koran? If it had been from other than God surely they would have found in it much inconsistency.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,83,83,N´When there comes to them a matter, be it of security or fear, they broadcast it; if they had referred it to the Messenger and to those in authority among them, those of them whose task it is to investigate would have known the matter. And but for the bounty of God to you, and His mercy, you would surely have followed Satan, except a few.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,84,84,N´So do thou fight in the way of God; thou art charged only with thyself. And urge on the believers; haply God will restrain the unbelievers´ might; God is stronger in might, more terrible in punishing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,85,85,N´Whoso intercedes with a good intercession shall receive a share of it; whosoever intercedes with a bad intercession, he shall receive the like of it; God has power over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,86,86,N´And when you are greeted with a greeting greet with a fairer than it, or return it; surely God keeps a watchful count over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,87,87,N´God -- there is no god but He. He will surely gather you to the Resurrection Day, no doubt of it. And who is truer in tidings than God?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,88,88,N´How is it with you, that you are two parties touching the hypocrites, and God has overthrown them for what they earned? What, do you desire to guide him whom God has led astray? Whom God leads astray, thou wilt not find for him a way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,89,89,N´They wish that you should disbelieve as they disbelieve, and then you would be equal; therefore take not to yourselves friends of them, until they emigrate in the way of God; then, if they turn their backs, take them, and slay them wherever you find them; take not to yourselves any one of them as friend or helper´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,90,90,N´except those that betake themselves to a people who are joined with you by a compact, or come to you with breasts constricted from fighting with you or fighting their people. Had God willed, He would have given them authority over you, and then certainly they would have fought you. If they withdraw from you, and do not fight you, and offer you peace, then God assigns not any way to you against them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,91,91,N´You will find others desiring to be secure from you, and secure from their people, yet whenever they are returned to temptation, they are overthrown in it. If they withdraw not from you, and offer you peace, and restrain their hands, take them, and slay them wherever you come on them; against them We have given you a clear authority.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,92,92,N´It belongs not to a believer to slay a believer, except it be by error. If any slays a believer by error, then let him set free a believing slave, and bloodwit is to be paid to his family unless they forgo it as a freewill offering. If he belong to a people at enmity with you and is a believer, let the slayer set free a believing slave. If he belong to a people joined with you by a compact, then bloodwit is to be paid to his family and the slayer shall set free a believing slave. But if he finds not the means, let him fast two successive months -- God´s turning; God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,93,93,N´And whoso slays a believer wilfully, his recompense is Gehenna, therein dwelling forever, and God will be wroth with him and will curse him, and prepare for him a mighty chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,94,94,N´O believers, when you are journeying in the path of God, be discriminating, and do not say to him who offers you a greeting, ´Thou art not a believer,´ seeking the chance goods of the present life. With God are spoils abundant. So you were aforetime; but God has been gracious to you. So be discriminating; surely God is aware of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,95,95,N´Such believers as sit at home -- unless they have an injury -- are not the equals of those who struggle in the path of God with their possessions and their selves. God has preferred in rank those who struggle with their possessions and their selves over the ones who sit at home; yet to each God has promised the reward most fair; and God has preferred those who struggle over the ones who sit at home for the bounty of a mighty wage,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,96,96,N´in ranks standing before Him, forgiveness and mercy; surely God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,97,97,N´And those the angels take, while still they are wronging themselves -- the angels will say, ´In what circumstances were you?´ They will say, ´We were abased in the earth.´ The angels will say, ´But was not God´s earth wide, so that you might have emigrated in it?´ Such men, their refuge shall be Gehenna -- an evil homecoming! --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,98,98,N´except the men, women, and children who, being abased, can devise nothing and are not guided to a way;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,99,99,N´haply them God will yet pardon, for God is All-pardoning, All-forgiving.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,100,100,N´Whoso emigrates in the way of God will find in the earth many refuges and plenty; whoso goes forth from his house an emigrant to God and His Messenger, and then death overtakes him, his wage shall have fallen on God; surely God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,101,101,N´And when you are journeying in the land there is no fault in you that you shorten the prayer, if you fear the unbelievers may afflict you; the unbelievers are for you a manifest foe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,102,102,N´When thou art amongst them, and performest for them the prayer, let a party of them stand with thee, and let them take their weapons. When they bow themselves, let them be behind you; and let another party who have not prayed come and pray with thee, taking their precautions and their weapons. The unbelievers wish that you should be heedless of your weapons and your baggage, then they would wheel on you all at once. There is no fault in you, if rain molests you, or you are sick, to lay aside your weapons; but take your precautions. God has prepared for the unbelievers a humbling chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,103,103,N´When you have performed the prayer, remember God, standing and sitting and on your sides. Then, when you are secure, perform the prayer; surely the prayer is a timed prescription for the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,104,104,N´Faint not in seeking the heathen; if you are suffering, they are also suffering as you are suffering, and you are hoping from God for that for which they cannot hope; God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,105,105,N´Surely We have sent down to thee the Book with the truth, so that thou mayest judge between the people by that God has shown thee. So be not an advocate for the traitors;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,106,106,N´and pray forgiveness of God; surely God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,107,107,N´And do not dispute on behalf of those who betray themselves; surely God loves not the guilty traitor.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,108,108,N´They hide themselves from men, but hide not themselves from God; for He is with them while they meditate at night discourse unpleasing to Him; God encompasses the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,109,109,N´Ha, there you are; you have disputed on their behalf in the present life; but who will dispute with God on their behalf on the Resurrection Day, or who will be a guardian for them?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,110,110,N´Whosoever does evil, or wrongs himself; and then prays God´s forgiveness, he shall find God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,111,111,N´And whosoever earns a sin, earns it against himself only; and God is ever All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,112,112,N´And whosoever earns a fault or a sin and then casts it upon the innocent, thereby has laid upon himself calumny and manifest sin.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,113,113,N´But for God´s bounty to thee and His mercy a party of them purposed to lead thee astray; but they lead only themselves astray; they do not hurt thee in anything. God has sent down on thee the Book and the Wisdom, and He has taught thee that thou knewest not; God´s´ bounty to thee is ever great.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,114,114,N´No good is there in much of their conspiring, except for him who bids to freewill offering, or honour, or setting things right between the people. Whoso does that, seeking God´s good pleasure, We shall surely give him a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,115,115,N´But whoso makes a breach with the Messenger after the guidance has become clear to him and follows a way other than the believers´, him We shall turn over to what he has turned to and We shall roast him in Gehenna - an evil homecoming!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,116,116,N´God forgives not that aught should be with Him associated; less than that He forgives to whomsoever He will. Whoso associates with God anything, has gone astray into far error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,117,117,N´In stead of Him, they pray not except to female beings; they pray not except to a rebel Satan accursed by God.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,118,118,N´He said, ´Assuredly I will take unto myself a portion appointed of Thy servants,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,119,119,N´and I will lead them astray, and fill them with fancies and I will command them and they will cut off the cattle´s ears; I will command them and they will alter God´s creation.´ Whoso takes Satan to him for a friend, instead of God, has surely suffered a manifest loss.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,120,120,N´He promises them and fills them with fancies, but there is nothing Satan promises them except delusion.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,121,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,121,121,N´Such men -- their refuge shall be Gehenna, and they shall find no asylum from it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,122,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,122,122,N´But those that believe, and do deeds of righteousness, them We shall admit to gardens underneath which rivers flow, therein dwelling for ever and ever; God´s promise in truth; and who is truer in speech than God?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,123,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,123,123,N´It is not your fancies, nor the fancies of the People of the Book. Whosoever does evil shall be recompensed for it, and will not find for him, apart from God, a friend or helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,124,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,124,124,N´And whosoever does deeds of righteousness, be it male or female, believing - they shall enter Paradise, and not be wronged a single date-spot.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,125,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,125,125,N´And who is there that has a fairer religion than he who submits his will to God being a good-doer, and who follows the creed of Abraham, a man of pure faith? And God took Abraham for a friend.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,126,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,126,126,N´To God belongs all that is in the heavens and in the earth, and God encompasses everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,127,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,127,127,N´They will ask thee for a pronouncement concerning women. Say: ´God pronounces to you concerning them, and what is recited to you in the Book concerning the orphan women to whom you give not what is prescribed for them, and yet desire to marry them, and the oppressed children, and that you secure justice for orphans. Whatever good you do, God knows of it.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,128,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,128,128,N´If a woman fear rebelliousness or aversion in her husband, there is no fault in them if the couple set things right between them; right settlement is better; and souls are very prone to avarice. If you do good and are godfearing, surely God is aware of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,129,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,129,129,N´You will not be able to be equitable between your wives, be you ever so eager; yet do not be altogether partial so that you leave her as it were suspended. If you set things right, and are godfearing, God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,130,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,130,130,N´But if they separate, God will enrich each of them of His plenty; God is All-embracing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,131,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,131,131,N´To God belongs all that is in the heavens and in the earth. We have charged those who were given the Book before you, and you, ´Fear God.´ If you disbelieve, to God belongs all that is in the heavens and in the earth; God is All-sufficient, All-laudable.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,132,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,132,132,N´To God belongs all that is in the heavens and in the earth; God suffices for a guardian.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,133,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,133,133,N´If He will, He can put you away, O men, and bring others; surely God is powerful over that.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,134,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,134,134,N´Whoso desires the reward of this world, with God is the reward of this world and of the world to come; God is All-hearing, All-seeing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,135,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,135,135,N´O believers, be you securers of justice, witnesses for God, even though it be against yourselves, or your parents and kinsmen, whether the man be rich or poor; God stands closest to either. Then follow not caprice, so as to swerve; for if you twist or turn, God is aware of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,136,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,136,136,N´O believers, believe in God and His Messenger and the Book He has sent down on His Messenger and the Book which He sent down before. Whoso disbelieves in God and His angels and His Books, and His Messengers, and the Last Day, has surely gone astray into far error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,137,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,137,137,N´Those who believe, and then disbelieve, and then believe, and then disbelieve, and then increase in unbelief-God is not likely to forgive them, neither to guide them on any way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,138,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,138,138,N´Give thou good tidings to the hypocrites that for them awaits a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,139,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,139,139,N´Those who take unbelievers for their friends instead of believers -- do they seek glory in them? But glory altogether belongs to God.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,140,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,140,140,N´He has sent down upon you in the Book: ´When you hear God´s signs being disbelieved and made mock of, do not sit with them until they plunge into some other talk, or else you will surely be like to them.´ God will gather the hypocrites and the unbelievers all in Gehenna.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,141,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,141,141,N´Those who wait upon you and, if a victory comes to you from God, say, ´Were we not with you?´ but if the unbelievers get a share, they say, ´Did we not gain the mastery over you, and did we not defend you from the believers?´ God will judge between you on the Resurrection Day, and God will not grant the unbelievers any way over the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,142,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,142,142,N´The hypocrites seek to trick God, but God is tricking them. When they stand up to pray they stand up lazily, showing off to the people and not remembering God save a little;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,143,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,143,143,N´wavering all the time -- not to these, not to those; and whom God leads astray, thou wilt not find for him a way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,144,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,144,144,N´O believers, take not the unbelievers as friends instead of the believers; or do you desire to give God over you a clear authority?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,145,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,145,145,N´Surely the hypocrites will be in the lowest reach of the Fire; thou wilt not find for them any helper;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,146,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,146,146,N´save such as repent, and make amends, and hold fast to God, and make their religion sincerely God´s; those are with the believers, and God will certainly give the believers a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,147,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,147,147,N´What would God do with chastising you if you are thankful, and believe? God is All-thankful, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,148,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,148,148,N´God likes not the shouting of evil words unless a man has been wronged; God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,149,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,149,149,N´If you do good openly or in secret or pardon an evil, surely God is All-pardoning, All-powerful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,150,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,150,150,N´Those who disbelieve in God and His Messengers and desire to make division between God and His Messengers, and say, ´We believe in part, and disbelieve in part,´ desiring to take between this and that a way --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,151,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,151,151,N´those in truth are the unbelievers; and We have prepared for the unbelievers a humbling chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,152,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,152,152,N´And those who believe in God and His Messengers and make no division between any of them, those -- We shall surely give them their wages; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,153,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,153,153,N´The People of the Book will ask thee to bring down upon them a Book from heaven; and they asked Moses for greater than that, for they said, ´Show us God openly.´ And the thunderbolt took them for their evildoing. Then they took to themselves the Calf, after the clear signs had come to them; yet We pardoned them that, and We bestowed upon Moses a clear authority.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,154,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,154,154,N´And We raised above them the Mount, taking compact with them; and We said to them, ´Enter in at the gate, prostrating´; and We said to them, ´Transgress not the Sabbath´; and We took from them a solemn compact.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,155,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,155,155,N´So, for their breaking the compact, and disbelieving in the signs of God, and slaying the Prophets without right, and for their saying, ´Our hearts are uncircumcised´ -- nay, but God sealed them for their unbelief, so they believe not, except a few --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,156,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,156,156,N´and for their unbelief, and their uttering against Mary a mighty calumny,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,157,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,157,157,N´and for their saying, ´We slew the Messiah, Jesus son of Mary, the Messenger of God´ -- yet they did not slay him, neither crucified him, only a likeness of that was shown to them. Those who are at variance concerning him surely are in doubt regarding him; they have no knowledge of him, except the following of surmise; and they slew him not of a certainty -- no indeed;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,158,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,158,158,N´God raised him up to Him; God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,159,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,159,159,N´There is not one of the People of the Book but will assuredly believe in him before his death, and on the Resurrection Day he will be a witness against them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,160,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,160,160,N´And for the evildoing of those of Jewry, We have forbidden them certain good things that were permitted to them, and for their barring from God´s way many,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,161,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,161,161,N´and for their taking usury, that they were prohibited, and consuming the wealth of the people in vanity; and We have prepared for the unbelievers among them a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,162,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,162,162,N´But those of them that are firmly rooted in knowledge, and the believers believing in what has been sent down to thee, and what was sent down before thee, that perform the prayer and pay the alms, and those who believe in God and the Last Day - them We shall surely give a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,163,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,163,163,N´We have revealed to thee as We revealed to Noah, and the Prophets after him, and We revealed to Abraham, Ishmael, Isaac, Jacob, and the Tribes, Jesus and Job, Jonah and Aaron and Solomon, and We gave to David Psalms,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,164,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,164,164,N´and Messengers We have already told thee of before, and Messengers We have not told thee of; and unto Moses God spoke directly --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,165,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,165,165,N´Messengers bearing good tidings, and warning, so that mankind might have no argument against God, after the Messengers; God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,166,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,166,166,N´But God bears witness to that He has sent down to thee; He has sent it down with His knowledge; and the angels also bear witness; and God suffices for a witness.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,167,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,167,167,N´Surely those who disbelieve, and bar from the way of God, have gone astray into far error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,168,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,168,168,N´Surely the unbelievers, who have done evil, God would not forgive them, neither guide them on any road´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,169,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,169,169,N´but the road to Gehenna, therein dwelling forever and ever; and that for God is an easy matter.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,170,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,170,170,N´O men, the Messenger has now come to you with the truth from your Lord; so believe; better is it for you. And if you disbelieve, to God belongs all that is in the heavens and in the earth; and God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,171,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,171,171,N´People of the Book, go not beyond the bounds in your religion, and say not as to God but the truth. The Messiah, Jesus son of Mary, was only the Messenger of God, and His Word that He committed to Mary, and a Spirit from Him. So believe in God and His Messengers, and say not, ´Three.´ Refrain; better is it for you. God is only One God. Glory be to Him -- That He should have a son! To Him belongs all that is in the heavens and in the earth; God suffices for a guardian.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,172,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,172,172,N´The Messiah will not disdain to be a servant of God, neither the angels who are near stationed to Him. Whosoever disdains to serve Him, and waxes proud, He will assuredly muster them to Him, all of them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,173,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,173,173,N´As for the believers, who do deeds of righteousness, He will pay them in full their wages, and He will give them more, of His bounty; and as for them who disdain, and wax proud, them He will chastise with a painful chastisement, and they shall not find for them, apart from God, a friend or helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,174,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,174,174,N´O men, a proof has now come to you from your Lord; We have sent down to you a manifest light.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,175,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,175,175,N´As for those who believe in God, and hold fast to Him, He will surely admit them to mercy from Him, and bounty, and will guide them to Him on a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,4,176,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,4,176,176,N´They will ask thee for a pronouncement. Say: ´God pronounces to you concerning the indirect heirs. If a man perishes having no children, but he has a sister, she shall receive a half of what he leaves, and he is her heir if she has no children. If there be two sisters, they shall receive two-thirds of what he leaves; if there be brothers and sisters, the male shall receive the portion of two females. God makes clear to you, lest you go astray; God has knowledge of everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,1,1,N´O believers, fulfil your bonds. Permitted to you is the beast of the flocks, except that which is now recited to you, so that you deem not game permitted to be hunted when you are in pilgrim sanctity. God decrees whatsoever He desires.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,2,2,N´O believers, profane not God´s waymarks nor the holy month, neither the offering, nor the necklaces, nor those repairing to the Holy House seeking from their Lord bounty and good pleasure. But when you have quit your pilgrim sanctity, then hunt for game. Let not detestation for a people who barred you from the Holy Mosque move you to commit aggression. Help one another to piety and godfearing; do not help each other to sin and enmity. And fear God; surely God is terrible in retribution.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,3,3,N´Forbidden to you are carrion, blood, the flesh of swine, what has been hallowed to other than God, the beast strangled; the beast beaten down, the beast fallen to death, the beast gored, and that devoured by beasts of prey - excepting that you have sacrificed duly -- as also things sacrificed to idols, and partition by the divining arrows; that is ungodliness. Today the unbelievers have despaired of your religion; therefore fear them not, but fear you Me. Today I have perfected your religion for you, and I have completed My blessing upon you, and I have approved Islam for your religion. But whosoever is constrained in emptiness and not inclining purposely to sin -- God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,4,4,N´They will question thee what is permitted them. Say: ´The good things are permitted you; and such hunting creatures as you teach, training them as hounds, and teaching them as God has taught you -- eat what they seize for you, and mention God´s Name over it. Fear God; God is swift at the reckoning.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,5,5,N´Today the good things are permitted you, and the food of those who were given the Book is permitted to you, and permitted to them is your food; Likewise believing women in wedlock, and in wedlock women of them who were given the Book before you if you give them their wages, in wedlock and not in licence, or as taking lovers. Whoso disbelieves in the faith, his work has failed, and in the world to come he shall be among the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,6,6,N´O believers, when you stand up to pray wash your faces, and your hands up to the elbows, and wipe your heads, and your feet up to the ankles. If you are defiled, purify yourselves; but if you are sick or on a journey, or if any of you comes from the privy, or you have touched women, and you can find no water, then have recourse to wholesome dust and wipe your faces and your hands with it. God does not desire to make any impediment for you; but He desires to purify you, and that He may complete His blessing upon you; haply you will be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,7,7,N´And remember God´s blessing upon you, and His compact which He made with you when you said, ´We have heard and we obey. And fear you God; surely God knows the thoughts in the breasts.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,8,8,N´O believers, be you securers of justice, witnesses for God. Let not detestation for a people move you not to be equitable; be equitable -- that is nearer to godfearing. And fear God; surely God is aware of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,9,9,N´God has promised those that believe, and do deeds of righteousness; they shall have; forgiveness and a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,10,10,N´And the unbelievers, who cried lies to Our signs -- they shall be the inhabitants of Hell.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,11,11,N´O believers, remember God´s blessing upon you, when a certain people purposed to stretch against you their hands, and He restrained their hands from you; and fear God; and in God let the believers put all their trust.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,12,12,N´God took compact with the Children of Israel; and We raised up from among them twelve chieftains. And God said, ´I am with you. Surely, if you perform the prayer, and pay the alms, and believe in My Messengers and succour them, and lend to God a good loan, I will acquit you of your evil deeds, and I will admit you to gardens underneath which rivers flow. So whosoever of you thereafter disbelieves, surely he has gone astray from the right way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,13,13,N´So for their breaking their compact We cursed them and made their hearts hard, they perverting words from their meanings; and they have forgotten a portion of that they were reminded of; and thou wilt never cease to light upon some act of treachery on their part, except a few of them. Yet pardon them, and forgive; surely God loves the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,14,14,N´And with those who say ´We are Christians´ We took compact; and they have forgotten a portion of that they were reminded of. So We have stirred up among them enmity and hatred, till the Day of Resurrection; and God will assuredly tell them of the things they wrought.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,15,15,N´People of the Book, now there has come to you Our Messenger, making clear to you many things you have been concealing of the Book, and effacing many things. There has come to you from God a light, and a Book Manifest´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,16,16,N´whereby God guides whosoever follows His good pleasure in the ways of peace, and brings them forth from the shadows into the light by His leave; and He guides them to a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,17,17,N´They are unbelievers who say, ´God is the Messiah, Mary´s son.´ Say: ´Who then shall overrule God in any way if He desires to destroy the Messiah, Mary´s son, and his mother, and all those who are on earth?´ For to God belongs the kingdom of the heavens and of the earth, and all that is between them, creating what He will. God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,18,18,N´Say the Jews and Christians, ´We are the sons of God, and His beloved ones.´ Say: ´Why then does He chastise you for your sins? No; you are mortals, of His creating; He forgives whom He will, and He chastises whom He will.´ For to God belongs the kingdom of the heavens and of the earth, and all that is between them; to Him is the homecoming.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,19,19,N´People of the Book, now there has come to you Our Messenger, making things clear to you, upon an interval between the Messengers lest you should say, ´There has not come to us any bearer of good tidings, neither any warner. Indeed, there has come to you a bearer of good tidings and a warner; God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,20,20,N´And when Moses said to his people, ´O my people, remember God´s blessing upon you, when He appointed among you Prophets, and appointed you kings, and gave you such as He had not given to any being.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,21,21,N´O my people, enter the Holy Land which God has prescribed for you, and turn not back in your traces, to turn about losers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,22,22,N´They said, ´Moses, there are people in. it very arrogant; we will not enter it until they depart from it; if they depart from it then we will enter.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,23,23,N´Said two men of those that feared God whom God had blessed, ´Enter against them the gate! When you enter it, you will be victors. Put you all your trust in God, if you are believers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,24,24,N´They said, ´Moses, we will never enter it so long as they are in it. Go forth, thou and thy Lord, and do battle; we will be sitting here.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,25,25,N´He said, ´O my Lord, I rule no one except myself and my brother. So do Thou divide between us and the people of the ungodly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,26,26,N´Said He, ´Then it shall be forbidden them for forty years, while they are wandering in the earth; so grieve not for the people of the ungodly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,27,27,N´And recite thou to them the story of the two sons of Adam truthfully, when they offered a sacrifice, and it was accepted of one of them, and not accepted of the other. ´I will surely slay thee,´ said one. ´God accepts only of the godfearing,´ said the other.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,28,28,N´´Yet if thou stretchest out thy hand against me, to slay me, I will not stretch out my hand against thee, to slay thee; I fear God, the Lord of all Being.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,29,29,N´I desire that thou shouldest be laden with my sin and thy sin, and so become an inhabitant of the Fire; that is the recompense of the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,30,30,N´Then his soul prompted him to slay his brother, and he slew him, and became one of the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,31,31,N´Then God sent forth a raven, scratching into the earth, to show him how he might conceal the vile body of his brother. He said, ´Woe is me! Am I unable to be as this raven, and so conceal my brother´s vile body?´ And he became one of the remorseful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,32,32,N´Therefore We prescribed for the Children of Israel that whoso slays a soul not to retaliate for a soul slain, nor for corruption done in the land, shall be as if he had slain mankind altogether; and whoso gives life to a soul, shall be as if he ha given life to mankind altogether. Our Messengers have already come to them with the clear signs; then many of them thereafter commit excesses in the earth.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,33,33,N´This is the recompense of those who fight against God and His Messenger, and hasten about the earth, to do corruption there: they shall be slaughtered, or crucified, or their hands and feet shall alternately be struck off; or they shall be banished from the land. That is a degradation for them in this world; and in the world to come awaits them a mighty chastisement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,34,34,N´except for such as repent, before you have power over them. So know you that God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,35,35,N´O believers, fear God, and seek the means to come to Him, and struggle in His way; haply you will prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,36,36,N´The unbelievers, though they possessed all that is in the earth, and the like of it with it, to ransom themselves from the chastisement of the Day of Resurrection thereby, it would not be accepted of them; for them awaits a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,37,37,N´They will desire to come forth from the Fire, but they will not come forth from it; for them awaits a lasting chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,38,38,N´And the thief, male and female: cut off the hands of both, as a recompense for what they have earned, and a punishment exemplary from God; God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,39,39,N´But whoso repents, after his evildoing, and makes amends, God will turn towards him; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,40,40,N´Knowest thou not that to God belongs the kingdom of the heavens and the earth? He chastises whom He will, and forgives whom He will; and God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,41,41,N´O Messenger, let them not grieve thee that vie with one another in unbelief, such men as say with their mouths ´We believe´ but their hearts believe not; and the Jews who listen to falsehood, listen to other folk, who have not come to thee, perverting words from their meanings, saying, ´If you are given this, then take it; if you are not given it, beware!´ Whomsoever God desires to try, thou canst not avail him anything with God. Those are they whose hearts God desired not to purify; for them is degradation in this world; and in the world to come awaits them a mighty chastisement;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,42,42,N´who listen to falsehood, and consume the unlawful. If they come to thee, judge thou between them, or turn away from them; if thou turnest away from them, they will hurt thee nothing; and if thou judgest, judge justly between them; God loves the just.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,43,43,N´Yet how will they make thee their judge seeing they have the Torah, wherein is God´s judgment, then thereafter turn their backs? They are not believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,44,44,N´Surely We sent down the Torah, wherein is guidance and light; thereby the Prophets who had surrendered themselves gave judgment for those of Jewry, as did the masters and the rabbis, following such portion of God´s Book as they were given to keep and were witnesses to. So fear not men, but fear you Me; and sell not My signs for a little price. Whoso judges not according to what God has sent down - they are the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,45,45,N´And therein We prescribed for them: ´A life for a life, an eye for an eye, a nose for a nose, an ear for an ear, a tooth for a tooth, and for wounds retaliation´; but whosoever forgoes it as a freewill offering, that shall be for him an expiation. Whoso judges not according to what God has sent down -- they are the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,46,46,N´And We sent, following in their footsteps, Jesus son of Mary, confirming the Torah before him and We gave to him the Gospel, wherein is guidance and light, and confirming the Torah before it, as a guidance and an admonition unto the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,47,47,N´So let the People of the Gospel judge according to what God has sent down therein. Whosoever judges not according to what God has sent down -- they are the ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,48,48,N´And We have sent down to thee the Book with the truth, confirming the Book that was before it, and assuring it. So judge between them according to what God has sent down, and do not follow their caprices, to forsake the truth that has come to thee. To every one of you We have appointed a right way and an open road. If God had willed, He would have made you one nation; but that He may try you in what has come to you. So be you forward in good works; unto God shall you return, all together; and He will tell you of that whereon you were at variance.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,49,49,N´And judge between them according to what God has sent down, and do not follow their caprices, and beware of them lest they tempt thee away from any of what God has sent down to thee. But if they turn their backs, know that God desires only to smite them for some sin they have committed; surely, many men are ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,50,50,N´Is it the judgment of pagandom then that they are seeking? Yet who is fairer in judgment than God, for a people having sure faith?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,51,51,N´O believers, take not Jews and Christians as friends; they are friends of each other. Whoso of you makes them his friends is one of them. God guides not the people of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,52,52,N´Yet thou seest those in whose hearts is sickness vying with one another to come to them, saying, ´We fear lest a turn of fortune should smite us.´ But it may be that God will bring the victory, or some commandment from Him, and then they will find themselves, for that they kept secret within them, remorseful,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,53,53,N´and the believers will say, ´What, are these the ones who swore by God most earnest oaths that they were with you? Their works have failed now they are losers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,54,54,N´O believers, whosoever of you turns from his religion, God will assuredly bring a people He loves, and who love Him, humble towards the believers, disdainful towards the unbelievers, men who struggle in the path of God, not fearing the reproach of any reproacher. That is God´s bounty; He gives it unto whom He will; and God is All-embracing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,55,55,N´Your friend is only God, and His Messenger, and the believers who perform the prayer and pay the alms, and bow them down.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,56,56,N´Whoso makes God his friend, and His Messenger, and the believers -- the party of God, they are the victors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,57,57,N´O believers, take not as your friends those of them, who were given the Book before you, and the unbelievers, who take your religion in mockery and as a sport -- and fear God, if you are believers --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,58,58,N´and when you call to prayer, take it in mockery and as a sport; that is because they are a people who have no understanding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,59,59,N´Say: ´People of the Book, do you blame us for any other cause than that we believe in God, and what has been sent down to us, and what was sent down before, and that most of you are ungodly?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,60,60,N´Say: ´Shall I tell you of a recompense with God, worse than that? Whomsoever God has cursed, and with whom He is wroth, and made some of them apes and swine, and worshippers of idols -- they are worse situated, and have gone further astray from the right way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,61,61,N´When they come to you, they say, ´We believe´; but they have entered in unbelief, and so they have departed in it; God knows very well what they were hiding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,62,62,N´Thou seest many of them vying in sin and enmity, and how they consume the unlawful; evil is the thing they have been doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,63,63,N´Why do the masters and the rabbis not forbid them to utter sin, and consume the unlawful? Evil is the thing they have been working.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,64,64,N´The Jews have said, ´God´s hand is fettered.´ Fettered are their hands, and they are cursed for what they have said. Nay, but His hands are outspread; He expends how He will. And what has been sent down to thee from thy Lord will surely increase many of them in insolence and unbelief; and We have cast between them enmity and hatred, till the Day of Resurrection. As often as they light a fire for war, God will extinguish it. They hasten about the earth, to do corruption there; and God loves not the workers of corruption.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,65,65,N´But had the People of the Book believed and been godfearing, We would have acquitted them of their evil deeds, and admitted them to Gardens of Bliss.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,66,66,N´Had they performed the Torah and the Gospel, and what was sent down to them from their Lord, they would have. eaten both what was above them, and what was beneath their feet. Some of them are a just nation; but many of them -- evil are the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,67,67,N´O Messenger, deliver that which has been sent down to thee from thy Lord; for if thou dost not, thou wilt not have delivered His Message. God will protect thee from men. God guides not the people of the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,68,68,N´Say: ´People of the Book, you do not stand on anything, until you perform the Torah and the Gospel, and what was sent down to you from your Lord.´ And what has been sent down to thee from thy Lord will surely increase many of them in insolence and unbelief; so grieve not for the people of the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,69,69,N´Surely they that believe, and those of Jewry, and the Sabaeans, and those Christians, whosoever believes in God and the Last Day, and works righteousness -- no fear shall be on them, neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,70,70,N´And We took compact with the Children of Israel, and We sent Messengers to them. Whensoever there came to them a Messenger with that their souls had not desire for, some they cried lies to, and some they slew.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,71,71,N´And they supposed there should be no trial; but blind they were, and deaf. Then God turned towards them; then again blind they were, many of them, and deaf; and God sees the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,72,72,N´They are unbelievers who say, ´God is the Messiah, Mary´s son.´ For the Messiah said, ´Children of Israel, serve God, my Lord and your Lord. Verily whoso associates with God anything, God shall prohibit him entrance to Paradise, and his refuge shall be the Fire; and wrongdoers shall have no helpers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,73,73,N´They are unbelievers who say, ´God is the Third of Three. No god is there but One God. If they refrain not from what they say, there shall afflict those of them that disbelieve a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,74,74,N´Will they not turn to God and pray His forgiveness? God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,75,75,N´The Messiah, son of Mary, was only a Messenger; Messengers before him passed away; his mother was a just woman; they both ate food. Behold, how We make clear the signs to them; then behold, how they perverted are!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,76,76,N´Say: ´Do you serve, apart from God, that which cannot hurt or profit you? God is the All-hearing, the All-knowing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,77,77,N´Say: ´People of the Book, go not beyond the bounds in your religion, other than the truth, and follow not the caprices of a people who went astray before, and led astray many, and now again have gone astray from the right way.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,78,78,N´Cursed were the unbelievers of the Children of Israel by the tongue of David, and Jesus, Mary´s son; that, for their rebelling and their transgression.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,79,79,N´They forbade not one another any dishonour that they committed; surely evil were the things they did.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,80,80,N´Thou seest many of them making unbelievers their friends. Evil is that they have forwarded to their account, that God is angered against them, and in the chastisement they shall dwell forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,81,81,N´Yet had they believed in God and the Prophet and what has been sent down to him, they would not have taken them as friends; but many of them are ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,82,82,N´Thou wilt surely find the most hostile of men to the believers are the Jews and the idolaters; and thou wilt surely find the nearest of them in love to the believers are those who say ´We are Christians´; that, because some of them are priests and monks, and they wax not proud;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,83,83,N´and when they hear what has been sent down to the Messenger, thou seest their eyes overflow with tears because of the truth they recognize. They say, ´Our Lord, we believe; so do Thou write us down among the witnesses.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,84,84,N´Why should we not believe in God and the truth that has come to us, and be eager that our Lord should admit us with the righteous people?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,85,85,N´And God rewards them for what they say with gardens underneath which rivers flow, therein dwelling forever; that is the recompense of the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,86,86,N´But those who disbelieve, and cry lies to Our signs -- they are the inhabitants of Hell.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,87,87,N´O believers, forbid not such good things as God has permitted you; and transgress not; God loves not transgressors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,88,88,N´Eat of what God has provided you lawful and good; and fear God, in whom you are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,89,89,N´God will not take you to task for a slip in your oaths; but He will take you to task for such bonds as you have made by oaths, whereof the expiation is to feed ten poor persons with the average of the food you serve to your families, or to clothe them, or to set free a slave; or if any finds not the means, let him fast for three days. That is the expiation of your oaths when you have sworn; but keep your oaths. So God makes clear to you His signs; haply you will be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,90,90,N´O believers, wine and arrow-shuffling, idols and divining-arrows are an abomination, some of Satan´s work; so avoid it; haply So you will prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,91,91,N´Satan only desires to precipitate enmity and hatred between you in regard to wine and arrow-shuffling, and to bar you from the remembrance of God, and from prayer. Will you then desist?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,92,92,N´And obey God and obey the Messenger, and beware; but if you turn your backs, then know that it is only for Our Messenger to deliver the Message Manifest.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,93,93,N´There is no fault in those who believe and do deeds of righteousness what they may eat, if they are godfearing, and believe, and do deeds of righteousness, and then are godfearing and believe, and then are godfearing and do good; God loves the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,94,94,N´O believers, God will surely try you with something of the game that your hands and lances attain, that God may know who fears Him in the Unseen. Whoso thereafter commits transgression, there awaits him a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,95,95,N´O believers, slay not the game while you are in pilgrim sanctity; whosoever of you slays it wilfully, there shall be recompense -- the like of what he has slain, in flocks as shall be judged by two men of equity among you, an offering to reach the Kaaba; or expiation -- food for poor persons or the equivalent of that in fasting, so that he may taste the mischief of his action. God has pardoned what is past; but whoever offends again, God will take vengeance on him, God is All-mighty, Vengeful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,96,96,N´Permitted to you is the game of the sea and the food of it, as a provision for you and for the journeyers; but forbidden to you is the game of the land, so long as you remain in pilgrim sanctity; and fear God, unto whom you shall be mustered.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,97,97,N´God has appointed the Kaaba, the Holy House, as an establishment for men, and the holy month; the offering, and the necklaces -- that, that you may know that God knows all that is in the heavens and in the earth; and that God has knowledge of everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,98,98,N´Know God is terrible in retribution, and God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,99,99,N´It is only for the Messenger to deliver the Message; and God knows what you reveal and what you hide.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,100,100,N´Say: ´The corrupt and the good are not equal, though the abundance of the corrupt please thee.´ So fear God, O men possessed of minds; haply so you will prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,101,101,N´O believers, question not concerning things which, if they were revealed to you, would vex you; yet if you question concerning them when the Koran is being sent down, they will be revealed to you. God has effaced those things; for God is All-forgiving, All-clement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,102,102,N´A people before you questioned concerning them, then disbelieved in them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,103,103,N´God has not appointed cattle dedicated to idols, such as Bahira, Sa´iba, Wasila, Hami; but the unbelievers forge against God falsehood, and most of them have no understanding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,104,104,N´And when it is said to them, ´Come now to what God has sent down, and the Messenger, they say, ´Enough for us is what we found our fathers doing.´ What, even if their fathers had knowledge of naught and were not guided?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,105,105,N´O believers, look after your own souls. He who is astray cannot hurt you, if you are rightly guided. Unto God shall you return, all together, and He will tell you what you were doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,106,106,N´O believers, the testimony between you when any of you is visited by death, at the bequeathing, shall be two men of equity among you; or two others from another folk, if you are journeying in the land and the affliction of death befalls you. Them you shall detain after the prayer, and they shall swear by God, if you are doubtful, ´We will not sell it for a price, even though it were a near kinsman, nor will we hide the testimony of God, for then we would surely be among the sinful´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,107,107,N´But if it be discovered that both of them have merited the accusation of any sin, then two others shall stand in their place, these being the nearest of those most concerned, and they shall swear by God, ´Our testimony is truer than their testimony, and we have not transgressed, for then we would assuredly be among the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,108,108,N´So it is likelier that they will bear testimony in proper form, or else they will be afraid that after their oaths, oaths may be rebutted. Fear God, and hearken; God guides not the people of the ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,109,109,N´The day when God shall gather the Messengers, and say, ´What answer were you given?´ They shall say, ´We have no knowledge; Thou art the Knower of the things unseen.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,110,110,N´When God said, ´Jesus Son of Mary, remember My blessing upon thee and upon thy mother, when I confirmed thee with the Holy Spirit, to speak to men in the cradle, and of age; and when I taught thee the Book, the Wisdom, the Torah, the Gospel; and when thou createst out of clay, by My leave, as the likeness of a bird, and thou breathest into it, and it is a bird, by My leave; and thou healest the blind and the leper by My leave, and thou bringest the dead forth by My leave; and when restrained from thee the Children of Israel when thou camest unto them with the clear signs, and the unbelievers among them said, \"This is nothing but sorcery manifest.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,111,111,N´And when I inspired the Apostles: \"Believe in Me and My Messenger\"; they said, \"We believe; witness Thou our submission.\"´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,112,112,N´And when the Apostles said, ´O Jesus son of Mary, is thy Lord able to send down on us a Table out of heaven?´ He said, ´Fear you God, if you are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,113,113,N´They said, ´We desire that we should eat of it and our hearts be at rest; and that we may know that thou hast spoken true to us, and that we may be among its witnesses.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,114,114,N´Said Jesus son of Mary, ´O God, our Lord, send down upon us a Table out of heaven, that shall be for us a festival, the first and last of us, and a sign from Thee. And provide for us; Thou art the best of providers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,115,115,N´God said, ´Verily I do send it down on you; whoso of you hereafter disbelieves, verily I shall chastise him with a chastisement wherewith I chastise no other being.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,116,116,N´And when God said, ´O Jesus son of Mary, didst thou say unto men, \"Take me and my mother as gods, apart from God\"?´ He said, ´To Thee be glory! It is not mine to say what I have no right to. If I indeed said it, Thou knowest it, knowing what is within my soul, and I know not what is within Thy soul; Thou knowest the things unseen´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,117,117,N´I only said to them what Thou didst command me: \"Serve God, my Lord and your Lord.\" And I was a witness over them, while I remained among them; but when Thou didst take me to Thyself, Thou wast Thyself the watcher over them; Thou Thyself art witness of everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,118,118,N´If Thou chastisest them, they are Thy servants; if Thou forgivest them, Thou art the All-mighty, the All-wise.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,119,119,N´God said, ´This is the day the truthful shall be profited by their truthfulness. For them await gardens underneath which rivers flow, therein dwelling forever and ever; God being well-pleased with them and they well-pleased with Him; that is the mighty triumph.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,5,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,5,120,120,N´To God belongs the kingdom of the heavens and of the earth, and all that is in them, and He is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,1,1,N´Praise belongs to God who created the heavens and the earth and appointed the shadows and light; then the unbelievers ascribe equals to their Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,2,2,N´It is He who created you of clay, then determined a term and a term is stated with Him; yet thereafter you doubt.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,3,3,N´He is God in the heavens and the earth; He knows your secrets, and what you publish, and He knows what you are earning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,4,4,N´Not a sign of their Lord comes to them, but they turn away from it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,5,5,N´They cried lies to the truth when it came to them, but there shall come to them news of that they were mocking.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,6,6,N´Have they not regarded how We destroyed before them many a generation We established in the earth, as We never established you, and how We loosed heaven upon them in torrents, and made the rivers to flow beneath them? Then We destroyed them because of their sins, and raised up after them another generation;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,7,7,N´Had We sent down on thee a Book on parchment and so they touched it with their hands, yet the unbelievers would have said, ´This is naught but manifest sorcery.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,8,8,N´´Why has an angel not been sent down on him?´ they say; yet had We sent down an angel, the matter would have been determined, and then no respite would be given them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,9,9,N´And had We made him an angel, yet assuredly We would have made him a man, and confused for them the thing which they themselves are confusing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,10,10,N´Messengers indeed were mocked at before thee; but those that scoffed at them were encompassed by that they mocked at.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,11,11,N´Say: ´Journey in the land, then behold how was the end of them that cried lies.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,12,12,N´Say: ´To whom belongs what is in the heavens and in the earth?´ Say: ´It is God´s. He has prescribed for Himself mercy. He will surely gather you to the Resurrection Day, of which is no doubt. Those who have lost their souls, they do not believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,13,13,N´And to Him belongs whatsoever inhabits the night and the day; and He is the All-hearing, the All-knowing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,14,14,N´Say: ´Shall I take to myself as protector other than God, the Originator of the heavens and of the earth, He. who feeds and is not fed?´ Say: ´I have been commanded to be the first of them that surrender: \"Be not thou of the idolaters.\"´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,15,15,N´Say: ´Indeed I fear, if I should rebel against thy Lord, the chastisement of a dreadful day.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,16,16,N´From whomsoever it is averted on that day, He will have mercy on him; that is the manifest triumph.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,17,17,N´And if God visits thee with affliction none can remove it but He; and if He visits thee with good, He is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,18,18,N´He is Omnipotent over His servants, and He is the All-wise, the All-aware. Say: ´What thing is greatest in testimony?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,19,19,N´Say: ´God is witness between me and you, and this Koran has been revealed to me that I may warn you thereby, and whomsoever it may reach. Do you indeed testify that there are other gods with God?´ Say: ´I do not testify.´ Say: ´He is only One God, and I am quit of that you associate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,20,20,N´Those to whom We have given the Book recognize it as they recognize their sons. Those who have lost their own souls, they do not believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,21,21,N´And who does greater evil than he who forges against God a lie, or cries lies to His signs? They shall not prosper, the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,22,22,N´And on the day when We shall muster them all together, then We shall say unto those who associated other gods with God, ´Where are your associates whom you were asserting?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,23,23,N´Then they shall have no proving, but to say, ´By God our Lord, we never associated other gods with Thee.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,24,24,N´Behold how they lie against themselves, and how that which they were forging has gone astray from them!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,25,25,N´And some of them there are that listen to thee, and We lay veils upon their hearts lest they understand it, and in their ears heaviness; and if they see any sign whatever, they do not believe in it, so that when they come to thee they dispute with thee, the unbelievers saying, ´This is naught but the fairy-tales of the ancient ones.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,26,26,N´And they forbid it, and keep afar from it, and it is only themselves they destroy, but they are not aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,27,27,N´If thou couldst see when they are stationed before the Fire, and they say, ´Would that we might be returned, and then not cry lies to the signs of our Lord, but that we might be among the believers!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,28,28,N´No; that which they were concealing before has now appeared to them; and even if they were returned, they would again commit the very thing they were prohibited; they are truly liars.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,29,29,N´And they say, ´There is only our present life; we shall not be raised.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,30,30,N´If thou couldst see when they are stationed before their Lord! He will say, ´Is not this the truth?´ They will say, ´Yes indeed, by our Lord!´ He will say, ´Then taste the chastisement for your unbelief.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,31,31,N´Lost indeed are they that cried lies to the encounter with God, so that when the Hour comes to them suddenly they shall say, ´Alas for us, that we neglected it!´ On their backs they shall be bearing their loads; O how evil the loads they bear!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,32,32,N´The present life is naught but a sport and a diversion; surely the Last Abode is better for those that are godfearing. What, do you not understand?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,33,33,N´We know indeed that it grieves thee the things they say; yet it is not thee they cry lies to, but the evildoers - it is the signs of God that they deny.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,34,34,N´Messengers indeed were cried lies to before thee, yet they endured patiently that they were cried lies to, and were hurt, until Our help came unto them. No man can change the words of God; and there has already come to thee some tiding of the Envoys.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,35,35,N´And if their turning away is distressful for thee, why, if thou canst seek out a hole in the earth, or a ladder in heaven, to bring them some sign but had God willed, He would have gathered them to the guidance; so be not thou one of the ignorant.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,36,36,N´Answer only will those who hear; as for the dead, God will raise them up, then unto Him they will be returned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,37,37,N´They also say, ´Why has no sign been sent down upon him from his Lord?´ Say: ´Surely God is able to send down a sign, but most of them know not.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,38,38,N´No creature is there crawling on the earth, no bird flying with its wings, but they are nations like unto yourselves. We have neglected nothing in the Book; then to their Lord they shall be mustered.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,39,39,N´And those who cry lies to Our signs are deaf and dumb, dwelling in the shadows. Whomsoever God will, He leads astray, and whomsoever He will, He sets him on a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,40,40,N´Say: ´What think you? If God´s chastisement comes upon you, or the Hour comes upon you, will you call upon any other than God if you speak truly?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,41,41,N´No; upon Him you will call, and He will remove that for which you call upon Him if He Will, and you will forget that you associate with Him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,42,42,N´Indeed We sent to nations before thee, and We seized them with misery and hardship that haply they might be humble;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,43,43,N´if only, when Our might came upon them, they had been humble! But their hearts were hard, and Satan decked out fair to them what they were doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,44,44,N´So, when they forgot what they were reminded of; We opened unto them the gates of everything until; when they rejoiced in what they were given, We seized them suddenly, and behold, they were sore confounded.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,45,45,N´So the last remnant of the people who did evil was cut off. Praise belongs to God the Lord of all Being.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,46,46,N´Say: ´What think you? If God seizes your hearing and sight, and sets a seal upon your hearts, who is a god other than God to give it back to you?´ Behold how We turn about the signs! Yet thereafter they are turning away.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,47,47,N´Say: ´What think you? If God´s chastisement comes upon you, suddenly or openly, shall any be destroyed, except the people of the evildoers?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,48,48,N´We do not send the Envoys, except good tidings to bear, and warning; whoever believes and makes amends -- no fear shall be on them, neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,49,49,N´But those who cry lies to Our signs, them the chastisement shall visit, for that they were ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,50,50,N´Say: ´I do not say to you, \"I possess the treasuries of God\"; I know not the Unseen. And I say not to you, \"I am an angel\"; I only follow what is revealed to me. Say: ´Are the blind and the seeing man equal? Will you not reflect?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,51,51,N´And warn therewith those who fear they shall be mustered to their Lord; they have, apart from God, no protector and no intercessor; haply they will be godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,52,52,N´And do not drive away those who call upon their Lord at morning and evening desiring His countenance; nothing of their account falls upon thee, and nothing of thy account falls upon them, that thou shouldst drive them away, and so become one of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,53,53,N´Even so We have tried some of them by others that they may say, Are these the ones God has been gracious to among us?´ Knows not God very well the thankful?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,54,54,N´And when those who believe in Our signs come to thee, say, ´Peace be upon you. Your Lord has prescribed for Himself mercy. Whosoever of you does evil in ignorance, and thereafter repents and makes amends, He is All-forgiving, All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,55,55,N´Thus We distinguish Our signs, that the sinners´ way may be manifest.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,56,56,N´Say: ´I am forbidden to serve those you call on apart from God.´ Say: ´I do not follow your caprices, or else I had gone astray, and would not be of the right-guided.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,57,57,N´Say: ´I stand upon a clear sign from my Lord, and you have cried lies to it. Not with me is that you seek to hasten; the judgment is God´s alone. He relates the truth, and He is the Best of deciders.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,58,58,N´Say: ´If what you seek to hasten were with me, the matter between you and me would be decided; and God knows very well the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,59,59,N´With Him are the keys of the Unseen; none knows them but He. He knows what is in land and sea; not a leaf falls, but He knows it. Not a grain in the earth´s shadows, not a thing, fresh or withered, but it is in a Book Manifest.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,60,60,N´It is He who recalls you by night, and He knows what you work by day; then He raises you up therein, that a stated term may be determined; then unto Him shall you return, then He will tell you of what you have been doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,61,61,N´He is the Omnipotent over His servants. He sends recorders over you till, when any one of you is visited by death, Our messengers take him and they neglect not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,62,62,N´Then they are restored to God their Protector, the True. Surely His is the judgment; He is the swiftest of reckoners.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,63,63,N´Say: ´Who delivers you from the shadows of land and sea? You call upon Him humbly and secretly, \"Truly, if Thou deliverest from these, we shall be among the thankful.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,64,64,N´Say: ´God delivers you from them and from every distress; then you assign Him associates.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,65,65,N´Say: ´He is able to send forth upon you chastisement, from above you or from under your feet, or to confuse you in sects and to make you taste the violence of one another.´ Behold how We turn about the signs; haply they will understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,66,66,N´Thy people have cried it lies; yet it is the truth. Say: ´I am not a guardian over you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,67,67,N´Every tiding has its time appointed; you will surely know.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,68,68,N´When thou seest those who plunge into Our signs, turn away from them until they plunge into some other talk; or if Satan should make thee forget, do not sit, after the reminding, with the people of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,69,69,N´Nothing of their account falls upon those that are godfearing; but a reminding; haply they will be godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,70,70,N´Leave alone those who take their religion for a sport and a diversion, and whom the present life has deluded. Remind hereby, lest a soul should be given up to destruction for what it has earned; apart from God, it has no protector and no intercessor; though it offer any equivalent, it shall not be taken from it. Those are they who are given up to destruction for what they have earned; for them awaits a draught of boiling water and a painful chastisement, for that they were unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,71,71,N´Say: ´Shall we call, apart from God, on that which neither profits nor hurts us, and shall we be turned back on our heels after that God has guided us? -- Like one lured to bewilderment in the earth by Satans, though he has friends who call him to guidance, \"Come to us!\" Say: ´God´s guidance is the true guidance, and we are commanded to surrender to the Lord of all Being,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,72,72,N´and: \"Perform the prayer, and fear Him; it is unto Him you shall be mustered.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,73,73,N´It is He who created the heavens and the earth in truth; and the day He says ´Be´, and it is; His saying is true, and His is the Kingdom the day the Trumpet is blown; He is Knower of the Unseen and the visible; He is the All-wise, the All-aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,74,74,N´And when Abraham said to his father Azar, ´Takest thou idols for gods? I see thee, and thy people, in manifest error.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,75,75,N´So We were showing Abraham the kingdom of the heavens and earth, that he might be of those having sure faith.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,76,76,N´When night outspread over him he saw a star and said, ´This is my Lord.´ But when it set he said, ´I love not the setters.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,77,77,N´When he saw the moon rising, he said, ´This is my Lord.´ But when it set he said, ´If my Lord does not guide me I shall surely be of the people gone astray.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,78,78,N´When he saw the sun rising, he said, ´This is my Lord; this is greater!´ But when it set he said, ´O my people, surely I am quit of that you associate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,79,79,N´I have turned my face to Him who originated the heavens and the earth, a man of pure faith; I am not of the idolaters.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,80,80,N´His people disputed with him. He said, ´Do you dispute with me concerning God, and He has guided me? I fear not what you associate with Him, except my Lord will aught. My Lord embraces all things in His knowledge; will you not remember?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,81,81,N´How should I fear what you have associated, seeing you fear not that you have associated with God that whereon He has not sent down on you any authority?´ Which of the two parties has better title to security, if you have any knowledge?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,82,82,N´Those who believe, and have not confounded their belief with evildoing -- to them belongs the true security; they are rightly guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,83,83,N´That is Our argument, which We bestowed upon Abraham as against his people. We raise up in degrees whom We will; surely thy Lord is All-wise, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,84,84,N´And We gave to him Isaac and Jacob -- each one We guided, And Noah We guided before; and of his seed David and Solomon, Job and Joseph, Moses and Aaron -- even so We recompense the good-doers --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,85,85,N´Zachariah and John, Jesus and Elias; each was of the righteous;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,86,86,N´Ishmael and Elisha, Jonah and Lot-each one We preferred above all beings;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,87,87,N´and of their fathers, and of their seed, and of their brethren; and We elected them, and We guided them to a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,88,88,N´That is God´s guidance; He guides by it whom He will of His servants; had they been idolaters, it would have failed them, the things they did.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,89,89,N´Those are they to whom We gave the Book, the Judgment, the Prophethood; so if these disbelieve in it, We have already entrusted it to a people who do not disbelieve in it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,90,90,N´Those are they whom God has guided; so follow their guidance. Say: ´I ask of you no wage for it; it is but a reminder unto all beings.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,91,91,N´They measured not God with His true measure when they said, ´God has not sent down aught on any mortal.´ Say: ´Who sent down the Book that Moses brought as a light and a guidance to men? You put it into parchments, revealing them, and hiding much; and you were taught that you knew not, you and your fathers.´ Say: ´God.´ Then leave them alone, playing their game of plunging.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,92,92,N´This is a Book We have sent down, blessed and confirming that which was before it, and for thee to warn the Mother of Cities and those about her; and those who believe in the world to come believe in it, and watch over their prayers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,93,93,N´And who does greater evil than he who forges against God a lie, or says,, ´To me it has been revealed´, when naught has been revealed to him, or he who says, ´I will send down the like of what God has sent down´? If thou couldst only see when the evildoers are in the agonies of death, and the angels are stretching out their hands: ´Give up your souls! Today you shall be recompensed with the chastisement of humiliation for what you said untruly about God, waxing proud against His signs.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,94,94,N´´Now you have come to Us one by one, as We created you upon the first time, and you have left what We conferred on you behind your backs. We do not see with you your intercessors, those you asserted to be associates in you; the bond between you is now broken; that which you ever asserted has now gone astray from you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,95,95,N´It is God who splits the grain and the date-stone, brings forth the living from the dead; He brings forth the dead too from the living. So that then is God; then how are you perverted?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,96,96,N´He splits the sky into dawn, and has made the night for a repose, and the sun and moon for a reckoning. That is the ordaining of the All-mighty, the All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,97,97,N´It is He who has appointed for you the stars, that by them you might be guided in the shadows of land and sea. We have distinguished the signs for a people who know.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,98,98,N´It is He who produced you from one living soul, and then a lodging-place, and then a repository. We have distinguished the signs for a people who understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,99,99,N´It is He who sent down out of heaven water, and thereby We have brought forth the shoot of every plant, and then We have brought forth the green leaf of it, bringing forth from it close-compounded grain, and out of the palm-tree, from the spathe of it, dates thick-lustered, ready to the hand, and gardens of vines, olives, pomegranates, like each to each, and each unlike to each. Look upon their fruits when they fructify and ripen! Surely, in all this are signs for a people who do believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,100,100,N´Yet they ascribe to God, as associates, the jinn, though He created them; and they impute to Him sons and daughters without any knowledge. Glory be to Him! High be He exalted above what they describe!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,101,101,N´The Creator of the heavens and the earth -- how should He have a son, seeing that He has no consort, and He created all things, and He has knowledge of everything?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,102,102,N´That then is God your Lord; there is no god but He, the Creator of everything. So serve Him, for He is Guardian over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,103,103,N´The eyes attain Him not, but He attains the eyes; He is the All-subtle, the All-aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,104,104,N´Clear proofs have come to you from your Lord. Whoso sees clearly, it is to his own gain, and whoso is blind, it is to his own loss; I am not a watcher over you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,105,105,N´So We turn about the signs, that they may say, ´Thou hast studied´; and that We may make it clear to a people having knowledge.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,106,106,N´Follow thou what has been revealed to thee from thy Lord; there is no god but He; and turn thou away from the idolaters.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,107,107,N´Had God willed, they were not idolaters; and We have not appointed thee a watcher over them, neither art thou their guardian.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,108,108,N´Abuse not those to whom they pray, apart from God, or they will abuse God in revenge without knowledge. So We have decked out fair to every nation their deeds; then to their Lord they shall return, and He will tell them what they have been doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,109,109,N´They have sworn by God the most earnest oaths if a sign comes to them they will believe in it. Say: ´Signs are only with God.´ What will make you realize that, when it comes, they will not believe?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,110,110,N´We shall turn about their hearts and their eyes, even as they believed not in it the first time; and We shall leave them in their insolence wandering blindly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,111,111,N´Though We had sent down the angels to them, and the dead had spoken with them, had We mustered against them every thing, face to face, yet they would not have been the ones to believe, unless God willed; but most of them are ignorant.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,112,112,N´So We have appointed to every Prophet an enemy -- Satans of men and jinn, revealing tawdry speech to each other, all as a delusion; yet, had thy Lord willed, they would never have done it. So leave them to their forging,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,113,113,N´and that the hearts of those who believe not in the world to come may incline to it, and that they may be well-pleased with it, and that they may gain what they are gaining.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,114,114,N´What, shall I seek after any judge but God? For it is He who sent down to you the Book well-distinguished; and those whom We have given the Book know it is sent down from thy Lord with the truth; so be not thou of the doubters.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,115,115,N´Perfect are the words of thy Lord in truthfulness and justice; no man can change His words; He is the All-hearing, the All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,116,116,N´If thou obeyest the most part of those on earth they will lead thee astray from the path of God; they follow only surmise, merely conjecturing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,117,117,N´Thy Lord knows very well who goes astray from His path; He knows very well the right-guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,118,118,N´Eat of that over which God´s Name has been mentioned, if you believe in His signs.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,119,119,N´How is it with you, that you do not eat of that over which God´s Name has been mentioned, seeing that He has distinguished for you that He has forbidden you, unless you are constrained to it? But surely, many lead astray by their caprices, without any knowledge; thy Lord knows very well the transgressors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,120,120,N´Forsake the outward sin, and the inward surely the earners of sin shall be recompensed for what they have earned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,121,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,121,121,N´And eat not of that over which God´s Name has not been mentioned; it is ungodliness. The Satans inspire their friends to dispute with you; if you obey them, you are idolaters.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,122,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,122,122,N´Why, is he who was dead, and We gave him life, and appointed for him a light to walk by among the people as one whose likeness is in the shadows, and comes not forth from them? So it is decked out fair to the unbelievers the things they have done.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,123,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,123,123,N´And even so We appointed in every city great ones among its sinners, to devise there; but they devised only against themselves, and they were not aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,124,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,124,124,N´And when a sign came to them, they said, ´We will not believe until we are given the like of what God´s Messengers were given.´ God knows very well where to place His Message; and humiliation in God´s sight shall befall the sinners, and a terrible chastisement, for what they devised.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,125,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,125,125,N´Whomsoever God desires to guide, He expands his breast to Islam; whomsoever He desires to lead astray, He makes his breast narrow, tight, as if he were climbing to heaven. So God lays abomination upon those who believe not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,126,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,126,126,N´This is the path of thy Lord; straight; We have distinguished the signs to a people who remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,127,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,127,127,N´Theirs is the abode of peace with their Lord, and He is their Protector for that they were doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,128,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,128,128,N´On the day when He shall muster them all together: ´Company of jinn, you have made much of mankind.´ Then their friends among mankind will say, ´Our Lord, we have profited each of the other, and we have reached the term determined by Thee for us. He will say: ´The Fire is your lodging, therein to dwell forever´ - except as God will; surely thy Lord is All-wise, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,129,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,129,129,N´So We make the evildoers friends of each other for what they have earned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,130,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,130,130,N´´Company of jinn and mankind, did not Messengers come to you from among you, relating to you My signs and warning you of the encounter of this your day?´ They shall say, ´We bear witness against ourselves.´ They were deluded by the present life, and they bear witness against themselves that they were unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,131,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,131,131,N´That is because thy Lord would never destroy the cities unjustly, while their inhabitants were heedless.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,132,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,132,132,N´All have degrees according to what they have done; thy Lord is not heedless of the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,133,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,133,133,N´Thy Lord is All-sufficient, Merciful. If He will, He can put you away, and leave after you, to succeed you, what He will, as He produced you from the seed of another people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,134,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,134,134,N´The thing you are promised, that will surely come; you cannot frustrate it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,135,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,135,135,N´Say: ´O my people, act according to your station; I am acting. And assuredly you will know who shall possess the Abode Ultimate. Surely the evildoers will not prosper.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,136,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,136,136,N´They appoint to God, of the tillage and cattle that He multiplied, a portion, saying, ´This is for God´ -- so they assert -- ´and this is for our associates.´ So what is for their associates reaches not God; and what is for God reaches their associates. Evil is their judgment!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,137,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,137,137,N´Thus those associates of theirs have decked out fair to many idolaters to slay their children, to destroy them, and to confuse their religion for them. Had God willed, they would not have done so; so leave them to their forging.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,138,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,138,138,N´They say, ´These are cattle and tillage sacrosanct; none shall eat them, but whom we will´-- so they assert -- ´and cattle whose backs have been forbidden, and cattle over which they mention not the Name of God.´ All that they say, forging against God; He will assuredly recompense them for what they were forging.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,139,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,139,139,N´And they say, ´What is within the bellies of these cattle is reserved for our males and forbidden to our spouses; but if it be dead, then they all shall be partners in it.´ He will assuredly recompense them for their describing; surely He is All-wise, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,140,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,140,140,N´Losers are they who slay their children in folly, without knowledge, and have forbidden what God has provided them, forging against God; they have gone astray, and are not right-guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,141,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,141,141,N´It is He who produces gardens trellised, and untrellised, palm-trees, and crops diverse in produce, olives, pomegranates, like each to each, and each unlike to each. Eat of their fruits when they fructify, and pay the due thereof on the day of its harvest; and be not prodigal; God loves not the prodigal.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,142,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,142,142,N´And of the cattle, for burthen and for slaughter, eat of what God has provided you; and follow not the steps of Satan; he is a manifest foe to you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,143,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,143,143,N´Eight couples: two of sheep, of goats two. Say: ´Is it the two males He has forbidden or the two females? Or what the wombs of the two females contain? Tell me with knowledge, if you speak truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,144,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,144,144,N´Of camels two, of oxen two. Say: ´Is it the two males He has forbidden or the two females? Or what the wombs of the two females contain? Or were you witnesses when God charged you with this? Then who does greater evil than he who forges against God a lie, in order that he may lead mankind astray without any knowledge? Surely God guides not the people of the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,145,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,145,145,N´Say: ´I do not find, in what is revealed to me, aught forbidden to him who eats thereof except it be carrion, or blood outpoured, or the flesh of swine -- that is an abomination -- or an ungodly thing that has been hallowed to other than God; yet whoso is constrained, not desiring nor transgressing, surely thy Lord is All-forgiving, All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,146,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,146,146,N´And to those of Jewry We have forbidden every beast with claws; and of oxen and sheep We have forbidden them the fat of them, save what their backs carry, or their entrails, or what is mingled with bone; that We recompensed them for their insolence; surely We speak truly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,147,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,147,147,N´So, if they cry thee lies, say: ´Your Lord is of mercy all-embracing, and His might will never be turned back from the people of the sinners.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,148,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,148,148,N´The idolaters will say, ´Had God willed, we would not have been idolaters, neither our fathers, nor would we have forbidden aught.´ Even so the people before them cried lies until they tasted Our might. Say: ´Have you any knowledge, for you to bring forth for us? You follow only surmise, merely conjecturing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,149,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,149,149,N´Say: ´To God belongs the argument conclusive; for had He willed, He would have guided you all.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,150,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,150,150,N´Say: ´Produce your witnesses, those who testify God has forbidden this.´ Then if they testify, bear not witness with them; and do not thou follow the caprices of those who cried lies to Our signs, and who believe not in the world to come, and ascribe equals to their Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,151,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,151,151,N´Say: ´Come, I will recite what your Lord has forbidden you: that you associate not anything with Him, and to be good to your parents, and not to slay your children because of poverty; We will provide you and them and that you approach not any indecency outward or inward, and that you slay not the soul God has forbidden, except by right. That then He has charged you with; haply you will understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,152,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,152,152,N´And that you approach not the property of the orphan, save in the fairer manner, until he is of age. And fill up the measure and the balance with justice. We charge not any soul save to its capacity. And when you speak, be just, even if it should be to a near kinsman. And fulfil God´s covenant. That then He has charged you with; haply you will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,153,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,153,153,N´And that this is My path, straight; so do you follow it, and follow not divers paths lest they scatter you from His path. That then He has charged you with; haply you will be godfearing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,154,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,154,154,N´Then We gave Moses the Book, complete for him who does good, and distinguishing every thing, and as a guidance and a mercy; haply they would believe in the encounter with their Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,155,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,155,155,N´This is a Book We have sent down, blessed; so follow it, and be godfearing; haply so you will find mercy;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,156,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,156,156,N´lest you should say, ´The Book was sent down only upon two parties before us, and we have indeed been heedless of their study´;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,157,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,157,157,N´or lest you say, ´If the Book had been sent down upon us, we had surely been more rightly guided than they.´ Yet indeed a clear sign has come to you from your Lord, and a guidance and a mercy; and who does greater evil than he who cries lies to God´s signs, and turns away from them? We shall surely recompense those who turn away from Our signs with an evil chastisement for their turning away.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,158,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,158,158,N´What, do they look for the angels to come to them, nothing less, or that thy Lord should come, or that one of thy Lord´s signs should come? On the day that one of thy Lord´s signs comes it shall not profit a soul to believe that never believed before, or earned some good in his belief. Say: ´Watch and wait; We too are waiting.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,159,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,159,159,N´Those who have made divisions in their religion and become sects, thou art not of them in anything; their affair is unto God, then He will tell them what they have been doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,160,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,160,160,N´Whoso brings a good deed shall have ten the like of it; and whoso brings an evil deed shall only be recompensed the like of it; they shall not be wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,161,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,161,161,N´Say: ´As for me, my Lord has guided me to a straight path, a right religion, the creed of Abraham, a man of pure faith; he was no idolater.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,162,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,162,162,N´Say: ´My prayer, my ritual sacrifice, my living, my dying -- all belongs to God, the Lord of all Being.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,163,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,163,163,N´No associate has He. Even so I have been commanded, and I am the first of those that surrender.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,164,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,164,164,N´Say: ´Shall I seek after a Lord other than God, who is the Lord of all things?´ Every soul earns only to its own account, no soul laden bears the load of another. Then to your Lord shall you return, and He will tell you of that whereon you were at variance.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,6,165,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,6,165,165,N´It is He who has appointed you viceroys in the earth, and has raised some of you in rank above others, that He may try you in what He has given you. Surely thy Lord is swift in retribution; and surely He is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,1,1,N´Alif Lam Mim Sad´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,2,2,N´A Book sent down to thee -- so let there be no impediment in thy breast because of it -- to warn thereby, and as a reminder to believers:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,3,3,N´Follow what has been sent down to you from your Lord, and follow no friends other than He; little do you remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,4,4,N´How many a city We have destroyed! Our might came upon it at night, or while they took their ease in the noontide,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,5,5,N´and they but cried, when Our might came upon there, ´We were evildoers.´;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,6,6,N´So We shall question those unto whom a Message was sent, and We shall question the Envoys,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,7,7,N´and We shall relate to them with knowledge; assuredly We were not absent.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,8,8,N´The weighing that day is true; he whose scales are heavy -- they are the prosperers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,9,9,N´and he whose scales are light -- they have lost their soul for wronging Our signs.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,10,10,N´We have established you in the earth and there appointed for you livelihood; little thanks you show.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,11,11,N´We created you, then We shaped you, then We said to the angels: ´Bow yourselves to Adam´; so they bowed themselves, save Iblis -- he was not of those that bowed themselves.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,12,12,N´Said He, ´What prevented thee to bow thyself, when I commanded thee?´ Said he, ´I am better than he; Thou createdst me of fire, and him Thou createdst of clay.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,13,13,N´Said He, ´Get thee down out of it; it is not for thee to wax proud here, so go thou forth; surely thou art among the humbled.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,14,14,N´Said he, ´Respite me till the day they shall be raised.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,15,15,N´Said He, ´Thou art among the ones that are respited.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,16,16,N´Said he, ´Now, for Thy perverting me, I shall surely sit in ambush for them on Thy straight path;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,17,17,N´then I shall come on them from before them and from behind them, from their right hands and their left hands; Thou wilt not find most of them thankful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,18,18,N´Said He, ´Go thou forth from it, despised and banished. Those of them that follow thee -- I shall assuredly fill Gehenna with all of you.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,19,19,N´´O Adam, inherit, thou and thy wife, the Garden, and eat of where you will, but come not nigh this tree, lest you be of the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,20,20,N´Then Satan whispered to them, to reveal to them that which was hidden from them of their shameful parts. He said, ´Your Lord has only prohibited you from this tree lest you become angels, or lest you become immortals.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,21,21,N´And he swore to them, ´Truly, I am for you a sincere adviser.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,22,22,N´So he led them on by delusion; and when they tasted the tree, their shameful parts revealed to them, so they took to stitching upon themselves leaves of the Garden. And their Lord called to them, ´Did not I prohibit you from this tree, and say to you, \"Verily Satan is for you a manifest foe\"?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,23,23,N´They said, ´Lord, we have wronged ourselves, and if Thou dost not forgive us, and have mercy upon us, we shall surely be among the lost.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,24,24,N´Said He, ´Get you down, each of you an enemy to each. In the earth a sojourn shall be yours, and enjoyment for a time.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,25,25,N´Said He, ´Therein you shall live, and therein you shall die, and from there you shall be brought forth.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,26,26,N´Children of Adam! We have sent down on you a garment to cover your shameful parts, and feathers; and the garment of godfearing -- that is better; that is one of God´s signs; haply they will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,27,27,N´Children of Adam! Let not Satan tempt you as he brought your parents out of the Garden, stripping them of their garments to show them their shameful parts. Surely he sees you, he and his tribe, from where you see them not. We have made the Satans the friends of those who do not believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,28,28,N´And whenever they commit an indecency they say, ´We found our fathers practising it, and God has commanded us to do it.´ Say: ´God does not command indecency; what, do you say concerning God such things as you know not?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,29,29,N´Say: ´My Lord has commanded justice. Set your faces in every place of worship and call on Him, making your religion sincerely His. As He originated you so you will return;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,30,30,N´a part He guided, and a part justly disposed to error -- they have taken Satans for friends instead of God, and think them guided.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,31,31,N´Children of Adam! Take your adornment at every place of worship; and eat and drink, but be you not prodigal; He loves not the prodigal.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,32,32,N´Say: ´Who has forbidden the ornament of God which He brought forth for His servants, and the good things of His providing?´ Say: ´These, on the Day of Resurrection, shall be exclusively for those who believed in this present life. So We distinguish the signs for a people who know.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,33,33,N´Say: ´My Lord has only forbidden indecencies, the inward and the outward, and sin, and unjust insolence, and that you associate with God that for which He sent down never authority, and that you say concerning God such as you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,34,34,N´To every nation a term; when their term comes they shall not put it back by a single hour nor put it forward.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,35,35,N´Children of Adam! If there should come to you Messengers from among you, relating to you My signs, then whosoever is godfearing and makes amends -- no fear shall be on them, neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,36,36,N´And those that cry lies to Our signs, and wax proud against them -- those shall be the inhabitants of the Fire, therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,37,37,N´And who does greater evil than he who forges against God a lie, or cries lies to His signs? Those - their portion of the Book shall reach them; till, when Our messengers come to them, to take them away, they say, ´Where is that you were calling on, beside God?´ They will say, ´They have gone astray from us, and they will bear witness against themselves that they were unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,38,38,N´He will say, ´Enter among nations that passed away before you, jinn and mankind, into the Fire.´ Whenever any nation enters, it curses its sister-nation; till, when they have all successively come there, the last of them shall say to the first of them, ´O our Lord, these led us astray; so give them a double chastisement of the Fire.´ He will say, ´Unto each a double, but you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,39,39,N´The first of them shall say to the last of them, ´You have no superiority over us, then; so taste the chastisement for what you have been earning.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,40,40,N´Those that cry lies to Our signs and wax proud against them the gates of heaven shall not be opened to them, nor shall they enter Paradise until the camel passes through the eye of the needle. Even so We recompense the sinners;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,41,41,N´Gehenna shall be their cradle, above them coverings. Even so We recompense the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,42,42,N´And those who believe, and do deeds of righteousness -- We charge not any soul, save according to its capacity; those are the inhabitants of Paradise, therein dwelling forever;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,43,43,N´We shall strip away all rancour that is in their breasts; and underneath them rivers flowing; and they will say, ´Praise belongs to God, who guided us unto this; had God not guided us, we had surely never been guided. Indeed, our Lord´s Messengers came with the truth.´ And it will be proclaimed: ´This is your Paradise; you have been given it as your inheritance for what you did.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,44,44,N´The inhabitants of Paradise will call to the inhabitants of the Fire: ´We have found that. which our Lord promised us true; have you found what your Lord promised you true?´ ´Yes,´ they will say. And then a herald shall proclaim between them: ´God´s curse is on the evildoers´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,45,45,N´who bar from God´s way, desiring to make it crooked, disbelieving in the world to come.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,46,46,N´And between them is a veil, and on the Ramparts are men knowing each by their mark, who shall call to the inhabitants of Paradise: ´Peace be upon you! They have not entered it, for all their eagerness.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,47,47,N´And when their eyes are turned towards the inhabitants of the Fire they shall say, ´Our Lord, do not Thou assign us with the people of the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,48,48,N´And the dwellers on the Battlements shall call to certain men they know by their sign: ´Your amassing has not availed you, neither your waxing proud.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,49,49,N´Are these the ones that you swore God would never reach with mercy?´ ´Enter Paradise; no fear upon you, nor shall you sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,50,50,N´The inhabitants of the Fire shall call to the inhabitants of Paradise: ´Pour on us water, or of that God has provided you!´ They will say: ´God has forbidden them to the unbelievers´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,51,51,N´who have taken their religion as a diversion and a sport, and whom the present life has deluded.´ -- Therefore today We forget them as they forgot the encounter of this their day, and that they denied Our signs.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,52,52,N´And We have brought to them a Book that We have well distinguished, resting on knowledge, a guidance and a mercy unto a people that believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,53,53,N´Do they look for aught else but its interpretation? The day its interpretation comes, those who before forgot it shall say, ´Indeed, our Lord´s Messengers came with the truth. Have we then any intercessors to intercede for us, or shall we be returned, to do other than that we have done?´ They have indeed lost their souls, and that which they were forging has gone astray from them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,54,54,N´Surely your Lord is God, who created the heavens and the earth in six days -- then sat Himself upon the Throne, covering the day with the night it pursues urgently -- and the sun, and the moon, and the stars subservient, by His command. Verily, His are the creation and the command. Blessed be God, the Lord of all Being.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,55,55,N´Call on your Lord, humbly and secretly; He loves not transgressors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,56,56,N´Do not corruption in the land, after it has been set, right; and call on Him fearfully, eagerly -- surely the mercy of God is nigh to the good-doers;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,57,57,N´It is He who looses the winds, bearing good tidings before His mercy, till, when they are charged with heavy clouds, We drive it to a dead land and therewith send down water, and bring forth, therewith all the fruits. Even so We shall bring forth the dead; haply you will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,58,58,N´And the good land -- its vegetation comes forth by the leave of its Lord, and the corrupt -- it comes forth but scantily. Even so We turn about the signs for a people that are thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,59,59,N´And We sent Noah to his people; and he said, ´O my people, serve God! You have no god other´ than He; truly, I fear for you the chastisement of a dreadful day.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,60,60,N´Said the Council of his people, ´We see thee in manifest error.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,61,61,N´Said he, ´My people, there is no error in me; but I am a Messenger from the Lord of all Being.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,62,62,N´I deliver to you the Messages of my Lord, and I advise you sincerely; for I know from God that you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,63,63,N´What, do you wonder that a reminder from your Lord should come to you by the lips of a man from among you? That he may warn you, and you be godfearing, haply to find mercy.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,64,64,N´But they cried him lies; so We delivered him, and those with him, in the Ark, and We drowned those who cried lies to Our signs; assuredly they were a blind people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,65,65,N´And to Ad their brother Hood; he said, ´O my people, serve God! You have no god other than He; will you not be godfearing?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,66,66,N´Said the Council of the unbelievers of his people, ´We see thee in folly, and we think that thou art one of the liars.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,67,67,N´Said he, ´My people, there is no folly in me; but I am a Messenger from the Lord of all Being.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,68,68,N´I deliver to you the Messages of my Lord; I am your adviser sincere, faithful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,69,69,N´What, do you wonder that a reminder from your Lord should come to you by the lips of a man from among you? That he may warn you; and remember when He appointed you as successors after the people of Noah, and increased you in stature broadly; remember God´s bounties; haply you will prosper.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,70,70,N´They said, ´Why, hast thou come to us that we may serve God alone, and forsake that our fathers served? Then bring us that thou promisest us, if thou speakest truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,71,71,N´Said he, ´Anger and wrath from your Lord have fallen upon you. What, do you dispute with me regarding names you have named, you and your fathers, touching which God has sent down never authority? Then watch and wait; I shall be with you watching and waiting.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,72,72,N´So We delivered him, and those with him, by a mercy from Us; and We cut off the last remnant of those who cried lies to Our signs and were not believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,73,73,N´And to Thamood their brother Salih; he said, ´O my people, serve God! You have no god other than He; there has now come to you a clear sign from your Lord -- this is the She-camel of God, to be a sign for you. Leave her that she may eat in God´s earth, and do not touch her with evil, lest you be seized by a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,74,74,N´And remember when He appointed you successors after Ad, and lodged you in the land, taking to yourselves castles of its plains, and hewing its mountains into houses. Remember God´s bounties, and do not mischief in the earth, working corruption.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,75,75,N´Said the Council of those of his people who waxed proud to those that were abased, to those of them who believed, ´Do you know that Salih is an Envoy from his Lord?´ They said, ´In the Message he has been sent with we are believers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,76,76,N´Said the ones who waxed proud, ´As for us, we are unbelievers in the thing in which you believe.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,77,77,N´So they hamstrung the She-camel and turned in disdain from the commandment of their Lord, saying, ´O Salih, bring us that thou promisest us, if thou art an Envoy.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,78,78,N´So the earthquake seized them, and morning found them in their habitation fallen prostrate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,79,79,N´So he turned his back on them, and said, ´O my people, I have delivered to you the Message of my Lord, and advised you sincerely; but you do not love sincere advisers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,80,80,N´And Lot, when he said to his people, ´What, do you commit such indecency as never any being in all the world committed before you?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,81,81,N´See, you approach men lustfully instead of women; no, you are a people that do exceed.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,82,82,N´And the only answer of his people was that they said, ´Expel them from your city; surely they are folk that keep themselves clean!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,83,83,N´So We delivered him and his family, except his wife; she was one of those that tarried.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,84,84,N´And We rained down upon them a rain; so behold thou, how was the end of the sinners!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,85,85,N´And to Midian their brother Shuaib; he said, ´O my people, serve God! You have no god other than He; there has now come to you a clear sign from your Lord. So fill up the measure and the balance, and diminish not the goods of the people; and do not corruption in the land, after it has been set right; that is better for you, if you are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,86,86,N´And do not sit in every path, threatening and barring from God´s way those who believe in Him, desiring to make it crooked. And remember when you were few, and He multiplied you; and behold, how was the end of the workers of corruption.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,87,87,N´And if there is a party of you who believe in the Message I have been sent with, and a party who believe not, be patient till God shall judge between us; He is the best of judges.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,88,88,N´Said the Council of those of his people who waxed proud, ´We will surely expel thee, O Shuaib, and those who believe with thee, from our city, unless you return into our creed.´ He said, ´What, even though we detest it?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,89,89,N´We should have forged against God a lie if we returned into your creed; after God delivered us from it. It is not for us to return into it, unless God our Lord so will. Our Lord embraces all things in His knowledge. In God we have put our trust. Our Lord, give true deliverance between us and our people; Thou art the best of deliverers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,90,90,N´Said the Council of those of his people who disbelieved, ´Now, if you follow Shuaib, assuredly in that case you will be losers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,91,91,N´So the earthquake seized them, and morning found them in their habitation fallen prostrate,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,92,92,N´those who cried lies to Shuaib, as if never they dwelt there; those who cried lies to Shuaib, they were the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,93,93,N´So he turned his back on them, and said, ´O my people, I have delivered to you the Messages of my Lord, and advised you sincerely; how should I grieve for a people of unbelievers?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,94,94,N´We have sent no Prophet to any city but that We seized its people with misery and hardship, that haply they might be humble;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,95,95,N´then We gave them in the place of evil good, till they multiplied, and said, ´Hardship and happiness visited our fathers.´ So We seized them suddenly, unawares.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,96,96,N´Yet had the peoples of the cities believed and been godfearing, We would have opened upon them blessings from heaven and earth; but they cried lies, and so We seized them for what they earned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,97,97,N´Do the people of the cities feel secure Our might shall not come upon them at night while they are sleeping?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,98,98,N´Do the people of the cities feel secure Our might shall not come upon them in daylight while they are playing?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,99,99,N´Do they feel secure against God´s devising? None feels secure against God´s devising but the people of the lost.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,100,100,N´Is it not a guidance to those who inherit the earth after those who inhabited it that, did We will, We would smite them because of their sins, sealing their hearts so they do not hear?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,101,101,N´Those cities We relate to thee tidings of; their Messengers came to them with the clear signs, but they were not the ones to believe in that they had cried lies before; so God seals the hearts of the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,102,102,N´We found no covenant in the most part of them; indeed, We found the most part of them ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,103,103,N´Then We sent, after them, Moses with Our signs to Pharaoh and his Council, but they did them wrong; so behold thou, how was the end of the workers of corruption!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,104,104,N´Moses said, ´Pharaoh, I am a Messenger from the Lord of all Being,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,105,105,N´worthy to say nothing regarding God except the truth. I have brought a clear sign to you from your Lord; so send forth with me the Children of Israel.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,106,106,N´Said he, ´If thou hast brought a sign, produce it, if thou speakest truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,107,107,N´So he cast his staff; and behold, it was a serpent manifest.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,108,108,N´And he drew forth his hand, and lo, it was white to the beholders.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,109,109,N´Said the Council of the people of Pharaoh, ´Surely this man is a cunning sorcerer´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,110,110,N´who desires to expel you from your land; what do you command?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,111,111,N´They said, ´Put him and his brother off a while, and send among the cities musterers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,112,112,N´to bring thee every cunning sorcerer.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,113,113,N´And the sorcerers came to Pharaoh, saying, ´We shall surely have a wage, if we should be the victors?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,114,114,N´He said, ´Yes, indeed; and you shall be among the near-stationed.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,115,115,N´They said, ´Moses, wilt thou cast, or shall we be the casters?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,116,116,N´He said, ´You cast.´ And when they cast they put a Spell upon the people´s eyes, and called forth fear of them, and produced a mighty sorcery.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,117,117,N´And We revealed to Moses: ´Cast thy staff!´ And lo, it forthwith swallowed up their lying invention.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,118,118,N´So the truth came to pass, and false was proved what they were doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,119,119,N´So they were vanquished there, and they turned about, humbled.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,120,120,N´And the sorcerers were cast down, bowing themselves.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,121,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,121,121,N´They said, ´We believe in the Lord of all Being,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,122,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,122,122,N´the Lord of Moses and Aaron.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,123,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,123,123,N´Said Pharaoh, ´You have believed in Him before I gave you leave. Surely this is a device you have devised in the city that you may expel its people from it. Now you shall know!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,124,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,124,124,N´I shall assuredly cut off alternately your hands and feet, then I shall crucify you all together.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,125,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,125,125,N´They said, ´Surely unto our Lord we are turning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,126,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,126,126,N´Thou takest vengeance upon us only because we have believed in the signs of our Lord when they came to us. Our Lord, pour out upon us patience, and gather us unto Thee surrendering.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,127,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,127,127,N´Then said the Council of the people of Pharaoh, ´Wilt thou leave Moses and his people to work corruption in the land, and leave thee and thy gods?´ Said he, ´We shall slaughter their sons and spare their women; surely we are triumphant over them!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,128,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,128,128,N´Said Moses to his people, ´Pray for succour to God, and be patient; surely the earth is God´s and He bequeaths it to whom He will among His servants. The issue ultimate is to the godfearing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,129,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,129,129,N´They said, ´We have been hurt before thou camest to us, and after thou camest to us.´ He said, ´Perchance your Lord will destroy your enemy, and will make you successors in the land, so that He may behold how you shall do.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,130,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,130,130,N´Then seized We Pharaoh´s people with years of dearth, and scarcity of fruits, that haply they might remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,131,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,131,131,N´So, when good came to them, they said, ´This belongs to us´; but if any evil smote them, they would augur ill by Moses and those with him. Why, surely their ill augury was with God; but the most of them knew not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,132,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,132,132,N´And they said, ´Whatsoever sign thou bringest to us, to cast a spell upon us, we will not believe thee.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,133,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,133,133,N´So We let loose upon them the flood and the locusts, the lice and the frogs, the blood, distinct signs; but they waxed proud and were a sinful people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,134,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,134,134,N´And when the wrath fell upon them, they said, ´Moses, pray to thy Lord for us by the covenant He has made with thee. If thou removest from us the wrath, surely we will believe thee, and send forth with thee the Children of Israel.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,135,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,135,135,N´But when We removed from them the wrath unto a term that they should come to, lo, they broke their troth.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,136,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,136,136,N´So We took vengeance on them, and drowned them in the sea, for that they cried lies to Our signs and heeded them not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,137,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,137,137,N´And We bequeathed upon the people that were abased all the east and the west of the land We had blessed; and perfectly was fulfilled the most fair word of thy Lord upon the Children of Israel, for that they endured patiently; and We destroyed utterly the works of Pharaoh and his people, and what they had been building.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,138,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,138,138,N´And We brought the Children of Israel over the sea, and they came upon a people cleaving to idols they had. They said, ´Moses, make for us a god, as they have gods.´ Said he, ´You are surely a people who are ignorant.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,139,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,139,139,N´Surely this they are engaged upon shall be shattered, and void is what they have been doing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,140,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,140,140,N´He said, ´What, shall I seek a god for you other than God, who has preferred you above all beings?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,141,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,141,141,N´And when We delivered you from the folk of Pharaoh who were visiting you with evil chastisement, slaying your sons, and sparing your women -- and in that was a grievous trial from your Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,142,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,142,142,N´And We appointed with Moses thirty nights and We completed them with ten, so the appointed time of his Lord was forty nights; and Moses said to his brother Aaron, ´Be my successor among my people, and put things right, and do not follow the way of the workers of corruption.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,143,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,143,143,N´And when Moses came to Our appointed time and his Lord spoke with him, he said, ´Oh my Lord, show me, that I may behold Thee!´ Said He, ´Thou shalt not see Me; but behold the mountain -- if it stays fast in its place, then thou shalt see Me.´ And when his Lord revealed Him to the mountain He made it crumble to dust; and Moses fell down swooning. So when he awoke, he said, ´Glory be to Thee! I repent to Thee; I am the first of the believers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,144,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,144,144,N´Said He, ´Moses, I have chosen thee above all men for My Messages and My Utterance; take what I have given thee, and be of the thankful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,145,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,145,145,N´And We wrote for him on the Tablets of everything an admonition, and a distinguishing of everything: ´So take it forcefully, and command thy people to take the fairest of it. I shall show you the habitation of the ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,146,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,146,146,N´I shall turn from My signs those who wax proud in the earth unjustly; though they see every sign, they will not believe in it, and though they see the way of rectitude they will not take it for a way, and though they see the way of error, they will take it for a way. That, because they have cried lies to Our signs and heeded them not.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,147,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,147,147,N´Those who cry lies to Our signs, and the encounter in the world to come -- their works have failed; shall they be recompensed, except according to the things they have done?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,148,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,148,148,N´And the people of Moses took to them, after him, of their ornaments a Calf -- a mere body that lowed. Did they not see it spoke not to them, neither guided them upon any way? Yet they took it to them, and were evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,149,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,149,149,N´And when they smote their hands, and saw that they had gone astray, they said, ´If our Lord has not mercy on us, and forgives us not, surely we shall be of the lost.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,150,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,150,150,N´And when Moses returned to his people, angry and sorrowful, he said, ´Evilly have you done in my place, after me; what, have you outstripped your Lord´s commandment?´ And he cast down the Tablets, and laid hold of his brother´s head, dragging him to him. He said, ´Son of my mother, surely the people have abased me, and well nigh slain me. Make not my enemies to gloat over me, and put me not among the people of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,151,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,151,151,N´He said, ´O my Lord, forgive me and my brother and enter us into Thy mercy; Thou art the most merciful of the merciful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,152,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,152,152,N´´Surely those who took to themselves the Calf -- anger shall overtake them from their Lord, and abasement in this present life; so We recompense those who are forgers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,153,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,153,153,N´And those who do evil deeds, then repent thereafter and believe, surely thereafter thy Lord is All-forgiving, All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,154,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,154,154,N´And when Moses´ anger abated in him, he took the Tablets and in the inscription of them was guidance, and mercy unto all those who hold their Lord in awe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,155,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,155,155,N´And Moses chose of his people seventy men for Our appointed time; and when the earthquake seized them, he said, ´My Lord, hadst Thou willed Thou wouldst have destroyed them before, and me. Wilt Thou destroy us for what the foolish ones of us have done? It is only Thy trial, whereby Thou leadest astray whom Thou wilt, and guidest whom Thou wilt. Thou art our Protector; so forgive us, and have mercy on us, for Thou art the best of forgivers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,156,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,156,156,N´And prescribe for us in this world good, and in the world to come; we have repented unto Thee.´ Said He, ´My chastisement -- I smite with it whom I will; and My mercy embraces all things, and I shall prescribe it for those who are godfearing and pay the alms, and those who indeed believe in Our signs,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,157,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,157,157,N´those who follow the Messenger, ´the Prophet of the common folk, whom they find written down with them in the Torah and the Gospel, bidding them to honour, and forbidding them dishonour, making lawful for them the good things and making unlawful for them the corrupt things, and relieving them of their loads, and the fetters that were upon them. Those who believe in him and succour him and help him, and follow the light that has been sent down with him -- they are the prosperers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,158,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,158,158,N´Say: ´O mankind, I am the Messenger of God to you all, of Him to whom belongs the kingdom of the heavens and of the earth. There is no god but He. He gives life, and makes to die. Believe then in God, and in His Messenger, the Prophet of the common folk, who believes in God and His words, and follow him; haply so you will be guided.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,159,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,159,159,N´Of the people of Moses there is a nation who guide by the truth, and by it act with justice.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,160,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,160,160,N´And We cut them up into twelve tribes, nations. And We revealed to Moses, when his people asked him for water: ´Strike with thy staff the rock´; and there gushed forth from it twelve fountains all the people knew now their drinking-place. And We outspread the cloud to overshadow them, and We sent down manna and quails upon them: ´Eat of the good things wherewith We have supplied you.´ And they worked no wrong upon Us, but themselves they wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,161,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,161,161,N´And when it was said to them, ´Dwell in this township and eat of it wherever you will; and say, Unburdening; and enter in at the gate, prostrating; We will forgive you your transgressions, and increase the good-doers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,162,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,162,162,N´Then the evildoers of them substituted a saying other than that which had been said to them; so We sent down upon them wrath out of heaven for their evildoing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,163,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,163,163,N´And question them concerning the township which was bordering the sea, when they transgressed the Sabbath, when their fish came to them on the day of their Sabbath, swimming shorewards, but on the day they kept not Sabbath, they came not unto them. Even so We were trying them for their ungodliness.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,164,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,164,164,N´And when a certain nation of them said, ´Why do you admonish a people God is about to destroy or to chastise with a terrible chastisement?´ They said, ´As an excuse to your Lord; and haply they will be godfearing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,165,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,165,165,N´So, when they forgot that they were reminded of; We delivered those who were forbidding wickedness, and We seized the evildoers with evil chastisement for their ungodliness.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,166,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,166,166,N´And when they turned in disdain from that forbidding We said to them, ´Be you apes, miserably slinking!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,167,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,167,167,N´And when Thy Lord proclaimed He would send forth against them, unto the Day of Resurrection, those who should visit them with evil chastisement. Surely thy Lord is swift in retribution; surely He is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,168,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,168,168,N´And We cut them up into nations in the earth, some of them righteous, and some of them otherwise; and We tried them with good things and evil, that haply they should return.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,169,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,169,169,N´And there succeeded after them a succession who inherited the Book, taking the chance goods of this lower world, and saying, ´It will be forgiven us´; and if chance goods the like of them come to them, they will take them. Has not the compact of the Book been taken touching them, that they should say concerning God nothing but the truth? And they have studied what is in it; and the Last Abode is better for those who are godfearing. Do you not understand?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,170,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,170,170,N´And those who hold fast to the Book, and perform the prayer -- surely We leave not to waste the wage of those who set aright.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,171,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,171,171,N´And when We shook the mountain above them as if it were a canopy, and they supposed it was about to fall on them: ´Take forcefully what We have given you, and remember what is in it; haply you will be godfearing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,172,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,172,172,N´And when thy Lord took from the Children of Adam, from their loins, their seed, and made them testify touching themselves, ´Am I not your Lord?´ They said, ´Yes, we testify´-- lest you should say on the Day of Resurrection, ´As for us, we were heedless of this,´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,173,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,173,173,N´or lest you say, ´Our fathers were idolaters aforetime, and we were seed after them. What, wilt Thou then destroy us for the deeds of the vain-doers?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,174,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,174,174,N´So We distinguish the signs; and haply they will return.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,175,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,175,175,N´And recite to them the tiding of him to whom We gave Our signs, but he cast them off; and Satan followed after him, and he became one of the perverts.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,176,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,176,176,N´And had We willed, We would have raised him up thereby; but he inclined towards the earth and followed his lust. So the likeness of him is as the likeness of a dog; if thou attackest it it lolls its tongue out, or if thou leavest it, it lolls its tongue out. That is that people´s likeness who cried lies to Our signs. So relate the story; haply they will reflect.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,177,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,177,177,N´An evil likeness is the likeness of the people who cried lies to Our signs, and themselves were wronging.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,178,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,178,178,N´Whomsoever God guides, he is rightly guided; and whom He leads astray -- they are the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,179,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,179,179,N´We have created for Gehenna many jinn and men: they have hearts, but understand not with them; they have eyes, but perceive not with them; they have ears, but they hear not with them. They are like cattle; nay, rather they are further astray. Those -- they are the heedless.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,180,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,180,180,N´To God belong the Names Most Beautiful; so call Him by them, and leave those who blaspheme His Names -- they shall assuredly be recompensed for the things they did.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,181,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,181,181,N´Of those We created are a nation who guide by the truth, and by it act with justice.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,182,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,182,182,N´And those who cry lies to Our signs We will draw them on little by little whence they know not;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,183,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,183,183,N´and I respite them -- assuredly My guile is sure.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,184,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,184,184,N´Have they not reflected? No madness is in their comrade; he is naught but a plain warner.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,185,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,185,185,N´Or have they not considered the dominion of the heaven and of the earth, and what things God has created, and that it may be their term is already nigh? In what manner of discourse then will they after this believe?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,186,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,186,186,N´Whomsoever God leads astray, no guide he has; He leaves them in their insolence blindly wandering.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,187,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,187,187,N´They will question thee concerning the Hour, when it shall berth. Say: ´The knowledge of it is only with my Lord; none shall reveal it at its proper time, but He. Heavy is it in the heavens and the earth; it will not come on you but suddenly!´ They will question thee, as though thou art well-informed of it. Say: ´The knowledge of it is only with God, but most men know not.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,188,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,188,188,N´Say: ´I have no power to profit for myself; or hurt, but as God will. Had I knowledge of the Unseen I would have acquired much good, and evil would not have touched me. I am only a warner, and a bearer of good tidings, to a people believing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,189,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,189,189,N´It is He who created you out of one living soul, and made of him his spouse that he might rest in her. Then, when he covered her, she bore a light burden and passed by with it; but when it became heavy they cried to God their Lord, ´If Thou givest us a righteous son, we indeed shall be of the thankful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,190,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,190,190,N´Thereafter, when He gave them a righteous son, they assigned Him associates in that He had given them; but God is high exalted above that they associate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,191,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,191,191,N´What, do they associate that which creates nothing and themselves are created,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,192,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,192,192,N´and that have no power to help them, neither they help themselves?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,193,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,193,193,N´If you call them to guidance they will not follow you; equal it is to you whether you call them, or whether you are silent.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,194,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,194,194,N´Those on whom you call apart from God, are servants the likes of you; call them and let them answer you, if you speak truly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,195,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,195,195,N´What, have they feet wherewith they walk, or have they hands wherewith they lay hold, or have they eyes wherewith they see, or have they ears wherewith they give ear? Say: ´Call you then to your associates; then try your guile on me, and give me no respite.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,196,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,196,196,N´My Protector is God who sent down the Book, and He takes into His protection the righteous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,197,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,197,197,N´And those on whom you call, apart from God, have no power to help you, neither they help themselves.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,198,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,198,198,N´If you call them to the guidance they do not hear; and thou seest them looking at thee, unperceiving.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,199,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,199,199,N´Take the abundance, and bid to what is honourable, and turn away from the ignorant.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,200,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,200,200,N´If a provocation from Satan should provoke thee, seek refuge in God; He is All-hearing, All-seeing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,201,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,201,201,N´The godfearing, when a visitation of Satan troubles them, remember, and then see clearly;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,202,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,202,202,N´and their brothers they lead on into error, then they stop not short.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,203,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,203,203,N´And when thou bringest them not a sign, they say, ´Why hast thou not chosen one?´ Say: ´I follow only what is revealed to me from my Lord; this is clear testimony from your Lord, guidance, and mercy for a people of believers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,204,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,204,204,N´And when the Koran is recited, give you ear to it and be silent; haply so you will find mercy.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,205,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,205,205,N´Remember thy Lord in thy soul, humbly and fearfully, not loud of voice, at morn and eventide. Be not thou among the heedless.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,7,206,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,7,206,206,N´Surely those who are with thy Lord wax not too proud to serve Him; they chant His praise, and to Him they bow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,1,1,N´They will question thee concerning the spoils. Say: ´The spoils belong to God and the Messenger; so fear you God, and set things right between you, and obey you God and His Messenger, if you are believers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,2,2,N´Those only are believers who, when God is mentioned, their hearts quake, and when His signs are recited to them, it increases them in faith, and in their Lord they put their trust,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,3,3,N´those who perform the prayer, and expend of what We have provided them,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,4,4,N´those in truth are the believers; they have degrees with their Lord, and forgiveness, and generous provision.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,5,5,N´As thy Lord brought thee forth from thy house with the truth, and a part of the believers were averse to it,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,6,6,N´disputing with thee concerning the truth after it had become clear, as though they were being driven into death with their eyes wide open.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,7,7,N´And when God promised you one of the two parties should be yours, and you were wishing that the one not accoutred should be yours; but God was desiring to verify the truth by His words, and to cut off the unbelievers to the last remnant,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,8,8,N´and that He might verify the truth and prove untrue the untrue, though the sinners were averse to it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,9,9,N´When you were calling upon your Lord for succour, and He answered you, ´I shall reinforce you with a thousand angels riding behind you.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,10,10,N´God wrought this not, save as good tidings and that your hearts thereby might be at rest; help comes only from God; surely God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,11,11,N´When He was causing slumber to overcome you as a security from Him, and sending down on you water from heaven, to purify you thereby, and to put away from you the defilement of Satan, and to strengthen your hearts, and to confirm your feet.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,12,12,N´When thy Lord was revealing to the angels, ´I am with you; so confirm the believers. I shall cast into the unbelievers´ hearts terror; so smite above the necks, and smite every finger of them!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,13,13,N´That, because they had made a breach with God and with His Messenger; and whosoever makes a breach with God and with His Messenger, surely God is terrible in retribution.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,14,14,N´That for you; therefore taste it; and that the chastisement of the Fire is for the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,15,15,N´O believers, when you encounter the unbelievers marching to battle, turn not your backs to them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,16,16,N´Whoso turns his back that day to them, unless withdrawing to fight again or removing to join another host, he is laden with the burden of God´s anger, and his refuge is Gehenna -- an evil homecoming!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,17,17,N´You did not slay them, but God slew them; and when thou threwest, it was not thyself that threw, but God threw, and that He might confer on the believers a fair benefit; surely God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,18,18,N´That for you; and that God weakens the unbelievers´ guile.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,19,19,N´If victory you are seeking, victory has already come upon you; and if you give over, it is better for you. But if you return, We shall return, and your host will avail you nothing though it be numerous; and that God is with the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,20,20,N´O believers, obey God and His Messenger, and do not turn away from Him, even as you are listening;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,21,21,N´and be not as those who say, ´We hear,´ and they hear not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,22,22,N´Surely the worst of beasts in God´s sight are those that are deaf and dumb and do not understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,23,23,N´If God had known of any good in them He would have made them hear; and if He had made them hear, they would have turned away, swerving aside.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,24,24,N´O believers, respond to God and the Messenger when He calls you unto that which will give you life; and know that God stands between a man and his heart, and that to Him you shall be mustered.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,25,25,N´And fear a trial which shall surely not smite in particular the evildoers among you; and know that God is terrible in retribution.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,26,26,N´And remember when you were few and abased in the land, and were fearful that the people would snatch you away; but He gave you refuge, and confirmed you with His help, and provided you with the good things, that haply you might be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,27,27,N´O believers, betray not God and the Messenger, and betray not your trusts and that wittingly;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,28,28,N´and know that your wealth and your children are a trial, and that with God is a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,29,29,N´O believers, if you fear God, He will assign you a salvation, and acquit you of your evil deeds, and forgive you; and God is of bounty abounding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,30,30,N´And when the unbelievers were devising against thee, to confine thee, or slay thee, or to expel thee, and were devising, and God was devising; and God is the best of devisers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,31,31,N´And When Our signs were being recited to them, they said, ´We have already heard; if we wished, we could say the like of this; this is naught but the fairy-tales of the ancients.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,32,32,N´And when they said, ´O God, if this be indeed the truth from Thee, then rain down upon us stones out of heaven, or bring us a painful chastisement.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,33,33,N´But God would never chastise them, with thee among them; God would never chastise them as they begged forgiveness.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,34,34,N´But what have they now, that God should not chastise them, when they are barring from the Holy Mosque, not being its protectors? Its only protectors are the godfearing; but most of them know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,35,35,N´And their prayer at the House is nothing but a whistling and a clapping of hands -- therefore taste you now the chastisement for your unbelief!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,36,36,N´The unbelievers expend their wealth to bar from God´s way, and still they will expend it, till it is an anguish for them, then be overthrown, and the unbelievers will be mustered into Gehenna,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,37,37,N´that God may distinguish the corrupt from the good, and place the corrupt one upon another, and so heap them up all together, and put them in Gehenna; those are the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,38,38,N´Say to the unbelievers, if they give over He will forgive them what is past; but if they return, the wont of the ancients is already gone!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,39,39,N´Fight them, till there is no persecution and the religion is God´s entirely; then if they give over, surely God sees the things they do;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,40,40,N´but if they turn away, know that God is your Protector -- an excellent Protector, an excellent Helper!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,41,41,N´Know that, whatever booty you take, the fifth of it is God´s, and the Messenger´s, and the near kinsman´s, and the orphans´, and for the needy, and the traveller, if you believe in God and that We sent down upon Our servant on the day of salvation,. the day the two hosts encountered; and God is powerful over everything;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,42,42,N´when you were on the nearer bank, and they were on the farther bank, and the cavalcade was below you; and had you made tryst together, you would have surely failed the tryst; but that God might determine a matter that was done, that whosoever perished might perish by a clear sign, and by a clear sign he might live who lived; and surely God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,43,43,N´When God showed thee them in thy dream as few; and had He shown them as many you would have lost heart, and quarrelled about the matter; but God saved; He knows the thoughts in the breasts.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,44,44,N´When God showed you them in your eyes as few, when you encountered, and made you few in their eyes, that God might determine a matter that was done; and unto God all matters are returned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,45,45,N´O believers, whensoever you encounter a host, then stand firm, and remember God frequently; haply so you will prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,46,46,N´And obey God, and His Messenger, and do not quarrel together, and so lose heart, and your power depart; and be patient; surely God is with the patient.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,47,47,N´Be not as those who went forth from their habitations swaggering boastfully to show off to men, and barring from God´s way; and God encompasses the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,48,48,N´And when Satan decked out their deeds fair to them, and said, ´Today no man shall overcome you, for I shall be your neighbour.´ But when the two hosts sighted each other, he withdrew upon his heels, saying, ´I am quit of you; for I see what you do not see. I fear God; and God is terrible in retribution.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,49,49,N´When the hypocrites, and those in whose hearts was sickness, said, ´Their religion has deluded them´; but whosoever puts his trust in God, surely God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,50,50,N´If thou couldst only see when the angels take the unbelievers, beating their faces and their backs: ´Taste the chastisement of the burning --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,51,51,N´that, for what your hands have forwarded, and for that God is never unjust unto His servants.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,52,52,N´Like Pharaoh´s folk, and the people before him, who disbelieved in God´s signs; God seized them because of their sins; God is strong, terrible in retribution.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,53,53,N´That is because God would never change His favour that He conferred on a people until they changed what was within themselves; and that God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,54,54,N´Like Pharaoh´s folk, and the people before him, who cried lies to the signs of their Lord, so We destroyed them because of their sins, and We drowned the folk of Pharaoh; and all were evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,55,55,N´Surely the worst of beasts in God´s sight are the unbelievers, who will not believe,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,56,56,N´those of them with whom thou hast made compact then they break their compact every time, not being godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,57,57,N´So, if thou comest upon them anywhere in the war, deal with them in such wise as to scatter the ones behind them; haply they will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,58,58,N´And if thou fearest treachery any way at the hands of a people, dissolve it with them equally; surely God loves not the treacherous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,59,59,N´And thou art not to suppose that they who disbelieve have outstripped Me; they cannot frustrate My will.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,60,60,N´Make ready for them whatever force and strings of horses you can, to terrify thereby the enemy of God and your enemy, and others besides them that you know not; God knows them. And whatsoever you expend in the way of God shall be repaid you in full; you will not be wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,61,61,N´And if they incline to peace, do thou incline to it; and put thy trust in God; He is the All-hearing, the All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,62,62,N´And if they desire to trick thee, God is sufficient for thee; He has confirmed thee with His help, and with the believers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,63,63,N´and brought their hearts together. Hadst thou expended all that is in the earth, thou couldst not have brought their hearts together; but God brought their hearts together; surely He is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,64,64,N´O Prophet, God suffices thee, and the believers who follow thee.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,65,65,N´O Prophet, urge on the believers to fight. If there be twenty of you, patient men, they will overcome two hundred; if there be a hundred of you, they will overcome a thousand unbelievers, for they are a people who understand not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,66,66,N´Now God has lightened it for you, knowing that there is weakness in you. If there be a hundred of you, patient men, they will overcome two hundred; if there be of you a thousand, they will overcome two thousand by the leave of God; God is with the patient.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,67,67,N´It is not for any Prophet to have prisoners until he make wide slaughter in the land. You desire the chance goods of the present world, and God desires the world to come; and God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,68,68,N´Had it not been for a prior prescription from God, there had afflicted you, for what you took, a mighty chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,69,69,N´Eat of what you have taken as booty, such as is lawful and good; and fear you God; surely God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,70,70,N´O Prophet, say to the prisoners in your hands: ´If God knows of any good in your hearts He will give you better than what has been taken from you, and He will forgive you; surely God is All-forgiving, All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,71,71,N´And if they desire treachery against thee, they have tricked God before; but He has given thee power over them; and God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,72,72,N´Those who believe, and have emigrated and struggled with their possessions and their selves in the way of God, and those who have given refuge and help -- those are friends one of another. And those who believe, but have not emigrated -- you have no duty of friendship towards them till they emigrate; yet if they ask you for help, for religion´s sake, it is your duty to help them, except against a people between whom and you there is a compact; and God sees the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,73,73,N´As for the unbelievers, they are friends one of another. Unless you do this, there will be persecution in the land and great corruption.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,74,74,N´And those who believe, and have emigrated and struggled in the way of God. those who have given refuge and help those in truth are the believers, and theirs shall be forgiveness and generous provision.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,8,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,8,75,75,N´And those who have believed afterwards and emigrated, and struggled with you -- they belong to you; but those related by blood are nearer to one another in the Book of God; surely God has knowledge of everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,1,1,N´An acquittal, from God and His Messenger, unto the idolaters with whom you made covenant:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,2,2,N´´Journey freely in the land for four months; and know that you cannot frustrate the will of God, and that God degrades the unbelievers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,3,3,N´A proclamation, from God and His Messenger, unto mankind on the day of the Greater Pilgrimage: ´God is quit, and His Messenger, of the idolaters. So if you repent, that will be better for you; but if you turn your backs; know that you cannot frustrate the will of God. And give thou good tidings to the unbelievers of a painful chastisement;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,4,4,N´excepting those of the idolaters with whom you made covenant, then they failed. you naught neither lent support to any man against you. With them fulfil your covenant till their term; surely God loves the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,5,5,N´Then, when the sacred months are drawn away, slay the idolaters wherever you find them, and take them, and confine them, and lie in wait for them at every place of ambush. But if they repent, and perform the prayer, and pay the alms, then let them go their way; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,6,6,N´And if any of the idolaters seeks of thee protection, grant him protection till he hears the words of God; then do thou convey him to his place of security -- that, because they are a people who do not know.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,7,7,N´How should the idolaters have a covenant with God and His Messenger? -- excepting those with whom you made covenant at the Holy Mosque; so long as they go straight with you, do you go straight with them; surely God loves the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,8,8,N´How? If they get the better of you, they will not observe towards you any bond or treaty, giving you satisfaction with their mouths but in their hearts refusing; and the most of them are ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,9,9,N´They have sold the signs of God for a small price, and have barred from His way; truly evil is that they have been doing,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,10,10,N´observing neither bond nor treaty towards a believer; they are the transgressors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,11,11,N´Yet if they repent, and perform the prayer, and pay the alms, then they are your brothers in religion; and We distinguish the signs for a people who know.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,12,12,N´But if they break their oaths after their covenant and thrust at your religion, then fight the leaders of unbelief; they have no sacred oaths; haply they will give over.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,13,13,N´Will you not fight a people who broke their oaths and purposed to expel the Messenger, beginning the first time against you? Are you afraid of them? You would do better to be afraid of God, if you are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,14,14,N´Fight them, and God will chastise them at your hands and degrade them, and He will help you against them, and bring healing to the breasts of a people who believe,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,15,15,N´and He will remove the rage within their hearts; and God turns towards whomsoever He will; God is All-knowing, All-wise´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,16,16,N´Or did you suppose you would be left in peace, and God knows not as yet those of you who have struggled, and taken not -- apart from God and His Messenger and the believers -- any intimate? God is aware of what you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,17,17,N´It is not for the idolaters to inhabit God´s places of worship, witnessing against themselves unbelief; those -- their works have failed them, and in the Fire they shall dwell forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,18,18,N´Only he shall inhabit God´s places of worship who believes in God and the Last Day, and performs the prayer, and pays the alms, and fears none but God alone; it may be that those will be among the guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,19,19,N´Do you reckon the giving of water to pilgrims and the inhabiting of the Holy Mosque as the same as one who believes in God and the Last Day and struggles in the way of God? Not equal are they in God´s sight; and God guides not the people of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,20,20,N´Those who believe, and have emigrated, and have struggled in the way of God with their possessions and their selves are mightier in rank with God; and those -- they are the triumphant;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,21,21,N´their Lord gives them good tidings of mercy from Him and good pleasure; for them await gardens wherein is lasting bliss,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,22,22,N´therein to dwell forever and ever; surely with God is a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,23,23,N´O believers, take not your fathers and brothers to be your friends, if they prefer unbelief to belief; whosoever of you takes them for friends, those -- they are the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,24,24,N´Say: ´If your fathers, your sons, your brothers, your wives, your clan, your possessions that you have gained, commerce you fear may slacken, dwellings you love -- if these are dearer to you than God and His Messenger, and to struggle in His way, then wait till God brings His command; God guides not the people of the ungodly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,25,25,N´God has already helped you on many fields, and on the day of Hunain, when your multitude was pleasing to you, but it availed you naught, and the land for all its breadth was strait for you, and you turned about, retreating.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,26,26,N´Then God sent down upon His Messenger His Shechina, and upon the believers, and He sent down legions you did not see, and He chastised the unbelievers; and that is the recompense of the unbelievers;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,27,27,N´then God thereafter turns towards whom He will; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,28,28,N´O believers, the idolaters are indeed unclean; so let them not come near the Holy Mosque after this year of theirs. If you fear poverty, God shall surely enrich you of His bounty, if He will; God is All-knowing; All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,29,29,N´Fight those who believe not in God and the Last Day and do not forbid what God and His Messenger have forbidden -- such men as practise not the religion of truth, being of those who have been given the Book -- until they pay the tribute out of hand and have been humbled.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,30,30,N´The Jews say, ´Ezra is the Son of God´; the Christians say, ´The Messiah is the Son of God.´ That is the utterance of their mouths, conforming with the unbelievers before them. God assail them! How they are perverted!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,31,31,N´They have taken their rabbis and their monks as lords apart from God, and the Messiah, Mary´s son -- and they were commanded to serve but One God; there is no god but He; glory be to Him, above that they associate --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,32,32,N´desiring to extinguish with their mouths God´s light; and God refuses but to perfect His light, though the unbelievers be averse.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,33,33,N´It is He who has sent His Messenger with the guidance and the religion of truth, that He may uplift it above every religion, though the unbelievers be averse.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,34,34,N´O believers, many of the rabbis and monks indeed consume the goods of the people in vanity and bar from God´s way. Those who treasure up gold and silver, and do not expend them in the way of God -- give them the good tidings of a painful chastisement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,35,35,N´the day they shall be heated in the fire of Gehenna and therewith their foreheads and their sides and their backs shall be branded: ´This is the thing you have treasured up for yourselves; therefore taste you now what you were treasuring!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,36,36,N´The number of the months, with God, is twelve in the Book of God, the day that He created the heavens and the earth; four of them are sacred. That is the right religion. So wrong not each other during them. And fight the unbelievers totally even as they fight you totally and know that God is with the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,37,37,N´The month postponed is an increase of unbelief whereby the unbelievers go astray; one year they make it profane, and hallow it another, to agree with the number that God has hallowed, and so profane what God has hallowed. Decked out fair to them are their evil deeds; and God guides not the people of the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,38,38,N´O believers, what is amiss with you, that when it is said to you, ´Go forth in the way of God,´ you sink down heavily to the ground? Are you so content with this present life, rather than the world to come? Yet the enjoyment of this present life, compared with the world to come, is a little thing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,39,39,N´If you go not forth, He will chastise you with a painful chastisement, and instead of you He will substitute another people; and you will not hurt Him anything, for God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,40,40,N´If you do not help him, yet God has helped him already, when the unbelievers drove him forth the second of two, when the two were in the Cave, when he said to his companion, ´Sorrow not; surely God is with us.´ Then God sent down on him His Shechina, and confirmed him with legions you did not see; and He made the word of the unbelievers the lowest; and God´s word is the uppermost; God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,41,41,N´Go forth, light and heavy! Struggle in God´s way with your possessions and your selves; that is better for you, did you know.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,42,42,N´Were it a gain near at hand, and an easy journey, they would have followed thee; but the distance was too far for them. Still they will swear by God, ´Had we been able, we would have gone out with you,´ so destroying their souls; and God knows that they are truly liars.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,43,43,N´God pardon thee! Why gavest thou them leave, till it was clear to thee which of them spoke the truth, and thou knewest the liars?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,44,44,N´Those who believe in God and the Last Day ask not leave of thee, that they may struggle with their possessions and their selves; and God knows the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,45,45,N´They only ask leave of thee who believe not in God and the Last Day, those whose hearts are filled with doubt, so that in their doubt they go this way and that.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,46,46,N´If they had desired to go forth, they would have made some preparation for it; but God was averse that they should be aroused, so He made them pause, and it was said to them, ´Tarry you with the tarriers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,47,47,N´Had they gone forth among you, they would only have increased you in trouble, and run to and fro in your midst, seeking to stir up sedition between you; and some of you would listen to them; and God knows the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,48,48,N´They sought to stir up sedition already before, and turned things upside down for thee, until the truth came, and God´s command appeared, though they were averse.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,49,49,N´Some of them there are that say, ´Give me leave and do not tempt me.´ Have not such men fallen into temptation? And surely Gehenna encompasses the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,50,50,N´If good fortune befalls thee, it vexes them; but if thou art visited by an affliction, they say, ´We took our dispositions before´, and turn away, rejoicing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,51,51,N´Say: ´Naught shall visit us but what God has prescribed for us; He is our Protector; in God let the believers put all their trust.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,52,52,N´Say: ´Are you awaiting for aught to come to us but one of the two rewards most fair? We are awaiting in your case too, for God to visit you with chastisement. from Him, or at our hands; so await; we are awaiting with you.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,53,53,N´Say: ´Expend willingly, or unwillingly, it shall not be accepted from you; you are surely a people ungodly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,54,54,N´And naught prevents that their expendings should be accepted from them, but that they believe not in God and His Messenger, and perform not the prayer save lazily, and that they expend not without they are averse.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,55,55,N´So let not their possessions or their children please thee; God only desires thereby to chastise them in this present life, and that their souls should depart while they are unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,56,56,N´They swear by God that they belong with you, but they are not of you they are a people that are afraid.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,57,57,N´If they could find a shelter, or some caverns, or any place to creep into, they would turn about and bolt away to it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,58,58,N´Some of them find fault with thee touching the freewill offerings; if they are given a share of them they are well-pleased, but if they are given none then they are angry.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,59,59,N´O were they well-pleased with what God and His Messenger have brought them, saying, ´Enough for us is God; God will bring us of His bounty, and His Messenger; to God we humbly turn.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,60,60,N´The freewill offerings are for the poor and needy, those who work to collect them, those whose hearts are brought together, the ransoming of slaves, debtors, in God´s way, and the traveller; so God ordains; God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,61,61,N´And some of them hurt the Prophet, saying, ´He is an ear!´ Say: ´An ear of good for you; he believes in God, and believes the believers, and he is a mercy to the believers among you. Those who hurt God´s Messenger -- for them awaits a painful chastisement.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,62,62,N´They swear to you by God, to please you; but God and His Messenger -- more right is it they should please Him, if they are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,63,63,N´Do they not know that whosoever opposes God and His Messenger -- for him awaits the fire of Gehenna, therein to dwell forever? That is the mighty degradation.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,64,64,N´The hypocrites are afraid, lest a sura should be sent down against them, telling thee what is in their hearts. Say: ´Mock on; God will bring forth what you fear.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,65,65,N´And if thou questionest them, then assuredly they will say, ´We were only plunging and playing.´ Say: ´What, then were you mocking God, and His signs, and His Messenger?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,66,66,N´Make no excuses. You have disbelieved after your believing. If We forgive one party of you, We will chastise another party for that they were sinners.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,67,67,N´The hypocrites, the men and the women, are as one another; they bid to dishonour, and forbid honour; they keep their hands shut; they have forgotten God, and He has forgotten them. The hypocrites -- they are the ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,68,68,N´God has promised the hypocrites, men and women, and the unbelievers, the fire of Gehenna, therein to dwell foreover. That is enough for them; God has cursed them; and there awaits them Like a lasting chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,69,69,N´those before you, who were stronger than you in might, and more abundant in wealth and children; they took enjoyment in their share; so do you take enjoyment in your share, as those before you took enjoyment in their share. You have plunged as they plunged. Those -- their works have failed in this world and in the world to come; those -- they are the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,70,70,N´Has there not come to you the tidings of those who were before you -- the people of Noah, Ad, Thamood, the people of Abraham, the men of Midian and the subverted cities? Their Messengers came to them with the clear signs God would not wrong them, but themselves they wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,71,71,N´And the believers, the men and the women, are friends one of the other; they bid to honour, and forbid dishonour; they perform the prayer, and pay the alms, and they obey God and His Messenger. Those -- upon them God will have mercy; God is All-mighty, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,72,72,N´God has promised the believers, men and women, gardens underneath which rivers flow, forever therein to dwell, and goodly dwelling-places in the Gardens of Eden; and greater, God´s good pleasure; that is the mighty triumph.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,73,73,N´O Prophet, struggle with the unbelievers and hypocrites, and be thou harsh with them; their refuge is Gehenna -- an evil homecoming!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,74,74,N´They swear by God that they said nothing, but they indeed said the word of unbelief and disbelieved, after they had surrendered. They purposed what they never attained to, and they took revenge only that God enriched them, and His Messenger, of His bounty. So if they repent it will be better for them; if they turn away, God will chastise them with a painful chastisement in this world and the next; on the earth they have no protector or helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,75,75,N´And some of them have made covenant with God: ´If He gives us of His bounty, we will make offerings and be of the righteous.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,76,76,N´Nevertheless, when He gave them of His bounty they were niggardly of it, and turned away, swerving aside.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,77,77,N´So as a consequence He put hypocrisy into their hearts, until the day they meet Him, for that they failed God in that they promised Him and they were liars.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,78,78,N´Did they not know that God knows their secret and what they conspire together, and that God knows the things unseen?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,79,79,N´Those who find fault with the believers who volunteer their freewill offerings, and those who find nothing but their endeavour they deride -- God derides them; for them awaits a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,80,80,N´Ask pardon for them, or ask not pardon for them; if thou askest pardon for them seventy times, God will not pardon them; that, because they disbelieved in God and His Messenger; God guides not the people of the ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,81,81,N´Those who were left behind rejoiced in tarrying behind the Messenger of God, and were averse to struggle with their possessions and their selves in the way of God. They said, ´Go not forth in the heat.´ Say: ´Gehenna´s fire is hotter, did they but understand.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,82,82,N´Therefore let them laugh little, and weep much, in recompense for what they have been earning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,83,83,N´So, if God returns thee to a party of them and they ask leave of thee to go forth, say: ´You shall not go forth with me ever, and you shall not fight with me any enemy. You were well-pleased to tarry the first time, so now tarry with those behind.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,84,84,N´And pray thou never over any one of them when he is dead, nor stand over his grave; they disbelieved in God and His Messenger, and died while they were ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,85,85,N´And let not their possessions and their children please thee; God only desires thereby to chastise them in this present world, and that their souls should depart while they are unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,86,86,N´And when a sura is sent down, saying, ´Believe in God, and struggle with His Messenger,´ the affluent among them ask leave of thee, saying, ´Let us be with the tarriers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,87,87,N´They are well-pleased to be with those behind, and a seal has been set upon their hearts, so the they understand not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,88,88,N´But Messenger, and the believers with him, have struggled with their possessions and their selves, and those -- for them await the good things; those -- they are the prosperers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,89,89,N´God has prepared for them gardens underneath which rivers flow, therein to dwell forever; that is the mighty triumph.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,90,90,N´And the Bedouins came with their excuses, asking for leave; those who lied to God and His Messenger tarried; there shall befall the unbelievers of them a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,91,91,N´There is no fault in the weak and the sick and those who find nothing to expend, if they are true to God and to His Messenger. There is no way against the good-doers -- God is All-forgiving, All-compassionate --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,92,92,N´neither against those who, when they came to thee, for thee to mount them, thou saidst to them, ´I find not whereon to mount you´; they turned away, their eyes overflowing with tears of sorrow, because they found nothing to expend.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,93,93,N´The way is open only against those who ask leave of thee, being rich; they are well-pleased to be with those behind; God has set a seal on their hearts, so they know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,94,94,N´They will excuse themselves to you, when you return to them. Say: ´Do not excuse yourselves; we will not believe you. God has already told us tidings of you. God will surely see your work, and His Messenger, then you will be returned to Him who knows the unseen and the visible, and He will tell you what you were doing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,95,95,N´They will swear to you by God, when you turn back to them, that you may turn aside from them. So turn aside from them, for they are an abomination, and their refuge is Gehenna -- a recompense for what they have been earning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,96,96,N´They will swear to you, that you may be well-pleased with them; but if you are well-pleased with them, God will surely not be well-pleased with the people of the ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,97,97,N´The Bedouins are more stubborn in unbelief and hypocrisy, and apter not to know the bounds of what God has sent down on His Messenger; and God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,98,98,N´Some of the Bedouins take what they expend for a fine, and await the turns of fortune to go against you. Theirs shall be the evil turn; God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,99,99,N´And some of the Bedouins believe in God and the Last Day, and take what they expend for offerings bringing them near to God, and the prayers of the Messenger. Surely they are an offering for them, and God will admit them into His mercy; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,100,100,N´And the Outstrippers, the first of the Emigrants and the Helpers, and those who followed them in good-doing -- God will be well-pleased with them and they are well-pleased with Him; and He has prepared for them gardens underneath which rivers flow, therein to dwell forever and ever; that is the mighty triumph.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,101,101,N´And some of the Bedouins who dwell around you are hypocrites; and some of the people of the City are grown bold in hypocrisy. Thou knowest them not; but We know them, and We shall chastise them twice, then they will be returned to a mighty chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,102,102,N´And other have confessed their sins; they have mixed a righteous deed with another evil. It may be that God will turn towards them; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,103,103,N´Take of their wealth a freewill offering, to purify them and to cleanse them thereby, and pray for them; thy prayers are a comfort for them; God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,104,104,N´Do they not know that God is He who accepts repentance from His servants, and takes the freewill offerings, and that God -- He turns, and is All-compassionate?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,105,105,N´Say: ´Work; and God will surely see your work, and His Messenger, and the believers, and you will be returned to Him who knows the unseen and the visible, and He will tell you what you were doing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,106,106,N´And others are deferred to God´s commandment, whether He chastises them, or turns towards them; God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,107,107,N´And those who have taken a mosque in opposition and unbelief, and to divide the believers, and as a place of ambush for those who fought God and His Messenger aforetime -- they will swear ´We desired nothing but good´; and God testifies they are truly liars.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,108,108,N´Stand there never. A mosque that was founded upon godfearing from the first day is worthier for thee to stand in; therein are men who love to cleanse themselves; and God loves those who cleanse themselves.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,109,109,N´Why, is he better who founded his building upon the fear of God and His good pleasure, or he who founded his building upon the brink of a crumbling bank that has tumbled with him into the fire of Gehenna? And God guides not the people of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,110,110,N´The buildings they have built will not cease to be a point of doubt within their hearts, unless it be that their hearts are cut into pieces; God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,111,111,N´God has bought from the believers their selves and their possessions against the gift of Paradise; they fight in the way of God; they kill, and are killed; that is a promise binding upon God in the Torah, and the Gospel, and the Koran; and who fulfils his covenant truer than God? So rejoice in the bargain you have made with Him; that is the mighty triumph.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,112,112,N´Those who repent, those who serve, those who pray, those whojourney, those who bow, those who prostrate themselves, those who bid to honour and forbid dishonour, those who keep God´s hounds -- and give thou good tidings to the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,113,113,N´It is not for the Prophet and the believers to ask pardon for the idolaters, even though they be near kinsmen, after that it has become clear to them that they will be the inhabitants of Hell.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,114,114,N´Abraham asked not pardon for his father except because of a promise he had made to him; and when it became clear to him that he was an enemy of God, he declared himself quit of him; Abraham was compassionate, clement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,115,115,N´God would never lead a people astray after that He has guided them, until He makes clear to them as to what they should be godfearing; surely God knows everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,116,116,N´Surely to God belongs the kingdom of the heavens and of the earth; He gives life, and makes to die; and you have not, apart from God, either protector or helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,117,117,N´God has turned towards the Prophet and the Emigrants and the Helpers who followed him in the hour of difficulty, after the hearts of a part of them well-nigh swerved aside; then He turned towards them; surely He is Gentle to them, and All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,118,118,N´And to the three who were left behind, until, when the earth became strait for them, for all its breadth, and their souls became strait for them, and they thought that there was no shelter from God except in Him, then He turned towards them, that they might also turn; surely God turns, and is All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,119,119,N´O believers, fear God, and be with the truthful ones.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,120,120,N´It is not for the people of the City and for the Bedouins who dwell around them to stay behind God´s Messenger, and to prefer their lives to his; that is because they are smitten neither by thirst, nor fatigue, nor emptiness in the way of God, neither tread they any tread enraging the unbelievers, nor gain any gain from any enemy, but a righteous deed is thereby written to their account; God leaves not to waste the wage of the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,121,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,121,121,N´Nor do they expend any sum, small or great, nor do they traverse any valley, but it is written to their account, that God may recompense them the best of what they were doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,122,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,122,122,N´It is not for the believers to go forth totally; but why should not a party of every section of them go forth, to become learned in religion, and to warn their people when they return to them, that haply they may beware?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,123,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,123,123,N´O believers, fight the unbelievers who are near to you; and let them find in you a harshness; and know that God is with the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,124,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,124,124,N´Whenever a sura is sent down to thee, some of them say, ´Which of you has this increased in belief?´ As for the believers, them it has increased in belief, and they are joyful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,125,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,125,125,N´But as for those in whose heart is sickness, them it has increased in abomination added to their abomination, and they have died while they were unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,126,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,126,126,N´Do they not see that they are tried every year once or twice? Yet still they do not repent, nor do they remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,127,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,127,127,N´And whenever a sura is sent down, they look one at another: ´Does anyone see you?´ Then they turn away. God has turned away their hearts, for that they are a people who do not understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,128,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,128,128,N´Now there has come to you a Messenger from among yourselves; grievous to him is your suffering; anxious is he over you, gentle to the believers, compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,9,129,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,9,129,129,N´So if they turn their backs, say: ´God is enough for me. There is no god but He. In Him I have put my trust. He is the Lord of the Mighty Throne.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,1,1,N´Alif Lam Ra. Those are the signs of the Wise Book.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,2,2,N´Was it a wonder to the people that We revealed to a man from among them: ´Warn the people, and give thou good tidings to the believers that they have a sure footing with their Lord´? The unbelievers say, ´This is a manifest sorcerer.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,3,3,N´Surely your Lord is God, who created the heavens and the earth in six days, then sat Himself upon the Throne, directing the affair. Intercessor there is none, save after His leave that then is God, your Lord; so serve Him. Will you not remember?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,4,4,N´To Him shall you return, all together -- God´s promise, in truth. He originates creation, then He brings it back again that He may recompense those who believe and do deeds of righteousness, justly. And those who disbelieve -- for them awaits a draught of boiling water, and a painful chastisement, for their disbelieving.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,5,5,N´It is He who made the sun a radiance, and the moon a light, and determined it by stations, that you might know the number of the years and the reckoning. God created that not save with the truth, distinguishing the signs to a people who know.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,6,6,N´In the alternation of night and day, and what God has created in the heavens and the earth -- surely there are signs for a godfearing people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,7,7,N´Surely those who look not to encounter Us and are well-pleased with the present life and are at rest in it, and those who are heedless of Our signs,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,8,8,N´those -- their refuge is the Fire, for that they have been earning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,9,9,N´Surely those who believe, and do deeds of righteousness, their Lord will guide them for their belief; beneath them rivers flowing in gardens of bliss;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,10,10,N´their cry therein, ´Glory to Thee, O God,´ their greeting, ´Peace,´ and their cry ends, ´Praise belongs to God, the Lord of all Being.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,11,11,N´If God should hasten unto men evil as they would hasten good, their term would be already decided for them. But We leave those, who look not to encounter Us, in their insolence wandering blindly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,12,12,N´When affliction visits a man, he calls Us on his side, or sitting, or standing; but when We have removed his affliction from him, he passes on, as if he never called Us to an affliction that visited him. So decked out fair to the prodigal is that they have been doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,13,13,N´We destroyed the generations before you when they did evil, and their Messengers came to them with the clear signs, but they would not believe; so We recompense the people of the sinners.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,14,14,N´Then We appointed you viceroys in the earth after them, that We might behold how you would do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,15,15,N´And when Our signs are recited to them, clear signs, those who look not to encounter Us say, ´Bring a Koran other than this, or alter it.´ Say: ´It is not for me to alter it of my own accord. I follow nothing, except what is revealed to me. Truly I fear, if I should rebel against my Lord, the chastisement of a dreadful day.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,16,16,N´Say: ´Had God willed I would not have recited it to you, neither would He have taught you it; I abode among you a lifetime before it -- will you not understand?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,17,17,N´And who does greater evil than he who forges against God a lie, or cries lies to His signs? Surely the sinners do not prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,18,18,N´They serve, apart from God, what hurts them not neither profits them, and they say, ´These are our intercessors with God.´ Say: ´Will you tell God what He knows not either in the heavens or in the earth?´ Glory be to Him! High be He exalted above that they associate!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,19,19,N´Mankind were only one nation, then they fell into variance. But for a word that preceded from thy Lord, it had been decided between them already touching their differences.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,20,20,N´They say, ´Why has a sign not been sent down upon him from his Lord?´ Say: ´The Unseen belongs only to God. Then watch and wait; I shall be with you watching and waiting.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,21,21,N´When We let the people taste mercy after hardship has visited them, lo, they have a device concerning Our signs. Say: ´God is swifter at devising; surely Our messengers are writing down what you are devising.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,22,22,N´It is He who conveys you on the land and the sea; and when you are in the ship -- and the ships run with them with a fair breeze, and they rejoice in it, there comes upon them a strong wind, and waves come on them from every side, and they think they are encompassed; they call upon God, making their religion His sincerely: ´If Thou deliverest us from these, surely we shall be among the thankful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,23,23,N´Nevertheless when He has delivered them behold, they are insolent in the earth, wrongfully. O men, your insolence is only against yourselves; the enjoyment of this present life, then unto Us you shall return, then We shall tell you what you were doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,24,24,N´The likeness of this present life is as water that We send down out of heaven, and the plants of the earth mingle with it whereof men and cattle eat, till, when the earth has taken on its glitter and has decked itself fair, and its inhabitants think they have power over it, Our command comes upon it by night or day, and We make it stubble, as though yesterday it flourished not. Even so We distinguish the signs for a people who reflect.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,25,25,N´And God summons to the Abode of Peace, and He guides whomsoever He will to to a straight path;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,26,26,N´the good-doers the reward most fair and a surplus; neither dust nor abasement shall overspread their faces. Those are the inhabitants of Paradise, therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,27,27,N´And for those who have earned evil deeds the recompense of an evil deed shall be the like of it; abasement shall overspread them, neither have they any defender from God, as if their faces were covered with strips of night shadowy. Those are the inhabitants of the Fire, therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,28,28,N´And the day We shall muster them all, then We shall say to those who associate other gods with God: ´Get you to your place, you and your associates!´ Then We shall set a space between them, and their associates will say, ´Not us you were serving.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,29,29,N´God is a sufficient witness between us and you; assuredly we were heedless of your service.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,30,30,N´There every soul shall prove its past deeds; and they shall be restored to God, their Protector, the True, and there shall go astray from them that they were forging.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,31,31,N´Say: ´Who provides you out of heaven and earth, or who possesses hearing and sight, and who brings forth the living from the dead and brings forth the dead from the living, and who directs the affair?´ They will surely say, ´God.´ Then say: ´Will you not be godfearing?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,32,32,N´That then is God, your Lord, the True; what is there, after truth, but error? Then how are you turned about?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,33,33,N´Thus the word of thy Lord is realized against the ungodly that they believe not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,34,34,N´Say: ´Is there any of your associates who originates creation, then brings it back again?´ Say: ´God -- He originates creation, then brings it back again; so how are you perverted?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,35,35,N´Say: ´Is there any of your associates who guides to the truth?´ Say: ´God -- He guides to the truth; and which is worthier to be followed -- He who guides to the truth, or he who guides not unless he is guided? What then ails you, how you judge?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,36,36,N´And the most of them follow only surmise, and surmise avails naught against truth. Surely God knows the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,37,37,N´This Koran could not have been forged apart from God; but it is a confirmation of what is before it, and a distinguishing of the Book, wherein is no doubt, from the Lord of all Being.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,38,38,N´Or do they say, ´Why, he has forged it´? Say: ´Then produce a sura like it, and call on whom you can, apart from God, if you speak truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,39,39,N´No; but they have cried lies to that whereof they comprehended not the knowledge, and whose interpretation has not yet come to them. Even so those that were before them cried lies; then behold how was the end of the evildoers!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,40,40,N´And some of them believe in it, and some believe not in it. Thy Lord knows very well those who do corruption.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,41,41,N´If they cry lies to thee, then do thou say: ´I have my work, and you have your work; you are quit of What I do, and I am quit of what you do.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,42,42,N´And some of them give ear to thee; what, wilt thou make the deaf to hear, though they understand not?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,43,43,N´And some of them look unto thee; what, wilt thou then guide the blind, though they do not see?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,44,44,N´Surely God wrongs not men anything, but themselves men wrong.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,45,45,N´And the day He shall muster them, as if they had not tarried but an hour of the day, mutually recognizing one another; lost will be those who cried lies to the encounter with God, and were not guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,46,46,N´Whether We show thee a part of that We promise them, or We call thee unto Us, to Us they shall return; then God is witness of the things they do´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,47,47,N´Every nation has its Messenger; then, when their Messenger comes, justly the issue is decided between them, and they are not wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,48,48,N´They say, ´When will this promise be, if you speak truly?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,49,49,N´Say: ´I have no power to profit for myself, or hurt, but as God will. To every nation a term; when their term comes they shall not put it back by a single hour nor put it forward.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,50,50,N´Say: ´Have you considered? If His chastisement comes upon you by night or day, what part of it will the sinners seek to hasten?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,51,51,N´What, when it has come to pass, will you then believe in it? Now, when already you seek to hasten it!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,52,52,N´Then it will be said to the evildoers: ´Taste the chastisement of eternity! Are you recompensed for aught but that you have been earning?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,53,53,N´They ask thee to tell them, ´Is it true?´ Say: ´Yes, by my Lord! It is true; you cannot frustrate Him.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,54,54,N´If every soul that has done evil possessed all that is in the earth, he would offer it for his ransom; and they will be secretly remorseful when they see the chastisement, and justly the issue is decided between them, and they are not wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,55,55,N´Why, surely to God belongs everything that is in the heavens and earth. Why, surely God´s promise is true; but the most of them have no knowledge´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,56,56,N´He gives life, and makes to die, and to Him you shall be returned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,57,57,N´O men, now there has come to you an admonition from your Lord, and a healing for what is in the breasts, and a guidance, and a mercy to the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,58,58,N´Say: ´In the bounty of God, and His mercy -- in that let them rejoice; it is better than that they amass.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,59,59,N´Say: ´Have you considered the provision God has sent down for you, and you have made some of it unlawful, and some lawful?´ Say: ´Has God given you leave, or do you forge against God?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,60,60,N´What will they think, who forge falsehood against God; on the Day of Resurrection? God is bountiful to men; but most of them are not thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,61,61,N´Thou art not upon any occupation, neither recitest thou any Koran of it, nor do you any work, without that We are witnesses over you when you press on it; and not so much as the weight of an ant in earth or heaven escapes from thy Lord, neither is aught smaller than that, or greater, but in a Manifest Book.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,62,62,N´Surely God´s friends -- no fear shall be on them, neither shall they sorrow.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,63,63,N´Those who believe, and are godfearing --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,64,64,N´for them is good tidings in the present life and in the world to come. There is no changing the words of God; that is the mighty triumph.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,65,65,N´And do not let their saying grieve thee; the glory belongs altogether to God; He is the All-hearing, the All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,66,66,N´Why, surely to God belongs everyone that is in the heavens and in the earth; they follow, who call upon associates, apart from God -- they follow nothing but surmise, merely conjecturing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,67,67,N´It is He who made for you the night to repose in it, and the day, to see; surely in that are signs for a people who have ears.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,68,68,N´They say, ´God has taken to Him a son. Glory be to Him! He is All-sufficient; to Him belongs all that is in the heavens and in the earth; you have no authority for this. What, do you say concerning God that you know not?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,69,69,N´Say: ´Those who forge against God falsehood shall not prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,70,70,N´Some enjoyment in this world; then unto Us they shall return; then We shall let them taste the terrible chastisement, for that they were unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,71,71,N´And recite to them the story of Noah when he said to his people, ´My people, if my standing here is grievous to you and my reminding you of the signs of God, in God have I put my trust; so resolve on your affair, with your associates, then let not your affair be a worry to you, but make decision unto me, and respite me not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,72,72,N´Then if you turn your backs, I have not asked you for any wage; my wage falls only on God, and I have been commanded to be of those that surrender.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,73,73,N´But they cried him lies; so We delivered him, and those with him, in the Ark, and We appointed them as viceroys, and We drowned those who cried lies to Our signs; then behold how was the end of them that were warned!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,74,74,N´Then We sent forth, after him, Messengers to their people, and they brought them the clear signs; but they were not men to believe in that they had cried lies to before. So We seal the hearts of the transgressors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,75,75,N´Then We sent forth, after them, Moses and Aaron to Pharaoh and his Council with Our signs, but they waxed proud, and were a sinful people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,76,76,N´So, when the truth came to them from Us, they said, ´Surely this is a manifest sorcery.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,77,77,N´Moses said, ´What, do you say this to the truth, when it has come to you? Is this a sorcery? But sorcerers do not prosper.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,78,78,N´They said, ´Art thou come to us to turn us from that we found our fathers practising, and that the domination in the land might belong to you two? We do not believe you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,79,79,N´Pharaoh said, ´Bring me every cunning sorcerer.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,80,80,N´Then, when the sorcerers came, Moses said to them, ´Cast you down what you will cast.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,81,81,N´Then, when they had cast, Moses said, ´What you have brought is sorcery; God will assuredly bring it to naught. God sets not right the work of those who do corruption.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,82,82,N´God verifies the truth by His words, though sinners be averse.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,83,83,N´So none believed in Moses, save a seed of his people, for fear of Pharaoh and their Council, that they would persecute them; and Pharaoh was high in the land, and he was one of the prodigals.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,84,84,N´Moses said, ´O my people, if you believe in God, in Him put your trust, if you have surrendered.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,85,85,N´They said, ´In God we have put our trust. Our Lord, make us not a temptation to the people of the evildoers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,86,86,N´and deliver us by Thy mercy from the people of the unbelievers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,87,87,N´And We revealed to Moses and his brother, ´Take you, for your people, in Egypt certain houses; and make your houses a direction for men to pray to; and perform the prayer; and do thou give good tidings to the believers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,88,88,N´Moses said, ´Our Lord, Thou hast given to Pharaoh and his Council adornment and possessions in this present life. Our Lord, let them go astray from Thy way; Our Lord, obliterate their possessions, and harden their hearts so that they do not believe, till they see the painful chastisement.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,89,89,N´He said, ´Your prayer is answered; so go you straight, and follow not the way of those that know not.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,90,90,N´And We brought the Children of Israel over the sea; and Pharaoh and his hosts followed them insolently and impetuously till, when the drowning overtook him, he said, ´I believe that there is no god but He in whom the Children of Israel believe; I am of those that surrender.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,91,91,N´´Now? And before thou didst rebel, being of those that did corruption.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,92,92,N´So today We shall deliver thee with thy body, that thou mayest be a sign to those after thee. Surely many men are heedless of Our signs.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,93,93,N´And We settled the Children of Israel in a sure settlement, and We provided them with good things; so they differed not until the knowledge came to them. Surely thy Lord will decide between them on the Day of Resurrection touching their differences.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,94,94,N´So, if thou art in doubt regarding what We have sent down to thee, ask those who recite the Book before thee. The truth has come to thee from thy Lord; so be not of the doubters,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,95,95,N´nor be of those who cry lies to God´s signs so as to be of the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,96,96,N´Those against whom thy Lord´s word is realized will not believe,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,97,97,N´though every sign come to them, till they see the painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,98,98,N´Why was there never a city that believed, and its belief profited it? -- Except the people of Jonah; when they believed, We removed from them the chastisement of degradation in this present life, and We gave unto them enjoyment for a time.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,99,99,N´And if thy Lord had willed, whoever is in the earth would have believed, all of them, all together. Wouldst thou then constrain the people, until they are believers?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,100,100,N´It is not for any soul to believe save by the leave of God; and He lays abomination upon those who have no understanding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,101,101,N´Say: ´Behold what is in the heavens and in the earth!´ But neither signs nor warnings avail a people who do not believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,102,102,N´So do they watch and wait for aught but the like of the days of those who passed away before them? Say: ´Then watch and wait; I shall be with you watching and waiting.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,103,103,N´Then We shall deliver Our Messengers and the believers. Even so, as is Our bounden duty, We shall deliver the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,104,104,N´Say: ´O men, if you are in doubt regarding my religion, I serve not those you serve apart from God, but I serve God, who will gather you to Him, and I am commanded to be of the believers, and:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,105,105,N´\"Set thy face to the religion, a man of pure faith, and be thou not of the idolaters;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,106,106,N´and do not call, apart from God, on that which neither profits nor hurts thee, for if thou dost, then thou wilt surely be of the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,107,107,N´And if God visits thee with affliction, none can remove it but He; and if He desires any good for thee, none can repel His bounty; He causes it to fall upon whomsoever He will of His servants.\" He is the All-forgiving, the All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,108,108,N´Say: ´O men, the truth has come to you from your Lord. Whosoever is guided is guided only to his own gain, and whosoever goes astray, it is only to his own loss. I am not a guardian over you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,10,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,10,109,109,N´And follow thou what is revealed to thee; and be thou patient until God shall judge; and He is the best of judges.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,1,1,N´Alif Lam Ra. A Book whose verses are set clear, and then distinguished, from One All-wise, All-aware:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,2,2,N´´Serve you none but God´ (I am to you a warner from Him and a bearer of good tidings)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,3,3,N´and: ´Ask forgiveness of your Lord, then repent to Him, and He will give you fair enjoyment unto a term stated, and He will give of His bounty to every man of grace. But if you should turn your backs I fear for you the chastisement of a mighty day;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,4,4,N´to God shall you return; He is powerful over everything.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,5,5,N´Behold, they fold their breasts, to hide them from Him; behold, when they wrap themselves in their garments He knows what they secrete and what they publish; surely He knows all the thoughts within the breasts.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,6,6,N´No creature is there crawling on the earth, but its provision rests on God; He knows its lodging-place and its repository. All is in a Manifest Book.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,7,7,N´And it is He who created the heavens and the earth in six days, and His Throne was upon the waters -- that He might try you, which one of you is fairer in works. And if thou sayest, ´you shall surely be raised up after death,´ the unbelievers will say, ´This is naught but a manifest sorcery.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,8,8,N´And if We postpone the chastisement from them till a reckoned moment they will say ´What is detaining it?´ Surely, the day it shall come to them, it shall not be turned aside from them, and they shall be encompassed by that they mocked at.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,9,9,N´And if We let a man taste mercy from Us, and then We wrest it from him, he is desperate, ungrateful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,10,10,N´But if We let him taste prosperity after hardship that has visited him, he will say, ´The evils have gone from me´; behold, he is joyous, boastful --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,11,11,N´save such as are patient, and do deeds of righteousness; for them awaits forgiveness and a mighty wage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,12,12,N´Perchance thou art leaving part of what is revealed to thee, and thy breast is straitened by it, because they say, ´Why has a treasure not been sent down upon him, or an angel not come with him?´ Thou art only a warner; and God is a Guardian over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,13,13,N´Or do they say, ´He has forged it´? Say: ´Then bring you ten suras the like of it, forged; and call upon whom you are able, apart from God, if you speak truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,14,14,N´Then, if they do not answer you, know that it has been sent down with God´s knowledge, and that there is no god but He. So have you surrendered?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,15,15,N´Whoso desires the present life and its adornment, We will pay them in full for their works therein, and they shall not be defrauded there;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,16,16,N´those are they for whom in the world to come there is only the Fire; their deeds there will have failed, and void will be their works.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,17,17,N´And what of him who stands upon a clear sign from his Lord, and a witness from Him recites it, and before him is the Book of Moses for an ensample and a mercy? Those believe in it; but whosoever disbelieves in it, being one of the partisans, his promised land is the Fire. So be thou not in doubt of it; it is the truth from thy Lord, but most men do not believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,18,18,N´And who does greater evil than he who forges against God a lie? Those shall be presented before their Lord, and the witnesses will say, ´Those are they who lied against their Lord.´ Surely the curse of God shall rest upon the evildoers´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,19,19,N´who bar from God´s way, desiring to make it crooked; they disbelieve in the world to come;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,20,20,N´they are unable to frustrate Him on earth and they have no protectors, apart from God. For them the chastisement shall be doubled; they could not hear, neither did they see.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,21,21,N´Those are they that have lost their souls, and that they forged has gone astray from them;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,22,22,N´they without doubt will be the greatest losers in the world to come.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,23,23,N´But those who believe, and do righteous deeds, and have humbled themselves unto their Lord -- they shall be the inhabitants of Paradise, therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,24,24,N´The likeness of the two parties is as the man blind and deaf, and the man who sees and hears; are they equal in likeness? Will you not remember?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,25,25,N´And We sent Noah to his people: ´I am for you a warner, and a bearer of good tidings:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,26,26,N´Serve you none but God. I fear for you the chastisement of a painful day.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,27,27,N´Said the Council of the unbelievers of his people, ´We see thee not other than a mortal like ourselves, and we see no? any following thee but the vilest of us, inconsiderately. We do not see you have over us any superiority; no, rather we think you are liars.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,28,28,N´He said, ´O my people, what think you? If I stand upon a clear sign from my Lord, and He has given me mercy from Him, and it has been obscured for you, shall we compel you to it while you are averse to it?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,29,29,N´O my people, I do not ask of you wealth for this; my wage falls only upon God. I will not drive away those who believe; they shall surely meet their Lord. But I see you are an ignorant people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,30,30,N´O my people, who would help me against God, if I drive you away?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,31,31,N´Will you not remember? I do not say to you, \"I possess the treasuries of God\"; I know not the Unseen; and I do not say, \"I am an angel.\" Nor do I say to those your eyes despise, \"God will not give them any good\"; God knows best what is in their hearts. ´Surely in that case I should be among the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,32,32,N´They said, ´Noah, thou hast disputed with us and make much disputation with us. Then bring us that thou promisest us, if thou speakest truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,33,33,N´He said, ´God will bring you it if He will; you cannot frustrate Him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,34,34,N´And my sincere counsel will not profit you, if I desire to counsel you sincerely, if God desires to pervert you; He is your Lord, and unto Him you shall be returned.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,35,35,N´(Or do they say, ´He has forged it´? Say: ´If I have forged it, upon me falls my sin; and I am quit of the sins you do.´)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,36,36,N´And it was revealed to Noah, saying, ´None of thy people shall believe but he who has already believed; so be thou not distressed by that they may be doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,37,37,N´Make thou the Ark under Our eyes, and as We reveal; and address Me not concerning those who have done evil; they shall be drowned.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,38,38,N´So he was making the Ark; and whenever a council of his people passed by him they scoffed at him, He said, ´If you scoff at us, we shall surely scoff at you, as you scoff´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,39,39,N´and you shall know to whom will come a chastisement degrading him, and upon whom there shall alight a lasting chastisement.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,40,40,N´Until, when Our command came, and the Oven boiled, We said, ´Embark in it two of every kind, and thy family -- except for him against whom the word has already been spoken and whosoever believes.´ And there believed not with him except a few.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,41,41,N´He said, ´Embark in it! In God´s Name shall be its course and its berthing. Surely my Lord is All-forgiving, All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,42,42,N´So it ran with them amid waves like mountains; and Noah called to his son, who was standing apart, ´Embark with us, my son, and be thou not with the unbelievers!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,43,43,N´He said, ´I will take refuge in a mountain, that shall defend me from the water.´ Said he, ´Today there is no defender from God´s command but for him on whom He has mercy.´ And the waves came between them, and he was among the drowned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,44,44,N´And it was said, ´Earth, swallow thy waters; and, heaven, abate!´ And the waters subsided, the affair was accomplished, and the Ark settled on El-Judi, and it was said: ´Away with the people of the evildoers!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,45,45,N´And Noah called unto his Lord, and said, ´O my Lord, my son is of my family, and Thy promise is surely the truth. Thou art the justest of those that judge.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,46,46,N´Said He, ´Noah, he is not of thy family; it is a deed not righteous. Do not ask of Me that whereof thou hast no knowledge. I admonish thee, lest thou shouldst be among the ignorant.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,47,47,N´He said, ´My Lord, I take refuge with Thee, lest I should ask of Thee that whereof I have no knowledge; for if Thou forgivest me not, and hast not mercy on me, I shall be among the losers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,48,48,N´It was said, ´Noah, get thee down in peace from Us, and blessings upon thee and on the nations of those with thee; and nations -- We shall give them enjoyment, then there shall visit them from Us a painful chastisement.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,49,49,N´(That is of the tiding´s of the Unseen, that We reveal to thee; thou didst not know it, neither thy people, before this. So be patient; the issue ultimate is to the godfearing.)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,50,50,N´And to Ad their brother Hood; he said, ´O my people, serve God! You have no god other than He; you are but forgers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,51,51,N´O my people, I do not ask of you a wage for this; my wage falls only upon Him who did originate me; will you not understand?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,52,52,N´And, O my people, ask forgiveness of your Lord, then repent to Him, and He will loose heaven in torrents upon you, and He will increase you in strength unto your strength; and turn not your backs as sinners.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,53,53,N´They said, ´Hood, thou hast not brought us a clear sign, and we will not leave our gods for what thou sayest; we do not believe thee.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,54,54,N´We say nothing, but that one of our gods has smitten thee with some evil.´ He said, ´I call God to witness; and witness you, that I am quit of that you associate´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,55,55,N´apart from Him; so try your guile on me, all together, then you shall give me no respite.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,56,56,N´Truly, I have put my trust in God, my Lord and your Lord; there is no creature that crawls, but He takes it by the forelock. Surely my Lord is on a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,57,57,N´But if you turn your backs, I have delivered to you that I was sent with unto you, and my Lord will make a people other than you successors; you will not hurt Him anything. My Lord is Guardian over everything.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,58,58,N´And when Our command came, We delivered Hood and those who believed with him by a mercy from Us, and delivered them from a harsh chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,59,59,N´That was Ad; they denied the signs of their Lord, and rebelled against His Messengers, and followed the command of every froward tyrant.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,60,60,N´And there was sent following after them in this world a curse, and upon the Day of Resurrection: ´Surely Ad disbelieved in their Lord: so away with Ad, the people of Hood!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,61,61,N´And to Thamood their brother Salih; he said, ´O my people, serve God! You have no god other than He. It is He who produced you from the earth and has given you to live therein; so ask forgiveness of Him, then repent to Him; surely my Lord is nigh, and answers prayer.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,62,62,N´They said, ´Salih, thou hast hitherto been a source of hope among us. What, dost thou forbid us to serve that our fathers served? Truly we are in doubt, concerning what thou callest us to, disquieting.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,63,63,N´He said, ´O my people, what think you? If I stand upon a clear sign from my Lord, and He has given me mercy from Him, who shall help me against God if I rebel against Him? You would do nothing for me, except increase my loss.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,64,64,N´O my people, this is the She-camel of God, to be a sign for you. Leave her that she may eat in God´s earth, and touch her not with evil, lest you´ be seized by a nigh chastisement.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,65,65,N´But they hamstrung her; and he said, ´Take your joy in your habitation three days -- that is a promise not to be belied.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,66,66,N´And when Our command came, We delivered Salih and those who believed with him by a mercy from Us, and from the degradation of that day; thy Lord is the All-strong, the All-mighty.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,67,67,N´And the evildoers were seized by the Cry, and morning found them in their habitations fallen prostrate´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,68,68,N´as if they never dwelt there: ´Surely Thamood disbelieved in their Lord, so away with Thamood!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,69,69,N´Our messengers came to Abraham with the good tidings; they said, ´Peace!´ ´Peace,´ he said; and presently he brought a roasted calf.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,70,70,N´And when he saw their hands not reaching towards it, he was suspicious of them and conceived a fear of them. They said, ´Fear not; we have been sent to the people of Lot.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,71,71,N´And his wife was standing by; she laughed, therefore We gave her the glad tidings of Isaac, and, after Isaac, of Jacob.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,72,72,N´She said, ´Woe is me! Shall I bear, being an old woman, and this my husband is an old man? This assuredly is a strange thing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,73,73,N´They said, ´What, dost thou marvel at God´s command? The mercy of God and His blessings be upon you, O people of the House! Surely He is All-laudable, All-glorious.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,74,74,N´So, when the awe departed from Abraham and the good tidings came to him, he was disputing with Us concerning the people of Lot;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,75,75,N´Abraham was clement, compassionate, penitent.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,76,76,N´´O Abraham, turn away from this; thy Lord´s command has surely come, and there is coming upon them a chastisement not to be turned back.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,77,77,N´And when Our messengers came to Lot, he was troubled on their account and distressed for them, and he said, ´This is a fierce day.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,78,78,N´And his people came to him, running towards him; and erstwhile they had been doing evil deeds. He said, ´O my people, these are my daughters; they are cleaner for you. So fear God, and do not degrade me in my guests. What, is there not one man among you of a right mind?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,79,79,N´They said, ´Thou knowest we have no right to thy daughters, and thou well knowest what we desire.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,80,80,N´He said, ´O would that I had power against you, or might take refuge in a strong pillar!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,81,81,N´They said, ´Lot, we are messengers of thy Lord. They shall not reach thee; so set forth, thou with thy family, in a watch of the night, and let not any one of you turn round, excepting thy wife; surely she shall be smitten by that which smites them. Their promised time is the morning; is the morning not nigh?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,82,82,N´So when Our command came, We turned it uppermost nethermost, and rained on it stones of baked clay, one on another,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,83,83,N´marked with thy Lord, and never far from the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,84,84,N´And to Midian their brother Shuaib; he said, ´O my people, serve God! You have no god other than He. And diminish not the measure and the balance. I see you are prospering; and I fear for you the chastisement of an encompassing day.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,85,85,N´O my people, fill up the measure and the balance justly, and do not diminish the goods of the people, and do not mischief in the land, working corruption.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,86,86,N´God´s remainder is better for you, if you are believers. And I am not a guardian over you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,87,87,N´They said, ´Shuaib, does thy prayer command thee that we should leave that our fathers served, or to do as we will with our goods? Thou art the clement one, the right-minded.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,88,88,N´He said, ´O my people, what think you? If I stand upon a clear sign from my Lord, and He has provided me with fair provision from Him -- and I desire not to come behind you, betaking me to that I forbid you; I desire only to set things right, so far as I am able. My succour is only with God; in Him I have put my trust, and to Him I turn, penitent.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,89,89,N´O my people, let not the breach with me move you, so that there smite you the like of what smote the people of Noah, or the people of Hood, or the people of Salih; and the people of Lot are not far away from you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,90,90,N´And ask forgiveness of your Lord, then repent to Him; surely my Lord is All-compassionate, All-loving.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,91,91,N´They said, ´Shuaib, we do not understand much of what thou sayest. Truly we see thee weak among us; but for thy tribe we would have stoned thee; for thou art not strong against us.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,92,92,N´He said, ´O my people, is my tribe stronger against you than God? And Him -- have you taken Him as something to be thrust behind you? My Lord encompasses the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,93,93,N´O my people, act according to your station; I am acting; and certainly you will know to whom will come the chastisement degrading him, and who is a liar. And be upon the watch; I shall be with you, watching.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,94,94,N´And when Our command came, We delivered Shuaib and those who believed with him by a mercy from Us, and the evildoers were seized by the Cry, and morning found them in their habitations fallen prostrate´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,95,95,N´as if they had never dwelt there: ´So away with Midian, even as Thamood was done away!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,96,96,N´And We sent Moses with Our signs, and a manifest authority,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,97,97,N´to Pharaoh and his Council; but they followed Pharaoh´s command, and Pharaoh´s command was not right-minded.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,98,98,N´He shall go before his people on the Day of Resurrection, and will have led them down to the Fire-evil the watering-place to be led down to!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,99,99,N´And there was sent following after them in this world a curse, and upon the Day of Resurrection -- evil the offering to be offered!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,100,100,N´That is of the tidings of the cities We relate to thee; some of them are standing and some stubble.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,101,101,N´And We wronged them not, but they wronged themselves; their gods availed them not that they called upon, apart from God, anything, when the command of thy Lord came; and they increased them not, save in destruction.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,102,102,N´Such is the seizing of thy Lord, when He seizes the cities that are evildoing; surely His seizing is painful, terrible.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,103,103,N´Surely in that is a sign for him who fears the chastisement in the world to come; that is a day mankind are to be gathered to, a day to witness,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,104,104,N´and We shall not postpone it, save to a term reckoned;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,105,105,N´the day it comes, no soul shall speak save by His leave; some of them shall be wretched and some happy.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,106,106,N´As for the wretched, they shall be in the Fire, wherein there shall be for them moaning and sighing, therein dwelling forever,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,107,107,N´so long as the heavens and earth abide, save as thy Lord will; surely thy Lord accomplishes what He desires.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,108,108,N´And as for the happy, they shall be in Paradise, therein dwelling forever, so long as the heavens and earth abide, save as thy Lord will -- for a gift unbroken.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,109,109,N´So be thou not in doubt concerning what these men serve; they serve only as their fathers served before; and We shall surely pay them in full their portion undiminished.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,110,110,N´And We gave Moses the Book; and there was difference regarding it, and but for a word that preceded from thy Lord, it had been decided between them; and they are in doubt of it disquieting.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,111,111,N´Surely each one of them -- thy Lord will pay them in full for their works; He is aware of the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,112,112,N´So go thou straight, as thou hast been commanded, and whoso repents with thee; and be you not insolent; surely He sees the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,113,113,N´And lean not on the evildoers, so that the Fire touches you -- you have no protectors apart from God -- and then you will not be helped.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,114,114,N´And perform the prayer at the two ends of the day and nigh of the night; surely the good deeds will drive away the evil deeds. That is a remembrance unto the mindful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,115,115,N´And be thou patient; God will not leave to waste the wage of the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,116,116,N´Or if there had been, of the generations before you, men of a remainder forbidding corruption in the earth -- except a few of those whom We delivered of them; but the evildoers followed the ease they were given to exult in and became sinners.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,117,117,N´Yet thy Lord would never destroy the cities unjustly, while as yet their people were putting things right.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,118,118,N´Had thy Lord willed, He would have made mankind one nation; but they continue in their differences´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,119,119,N´excepting those on whom thy Lord has mercy. To that end He created them, and perfectly is fulfilled the word of thy Lord: ´I shall assuredly fill Gehenna with jinn and men all together.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,120,120,N´And all that We relate to thee of the tidings of the Messengers is that whereby We strengthen thy heart; in these there has come to thee the truth and an admonition, and a reminder to the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,121,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,121,121,N´And say to the unbelievers: ´Act you according to your station; we are acting.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,122,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,122,122,N´And watch and wait; we are also watching and waiting.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,11,123,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,11,123,123,N´To God belongs the Unseen in the heavens and the earth. To Him the whole matter shall be returned; so serve Him, and put thy trust in Him. Thy Lord is not heedless of the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,1,1,N´Alif Lam Ra. Those are the signs of the Manifest Book.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,2,2,N´We have sent it down as an Arabic Koran; haply you will understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,3,3,N´We will relate to thee the fairest of stories in that We have revealed to thee this Koran, though before it thou wast one of the heedless.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,4,4,N´When Joseph said to his father, ´Father, I saw eleven stars, and the sun and the moon; I saw them bowing down before me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,5,5,N´He said, ´O my son, relate not thy vision to thy brothers, lest they devise against thee some guile. Surely Satan is to man a manifest enemy.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,6,6,N´So will thy Lord choose thee, and teach thee the interpretation of tales, and perfect His blessing upon thee and upon the House of Jacob, as He perfected it formerly on thy fathers Abraham and Isaac; surely thy Lord is All-knowing, All-wise.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,7,7,N´(In Joseph and his brethren were signs for those who ask questions.)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,8,8,N´When they said, ´Surely Joseph and his brother are dearer to our father than we, though we are a band. Surely our father is in manifest error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,9,9,N´Kill you Joseph, or cast him forth into some land, that your father´s face may be free for you, and thereafter you may be a righteous people.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,10,10,N´One of them said, ´No, kill not Joseph, but cast him into the bottom of the pit and some traveller will pick him out, if you do aught.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,11,11,N´They said, ´Father, what ails thee, that thou trustest us not with Joseph? Surely we are his sincere well-wishers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,12,12,N´Send him forth with us tomorrow, to frolic and play; surely we shall be watching over him.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,13,13,N´He said, ´It grieves me that you should go with him, and I fear the wolf may eat him, while you are heedless of him.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,14,14,N´They said, ´If the wolf eats him, and we a band, then are we losers!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,15,15,N´So when they went with him, and agreed to put him in the bottom of the well, and We revealed to him, ´Thou shalt tell them of this their doing when they are unaware.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,16,16,N´And they came to their father in the evening, and they were weeping.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,17,17,N´They said, ´Father, we went running races, and left Joseph behind with our things; so the wolf ate him. But thou wouldst never believe us, though we spoke truly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,18,18,N´And they brought his shirt with false blood on it. He said, ´No; but your spirits tempted you to do somewhat. But come, sweet patience! And God´s succour is ever there to seek against that you describe.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,19,19,N´Then came travellers, and they sent one of them, a water-drawer, who let down his bucket. ´Good news!´ he said. ´Here is a young man.´ So they hid him as merchandise; but God knew what they were doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,20,20,N´Then they sold him for a paltry price, a handful of counted dirhams; for they set small store by him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,21,21,N´He that bought him, being of Egypt, said to his wife, ´Give him goodly lodging, and it may be that he will profit us, or we may take him for our own son.´ So We established Joseph in the land, and that We might teach him the interpretation of tales. God prevails in His purpose, but most men know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,22,22,N´And when he was fully grown, We gave him judgment and knowledge. Even so We recompense the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,23,23,N´Now the woman in whose house he was solicited him, and closed the doors on them. ´Come,´ she said, ´take me!´ ´God be my refuge,´ he said. ´Surely my lord has given me a goodly lodging. Surely the evildoers do not prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,24,24,N´For she desired him; and he would have taken her, but that he saw the proof of his Lord. So was it, that We might turn away from him evil and abomination; he was one of Our devoted servants.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,25,25,N´They raced to the door; and she tore his shirt from behind. They encountered her master by the door. She said, ´What is the recompense of him who purposes evil against thy folk, but that he should be imprisoned, or a painful chastisement?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,26,26,N´Said he, ´It was she that solicited me´; and a witness of her folk bore witness, ´If his shirt has been torn from before then she has spoken truly, and he is one of the liars;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,27,27,N´but if it be that his shirt has been torn from behind, then she has lied, and he is one of the truthful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,28,28,N´When he saw his shirt was torn from behind he said, ´This is of your women´s guile; surely your guile is great.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,29,29,N´Joseph, turn away from this; and thou, woman, ask forgiveness of thy crime; surely thou art one of the sinners.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,30,30,N´Certain women that were in the city said, ´The Governor´s wife has been soliciting her page; he smote her heart with love; we see her in manifest error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,31,31,N´When she heard their sly whispers, she sent to them, and made ready for them a repast, then she gave to each one of them a knife. ´Come forth, attend to them,´ she said. And when they saw him, they so admired him that they cut their hands, saying, ´God save us! This is no mortal; he is no other but a noble angel.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,32,32,N´´So now you see, she said. ´This is he you blamed me for. Yes, I solicited him, but he abstained. Yet if he will not do what I command him, he shall be imprisoned, and be one of the humbled.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,33,33,N´He said, ´My Lord, prison is dearer to me than that they call me to; yet if Thou turnest not from me their guile, then I shall yearn towards them, and so become one of the ignorant.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,34,34,N´So his Lord answered him, and He turned away from him their guile; surely He is the All-hearing, the All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,35,35,N´Then it seemed good to them, after they had seen the signs, that they should imprison him for a while.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,36,36,N´And there entered the prison with him two youths. Said one of them, ´I dreamed that I was pressing grapes. Said the other, ´I dreamed that I was carrying on my head bread, that birds were eating of. Tell us its interpretation; we see that thou art of the good-doers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,37,37,N´He said, ´No food shall come to you for your sustenance, but ere it comes to you I shall tell you its interpretation. That I shall tell you is of what God has taught me. I have forsaken the creed of a people who believe not in God and who moreover are unbelievers in the world to come.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,38,38,N´And I have followed the creed of my fathers, Abraham, Isaac and Jacob. Not ours is it to associate aught with God. That is of God´s bounty to us, and to men; but most men are not thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,39,39,N´Say, which is better, my fellow-prisoners -- many gods at variance, or God the One, the Omnipotent?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,40,40,N´That which you serve, apart from Him, is nothing but names yourselves have named, you and your fathers; God has sent down no authority touching them. Judgment belongs only to God; He has commanded that you shall not serve any but Him. That is the right religion; but most men know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,41,41,N´Fellow-prisoners, as for one of you, he shall pour wine for his lord; as for the other, he shall be crucified, and birds will eat of his head. The matter is decided whereon you enquire.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,42,42,N´Then he said to the one he deemed should be saved of the two, ´Mention me in thy lord´s presence.´ But Satan caused him to forget to mention him to his master, so that he continued in the prison for certain years.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,43,43,N´And the king said, ´I saw in a dream seven fat kine, and seven lean ones devouring them; likewise seven green ears of corn, and seven withered. My counsellors, pronounce to me upon my dream, if you are expounders of dreams.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,44,44,N´´A hotchpotch of nightmares!´ they said. ´We know nothing of the interpretation of nightmares.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,45,45,N´Then said the one who had been delivered, remembering after a time, ´I will myself tell you its interpretation; so send me forth.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,46,46,N´´Joseph, thou true man, pronounce to us regarding seven fat kine, that seven lean ones were devouring, seven green ears of corn, and seven withered; haply I shall return to the men, haply they will know.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,47,47,N´He said, ´You shall sow seven years after your wont; what you have harvested leave in the ear, excepting a little whereof you eat.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,48,48,N´Then thereafter there shall come upon you seven hard years, that shall devour what you have laid up for them, all but a little you keep in store.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,49,49,N´Then thereafter there shall come a year wherein the people will be succoured and press in season.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,50,50,N´The king said, ´Bring him to me!´ And when the messenger came to him, he said, ´Return unto thy lord, and ask of him, \"What of the women who cut their hands?\" Surely my Lord has knowledge of their guile.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,51,51,N´´What was your business, women,´ he said, ´when you solicited Joseph?´ ´God save us!´ they said. ´We know no evil against him.´ The Governor´s wife said, ´Now the truth is at last discovered; I solicited him; he is a truthful man.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,52,52,N´´That, so that he may know I betrayed him not secretly, and that God guides not the guile of the treacherous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,53,53,N´Yet I claim not that my soul was innocent -- surely the soul of man incites to evil -- except inasmuch as my Lord had mercy; truly my Lord is All-forgiving, All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,54,54,N´The king said, ´Bring him to me! I would attach him to my person.´ Then, when he had spoken with him, he said, ´Today thou art established firmly in our favour and in our trust.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,55,55,N´He said, ´Set me over the land´s storehouses; I am a knowing guardian.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,56,56,N´So We established Joseph in the land, to make his dwelling there wherever he would. We visit with Our mercy whomsoever We will, and We leave not to waste the wage of the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,57,57,N´Yet is the wage of the world to come better for those who believe, and are godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,58,58,N´And the brethren of Joseph came, and entered unto him, and he knew them, but they knew him not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,59,59,N´When he had equipped them with their equipment be said, ´Bring me a certain brother of yours from your father. Do you not see that I fill up the measure, and am the best of hosts?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,60,60,N´But if you bring him not to me, there shall be no measure for you with me, neither shall you come nigh me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,61,61,N´They said, ´We will solicit him of our father; that we will do.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,62,62,N´He said to his pages, ´Put their merchandise in their saddlebags; haply they will recognize it when they have turned to their people; haply they will return.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,63,63,N´So, when they had returned to their father, they said, ´Father, the measure was denied to us; so send with us our brother, that we may obtain the measure; surely we shall be watching over him.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,64,64,N´He said, ´And shall I entrust him to you otherwise than as I entrusted before his brother to you? Why, God is the best guardian, and He is the most merciful of the merciful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,65,65,N´And when they opened their things, they found their merchandise, restored to them. ´Father,´ they said, ´what more should we desire? See, our merchandise here is restored to us. We shall get provision for our family, and we shall be watching over our brother; we shall obtain an extra camel´s load -- that is an easy measure.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,66,66,N´He said, ´Never will I send him with you until you bring me a solemn pledge by God that you will surely bring him back to me unless it be that you are encompassed.´ When they had brought him their solemn pledge he said, ´God shall be Guardian over what we say.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,67,67,N´He also said, ´O my sons, enter not by one door; enter by separate doors. Yet I cannot avail you anything against God; judgment belongs not to any but God. In Him I have put my trust; and in Him let all put their trust who put their trust.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,68,68,N´And when they entered after the manner their father commanded them, it availed them nothing against God; but it was a need in Jacob´s soul that he so satisfied. Verily he was possessed of a knowledge for that We had taught him; but most men know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,69,69,N´And when they entered unto Joseph, he said, taking his brother into his arms, ´I am thy brother; so do not despair of that they have done.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,70,70,N´Then, when he had equipped them with their equipment, he put his drinking-cup into the saddlebag of his brother. Then a herald proclaimed, ´Ho, cameleers, you are robbers!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,71,71,N´They said, turning to them, ´What is it that you are missing?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,72,72,N´They said, ´We are missing the king´s goblet. Whoever brings it shall receive a camel´s load; that I guarantee.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,73,73,N´´By God,´ they said, ´you know well that we came not to work corruption in the land. We are not robbers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,74,74,N´They said, ´And what shall be its recompense if you are liars?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,75,75,N´They said, ´This shall be its recompense -- in whoever´s saddlebag the goblet is found, he shall be its recompense. So we recompense the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,76,76,N´So he made beginning with their sacks, before his brother´s sack, then he pulled it out of his brother´s sack. So We contrived for Joseph´s sake; he could not have taken his brother, according to the king´s doom, except that God willed. Whomsoever We will, We raise in rank; over every man of knowledge is One who knows.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,77,77,N´They said, ´If he is a thief, a brother of his was a thief before.´ But Joseph secreted it in his soul and disclosed it not to them, saying, ´You are in a worse case; God knows very well what you are describing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,78,78,N´They said, ´Mighty prince, he has a father, aged and great with years; so take one of us in his place; we see that thou art one of the good-doers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,79,79,N´He said, ´God forbid that we should take any other but him in whose possession we found the goods; for if we did so, we would be evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,80,80,N´When they despaired of moving him, they conferred privily apart. Said the eldest of them, ´Do you not know how your father has taken a solemn pledge from you by God, and aforetime you failed regarding Joseph? Never will I quit this land, until my father gives me leave, or God judges in my favour; He is the best of judges.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,81,81,N´Return you all to your father, and say, \"Father, thy son stole; we do not testify except that we know; we were no guardians of the Unseen.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,82,82,N´Enquire of the city wherein we were, and the caravan in which we approached; surely we are truthful men\".´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,83,83,N´´No!´ he said ´But your spirits tempted you to do somewhat. But come, sweet patience! Haply God will bring them all to me; He is the All-knowing, the All-wise.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,84,84,N´And he turned away from them, and said, ´Ah, woe is me for Joseph!´ And his eyes turned white because of the sorrow that he choked within him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,85,85,N´´By God,´ they said, ´thou wilt never cease mentioning Joseph till thou art consumed, or among the perishing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,86,86,N´He said, ´I make complaint of my anguish and my sorrow unto God; I know from God that you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,87,87,N´Depart, my sons, and search out tidings of Joseph and his brother. Do not despair of God´s comfort; of God´s comfort no man despairs, excepting the people of the unbelievers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,88,88,N´So, when they entered unto him, they said, ´O mighty prince, affliction has visited us and our people. We come with merchandise of scant worth. Fill up to us the measure, and be charitable to us; surely God recompenses the charitable.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,89,89,N´He said, ´Are you aware of what you did with Joseph and his brother, when you were ignorant?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,90,90,N´They said, ´Why, art thou indeed Joseph?´ ´I am Joseph,´ he said. ´This is my brother. God has indeed been gracious unto us. Whosoever fears God, and is patient -- surely God leaves not to waste the wage of the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,91,91,N´´By God,´ they said, ´God has indeed preferred thee above us, and certainly we have been sinful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,92,92,N´He said, ´No reproach this day shall be on you; God will forgive you; He is the most merciful of the merciful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,93,93,N´Go, take this shirt, and do you cast it on my father´s face, and he shall recover his sight; then bring me your family all together.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,94,94,N´So, when the caravan set forth, their father said, ´Surely I perceive Joseph´s scent, unless you think me doting.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,95,95,N´They said, ´By God, thou art certainly in thy ancient error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,96,96,N´But when the bearer of good tidings came to him, and laid it on his face, forthwith he saw once again. He said, ´Did I not tell you I know from God that you know not?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,97,97,N´They said, ´Our father, ask forgiveness of our crimes for us; for certainly we have been sinful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,98,98,N´He said, ´Assuredly I will ask my Lord to forgive you; He is the All-forgiving, the All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,99,99,N´So, when they entered unto Joseph, he took his father and mother into his arms saying, ´Enter you into Egypt, if God will, in security.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,100,100,N´And he lifted his father and mother upon the throne; and the others fell down prostrate before him. ´See, father,´ he said, ´this is the interpretation of my vision of long ago; my Lord has made it true. He was good to me when He brought me forth from the prison, and again when He brought you out of the desert, after that Satan set at variance me and my brethren. My Lord is gentle to what He will; He is the All-knowing, the All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,101,101,N´O my Lord, Thou hast given me to rule, and Thou hast taught me the interpretation of tales. O Thou, the Originator of the heavens and earth, Thou art my Protector in this world and the next. O receive me to Thee in true submission, and join me with the righteous.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,102,102,N´That is of the tidings of the Unseen that We reveal to thee; thou wast not with them when they agreed upon their plan, devising.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,103,103,N´Yet, be thou ever so eager, the most part of men believe not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,104,104,N´Thou askest of them no wage for it; it is nothing but a reminder unto all beings.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,105,105,N´How many a sign there is in the heavens and in the earth that they pass by, turning away from it!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,106,106,N´And the most part of them believe not in God, but they associate other gods with Him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,107,107,N´Do they feel secure that there shall come upon them no enveloping of the chastisement of God, or that the Hour shall not come upon them suddenly when they are unaware?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,108,108,N´Say: ´This is my way. I call to God with sure knowledge, I and whoever follows after me. To God be glory! And I am not among the idolaters.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,109,109,N´We sent not forth any before thee, but men We revealed to of the people living in the cities. Have they not journeyed in the land? Have they not beheld how was the end of those before them?. Surely the abode of the world to come is better for those that are godfearing. What, do you not understand?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,110,110,N´Till, when the Messengers despaired, deeming they were counted liars, Our help came to them and whosoever We willed was delivered. Our might will never be turned back from the people of the sinners.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,12,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,12,111,111,N´In their stories is surely a lesson to men possessed of minds; it is not a tale forged, but a confirmation of what is before it, and a distinguishing of every thing, and a guidance, and a mercy to a people who believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,1,1,N´Alif Lam Mim Ra. Those are the signs of the Book; and that which has been sent down to thee from thy Lord is the truth, but most men do not believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,2,2,N´God is He who raised up the heavens without pillars you can see, then He sat Himself upon the Throne. He subjected the sun and the moon, each one running to a term stated. He directs the affair; He distinguishes the signs; haply you will have faith in the encounter with your Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,3,3,N´It is He who stretched out the earth and set therein firm mountains and rivers, and of every fruit He placed there two kinds, covering the day with the night. Surely in that are signs for a people who reflect.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,4,4,N´And on the earth are tracts neighbouring each to each, and gardens of vines, and fields sown, and palms in pairs, and palms single, watered with one water; and some of them We prefer in produce above others. Surely in that are signs for a people who understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,5,5,N´If thou wouldst wonder, surely wonderful is their saying, ´What, when we are dust shall we indeed then be raised up again in new creation?´ Those are they that disbelieve in their Lord; those -- on their necks are fetters; those shall be the inhabitants of the Fire, therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,6,6,N´They would have thee hasten the evil ere the good; yet there have passed away before them examples. Thy Lord is forgiving to men, for all their evil-doing, and thy Lord is terrible in retribution.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,7,7,N´The unbelievers say, ´Why has a sign not been sent down upon him from his Lord?´ Thou art only a warner, and a guide to every people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,8,8,N´God knows what every female bears, and the wombs´ shrinking and swelling; everything with Him has its measure --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,9,9,N´the Knower of the unseen and the visible, the All-great, the All-exalted:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,10,10,N´Alike of you is he who conceals his -- saying, and he who proclaims it, he who hides himself in the night, and he who sallies by day;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,11,11,N´he has attendant angels, before him and behind him, watching over him by God´s command. God changes not what is in a people, until they change what is in themselves. Whensover God desires evil for a people, there is no turning it back; apart from Him, they have no protector.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,12,12,N´It is He who shows you the lightning, for fear and hope, and produces the heavy clouds;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,13,13,N´the thunder proclaims. His praise, and the angels, in awe of Him. He looses the thunderbolts, and smites with them whomsoever He will; yet they dispute about God, who is mighty in power.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,14,14,N´To Him is the call of truth; and those upon whom they call, apart from Him, answer them nothing, but it is as a man who stretches out his hands to water that it may reach his mouth, and it reaches it not. The prayer of the unbelievers goes only astray.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,15,15,N´To God bow all who are in the heavens and the earth, willingly or unwillingly, as do their shadows also in the mornings and the evenings.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,16,16,N´Say: ´Who is the Lord of the heavens and of the earth?´ Say: ´God.´ Say: ´Then have you taken unto you others beside Him to be your protectors, even such as have no power to profit or hurt themselves?´ Say: ´Are the blind and the seeing man equal, or are the shadows and the light equal? Or have they ascribed to God associates who created as He created, so that creation is all alike to them?´ Say: ´God is the Creator of everything, and He is the One, the Omnipotent.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,17,17,N´He sends down out of heaven water, and the wadis flow each in its measure, and the torrent carries a swelling scum; and out of that over which they kindle fire, being desirous of ornament or ware, out of that rises a scum the like of it. So God strikes both the true and the false. As for the scum, it vanishes as jetsam, and what profits men abides in the earth. Even so God strikes His similitudes.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,18,18,N´For those who answer their Lord, the reward most fair; and those who answer Him not -- if they possessed all that is in the earth and the like of it with it, they would offer it for their ransom. Those theirs shall be the evil reckoning, and their refuge shall be Gehenna -- an evil cradling!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,19,19,N´What, is he who knows what is sent down to thee from thy Lord is the truth, like him who is blind? Only men possessed of minds remember;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,20,20,N´who fulfil God´s covenant, and break not the compact,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,21,21,N´who join what God has commanded shall be joined, and fear their Lord, and dread the evil reckoning,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,22,22,N´patient men, desirous of the Face of their Lord, who perform the prayer, and expend of that We have provided them, secretly and in public, and who avert evil with good -- theirs shall be the Ultimate Abode,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,23,23,N´Gardens of Eden which they shall enter; and those who were righteous of their fathers, and their wives, and their seed, shall enter them, and the angels shall enter unto them from every gate:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,24,24,N´´Peace be upon you, for that you were patient.´ Fair is the Ultimate Abode.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,25,25,N´And those who break the covenant of God after His compact, and who snap what God has commanded to be joined, and who work corruption in the earth -- theirs shall be the curse, and theirs the Evil Abode.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,26,26,N´God outspreads and straitens His provision unto whomsoever He will. They rejoice in this present life; and this present life, beside the world to come, is naught but passing enjoyment.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,27,27,N´The unbelievers say, ´Why has a sign not been sent down upon him from his Lord?´ Say: ´God leads astray whomsoever He will, and He guides to Him all who are penitent.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,28,28,N´Those who believe, their hearts being at rest in God´s remembrance -- in God´s remembrance are at rest the hearts´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,29,29,N´of those who believe and do righteous deeds; theirs is blessedness and a fair resort.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,30,30,N´Thus We have sent thee among a nation before which other nations have passed away, to recite to them that We have revealed to thee; and yet they disbelieve in the All-merciful. Say: ´He is my Lord -- there is no god but He. In Him I have put my trust, and to Him I turn.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,31,31,N´If only a Koran whereby the mountains were set in motion, or the earth were cleft, or the dead were spoken to -- nay, but God´s is the affair altogether. Did not the believers know that, if God had willed, He would have guided men all together? And still the unbelievers are smitten by a shattering for what they wrought, or it alights nigh their habitation, until God´s promise comes; and God will not fail the tryst.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,32,32,N´Messengers indeed were scoffed at before thee, and I respited the unbelievers; then I seized them -- and how was my retribution?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,33,33,N´What, He who stands over every soul for what it has earned? -- And yet they ascribe to God associates. Say: ´Name them! Or will you tell Him what He knows not in the earth? Or in apparent words?´ Nay; but decked out fair to the unbelievers is their devising, and they are barred from the way; and whomsoever God leads astray, no guide has he.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,34,34,N´For them is chastisement in the present life; and the chastisement of the world to come is yet more grievous; they have none to defend them from God.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,35,35,N´The likeness of Paradise, that is promised to the godfearing: beneath it rivers flow, its produce is eternal, and its shade. That is the requital of the god-fearing; and the requital of the unbelievers is -- the Fire!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,36,36,N´And those to whom We have given the Book rejoice in what is sent down unto thee; and of the parties some reject some of it. Say: ´I have only been commanded to serve God, and not to associate aught with Him. To Him I call, and to Him I turn.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,37,37,N´Even so We have sent it down as an Arabic judgment. And if thou dost follow their caprices, after the knowledge that has come to thee, thou shalt have no protector against God, and no defender.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,38,38,N´And We sent Messengers before thee, and We assigned to them wives, and seed; and it was not for any Messengers to bring a sign, but by God´s leave. Every term has a Book.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,39,39,N´God blots out, and He establishes whatsoever He will; and with Him is the Essence of the Book.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,40,40,N´Whether We show thee a part of that We promise them, or We call thee to Us, it is thine only to deliver the Message, and Ours the reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,41,41,N´Have they not seen how We come to the land diminishing it in its extremities? God judges; none repels His judgment; He is swift at the reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,42,42,N´Those that were before them devised; but God´s is the devising altogether. He knows what every soul earns. The unbelievers shall assuredly know whose will be the Ultimate Abode.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,13,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,13,43,43,N´The unbelievers say, ´Thou art not an Envoy.´ Say: ´God suffices as a witness between me and you, and whosoever possesses knowledge of the Book.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,1,1,N´Alif Lam Ra. A Book We have sent down to thee that thou mayest bring forth mankind from the shadows to the light by the leave of their Lord, to the path of the All-mighty, the All-laudable,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,2,2,N´God, to whom belongs all that is in the heavens and all that is in the earth. And woe to the unbelievers for a terrible chastisement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,3,3,N´such as prefer the present life over the world to come, and bar from God´s way, desiring to make it crooked -- they are in far error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,4,4,N´And We have sent no Messenger save with the tongue of his people, that he might make all clear to them; then God leads astray whomsoever He will, and He guides whomsoever He will; and He is the All-mighty, the All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,5,5,N´And We sent Moses with Our signs -- ´Bring forth thy people from the shadows to the light and remind thou them of the Days of God.´ Surely in that are signs for every man enduring, thankful!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,6,6,N´And when Moses said to his people, ´Remember God´s blessing upon you when He delivered you from the folk of Pharaoh, who were visiting you with evil chastisement, slaughtering your sons, and sparing your women -- and in that was a grievous trial from your Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,7,7,N´And when your Lord proclaimed, \"If you are thankful, surely I will increase you, but if you are thankless My chastisement is surely terrible.\"´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,8,8,N´And Moses said, ´If you are thankless, you and whoso is on earth, all together, yet assuredly God is All-sufficient, All-laudable.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,9,9,N´Has there not come to you the tidings of those who were before you -- the people of Noah, Ad, Thamood, and of those after them whom none knows but God? Their Messengers came to them with the clear signs; but they thrust their hands into their mouths, saying, ´We certainly disbelieve in the Message you have been sent with, and we are in doubt, concerning that you call us unto, disquieting.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,10,10,N´Their Messengers said, ´Is there any doubt regarding God, the Originator of the heavens and the earth, who calls you so that He may forgive you your sins, and defer you to a term stated?´ They said, ´You are nothing but mortals, like us; you desire to bar us from that our fathers served; then bring us a manifest authority.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,11,11,N´Their Messengers said to them, ´We are nothing but mortals, like you; but God is gracious unto whomsoever He will of His servants. It is not for us to bring you an authority save by the leave of God; and in God let the believers put all their trust.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,12,12,N´And why should we not put our trust in God, seeing that He has guided us in our ways? We will surely endure patiently, whatever you hurt us; and in God let all put their trust who put their trust.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,13,13,N´The unbelievers said to their Messengers, ´We will assuredly expel you from our land, or you will surely return into our creed.´ Then did their Lord reveal unto them: ´We will surely destroy the evildoers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,14,14,N´and We will surely make you to dwell in the land after them -- that, for him who fears My station and fears My threat.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,15,15,N´They sought a judgment; then was disappointed every froward tyrant --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,16,16,N´beyond him Gehenna, and he is given to drink of oozing pus,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,17,17,N´the which he gulps, and can scarce swallow, and death comes upon him from every side, yet he cannot die; and still beyond him is a harsh chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,18,18,N´The likeness of those who disbelieve in their Lord: their works are as ashes, whereon the wind blows strong upon a tempestuous day; they have no power over that they have earned - that is the far error!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,19,19,N´Hast thou not seen that God created the heavens and the earth in truth? If He will, He can put you away and bring a new creation;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,20,20,N´that is surely no great matter for God.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,21,21,N´They sally forth unto God, all together; then say the weak to those who waxed proud, ´We were your followers; will you avail us against the chastisement of God anything?´ They say, ´If God had guided us, we would have guided you. Alike it is for us whether we cannot endure, or whether we are patient; we have no asylum.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,22,22,N´And Satan says, when the issue is decided, ´God surely promised you a true promise; and I promised you, then I failed you, for I had no authority over you, but that I called you, and you answered me. So do not blame me, but blame yourselves; I cannot aid you, neither can you aid me. I disbelieved in your associating me with God aforetime.´ As for the evildoers, for them awaits a painful chastisement;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,23,23,N´but as for those who believe, and do deeds of righteousness, they shall be admitted to gardens underneath which rivers flow, therein dwelling forever, by the leave of their Lord, their greeting therein: ´Peace!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,24,24,N´Hast thou not seen how God has struck a similitude? A good word is as a good tree -- its roots are firm, and its branches are in heaven;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,25,25,N´it gives its produce every season by the leave of its Lord. So God strikes similitudes for men; haply they will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,26,26,N´And the likeness of a corrupt word is as a corrupt tree -- uprooted from the earth, having no stablishment.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,27,27,N´God confirms those who believe with the firm word, in the present life and in the world to come; and God leads astray the evildoers; and God does what He will.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,28,28,N´Hast thou not seen those who exchanged the bounty of God with unthankfulness, and caused their people to dwell in the abode of ruin? --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,29,29,N´Gehenna, wherein they are roasted; an evil stablishment!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,30,30,N´And they set up compeers to God, that they might lead astray from His way. Say: ´Take your joy! Your homecoming shall be -- the Fire!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,31,31,N´Say to My servants who believe, that they perform the prayer, and expend of that We have provided them, secretly and in public, before a day comes wherein shall be neither bargaining nor befriending.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,32,32,N´It is God who created the heavens and the earth, and sent down out of heaven water wherewith He brought forth fruits to be your sustenance. And He subjected to you the ships to run upon the sea at His commandment; and He subjected to you the rivers´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,33,33,N´and He subjected to you the sun and moon constant upon their courses, and He subjected to you the night and day,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,34,34,N´and gave you of all you asked Him. If you count God´s blessing, you will never number it; surely man is sinful, unthankful!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,35,35,N´And when Abraham said, ´My Lord, make this land secure, and turn me and my sons away from serving idols;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,36,36,N´my Lord, they have led astray many men. Then whoso follows me belongs to me; and whoso rebels against me, surely Thou art All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,37,37,N´Our Lord, I have made some of my seed to dwell in a valley where is no sown land by Thy Holy House; Our Lord, let them perform the prayer, and make hearts of men yearn towards them, and provide them with fruits; haply they will be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,38,38,N´Our Lord, Thou knowest what we keep secret and what we publish; from God nothing whatever is hidden in earth and heaven.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,39,39,N´Praise be to God, who has given me, though I am old, Ishmael and Isaac; surely my Lord hears the petition.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,40,40,N´My Lord, make me a performer of the prayer, and of my seed. Our Lord, and receive my petition.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,41,41,N´Our Lord, forgive Thou me and my parents, and the believers, upon the day when the reckoning shall come to pass.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,42,42,N´Deem not that God is heedless of what the evildoers work; He is only deferring them to a day when eyes shall stare,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,43,43,N´when they shall run with necks outstretched and heads erect, their glances never returned on themselves, their hearts void.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,44,44,N´And warn mankind of the day when the chastisement comes on them, And those who did evil shall say, ´Our Lord, defer us to a near term, and we will answer Thy call, and follow the Messengers.´ ´Ah, but did you not swear aforetime there should be no removing for you?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,45,45,N´And you dwelt in the dwelling-places of those who wronged themselves, and it became clear to you how We did with them, and how We struck similitudes for you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,46,46,N´They devised their devising, and their devising is known to God; though their devising were such as to remove mountains.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,47,47,N´So do not deem that God will fail in His promise to His Messengers; surely God is All-mighty, Vengeful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,48,48,N´Upon the day the earth shall be changed to other than the earth, and the heavens and they sally forth unto God, the One, the Omnipotent.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,49,49,N´And thou shalt see the sinners that day coupled in fetters,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,50,50,N´of pitch their shirts, their faces enveloped by the Fire,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,51,51,N´that God may recompense every soul for its earnings; surely God is swift at the reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,14,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,14,52,52,N´This is a Message to be delivered to mankind that they may be warned by it, and that they may know that He is One God, and that all possessed of minds may remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,1,1,N´Alif Lam Ra. Those are the signs of the Book and of a manifest Koran.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,2,2,N´Perchance the unbelievers will wish that they had surrendered:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,3,3,N´leave them to eat, and to take their joy, and to be bemused by hope; certainly they will soon know!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,4,4,N´Never a city have We destroyed, but it had a known decree,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,5,5,N´and no nation outstrips its term, nor do they put it back.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,6,6,N´They say: ´Thou, upon whom the Remembrance is sent down, thou art assuredly possessed!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,7,7,N´Why dost thou not bring the angels unto us, if thou speakest truly?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,8,8,N´We send not down the angels, save with truth;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,9,9,N´then they would not be respited. It is We who have sent down the Remembrance, and We watch over it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,10,10,N´Indeed, We sent Messengers before thee, among the factions of the ancients,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,11,11,N´and not a single Messenger came to them, but they mocked at him;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,12,12,N´even so We cause it to enter into the hearts of the sinners --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,13,13,N´they believe not in it, though the wont of the ancients is already gone.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,14,14,N´Though We opened to them a gate in heaven, and still they mounted through it,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,15,15,N´yet would they say, ´Our eyes have been dazzled; nay, we are a people bewitched!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,16,16,N´We have set in heaven constellations and decked them out fair to the beholders,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,17,17,N´and guarded them from every accursed Satan´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,18,18,N´excepting such as listens by stealth -- and he is pursued by a manifest flame.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,19,19,N´And the earth -- We stretched it forth, and cast on it firm mountains, and We caused to grow therein of every thing justly weighed,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,20,20,N´and there appointed for you livelihood, and for those you provide not for.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,21,21,N´Naught is there, but its treasuries are with Us, and We send it not down but in a known measure.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,22,22,N´And We loose the winds fertilising, and We send down out of heaven water, then We give it to you to drink, and you are not its treasurers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,23,23,N´It is We who give life, and make to die, and it is We who are the inheritors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,24,24,N´We know the ones of you who press forward, and We know the laggards;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,25,25,N´and it is thy Lord shall muster them, and He is All-wise, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,26,26,N´Surely We created man of a clay of mud moulded,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,27,27,N´and the jinn created We before of fire flaming.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,28,28,N´And when thy Lord said to the angels, ´See, I am creating a mortal of a clay of mud moulded.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,29,29,N´When I have shaped him, and breathed My spirit in him, fall you down, bowing before him!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,30,30,N´Then the angels bowed themselves all together,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,31,31,N´save Iblis; he refused to be among those bowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,32,32,N´Said He, ´What ails thee, Iblis, that thou art not among those bowing?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,33,33,N´Said he, ´I would never bow myself before a mortal whom Thou hast created of a clay of mud moulded.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,34,34,N´Said He, ´Then go thou forth hence; thou art accursed.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,35,35,N´Upon thee shall rest the curse, till the Day of Doom.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,36,36,N´Said he, ´My Lord, respite me till the day they shall be raised.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,37,37,N´Said He, ´Thou art among the ones that are respited´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,38,38,N´unto the day of a known time.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,39,39,N´Said he, ´My Lord, for Thy perverting me I shall deck all fair to them in the earth, and I shall pervert them, all together,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,40,40,N´excepting those Thy servants among them that are devoted.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,41,41,N´Said He, ´This is for Me a straight path:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,42,42,N´over My servants thou shalt have no authority, except those that follow thee,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,43,43,N´being perverse; Gehenna shall be their promised land all together.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,44,44,N´Seven gates it has, and unto each gate a set portion of them belongs.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,45,45,N´But the godfearing shall be amidst gardens and fountains:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,46,46,N´´Enter you them, in peace and security!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,47,47,N´We shall strip away all rancour that is in their breasts; as brothers they shall be upon couches set face to face;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,48,48,N´no fatigue there shall smite them, neither shall they ever be driven forth from there.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,49,49,N´Tell My servants I am the All-forgiving, the All-compassionate,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,50,50,N´and that My chastisement is the painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,51,51,N´And tell them of the guests of Abraham,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,52,52,N´when they entered unto him, saying, ´Peace!´ He said, ´Behold, we are afraid of you.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,53,53,N´They said, ´Be not afraid; behold, we give thee good tidings of a cunning boy.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,54,54,N´He said, ´What, do you give me good tidings, though old age has smitten me? Of what do you´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,55,55,N´give me good tidings?´ They said, ´We give thee good tidings of truth. Be not of those that despair.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,56,56,N´He said, ´And who despairs of the mercy of his Lord, excepting those that are astray?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,57,57,N´He said, ´And what is your business, envoys?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,58,58,N´They said, ´We have been sent unto a people of sinners,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,59,59,N´excepting the folk of Lot; them we shall deliver´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,60,60,N´all together, excepting his wife -- we have decreed, she shall surely be of those that tarry.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,61,61,N´So, when the envoys came to the folk of Lot,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,62,62,N´he said, ´Surely you are a people unknown to me!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,63,63,N´They said, ´Nay, but we have brought thee that concerning which they were doubting.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,64,64,N´We have come to thee with the truth, and assuredly we speak truly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,65,65,N´So set forth, thou with thy family, in a watch of the night, and follow after the backs of them, and let not any one of you turn round; and depart unto the place you are commanded.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,66,66,N´And We decreed for him that commandment, that the last remnant of those should be cut off in the morning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,67,67,N´And the people of the city came rejoicing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,68,68,N´He said, ´These are my guests; put me not to shame,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,69,69,N´and fear God, and do not degrade me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,70,70,N´They said, ´Have we not forbidden thee all beings?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,71,71,N´He said, ´These are my daughters, if you would be doing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,72,72,N´By thy life, they wandered blindly in their dazzlement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,73,73,N´and the Cry seized them at the sunrise,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,74,74,N´and We turned it uppermost nethermost and rained on it stones of baked clay.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,75,75,N´Surely in that are signs for such as mark;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,76,76,N´surely it is on a way yet remaining;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,77,77,N´surely in that is a sign for believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,78,78,N´Certainly the dwellers in the Thicket were evildoers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,79,79,N´and We took vengeance on them. The two of them were upon a roadway manifest.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,80,80,N´The dwellers in El-Hijr cried lies to the Envoys.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,81,81,N´We brought them. Our signs, and they turned away from them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,82,82,N´They were hewing the mountains into houses, therein dwelling securely;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,83,83,N´and the Cry seized them in the morning;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,84,84,N´that they earned did not avail them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,85,85,N´We created not the heavens and the earth, and all that is between them, save in truth. Surely the Hour is coming; so pardon thou, with a gracious pardoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,86,86,N´Surely thy Lord, He is the All-creator, the All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,87,87,N´We have given thee seven of the oft-repeated, and the mighty Koran.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,88,88,N´Stretch not thine eyes to that We have given pairs of them to enjoy; and do not sorrow for them, and lower thy wing unto the believers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,89,89,N´and say, ´Surely, I am the manifest warner.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,90,90,N´So We sent it down to the partitioners,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,91,91,N´who have broken the Koran into fragments.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,92,92,N´Now by thy Lord, We shall surely question them all together´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,93,93,N´concerning that they were doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,94,94,N´So shout that thou art commanded and turn thou away from the idolaters.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,95,95,N´We suffice thee against the mockers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,96,96,N´even against those who set up with God another god. Certainly they will soon know!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,97,97,N´We know indeed thy breast is straitened by the things they say.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,98,98,N´Proclaim thy Lord´s praise, and be of those that bow,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,15,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,15,99,99,N´and serve thy Lord, until the Certain comes to thee.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,1,1,N´God´s command comes; so seek not to hasten it. Glory be to Him! High be He exalted above that they associate with Him!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,2,2,N´He sends down the angels with the Spirit of His command upon whomsoever He will among His servants, saying: Give you warning that there is no God hut I; so fear you Me!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,3,3,N´He created the heavens and the earth in truth; high be He exalted above that they associate With Him!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,4,4,N´He created man of a sperm-drop; and, behold, he is a manifest adversary.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,5,5,N´And the cattle -- He created them for you; in them is warmth, and uses various, and of them you eat,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,6,6,N´and there is beauty in them for you, when you bring them home to rest and when you drive them forth abroad to pasture;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,7,7,N´and they bear your loads unto a land that you never would reach, excepting with great distress. Surely your Lord is All-clement, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,8,8,N´And horses, and mules, and asses, for you to ride, and as an adornment; and He creates what you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,9,9,N´God´s it is to show the way; and some do swerve from it. If He willed, He would have guided you all together.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,10,10,N´It is He who sends down to you out of heaven water of which you have to drink, and of which trees, for you to pasture your herds,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,11,11,N´and thereby He brings forth for you crops, and olives, and palms, and vines, and all manner of fruit. Surely in that is a sign for a people who reflect.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,12,12,N´And He subjected to you the night and day, and the sun and moon; and the stars are subjected by His command. Surely in that are signs for a people who understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,13,13,N´And that which He has multiplied for you in the earth of diverse hues. Surely in that is a sign for a people who remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,14,14,N´It is He who subjected to you the sea, that you may eat of it fresh flesh, and bring forth out of it ornaments for you to wear; and thou mayest see the ships cleaving through it; and that you may seek of His bounty, and so haply you will be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,15,15,N´And He cast on the earth firm mountains, lest it shake with you, and rivers and ways; so haply you will be guided;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,16,16,N´and waymarks; and by the stars they are guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,17,17,N´Is He who creates as he who does not create? Will you not remember?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,18,18,N´If you count God´s blessing, you will never number it; surely God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,19,19,N´And God knows what you keep secret and what you publish.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,20,20,N´And those they call upon, apart from God, created nothing, and themselves are created,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,21,21,N´dead, not alive; and are not aware when they shall be raised.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,22,22,N´Your God is One God. And they who believe not in the world to come, their hearts deny, and they have waxed proud.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,23,23,N´Without a doubt God knows what they keep secret and what they publish; He loves not those that wax proud.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,24,24,N´And when it is said to them, ´What has your Lord sent down?´ they say, ´Fairy-tales of the ancients.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,25,25,N´That they may bear their loads complete on the Day of Resurrection, and some of the loads of those that they lead astray without any knowledge. O evil the load they bear!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,26,26,N´Those that were before them contrived; then God came upon their building from the foundations, and the roof fell down on them from over them, and the chastisement came upon them from whence they were not aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,27,27,N´Then on the Day of Resurrection He will degrade them, saying, ´Where are My associates concerning which you made a breach together?´ Those that were given the knowledge will say, ´Degradation today and evil are on the unbelievers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,28,28,N´whom the angels take while still they are wronging themselves.´ Then they will offer surrender: ´We were doing nothing evil. ´Nay; but surety God has knowledge of the things you did.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,29,29,N´So enter the gates of Gehenna, there to dwell forever.´ Evil is the lodging of those that wax proud.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,30,30,N´And it shall be said to the godfearing, ´What has your Lord sent down?´ They will say, ´Good! For those who do good in this world good; and surely the abode of the world to come is better; excellent is the abode of the godfearing --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,31,31,N´Gardens of Eden they shall enter, underneath which rivers flow, wherein they shall have all they will. So God recompenses the godfearing,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,32,32,N´whom the angels take while they are goodly, saying, ´Peace be on you! Enter Paradise for that you were doing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,33,33,N´Do they look for aught but that the angels shall come to them, or thy Lord´s command shall come? So did those before them, and God wronged them not, but themselves they wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,34,34,N´So the evil things that they wrought. smote them, and they were encompassed by that they mocked at.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,35,35,N´The idolators say, ´If God had willed we would not have served; apart from Him, anything, neither we nor our fathers, nor would we have forbidden, apart from Him, anything.´ So did those before them; yet is aught for the Messengers, but to deliver the manifest Message?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,36,36,N´Indeed, We sent forth among every nation a Messenger,; saying: ´Serve you God, and eschew idols.´ Then some. of them God guided, and some were justly disposed to error. So journey in the land, and behold how was the end of them that cried lies.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,37,37,N´Though thou art ever so eager to guide them, God guides not those whom He leads astray; they have no helpers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,38,38,N´have sworn by God the most earnest oaths God will never raise up him who dies; nay, it is a promise binding upon Him, but most men know not,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,39,39,N´so that He may make clear to them that whereon they were at variance, and that the unbelievers may know that they were truly liars.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,40,40,N´The only words We say to a thing, when We desire it, is that We say to it ´Be,´ and it is.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,41,41,N´And those that emigrated in God´s cause after they were wronged -- We shall surely lodge them in this world in a goodly lodging, and the wage of the world to come is greater, did they but know;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,42,42,N´even such men as are patient, and put their trust in their Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,43,43,N´We sent not any before thee, except men to whom We revealed: ´Question the people of the Remembrance, if it should be that you do not know --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,44,44,N´with the clear signs, and the Psalms; and We have sent down to thee the Remembrance that thou mayest make clear to mankind what was sent down to them; and so haply they will reflect.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,45,45,N´Do they feel secure, those who devise evil things, that God will not cause the earth to swallow them, or that the chastisement will not come upon them, from whence they are not aware?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,46,46,N´Or that He will not seize them in their going to and fro, and they will not be able to frustrate Him?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,47,47,N´Or that He will not seize them, little by little destroying them? Surely thy Lord is All-clement, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,48,48,N´Have they not regarded all things that God has created casting their shadows to the right and to the left, bowing themselves before God in all lowliness?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,49,49,N´To God bows everything in the heavens, and every creature crawling on the earth, and the angels. They have not waxed proud;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,50,50,N´they fear their Lord above them, and they do what they are commanded.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,51,51,N´God says: ´Take not to you two gods. He is only One God; so have awe of Me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,52,52,N´To Him belongs all that is in the heavens and earth; His is the religion for ever. Then will you fear other than God?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,53,53,N´Whatsoever blessing you have, it comes from God; then when affliction visits you it is unto Him that you groan.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,54,54,N´Then, when He removes the affliction from you, lo, a party of you assign associates to their Lord,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,55,55,N´that they may show unthankfulness for that We have given them. So take your joy; certainly you will soon know!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,56,56,N´And they appoint a share of that We have provided them to what they know not. By God, you shall be questioned as to that you forged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,57,57,N´And they assign to God daughters; glory be to Him! -- and they have their desire;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,58,58,N´and when any of them is given the good tidings of a girl, his face is darkened and he chokes inwardly,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,59,59,N´as he hides him from the people because of the evil of the good tidings that have been given unto him, whether he shall preserve it in humiliation, or trample it; into the dust. Ah, evil is that they judge!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,60,60,N´Those who believe not in the world to come, theirs is the evil likeness; God´s is the loftiest likeness; He is the All-mighty, the All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,61,61,N´If God should take men to task for their evildoing, He would not leave on the earth one creature that crawls; but He is deferring them to a term stated; and when their term is come they shall not put it back by a single hour nor put it forward.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,62,62,N´They assign to God that they themselves dislike; and their tongues describe falsehood, that the reward most fair shall be theirs. Without any doubt theirs shall be the Fire, and they are hastened in.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,63,63,N´By God, assuredly We sent Messengers to nations before thee, but Satan decked out fair to them their deeds; he is their protector today, and there yet awaits them a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,64,64,N´And We have not sent down upon thee the Book except that thou mayest make clear to them that whereon they were at variance, and as a guidance and as a mercy to a people who believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,65,65,N´And it is God who sends down out of heaven water, and therewith revives the earth after it is dead. Surely in that is a sign for a people who have ears.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,66,66,N´And surely in the cattle there is a lesson for you; We give you to drink of what is in their bellies, between filth and blood, pure milk, sweet to drinkers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,67,67,N´And of the fruits of the palms and the vines, you take therefrom an intoxicant and a provision fair. Surely in that is a sign for a people who understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,68,68,N´And thy Lord revealed unto the bees, saying: ´Take unto yourselves, of the mountains, houses, and of the trees, and of what they are building.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,69,69,N´Then eat of all manner of fruit, and follow the ways of your Lord easy to go upon.´ Then comes there forth out of their bellies a drink of diverse hues wherein is healing for men. Surely in that is a sign for a people who reflect.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,70,70,N´God created you; then He will gather you to Him; and some of you will be kept back unto the vilest state of life, that after knowing somewhat, they may know nothing; God is All-knowing, All-powerful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,71,71,N´And God has preferred some of you over others in provision;, but those that were preferred shall not give over their provision to that their right hands possess, so that they may be equal therein. What, and do they deny God´s blessing?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,72,72,N´God has appointed for you of yourselves wives, and He has appointed for you of your wives sons and grandsons, and He has provided you of the good things. What, do they believe in vanity, and do they disbelieve in God´s blessing?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,73,73,N´And do they serve, apart from God, that which has no power to provide them anything from the heavens and the earth and can do nothing?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,74,74,N´So strike not any similitudes for God; surely God knows, and you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,75,75,N´God has struck a similitude: a servant possessed by his master, having no power over anything, and one whom We have provided of Ourselves with a provision fair, and he expends of it secretly and openly. Are they equal? Praise belongs to God! Nay, most of them know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,76,76,N´God has struck a similitude: two men, one of them dumb, having no power over anything, and he is a burden upon his master -- wherever he despatches him, he brings no good. Is he equal to him who bids to justice, and is on a straight path?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,77,77,N´To God belongs the Unseen in the heavens and in the earth. And the matter of the Hour is as a twinkling of the eye, or nearer. Surely God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,78,78,N´And it is God who brought you forth from your mothers´ wombs, and He appointed for you hearing, and sight, and hearts, that haply so you will be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,79,79,N´Have they not regarded the birds, that are subjected in the air of heaven? Naught holds them but God; surely in that are signs for´ a people who believe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,80,80,N´And it is God who has appointed a place of rest for you of your houses, and He has appointed for you of the skins of the cattle houses you find light on the day that you journey, and on the day you abide, and of their wool, and of their fur, and of their hair furnishing and an enjoyment for a while.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,81,81,N´And it is God who has appointed for you coverings of the things He created, and He has appointed for you of the mountains refuges, and He has appointed for you shirts to protect you from the heat, and shirts to protect you from your own violence. Even so He perfects His blessing upon you, that haply you will surrender.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,82,82,N´So, if they turn their backs, thine it is only to deliver the manifest Message.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,83,83,N´They recognize the blessing of God, then they deny it, and the most of them are the unthankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,84,84,N´And the day We shall raise up from every nation a witness, then to the unbelievers no leave shall be given, nor shall they be suffered to make amends.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,85,85,N´And when the evildoers behold the chastisement, it shall not be lightened for them, and no respite shall be given them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,86,86,N´And when the idolaters behold their associates, they shall say, ´Our Lord, these are our associates on whom we called apart from Thee.´ They will fling back at them the saying, ´Surely, you are truly liars.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,87,87,N´And they will offer God surrender that day, and there shall go astray from them that they were forging.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,88,88,N´Those that disbelieve and bar from the way of God -- them We shall give increase of chastisement upon chastisement, for that they were doing corruption.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,89,89,N´And the day We shall raise up from every nation a witness against them from amongst them, and We shall bring thee as a witness against those. And We have sent down on thee the Book making clear everything, and as a guidance and a mercy, and as good tidings to those who surrender.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,90,90,N´Surely God bids to justice and good-doing and giving to kinsmen; and He forbids indecency, dishonour, and insolence, admonishing you, so that haply you will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,91,91,N´Fulfil God´s covenant, when you make covenant, and break not the oaths after they have been confirmed, and you have made God your surety; surely God knows the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,92,92,N´And be not as a woman who breaks her thread, after it is firmly spun, into fibres, by taking your oaths as mere mutual deceit, one nation being more numerous than another nation. God only tries you thereby; and certainly He will make clear to you upon the Day of Resurrection that whereon you were at variance.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,93,93,N´If God had willed, He would have made you one nation; but He leads astray whom He will, and guides whom He will; and you will surely be questioned about the things you wrought.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,94,94,N´Take not your oaths as mere mutual deceit, lest any foot should slip after it has stood firm, and you should taste evil, for that you barred from the way of God, and lest there should await you a mighty chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,95,95,N´And do not sell the covenant of God for a small price; surely what is with God -- that is better for you, did you but know.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,96,96,N´What is with you comes to an end, but what is with God abides; and surely We shall recompense those who were patient their wage, according to the best of what they did.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,97,97,N´And whosoever does a righteous deed, be it male or female, believing, We shall assuredly give him to live a goodly life; and We shall recompense them their wage, according to the best of what they did.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,98,98,N´When thou recitest the Koran, seek refuge in God from the accursed Satan;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,99,99,N´he has no authority over those who believe and trust in their Lord;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,100,100,N´his authority is over those who take him for their friend and ascribe associates to God.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,101,101,N´And when We exchange a verse in the place of another verse and God knows very well what He is sending down -- they say, ´Thou art a mere forger!´ Nay, but the most of them have no knowledge.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,102,102,N´Say: ´The Holy Spirit sent it down from thy Lord in truth, and to confirm those who believe, and to be a guidance and good tidings to those who surrender.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,103,103,N´And We know very well that they say, ´Only a mortal is teaching him.´ The speech of him at whom they hint is barbarous; and this is speech Arabic, manifest.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,104,104,N´Those that believe not in the signs of God, God will not guide; there awaits them a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,105,105,N´They only forge falsehood, who believe not in the signs of God, and those -- they are the liars.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,106,106,N´Whoso disbelieves in God, after he has believed -- excepting him who has been compelled, and his heart is still at rest in his belief -- but whosoever´s breast is expanded in unbelief, upon them shall rest anger from God, and there awaits them a mighty chastisement;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,107,107,N´that, because they have preferred the present life over the world to come, and that God guides not the people of the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,108,108,N´Those -- God has set a seal on their hearts, and their hearing, and their eyes, and those -- they are the heedless ones;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,109,109,N´without a doubt, in the world to come they will be the losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,110,110,N´Then, surely thy Lord -- unto those who have emigrated after persecution, then struggled and were patient -- surely thy Lord thereafter is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,111,111,N´The day that every soul shall come disputing in its own behalf; and every soul shall be paid in full for what it wrought, and they shall not be wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,112,112,N´God has struck a similitude: a city that was secure, at rest, its provision coming to it easefully from every place, then it was unthankful for the blessings of God; so God let it taste the garment of hunger and of fear, for the things that they were working.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,113,113,N´There came indeed to them a Messenger from amongst them, but they cried him lies; so they were seized by the chastisement while they were evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,114,114,N´So eat of what God has provided you lawful and good; and be you thankful for the blessing of God, if it be Him that you serve.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,115,115,N´These things only He has forbidden you: carrion, blood, the flesh of swine, what has been hallowed to other than God. Yet whoso is constrained, not desiring nor transgressing, God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,116,116,N´And do not say, as to what your tongues falsely describe, ´This is lawful; and this is forbidden, so that you may forge against God falsehood; surely those who forge against God falsehood shall not prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,117,117,N´A little enjoyment, then for them awaits a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,118,118,N´And those of Jewry -- We have forbidden them what We related to thee before, and We wronged them not, but they wronged themselves.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,119,119,N´Then, surely thy Lord -- unto those who did evil in ignorance, then repented after that and put things right -- surely thy Lord thereafter is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,120,120,N´Surely, Abraham was a nation obedient unto God, a man of pure faith and no idolater,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,121,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,121,121,N´showing thankfulness for His blessings; He chose him, and He guided him to a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,122,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,122,122,N´And We gave him in this world good, and in the world to come he shall be among the righteous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,123,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,123,123,N´Then We revealed to thee: ´Follow thou the creed of Abraham, a man of pure faith and no idolater.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,124,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,124,124,N´The Sabbath was only appointed for those who were at variance thereon; surely thy Lord will decide between them on the Day of Resurrection, touching their differences.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,125,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,125,125,N´Call thou to the way of thy Lord with wisdom and good admonition, and dispute with them in the better way. Surely thy Lord knows very well those who have gone astray from His way, and He knows very well those who are guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,126,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,126,126,N´And if you chastise, chastise even as you have been chastised; and yet assuredly if you are patient, better it is for those patient.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,127,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,127,127,N´And be patient; yet is thy patience only with the help of God. And do not sorrow for them, nor be thou straitened for what they devise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,16,128,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,16,128,128,N´Surely God is with those who are godfearing, and those who are good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,1,1,N´Glory be to Him, who carried His servant by night from the Holy Mosque to the Further Mosque the precincts of which We have blessed, that We might show him some of Our signs. He is the All-hearing, the All-seeing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,2,2,N´And We gave Moses the Book, and made it a guidance to the Children of Israel: ´Take not unto yourselves any guardian apart from Me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,3,3,N´The seed of those We bore with Noah; he was a thankful servant.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,4,4,N´And We decreed for the Children of Israel in the Book: ´You shall do corruption in the earth twice, and you shall ascend exceeding high.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,5,5,N´So, when the promise of the first of these came to pass, We sent against you servants of Ours, men of great might, and they went through the habitations, and it was a promise performed.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,6,6,N´Then We gave back to you the turn to prevail over them, and We succoured you with wealth and children, and We made you a greater host.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,7,7,N´´If you do good, it is your own souls you do good to, and if you do evil it is to them likewise.´ Then, when the promise of the second came to pass, We sent against you Our servants to discountenance you, and to enter the Temple, as they entered it the first time, and to destroy utterly that which they ascended to.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,8,8,N´Perchance your Lord will have mercy upon you; but if you return, We shall return; and We have made Gehenna a prison for the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,9,9,N´Surely this Koran guides to the way that is straightest and gives good tidings to the believers who do deeds of righteousness, that theirs shall be a great wage,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,10,10,N´and that those who do not believe in the world to come -- we have prepared for them a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,11,11,N´Man prays for evil, as he prays for good; man is ever hasty.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,12,12,N´We have appointed the night and the day as two signs; then We have blotted out the sign of the night, and made the sign of the day to see, and that you may seek bounty from your Lord, and that you may know the number of the years, and the reckoning; and everything We have distinguished very distinctly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,13,13,N´And every man -- We have fastened to him his bird of omen upon his neck; and We shall bring forth for him, on the Day of Resurrection, a book he shall find spread wide open.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,14,14,N´´Read thy book! Thy soul suffices thee this day as a reckoner against thee.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,15,15,N´Whosoever is guided, is only guided to his own gain, and whosoever goes astray, it is only to his own loss; no soul laden bears the load of another. We never chastise, until We send forth a Messenger.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,16,16,N´And when We desire to destroy a city, We command its men who live at ease, and they commit ungodliness therein, then the Word is realized against it, and We destroy it utterly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,17,17,N´How many generations We have destroyed after Noah! Thy Lord suffices as one who is aware of and sees the sins of His servants.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,18,18,N´Whosoever desires this hasty world, We hasten for him therein what We will unto whomsoever We desire; then We appoint for him Gehenna wherein he shall roast, condemned and rejected.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,19,19,N´And whosoever desires the world to come and strives after it as he should, being a believer -- those, their striving shall be thanked.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,20,20,N´Each We succour, these and those, from thy Lord´s gift; and thy Lord´s gift is not confined.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,21,21,N´Behold, how We prefer some of them over others! And surely the world to come is greater in ranks, greater in preferment.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,22,22,N´Set not up with God another god, or thou wilt sit condemned and forsaken.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,23,23,N´Thy Lord has decreed you shall not serve any but Him, and to be good to parents, whether one or both of them attains old age with thee; say not to them ´Fie´ neither chide them, but speak unto them words respectful,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,24,24,N´and lower to them the wing of humbleness out of mercy and say; ´My Lord, have mercy upon them, as they raised me up when I was little.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,25,25,N´Your Lord knows very well what is in your hearts if you are righteous, for He is All-forgiving to those who are penitent.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,26,26,N´And give the kinsman his right, and the needy, and the traveller; and never squander;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,27,27,N´the squanderers are brothers of Satan, and Satan is unthankful to his Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,28,28,N´But if thou turnest from them, seeking mercy from thy Lord that thou hopest for, then speak unto them gentle words.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,29,29,N´And keep not thy hand chained to thy neck, nor outspread it widespread altogether, or thou wilt sit reproached and denuded.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,30,30,N´Surely thy Lord outspreads and straitens His provision unto whom He will; surely He is aware of and sees His servants.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,31,31,N´And slay not your children for fear of poverty; We will provide for you and them; surely the slaying of them is a grievous sin.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,32,32,N´And approach not fornication; surely it is an indecency, and evil as a way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,33,33,N´And slay not the soul God has forbidden, except by right. Whosoever is slain unjustly, We have appointed to his next-of-kin authority; but let him not exceed in slaying; he shall be helped.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,34,34,N´And do not approach the property of the orphan save in the fairest manner, until he is of age. And fulfil the covenant; surely the covenant shall be questioned of.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,35,35,N´And fill up the measure when you measure, and weigh with the straight balance; that is better and fairer in the issue.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,36,36,N´And pursue not that thou hast no knowledge of; the hearing, the sight, the heart -- all of those shall be questioned of.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,37,37,N´And walk not in the earth exultantly; certainly thou wilt never tear the earth open, nor attain the mountains in height.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,38,38,N´All of that -- the wickedness of it is hateful in the sight of thy Lord has revealed to thee:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,39,39,N´That is of the wisdom thy Lord set not up with God another god, or thou wilt be cast into Gehenna, reproached and rejected.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,40,40,N´What, has your Lord favoured you with sons and taken to Himself from the angels females? Surely it is a monstrous thing you are saying!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,41,41,N´We have turned about in this Koran, that they may remember; and it increases them only in aversion.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,42,42,N´Say: ´If there had been other gods with Him, as they say, in that case assuredly they would have sought a way unto the Lord of the Throne.´ Glory be to Him!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,43,43,N´High indeed be He exalted above that they say!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,44,44,N´The seven heavens and the earth, and whosoever in them is, extol Him; nothing is, that does not proclaim His praise, but you do not understand their extolling. Surely He is All-clement, All-forgiving.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,45,45,N´When thou recitest the Koran, We place between thee, and those who do not believe in the world to come, a curtain obstructing,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,46,46,N´and We lay veils upon their hearts lest they understand it, and in their ears heaviness. And when thou mentionest thy Lord only in the Koran, they turn in their traces in aversion.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,47,47,N´We know very well how they listen when they listen to thee, and when they conspire, when the evildoers say, ´You are only following a man bewitched!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,48,48,N´Behold, how they strike similitudes for thee, and go astray, and cannot find a way!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,49,49,N´They say, ´What, when we are bones and broken bits, shall we really be raised up again in a new creation?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,50,50,N´Say: ´Let you be stones, or iron,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,51,51,N´or some creation yet more monstrous in your minds!´ Then they will say, ´Who will bring us back?´ Say: ´He who originated you the first time.´ Then they will shake their heads at thee, and they will say, ´When will it be?´ Say: ´It is possible that it may be nigh,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,52,52,N´on the day when He will call you, and you will answer praising Him, and you will think you have but tarried a little.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,53,53,N´And say to My servants, that they say words that are kindlier. For surely Satan provokes strife between them, and Satan is ever a manifest foe to man.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,54,54,N´Your Lord knows you very well; if He will, He will have mercy on you, or, if He will, He will chastise you. We sent thee not to be a guardian over them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,55,55,N´And thy Lord knows very well all who are in the heavens and the earth; and We have preferred some Prophets over others; and We gave to David Psalms.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,56,56,N´Say: ´Call on those you asserted apart from Him; they have no power to remove affliction from you, or to transfer it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,57,57,N´Those they call upon are themselves seeking the means to come to their Lord, which of them shall be nearer; they hope for His mercy, and fear His chastisement. Surely thy Lord´s chastisement is a thing to beware of.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,58,58,N´No city is there, but We shall destroy it before the Day of Resurrection, or We shall chastise it with a terrible chastisement; that is in the Book inscribed.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,59,59,N´Naught prevented Us from sending the signs but that the ancients cried lies to them; and We brought Thamood the She-camel visible, but they did her wrong. And We do not send the signs, except to frighten.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,60,60,N´And when We said to thee, ´Surely thy Lord encompasses men,´ and We made the vision that We showed thee and the tree cursed in the Koran to be only a trial for men; and We frighten them, but it only increases them in great insolence.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,61,61,N´And when We said to the angels, ´Bow yourselves to Adam´; so they bowed themselves, save Iblis; he said,, ´Shall I bow myself unto one Thou hast created of clay?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,62,62,N´He said, ´What thinkest Thou? This whom Thou hast honoured above me -- if Thou deferrest me to the Day of Resurrection I shall assuredly master his seed, save a few.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,63,63,N´Said He, ´Depart! Those of them that follow thee -- surely Gehenna shall be your recompense, an ample recompense!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,64,64,N´And startle whomsoever of them thou canst with thy voice; and rally against them thy horsemen and thy foot, and share with them in their wealth and their children, and promise them!´ But Satan promises them naught, except delusion.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,65,65,N´´Surely over My servants thou shalt have no authority.´ Thy Lord suffices as a guardian.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,66,66,N´Your Lord it is who drives for you the ships on the sea that you may seek His bounty; surely He is All-compassionate towards you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,67,67,N´And when affliction visits you upon the sea, then there go astray those on whom you call except Him; and when He delivers you to land, you turn away; man is ever unthankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,68,68,N´Do you feel secure that He will not cause the shore to swallow you up, or loose against you a squall of pebbles, then you will find no guardian for you?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,69,69,N´Or do you feel secure that He will not send you back into it a second time, and loose against you a hurricane of wind and drown you for your thanklessness, then you will find no prosecutor for you against Us?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,70,70,N´We have honoured the Children of Adam and carried them on land and sea, and provided them with good things, and preferred them greatly over many of those We created.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,71,71,N´On the day when We shall call all men with their record, and whoso is given his book in his right hand -- those shall read their book, and they shall not be wronged a single date-thread.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,72,72,N´And whosoever is blind in this world shall be blind in the world to come, and he shall be even further astray from the way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,73,73,N´Indeed they were near to seducing thee from that We revealed to thee, that thou mightest forge against Us another, and then they would surely have taken thee as a friend;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,74,74,N´and had We not confirmed thee, surely thou wert near to inclining unto them a very little;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,75,75,N´then would We have let thee taste the double of life and the double of death; and then thou wouldst have found none to help thee against Us.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,76,76,N´Indeed they were near to startling thee from the land, to expel thee from it, and then they would have tarried after thee only a little --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,77,77,N´the wont of those We sent before thee of Our Messengers; thou wilt find no change to Our wont.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,78,78,N´Perform the prayer at the sinking of the sun to the darkening of the night and the recital of dawn; surely the recital of dawn is witnessed.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,79,79,N´And as for the night, keep vigil a part of it, as a work of supererogation for thee; it may be that thy Lord will raise thee up to a laudable station.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,80,80,N´And say: ´My Lord, lead me in with a just ingoing, and lead me out with a just outgoing; grant me authority from Thee, to help me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,81,81,N´And say: ´The truth has come, and falsehood has vanished away; surely falsehood is ever certain to vanish.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,82,82,N´And We send down, of the Koran, that which is a healing and a mercy to the believers; and the unbelievers it increases not, except in loss.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,83,83,N´And when We bless man, he turns away, and withdraws aside; but when evil visits him, he is in despair.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,84,84,N´Say: ´Every man works according to his own manner; but your Lord knows very well what man is best guided as to the way.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,85,85,N´They will question thee concerning the Spirit. Say: ´The Spirit is of the bidding of my Lord. You have been given of knowledge nothing except a little.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,86,86,N´If We willed, We could take away that We have revealed to thee, then thou wouldst find none thereover to guard thee against Us,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,87,87,N´excepting by some mercy of thy Lord; surly His favour to thee is great.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,88,88,N´Say: ´If men and jinn banded together to produce the like of this Koran, they would never produce its like, not though they backed one another.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,89,89,N´We have indeed turned about for men in this Koran every manner of similitude; yet most men refuse all but unbelief.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,90,90,N´They say, ´We will not believe thee till thou makest a spring to gush forth from the earth for us,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,91,91,N´or till thou possessest a garden of plants and vines, and thou makest rivers to gush forth abundantly all amongst it,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,92,92,N´or till thou makest heaven to fall, as thou assertest, on us in fragments, or thou bringest God and the angels as a surety,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,93,93,N´or till thou possessest a house of gold ornament, or till thou goest up into heaven; and we will not believe thy going up till thou bringest down on us a book that we may read. Say: ´Glory be to my Lord. Am I aught but a mortal, a Messenger?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,94,94,N´And naught prevented men from believing when the guidance came to them, but that they said, ´Has God sent forth a mortal as Messenger?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,95,95,N´Say: ´Had there been in the earth angels walking. at peace, We would have sent down upon them out of heaven an angel as Messenger.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,96,96,N´Say: ´God suffices as a witness between me and you; surely He is aware of and sees His servants.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,97,97,N´Whomsoever God guides, he is rightly guided; and whom He leads astray -- thou wilt not find for them protectors, apart from Him. And We shall muster them on the Resurrection Day upon their faces, blind, dumb, deaf; their refuge shall be Gehenna, and whensoever it abates We shall increase for them the Blaze.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,98,98,N´That is their recompense because they disbelieved in Our signs and said, ´What, when we are bones and broken bits, shall we really be raised up again in a new creation?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,99,99,N´Have they not seen that God, who created the heavens and earth, is powerful to create the like of them? He has appointed for them a term, no doubt of it; yet the unbelievers refuse all but unbelief.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,100,100,N´Say: ´If you possessed the treasuries of my Lord´s mercy, yet would you hold back for fear of expending; and man is ever niggardly.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,101,101,N´And We gave Moses nine signs, clear signs. Ask the Children of Israel when he came to them, and Pharaoh said to him, ´Moses, I think thou art bewitched.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,102,102,N´He said, ´Indeed thou knowest that none sent these down, except the Lord of the heavens and earth, as clear proofs; and, Pharaoh, I think thou art accursed.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,103,103,N´He desired to startle them from the land; and We drowned him and those with him, all together.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,104,104,N´And We said to the Children of Israel after him, ´Dwell in the land; and when the promise of the world to come comes to pass, We shall bring you a rabble.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,105,105,N´With the truth We have sent it down, and with the truth it has come down; and We have sent thee not, except good tidings to bear, and warning;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,106,106,N´and a Koran We have divided, for thee to recite it to mankind at intervals, and We have sent it down successively.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,107,107,N´Say: ´Believe in it, or believe not; those who were given the knowledge before it when it is recited to them, fall down upon their faces prostrating,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,108,108,N´and say, \"Glory be to our Lord! Our Lord´s promise is performed.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,109,109,N´And they fall down upon their faces weeping; and it increases them in humility.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,110,110,N´Say: ´Call upon God, or call upon the Merciful; whichsoever you call upon, to Him belong the Names Most Beautiful.´ And be thou not loud in thy prayer, nor hushed therein, but seek thou for a way between that.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,17,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,17,111,111,N´And say: ´Praise belongs to God, who has not taken to Him a son, and who has not any associate in the Kingdom, nor any protector out of humbleness.´ And magnify Him with repeated magnificats.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,1,1,N´Praise belongs to God who has sent down upon His servant the Book and has not assigned unto it any crookedness;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,2,2,N´right, to warn of great violence from Him, and to give good tidings unto the believers, who do righteous deeds, that theirs shall be a goodly wage´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,3,3,N´therein to abide for ever,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,4,4,N´and to warn those who say, ´God has taken to Himself a son´;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,5,5,N´they have no knowledge of it, they nor their fathers; a monstrous word it is, issuing out of their mouths; they say nothing but a lie.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,6,6,N´Yet perchance, if they believe not in this tiding, thou wilt consume thyself, following after them, of grief.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,7,7,N´We have appointed all that is on the earth for an adornment for it, and that We may try which of them is fairest in works;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,8,8,N´and We shall surely make all that is on it barren dust.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,9,9,N´Or dost thou think the Men of the Cave and Er-Rakeem were among Our signs a wonder?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,10,10,N´When the youths took refuge in the Cave saying, ´Our lord, give us mercy from Thee, and furnish us with rectitude in our affair.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,11,11,N´Then We smote their ears many years in the Cave.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,12,12,N´Afterwards. We raised them up again, that We might know which of the two parties would better calculate the while they had tarried.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,13,13,N´We will relate to thee their tidings truly. They were youths who believed in their Lord, and We increased them in guidance.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,14,14,N´And We strengthened their hearts, when they stood up and said, ´Our Lord is the Lord of the heavens and earth; we will not call upon any god, apart from Him, or then we had spoken outrage.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,15,15,N´These our people have taken to them other gods, apart from Him. Ah, if only they would bring some clear authority regarding them! But who does greater evil than he who forges against God a lie?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,16,16,N´So, when you have gone apart from them and that they serve, excepting God, take refuge in the Cave, and your Lord will unfold to you of His mercy, and will furnish -- you with a gentle issue of your affair.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,17,17,N´And thou mightest have seen the sun, when it rose, inclining from their Cave towards the right, and, when it set, passing them by on the left, while they were in a broad fissure of the Cave. That was one of God´s signs; whomsoever God guides, he is rightly guided, and whomsoever He leads astray, thou wilt not find for him a protector to direct.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,18,18,N´Thou wouldst have thought them awake, as they lay sleeping, while We turned them now to the right, now to the left,´ and their dog ´stretching its paws on the threshold. Hadst thou observed them surely thou wouldst have .turned thy back on them in flight, and been filled with terror of them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,19,19,N´And even so We raised them up again that they might question one another. One of them said, ´How long have you tarried?´ They said, ´We have tarried a day, or part of a day.´ They said, ´Your Lord knows very well how long you have tarried. Now send one of you forth with this silver to the city, and let him look for which of them has purest food, and bring you provision thereof; let him be courteous, and apprise no man of you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,20,20,N´If they should get knowledge of you they will stone you, or restore you to their creed, then you will not prosper ever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,21,21,N´And even so We made them stumble upon them, that they might know that God´s promise is true, and that the Hour -- there is no doubt of it. When they were contending among themselves of their affair then they said, ´Build over them a building; their Lord knows of them very well.´ Said those who prevailed over their affair, ´We will raise over them a place of worship.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,22,22,N´(They will say, ´Three; and ´their dog was the fourth of them.´ They will say, ´Five; and their dog was the sixth of them, guessing at the Unseen. They will say, ´Seven; and their dog was the eighth of them.´ Say: ´My Lord knows very well their number, and none knows them, except a few.´ So do not dispute with them, except in outward disputation, and ask not any of them for a pronouncement on them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,23,23,N´And do not say, regarding anything, ´I am going to do that tomorrow,´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,24,24,N´but only, ´If God will´; and mention thy Lord, when thou forgettest, and say, ´It may be that my Lord will guide me unto something nearer to rectitude than this.´)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,25,25,N´And they tarried in the Cave three hundred years, and to that they added nine more.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,26,26,N´Say: ´God knows very well how long they tarried. To Him belongs the Unseen in the heavens and in the earth. How well He sees! How well He hears! They have no protector, apart from Him, and He associates in His government no one.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,27,27,N´Recite what has been revealed to thee of the Book of thy Lord; no man can change His words. Apart from Him, thou wilt find no refuge.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,28,28,N´And restrain thyself with those who call upon their Lord at morning and evening, desiring His countenance, and let not thine eyes turn away from them, desiring the adornment of the present life; and obey not him whose heart We have made neglectful of Our remembrance so that he follows his own lust, and his affair has become all excess.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,29,29,N´Say: ´The truth is from your Lord; so let whosoever will believe, and let whosoever will disbelieve.´ Surely We have prepared for the evildoers a fire, whose pavilion encompasses them; if they call for succour, they will be succoured with water like molten copper, that shall scald their faces -- how evil a potion, and how evil a resting-place!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,30,30,N´Surely those who believe, and do deeds of righteousness -- surely We leave not to waste the wage of him who does good works;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,31,31,N´those -- theirs shall be Gardens of Eden, underneath which rivers flow; therein they shall be adorned with bracelets of gold, and they shall be robed in green garments of silk and brocade, therein reclining upon couches -- O, how excellent a reward! And O, how fair a resting-place!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,32,32,N´And strike for them a similitude: two men. To one of them We assigned two gardens of vines, and surrounded them with palm-trees, and between them We set a sown field;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,33,33,N´each of the two gardens yielded its produce and failed naught in any wise; and We caused to gush amidst them a river.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,34,34,N´So he had fruit; and he said to his fellow, as he was conversing with him, ´I have more abundance of wealth than thou and am mightier in respect of men.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,35,35,N´And he entered his garden, wronging himself; he said, ´I do not think that this will ever perish;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,36,36,N´I do not think that the Hour is coming; and if I am indeed returned to my Lord, I shall surely find a better resort than this.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,37,37,N´Said his fellow, as he was conversing with him, ´What, disbelievest thou in Him who created thee of dust, then of a sperm-drop, then shaped thee as a man?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,38,38,N´But lo, He is God, my Lord, and I will not associate with my Lord any one.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,39,39,N´Why, when thou wentest into thy garden, didst thou not say, \"As God will; there is no power except in God\"? If thou seest me, that I am less than thou in wealth and children,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,40,40,N´yet it may be that my Lord will give me better than thy garden, and loose on it a thunderbolt out of heaven, so that in the morning it will be a slope of dust,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,41,41,N´or in the morning the water of it will be sunk into the earth, so that thou wilt not be able to seek it out.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,42,42,N´And his fruit was all encompassed, and in the morning he was wringing his hands for that he had expended upon it, and it was fallen down upon its trellises, and he was saying, ´Would I had not associated with my Lord any one!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,43,43,N´But there was no host to help him, apart from God, and he was helpless.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,44,44,N´Thereover protection belongs only to God the True; He is best rewarding, best in the issue.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,45,45,N´And strike for them the similitude of the present life: it is as water that We send down out of heaven, and the plants of the earth mingle with it; and in the morning it is straw the winds scatter; and God is omnipotent over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,46,46,N´Wealth and sons are the adornment of the present world; but the abiding things, the deeds of righteousness, are better with God in reward, and better in hope.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,47,47,N´And on the day We shall set the mountains in motion, and thou seest the earth coming forth, and We muster them so that We leave not so much as one of them behind;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,48,48,N´and they shall be presented before their Lord in ranks -- ´You have come to Us, as We created you upon the first time; nay, you asserted We should not appoint for you a tryst.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,49,49,N´And the Book shall be set in place; and thou wilt see the sinners fearful at what is in it, and saying, ´Alas for us! How is it with this Book, that it leaves nothing behind, small or great, but it has numbered it?´ And they shall find all they wrought present, and thy Lord shall not wrong anyone.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,50,50,N´And when We said to the angels, ´Bow yourselves to Adam´; so they bowed themselves, save Iblis; he was one of the jinn, and committed ungodliness against his Lord´s command. What, and do you take him and his seed to be your friends, apart from Me, and they an enemy to you? How evil is that exchange for the evildoers!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,51,51,N´I made them not witnesses of the creation of the heavens and earth, neither of the creation of themselves; I would not ever take those who lead others astray to be My supporters.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,52,52,N´And on the day He shall say, ´Call on My associates whom you asserted´; and then they shall call on them, but they will not answer them, and We shall set a gulf between them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,53,53,N´Then the evildoers will see the Fire, and think that they are about to fall into it, and will find no escape from it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,54,54,N´We have indeed turned about for men in this Koran every manner of similitude; man is the most disputatious of things.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,55,55,N´And naught prevented men from believing when the guidance came unto them, and seeking their Lord´s forgiveness, but that the wont of the ancients should come upon them, or that the chastisement should come upon them face to face.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,56,56,N´And We send not the Envoys, but good tidings to bear, and warning. Yet do the unbelievers dispute with falsehood, that they may rebut thereby the truth. They have taken My signs, and what they are warned of, in mockery.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,57,57,N´And who does greater evil than he who, being reminded of the signs of his Lord, turns away from them and forgets what his hands have forwarded? Surely We have laid veils on their hearts lest they understand it, and in their ears heaviness; and though thou callest them to the guidance, yet they will not be guided ever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,58,58,N´But thy Lord is the All-forgiving, full of mercy. If He should take them to task for that they have earned, He would hasten for them the chastisement; but they have a tryst, from which they will find no escape.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,59,59,N´And those cities, We destroyed them when they did evil, and appointed for their destruction a tryst.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,60,60,N´And when Moses said to his page, ´I will not give up until I reach the meeting of the two seas, though I go on for many years.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,61,61,N´Then, when they reached their meeting, they forgot their fish, and it took its way into the sea, burrowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,62,62,N´When they had passed over, he said to his page, ´Bring us our breakfast; indeed, we have encountered weariness from this our journey.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,63,63,N´He said, ´What thinkest thou? When we took refuge in the rock, then I forgot the fish-and it was Satan himself that made me forget it so that I should not remember it -- and so it took its way into the sea in a manner marvellous.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,64,64,N´Said he, ´This is what we were seeking!´ And so they returned upon their tracks, retracing them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,65,65,N´Then they found one of Our servants unto whom We had given mercy from Us, and We had taught him knowledge proceeding from Us.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,66,66,N´Moses said to him, ´Shall I follow thee so that thou teachest me, of what thou hast been taught, right judgment.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,67,67,N´Said he, ´Assuredly thou wilt not be able to bear with me patiently.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,68,68,N´And how shouldst thou bear patiently that thou hast never encompassed in thy knowledge?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,69,69,N´He said, ´Yet thou shalt find me, if God will, patient; and I shall not rebel against thee in anything.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,70,70,N´Said he, ´Then if thou followest me, question me not on anything until I myself introduce the mention of it to thee.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,71,71,N´So they departed; until, when they embarked upon the ship, he made a hole in it. He said, ´What, hast thou made a hole in it so as to drown its passengers? Thou hast indeed done a grievous thing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,72,72,N´Said he, ´Did I not say that thou couldst never bear with me patiently?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,73,73,N´He said, ´Do not take me to task that I forgot, neither constrain me to do a thing too difficult.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,74,74,N´So they departed; until, when they met a lad, he slew him. He said, ´What, hast thou slain a soul innocent, and that not to retaliate for a soul slain? Thou hast indeed done a horrible thing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,75,75,N´Said he, ´Did I not say that thou couldst never bear with me patiently?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,76,76,N´He said, ´If I question thee on anything after this, then keep me company no more; thou hast already experienced excuse sufficient on my part.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,77,77,N´So they departed; until, when they reached the people of a city, they asked the people for food, but they refused to receive them hospitably. There they found a wall about to tumble down, and so he set it up. He said, ´If thou hadst wished, thou couldst have taken a wage for that.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,78,78,N´Said he, ´This is the parting between me and thee. Now I will tell thee the interpretation of that thou couldst not bear patiently.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,79,79,N´As for the ship, it belonged to certain poor men, who toiled upon the sea; and I desired to damage it, for behind them there was a king who was seizing every ship by brutal force.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,80,80,N´As for the lad, his parents were believers; and we were afraid he would impose on them insolence and unbelief;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,81,81,N´so we desired that their Lord should give to them in exchange one better than he in purity, and nearer in tenderness.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,82,82,N´As for the wall, it belonged to two orphan lads in the city, and under it was a treasure belonging to them. Their father was a righteous man; and thy Lord desired that they should come of age and then bring forth their treasure as a mercy from thy Lord. I did it not of my own bidding. This is the interpretation of that thou couldst not bear patiently.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,83,83,N´They will question thee concerning Dhool Karnain. Say: ´I will recite to you a mention of him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,84,84,N´We established him in the land, and We gave him a way to everything;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,85,85,N´and he followed a way´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,86,86,N´until, when he reached the setting of the sun, he found it setting in a muddy spring, and he found nearby a people. We said, ´O Dhool Karnain, either thou shalt chastise them, or thou shalt take towards them a way of kindness.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,87,87,N´He said, ´As for the evildoer, him we shall chastise, then he shall be returned to his Lord and He shall chastise him with a horrible chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,88,88,N´But as for him who believes, and does righteousness, he shall receive as recompense the reward most fair, and we shall speak to him, of our command, easiness.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,89,89,N´Then he followed a way´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,90,90,N´until, when he reached the rising of the sun, he found it rising upon a people for whom We had not appointed any veil to shade them from it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,91,91,N´So; and We encompassed in knowledge what was with him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,92,92,N´Then he followed a way´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,93,93,N´until, when he reached between the two barriers, he found this side of them a people scarcely able to understand speech.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,94,94,N´They said, ´O Dhool Karnain, behold, Gog and Magog are doing corruption in the earth; so shall we assign to thee a tribute, against thy setting up a barrier between us and between them?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,95,95,N´He said, ´That wherein my Lord has established me is better; so aid me forcefully, and I will set up a rampart between you and between them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,96,96,N´Bring me ingots of iron!´ Until, when he had made all level between the two cliffs, he said, ´Blow!´ Until, when he had made it a fire, he said, ´Bring me, that I may pour molten brass on it.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,97,97,N´So they were unable either to scale it or pierce it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,98,98,N´He said, ´This is a mercy from my Lord. But when the promise of my Lord comes to pass, He will make it into powder; and my Lord´s promise is ever true.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,99,99,N´Upon that day We shall leave them surging on one another, and the Trumpet shall he blown, and We shall gather them together,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,100,100,N´and upon that day We shall present Gehenna to the unbelievers´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,101,101,N´whose eyes were covered against My remembrance, and they were not able to hear.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,102,102,N´What, do the unbelievers reckon that they may take My servants as friends, apart from Me? We have prepared Gehenna for the unbelievers´ hospitality.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,103,103,N´Say: ´Shall We tell you who will be the greatest losers in their works?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,104,104,N´Those whose striving goes astray in the present life, while they think that they are working good deeds.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,105,105,N´Those are they that disbelieve in the signs of their Lord and the encounter with Him; their works have failed, and on the Day of Resurrection We shall not assign to them any weight.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,106,106,N´That is their recompense -- Gehenna for that they were unbelievers and took My signs and My messengers in mockery.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,107,107,N´But those who believe, and do deeds of righteousness -- the Gardens. of Paradise shall be their hospitality,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,108,108,N´therein to dwell forever, desiring no removal out of them.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,109,109,N´Say:. ´If the sea were ink for the Words of my Lord, the sea would be spent before the Words of my Lord are spent, though We brought replenishment the like of it.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,18,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,18,110,110,N´Say: ´I am only a mortal the like of you; it is revealed to me that your God is One God. So let him, who hopes for the encounter with his Lord, work righteousness, and not associate with his Lord´s service anyone.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,1,1,N´Kaf Ha Ya Ain Sad´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,2,2,N´The mention of thy Lord´s mercy unto His servant Zachariah;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,3,3,N´when he called upon his Lord secretly´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,4,4,N´saying, ´O my Lord, behold the bones within me are feeble and my head is all aflame with hoariness. And in calling on Thee, my Lord, I have never been hitherto unprosperous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,5,5,N´And now I fear my kinsfolk after I am gone; and my wife is barren. So give me, from Thee, a kinsman´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,6,6,N´who shall be my inheritor and the inheritor of the House of Jacob; and make him, my Lord, well-pleasing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,7,7,N´´O Zachariah, We give thee good tidings of a boy, whose name is John. No namesake have We given him aforetime.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,8,8,N´He said, ´O my Lord, how shall I have a son, seeing my wife is barren, and I have attained to the declining of old age?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,9,9,N´Said He, ´So it shall be; thy Lord says, \"Easy is that for Me, seeing that I created thee aforetime, when thou wast nothing.\"´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,10,10,N´He said, ´Lord, appoint to me some sign.´ Said He, ´Thy sign is that thou shall not speak to men, though being without fault, three nights.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,11,11,N´So he came forth unto his people from the Sanctuary, then he made signal to them, ´Give you glory at dawn and evening.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,12,12,N´´O John, take the Book forcefully´; and We gave him judgment, yet a little child,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,13,13,N´and a tenderness from Us, and purity; and he was godfearing,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,14,14,N´and cherishing his parents, not arrogant, rebellious.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,15,15,N´´Peace be upon him, the day he was born, and the day he dies, and the day he is raised up alive!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,16,16,N´And mention in the Book Mary when she withdrew from her people to an eastern place,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,17,17,N´and she took a veil apart from them; then We sent unto her Our Spirit that presented himself to her a man without fault.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,18,18,N´She said, ´I take refuge in the All-merciful from thee!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,19,19,N´If thou fearest God ... He said, ´I am but a messenger come from thy Lord, to give thee a boy most pure.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,20,20,N´She said, ´How shall I have a son whom no mortal has touched, neither have I been unchaste?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,21,21,N´He said, ´Even so thy Lord has said: \"Easy is that for Me; and that We may appoint him a sign unto men and a mercy from Us; it is a thing decreed.\"´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,22,22,N´So she conceived him, and withdrew with him to a distant place.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,23,23,N´And the birthpangs surprised her by the trunk of the palm-tree. She said, ´Would I had died ere this, and become a thing forgotten!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,24,24,N´But the one that was below her called to her, ´Nay, do not sorrow; see, thy Lord has set below thee a rivulet.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,25,25,N´Shake also to thee the palm-trunk, and there shall come tumbling upon thee dates fresh and ripe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,26,26,N´Eat therefore, and drink, and be comforted; and if thou shouldst see any mortal, say, \"I have vowed to the All-merciful a fast, and today I will not speak to any man.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,27,27,N´Then she brought the child to her folk carrying him; and they said, ´Mary, thou hast surely committed a monstrous thing!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,28,28,N´Sister of Aaron, thy father was not a wicked man, nor was thy mother a woman unchaste.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,29,29,N´Mary pointed to the child then; but they said, ´How shall we speak to one who is still in the cradle, a little child?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,30,30,N´He said, ´Lo, I am God´s servant; God has given me the Book, and made me a Prophet.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,31,31,N´Blessed He has made me, wherever I may be; and He has enjoined me to pray, and to give the alms, so long as I live,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,32,32,N´and likewise to cherish my mother; He has not made me arrogant, unprosperous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,33,33,N´Peace be upon me, the day I was born, and the day I die, and the day I am raised up alive!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,34,34,N´That is Jesus, son of Mary, in word of truth, concerning which they are doubting.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,35,35,N´It is not for God to take a son unto Him. Glory be to Him! When He decrees a thing, He but says to it ´Be,´ and it is.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,36,36,N´Surely God is my Lord, and your Lord; So serve you Him. This is a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,37,37,N´But the parties have fallen into variance among themselves; then woe to those who disbelieve for the scene of a dreadful day.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,38,38,N´How well they will hear and see on the day they come to Us! But the evildoers even today are in error manifest.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,39,39,N´Warn thou them of the day of anguish, when the matter shall be determined, and they yet heedless and unbelieving.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,40,40,N´Surely We shall inherit the earth and all that are upon it, and unto Us they shall be returned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,41,41,N´And mention in the Book Abraham; surely he was a true man, a Prophet.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,42,42,N´When he said to his father, ´Father, why worshippest thou that which neither hears nor sees, nor avails thee anything?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,43,43,N´Father, there has come to me knowledge such as came not to thee; so follow me, and I will guide thee on a level path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,44,44,N´Father, serve not Satan; surely Satan is a rebel against the All-merciful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,45,45,N´Father, I fear that some chastisement from the All-merciful will smite thee, so that thou becomest a friend to Satan.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,46,46,N´Said he, ´What, art thou shrinking from my gods, Abraham? Surely, if thou givest not over, I shall stone thee; so forsake me now for some while.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,47,47,N´He said, ´Peace be upon thee! I will ask my Lord to forgive thee; surely He is ever gracious to me.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,48,48,N´Now I will go apart from you and that you call upon, apart from God; I will call upon my Lord, and haply I shall not be, in calling upon my Lord, unprosperous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,49,49,N´So, when he went apart from them and that they were serving, apart from God, We gave him Isaac and Jacob, and each We made a Prophet;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,50,50,N´and We gave them of Our mercy, and We appointed unto them a tongue of truthfulness, sublime.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,51,51,N´And mention in the Book Moses; he was devoted, and he was a Messenger, a Prophet.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,52,52,N´We called to him from the right side Of the Mount, and We brought him near in communion.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,53,53,N´And We gave him his brother Aaron, of Our mercy, a Prophet.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,54,54,N´And mention in the Book Ishmael; he was true to his promise, and he was a Messenger, a Prophet.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,55,55,N´He bade his people to pray and to give the alms, and he was pleasing to his Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,56,56,N´And mention in the Book Idris; he was a true man, a Prophet.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,57,57,N´We raised him up to a high place.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,58,58,N´These are they whom God has blessed among the Prophets of the seed of Adam, and of those We bore with Noah, and of the seed of Abraham and Israel, and of those We guided and chose. When the signs of the All-merciful were recited to them, they fell down prostrate, weeping.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,59,59,N´Then there succeeded after them a succession who wasted the prayer, and followed lusts; so they shall encounter error´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,60,60,N´save him who repents, and believes, and does a righteous deed; those -- they shall enter Paradise, and they shall not be wronged anything;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,61,61,N´Gardens of Eden that the All-merciful promised His servants in the Unseen; His promise is ever performed.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,62,62,N´There they shall hear no idle talk, but only ´Peace.´ There they shall have their provision at dawn and evening.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,63,63,N´That is Paradise which We shall give as an inheritance to those of Our servants who are godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,64,64,N´We come not down, save at the commandment of thy Lord. To Him belongs all that is before us, and all that is behind us, and all between that.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,65,65,N´And thy Lord is never forgetful, Lord He of the heavens and earth and all that is between them. So serve Him, and be thou patient in His service; knowest thou any that can be named with His Name?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,66,66,N´Man says, ´What, when I am dead shall I then be brought forth alive?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,67,67,N´Will not man remember that We created him aforetime, when he was nothing?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,68,68,N´Now, by thy Lord, We shall surely muster them, and the Satans, then We shall parade them about Gehenna hobbling on their knees.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,69,69,N´Then We shall pluck forth from every party whichever of them was the most hardened in disdain of the All-merciful;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,70,70,N´then We shall know very well those most deserving to burn there.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,71,71,N´Not one of you there is, but he shall go down to it; that for thy Lord is a thing decreed, determined.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,72,72,N´Then We shall deliver those that were godfearing; and the evildoers We shall leave there, hobbling on their knees.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,73,73,N´When Our signs are recited to them as clear signs, the unbelievers say to the believers, ´Which of the two parties is better in station, fairer in assembly?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,74,74,N´And how many a generation We destroyed before them, who were fairer in furnishing and outward show!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,75,75,N´Say: ´Whosoever is in error, let the All-merciful prolong his term for him! Till, when they see that they were threatened, whether the chastisement, or the Hour, then they shall surely know who is worse in place, and who is weaker in hosts.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,76,76,N´And God shall increase those who were guided in guidance; and the abiding things, the deeds of righteousness, are better with thy Lord in reward, and better in return´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,77,77,N´Hast thou seen him who disbelieves in Our signs and says, ´Assuredly I shall be given wealth and children´?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,78,78,N´What, has he observed the Unseen, or taken a covenant with the All-merciful?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,79,79,N´No, indeed! We shall assuredly write down all that he says, and We shall prolong for him the chastisement;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,80,80,N´and We shall inherit from him that he says, and he shall come to Us alone.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,81,81,N´And they have taken to them other gods apart from God, that they might be for them a might.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,82,82,N´No, indeed! They shall deny their service, and they shall be against them pitted.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,83,83,N´Hast thou not seen how We sent the Satans against the unbelievers, to prick them?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,84,84,N´So hasten thou not against them; We are only numbering for them a number.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,85,85,N´On the day that We shall muster the godfearing to the All-merciful with pomp´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,86,86,N´and drive the evildoers into Gehenna herding,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,87,87,N´having no power of intercession, save those who have taken with the All-merciful covenant.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,88,88,N´And they say, ´The All-merciful has taken unto Himself a son.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,89,89,N´You have indeed advanced something hideous!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,90,90,N´The heavens are wellnigh rent of it and the earth split asunder, and the mountains wellnigh fall down crashing for´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,91,91,N´that they have attributed to the All-merciful a son;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,92,92,N´and it behoves not the All-merciful to take a son.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,93,93,N´None is there in the heavens and earth but he comes to the All-merciful as a servant;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,94,94,N´He has indeed counted them, and He has numbered them exactly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,95,95,N´Every one of them shall come to Him upon the Day of Resurrection, all alone.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,96,96,N´Surely those who believe and do deeds of righteousness -- unto them the All-merciful shall assign love.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,97,97,N´Now We have made it easy by thy tongue that thou mayest bear good tidings thereby to the godfearing, and warn a people stubborn.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,19,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,19,98,98,N´And how many a generation We destroyed before them! Dost thou perceive so much as one of them, or hear of them a whisper?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,1,1,N´Ta Ha´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,2,2,N´We have not sent down the Koran upon thee for thee to be unprosperous,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,3,3,N´but only as a reminder to him who tears,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,4,4,N´a revelation from Him who created the earth and the high heavens;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,5,5,N´the All-compassionate sat Himself upon the Throne; to Him belongs´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,6,6,N´all that is in the heavens and the earth and all that is between them, and all that is underneath the soil.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,7,7,N´Be thou loud in thy speech, yet surely He knows the secret and that yet more hidden.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,8,8,N´God -- there is no god but He. To Him belong the Names Most Beautiful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,9,9,N´Hast thou received the story of Moses?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,10,10,N´When he saw a tire, and said to his family, ´Tarry you here; I observe a fire. Perhaps I shall bring you a brand from it, or I shall find at the fire guidance.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,11,11,N´When he came to it, a voice cried, ´Moses,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,12,12,N´I am thy Lord; put off thy shoes; thou art in the holy valley, Towa.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,13,13,N´I Myself have chosen thee; therefore give thou ear to this revelation.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,14,14,N´Verily I am God; there is no god but I; therefore serve Me, and perform the prayer of My remembrance.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,15,15,N´The Hour is coming; I would conceal it that every soul may be recompensed for its labours.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,16,16,N´Let none bar thee from it, that believes not in it but follows after his own caprice, or thou wilt perish.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,17,17,N´What is that, Moses, thou hast in thy right hand?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,18,18,N´´Why, it is my staff,´ said Moses. ´I lean upon it, and with it I beat down leaves to feed my sheep; other uses also I find in it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,19,19,N´Said He, ´Cast it down, Moses!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,20,20,N´and he cast it down, and behold it was a serpent sliding.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,21,21,N´Said He, ´Take it, and fear not; We will restore it to its first state.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,22,22,N´Now clasp thy hand to thy arm-pit; it shall come forth white, without evil. That is a second sign.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,23,23,N´So We would show thee some of Our greatest signs.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,24,24,N´Go to Pharaoh; he has waxed insolent.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,25,25,N´´Lord, open my breast,´ said Moses,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,26,26,N´´and do Thou ease for me my task.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,27,27,N´Unloose the knot upon my tongue, t´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,28,28,N´hat they may understand my words.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,29,29,N´Appoint for me of my folk a familiar,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,30,30,N´Aaron, my brother;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,31,31,N´by him confirm my strength,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,32,32,N´and associate him with me in my task.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,33,33,N´So shall we glorify Thee,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,34,34,N´and remember Thee abundantly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,35,35,N´Surely Thou seest into us.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,36,36,N´Said He, ´Thou art granted, Moses, thy petition.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,37,37,N´Already another time We favoured thee,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,38,38,N´when We revealed what was revealed unto thy mother:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,39,39,N´\"Cast him into the ark, and cast him into the river, and let the river throw him up on the shore. An enemy of Mine and his shall take him.\" And I loaded on thee love from Me, and to be formed in My sight,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,40,40,N´when thy sister went out, saying, \"Shall I point you to one to have charge of him?\" So We returned thee to thy mother that she might rejoice, and not sorrow. Then thou slewest; a living soul, and We delivered thee out of grief, and We tried thee with many trials. Many years among the people of Midian thou didst sojourn, then camest hither, Moses, according to a decree.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,41,41,N´I have chosen thee for My service;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,42,42,N´go therefore, thou and thy brother; with My signs, and neglect not to remember Me.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,43,43,N´Go to Pharaoh, for he has waxed insolent;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,44,44,N´yet speak gently to him, that haply he may be mindful, or perchance fear.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,45,45,N´´O our Lord,´ said Moses and Aaron, ´truly we fear he may exceed against us, or wax insolent.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,46,46,N´´Fear not,´ said He. ´Surely I shall be with you, hearing and seeing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,47,47,N´So go you both to Pharaoh, and say, \"We are the Messengers of thy Lord, so send forth with us the Children of Israel and chastise them not; we have brought thee a sign from thy Lord; and peace be upon him who follows the guidance!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,48,48,N´It has been revealed to us that chastisement shall light upon him who cries lies and turns his back.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,49,49,N´Pharaoh said, ´Who is your Lord, Moses?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,50,50,N´He said, ´Our Lord is He who gave everything its creation, then guided it.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,51,51,N´Pharaoh said, ´And what of the former generations?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,52,52,N´Said Moses, ´The knowledge of them is with my Lord, in a Book; my Lord goes not astray, nor forgets --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,53,53,N´He who appointed the earth to be a cradle for you, and therein threaded roads for you, and sent down water out of heaven, and therewith We have brought forth divers kinds of plants.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,54,54,N´Do you eat, and pasture your cattle! Surely in that are signs for men possessing reason.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,55,55,N´Out of the earth We created you, and We shall restore you into it, and bring you forth from it a second time.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,56,56,N´So We showed Pharaoh all Our signs, but he cried lies, and refused.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,57,57,N´´Hast thou come, Moses,´ he said, ´to expel us out of our land´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,58,58,N´by thy sorcery? We shall assuredly bring thee sorcery the like of it; therefore appoint a tryst between us and thee, a place mutually agreeable, and we shall not fail it, neither thou.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,59,59,N´´Your tryst shall be upon the Feast Day.´ said Moses. ´Let the people be mustered at the high noon.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,60,60,N´Pharaoh then withdrew, and gathered his guile. Thereafter he came again,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,61,61,N´and Moses said to them, ´O beware! Forge not a lie against God, lest He destroy you with a chastisement. Whoso forges has ever failed.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,62,62,N´And they disputed upon their plan between them, and communed secretly´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,63,63,N´saying, ´These two men are sorcerers and their purpose is to expel you out of your land by their sorcery, and to extirpate your justest way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,64,64,N´So gather your guile; then come in battle-line. Whoever today gains the upper hand shall surely prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,65,65,N´They said, ´Moses, either thou wilt cast, or we shall be the first to cast.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,66,66,N´´No,´ said Moses. ´Do you cast!´ And lo, it seemed to him, by their sorcery, their ropes and their staffs were sliding;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,67,67,N´and Moses conceived a fear within him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,68,68,N´We said unto him, ´Fear not; surely thou art the uppermost.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,69,69,N´Cast down what is in thy right hand, and it shall swallow what they have fashioned; for they have fashioned only the guile of a sorcerer, and the sorcerer prospers not; wherever he goes.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,70,70,N´And the sorcerers cast themselves down prostrating. ´We believe,´ they said, ´in the Lord of Aaron and Moses.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,71,71,N´Pharaoh said, ´Have you believed him before I gave you leave? Why, he is the chief of you, the same that taught you sorcery; I shall assuredly cut off alternately your hands and feet, then I shall crucify you upon the trunks of palm-trees; you shall know of a certainty which of us is more terrible in chastisement, and more abiding.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,72,72,N´They said, ´We will not prefer thee over the clear signs that have come to us, nor over Him who originated us. Decide then what thou wilt decide; thou canst only decide touching this present life.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,73,73,N´We believe in our Lord, that He may pardon us our offences, and the sorcery thou hast constrained us to practise; God is better, and more abiding.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,74,74,N´Whosoever comes unto his Lord a sinner, for him awaits Gehenna wherein he shall neither die nor live.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,75,75,N´And whoso comes unto Him a believer having done deeds of righteousness, those -- for them await the most sublime degrees;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,76,76,N´Gardens of Eden, underneath which rivers flow, therein dwelling forever; that is the recompense of the self-purified.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,77,77,N´Also We revealed unto Moses, ´Go with My servants by night; strike for them a dry path in the sea, fearing not overtaking, neither afraid.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,78,78,N´Pharaoh followed them with his hosts, but they were overwhelmed by the sea;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,79,79,N´so Pharaoh had led his people astray, and was no guide to them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,80,80,N´Children of Israel, We delivered you from your enemy; and We made covenant with you upon the right side of the Mount, and sent down on you manna and quails:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,81,81,N´´Eat of the good things wherewith We have provided you; but exceed not therein, or My anger shall alight on you; and on whomsoever My anger alights, that man is hurled to ruin.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,82,82,N´Yet I am All-forgiving to him who repents and believes, and does righteousness, and at last is guided.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,83,83,N´´What has sped thee far from thy people, Moses?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,84,84,N´´They are upon my tracks,´ Moses said. ´I have hastened, Lord, only that I may please Thee.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,85,85,N´Said He, ´We have tempted thy people since thou didst leave them. The Samaritan has misled them into error.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,86,86,N´Then Moses returned very angry and sorrowful to his people, saying, ´My people, did your Lord not promise a fair promise to you? Did the time of the covenant seem so long to you, or did you desire that anger should alight on you from your Lord, so that you failed in your tryst with me?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,87,87,N´´We have not failed in our tryst with thee,´ they said, ´of our volition; but we were loaded with fardels, even the ornaments of the people, and we cast them, as the Samaritan also threw them, into the fire.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,88,88,N´(Then he brought out for them a Calf, a mere body that lowed; and they said, ´This is your god, and the god of Moses, whom he has forgotten.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,89,89,N´What? Did they not see that thing returned no speech unto them, neither had any power to hurt or profit them?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,90,90,N´Yet Aaron had aforetime said to them, ´My people, you have been tempted by this thing, no more; surety your Lord is the All-merciful; therefore follow me, and obey my commandment!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,91,91,N´´We will not cease,´ they said, ´to cleave to it, until Moses returns to us.´)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,92,92,N´Moses said, ´What prevented thee, Aaron, when thou sawest them in error,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,93,93,N´so that thou didst not follow after me? Didst thou then disobey my commandment?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,94,94,N´´Son of my mother,´ Aaron said, ´take me not by the beard, or the head! I was fearful that thou wouldst say, \"Thou hast divided the Children of Israel, and thou hast not observed my word.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,95,95,N´Moses said, ´And thou, Samaritan, what was thy business?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,96,96,N´´I beheld what they beheld not,´ he said, ´and I seized a handful of dust from the messenger´s track, and cast it into the thing. So my soul prompted me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,97,97,N´´Depart!´ said Moses. ´It shall be thine all this life to cry \"Untouchable!\" And thereafter a tryst awaits thee thou canst not fail to keep Behold thy god, to whom all the day thou wast cleaving! We will surely burn it and scatter its ashes into the sea.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,98,98,N´Your God is only the One God; there is no god, but He alone who in His knowledge embraces everything.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,99,99,N´So We relate to thee stories of what has gone before, and We have given thee a remembrance from Us.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,100,100,N´Whosoever turns away from it, upon the Day of Resurrection He shall bear a fardel,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,101,101,N´therein abiding forever; how evil upon the Day of Resurrection that burden for them!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,102,102,N´On the day the Trumpet is blown; and We shall muster the sinners upon that day with eyes staring,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,103,103,N´whispering one to another, ´You have tarried only ten nights.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,104,104,N´We know very well what they will say, when the justest of them in the way will say, ´You have tarried only a day.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,105,105,N´They will question thee concerning the mountains. Say: ´My Lord will scatter them as ashes;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,106,106,N´then He will leave them a level hollow´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,107,107,N´wherein thou wilt see no crookedness neither any curving.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,108,108,N´On that day they will follow the Summoner in whom is no crookedness; voices will be hushed to the All-merciful, so that thou hearest naught but a murmuring.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,109,109,N´Upon that day the intercession will not profit, save for him to whom the All-merciful gives leave, and whose speech He approves.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,110,110,N´He knows what is before them and behind them, and they comprehend Him not in knowledge.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,111,111,N´And faces shall be humbled unto the Living, the Eternal. He will have failed whose burden is of evildoing;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,112,112,N´but whosoever does deeds of righteousness, being a believer, shall fear neither wrong nor injustice.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,113,113,N´Even so We have sent it down as an Arabic Koran, and We have turned about in it something of threats, that haply they may be godfearing, or it may arouse in them remembrance.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,114,114,N´So high exalted be God, the true King! And hasten not with the Koran ere its revelation is accomplished unto thee; and say, ´O my Lord, increase me in knowledge.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,115,115,N´And We made covenant with Adam before, but he forgot, and We found in him no constancy.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,116,116,N´And when We said to the angels, ´Bow yourselves to Adam´; so they bowed themselves, save Iblis; he refused.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,117,117,N´Then We said, ´Adam, surely this is an enemy to thee and thy wife. So let him not expel you both from the Garden, so that thou art unprosperous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,118,118,N´It is assuredly given to thee neither to hunger therein, nor to go naked,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,119,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,119,119,N´neither to thirst therein, nor to suffer the sun.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,120,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,120,120,N´Then Satan whispered to him saying, ´Adam, shall I point thee to the Tree of Eternity, and a Kingdom that decays not?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,121,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,121,121,N´So the two of them ate of it, and their shameful parts revealed to them, and they took to stitching upon themselves leaves of the Garden. And Adam disobeyed his Lord, and so he erred.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,122,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,122,122,N´Thereafter his Lord chose him, and turned again unto him, and He guided him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,123,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,123,123,N´Said He, ´Get you down, both of you together, out of it, each of you an enemy to each; but if there comes to you from Me guidance, then whosoever follows My guidance shall not go astray, neither shall he be unprosperous;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,124,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,124,124,N´but whosoever turns away from My remembrance, his shall be a life of narrowness, and on the Resurrection Day We shall raise him blind.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,125,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,125,125,N´He shall say, ´O my Lord, why hast thou raised me blind, and I was wont to see?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,126,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,126,126,N´God shall say, ´Even so it is. Our signs came unto thee, and thou didst forget them; and so today thou art forgotten.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,127,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,127,127,N´So We recompense him who is prodigal and believes not in the signs of his Lord; and the chastisement of the world to come is more terrible and more enduring.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,128,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,128,128,N´Is it not a guidance to them, how many generations We destroyed before them in whose dwelling-places they walk? Surely in that are signs for men possessing reason.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,129,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,129,129,N´And but for a word that preceded from thy Lord, and a stated term, it had been fastened.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,130,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,130,130,N´So be thou patient under what they say, and proclaim thy Lord´s praise before the rising of the sun, and before its setting, and proclaim thy Lord´s praise in the watches of the night, and at the ends of the day; haply thou wilt be well-pleasing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,131,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,131,131,N´Stretch not thine eyes to that We have given pairs of them to enjoy - the flower of the present life, that We may try them therein and thy Lord´s provision is better, and more enduring.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,132,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,132,132,N´And bid thy family to pray, and be thou patient in it; We ask of thee no provision, but it is We who provide thee and the issue ultimate is to godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,133,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,133,133,N´They say, ´Why does he not bring us a sign from his Lord?´ Has there not come to them the clear sign of what is in the former scrolls?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,134,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,134,134,N´Had We destroyed them with a chastisement aforetime, they would have said, ´Our Lord, why didst Thou not send us a Messenger, so that we might have followed Thy signs before that we were humiliated and degraded?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,20,135,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,20,135,135,N´Say: ´Everyone is waiting; so wait, and assuredly you shall know who are the travellers on the even path, and who is guided.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,1,1,N´Nigh unto men has drawn their reckoning, while they in heedlessness are yet turning away;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,2,2,N´no Remembrance from their Lord comes to them lately renewed, but they listen to it yet playing,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,3,3,N´diverted their hearts. The evildoers whisper one to another, ´Is this aught but a mortal like to yourselves? What, will you take to sorcery with your eyes open?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,4,4,N´He says: ´My Lord knows what is said in the heavens and the earth, and He is the All-hearing, the All-knowing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,5,5,N´Nay, but they say: ´A hotchpotch of nightmares! Nay, he has forged it; nay, he is a poet! Now therefore let him bring us a sign, even as the ancient ones were sent as Messengers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,6,6,N´Not one city that We destroyed before them believed; what then, will they not believe?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,7,7,N´And We sent none before thee, but men to whom We made revelation -- question the People of the Remembrance, if you do not know --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,8,8,N´nor did We fashion them as bodies that ate not food, neither were they immortal;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,9,9,N´then We made true the promise We gave them and We delivered them, and whomsoever We would; and We destroyed the prodigal.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,10,10,N´Now We have sent down to you a Book wherein is your Remembrance; will you not understand?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,11,11,N´How many a city that was evildoing We have shattered, and set up after it another people!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,12,12,N´Then, when they perceived Our might, behold, they ran headlong out of it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,13,13,N´´Run not! Return you unto the luxury that you exulted in, and your dwelling-places; haply you shall be questioned.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,14,14,N´They said, ´Alas for us! We have been evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,15,15,N´So they ceased not to cry, until We made them stubble, silent and still.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,16,16,N´We created not the heaven and the earth, and whatsoever between them is, as playing;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,17,17,N´had We desired to take to Us a diversion We would have taken it to Us from Ourselves, had We done aught.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,18,18,N´Nay, but We hurl the truth against falsehood and it prevails over it, and behold, falsehood vanishes away. Then woe to you for that you describe!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,19,19,N´To Him belongs whosoever is in the heavens and the earth; and those who are with Him wax not too proud to do Him service neither grow weary,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,20,20,N´glorifying Him by night and in the daytime and never failing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,21,21,N´Or have they taken gods out of the earth who raise the dead?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,22,22,N´Why, were there gods in earth and heaven other than God, they would surely go to ruin; so glory be to God, the Lord of the Throne, above that they describe!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,23,23,N´He shall not be questioned as to what He does, but they shall he questioned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,24,24,N´Or have they taken gods apart from Him? Say: ´Bring your proof! This is the Remembrance of him who is with me, and the Remembrance of those before me. Nay, but the most part of them know not the truth, so therefore they are turning away.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,25,25,N´And We sent never a Messenger before thee except that We revealed to him, saying, ´There is no god but I; so serve Me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,26,26,N´They say:´ ´The All-merciful has taken to Him a son.´ Glory be to Him! Nay, but they are honoured servants´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,27,27,N´that outstrip Him not in speech, and perform as He commands.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,28,28,N´He knows what is before them and behind them, and they intercede not save for him with whom He is well-pleased, and they tremble in awe of Him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,29,29,N´If any of them says, ´I am a god apart from Him´, such a one We recompense with Gehenna; even so We recompense the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,30,30,N´Have not the unbelievers then beheld that the heavens and the earth were a mass all sewn up, and then We unstitched them and of water fashioned every living thing? Will they not believe?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,31,31,N´And We set in the earth firm mountains lest it should shake with them, and We set in it ravines to serve as ways, that haply so they may be guided;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,32,32,N´and We set up the heaven as a roof well-protected; yet still from Our signs they are turning away.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,33,33,N´It is He who created the night and the day, the sun and the moon, each swimming in a sky.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,34,34,N´We have not assigned to any mortal before thee to live forever; therefore, if thou diest, will they live forever?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,35,35,N´Every soul shall taste of death; and We try you with evil and good for a testing, then unto Us you shall be returned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,36,36,N´When the unbelievers behold thee, they take thee only for mockery: ´Ha, is this the one who makes mention of your gods?´ Yet they in the Remembrance of the All-merciful are unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,37,37,N´Man was created of haste. Assuredly I shall show you My signs; so demand not that I make haste.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,38,38,N´They say, ´And when shall the promise come to pass, if you speak truly?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,39,39,N´If the unbelievers but knew when that they shall not ward off the Fire from their faces nor from their backs, neither shall they be helped!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,40,40,N´Nay, but it shall come upon them suddenly, dumbfounding them, and they shall not be able to repel it, nor shall they be respited.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,41,41,N´Messengers indeed were mocked at before thee, but those that scoffed at them were encompassed by that they mocked at.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,42,42,N´Say: ´Who shall guard you by night and in the daytime from the All-merciful?´ Nay, but from the Remembrance of their Lord they are turning away.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,43,43,N´Or have they gods that shall defend them apart from Us? Why, they are not able to help themselves, nor shall they be guarded in safety from Us.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,44,44,N´Nay, but Ourselves gave these and their fathers enjoyment of days, until their life had lasted long while upon them. What, do they not see how We come to the land, diminishing it in its extremities? Or are they the victors?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,45,45,N´Say: ´I warn you only by the Revelation´; but they that are deaf do not hear the call when they are warned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,46,46,N´If but a breath of thy Lord´s chastisement touched them, they would surely say, ´Alas for us! We were evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,47,47,N´And We shall set up the just balances for the Resurrection Day, so that not one soul shall be wronged anything; even if it be the weight of one grain of mustard-seed We shall produce it, and sufficient are We for reckoners.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,48,48,N´We gave Moses and Aaron the Salvation and a Radiance, and a Remembrance for the godfearing´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,49,49,N´such as fear God in the Unseen, trembling because of the Hour.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,50,50,N´And this is a blessed Remembrance that We have sent down; so are you now denying it?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,51,51,N´We gave Abraham aforetime his rectitude -- for We knew him --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,52,52,N´when he said to his father and his people, ´What are these statues unto which you are cleaving?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,53,53,N´They said, ´We found our fathers serving them.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,54,54,N´He said, ´Then assuredly you and your fathers have been in manifest error.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,55,55,N´They said, ´What, hast thou come to us with the truth, or art thou one of those that play?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,56,56,N´He said, ´Nay, but your Lord is the Lord of the heavens and the earth who originated them, and I am one of those that bear witness thereunto.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,57,57,N´And, by God, I shall assuredly outwit your idols, after you have gone away turning your backs.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,58,58,N´So he broke them into fragments, all but a great one they had, for haply they would return to it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,59,59,N´They said, ´Who has. done this with our gods? Surely he is one of the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,60,60,N´They said, ´We heard a young man making mention of them, and he was called Abraham.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,61,61,N´They said, ´Bring him before the people´s eyes; haply they shall bear witness.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,62,62,N´They said, ´So, art thou the man who did this unto our gods, Abraham?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,63,63,N´He said, ´No; it was this great one of them that did it. Question them; if they are able to speak!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,64,64,N´So they returned one to another, and they said, ´Surely it is you who are the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,65,65,N´Then they were utterly put to confusion saying, ´Very well indeed thou knowest these do not speak.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,66,66,N´He said, ´What, and do you serve, apart from God, that which profits you nothing; neither hurts you?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,67,67,N´Fie upon you and that you serve apart from God! Do you not understand?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,68,68,N´They said, ´Burn him, and help your gods, if you would do aught.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,69,69,N´We said, ´O fire, be coolness and safety for Abraham!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,70,70,N´They desired to outwit him; so We made them the worse losers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,71,71,N´and We delivered him, and Lot, unto the land that We had blessed for all beings.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,72,72,N´And We gave him Isaac and Jacob in superfluity, and every one made We righteous´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,73,73,N´and appointed them to be leaders guiding by Our command, and We revealed to them the doing of good deeds, and to perform the prayer, and to pay the alms, and Us they served.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,74,74,N´And Lot -- to him We gave judgment and knowledge; and we delivered him from the city that had been doing deeds of corruption; they were an evil people, truly ungodly;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,75,75,N´and We admitted him into Our mercy; he was of the righteous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,76,76,N´And Noah -- when he called before, and We answered him, and delivered him and his people from the great distress,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,77,77,N´and We helped him against the people who cried lies to Our signs; surely they were an evil people, so We drowned them all together.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,78,78,N´And David and Solomon -- when they gave judgment concerning the tillage, when the sheep of the people strayed there, and We bore witness to their judgment;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,79,79,N´and We made Solomon to understand it, and unto each gave We judgment and knowledge. And with David We subjected the mountains to give glory, and the birds, and We were doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,80,80,N´And We taught him the fashioning of garments for you, to fortify you against your violence; then are you thankful?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,81,81,N´And to Solomon the wind, strongly blowing, that ran at his command unto the land that We had blessed; and We had knowledge of everything;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,82,82,N´and of the Satans some dived for him and did other work besides; and We were watching over them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,83,83,N´And Job -- when he called unto his Lord, ´Behold, affliction has visited me, and Thou art the most merciful of the merciful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,84,84,N´So We answered him, and removed the affliction that was upon him, and We gave his people, and the like of them with them, mercy from Us, and a Reminder to those who serve.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,85,85,N´And Ishmael, Idris, Dhul Kifl -- each was of the patient,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,86,86,N´and We admitted them into Our mercy; they were of the righteous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,87,87,N´And Dhul Nun -- when he went forth enraged and thought that We would have no power over him; then he called out in the darkness, ´There is no god but Thou. Glory be to Thee! I have done evil.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,88,88,N´So We answered him, and delivered him out of grief; even so do, We deliver the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,89,89,N´And Zachariah -- when he called unto his Lord, ´O my Lord, leave me not solitary; though Thou art the best of inheritors.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,90,90,N´So We answered him, and bestowed on him John, and We set his wife right for him; truly they vied with one another, hastening to good works, and called upon Us out of yearning and awe; and they were humble to Us.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,91,91,N´And she who guarded her virginity, so We breathed into her of Our spirit and appointed her and her son to be a sign unto all beings.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,92,92,N´´Surely this community of yours is one community, and I am your Lord; so serve Me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,93,93,N´But they split up their affair between them; all shall return to Us.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,94,94,N´And whosoever does deeds of righteousness, being a believer, no unthankfulness shall befall his endeavour; We Ourselves write it down for him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,95,95,N´There is a ban upon any city that We have destroyed; they shall not return´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,96,96,N´till, when Gog and Magog are unloosed, and they slide down out of every slope,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,97,97,N´and nigh has drawn the true promise, and behold, the eyes of the unbelievers staring: ´Alas for us! We were heedless of this; ´nay, we were evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,98,98,N´´Surely you, and that you were serving apart from God, are fuel for Gehenna; you shall go down to it.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,99,99,N´If those had been gods, they would never have gone down to it; yet every one of them shall therein abide forever;´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,100,100,N´there shall be sighing for them therein, and naught they shall hear.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,101,101,N´But as for those unto whom already the reward most fair has gone forth from Us,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,102,102,N´they shall be, kept far from it neither shall they hear any whisper of it, and they shall dwell forever in that their souls desired;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,103,103,N´the greatest terror shall not grieve them, and the angels shall receive them: ´This is your day that you were promised.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,104,104,N´On the day when We shall roll up heaven as a scroll is rolled for the writings; as We originated the first creation, so We shall bring it back again -- a promise binding on Us; so We shall do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,105,105,N´For We have written in the Psalms, after the Remembrance, ´The earth shall be the inheritance of My righteous servants.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,106,106,N´Surely in this is a Message delivered unto a people who serve.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,107,107,N´We have not sent thee, save as a mercy unto all beings.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,108,108,N´Say: ´It is revealed unto me only that your God is One God; do you then surrender?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,109,109,N´Then, if they should turn their backs, say: ´I have proclaimed to you all equally, even though I know not whether near or far is that you are promised.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,110,110,N´Surely He knows what is spoken aloud and He knows what you hide.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,111,111,N´I know not; haply it is a trial for you and an enjoyment for a time.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,21,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,21,112,112,N´He said: ´My Lord, judge Thou with truth! And our Lord is the All-merciful; His succour is ever to be sought against that you describe.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,1,1,N´O men, fear your Lord! Surely the earthquake of the Hour is a mighty thing;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,2,2,N´on the day when you behold it, every suckling woman shall neglect the child she has suckled, and every pregnant woman shall deposit her burden, and thou shalt see mankind drunk, yet they are not drunk, but God´s chastisement is terrible.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,3,3,N´And among men there is such a one that disputes concerning God without knowledge and follows every rebel Satan,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,4,4,N´against whom it is written down that whosoever takes him for a friend, him he leads astray, and he guides him to the chastisement of the burning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,5,5,N´O men, if you are in doubt as to the Uprising, surely We created you of dust then of a sperm-drop, then of a blood clot, then of a lump of flesh, formed and unformed that We may make clear to you. And We establish in the wombs what We will, till a stated term, then We deliver you as infants, then that you may come of age; and some of you die, and some of you are kept back unto the vilest state of life, that after knowing somewhat, they may know nothing. And thou beholdest the earth blackened, then, when We send down water upon it, it quivers, and swells, and puts forth herbs of every joyous kind.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,6,6,N´That is because God -- He is the Truth, and brings the dead to life, and is powerful over everything,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,7,7,N´and because the Hour is coming, no doubt of it, and God shall raise up whosoever is within the tombs.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,8,8,N´And among men there is such a one that disputes concerning God without knowledge or guidance, or an illuminating Book,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,9,9,N´turning his side to lead astray from God´s way; for him is degradation in this world; and on the Resurrection Day We shall let him taste the chastisement of the burning:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,10,10,N´´That is for what thy hands have forwarded and for that God is never unjust unto His servants.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,11,11,N´And among men there is such a one as serves God upon the very edge -- if good befalls him he is at rest in it, but if a trial befalls him he turns completely over; he loses this world and the world to come; that is indeed the manifest loss.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,12,12,N´He calls, apart from God, upon that which hurts him not, and which neither profits him anything; that is indeed the far error.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,13,13,N´He calls upon him who is likelier to hurt him, rather than to profit him -- an evil protector indeed, he, an evil friend!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,14,14,N´God shall surely admit those who believe and do righteous deeds into gardens underneath which rivers flow; surely God does that He desires.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,15,15,N´Whosoever thinks God will not help him in the present world and the world to come; let him stretch up a rope to heaven, then let him sever it, and behold whether his guile does away with what enrages him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,16,16,N´Even so We have sent it down as signs, clear signs, and for that God guides whom He desires.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,17,17,N´Surely they that believe, and those of Jewry, the Sabaeans, the Christians, the Magians and the idolaters -- God shall distinguish between them on the Day of Resurrection; assuredly God is witness over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,18,18,N´Hast thou not seen how to God bow all who are in the and all who are in the earth, heavens the sun and the moon, the stars and the mountains, the trees and the beasts, and many of mankind? And many merit the chastisement; and whom God abases, there is none to honour him. God does whatsoever He will.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,19,19,N´These are two disputants who have disputed concerning their Lord. As for the unbelievers, for them garments of fire shall, be cut, and there shall be poured over their heads boiling water´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,20,20,N´whereby whatsoever is in their bellies and their skins shall be melted;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,21,21,N´for them await hooked iron rods;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,22,22,N´as often as they desire in their anguish to come forth from it, they shall be restored into it, and: ´Taste the chastisement of the burning!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,23,23,N´God shall surely admit those who believe and do righteous deeds into gardens underneath which rivers flow; therein they shall be adorned with bracelets of gold and with pearls, and their apparel there shall be of silk;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,24,24,N´and they; shall be guided unto goodly speech, and they shall be guided unto the path of the All-laudable.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,25,25,N´Those who disbelieve, and bar from God´s way and the Holy Mosque that We have appointed equal unto men, alike him who cleaves to it and the tent-dweller, and whosoever purposes to violate it wrongly, We shall let him taste a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,26,26,N´And when We settled for Abraham the place of the House: ´Thou shall not associate with Me anything. And do thou purify My House for those that shall go about it and those that stand, for those that bow and prostrate themselves;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,27,27,N´and proclaim among men the Pilgrimage, and they shall come unto thee on foot and upon every lean beast, they shall come from every deep ravine´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,28,28,N´that they may witness things profitable to them and mention God´s Name on days well-known over such beasts of the flocks as He has provided them: \"So eat thereof, and feed the wretched poor.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,29,29,N´Let them then finish with their self-neglect and let them fulfil their vows, and go about the Ancient House.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,30,30,N´All that; and whosoever venerates the sacred things of God, it shall be better for him with his Lord. And permitted to you are the flocks, except that which is recited to you. And eschew the abomination of idols, and eschew the speaking of falsehood,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,31,31,N´being men pure of faith unto God, not associating with Him anything; for whosoever associates with God anything, it is as though he has fallen from heaven and the birds snatch him away, or the wind sweeps him headlong into a place far away.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,32,32,N´All that; and whosoever venerates God´s waymarks, that is of the godliness of the hearts.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,33,33,N´There are things therein profitable to you unto a stated term; thereafter their lawful place of sacrifice is by the Ancient House.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,34,34,N´We have appointed for every nation a holy rite, that they may mention God´s Name over such beasts of the flocks as He has provided them. Your God is One God, so to Him surrender. And give thou good tidings unto the humble´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,35,35,N´who, when God is mentioned, their hearts quake, and such as endure patiently whatever visits them, and who perform the prayer, and expend of what We have provided them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,36,36,N´And the beasts of sacrifice -- We have appointed them for you as among God´s waymarks; therein is good for you. So mention God´s Name over them, standing in ranks then, when their flanks collapse, eat of them and feed the beggar and the suppliant. So We have subjected them to you; haply you will be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,37,37,N´The flesh of them shall not reach God, neither their blood, but godliness from you shall reach Him. So He has subjected them to you, that you may magnify God for that He has guided you. And give thou good tidings unto the good-doers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,38,38,N´Assuredly God will defend those who believe; surely God loves not any ungrateful traitor.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,39,39,N´Leave is given to those who fight because they were wronged -- surely God is able to help them --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,40,40,N´who were expelled from their habitations without right, except that they say ´Our Lord is God.´ Had God not driven back the people, some by the means of others, there had been destroyed cloisters and churches, oratories and mosques, wherein God´s Name is much mentioned. Assuredly God will help him who helps Him -- surely God is All-strong, All-mighty´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,41,41,N´who, if We establish them in the land, perform the prayer, and pay the alms, and bid to honour, and forbid dishonour; and unto God belongs the issue of all affairs.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,42,42,N´If they cry lies to thee, so too before them the people of Noah cried lies, and Ad and Thamood,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,43,43,N´and the people of Abraham, the people of Lot,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,44,44,N´and the men of Midian; to Moses also they cried lies. And I respited the unbelievers, then I seized them; and how was My horror!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,45,45,N´How many a city We have destroyed in its evildoing, and now it is fallen down upon its turrets! How many a ruined well, a tall palace!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,46,46,N´What, have they not journeyed in the land so that they have hearts to understand with or ears to hear with? It is not the eyes that are blind, but blind are the hearts within the breasts.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,47,47,N´And they demand of thee to hasten the chastisement! God will not fail His promise; and surely a day with thy Lord is as a thousand years of your counting.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,48,48,N´How many a city I have respited in its evildoing; then I seized it, and to Me was the homecoming.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,49,49,N´Say: ´O men, I am only for you a plain warner.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,50,50,N´Those who believe, and do deeds of righteousness -- theirs shall be forgiveness and generous provision.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,51,51,N´And those who strive against Our signs to void them -- they shall be the inhabitants of Hell.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,52,52,N´We sent not ever any Messenger or Prophet before thee, but that Satan cast into his fancy, when he was fancying; but God annuls what Satan casts, then God confirms His signs -- surely God is All-knowing, All-wise --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,53,53,N´that He may make what Satan casts a trial for those in whose hearts is sickness, and those whose hearts are hard; and surely the evildoers are in wide schism;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,54,54,N´and that they who have been given knowledge may know that it is the truth from thy Lord and believe in it, and so their hearts be humble unto Him; and assuredly God ever guides those who believe to a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,55,55,N´And the unbelievers will not cease to be in doubt of it, until the Hour comes on them suddenly, or there shall come upon them the chastisement of a barren day.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,56,56,N´The Kingdom upon that day shall belong to God, and He shall judge between them. As for those who believe, and do deeds of righteousness, they shall be in Gardens of Bliss.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,57,57,N´But as for the unbelievers, who cried lies to Our signs; for them awaits a humbling chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,58,58,N´And those who emigrated in God´s way and were slain, or died, God shall provide them with a fair provision; and surely God is the best of providers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,59,59,N´He shall admit them by a gate that is well-pleasing to them; and surely God is All-knowing, All-clement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,60,60,N´All that; and whosoever chastises after the manner that he was chastised and then again is oppressed, assuredly God will help him; surely God is All-pardoning, All-forgiving.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,61,61,N´That is because God makes the night to enter into the day and makes the day to enter into the night; and that God is All-hearing, All-seeing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,62,62,N´That is because God -- He is the Truth, and that they call upon apart from Him -- that is the false; and for that God is the All-high, the All-great.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,63,63,N´Hast thou not seen how that God has sent down out of heaven water, and in the morning the earth becomes green? God is All-subtle, All-aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,64,64,N´To Him belongs all that is in the heavens and in the earth; surely God -- He is the All-sufficient, the All-laudable.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,65,65,N´Hast thou not seen how that God has subjected to you all that is in the earth and the ships to run upon the sea at His commandment, and He holds back heaven lest it should fall upon the earth, save by His leave? Surely God is All-gentle to men, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,66,66,N´It is He who gave you life, then He shall make you dead, then He shall give you life. Surely man is ungrateful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,67,67,N´We have appointed for every nation a holy rite that they shall perform. Let them not therefore wrangle with thee upon the matter, and do thou summon unto thy Lord; surely thou art upon a straight guidance.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,68,68,N´And if they should dispute with thee, do thou say, ´God knows very well what you are doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,69,69,N´God shall judge between you on the Day of Resurrection touching that whereon you were at variance.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,70,70,N´Didst thou not know that God knows all that is in heaven and earth? Surely that is in a Book; surely that for God is an easy matter.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,71,71,N´They serve, apart from God, that whereon He has sent down never authority and that whereof they have no knowledge; and for the evildoers there shall be no helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,72,72,N´And when Our signs are recited to them, clear signs, thou recognisest in the faces of the unbelievers denial; wellnigh they rush upon those who recite to them Our signs. Say: ´Shall I tell you of something worse than that? The Fire -- God has promised it to the unbelievers -- an evil homecoming!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,73,73,N´O men, a similitude is struck; so give you ear to it. Surely those upon whom you call, apart from God, shall never create a fly, though they banded together to do it; and if a fly should rob them of aught, they would never rescue it from him. Feeble indeed alike are the seeker and the sought!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,74,74,N´They measure not God with His true measure; surely God is All-strong, All-mighty.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,75,75,N´God chooses of the angels Messengers and of mankind; surely God is All-hearing, All-seeing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,76,76,N´He knows whatsoever is before them and behind them, and unto God all matters are returned.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,77,77,N´O men, bow you down and prostrate yourselves, and serve your Lord, and do good; haply so you shall prosper;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,22,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,22,78,78,N´and struggle for God as is His due, for He has chosen you, and has laid on you no impediment in your religion, being the creed of your father Abraham; He named you Muslims aforetime and in this, that the Messenger might be a witness against you, and that you might be witnesses against mankind. So perform the prayer, and pay the alms, and hold you fast to God; He is your Protector -- an excellent Protector, an excellent Helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,1,1,N´Prosperous are the believers´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,2,2,N´who in their prayers are humble´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,3,3,N´and from idle talk turn away´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,4,4,N´and at almsgiving are active´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,5,5,N´and guard their private parts´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,6,6,N´save from their wives and what their right hands own then being not blameworthy´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,7,7,N´(but whosoever seeks after more than that, those are the transgressors)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,8,8,N´and who preserve their trusts and their covenant´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,9,9,N´and who observe their prayers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,10,10,N´Those are the inheritors´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,11,11,N´who shall inherit Paradise therein dwelling forever.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,12,12,N´We created man of an extraction of clay,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,13,13,N´then We set him, a drop, in a receptacle secure,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,14,14,N´then We created of the drop a clot then We created of the clot a tissue then We created of the tissue bones then We garmented the bones in flesh; thereafter We produced him as another creature. So blessed be God, the fairest of creators!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,15,15,N´Then after that you shall surely die,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,16,16,N´then on the Day of Resurrection you shall surely be raised up.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,17,17,N´And We created above you seven ways, and We were not heedless of creation.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,18,18,N´And We sent down out of heaven water in measure and lodged it in the earth; and We are able to take it away.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,19,19,N´Then We produced for you therewith gardens of palms and vines wherein are many fruits for you, and of them you eat,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,20,20,N´and a tree issuing from the Mount of Sinai that bears oil and seasoning for all to eat.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,21,21,N´And surely in the cattle there is a lesson for you; We give you to drink of what is in their bellies, and many uses there are in them for you, and of them you eat;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,22,22,N´and upon them, and on the ships, you are borne.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,23,23,N´And We sent Noah to his people; and he, said, ´O my people, serve God! You have no god other than He. Will you not be godfearing?\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,24,24,N´Said the Council of the unbelievers of his people, ´This is naught but a mortal like yourselves, who desires to gain superiority over you. And if God willed, He would have sent down angels. We never heard of this among, our fathers, the ancients.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,25,25,N´He is naught but a man bedevilled; so wait on him for a time.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,26,26,N´He said, ´O my Lord, help me, for that they cry me lies.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,27,27,N´Then We said to him, ´Make thou the Ark under Our eyes and as We reveal, and then, when Our command comes and the Oven boils, insert in it two of every kind and thy, family -- except for him against whom the word already has been spoken; and address Me not concerning those who have done evil; they shall be drowned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,28,28,N´Then, when thou art seated in the Ark and those with thee, say, \"Praise belongs to God, who has delivered us from the people of the evildoers.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,29,29,N´And say, \"´O my Lord, do Thou harbour me in a blessed harbour, for Thou art the best of harbourers.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,30,30,N´Surely in that are signs, and surely We put to the test.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,31,31,N´Thereafter, after them, We produced another generation,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,32,32,N´and We sent amongst them a Messenger of themselves, saying, ´Serve God! You have no god other than He. Will you not be godfearing?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,33,33,N´Said the Council of the unbelievers of his people, who cried lies to the encounter of the world to come, and to whom We had given ease in the present´ life, ´This is naught but a mortal like yourselves, who eats of what you eat and drinks of what you drink.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,34,34,N´If you obey a mortal like yourselves, then you will be losers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,35,35,N´What, does he promise you that when you are dead, and become dust and bones, you shall be brought forth?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,36,36,N´Away, away with that you are promised!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,37,37,N´There is nothing but our present life; we die, and we live, and we shall not be raised up.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,38,38,N´He is naught but a man who has forged against God a lie, and we will not believe him.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,39,39,N´He said, ´O my Lord, help me, for that they cry me lies.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,40,40,N´He said, ´In a little they will be remorseful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,41,41,N´And the Cry seized them justly, and We made them as scum; so away with the people of the evildoers!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,42,42,N´Thereafter, after them, We produced other generations;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,43,43,N´no nation outstrips its term, nor do they put it back.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,44,44,N´Then sent We Our Messengers successively; whenever its Messenger came to a nation they cried him lies, so We caused some of them to follow others, and We made them as but tales; so away with a people who do not believe!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,45,45,N´Then We sent Moses and his brother Aaron with Our signs and a manifest authority´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,46,46,N´unto Pharaoh and his Council; but they waxed proud, and they were a lofty people,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,47,47,N´and they said, ´What, shall we believe two mortals like ourselves, whose people are our servants?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,48,48,N´So they cried them lies, and they were among the destroyed.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,49,49,N´And We gave Moses the Book, that haply they would be guided;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,50,50,N´and We made Mary´s son, and his mother, to be a sign, and gave them refuge upon a height, where was a hollow and a spring:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,51,51,N´´O Messengers, eat of the good things and do righteousness; surely I know the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,52,52,N´Surely this community of yours is one community, and I am your Lord; so fear Me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,53,53,N´But they split in their affair between them into sects, each party rejoicing in what is with them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,54,54,N´So leave thou them in their perplexity for a time.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,55,55,N´What, do they think that We succour them with of wealth and children,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,56,56,N´We vie in good works for them? Nay, but they are not aware.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,57,57,N´Surely those who tremble in fear of their Lord´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,58,58,N´and those who believe in the signs of their Lord´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,59,59,N´and those who associate naught with their Lord´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,60,60,N´and those who give what they give, their hearts quaking that they are returning to their Lord --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,61,61,N´those vie in good works, outracing to them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,62,62,N´We charge not any soul save to its capacity, and with Us is a Book speaking truth, and they shall not be wronged.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,63,63,N´Nay, but their hearts are in perplexity as to this, and they have deeds besides that that they are doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,64,64,N´Till, when We seize with the chastisement the ones of them that live at ease, behold, they groan.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,65,65,N´´Groan not today; surely you shall not be helped from Us.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,66,66,N´My signs were recited to you, but upon your heels you withdrew,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,67,67,N´waxing proud against it, talking foolish talk by night.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,68,68,N´Have they not pondered the saying, or came there upon them that which came not upon their fathers, the ancients?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,69,69,N´Or did they not recognise their Messenger and so denied him?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,70,70,N´Or do they say, ´He is bedevilled´? Nay, he has brought them the truth, but most of them are averse from the truth.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,71,71,N´Had the truth followed their caprices, the heavens and the earth and whosoever in them is had surely corrupted. Nay, We brought them their Remembrance, but from their Remembrance they turned.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,72,72,N´Or dost thou ask them for tribute? Yet the tribute of thy Lord is better, and He is the best of providers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,73,73,N´Assuredly thou art calling them to a straight path;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,74,74,N´and surely they that believe not in the world to come are deviating from the path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,75,75,N´Did We have mercy on them, and remove the affliction that is upon them, they would persist in their insolence wandering blindly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,76,76,N´We already seized them with the chastisement, yet they abased not themselves to their Lord nor were they humble;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,77,77,N´until, when We open against them a door of terrible chastisement, lo, they are sore confounded at it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,78,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,78,78,N´It is He who produced for you hearing, and eyes, and hearts; little thanks you show.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,79,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,79,79,N´It is He who scattered you in the earth, and to Him you shall be mustered.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,80,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,80,80,N´It is He who gives life, and makes to die, and to Him belongs the alternation of night and day; what, will you not understand?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,81,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,81,81,N´Nay, but they said the like of what the ancients said.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,82,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,82,82,N´They said, ´What, when we are dead and become dust and bones, shall we be indeed raised up?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,83,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,83,83,N´We and our fathers have been promised this before; this is naught but the fairy-tales of the ancients.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,84,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,84,84,N´Say: ´Whose is the earth, and whoso is in it, if you have knowledge?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,85,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,85,85,N´They will say, God´s.´ Say: ´Will you not then remember?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,86,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,86,86,N´Say: ´Who is the Lord of the seven heavens and the Lord of the mighty Throne?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,87,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,87,87,N´They will say, ´God´s.´ Say: ´Will you not then be godfearing?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,88,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,88,88,N´Say: ´In whose hand is the dominion of everything, protecting and Himself unprotected, if you have knowledge?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,89,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,89,89,N´They will say, ´God´s.´ Say: ´How then are you bewitched?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,90,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,90,90,N´Nay, but We brought them the truth and they are truly liars.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,91,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,91,91,N´God has not taken to Himself any son, nor is there any god with Him; for then each god would have taken off that he created and some of them would have risen up over others; glory to be God, beyond that they describe,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,92,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,92,92,N´who has knowledge of the Unseen and the Visible; high exalted be He, above that they associate!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,93,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,93,93,N´Say: ´O my Lord, if Thou shouldst show me that they are promised,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,94,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,94,94,N´O my Lord, put me not among the people of the evildoers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,95,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,95,95,N´Assuredly, We are able to show thee that We promise them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,96,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,96,96,N´Repel thou the evil with that which is fairer. We Ourselves know very well that they describe.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,97,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,97,97,N´And say: ´O my Lord, I take refuge in Thee from the evil suggestions of the Satans,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,98,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,98,98,N´and I take refuge in Thee, O my Lord, lest they attend me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,99,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,99,99,N´Till, when death comes to one of them, he says, ´My Lord, return me;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,100,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,100,100,N´haply I shall do righteousness in that I forsook.´ Nay, it is but a word he speaks; and there; behind them, is a barrier until the day that they shall be raised up.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,101,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,101,101,N´For when the Trumpet is blown, that day there shall be no kinship any more between them, neither will they question one another.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,102,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,102,102,N´Then he whose scales are heavy -- they are the prosperers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,103,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,103,103,N´and he whose scales are light -- they have lost their souls in Gehenna dwelling forever,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,104,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,104,104,N´the Fire smiting their faces the while they glower there.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,105,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,105,105,N´´What, were My signs not recited to you, and you cried them lies?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,106,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,106,106,N´They shall say, ´Our Lord, our adversity prevailed over us; we were an erring people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,107,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,107,107,N´Our Lord, bring us forth out of it! Then, if we revert, we shall be evildoers indeed.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,108,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,108,108,N´´Slink you into it,´ He shall say, ´and do not speak to Me.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,109,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,109,109,N´There is a party of My servants who said, \"Our Lord, we believe; therefore forgive us, and have mercy on us, for Thou art the best of the merciful.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,110,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,110,110,N´But you took them for a laughing-stock, till they made you forget My remembrance, mocking at them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,111,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,111,111,N´Now today I have recompensed them for their patient endurance; they are the triumphant.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,112,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,112,112,N´He shall say, ´How long have you tarried in the earth, by number of years?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,113,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,113,113,N´They shall say, ´We have tarried a day, or part of a day; ask the numberers!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,114,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,114,114,N´He shall say, ´You have tarried but a little, did you know.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,115,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,115,115,N´What, did you think that We created you only for sport, and that you would not be returned to Us?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,116,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,116,116,N´Then high exalted be God, the King, the True! There is no god but He? the Lord of the noble Throne.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,117,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,117,117,N´And whosoever calls upon another god with God, whereof he has no proof, his reckoning is with his Lord; surely the unbelievers shall not prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,23,118,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,23,118,118,N´And say: ´My Lord, forgive and have mercy, for Thou art the best of the merciful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,1,1,N´A sura that We have sent down and appointed; and We have sent down in it signs, clear signs, that haply you will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,2,2,N´The fornicatress and the fornicator -- scourge each one of them a hundred stripes, and in the matter of God´s religion let no tenderness for them seize you if you believe in God and the Last Day; and let a party of the believers witness their chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,3,3,N´The fornicator shall marry none but a fornicatress or an idolatress, and the fornicatress -- none shall marry her but a fornicator or an idolator; that is forbidden to the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,4,4,N´And those who cast it up on women in wedlock, and then bring not four witnesses, scourge them with eighty stripes, and do not accept any testimony of theirs ever; those -- they are the ungodly,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,5,5,N´save such as repent thereafter and make amends; surely God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,6,6,N´And those who cast it up on their wives having no witnesses except themselves, the testimony of one of them shall be to testify by God four times that he is of the truthful,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,7,7,N´and a fifth time, that the curse of God shall be upon him, if he should be of the liars.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,8,8,N´It shall avert from her the chastisement if she testify by God four times that he is of the liars,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,9,9,N´and a fifth time, that the wrath of God shall be upon her, if he should be of the truthful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,10,10,N´But for God´s bounty to you and His mercy and that God turns, and is All-wise --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,11,11,N´Those who came with the slander are a band of you; do not reckon it evil for you; rather it is good for you. Every man of them shall have the sin that he has earned charged to him; and whosoever of them took upon himself the greater part of it, him there awaits a mighty chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,12,12,N´Why, when you heard it, did the believing men and women not of their own account think good thoughts, and say, ´This is a manifest calumny´?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,13,13,N´Why did they not bring four witnesses against it? But since they did not bring the witnesses, in God´s sight they are the liars.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,14,14,N´But for God´s bounty to you and His mercy in the present world and the world to come there would have visited you for your mutterings a mighty chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,15,15,N´When you received it on your tongues, and were speaking with your mouths that whereof you had no knowledge, and reckoned it a light thing, and with God it was a mighty thing --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,16,16,N´And why, when you heard it, did you not say, ´It is not for us to speak about this; glory be to Thee! This is a mighty calumny´?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,17,17,N´God admonishes you, that you shall never repeat the like of it again; if you are believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,18,18,N´God makes clear to you the signs; and God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,19,19,N´Those who love that indecency should be spread abroad concerning them that believe -- there awaits them a painful chastisement in the present world and the world to come; and God knows, and you know not.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,20,20,N´But for God´s bounty to you and His mercy and that God is All-gentle, All-compassionate --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,21,21,N´O believers, follow not the steps of Satan; for whosoever follows the steps of Satan, assuredly he bids to indecency and dishonour. But for God´s bounty to you and His mercy not one of you would have been pure ever; but God purifies whom He will; and God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,22,22,N´Let not those of you who possess bounty and plenty swear off giving kinsmen and the poor and those who emigrate in the way of God; but let them pardon and forgive. Do you not wish that God should forgive you? God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,23,23,N´Surely those who cast it up on women in wedlock that are heedless but believing shall be accursed in the present world and the world to come; and there awaits them a mighty chastisement´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,24,24,N´on the day when their tongues, their hands and their feet shall testify against them touching that they were doing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,25,25,N´Upon that day God will pay them in full their just due, and they shall know that God is the manifest Truth.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,26,26,N´Corrupt women for corrupt men, and corrupt men for corrupt women; good women for good men, and good men for good women -- these are declared quit of what they say; theirs shall be forgiveness and generous provision.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,27,27,N´O believers, do not enter houses other than your houses until you first ask leave and, salute the people thereof; that is better for you; haply you will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,28,28,N´And if you find not anyone therein, enter it not until leave is given to you. And if you are told, ´Return,´ return; that is purer for you; and God knows the things you do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,29,29,N´There is no fault in you that you enter houses uninhabited wherein enjoyment is for you. God knows what you reveal and what you hide.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,30,30,N´Say to the believers, that they cast down their eyes and guard their private parts; that is purer for them. God is aware of the things they work.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,31,31,N´And say to the believing women, that they cast down their eyes´ and guard their private parts, and reveal not their adornment save such as is outward; and let them cast their veils over their bosoms, and not reveal their adornment save to their husbands, or their fathers, or their husbands´ fathers, or their sons, or their husbands´ sons, or their brothers, or their brothers´ sons, or their sisters´ sons, or their women, or what their right hands own, or such men as attend them, not having sexual desire, or children who have not yet attained knowledge of women´s private parts; nor let them stamp their feet, so that their hidden ornament may be known. And turn all together to God, O you believers; haply so you will prosper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,32,32,N´Marry the spouseless among you, and your slaves and handmaidens that are righteous; if they are poor, God will enrich them of His bounty; God is All-embracing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,33,33,N´And let those who find not the means to marry be abstinent till God enriches them of His bounty. Those your right hands own who seek emancipation, contract with them accordingly, if you know some good in them; and give them of the wealth of God that He has given you. And constrain not your slave-girls to prostitution, if they desire to live in chastity, that you may seek the chance goods of the present life. Whosoever constrains them, surely God, after their being constrained, is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,34,34,N´Now We have sent down to you signs making all clear, and an example of those who passed away before you, and an admonition for the godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,35,35,N´God is the Light of the heavens and the earth; the likeness of His Light is as a niche wherein is a lamp (the lamp in a glass, the glass as it were a glittering star) kindled from a Blessed Tree, an olive that is neither of the East nor of the West whose oil wellnigh would shine, even if no fire touched it; Light upon Light; (God guides to His Light whom He will.) (And God strikes similitudes for men, and God has knowledge of everything.)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,36,36,N´in temples God has allowed to be raised up, and His Name to be commemorated therein; therein glorifying Him, in the mornings and the evenings,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,37,37,N´are men whom neither commerce nor trafficking diverts from the remembrance of God and to perform the prayer, and to pay the alms, fearing a day when hearts and eyes shall be turned about,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,38,38,N´that God may recompense them for their fairest works and give them increase of His bounty; and God provides whomsoever He will, without reckoning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,39,39,N´And as for the unbelievers, their works are as a mirage in a spacious plain which the man athirst supposes to be water, till, when he comes to it, he finds it is nothing; there indeed he finds God, and He pays him his account in full; (and God is swift at the reckoning.)´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,40,40,N´or they are as shadows upon a sea obscure covered by a billow above which is a billow above which are clouds, shadows piled one upon another; when he puts forth his hand, wellnigh he cannot see it. And to whomsoever God assigns no light, no light has he.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,41,41,N´Hast thou not seen how that whatsoever is in the heavens and in the earth extols God, and the birds spreading their wings? Each -- He knows its prayer and its extolling; and God knows the things they do.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,42,42,N´To God belongs the Kingdom of the heavens and the earth, and to Him is the homecoming.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,43,43,N´´hast thou not seen how God drives the clouds, then composes them, then converts them into a mass, then thou seest the rain issuing out of the midst of them? And He sends down out of heaven mountains, wherein is hail, so that He smites whom He will with it, and turns it aside from whom He will; wellnigh the gleam of His lightning snatches away the sight.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,44,44,N´God turns about the day and the night; surely in that is a lesson for those who have eyes.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,45,45,N´God has created every beast of water, and some of them go upon their bellies, and some of them go upon two feet, and some of them go upon four; God creates whatever He will; God is powerful over everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,46,46,N´Now We have sent down signs making all clear; God guides whomsoever He will to a straight path.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,47,47,N´They say, ´We believe in God and the Messenger, and we obey.´ Then after that a party of them turn away; those -- they are not believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,48,48,N´When they are called to God and His Messenger that he may judge between them, lo, a party of them are swerving aside;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,49,49,N´but if they are in the right, they will come to him submissively.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,50,50,N´What, is there sickness in their hearts, or are they in doubt, or do they fear that God may be unjust towards them and His Messenger? Nay, but those -- they are the evildoers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,51,51,N´All that the believers say, when they are called to God and His Messenger, that he may judge between them, is that they say, ´We hear, and we obey´; those -- they are the prosperers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,52,52,N´Whoso obeys God and His Messenger, and fears God and has awe of Him, those -- they are the triumphant.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,53,53,N´They have sworn by God the most earnest oaths, if thou commandest them they will go forth. Say: ´Do not swear; honourable obedience is sufficient. Surely God is aware of the things you do.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,54,54,N´Say: ´Obey God, and obey the Messenger; then, if you turn away, only upon him rests what is laid on him, and upon you rests what is laid on you. If you obey him, you will be guided. It is only for the Messenger to deliver the manifest Message.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,55,55,N´God has promised those of you who believe and do righteous deeds that He will surely make you successors in the land, even as He made those who were before them successors, and that He will surely establish their religion for them that He has approved. for them, and will give them in exchange, after their fear, security: ´They shall serve Me, not associating with Me anything.´ Whoso disbelieves after that, those - they are the ungodly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,56,56,N´Perform the prayer, and pay the alms, and obey the Messenger -- haply so you will find mercy.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,57,57,N´Think not the unbelievers able to frustrate God in the earth; their refuge is the Fire -- an evil homecoming.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,58,58,N´O believers, let those your right hands own and those of you who have not reached puberty ask leave of you three times -- before the prayer of dawn, and when you put off your garments at the noon, and after the evening prayer -- three times of nakedness for you. There is no fault in you or them, apart from these, that you go about one to the other. So God makes clear to you the signs; and God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,59,59,N´When your children reach puberty, let them ask leave, as those before them asked leave. So God makes clear to you His signs; and God is All-knowing, All-wise.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,60,60,N´Such women as are past child- bearing and have no hope of marriage -- there is no fault in them that they put off their clothes, so be it that they flaunt no ornament; but to abstain is better for them; and God is All-hearing, All-knowing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,61,61,N´There is no fault in the blind, and there is no fault in the lame, and there is no fault in the sick, neither in yourselves, that you eat of your houses, or your fathers´ houses, or your mothers´ houses, or your brothers´ houses, or your sisters´ houses, or the houses of your uncles or your aunts paternal, or the houses of your uncles or your aunts maternal, or that whereof you own the keys, or of your friend; there is no fault in you that you eat all together, or in groups separately. But when you enter houses, greet one another with a greeting from God, blessed and good. So God makes clear to you the signs; haply you will understand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,62,62,N´Those only are believers, who believe in God and His Messenger and who, when they are with him upon a common matter, go not away until they ask his leave. Surely those who ask thy leave -- those are they that believe in God and His Messenger; so, when they ask thy leave for some affair of their own, give leave to whom thou wilt of them, and ask God´s forgiveness for them; surely God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,63,63,N´Make not the calling of the Messenger among yourselves like your calling one of another. God knows those of you who slip away surreptitiously; so let those who go against His command beware, lest a trial befall them, or there befall them a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,24,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,24,64,64,N´Why, surely to God belongs whatsoever is in the heavens and the earth; He ever knows what state you are upon; and the day when they shall be returned to Him, then He will tell them of what they did; and God knows everything.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,1,1,N´Blessed be He who has sent down the Salvation upon His servant, that he may be a warner to all beings;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,2,2,N´to whom belongs the Kingdom of the heavens and the earth; and He has not taken to Him a son, and He has no associate in the Kingdom; and He created every thing, then He ordained it very exactly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,3,3,N´Yet they have taken to them gods, apart from Him, that create nothing and themselves are created, and have no power to hurt or profit themselves, no power of death or life or raising up.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,4,4,N´The unbelievers say, ´This is naught but a calumny he has forged, and other folk have helped him to it.´ So they have committed wrong and falsehood.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,5,5,N´They say, ´Fairy-tales of the ancients that he has had written down, so that they are recited to him at the dawn and in the evening.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,6,6,N´Say: ´He sent it down, who knows the secret in the heavens and earth; He is All-forgiving, All-compassionate.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,7,7,N´They also say, ´What ails this Messenger that he eats food, and goes in the markets? Why has an angel not been sent down to him, to be a warner with him?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,8,8,N´Or why is not a treasure thrown to him, or why has he not a Garden to eat of?´ The evildoers say, ´You are only following a man bewitched!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,9,9,N´Behold, how they strike similitudes for thee, and go astray, and are unable to find a way!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,10,10,N´Blessed be He who, if He will, shall assign to them better than that -- gardens underneath which rivers flow, and he shall assign to thee palaces.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,11,11,N´Nay, but they cry lies to the Hour; and We have prepared for him who cries lies to the Hour a Blaze.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,12,12,N´When it sees them from a far place, they shall hear its bubbling and sighing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,13,13,N´And when they are cast, coupled in fetters, into a narrow place of that Fire, they will call out there for destruction.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,14,14,N´´Call not out today for one destruction, but call for many!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,15,15,N´Say: ´Is that better, or the Garden of Eternity, that is promised to the godfearing, and is their recompense and homecoming?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,16,16,N´Therein they shall have what they will dwelling forever; it is a promise binding upon thy Lord, and of Him to be required.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,17,17,N´Upon the day when He shall muster them and that they serve, apart from God, and He shall say, ´Was it you that led these My servants astray, or did they themselves err from the way?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,18,18,N´They shall ´Glory be to Thee! It did not behove us to take unto say, ourselves protectors apart from Thee; but Thou gavest them and their fathers enjoyment of days, until they forgot´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,19,19,N´the Remembrance, and were a people corrupt.´ So they cried lies touching the things you say, and you can neither turn it aside, nor find any help. Whosoever of you does evil, We shall let him taste a great chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,20,20,N´And We sent not before thee any Envoys, but that they ate food, and went in the markets; and We appointed some of you to be a trial for others: ´Will you endure?´ Thy Lord is ever All-seeing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,21,21,N´Say those who look not to encounter Us, ´Why have the angels not been sent down on us, or why see we not our Lord?´ Waxed proud they have within them, and become greatly disdainful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,22,22,N´Upon the day that they see the angels, no good tidings that day for the sinners; they shall say, ´A ban forbidden!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,23,23,N´We shall advance upon what work they have done, and make it a scattered dust.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,24,24,N´The inhabitants of Paradise that day, better shall be their lodging, fairer their resting-place.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,25,25,N´Upon the day that heaven is split asunder with the clouds and the angels are sent down in majesty,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,26,26,N´the Kingdom that day, the true Kingdom, shall belong to the All-merciful and it shall be a day harsh for the unbelievers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,27,27,N´Upon the day the evildoer shall bite his hands, saying, ´Would that I had taken a way along with the Messenger!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,28,28,N´Alas, would that I had not taken So-and-so for a friend!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,29,29,N´He indeed led me astray from the Remembrance, after it had come to me; Satan is ever a forsaker of men.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,30,30,N´The Messenger says, ´O my Lord, behold, my people have taken this Koran as a thing to be shunned.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,31,31,N´Even so We have appointed to every Prophet an enemy among the sinners; but thy Lord suffices as a guide and as a helper.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,32,32,N´The unbelievers say, ´Why has the Koran not been sent down upon him all at once?´ Even so, that We may strengthen thy heart thereby, and We have chanted it very distinctly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,33,33,N´They bring not to thee any similitude but that We bring thee the truth, and better in exposition.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,34,34,N´Those who shall be mustered to Gehenna upon their faces -- they shall be worse in place, and gone further astray from the way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,35,35,N´We gave Moses the Book, and appointed with him his brother Aaron as minister´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,36,36,N´and We said, ´Go to the people who have cried lies to Our signs´; then We destroyed them utterly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,37,37,N´And the people of Noah, when they cried lies to the Messengers, We drowned them, and made them to be a sign to mankind; and We have prepared for the evildoers a painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,38,38,N´And Ad, and Thamood, and the men of Er-Rass, and between that generations a many,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,39,39,N´for each We struck similitudes, and each We ruined utterly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,40,40,N´Surely they have come by the city that was rained on by an evil rain; what, have they not seen it? Nay, but they look for no upraising.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,41,41,N´And when they see thee, they take thee in mockery only: ´What, is this he whom God sent forth as a Messenger?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,42,42,N´Wellnigh he had led us astray from our gods, but that we kept steadfast to them.´ Assuredly they shall know, when they see the chastisement, who is further astray from the way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,43,43,N´Hast thou seen him who has taken his caprice to be his god? Wilt thou be a guardian over them?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,44,44,N´Or deemest thou that most of them hear or understand? They are but as the cattle; nay, they are further astray from the way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,45,45,N´Hast thou not regarded thy Lord, how He has stretched out the shadow? Had He willed, He would have made it still. Then We appointed the sun, to be a guide to it;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,46,46,N´thereafter We seize it to Ourselves, drawing it gently.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,47,47,N´It is He who appointed the night for you to be a garment and sleep for a rest, and day He appointed for a rising.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,48,48,N´And it is He who has loosed the winds, bearing good tidings before His mercy; and We sent down from heaven pure water´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,49,49,N´so that We might revive a dead land, and give to drink of it, of that We created, cattle and men a many.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,50,50,N´We have indeed turned it about amongst them, so that they may remember; yet most men refuse all but unbelief.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,51,51,N´If We had willed, We would have raised up in every city a warner.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,52,52,N´So obey not the unbelievers, but struggle with them thereby mightily.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,53,53,N´And it is He who let forth the two seas, this one Sweet, grateful to taste, and this salt, bitter to the tongue, and He set between them a barrier, and a ban forbidden.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,54,54,N´And it is He who created of water a mortal, and made him kindred of blood and marriage; thy Lord is All-powerful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,55,55,N´And they serve, apart from God, what neither profits them nor hurts them; and the unbeliever is ever a partisan against his Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,56,56,N´We have sent thee not, except good tidings to bear, and warning.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,57,57,N´Say: ´I do not ask of you a wage for this, except for him who wishes to take to his Lord a way.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,58,58,N´Put thy trust in the Living God, the Undying, and proclaim His praise. Sufficiently is He aware of His servants sins´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,59,59,N´who created the heavens and the earth, and what between them is, in six days, then sat Himself upon the Throne, the All-compassionate: ask any informed of Him!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,60,60,N´But when they are told, ´Bow yourselves to the All-merciful,´ they say, ´And what is the All-merciful? Shall we bow ourselves to what thou biddest us?´ And it increases them in aversion.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,61,61,N´Blessed be He who has set in heaven constellations, and has set among them a lamp, and an illuminating moon.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,62,62,N´And it is He who made the night and day a succession for whom He desires to remember or He desires to be thankful.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,63,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,63,63,N´The servants of the All-merciful are those who walk in the earth modestly and who, when the ignorant address them, say, ´Peace´;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,64,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,64,64,N´who pass the night prostrate to their Lord and standing;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,65,65,N´who say, ´Our Lord, turn Thou from us the chastisement of Gehenna; surely its chastisement is torment most terrible;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,66,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,66,66,N´evil it is as a lodging-place and an abode´;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,67,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,67,67,N´who, when they expend, are neither prodigal nor parsimonious, but between that is a just stand;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,68,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,68,68,N´who call not upon another god with God, nor slay the soul God has forbidden except by right, neither fornicate, for whosoever does that shall meet the price´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,69,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,69,69,N´of sin-doubled shall be the chastisement for him on the Resurrection Day, and he shall dwell therein humbled,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,70,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,70,70,N´save him who repents, and believes, and does righteous work -- those, God will change their evil deeds into good deeds, for God is ever All-forgiving, All-compassionate;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,71,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,71,71,N´and whosoever repents, and does righteousness, he truly turns to God in repentance.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,72,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,72,72,N´And those who bear not false witness and, when they pass by idle talk, pass by with dignity;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,73,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,73,73,N´who, when they are reminded of the signs of their Lord, fall not down thereat deaf and blind;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,74,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,74,74,N´who say, ´Our Lord, give us refreshment of our wives and seed, and make us a model to the godfearing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,75,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,75,75,N´Those shall be recompensed with the highest heaven, for that they endured patiently, and they shall receive therein a greeting and --´ Peace!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,76,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,76,76,N´Therein they shall dwell forever; fair it is as a lodging-place and an abode.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,25,77,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,25,77,77,N´Say: ´My Lord esteems you not at all were it not for your prayer, for you have cried lies, and it shall surely be fastened.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,1,1,N´Ta Sin Mim´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,2,2,N´Those are the signs of the Manifest Book.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,3,3,N´Perchance thou consumest thyself that they are not believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,4,4,N´If We will, We shall send down on them out of heaven a sign, so their necks will stay humbled to it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,5,5,N´But never fresh remembrance comes to them from the All-merciful, except they turn away from it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,6,6,N´So they have cried lies; therefore assuredly tidings will come to them of that they mocked at.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,7,7,N´What, have they not regarded the earth, how many therein We have caused to grow of every generous kind?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,8,8,N´Surely in that is a sign, yet most of them are not believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,9,9,N´Surely thy Lord, He is the All-mighty, the All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,10,10,N´And when thy Lord called to Moses, ´Go to the people of the evildoers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,11,11,N´the people of Pharaoh; will they not be godfearing?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,12,12,N´He said, ´My Lord, I fear they will cry me lies,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,13,13,N´and my breast will be straitened, and my tongue will not be loosed; so send to Aaron.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,14,14,N´They also have a sin against me, and I fear they will slay me.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,15,15,N´Said He, ´No indeed; but go, both of you, with Our signs, and We assuredly shall be with you, listening.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,16,16,N´So go you to Pharaoh, and say, \"Verily, I am the Messenger of the Lord of all Being;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,17,17,N´so send forth with us the Children of Israel.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,18,18,N´He said, ´Did we not raise thee amongst us as a child? Didst thou not tarry among us years of thy life?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,19,19,N´And thou didst the deed thou didst, being one of the ungrateful!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,20,20,N´Said he, ´Indeed I did it then, being one of those that stray;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,21,21,N´so I fled from you, fearing you. But my Lord gave me Judgment and made me one of the Envoys.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,22,22,N´That is a blessing thou reproachest me with, having enslaved the Children of Israel.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,23,23,N´Pharaoh said, ´And what is the Lord of all Being?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,24,24,N´He said, ´The Lord of the heavens and earth, and what between them is, if you have faith.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,25,25,N´Said he to those about him, ´Do you not hear?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,26,26,N´He said, ´Your Lord and the Lord of your fathers, the ancients.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,27,27,N´Said he, ´Surely your Messenger who was sent to you is possessed!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,28,28,N´He said, ´The Lord of the East and West, and what between them is, if you have understanding,´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,29,29,N´Said he, ´If thou takest a god other than me, I shall surely make thee one of the imprisoned.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,30,30,N´He said, ´What, even though I brought thee something so manifest?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,31,31,N´Said he, ´Bring it then, if thou art of the truthful.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,32,32,N´So he cast his staff, and behold, it was a serpent manifest.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,33,33,N´And he drew forth his hand, and lo, it was white to the beholders.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,34,34,N´Said he to the Council about him, ´Surely this man is a cunning sorcerer´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,35,35,N´who desires to expel you from your land by his sorcery; what do you command?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,36,36,N´They said, ´Put him and his brother off a while, and send among the cities musterers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,37,37,N´to bring thee every cunning sorcerer.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,38,38,N´So the sorcerers were assembled for the appointed time of a fixed day.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,39,39,N´The people were asked, ´Will you assemble?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,40,40,N´Haply we shall follow the sorcerers if it should be they are the victors.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,41,41,N´Then, when the sorcerers came, they said to Pharaoh, ´Shall we indeed have a wage, if we should be the victors?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,42,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,42,42,N´He said, ´Yes indeed; and you shall then be among the near-stationed.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,26,43,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,26,43,43,N´Moses said to them, ´Cast you down what you will cast.´´)');
