INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,3,3,N´and the reciters of a Remembrance,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,4,4,N´surely your God is One,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,5,5,N´Lord of the heavens and the earth, and of what between them is Lord of the Easts.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,6,6,N´We have adorned the lower heaven with the adornment of the stars´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,7,7,N´and to preserve against every rebel Satan;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,8,8,N´they listen not to the High Council, for they are pelted from every side,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,9,9,N´rejected, and theirs is an everlasting chastisement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,10,10,N´except such as snatches a fragment, and he is pursued by a piercing flame.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,11,11,N´So ask them for a pronouncement -- Are they stronger in constitution, or those We created? We created them of clinging clay.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,12,12,N´Nay, thou marvellest; and they scoff´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,13,13,N´and, when reminded, do not remember´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,14,14,N´and, when they see a sign, would scoff;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,15,15,N´and they say, ´This is nothing but manifest sorcery.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,16,16,N´What, when we are dead and become dust and bones, shall we indeed be raised up?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,17,17,N´What, and our fathers, the ancients?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,18,18,N´Say: ´Yes, and in all lowliness.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,19,19,N´For it is only a single scaring, then behold, they are watching´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,20,20,N´and they say, ´Woe, alas for us! This is the Day of Doom.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,21,21,N´´This is the Day of Decision, even that you cried lies to.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,22,22,N´Muster those who did evil, their wives, and that they were serving,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,23,23,N´apart from God, and guide them unto the path of Hell!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,24,24,N´And halt them, to be questioned:´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,25,25,N´\"Why help you not one another?\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,26,26,N´No indeed; but today they resign themselves in submission´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,27,27,N´and advance one upon another, asking each other questions.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,28,28,N´These say, ´Why, you of old would come to us from the right hand.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,29,29,N´Those say, ´No; on the contrary, you were not believers;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,30,30,N´we had no authority over you; no, you were an insolent people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,31,31,N´So our Lord´s Word is realised against us; we are tasting it.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,32,32,N´Therefore we perverted you, and we ourselves were perverts.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,33,33,N´So all of them on that day are sharers in the chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,34,34,N´Even so We do with the sinners;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,35,35,N´for when it was said to them, ´There is no god but God,´ they were ever waxing proud,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,36,36,N´saying, ´What, shall we forsake our gods for a poet possessed?´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,37,37,N´´No indeed; but he brought the truth, and confirmed the Envoys.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,38,38,N´Now certainly you shall be tasting the painful chastisement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,39,39,N´and not be recompensed, except according to what you were doing.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,40,40,N´Except for God´s sincere servants;´)');
