INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,56,56,N´He says, ´By God, wellnigh thou didst destroy me;´)');
