INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,60,60,N´This is indeed the mighty triumph,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,61,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,61,61,N´for the like of this let the workers work.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,62,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,62,62,N´Is that better as a hospitality, or the Tree of Ez-Zakkoum?´)');
