INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,37,65,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,37,65,65,N´its spathes are as the heads of Satans,´)');
