INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,50,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,50,45,45,N´We know very well what they say; thou art not a tyrant over them. Therefore remind by the Koran him who fears My threat.´)');
