INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,35,35,N´So We brought forth such believers as were in it,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,36,36,N´but We found not therein except one house of those that have surrendered themselves.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,37,37,N´And therein We left a sign to those who fear the painful chastisement.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,38,38,N´And also in Moses, when We sent him unto Pharaoh, with a clear authority,´)');
