INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,40,40,N´So We seized him and his hosts, and We cast them into the sea, and he blameworthy.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,41,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,41,41,N´And also in Ad, when We loosed against them the withering wind´)');
