INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,44,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,44,44,N´Then they turned in disdain from the commandment of their Lord, and the thunderbolt took them and they themselves beholding´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,45,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,45,45,N´and they were not able to stand upright, and were not helped.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,46,46,N´And the people of Noah before; surely they were an ungodly people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,47,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,47,47,N´And heaven -- We built it with might, and We extend it wide.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,48,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,48,48,N´And the earth -- We spread it forth; O excellent Smoothers!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,49,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,49,49,N´And of everything created We two kinds; haply you will remember.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,50,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,50,50,N´Therefore flee unto God! I am a clear warner from Him to you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,51,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,51,51,N´And set not up with God another god; I am a clear warner from Him to you.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,52,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,52,52,N´Even so not a Messenger came to those before them but they said, ´A sorcerer, or a man possessed!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,53,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,53,53,N´What, have they bequeathed it one to another? Nay, but they are an insolent people.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,54,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,54,54,N´So turn thou from them; thou wilt not be reproached.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,55,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,55,55,N´And remind; the Reminder profits the believers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,56,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,56,56,N´I have not created jinn and mankind except to serve Me.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,57,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,57,57,N´I desire of them no provision, neither do I desire that they should feed Me.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,58,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,58,58,N´Surely God is the All-provider, the Possessor of Strength, the Ever-Sure.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,59,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,59,59,N´The evildoers shall have their portion, like the portion of their fellows; so let them not hasten Me!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,51,60,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,51,60,60,N´So woe to the unbelievers, for that day of theirs that they are promised.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,52,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,52,1,1,N´By the Mount´)');
