INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,52,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,52,19,19,N´´Eat and drink, with wholesome appetite, for that you were working.´)');
