INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,11,11,N´His heart lies not of what he saw;´)');
