INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,14,14,N´by the Lote-Tree of the Boundary´)');
