INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,17,17,N´his eye swerved not; nor swept astray.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,18,18,N´Indeed, he saw one of the greatest signs of his Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,19,19,N´Have you considered El-Lat and El-´Uzza´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,20,20,N´and Manat the third, the other?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,21,21,N´What, have you males, and He females?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,22,22,N´That were indeed an unjust division.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,23,23,N´They are naught but names yourselves have named, and your fathers; God has sent down no authority touching them. They follow only surmise, and what the souls desire; and yet guidance has come to them from their Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,24,24,N´Or shall man have whatever he fancies?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,25,25,N´And to God belongs the First and the Last.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,26,26,N´How many an angel there is in the heavens whose intercession avails not anything, save after that God gives leave to whomsoever He wills and is well-pleased.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,27,27,N´Those who do not believe in the world to come name the angels with the names of females.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,28,28,N´They have not any knowledge thereof; they follow only surmise, and surmise avails naught against truth.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,29,29,N´So turn thou from him who turns away from Our Remembrance, and desires only the present life.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,30,30,N´That is their attainment of knowledge. Surely thy Lord knows very well those who have gone astray from His way, and He knows very well those who are guided.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,31,31,N´To God belongs whatsoever is in the heavens and whatsoever is in the earth, that He may recompense those who do evil for what they have done, and recompense those who have done good with the reward most fair.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,32,32,N´Those who avoid the heinous sins and indecencies, save lesser offences surely thy Lord is wide in His forgiveness. Very well He knows you, when He produced you from the earth, and when you were yet unborn in your mothers´ wombs; therefore hold not yourselves purified; God knows very well him who is godfearing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,33,33,N´Hast thou considered him who turns his back´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,34,34,N´and gives a little, and then grudgingly?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,35,35,N´Does he possess the knowledge of the Unseen, and therefore he sees?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,36,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,36,36,N´Or has he not been told of what is in the scrolls of Moses,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,37,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,37,37,N´and Abraham, he who paid his debt in full?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,38,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,38,38,N´That no soul laden bears the load of another,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,39,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,39,39,N´and that a man shall have to his account only as he has laboured,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,40,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,40,40,N´and that his labouring shall surely be seen,´)');
