INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,53,46,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,53,46,46,N´of a sperm-drop, when it was cast forth,´)');
