INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,54,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,54,1,1,N´The Hour has drawn nigh: the moon is split.´)');
