INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,71,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,71,1,1,N´We sent Noah to his people, saying, ´Warn thy people, ere there come on them a painful chastisement.´´)');
