INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,71,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,71,23,23,N´and have said, \"Do not leave your gods, and do not leave Wadd, nor Suwa´, Yaghuth, Ya´uq, neither Nasr.\"´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,71,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,71,24,24,N´And they have led many astray. Increase Thou not the evildoers save in error!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,71,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,71,25,25,N´And because of their transgressions they were drowned, and admitted into a Fire, for they found not, apart from God, any to help them.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,71,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,71,26,26,N´And Noah said, ´My Lord, leave not upon the earth of the unbelievers even one.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,71,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,71,27,27,N´Surely, if Thou leavest them, they will lead Thy servants astray, and will beget none but unbelieving libertines.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,71,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,71,28,28,N´My Lord, forgive me and my parents and whosoever enters my house as a believer, and the believers, men and women alike; and do Thou not increase the evildoers save in ruin!´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,1,1,N´Say: ´It has been revealed to me that a company of the jinn gave ear, then they said, \"We have indeed heard a Koran wonderful,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,2,2,N´guiding to rectitude. We believe in it, and we will not associate with our Lord anyone.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,3,3,N´He -- exalted be our Lord´s majesty! has not taken to Himself either consort or a son.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,4,4,N´The fool among us spoke against God outrage,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,5,5,N´and we had thought that men and jinn would never speak against God a lie.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,6,6,N´But there were certain men of mankind who would take refuge with certain men of the jinn, and they increased them in vileness,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,7,7,N´and they thought, even as you also thought, that God would never raise up anyone.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,8,8,N´And we stretched towards heaven, but we found it filled with terrible guards and meteors.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,9,9,N´We would sit there on seats to hear; but any listening now finds a meteor in wait for him.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,10,10,N´And so we know not whether evil is intended for those in the earth, or whether their Lord intends for them rectitude.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,11,11,N´And some of us are the righteous, and some of us are otherwise; we are sects differing.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,12,12,N´Indeed, we thought that we should never be able to frustrate God in the earth, neither be able to frustrate Him by flight.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,13,13,N´When we heard the guidance, we believed in it; and whosoever believes in his Lord, he shall fear neither paltriness nor vileness.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,14,14,N´And some of us have surrendered, and some of us have deviated. Those who have surrendered sought rectitude;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,15,15,N´but as for those who have deviated, they have become firewood for Gehenna!\"´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,16,16,N´Would they but go straight on the way, We would give them to drink of water copious,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,17,17,N´that We might try them therein. And whosoever turns away from the Remembrance of his Lord, He will thrust him into chastisement rigorous.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,18,18,N´The places of worship belong to God; so call not, along with God, upon anyone.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,19,19,N´When the servant of God stood calling on Him, they were wellnigh upon him in swarms.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,20,20,N´Say: ´I call only upon my Lord, and I do not associate with Him anyone.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,21,21,N´Say: ´Surely I possess no power over you, either for hurt or for rectitude.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,22,22,N´Say: ´From God shall protect me not anyone, and I shall find, apart from Him, no refuge,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,23,23,N´excepting a Deliverance from God and His Messages. And whoso rebels against God and His messenger, for him there awaits the Fire of Gehenna; therein they shall dwell forever.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,24,24,N´Until, when they see that which they are promised, then they will know who is weaker in helpers and fewer in numbers.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,25,25,N´Say: ´I do not know whether that which you are promised is nigh, or whether my Lord will appoint for it a space;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,26,26,N´Knower He of the Unseen, and He discloses not His Unseen to anyone,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,27,27,N´save only to such a Messenger as He is well-pleased with; then He despatches before him and behind him watchers,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,72,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,72,28,28,N´that He may know they have delivered the Messages of their Lord; and He encompasses all that is with them, and He has numbered everything in numbers.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,1,1,N´O thou enwrapped in thy robes,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,2,2,N´keep vigil the night, except a little´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,3,3,N´(a half of it, or diminish a little,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,4,4,N´or add a little), and chant the Koran very distinctly;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,5,5,N´Behold, We shall cast upon thee a weighty word;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,6,6,N´surely the first part of the night is heavier in tread, more upright in speech,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,7,7,N´surely in the day thou hast long business.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,8,8,N´And remember the Name of thy Lord, and devote thyself unto Him very devoutly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,9,9,N´Lord of the East and the West; there is no god but He; so take Him for a Guardian.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,10,10,N´And bear thou patiently what they say, and forsake them graciously.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,11,11,N´Leave Me to those who cry lies, those prosperous ones, and respite them a little,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,12,12,N´for with Us there are fetters, and a furnace,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,13,13,N´and food that chokes, and a painful chastisement,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,14,14,N´upon the day when the earth and the mountains shall quake and the mountains become a slipping heap of sand.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,15,15,N´Surely We have sent unto you a Messenger as a witness over you, even as We sent to Pharaoh a Messenger,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,16,16,N´but Pharaoh rebelled against the Messenger, so We seized him remorselessly.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,17,17,N´If therefore you disbelieve, how will you guard yourselves against a day that shall make the children grey-headed?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,18,18,N´Whereby heaven shall be split, and its promise shall be performed.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,19,19,N´Surely this is a Reminder; so let him who will take unto his Lord a way.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,73,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,73,20,20,N´Thy Lord knows that thou keepest vigil nearly two-thirds of the night, or a half of it, or a third of it, and a party of those with thee; and God determines the night and the day. He knows that you will not number it, and He has turned towards you. Therefore recite of the Koran so much as is feasible. He knows that some of you are sick, and others journeying in the land, seeking the bounty of God, and others fighting in the way of God. So recite of it so much as is feasible. And perform the prayer, and pay the alms, and lend to God a good loan. Whatever good you shall forward to your souls´ account, you shall find it with God as better, and mightier a wage. And ask God´s forgiveness; God is All-forgiving, All-compassionate.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,1,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,1,1,N´O thou shrouded in thy mantle,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,2,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,2,2,N´arise, and warn!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,3,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,3,3,N´Thy Lord magnify´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,4,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,4,4,N´thy robes purify´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,5,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,5,5,N´and defilement flee!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,6,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,6,6,N´Give not, thinking to gain greater´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,7,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,7,7,N´and be patient unto thy Lord.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,8,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,8,8,N´For when the Trump is sounded´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,9,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,9,9,N´that day will be a harsh day,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,10,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,10,10,N´for the unbelievers not easy.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,11,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,11,11,N´Leave Me with him whom I created alone,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,12,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,12,12,N´and appointed for him ample wealth´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,13,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,13,13,N´and sons standing before him,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,14,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,14,14,N´and made all things smooth for him;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,15,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,15,15,N´then he is eager that I should do more.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,16,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,16,16,N´Nay! He is forward unto Our signs;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,17,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,17,17,N´and I shall constrain him to a hard ascent.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,18,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,18,18,N´Lo! He reflected, and determined --´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,19,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,19,19,N´death seize him, how he determined!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,20,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,20,20,N´Again, death seize him, how he determined!´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,21,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,21,21,N´Then he beheld,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,22,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,22,22,N´then he frowned, and scowled,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,23,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,23,23,N´then he retreated, and waxed proud.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,24,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,24,24,N´He said, ´This is naught but a trumped-up sorcery;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,25,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,25,25,N´´this is nothing but mortal speech.´´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,26,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,26,26,N´I shall surely roast him in Sakar;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,27,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,27,27,N´and what will teach thee what is Sakar?´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,28,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,28,28,N´It spares not, neither leaves alone´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,29,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,29,29,N´scorching the flesh;´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,30,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,30,30,N´over it are nineteen.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,31,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,31,31,N´We have appointed only angels to be masters of the Fire, and their number We have appointed only as a trial for the unbelievers, that those who were given the Book may have certainty, and that those who believe may increase in belief, and that those who were given the Book and those who believe may not be in-doubt, and that those in whose hearts there is sickness, and the unbelievers, may say, ´What did God intend by this as a similitude?´ So God leads astray whomsoever He will, and He guides whomsoever He will; and none knows the hosts of thy Lord but He. And it is naught but a Reminder to mortals.´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,32,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,32,32,N´Nay! By the moon´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,33,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,33,33,N´and the night when it retreats´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,34,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,34,34,N´and the dawn when it is white,´)');
INSERT INTO `english_arberry` (`qdb`,`sura`,`verse`,`text`) VALUES (101,74,35,'INSERT INTO Quran (QuranID, ChapterID, VerseIdBegin, VerseIdEnd, AyahText) VALUES (101,74,35,35,N´surely it is one of the greatest things´)');
